        db.execSQL("INSERT INTO akilathirattu2 VALUES(14,'சாதி வளமை','சான்றோர் முதலாய்ச் சக்கிலி யன்வரையும் உண்டான சாதி ஒக்கவொரு இனம்போல் தங்கள்தங்கள் நிலைமை தப்பிமிகப் போகாமல் திங்கள் மும்மாரி சிறந்தோங்கி யேவாழ்ந்தார் செல்வம் பெருக சிவநிலைமை மாறாமல் அலுவல் தினஞ்செய்து அன்புற் றிருந்தனராம் தான்பெரி தென்று தப்புமிகச் செய்யாமல் வான்பெரி தென்று மகிழ்ந்திருந்தா ரம்மானை ஒருவர்க் கொருவர் ஊழியங்கள் செய்யாமல் கருதல் சிவன்பேரில் கருத்தா யிருந்தனராம் செய்யும் வழக்குச் சிவன்பேரி லல்லாது வையம்வழக்கு வாரா தேயிருந்தார் அடிபணிய வென்று அலைச்சல்மிகச் செய்யாமல் குடிபொருந்தி வாழ்ந்து குடியிருந்தா ரம்மானை சேயினுட ஆட்டுச் செவிகேட் டிருப்பதல்லால் பேயினுட ஆட்டோர் பூதரறியா திருந்தார் இந்தப் படிமனுவோர் எல்லா மிருந்துவொரு விந்துக் கொடிபோல் வீற்றிருந்தா ரம்மானை இப்படித் தெய்வ இராச்சிய நீதமும் மற்படித் தேச மனுவுட நீதமும் நற்புடன் தேசம் நாடி வாழ்வதைக் கற்பு ரீசர் கண்டு மகிழ்ந்தனர்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(15,'கயிலை வளமை','முத்தான சீமை மூன்று நீதத்தோடு பத்தாசை யாகப் பண்பாய்த் தழைத்திடவே நாலான வேதம் நல்ல கலியுகமாய் மேலாம் பரமாய் விளங்கி யிருந்திடவே தேவ ருறையும் திருக்கயிலை தன்வளமை பாவலர்கள் முன்னே பாடினா ரம்மானை ஈச ருறையும் இரத்தின கிரிதனிலே வாசவனுந் தேவர் மறையோரும் வீற்றிருக்க பொன்னம் பலநாதர் பொருந்திருக்கும் மண்டபமும் கின்னரர்கள் வேதம் கிளர்த்துகின்ற மண்டபமும் வேதப் புரோகி விளங்குகின்ற மண்டபமும் சீத உமையாள் சிறந்திலங்கும் மண்டபமும் நீதத் திருமால் நிறைந்திலங்கும் மண்டபமும் சீதை மகிழ்ந்து சிறந்திருக்கும் மண்டபமும் ஆதவனுஞ் சந்திரனும் அவதரிக்கும் மண்டபமும் வேதாவும் ருத்திரனும் வீற்றிருக்கும் மண்டபமும் ஆரு மறிந்து அளவிடக் கூடாத பாரு படைத்த பரமே சுரனாரை விசுவாச மேலோர் விமல னடிவணங்கி வசுவாசு தேவன் வந்து மிகவணங்கி மறைவேத சாஸ்திரங்கள் மலரோ னடிவணங்கி இறவாத தேவர் இறைஞ்சி மிகவணங்கி எமதர்ம ராசன் எப்போதும் வந்துநிற்க பூமகளும் வேதப் புரோகிவந்து தெண்டனிட காமதேவன் முதலாய்க் கணக்கர் மிகுமுனிவர் நாமநெடி யோன்பதத்தை நாள்தோறும் போற்றிநிற்க தலைவ னிருக்கும் தங்கத் திருக்கயிலை நிலைமை யெடுத்துரைக்க நிலையாது அம்மானை ஆறு செஞ்சடை சூடிய அய்யனார் அமர்ந்து வாழுங் கயிலை வளமதைக் கூறக் கூறக் குறைவில்லை காணுமே கொன்றை சூடும் அண்டர் திருப்பதம் வாறு வாறு வகுக்க முடிந்திடா மகிழுங் குண்ட வளஞ்சொல்லி யப்புறம் வேறு வேறு விளம்பவே கேளுங்கோ மெய்யுள் ளோராகிய வேத அன்பரே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(16,'அகில வளமை','கயிலை வளமை கட்டுரைக்கக் கூடாது அகில வளமை அருளக்கே ளம்மானை தேவாதி தேவர் திருக்கூட்ட மாயிருக்க மூவாதி மூவர் மிக்கவொரு மிக்கவொரு சிங்கா சனத்தில் சிறந்திருக்கும் வேளையிலே மங்காத தேவி மாதுதிரு லட்சுமியாள் ஈரே ழுலகும் இரட்சித்த வுத்தமியாள் பாரேழும் படைத்த பரமதிரு லட்சுமியாள் நன்றா யெழுந்திருந்து நாரா யணர்பதத்தைத் தெண்டனிட்டு லட்சுமியும் செப்புவா ளம்மானை தேவரீ ரென்னைத் திருக்கலியா ணமுகித்துக் கோவரி குண்டக் குடியிருப்பி லேயிருத்திப் போவது என்ன புதுமை எனக்கறிய தேவரீர் நீர்தான் செப்பிடீர் என்றுரைத்தாள் பின்னுமந்த லட்சுமியாள் பெருமாள் தனைத்தொழுது என்றும் இருக்க இறவா திருப்போனே ஆதியால் சூட்சம் அளவெடுக்கக் கூடாத நாதியாய் நின்ற நாரா யணப்பொருளே தேவாதிக் கெல்லாம் திருமுதலாய் நின்றோனே மூவாதிக் கெல்லாம் முதன்மையாய் நின்றோனே உமக்கு எதிரி உலகமதி லுண்டோகாண் தமக்கு எதிரிவந்த தன்மை மிகவுரையும் உகத்துக் குகம்பிறந்து உலகிடத்தி லேயிருக்க அகத்துவந்த ஞாயம் அருளுவீ ரெம்பெருமாள் என்று திருவும் இதுவுரைக்க மாயவரும் நன்று நன்றென்று நாரா யணருரைப்பார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(17,'நீடிய யுகம்','சக்தி சிவமும் தானுதித்த காலமதில் எத்திசையும் நாமள் இருபேர் பிறப்பதிலும் ருத்திரர் மயே சுரருதித்த நாளதிலும் பத்தியுள்ள தேவர் பரநாதர் நாளதிலும் வானோர்கள் தெய்வார் மறைவேத சாஸ்திரமும் ஈனமாய்ச் சண்டன் இவன்பிறந்த நாளதிலும் ஆதித்தன் வாயு அண்டபிண்டம் தோன்றியபின் ஈதுதித்த காலம் இராச்சியமொன் றுண்டுகண்டாய் அவ்வுகத்தைக் கண்டு ஆதிபிரமா மகிழ்ந்து இவ்வுகத்து நாமம் என்னவிடு வோமென்று மாலும் பிரமாவும் மாயா திருப்போனும் ஆலோசித் தவர்கள் ஆகமத்தைத் தான்பார்த்து நீடிய யுகமெனவே நியமித் துறுதிகொண்டு தேடிய முப்பொருளும் செப்பினர்கா ணம்மானை அவ்வுகத்தை ஈசர் ஆர்ப்பரித்த காலமதில் இவ்வுகத்துக் காரை இருத்துவோ மென்றுசொல்லி எல்லோருங் கூடி இதமித்தா ரம்மானை அல்லோருங் கூடி ஆலோசிக்கு மளவில் தில்லையா ரீசன் திருவேள்வி தான்வளர்க்க நல்லையா வேள்வி நன்றாய் வளர்த்திடவே பிறந்தான் குறோணி பெரிய மலைபோலே அறந்தா னறியா அநியாயக் கேடனுமாய் பிண்டம் நிறையும் பொல்லாதான் தன்னுடம்பு அண்டம் அமைய அவன்பிறந்தா னம்மானை முகங்கண் களெல்லாம் முதுகுப்பு றமேலாட நகக்கரங்கள் கோடி கால்க ளொருகோடி தவங்க ளறியாச் சண்டித் தடிமோடன் பவமே நாள்தோறும் பண்ணு மியல்புடையோன் குறோணி யவனுயரம் கோடிநாலு முழமாய் கயிலை கிடுகிடெங்கும் கால்மாறி வைக்கையிலே அகிலங் கிடுகிடெங்கும் அவனெழுந்தா லம்மானை இப்படியே குறோணி என்றவொரு அசுரன் முப்படியே நீயே யுகத்தி லிருந்தான்காண் இருந்து சிலநாள் இவன்தூங்கித் தான்விழித்து அருந்தும் பசியால் அவனெழுந்து பார்ப்பளவில் பாருகங் காணான் பலபேருடல் காணான் வாருதி நீரை வாரி விழுங்கினன்காண் கடல்நீ ரத்தனையும் கடவாய் நனையாமல் குடலெல்லா மெத்தக் கொதிக்கு தெனவெகுண்டு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(18,'நீடிய யுகம்','அகிலம் விழுங்க ஆர்ப்பரித்து நிற்பளவில் கயிலை தனைக்கண்டு கண்கள்மிகக் கொண்டாடி ஆவி யெடுத்து அவன்விழுங்கு மப்போது தாவிக் குவித்துத் தப்பினார் மாயவரும் மாயவரு மோடி மண்ணுலோகம் புகுந்து தூயவரு மங்கே சிவனை மிகநினைத்துத் தவசு இருந்தார்காண் தாமோ தரனாரும் தவசு தனிலீசன் சன்னாசி போலேவந்து ஆருநீ யிந்த ஆழ வனந்தனிலே ஏதுநீ தவசு எனைநினைந்த வாறேது என்று சன்னாசி இதுவுரைக்க மாயவரும் பண்டுபட்ட பாட்டைப் பகர்ந்தா ரவரோடே கயிலை யெமலோகம் கறைக்கண்டர் சக்திவரை அகில மதைக்குறோணி அசுரனென்ற மாபாவி விழுங்கினான் நானும் உபாயமாய்த் தப்பிவந்தேன் பளிங்கு மலைநாதன் பாரத்தே வாதிமுதல் பண்டுபோல் நாளும் பதியி லிருந்திடவே என்றுங் கயிலை இலங்கி இருந்திடவும் முண்டு செய்தபாவி முகமு மவனுடம்பும் துண்டா றதாகத் தொல்புவியி லிட்டிடவும் கண்டங்கண்ட மாய்ப்போடக் கடிய வரமெனக்கு தண்டமிழீர் நீரும் தரவே தவசிருந்தேன் என்று திருமால் எடுத்துரைக்க வேயீசர் மன்று தனையளந்த மாலோ டுரைக்கலுற்றார் கேளாய்நீ விட்டிணுவே கேடன் குறோணிதனைத் தூளாக்கி யாறு துண்ட மதுவாக்கி விட்டெறிந்தா லவனுதிரம் மேலு மொருயுகத்தில் கெட்டுக் கிளையாய்க் கொடிய சூரக்குலமாய்ப் பிறக்கு மவனுதிரம் பொல்லாதான் தன்னுடம்பு துண்ட மொன்றுதானும் தொல்புவியி லேகடிய குண்டோம சாலியனாய்க் குவலயத்தி லேபிறப்பான் அப்படியே குறோணி அவனுதிர மானதுவும் இப்படியே ஆறு யுகத்துக் கவனுடம்பு வந்து பிறப்பான்காண் மாற்றானா யுன்றனக்கு உகத்துக் குகமே உத்தமனாய் நீபிறந்து அகத்துக் கவன்பிறப்பு ஆறு யுகமதிலும் உண்டு மவன்சீவன் உயிரழிவு வந்தவந்நாள்  பண்டு நடுக்கேட்டுப் பாவி யவனுயிரைக் கொன்றுபோட் டேநரகக் குழிதூர்க்க நாள்வருங்காண் என்று விடைகொடுத்தார் ஈசுரர்கா ணம்மானை அன்று விடைவேண்டி அதிகத் திருமாலும் குன்றுபோல் வந்த கொடிய படுபாவி குறோணி தனைச்செயிக்கக் கோபம்வெகுண் டெழுந்து சுறோணித வேதன் துடியாய் நடந்தனராம் நாகத்தணை கிடந்த நாரா யணமூர்த்தி வேகத்தால் குறோணிதனை வெட்டிப் பிளக்கலுற்றார் வெட்டினா ராறு மிகுதுண்ட மம்மானை கெட்டி தானென்று கிருபைகூர்ந்தே தேவர் துண்டம தாறும் தொல்புவியி லேபோட்டுப் பிண்டமதைச் சுமந்து போட்டனர்கா ணம்மானை அந்தக் குறோணி அவனுதிர மானதையும் கொந்து கொந்தாகக் குளம்போலே குண்டுவெட்டி உதிரமதை விட்டு உயர்ந்தபீடம் போட்டுச் சதுர யுகமெனவே தான்வகுத்தா ரோர்பீடத்தை ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(19,'சதுரயுகம் - குண்டோமசாலி பாடு','அவ்வுகத் திலேயுதிரம் அசுரக் குலமாகி முவ்வுகத்துப் பாவி முடிந்தவொரு துண்டமதைக் குண்டோம சாலி எனவே கொடியவனாய்ப்  பண்டோர் குறோணி பாதகனாறு துண்டமதில் வந்துபிறந் தான்சதுர வையகத்தி லம்மானை முந்து பிறந்த முழுமோச மானதிலும் மந்து முகமாய் மாபாவி தன்னுயரம் நானூறா யிரமுழங்கள் நாடுமவன் கரங்கள் முந்நூறு கால்கை வேழ்கள் துதிபோலே உடைதோ ளுடம்பு உருவறியா மாபாவி படைத்தோன் தனையறியான் பாரியென்று மறியான் அட்டைபோ லேசுருண்டு அம்மிபோ லேகிடப்பான் மட்டைபோ லேதிரிவான் வயிறு மிகப்பசித்தால் அன்னுகத் திலுள்ள அசுரக் குலங்களையும்  தன்வயிற்றுக் கிட்டுத் தடிபோ லுருண்டிடுவான் இப்படியே நாளும் இவன்குலங்க ளானதெல்லாம் அப்படியே தின்று அவன்பசிக ளாற்றாமல் அய்யையோ வென்று அலறினன்கா ணம்மானை மெய்யை யனான விறுமா அதுகேட்டுச் சிவனைத் தொழுது செப்புவா ரம்மானை தவமே தவப்பொருளே தாண்டவசங் காரவனே எவனோ ஒருத்தன் இட்டசத்த மானதிலே தவலோக மெல்லாம் தானலைவ தேதெனவே மாய னதுகேட்க வகுப்பா ரங்கீசுரரும் ஆயனே நீயும் அறியலையோ ஞாயமது குண்டோ மசாலி கொடியமா பாவியனாய்ப் பண்டோர் குறோணி பாதகன்தன் துண்டமதாய்ப் பிறந்தா னவனும் பேருதிரந் தன்கிளையாய் இறந்தா ரவர்கள் இரையா யவன்தனக்கு ஆன பசிகள் ஆற்றாம லேயவனும் வானமது அலைய வாய்விட்டான் கண்டாயே என்று சிவனார் ஈதுரைக்க மாயவரும் அன்று மகாமாலும் அக்குண்டோ மசாலினுக்கு இரையாகத் தேவர்களை ஏற்றநாங் கிலாக்கி வரையா னதைத்தூண்டில் மறையைக் கயிறாக்கி வாயுவைத் தோணி வருணன் தனைமிதப்பாய்த் தேய மதைச்சூழத் திரைகடலைத் தான்வருத்தி ஓடையாய்ச் சதுர யுகம்வழியே தானேவி தேடரிய மாயன் திருவோணி தானேறி மூவாதி மூவர் ஓணிதனைத் தள்ளிவரக் காவாலி மாயன் கன்னியிலே தூண்டலிடச் சதுர யுகமாளும் சண்டித்தடி மோடன் எதிரே வருமாற்றில் இரையைமிகக் கண்டாவி  நாடிப் பசிதீர நல்லஇரை யாகுமென்று ஓடிவந்து பாவி விழுங்கினான் தூண்டல்தனை தூண்டில் விழுங்க சுரண்டி மிகக்கொளுவி மாண்டனன் காண்பாவி வலிய மலைபோலே பாவி மடிய பரமே சுரனாரும் தாவிச் சலத்தால் சதுர யுகமழித்தார் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(20,'தேவர்கள் வேண்டுகோள்','சதுர யுகமழிய தானவர்க ளெல்லோரும் மதுர மொழியீசன் மலரடியைத் தான்பூண்டு தேவர் மறையோர் தெய்வேந் திரன்முதலாய் மூவர்களும் வந்து முதலோ னடிபணிந்து பரமனே நீரும் படைத்தயுகம் ரண்டதிலும் வரமே துங்கேட்டு வாழ்ந்தவரைக் கண்டிலமே அந்த சந்தமில்லை ஆணுவங்கள் தானுமில்லை இந்த வகைச்சாதி இல்லாம லீசுரரே பிறந்தா லவனும் பெரியோ னடிவணங்கி வரந்தா ருமென்று வாளா யுதத்தோடே வலுவும் பலமும் வாய்த்தசூ ரப்படையும் கொலுவும் பெரிய குவிந்தமதில் கோட்டைகளும் கெட்டுக் கிளைபாணி கிரண மதுவுடனே நட்டுப் பயிரால் நாளும் பசிதீர்ந்து   இருந்து பொறுக்க இராச்சியமொன் றுண்டாக்கும் வருந்தி மகாதேவர் மலரோ னடிவணங்க ஆதி சிவனும் அதிகசந் தோசமதாய் வேதியரைத் தான்வருத்தி விளம்புவா ரீசுரரும் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(21,'நெடிய யுகம்','தில்லைமலாலன் மல்லோசி வாகனன் பாடு மாலும் பிரம்மாவும் வாய்த்தபர மேசுரரும் நாலு மறையோரும் நடுவர்மிகக் கூடி முன்னேயுள்ள துண்டம் ஒன்றைரண் டாக்கிவைத்துப் பின்னே படைப்புப் பிரம்மா வுருப்படைக்க சிவாயப் பொருள்தான் சீவ நிலைகொடுக்க உபாயப் பொருள்தான் உல்லாச மேகொடுக்க முண்ட மிருபேரும் உருவா யுருவெடுத்துத் தெண்டமது கொண்டார் சிவனை மிகப்போற்றி அப்போது மாயன் ஆதி யடிவணங்கி இப்போது ஈசுரரே இவர்களிரு பேர்க்கும் என்னபேர் தானும் இடுவோ மெனவுரைக்க வன்னப் பரமேசு வரனார் வகுக்கலுற்றார் திறந்தான் பெருகும் திருமாலே நீர்கேளும் பிறந்த அசுரருக்குப் பேரிட வேணுமென்றால் மாயனே நானுமொரு உபாயம் வகுப்பேன்காள் ஆயனே நீயும் அதுகேட்க வேணுமென்றார்  அண்டபிண்டங் காணாத ஆதிகயி லாசமதில் தெண்டனிட்டு நிஷ்டை செய்கிறான் சுருதிமுனி இந்த முனியடுக்கல் இவர்கள்ரண்டு பேரைவிட்டு அந்த முனிதவத்தை அழிக்கவே சொல்லிடுவோம் என்று சிவமுரைக்க எல்லோருஞ் சம்மதித்து அன்று பிறந்த அசுரர்களைத் தானேவி போறாரே சூரர் பொருப்பொரு நூறானதுபோல் வாறாரே சூரர் வாய்களிரு காதவழி சூரருட கைகள் தொண்ணூற்றீ ரஞ்சதுவும் மூரர்கால் நூறு உயர்ந்தசிர சன்பதுவும் கண்களொருநூறு வெண்டரள மிருகலமே துங்கணங் களாகச் சூர ரடந்தேறி கண்கவிழ்ந்து யோகம் கருத்துருத்தாய் நிற்குகின்ற வண்கவிழ்ந்த மாமுனியை வாரி யெடுத்தவர்கள் அலைமே லெறிய ஆர்ப்பரிக்கு மவ்வளவில் கலைமேல் பரந்த கடிய முனிபகர்வான் ஏனடா என்னை இருந்த தவசழித்து வீணடா செய்தாய் விழலா யறமோடா ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(22,'நெடிய யுகம்','என்னை யெடுத்து இக்கடல்மேல் போட்டாலும் உன்னை யறுக்க ஓரம்பா யுருவெடுத்துப்  பங்கயக் கண்மாயன் பக்கமதில் நான்சேர்ந்து உங்க ளிருபேரை ஊடுருவ நானறுத்து இந்தக் கடலில் எடுத்துங்களை யெறிந்து உந்தனி னூரை ஒக்கக்கரிக் காடாக்கி நானும் வைகுண்டம் நற்பேறு பெற்றிருப்பேன் வானுதிரு வாணையென்று மாமுனியுஞ் சாபமிட்டான் உடனே முனியை உயர்த்தியெடுத் தேசூரர் கடல்மே லெறிந்தார் கர்ம விதிப்படியால் அந்த முனியும் அரனா ரருளாலே மந்திரபுரக் கணையாய் வாரியலைக் குள்ளிருந்தான் சுருதி முனிதனையும் தோயமதில் விட்டெறிந்து உருதிக் குடிசூரர் ஓடிவந் தேகயிலை மோச முடன்வந்த முழுநீசப் பாவியர்கள் ஈசன் தனைத்தொழுது இறைஞ்சிநின்றா ரம்மானை சுருதி முனியுட நிஷ்டை தொலைத்தவர் கருதிய சூரர் கயிலை மேவியே பருதி சூடும் பரமனைப் போற்றியே வருதி கேட்டு வருந்தினர் சூரரே சுருதி முனிதவத்தைத் தொலைத்தே யவன்தனையும் பொருதி கடல்மீதில் போட்டெறிந்து வந்தவர்கள்  ஈசன் தனைத்தொழுது இறைஞ்சிவரங் கேட்டனராம் வாசமுள்ள ஈசன் மாதுமையைத் தானோக்கித் தூயவளே மாயவளே சூர ரிருவருக்கும் நேயமுள்ள தோர்வரங்கள் நீகொடுக்க வேணுமென்றார் வரங்கொடுக்க வென்று மறையோ னதிசயித்துச் சிரசன் பதுடைய சீர்சூரனை நோக்கி ஏதுவர முங்களுக்கு இப்போது வேணுமென்றார் தீது குடிகொண்ட சிரசன்ப தோனுரைப்பான் மாதவரே தேவர்களே மறையவரே மூவர்களே ஆதவரே யெங்களுக்கு அதிகவரம் வேணுமென்றான் அம்புவியி லுள்ள அஸ்திரங்கள் வாளாலும் தம்பிரா னானாலும் தாண்டமுடி யாதவரம் வானமிது பூமி மலைகளிது மூன்றிலுள்ள ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(23,'நெடிய யுகம்','தானவராய் வாழுகின்ற தங்களா லெங்களையும் கொல்லத் தொலையாத கொடிய வரமதுவும் மல்லுக் குபாயமதும் வலுவும் பலமதுவும் ஏவலாய் வானோர் எமைத்தொழுது நின்றிடவும் தவறாம லிந்தவரம் தரவேணு மென்றுரைத்தான் உடனே சிவனாரும் உற்ற அசுரருக்கு அடமா யவன்கேட்ட அவ்வரங்கள் தாங்கொடுத்து  நீச னிருக்க நெடிய யுகம்வகுத்துப் பாசனுக்குப் பேரு பகர்ந்தே விடைகொடுத்தார் விடைவேண்டிப் பாவி விமலன் தனைத்தொழுது மடைப்பாவி யான மல்லோசி வாகனனும் தில்லைமல் லாலனுமாய்ச் சேர்ந்தங் கிருபேரும் வல்ல சிவன்வகுத்த வையகத்தில் வந்தனராம் வந்தார் சிவன்வகுத்த வையகத்தி லம்மானை அந்த அசுரர் அவரிருக்கு மந்நாளில் உதிர மதுசூரர் ஒக்க உதித்தெழுந்து செதிர்சூரப் படையாய்ச் சேர்த்தங் கிருந்தனராம் இப்படியே சூரர் இவர்சேர்க்கை தன்னுடனே அப்படியே அந்தயுகம் ஆண்டிருந்தா ரம்மானை ஆண்டிருந்த சூரர் அவரிருக்க மேடைகளும் தாண்டிநின்ற வானத் தடாக உயரமதே சூரப் படைகள் தொழுது அடிபணிந்து பாதக ருக்குநித்தம் பணிந்தேவல் செய்திடுவார் ஊழியங்கள் செய்து உற்றயிறை யிறுத்துப் பாளையங் களாகப் பணிந்திருந்தா ரம்மானை சூரர் கொடுமுடியைச் சூட்டி யரசாண்டு பாரமுள்ள கோட்டைப் பண்ணினா ரம்மானை  இப்படியே சூரர் இவர்வாழு மந்நாளில் முப்படியே சூரர் ஊழி விதிப்படியால் இறப்ப தறியாமல் எரியைமிகக் கண்டாவி உறப்பொசிக்கச் சென்ற விட்டி லிறந்தாற்போல் தம்பி தமையன் சந்ததிகள் மந்திரிமார் மும்பிலுள்ள சூரர் முடுக்கமதைக் கண்டாவி ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(24,'நெடிய யுகம்','நம்பிபத மறந்து நாம்தாம் பெரிதெனவே கெம்பினார் சூரர் கெட்டனர்கா ணம்மானை சூர ரவர்செய்த துட்டம் பொறுக்காமல் வீர முள்ளதேவர் விரைந்தே முறையமிட தேவர் முறையம் சிவனார் மிகக்கேட்டுக் காவலாய் நித்தம் கைக்குள் ளிருக்குகின்ற பெண்ணமுதைப் பார்த்துப் புகல்வாரங் கீசுரரும் கண்ணே மணியே கருத்தினுள் ளானவளே பூலோகந் தன்னிலுள்ள புருடரா யுதத்தாலும் மேலோகம் வாழும் விமலரா யுதத்தாலும் மலைமேலே வாழும் மாமுனிவர் தம்மாலும் அலையா வரங்கள் அச்சூரர்க் கேகொடுத்தோம் தரியா முடுக்கம் தான்பொறுக்காத் தேவரெல்லாம் அரியோ யெனமுறையம் அநேகம் பொறுக்கரிதே  என்றீசர் சொல்ல இயல்கன்னி யேதுரைப்பாள் மலைலோகம் மேலோகம் வையமதி லாகாட்டால் அலைமேல் துயிலுமொரு ஆண்டியுண்டு கண்டீரே முன்னேயச் சூரருக்கு முற்சாப மிட்டதொரு வன்னச் சுருதிமுனி மந்திரபுரக் கணையாய் வளர்த்தங் கிருப்பான்காண் மாயருட பக்கலிலே கிளர்ந்த மொழிகேட்டுக் கிருபைகூர்ந் தேயீசர் மாலை வரவழைத்து வளப்பமெல் லாமுரைக்கச் சாலப் பொருளும் சம்மதித்தாங் காரமுடன் அலையில் வளர்ந்த அதிகக் கணையெடுத்துச் சிலையேற்றி யம்பைச் சிரித்து மிகத்தொடுக்க அம்புப் பகையாலும் அதிகமால் பகையாலும் பம்பழித்துச் சூரனூர் பற்பம்போல் தானாக்கிச் சூர ரிருவருட சிரசை மிகஅறுத்து வாரிதனில் விட்டெறிந்து வாளி சுனையாடி மலரோ னடிபணிந்து வைகுண்டங் கேட்டிடவே பலமான குண்டப் பதவி மிகக்கொடுத்தார் அவ்வுகத்தை மாயன் அன்றழித்து ஈசரிடம் செவ்வாக நின்று செப்பினா ரீசருடன் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(25,'கிரேதா யுகம்','இன்னமொரு யுகத்தை இப்போ படைக்கவென்று  மன்னதியத் திருமால் மனமே மிகமகிழ்ந்து சொன்னவுட னீசுரரும் தொன்னூல் மறைதேர்ந்து முன்னே குறோணி முடிந்ததுண்ட மாறதிலே ஓரிரண்டு துண்டம் உகமாய்ப் பிறந்தழிந்து ஈரிரண்டு துண்டம் இருக்குதுகாண் மாயவரே நூல்முறையைப் பார்க்கில் நெடிய யுகங்கழிந்தால் மேலுகந்தா னிங்கே மிகுத்தகிரே தாயுகந்தான் இருக்குதுகா ணென்று ஈச ருரைத்திடவே மருக்கிதழும் வாயான் மனமகிழ்ந்து கொண்டாடி துண்டமொன்றை ரண்டாய்த் தூயவனார் தாம்வகிர்ந்து மண்டலங்கள் மெய்க்க வாணாள் கொடுத்தருளி சிங்கமுகச் சூரனெனும் திறல்சூர பற்பனெனும் வங்கணமாய்ப் பிண்டம் வகுத்தனர்கா ணம்மானை சூர னுடசிரசு தொளாயிரத்து நூறதுவும் போரக்கால் கைகள் பொருப்பெடுக்கு மாபலமும் சூரன் சுரோணிதத்தைச் சுக்கிலங்கள் தானாக்கி ஊரேநீ போவென்று உற்ற விடைகொடுத்தார் விடைவேண்டிச் சூரன் வேண்டும் படையோடே திடமாகப் பூமி செலுத்தியர சாளுகையில் வரம்வேண் டவென்று மலரோ னடிவணங்கி  திறமான ஓமமிட்டுச் செப்புக் குடம்நிறுத்தி நின்ற தவத்தில் நெடியோனைக் காணாமல் அன்றந்தச் சூரன் அக்கினி யில்விழுந்தான் சூரபற்பன் விழவே சிங்கமுகச் சூரனவன் பாரமுள்ள தன்சிரசைப் பறித்தெறிந்தா னக்கினியில் ஆனதா லீசுரரும் அம்மைஉமை யுமிரங்கி ஈனமாஞ் சூரனுக்கு ஏதுவரம் வேணுமென்றார் ஈச னுரைக்க ஏற்றஅந்தச் சூரனுந்தான் பாசமுடன் செத்த பற்பனென்ற சூரனையும் எழுப்பித் தரவேணும் யாங்கள்மிகக் கேட்டவரம் மழுப்பில்லா வண்ணம் வரமருள்வீ ரென்றுரைத்தான் சூர னிவன்கேட்க சிவனா ரகமகிழ்ந்து பாரமுள்ள ஓம பற்பமதைத் தான்பிடித்துச் சிவஞான வேதம் சிந்தித்தா ரப்பொழுது பவமான சூர பற்பன் பிறந்தனனாம் இறந்து பிறந்தனற்கும் இளையோ னவன்தனக்கும் சிறந்த புகழீசர் செப்புவா ரப்பொழுது சூரரே உங்களுக்குத் தோற்றமுள்ள தோர்வரங்கள் வீரரே கேளுமென்று வேத னிவையுரைக்க அந்நாளில் சூரன் அகமகிழ்ந்து கொண்டாடி  உன்னாலு மைந்துமுகம் உள்ளவர்கள் தம்மாலும் உலகமதில் பண்ணிவைத்த உற்றஆயு தத்தாலும் இலகுமன்ன ராலும் இந்திரனார் தம்மாலும் கொல்லத் தொலையாத கொடிய வரமதுவும் வல்லவனே நீயும் வாழுங் கயிலையதும் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(26,'கிரேதா யுகம்','தேவர்தே வேந்திரனும் திருக்கன்னி மாமறையும் ஏவலா யுன்னுடைய லோகமதி லுள்ளவர்கள் முழுது மெனக்கு ஊழியங்கள் செய்திடவும் பழுதில்லா திந்தவரம் பரமனேநீர் தாருமென்றான் தாருமென்று சூரன் தாழ்மை யுடன்கேட்க ஆரு மொப்பில்லா ஆதி யகமகிழ்ந்து கேட்டவர முழுதும் கெட்டியா யுங்களுக்குத் தாட்டிமையா யிப்போ தந்தோ மெனவுரைத்தார் வரங்கொடுத் தீசர் மலைகயிலைக் கேகாமல் பரம உமையாளைப் பையஎடுத் தணைத்து அலைமே லேஆயன் அருகிலே போயிருந்து மலைமே லேசூரன் வாய்த்ததென் றவ்வரங்கள் கயிலை முழுதும் காவலிட்டுத் தேவரையும் அகில முழுதும் அடக்கியர சாண்டனனே அப்படியே சூரன் அரசாண் டிருக்கையிலே  முப்படியே விட்டகுறை முடிவாகும் நாளையிலே தேவரையும் வானவரைத் தெய்வேந் திரன்வரையும் மூவரையும் பாவி முட்டுப் படுத்தினனே ஆனதால் தேவர் அரிக்கே முறையமிட ஈனமில்லா தாயன் எடுத்தா ரொருவேசம் ஈசனிடஞ் சென்று இயம்பினா ரெம்பெருமாள் வாசமுள்ள ஈசுரரே மாபாவிச் சூரனுக்கு ஏது வரங்கள் ஈந்திர்கா ணென்றுரைக்கத் தாது கரமணிந்த தாமன்பின் னேதுரைப்பார் வையகத் திலுள்ள வலுவாயு தத்தாலும் தெய்வலோ கத்தில் சிறந்தமன்னர் தம்மாலும் அஞ்சு முகத்தாலும் அழியா தவனுயிரும் தஞ்சமிட வானோர் தையல்தெய்வக் கன்னிமுதல் கயிலை முழுதும் கமண்டலங்க ளேழுமுதல் அகில முழுதும் அடக்கி வரங்கொடுத்தோம் என்று வேதாவும் இவையுரைக்க மாலோனும் நின்று தியங்கி நெஞ்சமது புண்ணாகிப் பேயனுக் கென்னுடைய பிறப்பைக் கொடுத்தல்லவோ தேயமதில் நானும் திரிந்தலையக் காரணந்தான் என்று திருமால் இதுசதையஞ் சொல்லியவர்  இன்றந்தச் சூரர் இருவர்தமைக் கொல்லவே ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(27,'கந்தன் அவதாரம்','ஆறு முகமாய் ஆய னளவிடவே கூறிடவே சத்திதனைக் கொண்டார்வே லாயுதமாய் நல்ல சிவனாரை நந்தீ சுரராக்கி வல்ல பெலமுள்ள வாய்த்ததிக் கெட்டிலுள்ளப் பாலரையும் வீரர்களாய்ப் பண்ணினா ரெம்பெருமாள் வாலமுள்ள சன்னாசி மாரைப் பெரும்படையாய்க் கந்தனெனும் நாமம் கனத்தசடை யாண்டியுமாய்க் கொந்துகொந்தாய்ப் பீற்றைக் கூறைமிக வணிந்து வேலு மிகப்பிடித்து வெண்ணீறு மேதரித்து நாலுரண்டு சிரசில் நல்லருத்தி ராச்சமிட்டுப் பத்துரண்டு காதில் பசும்பொன்னொவ்வாச் செம்பணிந்து முத்திரிக ளிட்டுக்கந்தப் பொக்கணங்கள் தோளிலிட்டுச் சன்னாசி போலே தானடந் தெம்பெருமான் நன்னாதா னாவெனவே நாலஞ்சுகவி தான்பாடி வந்து ஒருமலைமேல் வாய்த்தகூ டாரமிட்டுச் சந்து மிகச்சொல்லி தான்விட்டார் சூரனுக்கு ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(28,'வீரவாகுதேவர் தாது','சூர னிடத்தில் தூதாநீ சென்றேகிப் பாரமுள்ள கயிலைச் பருவதமுந் தேவருட சிறைக ளகற்றிவானோர் தேவரையும் நீயனுப்பித்  திறவா னாயாகச் சீமையர சாளுமென்று இப்படியே ஆகாட்டால் இன்றுகழித் தெட்டாம்நாள் அப்படியே உன்றனக்கும் ஆனகந்த சுவாமியர்க்கும் சண்டை அன்றென்று தரங்கூறி வாவெனவே அண்டர்பிரான் தூது அனுப்பினா ரம்மானை தூதன் மிகநடந்து சிவனே செயலெனவே காதமொன்று தான்கடந்து கண்டானே சூரனையும் கந்த சுவாமி கருத்தா யுரைத்ததெல்லாம் அந்த அசுரனுக்கு அத்தூதன் தானுரைத்தான் சூரா கேள்கந்த சுவாமியரு ளென்றுரைக்க ஏராத பாவி இகழ்த்தினா னப்போது தூதனென்றோன் போகாமல் துடர்ந்து மிகப்பிடித்துப் பாரதப் பெருவிலங்கில் பாவியைவை யென்றுரைத்தான் ஆரடா நீதான் அறியாயோ என்பலங்கள் பாரடா வுன்றன் கந்தன் படுகிறதை ஈசுரனு மென்றனுக்கு இருந்த இடமுமருளி மாயனிடம் போயலையில் வாழ்ந்ததுநீ கண்டிலையோ எமலோகம் வானம் இந்திரலோ கம்வரையும் நவகோளும் நானல்லவோ நாட்ட மறிந்திலையோ முப்பத்து முக்கோடி உற்றதே வாதிகளும்  நாற்பத்துநாற் கோடிரிஷி நமக்கென்ற றிந்திலையோ அறியாத வாறோகாண் ஆண்டிக்குத் தூதுவந்தாய்ச் சிறியனென் றிராதேயென் சிரசுடம்பு கண்டிலையோ உன்னுடைய கந்தன் உயரமது நானறிவேன் என்னுடைய உயரம் இனிநீ யறிவாயே ஆனதா லென்னுடைய ஆங்கார மத்தனையும் கானகத்தில் வாழும் கந்தனுக் கேவுரைநீ என்று மதமாய் இவன்பேசத் தூதனுந்தான் அன்று அந்தச்சூரனுக்கு அறையாம லேதுரைப்பான் நீயோ தானெங்கள் நிமலன் தனக்கெதிரி பேயோரி நாய்நரிகள் பிய்ச்சிப் பிடுங்கியுன்னை கண்ட விடத்தில் கழுக்கள் மிகப்பிடுங்கிக் கொண்டோடித் தின்னவே லாயுதங் கொண்டுவந்தார் தேவர் சிறையும் தெய்வமட வார்சிறையும் மூவர் சிறையும் மும்முடுக்க முந்தீர்த்து உன்னுடைய சேனை உற்றபடை யழித்து நின்னுடைய கோட்டை நீறு பொடியாக்கி அரசாள்வா ரெங்கள் ஆறுமுக வேலவனார் துரையான கந்த சுவாமிசொல்லி யனுப்பினர்காண் என்றேதான் தூதன் இவையுரைக்கச் சூரனுந்தான்  அன்றே மனது அளறித்துணிந் தேதுரைப்பான் ஆனா லறிவோம் ஆண்டி தனையுமிங்கே போனா லென்னோடே போருசெய்ய யேவிடுநீ சூர னிவையுரைக்கச் சூலாயுதப் பெருமாள் தூதன் மிகநடந்து சொன்னான் சுவாமியர்க்கு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(29,'சூரன்பாடு','சுவாமி மனமகிழ்ந்து சூரன் தனையறுக்கக் காமிவே லாயுதத்தைக் கையிலெடுத்தா ரம்மானை வேலா யுதமெடுத்து வேதப் படைசூழ சூலாயுதப் பெருமாள் துடியாய் நடக்கலுற்றார் கந்தனார் வேசம் கரந்திருந்த மாயவனார் வந்தார்காண் சூரன் வலுவிழந்தா னம்மானை சூர னவன்கண்டு தோசப் படையணிந்து மூரன் படைக்கு முன்னே நடக்கலுற்றான் கண்டா ரீராறு கரத்தோ னகமகிழ்ந்து பண்டார வேசம்பண்பா யெடுத்திறுக்கி முன்னே வருஞ்சூரன் முகத்தை யவர்பார்த்துப் பின்னே சுவாமி புத்தி மிகவுரைப்பார் வம்பி லிறவாதே வாழ்விழந்து போகாதே தம்பி தலைவன் தளமு மிழவாதே பற்பக் கிரீடப் பவுசு மிழவாதே   அற்ப மிந்தவாழ்வு அநியாயம் விட்டுவிடு கரணமீ தில்லாமல் கௌவையற்று வாழ்ந்திருந்து மரணம் வந்துசீவன் மாண்டுபோ கும்போது நன்மை யதுகூட நாடுமே யல்லாது தின்மை வராது தேவரையும் விட்டுவிடு தீட்சை யுடன்புத்தி செவ்வேநே ரிட்டுவொரு மோட்ச மதுதேட முடுக்கமதை விட்டுவிடு இத்தனையும் நாதன் எடுத்து மிகவுரைக்கப் புத்திகெட்டப் பாவி போர்சூர னேதுரைப்பான் இரந்து திரியுகின்ற இரப்பனுக் குள்ளபுத்திப் பரந்த புவியாளும் பாரமுடிக் காவலற்கு ஏற்குமோ ஞானம் இரப்போருக் கல்லாது ஆர்க்குமே சொல்லாதே ஆண்டிவுன் ஞாயமதை சண்டைக்கு வாவெனவே தரங்கூறித் தூதுவிட்டப் பண்டார மென்ற படைக்கார னும்நீயோ என்னுடைய சேனை எல்லாமிக அழித்து என்னையும் நாய்நரிக்கு இடுவேனென் றதும்நீயோ என்றே யச்சூரன் இயம்பி மிகநகைத்துப் பண்டார னோடே படையெடுத்தா னம்மானை சூரனுட படைகள் துண்டந்துண்ட மாய்விழவே  வீரர்களும் வந்து வெட்டினா ரம்மானை வெட்டிதினால் செத்தார் மிகுசூ ரக்குலங்கள் பட்டார்க ளென்று பார்சூரன் தான்கேட்டு வந்து எதிர்த்தான்காண் மாயாண்டி தன்னோடே இன்றுவந்து வாய்த்துதென்று எம்பெருமா ளுமகிழ்ந்து வேலா யுதத்தை விறுமா பதஞ்சேவித்து ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(30,'சூரன்பாடு','மேலாம் பரனார் விமல னருளாலே எறிந்தார்காண் சூரன் இறந்தானே மண்மீதில் பறிந்தேவே லாயுதமும் பாற்கடலில் மூழ்கியதே சூரன் மடிந்து துடித்துயிர் போகுகையில் வீரமுள்ள நாதன் வீணனவன் முன்பில்வந்து சொன்ன மொழியெல்லாம் சூட்சமாய்க் கேளாமல் இந்நிலமேல் பாவி இறந்தாயே வம்பாலே நாட்டமுடன் நானுரைத்த நல்லமொழி கேளாமல் கோட்டையு முன்னுடைய குஞ்சரமுந் தோற்றாயே சந்துவிட்ட சொற்படிக்குத் தந்தரசு ஆளாமல் விந்துக் குலங்களற்று வீணாய்நீ மாண்டாயே மாளா வரங்கள் மாகோடி பெற்றோமென்று பாழாக மாண்டாயே பண்டாரங் கையாலே என்றந்த ஆதி இத்தனையுந் தான்கூற  முந்து பிறந்த முப்பிறப்புச் சூரமதால் என்னையோ கொல்ல இரப்பனோ ஏலுவது உன்னையோ கொல்ல ஒட்டுவனோ நான்துணிந்தால் வேலா யுதத்தாலே வென்றுகொன்ற தல்லாது ஏலாது வுன்னாலே இளப்பமிங்கே பேசாதே என்றானே சூரன் எம்பெருமாள் கோபமுடன் கொன்றாரே சூரனுட குறவுயிரை யம்மானை சூரனைத் துணித்த சத்தி சூலமும் கடலில் மூழ்கி வீரமால் பதத்தைப் போற்றி விளம்புவாள் சத்தி மாது மூரனைச் செயிக்க முன்னே முச்சூலமாய்ச் சபித்த சாபம் தீரவே வேணு மென்று திருப்பதம் வணங்கி நின்றாள் ஆதியே நாதி அனாதித் திருவுளமே சோதியே யென்னுடைய சூலசா பந்தீரும் என்று உமையாள் எடுத்து மிகவுரைக்க நன்றெனவே அந்த நாரா யணர்மகிழ்ந்து சாப மதுதீரச் சாந்தி மிகவளர்த்தார் தாபமுடன் மாயன் சாந்தி மிகவளர்க்க அம்மை உமையாளின் ஆனசாபந் தீர்ந்து செம்மையுடன் கயிலை சென்றனள்கா ணம்மானை ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(31,'இரணியன் பாடு','சத்திசா பந்தீர்த்துத் தவலோக மேயனுப்பித்  தத்தியாய்ச் சூரனையும் சங்காரஞ் செய்துஅந்த சூரனூர் தன்னைத் தீயோன் தனக்களித்து வீரசூ ரன்தனையும் மேலுமந் தவ்வுகத்தில் பார இரணியனாய்ப் படைத்தார்கா ணம்மானை சூர னிரணியனாய்த் தோன்றினா னவ்வுகத்தில் மாய னொருகோலம் மகவா யுருவெடுத்து வாயல் நடையில்வைத்து மாபாவிச் சூரனையும் நெஞ்சை யவர்நகத்தால் நேரேப் பிளந்துவைத்து வஞ்சக னோடே மாயன் மிகவுரைத்தார் சூர பற்பனாகத் தோன்றினா யந்நாளில் ஊரிரப்ப னாக உருவாக நான்தோன்றிக் கொன்னே னானென்று கூறினே னப்போது அந்நேரம் நீதான் ஆண்டியல்லக் கொன்னதென்றாய் வேலா யுதத்தாலே வென்றாய்நீ யல்லாது ஏலாது உன்னாலே என்றன்று பேசினையே ஆயுதங்க ளம்பு அஸ்திரம்வா ளில்லாமல் வாயிதமா யென்னகத்தால் வகிர்ந்தேனா னுன்வயிற்றை என்றுமா யனுரைக்க ஏதுரைப் பான்சூரன் பத்து மலையைப் பாரநக மாய்ப்பதித்து இத்தலத்தி லென்னை இறக்கவைத்தா யல்லாது  ஏலாது உன்னாலே இந்தமொழி பேசாதே மாலா னவேதன் மனதுகோ பம்வெகுண்டு உன்னை யின்னமிந்த உலகில் பிறவிசெய்து கொன்னா லேவிடுவேன் கிரேதா யுகம்வயது திகைந்தல்லோ போச்சு திரேதா யுகம்பிறந்தால் பகையுந் தானப்போ பார்மீதி லுண்டாகும் முப்பிறவித் துண்டம் உயிர்ப்பிறவி செய்கையிலே இப்பிறவி தன்னில் இசைந்தமொழி கேட்பேனான் என்று இரணியனை இரணசங் காரமிட்டு அன்று கிரேதா யுகமழித்தா ரம்மானை அந்தக் கிரேதா யுகமழித்த அந்நாளில் கந்தத் திருவேசம் கலந்திருந்த மாயவனார் செந்தூர்ப் பதியில் சேர்ந்திருந்தா ரம்மானை அந்தூர்ப் பதியில் அலங்கரித்த நாட்கழித்து ஆதி கயிலை அரனா ரிடத்தில்வந்து வேதியரும் நன்றாய் விளம்புவா ரம்மானை சூரபற்ப னென்ற சூரக்குலங் களைத்துணித்து வீரம் பறைந்ததினால் மேலுமந்த அவ்வுகத்தில் இரணியனாய்த் தோன்றி நின்றஇ ராச்சதனைக் கொன்னு கிரேதா குவலயமும் நானழித்து முன்னு கரந்திருந்த ஆறு முகவேசம் செந்தூரு வாரி திரைமடக்கில் வாழ்ந்திரென்று வந்தோங் காணய்யா மலர்ப்பாதந் தெண்டனிட என்றேதா னாதி ஈசுரரோ டேதுரைப்பார் அன்றேதா னீசர் அருளுவா ராயருக்கு');");


