        db.execSQL("INSERT INTO akilathirattu2 VALUES(32,'திரேதா யுகம் - இராவணன் பாடு','முன்னே குறோணி முடிந்ததுண் டாறதிலே இன்னமூணு துண்டம் இருக்குதுகாண் மாயவரே என்றீசர் சொல்ல எல்லோருந் தான்கூடி அன்றிருந்த துண்டம் அதிலேயொரு துண்டமதை உருவாய்ப் படைத்து உயிர்க்கொடுக்கு மவ்வளவில் அருகே யிருந்த அச்சுதரு மேதுரைப்பார் முன்னே இவனும் முற்பிறப் பானதிலே என்னோடே பேசி எதிர்த்தான் காணீசுரரே  ஆனதா லிப்பிறப்பு அரக்க னிவன்றனக்கு ஈனமில்லாச் சிரசு ஈரஞ் சாய்ப்படையும் பத்துச் சிரசும் பத்துரண்டு கண்காதும் தத்துவங் களோடே தான்படையு மென்றுரைத்தார் மாய னுரைக்க மறையோ னகமகிழ்ந்து வாய்சிரசு பத்தாய் வகுத்தார்கா ணம்மானை கூடப் பிறக்கக் குண்டிலிட்ட ரத்தமதை வாட அரக்கர் குலமாய்ப் பிறவிசெய்தார் அப்போ படைக்கும் அவ்வளவில் மாயவனார் மெய்ப்பான மேனிதனில் வியர்வை தனையுருட்டி விபீஷண னென்று மெய்யுருவந் தானாக்கிக் கவுசலமா யரக்கன் தம்பியெனக் கருத்தாய்க் கூடி யிருந்து அரக்கன் குறியறிந்து வேடிக்கை யாக வீற்றிரென்று தானுரைத்தார் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(33,'இராவணன் வரம் வேண்டல்','பத்துத் தலையான பாவி அரக்கனவன் மற்று நிகரொவ்வா வாய்த்தபர மேசுரரை வணங்கி வரம்வேண்ட மனதிற் பிரியமுற்று இணங்கியே ஈசர்பதம் இறைஞ்சிநின்றா னம்மானை தவத்தருமை கண்டு தலைபத் தரக்கனுக்குச் சிவத்தலைவ ரானோர் செப்புவா ரம்மானை  காதுரண்டு பத்து கண்ணிருப துள்ளோனே ஏது வரம்வேணும் இப்போது சொல்லுமென்றார் அப்போது ஈசுரரை அரக்க னவன்வணங்கி இப்போது ஈசுரரே யான்கேட்கு மவ்வரங்கள் தருவோ மெனவே தந்தி முகன்பேரில் உருவா யெனக்கு உறுதிசெப்பு மென்றுரைத்தான் அப்படியே ஈசர் ஆணையிட்டுத் தான்கொடுக்க சொற்படிகேட் டேயரக்கன் சொல்லுவா னப்போது மூன்றுலோ கத்திலுள்ள முனிவர்தே வாதிகளும் வேண்டும்பல ஆயுதமும் விதம்விதமா யம்புகளும் வீரியமாய் நானிருக்கும் விண்தோயுங் கோட்டைசுற்றிச் சூரியனுஞ் சந்திரனும் சுற்றியது போய்விடவும் சிவனா ரொருகோடி செய்ய வரந்தரவும் புவனம் படைக்கும் பிரம்மா வொருகோடி உமையா ளொருகோடி உற்ற வரந்தரவும் இமையோ ரரைக்கோடி இப்படியே உள்ளவரம் மூணரைக் கோடி உள்ளவர மத்தனையும் கொடுக்கு மளவில் குன்றெடுத்தோன் தன்தேவி  அலைமேல் துயிலும் அச்சுதனார் தன்தேவி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(34,'இராவணன் வரம் வேண்டல்','சிலைவே லெடுத்துச் செயிக்கவல்லான் தன்தேவி அலங்கா ரமாகி அன்னைப் பொழுததிலே கலங்கா தான்தேவி கயிலைஉமை யாளருகே மணிமேடை தன்னில் மகிழ்ந்திருக்கு மவ்வளவில் கெணியா மலேயரக்கன் கெடுவ தறியாமல் அம்மை தனைக்கண்டு அயர்ந்து முகஞ்சோர்ந்து கர்ம விதியால் கைமறந்து நின்றனனே நின்ற உணர்வை நெடிய சிவமறிந்து அன்றந்த அரக்கனுக்கு ஆதிவரங் கொடுத்து கேட்ட வரங்கள் கெட்டியாய்த் தான்கொடுத்து நாட்ட முடன்சீதை நகையா லழிவையென்று இரக்கமில் லான்கேட்ட ஏற்றவரங்க ளெல்லாம் அரக்க னவன்றனக்கு ஆதிமிகக் கொடுத்தார் ஆதிகொடுத்த அவ்வரங்க ளத்தனையும் நீதியில் லான்வேண்டி நெடியோன் தனைவணங்கிக் கொண்டுபோ கும்போது குன்றெடுத்த மாயவனார் கண்டு மறித்துக் கௌசலமிட் டேமாயன் அரைக்கோடி வரமாய் ஆக்கிவிட்டார் மாயவரும் சரக்கோடி மூணும் தான்தோற்றுப் போயினனே  ஆதியைத் தொழுது போற்றி அரக்கனும் வரமும் வேண்டி சீரிய சீதை யாலே சீவனுக் கிடறும் பெற்று வாரிய மூணு கோடி வரமது தோற்றுப் பின்னும் மூரியன் மூணு லோகம் முழுதுமே யடக்கி யாண்டான்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(35,'இராவணன் கொடுமை','முக்கோடி வரத்தை உச்சிக்கொண்டார் மாயவரும் அக்கரைக் கோடி அரக்கன்வரம் கொண்டேகித் தேவரையும் மூவரையும் தேவேந் திரன்வரையும் நால்வரையும் வேலைகொண்டு நாடாண்டா னம்மானை வாசு அரக்கன் மணிமேடை தூராட்டால் வீசுவேன் வாளாலே வெட்டுவேன் பாருவென்பான் வருண னவன்மேடை வந்துத்தொளி யாதிருந்தால் மரணம்வரும் வரையும் வலுவிலங்கில் வைப்பேனென்பான் சந்திரருஞ் சூரியரும் சாய்ந்துமிகப் போகாட்டால் சந்து சந்தாகச் சரத்தாலறுப் பேனென்பான் தெய்வ மடவார்கள் திருக்கவரி வீசாட்டால் கைவரைந்து கட்டிக் கடுவிலங்கில் வைப்பேனென்பான் நாலு மறையும் நல்லாறு சாஸ்திரமும் பாலு குடஞ்சுமந்து பணிந்துமுன்னே நில்லாட்டால் அஸ்திரத்தால் வாந்து அம்மிதனில் வைத்தரைத்து நெற்றிதனில் பொட்டிடுவேன் நிச்சயமென் பானரக்கன்         வானவர்க ளெல்லாம் மலரெடுத்து என்காலில் தானமது பண்ணித் தாழ்ந்துநில்லா தேயிருந்தால் சாகும் வரைக்கும் தடியிரும்பி லிட்டவரை வேகும் படிக்கு வேள்வியதி லிட்டிடுவேன் மாமறலி மூவர் வந்தென்சொல் கேளாட்டால் காமனையும் ஈசன் கண்ணா லெரித்ததுபோல் என்னுடைய கண்ணால் இயமனையுங் காலனையும் துன்னுடைய வல்லத் தூதனையும் நானெரிப்பேன் இவ்வுகத்தி லுள்ள இராசாதி ராசரெல்லாம் முவ்வுகமுங் கப்பமிங்கே முன்னாடி தாராட்டால் நெருப்பெடுத் திட்டிடுவேன் நேரேகொண்டு வாராட்டால் எரிப்பே னொருஅம்பால் இராசாதி ராசரையும் இப்படியே பாவி இந்திரலோ கம்வரையும் அப்படியே அரக்கன் அடக்கியர சாண்டிருந்தான்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(36,'தேவர்கள் முறையம்','அப்படியே அரக்கன் ஆண்டிருக்கு மந்நாளில் முப்படியே விட்டகுறை முடிவாகும் நாளையிலே தேவாதி தேவர் தினமேவல் செய்திடவே மூவாதி மூவர் ஊழியங்கள் செய்திடவே முறுக்கம தால்பாவி ஊழியங்கள் கொண்டதினால் பொறுக்க முகியாமல் பூலோகத் தார்களெல்லாம்  தெய்வ ஸ்திரீயும் தேவாதி தேவர்களும் அய்யா திருமாலுக்(கு) அபயம் முறையமென அபயமிடு மொலியை அச்சுதருந் தானமர்த்திக் கபயமிடும் வேதன் கயிலையது தானேகி ஆதி பரமன் அடியைமிகப் போற்றி சோதித் திருமால் சொல்லுவா ரம்மானை பத்துத் தலையுடைய பாவி யரக்கனுக்கு மற்றும் பலகோடி வரங்கள்மிக ஈந்ததினால் தேவரையும் மூவரையும் தேவேந்தி ரன்வரையும் நால்வரையும் வேலைகொண்டு நாடாண்டா னம்மானை ஆனதால் தேவர் அரிக்கே முறையமிட ஈனமாய்க் கேட்டு இருக்க முடியுதில்லை பள்ளி யுறக்கம் பரிவாய் வருகுதில்லை தள்ளினால் தேவரையும் தற்காப்பா ராருமில்லை என்ன வசமாய் எடுப்போஞ் சொரூபமது தன்னிகரில் லாதவனே சாற்றுவீ ரென்றனராம் மாய னுரைக்க மறையோ னகமகிழ்ந்து தூயவரு மங்கே சொல்லுவா ரம்மானை பாவி யரக்கனுக்குப் பண்டுநா மீந்தவரம் தாவிப் பறிக்கத் தானாகா தென்னாலே ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(37,'இராமாவதாரம்','என்றரனார் சொல்ல எம்பெருமா ளச்சுதரும் அன்றெம் பெருமாள் ஆலோ சனையாகி என்னை மகவாய் எடுக்கத் தசரதரும் முன்னே வரங்கேட்டு உலகி லவரிருக்க அன்னுகத்தி லுள்ள அரசன் தினகரனும் பொன்னு திருவைப் பிள்ளையென வந்தெடுக்க நெட்டையா யரசர் நெடுநாள் தவசிருக்க சட்டமதைப் பார்த்துத் தானனுப்பு மீசுரரே பின்னும் பெருமாள் பெரியோனைப் பார்த்துரைப்பார் முன்னு முறையாய் முறைப்படியே தேவரையும் வானரமாய்ப் பூமியிலே வந்து பிறந்திருக்கத் தானவரே யிப்போ தான்படைக்க வேணுமென்றார் ஏவலா யென்றனுக்கு இப்பிறப் பானதிலே காவலா யென்றனுக்குக் கைக்குள்ளே நிற்பதற்குப் பள்ளிகொண்டு நானிருந்த பாம்புரா சன்தனையும் வெள்ளிமணி மெத்தையையும் வீற்றிருக்கு மாசனமும் இம்மூணு வேரும் என்னோ டுடன்பிறக்கச் சம்மூலப் பொருளே தான்படையு மென்றுரைத்தார் மாய னுரைக்க மறையோ னகமகிழ்ந்து தூயவரு மந்தப் படியே தெளிந்திருக்கப் படைக்கும் பொழுதில் பரதேவ ரெல்லோரும் அடைக்கலமே மாயன் அடியெனத் தெண்டனிட்டார் தெண்டனிட்டுத் தேவரெல்லாம் செப்புவா ரம்மானை மண்டல மெங்கும் மயமாய் நிறைந்தோனே எங்களை லோகமதில் இப்போ படைப்பீரால் மங்களமா யுள்ள வலுவும் பெலமதுவும் ஆயனுக்கு நாங்கள் அடிபணிந் தேவல்செய்ய நேயனே நீரும் நெறியாய்ப் படையுமென்றார் ஈசர் மகிழ்ந்து இப்படியே தான்படைக்க வாசத் தசரதரும் வந்து தினகரரும் மகவாசை யுற்று மகாபரனைத் தானோக்கி அகப்பாச மற்று அதிகத் தவமிருந்து நாத னறிந்து நன்மறையைத் தான்பார்த்து பாத னரக்கன் பத்துச் சிரத்தானைத்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(38,'இராமாவதாரம்','தேவர் துயரமறத் திருமால் தசரதற்கு மூவ ருடன்கூடி உலகமதி லேபிறக்க அரக்கர் குலமறவே அம்மைசீதா லட்சுமியை இரக்கம்போ லுள்ள ஏற்ற தினகரற்கு மகவாய்ப் பிறக்க மறைதான் விதித்தபடி செகமீதில் ஞான சிருஷ்டி சிவமயமாய்  இப்படியே வேதத்(து) எழுத்தின் படியாலே அப்படியே ஈசர் அமைக்கத் துணிந்தனராம் உடனே தசரதற்கு உற்றதிரு மாலைத் திடமாய்ப் பிறவி செய்ய எனத்துணிந்தார் அப்போது ராமர் அய்யா திருமாலை இப்போ துபடைக்க ஈசுரனார் சம்மதிக்கப் படைக்கும் பொழுது பரமசிவ னாரை நடக்குந் திருமால் நாதனைப்பார்த் தேதுரைப்பார் அவ்வுலகி லென்னை அதிகத் தசரதற்கு இவ்வுலகு விட்டங்(கு) என்னைப் பிறவிசெய்ய வந்த விபரம் வகைவகையா யீசுரரே என்றனக்குத் தானமைத்து என்னை யனுப்புமென்றார் மாய னிதுகேட்க மறையோ னகமகிழ்ந்து தூயவரு மங்கே சொல்லுகிறா ரம்மானை பரலோ கமென்ற பைம்பொன் கயிலையதில் சாலோ கந்தன்னில் தானிருந்த மாமுனிவர் துசுவீசு மாமுனியும் சுகசீல மாமுனியும் விசுவாச மாயிருந்து விசாரம திட்டனராம் வாருந் துசுவீசு மாமுனியே நீர்கேளும் நீரும் நாமுங்கூடி நீணிலத்தில் போயிருந்து  நானுமொரு பெண்மதலை நல்ல மகவாகத் தானுமோ ராண்மதலை தலைவன் தனைவாங்கிச் சம்மந் தமாகித் தானிருந்து நாடாண்டு உம்மந் தமான உதவிபெற வேணுமென்று இருபேரு மிருந்து எனைநினைந்து மாமுனிவர் உருவேற்றி வேள்வி ஓம மதுவளர்க்க இந்தப் படியே இவர்களிரு மாமுனியும் எந்தன் தனைநோக்கி இருந்தார் தவசுகண்டீர்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(39,'குறு முனி சாபம்','அல்லாமற் பின்னும் அரக்கன்ரா வணன்தனக்குக் கொல்லாமற் கொல்லவொரு குறுமுனிவன் சாபமுண்டு அரக்கன் வரம்வேண்டி அவன்போகு மவ்வளவில் இரக்கமாய் மாமுனியும் இருந்தான் தவசதிலே தவசுநிலை பாராமல் தலைபத் தரக்கனுந்தான் பவிசு மதமாய்ப் படுவ தறியாமல் முனியையவன் காலால் ஒத்தினான் மாபாவி அநியாயப் பாவி அரக்க னவன்றனக்குக் கூறினான் மாமுனியும் கொள்ளைகொண்டச் சாபமது தூறின சாபத் துல்லியத்தை நீர்கேளும் மாலை மிகப்போற்றி வாய்த்ததவம் நிற்கையிலே காலெடுத்து ஒத்தினையே கள்ளாவுன் றனக்கு  இந்தத் தவசுதனில் ஈசுரரைத் தானினைந்து உந்தன் தனையறுக்க ஒருராம பாணமது வந்து பிறந்திடவும் மாய னதைத்தானெடுத்து உந்தனுட மார்பில் ஊடுரு வவிடவும் துசுவீசு மாமுனிவன் தசரதராய்ப் பூமிதனில் பிறந்து இருந்திடவும் பின்னுஞ் சுகசீல மாமுனியும் தினகரராய் வந்து பிறந்திடவும் ஓமுனிக்கு லட்சமியும் வில்லோ டுதித்திடவும் இராமபா ணத்தோடே இராமர் தசரதற்கு முராகமத் தின்படியே உலகி லுதித்திடவும் பாணமது தன்னாலும் பத்தினிதன் கற்பாலும் நாணங்கெட் டரக்காவுன் நல்லதலைப் பத்திழந்து சேனைத் தளமிழந்து சிரசிழந்து வாழ்விழந்து வானரங்கள் வந்துன் வையகத்தைச் சுட்டழித்து உன்சடல மெல்லாம் உழுத்துப் புழுப்புழுத்துத் தன்சடலப் பட்டுச் சண்டாளா நீ மடிவாய் வேண்டுவேன் தவசு விமலன் தனைநோக்கி ஆண்டுபன்னி ரண்டாய் அவன்றவசு நின்றனனே நின்ற தவத்தின் நிலைமையறிந் துமையாள் அன்றைக்கு வந்து அருளினா ளாயிழையும்  உரைத்த மொழிகேட்டு உற்ற முனிதனக்குத் துரைத்தனமா யித்தனையும் சொல்லி விடைகொடுத்தேன் இப்படியே மாமுனிக்கு ஈந்திருக்கு மிவ்வரங்கள் அப்படியே துசுவீசு மாமுனியுஞ் சுகசீல');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(40,'குறு முனி சாபம்','மாமுனியும் நம்மை வருந்திநிஷ்டை செய்யுகிறார் ஓமுனிக்கு நல்ல ஒழுங்குசெய்ய வேணுமல்லோ அல்லாமல் தேவாதி அபயம் பொறுக்கரிது எல்லா மிதுகண்டு இப்பிறவி செய்யுமென்றார் நல்லதுகா ணென்று நல்லதிரு லட்சுமியை வல்லமுள்ள பெட்டகத்தில் வைத்தார்கா ணீசுரரும் இராமபா ணமதையும் நன்றாய்ப் பிறவிசெய்ய சீராமஸ்ரீ யம்பகனும் சிவனு மகமகிழ்ந்து என்ன விதமாய் இதுபிறவி செய்வோமென்று பொன்னம் பலத்தோர் புத்திநொந்து தாமிருந்தார் ஈசுரரு மப்போ இரத்தின கிரிதனிலே வீசு பரனும் வேள்வி யதுவளர்க்க வேள்வி வளர்த்து விமல னுருவேற்றத் தாழ்வில்லா ஆயன் தற்சொரூபந் தானாகித் திருக்கணைக் காலில் செய்ய நரம்புருவி கருக்கணமாய் ராம பாணக் கணையெனவே உறுதிகொண்டு வேள்விதனில் விட்டெறிந்தா ரம்மானை பிறிதிகொண்டு லட்சுமியும் பிலத்த திருக்கரத்தில் தூண்டு விரலில் துய்ய நரம்புருவி வேண்டும் பெரிய வீரவில் லீதெனவே ஆராரு மிந்தவில்லை அம்பேற்றக் கூடாமல் சீரா மரேற்ற சிந்தித்தா ளம்மானை உடனேயது வில்லாய் ஓம மதில்பிறக்கத் திடமாக ராமர் திருக்கணைக்கா லுள்நரம்பு பாணமதாய் வேள்விதனில் பரிவாயப் பிறந்திடவே தாணரும் வானோரும் சங்கத்தோ ருங்காண எல்லோருங் கண்டு இதுகண் மாயமெனவே வல்லோர்க ளான வாய்த்ததே வாதியெல்லாம் சங்கடங்கள் தீர்ந்ததென்று சந்தோசங் கொண்டாட அங்கணங்க ளான அலகைமிகக் கூத்தாட இராமபா ணத்தாலே இராவண சூரனையும் ஸ்ரீராமர் சென்று தென்னிலங்கை தன்னிலுள்ள அரக்கர்குல மறுப்பார் அச்சுதனா ரென்றுசொல்லி இரக்கமுள்ள தேவரெல்லாம் இரங்கிமிகக் கொண்டாட வில்லோ டுடன்பிறந்த வீரலட்சு மியெனவே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(41,'குறு முனி சாபம்','வல்லோர்க ளாராலும் வந்திந்த வில்லதையும்  வளைத்தோர்க்கு நல்ல மாலைசூட்ட லாமெனவே தழைத்த சுகசீல தாட்டீக மாமுனியைத் தினகரராய்ப் பூமிதனில் செய்துவைத்து லட்சமியை மனோகரமாய்ப் பூமியிலே வைத்தபெட்ட கத்தோடே வில்லோ டேபிறக்க விமல னருளினராம் நல்ல துசுவீசு மாமுனியை நாடதிலே தசரதராய்த் தோன்றவைத்துத் தேசாதி ராசனுக்கு விசமாலை ராமருமாய் வீரக் கணையோடே கூடப் பிறக்கக் குன்றெடுத்தார் தான்துயிலும் நீட அரவணையை நீதலட்சு மணராக்கித் தலையணை மெத்தையையும் சத்துரு பரதனுமாய் நிலைவரமாய் ஈசன் நெறியாய்ப் பிறவிசெய்யத் தேவரையும் வானரமாய்ச் சிவனார் பிறவிசெய்ய மூவரை யும்பிறவி உள்ளதெல்லாஞ் செய்திடவே கொடுமுடியாய்ப் பிறந்த கோளிலங்கைப் பாவிகளை முடியடிவே ரில்லாமல் முழுது மறுப்பதற்கு வேண்டும் பிறவியெல்லாம் விமல னருளிமிகத் தாண்டவ சங்காரம் தானிதென்றா ரம்மானை இராவணன் தன்னைக் கொல்ல இராம பாணங்க ளோடே சிராம ராய்மாயன் தானும் தசரதன் தனக்குத் தோன்ற         விராகன மாது சீதை வில்லுட னுதிக்கத் தேவர் மராம ரக்குலங் களாகி வந்தனர் புவியின் மீதே சிராமருந்தான் ராவணனைச் செயிக்க ஒருவிதமாய் இராமபா ணத்தோடே நாட்டில் பிறந்தனராம் தென்னிலங்கை தான்முடிய சீதை சிறையிருக்கப் பொன்னரிய வில்லோ(டு) உடன்பிறந்தாள் பொன்மாது தேவரெல்லாம் வானரமாய்த் தென்னிலங்கை சுட்டழிக்கப் பூவர் சுகுமுனிவர் போர்விசுவ கன்மனெனும் வானரத்துக் கேற்ற மந்திரி தானாகித் தானவரையு மனுப்பித் தரணிதனி லெம்பெருமாள் லட்சும ணரெனவே ஏற்றசத்து ருபரதன் கட்சியுடன் மாயன் கணையோ டுடன்பிறந்தார் இப்படியே ராவணற்கு எல்லோ ரும்பகையாய் முப்படியே உள்ள முறைநூற் படியாலே வந்து பிறந்தார்காண் மாயவரு மம்மானை இப்படியே ராமர் ஏற்ற தசரதற்கு அப்படியே பிறந்து அங்கிருந்தா ரம்மானை');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(42,'சீதா கல்யாணம்','சீதை வளர திருவில்லுந் தான்வளர கோதைக் குழல்சீதைக் கோமான் மிகவளர சிராமர் பிறப்போர் சிறப்பாய் வளர்ந்திடவே  இராமர் குலங்கள் இரகசிய மாய்வளர அரக்கன் கொடுமை அண்டமள வேவளர சீதை வளர்ந்து சிறந்த வயதானதிலே மாதை மணமிடவே மாதா மனதிலுற்றுத் தன்புரு சனோடே தையல்நின் றேதுசொல்வாள் அம்பும்வில் லும்வளர ஆயிழையுந் தான்வளர்ந்து பக்குவங்க ளாச்சே பைங்கிளிக்கு மாலையிட ஒக்குவ தென்ன உரைப்பீரென் னுத்தமரே என்று மடமாது ஏற்ற தினகரரை நின்று வணங்கி நேரிழையுஞ் சொல்கையிலே வில்லை வளைத்தல்லவோ மெல்லிமணஞ் சூடுவது வல்ல கலைக்கோட்டு மாமுனியைத் தான்வருத்தி இன்னபடி யீதென்று எடுத்துரைக்க மாமுனியும் அன்னப் பொழுதில் அருளுவான் மாமுனியும் பூரா சமான புவியைம்பத் தாறிலுள்ள இராசாதி ராசரெல்லாம் இப்போ வரவழைத்து வில்லை வளைத்தவர்க்கு மெல்லிமணஞ் சூட்டுமென்று சொல்லியே மாமுனியும் தசரதனார் கண்மணியைக் கண்டுநின்று மாமுனியும் கண்ணனார்க் கேதுரைப்பான் பண்டு உனக்குப் பரம சிவனாரும்            வில்வளைத்து மாலையிட விதியில் விதித்திருக்குச் செல்லந்த மன்னன் தினகரானர் தன்மகட்கு இன்று கலியாணம் இப்போது அங்குசென்றால் பண்டு அமைத்த பலனுனக்குக் கிட்டுமிப்போ என்று கலைக்கோட்டு மாமுனியுந் தானேகி சென்றான் தினகரரின் செல்வி மணந்தனிலே அன்றைம் பத்தாறு அரசருக்கு மாளனுப்பி தேசாதி தேசர் திசைவென்ற மன்னரெல்லாம் மேசாதி யானோரும் மேவுந்தெய் வேந்திரனும் இராவண சூரன் இராமர்முத லானவரும் இராமர்குல ராசாதி நல்லமன்னர் வந்தனராம் வில்லை வளைத்து வில்லில் நாண்பூட்டாமல் முல்லைமன்ன ரெல்லாம் முகம்வாடிப் போயிருந்தார் இராம ரெடுத்து இராம சரமேற்றி சிராமர் மணஞ்செய்தார் சீதைத் திருமாதை மணம் முகித்துவானோர் மங்களகீ தத்தோடே துணைவர் தலைவரொடு சென்றா ரயோத்தியிலே அயோத்தியா புரியில் ஆனதம்பி மாரோடும் கையேற்று வந்த கன்னி திருவோடும் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(43,'ஸ்ரீராமர் வனவாசம்','வாழ்ந்திருக்கும் நாளில் மாதாகைகேசியம்மை  தாழ்ந்தமொழி சொன்னதினால் தம்பி பரதனையும் நாடாள வைத்து நல்லஸ்ரீ ராமருந்தான் கூடவொரு தம்பியோடும் குழல்சீதை மாதோடும் நாடி நடந்தார் நல்லவன வாசமதில் வாடிவந்து கானகத்தில் வாழ்ந்திருக்கும் நாளையிலே லட்சுமண ருமங்கே ஏற்றகனி தான்பறிக்கக் கட்சியுடன் நடந்து காட்டில் மிகப்போகச் சூர்ப்பநகை தன்மகனும் துய்ய தவசுநிற்க ஆர்ப்பரவா யீசர் ஆகாசத் தேவழியே வாளை அயச்சார் வாய்த்தபர மேசுரரும் தாழ வரும்போது தாமனந்த லட்சுமணர் கண்டந்த வாளைக் கைநீட்டித் தான்பிடித்துத் துண்டம் விழஆலைத் துஞ்சிவிழ வெட்டினர்காண் பட்டந்த ஆலும் படபடெனச் சாய்ந்திடவே வெட்டந்து வீழ்ந்தான் மீண்டுதிரம் பாய்ந்ததுவே அரக்கன்தாய் கண்டு அலறி வெகுண்டெழுந்து இரக்கமில்லாச் சூர்ப்பநகை இலட்சுமண ரைத்தேடி தேடி ஸ்ரீராமர் சீர்பாதங் கண்டணுகி நாடியெனைத் தாவுமென்று நாணமில்லா தேயுரைத்தாள் என்பாரி யிங்கே இருக்கவே வேறொருவர்  தன்பாரி தன்னைத் தான்விரும்பேன் போடியென்றார் போடிநீ யென்ற புத்திதனைக் கேட்டரக்கி தேடியே லட்சுமணரைச் சேரவுற வாடிநின்றாள் உறவாடி நின்ற ஒயிலை யவரறிந்து  பறபோடி யென்று பம்பத்தன மூக்கரிந்தார் மூக்கு முலையும் முகமும் வடிவிழந்து நாக்குரைக்க வாடி நாணங்கெட்டத் தீயரக்கி தன்னுடனே கூடித் தமய னெனப்பிறந்த மன்னன்ரா வணன்தமக்கு வகையா யுரைக்கலுற்றாள் காட்டி லொருபெண் கமலத் திருமகள்போல் நாட்டிலொவ்வாக் கன்னி நான்கண்ட தில்லையண்ணே உன்றனக்கு ஆகுமென்று உற்றவளை நான்பிடித்தேன் என்றனுட மூக்கரிந்தோர் இருவருண் டல்லாது மற்றொருவ ரங்கே வாழ்ந்திருக்கக் கண்டதில்லை');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(44,'ஸ்ரீராமர் வனவாசம்','கற்றொ ருவர் காணாது கையிலம்பு காணாது மருவனைய அண்ணேவுன் வாய்த்த விரலதிலே ஒருயிறைக்கே வுண்டு உற்றஅவர் தன்னுயரம் என்றந்த அரக்கி ஈனம்பல துரைக்க அன்றந்த ராவணனும் ஆகாசத் தேரேறிப் பிச்சைக் கெனவே புறப்பட்டான் காடதிலே  அச்சமில்லா லட்சுமணர் ஆனஸ்ரீ ராமருமாய் மானின் பிறகே மனம்வைத்து நின்றிடவே வானின் செயலால் வானவர்கள் பார்த்திருக்க நாரா யணர்தேவி நல்லதிரு லெட்சுமியை ஏராத பாவி இலச்சைகெட்டத் தீயரக்கன் தேரிலே அம்மைதனைத் திருடிக்கொண் டேகினனாம் பாரிலே வுள்ள பட்சி பறவைகளும் கண்டு பதறிக் கதறிமிக அழவே அண்டர் முனிதேவர் எல்லோருந் தாமழவே மான்வேட்டையாடி மாரீசனை யறுத்துத் தான்வேட் டையாடும் தகையாலே தம்பியுடன் வந்தார்காண் லட்சுமியும் வாழ்ந்திருக்கு மண்டபத்தில் பந்தார் குழலனைய பாவையரைக் காணாமல் கலங்கி மிகவாடி கண்ணீர் மிகச்சொரிய மலங்கியே லட்சுமணர் மண்ணிற் புரண்டழுதார் என்னே மணியே எனைப்பெற்ற மாதாவே பொன்னே யமுதே பெற்றவளே யென்றழுதார் இராமர் முகம்வாடி நாயகியைத் தான்தேடி ஸ்ரீரா மர்கலங்கி சினேக முடனழுதார் மனுவாய்ப் பிறக்க மனுவுடம்பு கொண்டதினால்  தனுவா னதையடக்கித் தானே புலம்பலுற்றார் அன்ன மயிலோடும் அன்றில் குயிலோடும் புன்னை மலரோடும் புலம்பி மிகவழுதார் வன்னமுள்ள யானை வாய்த்தசிங் கத்தோடும் பெண்ணமுதைக் கண்டீரோ என்று புலம்பலுற்றார் இப்படியே ராமர் இளைய பெருமாளும் அப்படியே சொல்லி அழுதழுது தான்வாடிச் சோலை மரத்தின்கீழ்ச் சோர்ந்து முகம்வாடி மாலவருந் தம்பி மடிமேல் துயின்றிருக்க அஞ்சனை யாள்பெற்ற அனுமனதில் வந்தடைய');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(45,'ஸ்ரீராமர் வனவாசம்','சஞ்சல மேதென்று சாரதியுங் தெண்டனிட மின்செறியு மாயன் விழித்தவனைத் தானோக்கிக் கவசகுண்ட லமணிந்த கார்குத்தா யாரெனவே உபசரித்துச் சொன்ன உச்சிதத்தைத் தானறிந்து அய்யரே யென்னை ஆட்கொண்ட நாயகமே மெய்யரே நீங்கள் மெலிந்திருப்ப தேதெனவே கேட்க அனுமன் கிருபைகூர்ந் தெம்பெருமாள் சேர்க்கையுடன் சொன்னார் சீதையுட தன்வளமை ஏற்கை யாகக்கேட்டு இயலுனுமன் ஏதுசொல்வான் நல்லது அடியேன் கேட்டேன் நானினி வுரைக்கும் வாறு   சொல்லவுங் கேட்பீ ரெந்தன் திருவடை யாளஞ் சொன்னீர் வல்லவர் தகப்ப னானீர் வரந்தர வேணு மிப்போ புல்லர்வா ழிலங்கை சுட்டு அம்மையைக் கூட்டி வாறேன் என்தாய் மொழிந்த இயலடையா ளத்தாலே முன்தானே பெற்ற முதல்வர்தா னென்றனக்கு அய்யா தானாகும் அடைக்கலமே யாமடியார் பொய்யாம லென்றன் பேரனுமன் கண்டீரே இலங்கை தனைச்சுட்டு அம்மையைக் கூட்டிவர மலங்காதே போவென்று வாக்கருள வேணுமையா சுக்ரீவன் சாம்புவனும் சுத்தமுள்ள வீரர்களும் ஒக்கவொரு முகமாய் உடையோன் பதமடைய கெருடன் மிகமறித்துக் கேலியிட்ட ஞாயமதும் திருடன் கொடிய தீபாவி ராவணனும் அம்மை தனைக்கொண்டு அவன்கோட்டை யானதிலே செம்மையில்லாப் பாவி சிறையில்வைத்த ஞாயமதும் எல்லா மறிந்து எம்பெருமாள் கோபமுற்று வெல்லத் துணிந்தார் மிகுபடைகள் தான்திரட்டி எழுபது வெள்ளம் ஏற்றவா னரங்களையும் முழுதும் வருத்தி ஒப்பமிட் டெம்பெருமாள் விசுவகம் மாளனையும் விரைவா யருகழைத்து  வசுவாசு மைந்தன் வாய்த்த அனுமனையும் அனுமன் தனையேவி ஆன மலையெடுத்துத் துனும னுடனே தோயக் கடலதுக்குள் கோட்டைய திட்டுக் குக்குளித் தங்கேகி ஓட்டனாய் விட்டார் உற்ற அனுமனையும் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(46,'அனுமன் தூது','பூட்டமுடன் வாயு புத்திரனுந் தான்மகிழ்ந்து நாட்ட முடனிலங்கை நாடாளும் பாதகனைக் கண்டு முடுகிக் கருத்தழியத் தான்பேசி கொண்டு இலங்கை குப்பையிடத் தீக்கொளுத்தி மால்கொடுத்த வாளி மாதுகையி லேகொடுத்து வால்கொண்டு வீசி வனங்காவு தானழித்துக் கேதார மாமலையும் கீர்த்தியுட னேகுதித்துப் பாதார மென்று பணிந்தானே ராமரையும் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(47,'இராவணன் பாடு','அரக்க னவன்பேசும் அநியாயந் தான்கேட்டு இரக்கமுள்ள எம்பெருமாள் எழுந்தார் படைக்கெனவே படைக்கு இவர்நடக்க பார்த்துவி பீஷணனும் அடைக்கல மென்று அவர்பாதஞ் சேர்ந்தான்காண் சேர்ந்த வுடனே திருமால் மனமகிழ்ந்து ஓர்ந்த படையோடே உடன்யுத்தஞ் செய்திடவே அரக்கன் படையும் அச்சுதனார் தன்படையும்  இரக்க மில்லாமல் இருபேருஞ் சண்டையிட்டார் படைபொருது வெல்லாமல் பத்துத்தலை யுள்ளோனும் கடகரியுந் தோற்றுக் கையிழந்தா னம்மானை பத்துத் தலையும் பார்மீதி லற்றுவிழ மற்று நிகரொவ்வா மாயத் திருநெடுமால் அரக்க னுயிரு அங்குமிங்கும் நிற்கையிலே இரக்கமுடன் நின்றங்(கு) ஏதுரைப்பா ரம்மானை கேளாய்நீ ராவணா கிரேதா யுகந்தனிலே பாழாய் இரணியனாய்ப் பாரில் பிறந்திருந்தாய் உன்னிடுக்கங் கண்டு உன்புதல்வ னானாகி தன்னடுக்கல் வந்துவுன்னைச் சங்காரஞ் செய்தேனான் மகனாய்ப் பிறந்து வதைத்தேனான் பாரறிய பகை நானென்று பண்பு மிகக்கூற அப்போது நீயும் அன்றுரைத்த வார்த்தையினால் இப்போது உன்குலங்கள் எல்லாங் கருவறுத்து நாணமில்லா துன்னிலங்கை நகரிதீ யாலழித்துப் பாணமொன்றா லுன்னைப் படஎய்தேன் கண்டாயே அல்லாம லுன்றனக்கு அங்கமொரு நூறு எல்லாரும் வெல்லா ஈசன் கயிலையதும் எடுத்த பெலமுமுண்டே ஈரஞ்சு சென்னியுண்டே  கடுத்த பெலமுள்ள காலாட்டுகள் பொரவுண்டே இந்திரனை வென்ற ஏற்ற புதல்வருண்டே சந்திரனுஞ் சூரியனும் தன்னிடத்தி லுண்டல்லவோ மூணுலோ கத்தாரும் உன்னிடத்தி லுண்டல்லவோ ஆணுவங்கள் பேசினையே அம்பொன்றில் மாண்டாயே கோலுபோ லேயுயர்ந்த கொடும்பாவி நீகேளு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(48,'இராவணன் பாடு','நாலு முழமல்லவோ நல்லதலை ஒன்றல்லவோ என்கை வாளி எடுத்துவிடத் தாங்காமல் உன்கை காலுமற்று உயிரழிந்து மாண்டாயே பத்து மலைபோலே பருந்தலைகள் பெற்றதெல்லாம் இத்தலத்தில் கண்டிலனே என்கையால் மாண்டாயே மாண்டாய்நீ யென்று வசைகூறக் கேட்டரக்கன் ஏண்டா மழுப்புகிறாய் இராமனோ கொல்லுவது தம்பி யெனப்பிறந்து சத்துருப்போல் தான்சமைந்து என்பெலங்க ளெல்லாம் எடுத்துரைத்தா னுன்றனுக்கு ஆனதா லென்னுயிரு அடையாளம் பார்த்துலக்காய் ஊனமுற எய்தாய் உயிரழிந்தே னல்லாது நீயோடா என்பெலங்கள் நிலைபார்த்துக் கொல்லுவது பேயா நீபோடா புலம்பாதே யென்னிடத்தில் அப்போது மாயன் அதிகசீற் றத்துடனே  ஒப்பொன் றில்லாதார் உரைப்பார்கா ணம்மானை உன்னுட தம்பி யாலே உயிர்நிலை யறிந்து யானும் என்னுட சரத்தால் கொன்றேன் என்றியம்பிய அரக்கா வுன்னைப் பின்னுகப் பிறப்பு தன்னில் பிறப்புநூ றோடுங் கூடி அன்னுகந் தன்னில் தோன்ற அருளுவே னுன்னை நானே என்னொரு தம்பி யாலே என்னையுங் கொன்றா யென்று தன்னொரு மதத்தால் நீயும் சாற்றிய அரக்கா வுன்னைப் பின்னொரு யுகத்தில் நூறு பிறப்புடன் பிறவி செய்து இன்னொரு ஆளின் கையால் இறந்திடச் செய்வே னுன்னை என்னுடைய தம்பி யாலேதா னென்னுயிரை உன்னுடைய அம்பால் உயிரழிந்தே னல்லாது என்னைநீ கொல்ல ஏலாது என்றுரைத்தாய் உன்னை நானிப்போ ஒருபிறவி செய்யுகிறேன் என்றுசொல்லி மாயன் எண்ணவொண்ணாக் கோபமுடன் அன்று கயிலை அரனிடத்தில் சென்றிருந்து முன்னேயுள்ள துண்டம் ஓரிரண்டு உள்ளதிலே ஒன்னேயொரு துண்டம் ஒருநூறு பங்குவைத்துத் துவாபர யுகம்வகுத்துத் துரியோதன னெனவே கிரேதா யுகமழித்துக் கீழுலகில் தோணவைத்தார்');");


        db.execSQL("INSERT INTO akilathirattu2 VALUES(49,'துவாபர யுகம்','பாவி பிறக்க பச்சைமால் தான்கூடத்  தாவிப் பிறந்த தம்பியர் மூவரையும் விபீஷணனும் நல்ல வெற்றிச்சாம் புவனையும் அபூருவமாய்க் குந்தியர்க்கு ஐவரையும் பிறவிசெய்தார் ஐவரையும் பூமி அதிலே பிறவிசெய்து மொய்குழ லான மெல்லி திரௌபதியாய் அம்மை திருக்குழலில் அமர்ந்திருந்த பொற்சடையை நம்மை விபீஷணற்கு நல்மக ளாக்கிவைத்து அரக்கர் குலமறுக்க அம்மை யெழுந்தருளி இரக்கர் புரமேகி இருக்குமந்த நாளையிலே தோழியாய் முன்னிருந்த துய்யத் திரிசடையை நாழிகை தன்னில் நாதன் பிறவிசெய்தார் பின்னுமந்த மாயன் பெரியோ னடிவணங்கி நின்னுகரங் குவித்து நெடியோ னுரைத்தனராம் எழுபது வெள்ளம் ஏற்றவா னரங்களுடன் முழுது மிலங்கை முடித்துநான் நிற்கையிலே என்படைக ளான எழுபதுவெள் ளமதிலும் உன்படைக ளெல்லாம் உயிரழிந் தாரெனவே ஆராய்ந்து என்படையை அளவிட்டு நிற்கையிலே தோராத மன்னன் துடியானச் சேவகன்தான் ஒருவன் தனைக்காணேன் உடனே மறலிதனை  வருக அழைத்து வகையே தெனக்கேட்க நானில்லை யென்றே நமன்தான் மிகவுரைக்க ஏனில்லை யென்று இறுக்கிநான் கேட்கையிலே அய்யாவே வும்முடைய ஆளான வீரனைத்தான் கையால முள்ள கும்பகர்ண னொருசமரில் பிடித்து நசுக்கிப் பிசைந்து திலதமிட்டான் அடுத்துநின்று பார்த்து அரக்க னவனுயிரைக் கொண்டேகி நானும் கொடுநரகில் வைத்திருக்கேன் என்றே தான்மறலி இத்தனையுஞ் சொன்னான்காண் ஆனதால் கும்ப கர்ண னவன்தனையும் ஈனமில்லாக் கஞ்சன் எனப்படையு மிவ்வுகத்தில்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(50,'துவாபர யுகம்','என்றே தான்மாயன் இதுவுரைக்க ஈசுரரும் அன்றே தான்கும்பனையும் அப்படியே தான்படைத்தார் படைத்த சடமதுக்குப் பரம னுயிர்கொடுக்கச் சடத்தை மிகயெழுப்பித் தானனுப்ப ஈசுரரும் துவாபர யுகமெனவே தொன்முறையைத் தான்பார்த்துத் தவறாத வண்ணம் தான்படைத்தா ரெல்லோரும் அப்படியே ஈசர் அன்று படைத்தபடி இப்படியே வந்து இவர்கள் பிறந்தனராம் முன்னுள்ள பீடத்து உதிரமெல்லாந் தானெழுந்து  அன்றுள்ள பாவி அவன்கூடத் தானிருக்க அரக்க னிராவணனும் அதிகத்துரி யோதனனாய் மூர்க்கன் பிறப்போர் ஒருநூறு பேரோடு வந்து பிறந்தான்காண் வையகத்தி லம்மானை சந்துபயில் மாயன் தன்னோ டுடன்பிறந்த தம்பி பரதன் சத்ருக்கன் லட்சுமணன் நம்பி விபீஷணனும் நல்லதொரு சாம்புவனும் ஐவரும் பூமிதனில் அப்போது தோன்றினராம் தெய்வத் திரிசடையும் தேவி துரோபதையாய் மெய்வரம்பா யுள்ள மேன்மை யதின்படியே ஐவருட தேவியென்று ஆயிழையுந் தோன்றினளாம் கும்பகர்ண னென்ற கொடும்பாவி கஞ்சனுமாய் வம்பகனாய் வந்து மதுரை தனில்பிறந்தான் இப்படியே ஐபேரும் ஏற்றரிய நூற்றுவரும் அப்படியே பங்கு வகைபாதி யாய்ப்பிறந்தார் இராச்சியத்தி லுள்ள இறைதான முள்ளதெல்லாம் தராச்சியமாய்ப் பாதியெனத் தங்கள்சில நாடாண்டார் கஞ்ச னவன்பிறந்து கடிய கொடியவனாய் வஞ்சனையும் பெற்ற மாதா பிதாக்களையும் சிறையதிலே வைத்துத் தேசமதைத் தானாண்டு  இறைமிகுதி வேண்டி இராச்சியத்தை யாண்டனனே ஆனதா லவனுடைய அம்புவியி லுள்ளோர்கள் ஈனதுன்ப மாகி இருந்தார் பயமடைந்து தேவரையும் வானவரைத் தேவேந்தி ரன்வரையும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(51,'துவாபர யுகம்','வாயு பகவான் வடிவுமிகப் போலே ஆயும் பலகல்வி ஆராய்ந்த அச்சுதர்போல் கண்கொள்ளாக் காட்சி கைமதலை தன்னழகு  விண்கொள்ளாக் காட்சி மெல்லியரே யிம்மதலை இம்மதலை தன்னை எடுத்துநாம் தாலாட்டி பொன்மதலை தன்னைப் போட்டுவைத்துப் போவோம்நாம் என்றுசொல்லி மாதர் இருபேருஞ் சம்மதித்து வண்டுசுற்று மெல்லி வந்தெடுத்தா ரம்மானை மதலை தனையெடுத்து மார்போடு தானணைத்து குதலை மொழியமர்த்திக் கோதைரண்டு மாமயிலும் கயிலை யதுக்கேகி கறைக்கண்டர் பாதமதில் மயிலனைய மாமயிலார் வந்தார்கா ணம்மானை வந்த மாடவர்கள் மார்பி லமுதிளகி அந்தப் பரமே சுரனா ரவரறிந்து கன்னியரே உங்கள் கற்பு அகன்றதென்ன மின்னித் தனங்கள் மெல்லியுட லானதென்ன பச்ச நிறமேனி பால்வீச்சு வீசுதென்ன அச்சமில்லாக் கொங்கை அயர்ந்துஉடல் வாடினதேன் ஏதெனவே சொல்லுமென்று ஈசுரனார் தான்கேட்க சூதகமாய் நின்ற தோகைபோல் வாடிநின்றார் வாடியே மடவார் தாமும் மார்பதில் துகிலை மூடி கோடியே அயர்ந்து ஏங்கிக் குருவதைப் பாரா வண்ணம் நாடியே தாழ்ந்து பெண்கள்  நாணியே நின்ற தன்மை தேடியே மாயன் செய்தச் செயலென அறிந்தா ரீசர் அறிந்தார் மாயன் சேயாகி அழுதே இரங்குங் குரலதினால் செறிந்தார் குழலார் இவர்கள் சென்று சேர்த்தே யெடுத்து அணைத்ததுவும் பறிந்தே யிவர்கள் நாணினதும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(52,'துவாபர யுகம்','பால்தான் தனத்தில் பாய்ந்ததுவும் அறிந்தே கன்னி யிருவரையும் அழித்தே பிறவி செய்தனராம் பிறவி யதுதான் செய்யவென்று பெரியோ னுரைக்கப் பெண்ணார்கள் அறவி யழுது முகம்வாடி அரனா ரடியை மிகப்போற்றித் திறவி முதலே திரவியமே சிவமே உமது செயலைவிட்டு இறவி யானா லெங்களுக்கு இனிமேல் பிறவி வேண்டாமே வேண்டா மெனவே மெல்லியர்கள் விமல னடியை மிகப்போற்றி மாண்டா ரெலும்பை மார்பணியும் மறையோன் பின்னு மகிழ்ந்துரைப்பார் தூண்டாச் சுடரோன் திருமாலைச் சேயென் றெடுத்தச் செய்கையினால் ஆண்டா ருமக்கு மகனாகி அதின்மேல் பதவி உங்களுக்கே மெல்லியரே உங்களுட மெய்வரம்பு தப்பினதால் செல்ல ஏதுவாச்சு சீமையிலே யம்மானை என்றே அரனார் இருகன்னி யோடுரைக்க அன்றே மடவார் ஆதிதனை நோக்கி ஆதியே நாதி ஆனந்த மெய்ப்பொருளே சோதியே நாதி சொரூபத் திருவிளக்கே அல்லாய்ப் பகலாய் ஆரிருளாய் நிற்போனே  வல்லாய்ப் புவியை வார்த்தையொன் றாற்படைத்த ஆதிப் பொருளே அனாதித் திருவுளமே சோதிப் பொருளே சொல்லொணா தோவியமே எட்டாப் பொருளே எளியோர்க் கெளியோனே முட்டாத செல்வ முதலே முழுமணியே கண்ணுள் மணியே கருத்தினுள் ளானவனே பெண்ணு மாணாகிப் பிறப்பில்லா நின்றோனே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(53,'துவாபர யுகம்','இறப்புப் பிறப்பில்லாத எளியோர்க் கெளியோனே பிறப்பு இறப்பில்லாத பேசமுடி யாதவனே கருவிதொண்ணூற் றாறும் காவலைந்து பேருடனே உருவு பிடித்து உயிர்ப்பிறவி செய்வோனே மூவரு முன்றன் முடிகாண மாட்டாமல் தேவருந் தேடித் திரிந்தலைய வைத்தோனே மவ்வவ் வாகி வருந்தியவ்வும் ஒவ்வாகி செவ்வவ் வாய்நின்ற சிவனே சிவமணியே மாய னெடுத்த மகவான கோலமதை நேய மறியாமல் நெறிதவறிப் போனதினால் பின்னேநீ ரெங்களையும் பிறவிசெய்ய வேண்டாங்காண் எந்நேரங் கயிலை இப்பூமி யானதிலே தெய்வப் பசுப்போல் தேவரீ ரெங்களையும்  மெய்யறிவு கூடி மிகப்புடையு மேலோனே அல்லாதே போனால் அடியார்க ளெங்களையும் இல்லாதே செய்யும் இனிப்பிறவி வேண்டாங்காண் அம்மை உமையாள் அருகே மிகஇருந்து உம்மையும் போற்றி உம்மூழி யங்கள்செய்து பூவுலகில் நாங்கள் பெண்ணாய்ப் பிறந்ததுண்டால் பாவிகட்கு வேலைப் பண்ணி முடியாதே பூவு முடித்துப் பூமேடை யும்பெருக்கி நாவுலகு மெய்க்க நல்லகவி தான்பாடி தம்புரு வீணை சப்தக் குழலுடனே இன்புருக ராகம் இசைந்தமுறை தானிகழ்த்தி நரசென்மம் வாழும் நசுரக் குலங்களிலே அரசேநீ ரெங்களையும் அழுந்தப் படையாதேயும் இப்படியே கன்னி இரங்கி யழுதிடவே அப்படியே கன்னி அவர்களுக் காதியுந்தான் சொல்லுவார் வேதச் சுடரோ னதிசயித்து நல்லதுகாண் பெண்ணே நம்மைரண்டுஞ் சொல்லாதே மாயன் துவாபர வையகத்தில் போய்ப்பிறக்க வாயமிட்டா ருங்களையும் மாதா வெனவாக்க நாமென்ன செய்வோம் நவ்விமால் செய்ததற்குப்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(54,'துவாபர யுகம்','போமென் னஈசர் பிறவிசெய்தா ரம்மானை வாயு மகாதேவன் வடிவென்ற இச்சையினால் ஆசையா லுங்களுக்கு அவனே புருஷனென்றார் சங்கு சரங்கொண்ட தம்பியென்ற இச்சையினால் அங்குப் பகவதியும் அச்சுதரு முங்களுக்கு மதலையென வந்துதிப்பார் வையமது கொண்டாட குதலை மொழியாரே கொம்பே றிடையாரே விலங்குச் சிறையும் மிகுசிறையு முங்களுக்கு மலங்குவது மெத்த மாய்ச்சலுண் டாகியபின் ஸ்ரீகிருஷ்ண னாகத் திருமா லுதித்தபின்பு குறுக்கிட்ட தோசம் கொடுமை மிகத்தீர்ந்து பின்னே பதவிப் பேறுண்டு முங்களுக்கு மின்னே ரொளியை விமலன் பிறவிசெய்தார் பிறவியரன் செய்யப் பிறந்தார்கள் பூமியிலே திறவியது விட்டுத் தேவகியும் ரோகணியும் மதுரைதனில் வந்து மாபாவிக் கஞ்சனுக்கு இருதையல் மாமயிலார் ஏற்ற பிறப்பெனவே கூடப் பிறந்தால் கொடியதோ சமெனவே தேட வொருராசன் சிறுவி யெனப்பிறந்தார் பிறந்தே தான்கஞ்சனுக்குப் பிறப்பெனவே பேசும்வளம்        சிறந்தே யிவர்கள் திருத்தமாய் வாழுகையில் இப்படியே கஞ்சன் இவர்களிரு தங்கையையும் அப்படியே கூட்டி அவன்வேட்டை யாடிவர மாமுனிவன் தனக்கு வாய்த்திருந்த மாம்பழத்தைத் தாமுனிந்து கஞ்சன் தானக்கனி பறித்துத் தங்கையர்க்குத் தானீந்தான் சயோகமுனி யறிந்து மங்கையர்க்கு வந்த மகனா லழிவையென்று மாயனுட ஏவலினால் மாமுனியு மப்போது பாயமுள்ள கஞ்சனுக்குப் படுசாப மிட்டனனே சாபமது கஞ்சன் தானறியா வண்ணமுந்தான் பாவமிக தான்வருத்தி மிகுத்தகே டுள்ளகஞ்சன் சோதிரியங் கேட்கத் தொடர்ந்தான்கா ணம்மானை அப்போது சோதிரிஷி அந்தமுனி சாபமதால்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(55,'துவாபர யுகம்','எப்போது ஆகிடினும் இடுக்கம்வரு மென்றுரைத்தான் சோதிரியங் கேட்டுத் துக்கமுற்றுக் கஞ்சனுந்தான் வேதி யனையனுப்பி மிகுத்தகே டுள்ளகஞ்சன் கலியாணஞ் செய்யக் கருத்தல்ல வென்றுசொல்லி வலியான கஞ்சன் வைத்திருந்தா னம்மானை அந்தநல்ல செய்தி அறிந்தந்த நாரதரும் வந்துகஞ் சன்தனக்கு வளப்பமெல்லாஞ் சொல்லலுற்றார் கேளாய் நீகஞ்சா கீர்த்தியுள்ள சாஸ்திரங்கள் வாழாத மங்கையரை வைத்திருந்தால் ராச்சியத்தில் தர்மந் தலைகெடுங்காண் சாஸ்திரத்துக் கேராது வற்மம் வந்துசிக்கும் மாரியது பெய்யாது கோத்திரத்துக் கேராது குடும்பந் தழையாது சூத்திர நோய்கள் சுற்றுமடா அக்குடும்பம் மானம் வரம்புகெட்டு மனுநீதி தானழிந்து ஊனமடா அக்குடும்பம் உலகத்துக் கேராது கோட்டை யழியும் குளங்கரைகள் தானிடியும் நாட்டை முடிக்குமடா நல்லகன்னி காவல்வைத்தால் பொன்னான சீதை பிலத்தகற் பானதிலே கண்ணான இலங்கை கரிந்ததுவுங் கண்டிலையோ இப்படியே வேதம் இயம்பி யிருப்பதினால் அப்படியே பெண்ணார் அவர்கள்ரண்டு பேரையுந்தான் காவலிட்டு வைத்தால் கற்பதினா லுன்றனக்குப் பாவம்வந்து சுற்றும் பலிக்குமடா வேதமது ஆனதால் பெண்கள் அவர்கள்ரண்டு பேரையுந்தாம் மானமுள்ள மன்னனுக்கு மணஞ்செய்து தான்கொடுத்துக் கெற்பமது உண்டாகிக் கீழே பிறக்கையிலே  அப்போநீ கொன்றாலும் ஆனாலும் பாரமில்லை என்றுஅந்த நாரதரும் இத்தனையும் தான்கூற நன்று மொழியெனவே நவ்வியே கஞ்சனுந்தான் எனக்குப் படிப்புரைத்த ஏற்றவசு தேவன் தனக்கு இவர்களையும் தான்சூட்ட வேணுமென்று வேணுமென்று சீட்டோ விரும்பிவசு தேவனுந்தான் வாணுவங்க ளோடே வந்தான் மதுரையிலே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(56,'துவாபர யுகம்','தேவகியை ரோகணியைச் சிறப்பா யலங்கரித்து வைபோக முள்ளவசு தேவனையு மொப்பிவித்துச் சங்கீதத் தோடே தையல்ரண்டு பெண்களையும் மங்களத் தோடே வசுதேவன் தாலிவைத்துக் கட்டிக் கைபிடித்துக் கனசீ தனத்தோடே கொட்டித் திமிர்த்தூதும் குழலோடே வீற்றிருந்தான் வாழ்ந்திருந்து பெண்கள் வயிறு வளருகையில் ஆய்ந்தறிந்து கஞ்சன் அருவிலங்கில் வைத்தனனே தெய்வகியாளு மழுது சிந்தைமுகம் வாடிருப்பாள் மெய்யன் வசுதேவன் மிகக்கலங்கி தானிருப்பான் மங்கைநல்லாள் ரோகணியும் வாடி யழுதிருப்பாள் சங்கையுள்ள தேவர்களும் தையல்தெய்வக் கன்னியரும்     எல்லோருங் கஞ்சனுட இடுக்கமது தன்னாலே அல்லோரு மெத்த அறமெலிந்தா ரம்மானை இப்படியே செய்யும் இடுக்கமதுக் காற்றாமல் அப்படியே மாயனுக்கு அபயம் அமரரிட ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(57,'தேவர் முறையம்','நிலந்தேவி ஆகாய நீர்தேவி யார்கூடி தலந்தேவி மன்னன் தனக்கே யபயமிட தேவாதி தேவர் தேவிமுறை யாற்றாமல் மூவாதி முத்தன் முழித்தார்காண் பள்ளியது கண்டுதே வாதிகளும் கன்னி புவிமகளும் விண்டுரைக்கக் கூடாத விமல னடிபோற்றி ஆதியே நாதி அய்யாநா ராயணரே சோதியே வேதச் சுடரே சுடரொளியே பத்தர்க்கு நித்தா பரனேற்றார்க் கேற்றோனே மூவர்க்கு மாதி முழுமணியாய் நின்றோனே தேவர்க்கும் நல்ல சிநேகமாய் நின்றோனே கிட்டவரார்க் கெட்டாத கிருஷ்ணாவுன் நற்பதத்தைக் கட்டவரு மட்டான கருணா கரக்கடலே உச்சிச் சுழியே உம்மென் றெழுத்தோனே அச்சிச் சுழியே அம்மென் றெழுத்தோனே அஞ்செழுத்து மூன்றெழுத்தும் ஆதிஅ வென்றெழுத்தும் நெஞ்செழுத்து மெட்டெழுத்தும் நீயாகி நின்றோனே எண்ணொருபத் தெண்ணிரண்டும் இவ்வே நடுவாக ஒண்ணெழுத்தாய் நின்ற உடைய பெருமாளே தண்டரளத் தூணாகித் தருவைந்து பேராகி மண்டலத்துள் ளூறலுமாய் மயமுமாய் நின்றோனே போக்கு வரத்துப் புகுந்துரண்டு கால்வீட்டில் நாக்குரண் டுபேசி நடுநின்ற நாரணரே அல்லாய்ப் பகலாய் ஆணாகிப் பெண்ணாகி எல்லார்க்குஞ் சீவன் ஈயுகின்ற பெம்மானே பட்சிப் பறவை பலசீவ செந்துமுதல் இச்சையுடன் செய்நடப்பு இயல்கணக்குச் சேர்ப்போனே ஒருபிறப்பி லும்மை உட்கொள்ளாப் பேர்களையும் கருவினமா யேழு பிறப்பிலுங் கைகேட்போனே ஆறு பிறப்பில் அரியேவுன்னைப் போற்றாமல்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(58,'தேவர் முறையம்','தூறு மிகப்பேசித் தொழாதபே ரானாலும் ஏழாம் பிறப்பிலும்மை எள்ளளவுதா னினைத்தால் வாழலா மென்றவர்க்கு வைகுண்ட மீந்தோனே இப்படியே ஏழு பிறப்பதிலும் உம்மையுந்தான் அப்படியே ஓரணுவும் அவர்நினையா தேயிருந்தால் குட்டங் குறைநோய் கொடியகன்னப் புற்றுடனே  கட்டம் வந்து சாக கழுத்திற்கண்ட மாலையுடன் தீராக் கருவங்கும் தீன்செல்லா வாய்வுகளும் காதா னதுகேளார் கண்ணே குருடாகும் புத்தியற்று வித்தையற்றுப் பேருறுப்பு மில்லாமல் முத்திகெட்ட புத்தி முழுக்கிரிகை தான்பிடித்துப் பிள்ளையற்று வாழ்வுமற்றுப் பெண்சாதி தானுமற்றுத் தள்ளையற்று வீடுமற்று சப்பாணி போலாகி மாநிலத்தோ ரெல்லாம் மாபாவி யென்றுசொல்லி வானிழுத்து மாண்டு மறலியுயிர் கொள்கையிலே நாய்நரிகள் சென்று நாதியற்றான் தன்னுடலைத் தேயமது காணத் திசைநாலும் பிச்செறிந்து காக்கை விடக்கைக் கண்டவிடங் கொண்டுதின்னப் போக்கடித்துப் பின்னும் பொல்லாதான் தன்னுயிரை மறலி கொடுவரச்சே வலியதண் டாலடித்துக் குறளி மிகக்காட்டிக் கொடும்பாவி தன்னுயிரை நரகக் குழிதனிலே நல்மறலி தள்ளிடவே இரைநமக் கென்று எட்டிப் புழுப்பிடித்து அச்சுத ரைநினையான் அய்யாவைத் தானினையான் கச்சி மனதுடைய காமாட்சியை நினையான் வள்ளிக்குந் தேவ மாலவருக் காகாமல்  கொள்ளிக்குப் பிள்ளையில்லாக் கொடும்பாவி யென்றுசொல்லி எவ்வியே அட்டை எழுவாய் முதலைகளும் கவ்வியே சென்று கடித்துப் புழுப்பொசிக்கும் என்றுநீ ரேழு இராச்சியமுந் தானறிய');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(59,'தேவர் முறையம்','அன்று பறைசாற்றி அருளிவைத்த அச்சுதரே வானமது பூமியிலே மடமடென வீழாமல் தானவனே உன்விரலால் தாங்கிவைத்த பெம்மானே வாரி வரம்பைவிட்டு வையகத்திற் செல்லாமல் காரியமாய்ப் பள்ளி கடலில் துயின்றோனே மானம் வரம்பு மகிமைகெட்டுப் போகாமல் ஊன மில்லாதே உறும்பொருளாய் நின்றோனே சீவனுள்ள செந்துகட்குத் தினந்தோறு மேபொசிப்புத் தாவமுட னீயுகின்ற தர்மத் திறவோனே வலியோ ரெளியோர்க்கும் மண்ணூருஞ் செந்துகட்கும் கலிதீர ஞாயம் கண்டுரைக்கும் பெம்மானே சிவசெந்துக் கெல்லாம் சீவனுமாய் நின்றோனே பாவமும் புண்ணியமாய்ப் பாராகி நின்றோனே கண்ணாகி மூக்குக் கருணா கரராகி மண்ணாகி வேத மறையாகி நின்றோனே சாத்திரமும் நீயாய் சந்திரனும் நீயாகிச் சூத்திரமும் நீயாய் சுழியாகி நின்றோனே நட்சேத்தி ரமாகி நாட்கிரகம் நீயாகி இச்சேத்தி ரமாய் இருக்குகின்ற பெம்மானே மெய்யனுக்கு மெய்யாய் மேவி யிருப்போனே பொய்யனுக்குப் பொய்யாய்ப் பொருந்தி யிருப்போனே இமசூட் சமாகி ஏகம் நிறைந்தோனே நமசூட் சமான நாரா யணப்பொருளே கல்வித் தமிழாய் கனகப் பொருளாகிச் செல்வித் திதுவாய்ச் செவ்வாகி நின்றோனே அண்ட பிண்டமாகி அநேகமாய் நின்றோனே கண்ட இடமும் கண்ணுக்குள் ளானோனே ஈசர் கொடுத்த ஏதுவர மானாலும் வேசமிட்டு வெல்ல மிகுபொருளாய் நின்றோனே நடக்க இருக்க நடத்துவதும் நீயல்லவோ திடக்க மயக்கம் செய்வதும் நீயல்லவோ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(60,'தேவர் முறையம்','ஆரு மொருவர் அளவிடக் கூடாமல் மேரு போலாகி விண்வளர்ந்து நிற்போனே எண்ணத் தொலையாத ஏது சொரூபமதும் கண்ணிமைக்கு முன்னே கனகோடி செய்வோனே மூவரா லுன்சொரூபம் உள்ளறியக் கூடாது  தேவரா லுந்தெரியாத் திருவுருவங் கொள்வோனே ஆயனே யெங்கள் ஆதிநா ராயணரே மாயனே கஞ்சன் வலிமைதனை மாற்றுமையா பாவமாய்க் கஞ்சன் பலநாளா யெங்களையும் ஏவல்தான் கொண்ட இடுக்கமதை மாற்றுமையா  ஊழியங்கள் செய்து உடலெல்லாம் நோகுதையா ஆழி யடைத்த அச்சுதரே யென்றுரைத்தார் வாண னென்றகஞ்சன் மாபாவி யேதுவினால் நாணமது கெட்டு நாடுவிட்டுப் போறோங்காண் நரபால னென்ற நன்றிகெட்ட கஞ்சனினால் வரம்பா னதுகுளறி மானிபங்கள் கெட்டோமே இத்தனையுங் காத்து இரட்சிக்க வேணுமையா முத்தியுள்ள தேவர் முறையம் அபயமிட பூமா தேவி புலம்பி முறையமிட நாமாது லட்சுமியும் நன்றா யபயமிட நாரா யணர்பதத்தை நாயகியுந் தெண்டனிட்டுச் சீரான லட்சுமியும் செப்பினள்கா ணம்மானை என்னைப்போல் பெண்ணல்லவோ இவள்தா னிடுமுறையம் வன்னமுள்ள மாலே மனதிரங்கிக் காருமையா உடனேதா னாதி ஓலமிட்டுத் தேவருக்கும்  திடமான பூமா தேவிக்குஞ் சொல்லலுற்றார் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(61,'தேவர் வேண்டுதலுக்கு இரங்கல்','வந்து பிறப்போங்காண் மாபாரத முடிக்க நந்தி குலம்வளர நாம்பிறப்போங் கண்டீரே சாரமில்லாக் கஞ்சன் தனைவதைத்துப் பூமியுட பாரமது தீர்ப்போம் பாரத முடித்துவைப்போம் துவாபர யுகத்தில் துரியோ தனன்முதலாய்த் தவறாத வம்பன் சராசந் தன்வரையும் அவ்வுகத் திலுள்ள அநியாயமு மடக்கிச் செவ்வுகத்த மன்னவர்க்குச் சிநேகமது செய்வதற்கும் உங்களுக்கும் நல்ல உதவிமிகச் செய்வதற்கும் அங்குவந்து தோன்றி ஆய ருடன்வளர்வோன் போங்களென்று பூமா தேவியையுந் தேவரையும்  சங்குவண்ண மாலோன் தானே விடைகொடுத்தார் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(62,'கண்ணன் அவதாரம்','விடைவேண்டித் தேவர் மேதினியில் தாம்போகப் படைவீர ரான பச்சைமால் தானெழுந்து ஆதி கயிலை அரனிடத்தில் வந்திருந்து சோதி மணிநாதன் சொல்லுவா ரம்மானை வாணநர பாலனென்ற மாபாவிக் கஞ்சனினால் நாணமது கெட்டோமென நாடிமிகத் தேவரெல்லாம் பூமா தேவிமுதல் பொறுக்கமிகக் கூடாமல்  ஆமா அரியே ஆதி முறையமென்றார் முறையம் பொறுக்காமல் முடுகியிங்கே வந்தேனென்று மறைவேத மாமணியும் மகிழ்ந்துரைத்தா ரம்மானை அதுகேட்டு ஈசர் அச்சுதருக் கேதுரைப்பார் இதுநானுங் கேட்டு இருக்குதுகா ணிம்முறையம் ஆனதால் கஞ்சன் அவனைமுதல் கொல்வதற்கு ஏனமது பாருமென்று எடுத்துரைத்தா ரீசுரரும் பாருமென்று ஈசர் பச்சைமா லோடுரைக்க ஆருமிக வொவ்வாத அச்சுதரு மேதுரைப்பார் மேருதனில் முன்னாள் வியாசர் மொழிந்தபடி பாருபா ரதமுதலாய்ப் பாரதப்போர் தான்வரையும் நாரா யணராய் நாட்டில் மிகப்பிறந்து வீரான பார்த்தன் மிகுதேரை ஓட்டுவித்துச் சத்தபல முள்ள சராசந் தன்வரையும் மற்றவ னோராறு வலியபலக் காரரையும் கொல்லவகை கூறி குருநிலையைத் தான்பார்த்து வெல்லப்பிறப் பாரெனவே வியாசர் மொழிந்தபடி அல்லாமற் பின்னும் ஆனதெய்வ ரோகணியும் நல்ல மகவான நாரா யணர்நமக்கு மகவா யுதிப்பாரென மகாபரனார் சொன்னபடி  தவமா யிருந்து தவிக்கிறாள் தேவகியும் இப்படியே யுள்ள எழுத்தின் படியாலே அப்படியே யென்னை அனுப்புமென்றா ரம்மானை முன்னே வியாசர் மொழிந்தபடி முறைநூல் தவறிப் போகாமல்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(63,'கண்ணன் அவதாரம்','தன்னை மதலை யெனஎடுக்கத் தவமா யிருந்த தெய்வகிக்குச் சொன்ன மொழியுந் தவறாமல் துயர மறவே தேவருக்கும் என்னைப் பிறவி செய்தனுப்பும் இறவா திருக்கும் பெம்மானே முன்னே வியாசர் மொழிந்தமொழி மாறாமல் என்னையந்தப் பூமியிலே இப்போ பிறவிசெய்யும் செந்தமிழ்சேர் மாயன் சிவனாரை யும்பணிந்து எந்தனக்கு ஏற்ற ஈரஞ்சாயிர மடவை கன்னியரா யென்றனக்கு கவரியிட நீர்படையும் பன்னீர்க் குணம்போல் பைம்பொன் னிறத்தவராய் ஆயர் குலத்தில் அநேக மடவாரை பாயமுற வாடிருக்கப் படைப்பீர்கா ணீசுரரே உருப்பிணியாய் இலட்சுமியை உலகிற்பிறவி செய்யும் கரும்பினிய தெய்வக் கயிலாச மாமணியே சத்தபெல முள்ள தத்துவத்தார் தங்களையும் மெத்தவரம் பெற்ற மிகுவசுரர் தங்களையும் எல்லோரையு மிப்பிறவி இதிலே வதைத்திடவே  அல்லோரை யும்பிறவி ஆக்கிவைய்யு மென்றுரைத்தார் என்றனக்கு நல்ல ஏற்ற கிளைபோலே விந்து வழிக்குலம்போல் மிகுவாய்ப் படையுமென்றார் இப்படியே மாயன் இசையஅந்த ஈசுரரும் அப்படியே பிறவி அமைக்கத் துணிந்தனராம் துணிந்தாரே மாயன் தொகுத்ததெல்லா மாராய்ந்து வணிந்தார மார்பன் வகைப்படியே செய்யலுற்றார் தெய்வகியாள் வயிற்றில் திருமால் பிறந்திடவே ஐவர்க் குறுதியிட அச்சுதருந் தோன்றுவாராம் கலக்கமுடன் விலங்கில் கவிழ்ந்திருந்த மாதுவுட மலக்கமது தீர மலரோன் பிறக்கலுற்றார் ஐவர்க் குபகாரம் அன்பாகச் செய்திடவும் தெய்வகிக்கும் ரோகணிக்கும் சிவகெதிக ளீவதற்கும் கஞ்சனுட வலுமை கட்டழித்துக் கொல்வதற்கும் விஞ்சவரம் பெற்று வீறுசெய்யும் பேர்களையும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(64,'கண்ணன் அவதாரம்','சத்த பெலமுள்ள தத்துவங்க ளுள்ளோரைத் தத்தியுள்ள விமனையும் தன்னா ளாக்கிவிட்டுக் கொல்வதற்கும் தேவருட கூர்முறையந் தீர்ப்பதற்கும் வெல்வதற்கும் பூமியுட விதனமதை மாற்றுதற்கும் முன்னே வியாசர் மொழிந்த முறைப்படியே  தன்னிக ரில்லாத தையல்தெய்வ கிவயிற்றில் பிறக்கிறா ரென்று பெரியோர்கள் கொண்டாட இறக்கிறார் பொல்லாதார் என்றுமிகக் கொண்டாட முன்பெற்ற பிள்ளை முழுதுமவன் கொன்றதினால் வன்பற்ற மாது மங்கையந்தத் தெய்வகியும் மெத்த மயங்கி முன்னம்விதி தன்னைநொந்து கர்த்தன் செயலோ கரியமால் தன்செயலோ என்றுஅந்தக் கன்னி இருபேருந் தான்புலம்பி விண்டு சொல்லாத விதன மிகவடைந்து அழுது கரைந்து அவளிருக்கும் வேளையிலே பழுதில்லா தாயன் பாவை வயிற்றிலுற்றார் பகவதியு மங்கே பாவையசோ தைவயிற்றில் சுகபதியு மங்கே தோன்றினள்கா ணம்மானை மாயனந்தத் தெய்வகியாள் வயிற்றிலுற்ற தவ்வளவில் தேசமெல்லாம் நன்றாய்ச் செழித்ததுகா ணம்மானை கெற்பமுற்ற தெய்வகியாள் கெஞ்சுகவா யஞ்சுகமும் நற்பதமாய்த் தேகம் நாட்டமுடன் கோட்டியுமாய் பத்துமாதந் திகைந்து பாலன் பிறந்திடவே சுற்றுமதிற் காவல்வைத்த துடியோர் வலுவிழந்து வசுதேவன் காலிலிட்ட வாய்த்த விலங்குமற்று  விசுவாச மாதருட விலங்கது தானுமற்றுத் தாள்திறந்து நேரம் தான்விடியு முன்னாகக் கண்டாளே தெய்வகியும் கனத்த மதலைதனை கொண்டாடித் தானெடுத்துக் கூறுவா ளம்மானை கண்ணணோ சீவகனோ கரியமுகில் மாயவனோ வண்ணனோ தெய்வேந்திரனோ மறையவனோ தூயவனோ அய்யோமுன் பெற்ற அதிக மதலையெல்லாம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(65,'கண்ணன் அவதாரம்','மெய்யோதா னிம்மதலைக்(கு) ஒவ்வாது மேதினியே என்று பிரியமுற்று ஏற்ற மதலைதனை அன்று கொடுத்து அனுப்பினா ளயோதையிடம் கொண்டு வசுதேவன் அயோதைக் குடிலேகிக் கண்டு அயோதை கன்னியங்கே பெற்றிருந்த பெண்மதலை தன்னைப் பூராயமா யெடுத்து ஆண்மதலை தன்னை அயோதை யிடமிருத்தி வந்து வசுதேவன் மங்கைகை யில்கொடுத்துப் புந்திமிக நொந்து போயிருந்தா னம்மானை அந்த யிராவிடிந்து அலைகதிரோன் தோன்றினபின் கந்த மனசுள்ள கஞ்ச னவன்தனக்கு ஒற்றாளாய்த் தூதன் ஒருவன் மிகஓடி பெற்றா ளுன்தங்கை பிள்ளை யெனவுரைத்தான்  கேட்டானே கஞ்சன் கெருவிதமாய்த் தானெழுந்து பூட்டான நெஞ்சன் பிள்ளைதனை வந்தெடுத்துத் தூக்கி நிலத்தில் துண்ணெனவே தானடிக்க ஆக்கிரமத் தாலே அவன்தூக்க ஏலாமல் சோர்வுறவே கஞ்சன் துடியிழந்து நிற்கையிலே பாரளந்தோன் தங்கை பகவதியு மேதுசொல்வாள் என்னை யெடுத்து ஈடுசெய்யா தேகெடுவாய் உன்னை வதைக்க உற்றயெங்க ளச்சுதரும் ஆயர்பா டிதன்னில் அண்ணர் வளருகிறார் போய்ப்பா ரெனவே போனாள் பகவதியும் கேட்டந்தக் கஞ்சன் கிலேச மிகவாகி வீட்டுக்குள் போயிருந்து விசாரமுற்றா னம்மானை அய்யோ முனிதான் சபித்தபடி ஆயர் பதியில் போகறியேன் மெய்யோ தளரு துடல்மெலியு மெல்லி மொழிந்த விசளமதால் கையோ சலித்துக் காலயர்ந்து கால விதியாற் கருத்தயர்ந்து பையோ ராளைத் தானழைத்துப் பார்க்க விடுத்தான் கஞ்சனுமே ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(66,'கஞ்சன் பாடு','அய்யோ மறையோன் அன்று சபிபத்தபடி சையோ இடையர் தம்பதியில் போகறியேன் தங்கை வயிற்றில் செனித்தபிள்ளை யானதுண்டால் பங்கம்வரு மென்றனக்குப் பதறுதடா என்னுடம்பு  என்று மனங்கருகி இயலழிந்த கஞ்சனுந்தான் அன்றுஒரு தூதனையும் அனுப்பினன்கா ணம்மானை வாராய்நீ தூதா மற்றோ ரறியாமல் பாராய்நீ தூதா அயோதைப் பதியேகி என்றுஅந்தத் தூதனையும் ஏகவிட்டான் கஞ்சனுமே நன்றுநன்று என்று நடந்தானே தூதனுந்தான் தூதன் மிகநடந்து தோகைஅயோ தைமனையில் புகுந்தந்தப் பிள்ளை பொன்தொட்டிலி லேகிடக்கக் கண்டுஅந்தத் தூதன் கஞ்சனுக்கு நஞ்செனவே விண்டு பறையாமல் விரைவாகப் போயினனே போயந்தத் தூதன் பொறையுள்ள கஞ்சனுக்கு வாயயர்ந்து சிந்தை மறுகியே சொல்லலுற்றான் காமனோ சீவகனோ கண்ணனோ சந்திரனோ மாமதனோ சூரியனோ மறையவனோ இறையவனோ நாமத் திறவானோ நாரா யணன்தானோ சோமத் திருவுளமோ தெய்வேந் திரன்தானோ ஈசனோ வாசவனோ இந்திரனோ சந்திரனோ மாயனோ வுன்னை வதைக்கவந்த மாற்றானோ ஆரோ எனக்கு அளவிடவுங் கூடுதில்லை பேரோ அயோதைப் புதல்வனெனக் காணுதில்லை  தெய்வகியாள் தேகத் திருச்சுவடு தோணுகொஞ்சம் மெய்யதிப னான விட்டிணுபோல் முச்சுவடு என்றந்தக் கஞ்சனுக்கு இத்தூதன் சொல்லிடவே அன்றவன் கேட்டு அயர்ந்திருந்தா னம்மானை என்செய்வோ மென்று இருக்குமந்த நாளையிலே முன்செறியும் பூத மூரிதனை யேவியவன் கொல்லவென்று விட்டான் குழந்தைதனை யம்மானை வல்ல பெலமுள்ள மாபூதம் பட்டபின்பு பின்னுமே தானும் பிலஅரக்கரை யேவிக் கொன்றுவா வென்றான் குழந்தைதனை யம்மானை');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(67,'கஞ்சன் பாடு','கொல்லவந்த பேரையெல்லாம் கொன்றதுகா ணக்குழந்தை வெல்லவிட்டப் பேரிழந்து மெலிந்திருந்தான் கஞ்சனுமே ஆயர் குடியில் அரியோன் மிகவளர்ந்து மாயன் விளையாடி மடந்தையோடுங் கூடி வென்றுபால் வெண்ணெய் மிகப்பொசித்துக் காடதிலே கன்றாவு மேய்த்துக் காளியன் தனைவதைத்துக் கஞ்சனுட ஏவலினால் காட்டில்வந்த சூரரையும் துஞ்சிவிடக் கொன்று தொலைத்தனர்கா ணம்மானை ஆயருக்கு வந்த ஆபத்து அத்தனையும் போயகற்றி நந்தன் பிள்ளையெனவே வளர்ந்தார்  மதுரைதனில் வாழும் மாபாவிக் கஞ்சனுக்கு இறுதிவரும் போது ஏதுசெய்தான் கஞ்சனுமே நந்திடையன் பாலன் நல்லஸ்ரீ கிட்டிணனை இந்திடத்தில் கொண்டுவர ஏவிவிட்டான் ஓராளை கஞ்சனுட ஆளும் கண்ணர்ஸ்ரீ கிட்டிணரை அஞ்சலென்று கண்டு அழைத்தாருமைக் கஞ்சனென்றான் அந்தவிச ளமறிந்து அரியோ னகமகிழ்ந்து வந்த விசளம் வாச்சுதென் றெம்பெருமாள் கூடச் சிலபேரைக் குக்குளிக்கத் தான்கூட்டி ஈடவி யென்ற எக்காள சத்தமுடன் குஞ்சரமும் பரியும் குரவைத் தொனியுடனே கஞ்ச னரசாளும் கனமதுரை சென்றனரே நாட்டமுட னயோதை நல்லமகன் வந்தானென்று கேட்ட விசளம் கெட்டியெனக் கஞ்சனுந்தான் இங்கேநான் சென்று ஏற்றவனைக் கொல்லவென்று சங்கையற்றக் கோடி தத்திப் படையுடனே கொல்லவிட்டக் கஞ்சன் குதிரைத் தளம்படையும் எல்லாந் திருமால் இறக்கவைத்தா ரம்மானை கஞ்சன் படைகள் கட்டழிந்து போனவுடன் வஞ்சகனும் வந்து மாயனு டனெதிர்த்தான்  மாயனுட போரும் வஞ்சகக்கஞ் சன்போரும் தேசமெல்லா மதிரச் சென்றெதிர்த்தா ரம்மானை மாயனுக்குக் கஞ்சன் மாட்டாமல் கீழ்விழவே வாயமிட்டுக் கஞ்சன் மார்பிலே தானிருந்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(68,'கஞ்சன் பாடு','கஞ்சன் குடலைக் கண்ணியறத் தான்பிடுங்கி வெஞ்சினத்தால் மாயன் விட்டெறிந்தார் திக்கதிலே கஞ்சனையுங் கொன்று கர்மமது செய்தவரும் தஞ்சமிட்டத் தேவருட தன்னிடுக்கமு மாற்றி மாதாபிதா வுடைய வன்சிறையுந் தான்மாற்றி சீதான உக்கிர சேனனையு மாளவைத்து அவரவர்க்கு நல்ல ஆனபுத்தி சொல்லிமிக எவரெவர்க்கும் நல்லாய் இருமென்று சொல்லியவர் திரும்பிவரும் வேளையிலே தீரன்சரா சந்தனவன் எந்தன் மருமகனை இவனோதான் கொன்றதென்று வந்தனனே மாயனுடன் வாதாடி யுத்தமிட அப்போது மாயன் ஆராய்ந்து தான்பார்த்து இப்போ திவனை ஈடுசெய்யக் கூடாது சத்தபெல மல்லோ சராசந்த வேந்தனுக்குக் கொற்றவ வீமனல்லோ கொல்லவகை யுண்டுமென்று இதுவேளை தப்பவென்று எம்பெருமாள் தானடந்து     செதுவோடு கூடிச் செகலதுக்குள் ளேகினரே கடலதுக்குள் வந்து கனத்தமதி லிட்டவரும் திடமுடனே மாயன் துவரம் பதியிருந்தார் பூதனைச் சகட னோடு பின்னுள்ள அரக்கர் தம்மைச் சூதனை யெல்லாங் கொன்று சுத்தமாய் மதுரை புக்கி நீதமே யில்லாக் கஞ்சன் நெஞ்சையும் பிளந்து கொன்று கூதவன் துவா ரகையில் குணமுட னிருந்தா ரன்றே  கஞ்சனையு மற்றுமுள்ள காலவுணர் தங்களையும் வஞ்சகமா யுள்ள வாணநர பாலனையும் இம்முதலா யுள்ள ஏற்ற அரக்கரையும் அம்முதலா யுள்ளவரை அரியோ னறுத்தனராம் சத்தபெல முள்ள தத்துவத்தார் தங்களையும் வித்தகனார் கொல்ல மேல்நினைத்தா ரம்மானை கஞ்சன் வலிமை கட்டழித்துத் தேவருக்கு அஞ்ச லருளி ஆழிக்குள் வீற்றிருந்தார் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(69,'ருக்மணி கல்யாணம்','வாரிக்குள் கோட்டை வளைந்துமணி மேடைவைத்து வீரக் குருநாதன் வீற்றிருந்தா ரம்மானை மாயன் துவாரகா பதிவாழ்ந்து தானிருக்க நாயனுக்கு வந்த நல்ல உருப்பிணியை விதியை யறியாமல் மேலுமொரு ராசனுக்குத் திருமாங் கலியம் சேர்க்கத் துணிந்தனராம் அப்போது நாரதரும் அரியோ னடிபணிந்து இப்போது வுன்றனக்கு இசைந்த உருப்பிணியைப் பாணிக் கிரணம் பண்ணப் பறையடித்தார் நானிதையுங் கேட்டுநாடியுரைத்தே னென்றார் அம்முனிவன் சொல்ல அரியோன் மிகக்கேட்டு இம்முனிந்து போக இசைந்தார்கா ணம்மானை  மோகத்திரு மாலை முக்கோடி பொன்னதுக்கு வேகத்தா லுண்டுபண்ணி விரைவாய்க் கொடுநடந்தார் முழுத்திரு மாலைதன்னை மொய்குழலாள் கன்னியுடக் கழுத்திலே யிட்டுக் காமக்கண் ணீட்டிடவே மாயனுக்கும் மோகம் மாதுக்கும் மும்மோகம் தேயமெங்கும் மோகம் சென்றதுகா ணம்மானை முன்னாலே கேட்டு முகூர்த்தமிட்ட பேர்களையும் அந்நாளே கொன்று அவன்படையுந் தானறுத்து உருப்பிணிக்குத் தேவரெல்லாம் ஓலமிட மாலையிட்டு விருப்புகழ்ந்த மாயன் விமான மதிலேறி மனோன்மணியை யுங்கூட்டி மாயன் படையுடனே வினோகர மால்தானும் விரைவாய் நடைநடந்து வடவெல்லாந் தீர்த்து வைகுண்டப் பெம்மானும் கடலுக்குள் சென்றிருந்தார் காயாம்பு மேகவண்ணர் இப்படியே முன்னம் இசைந்திருந்த பெண்ணையெல்லாம் அப்படியே மாலையிட்டு அமர்ந்திருந்தா ரம்மானை ஆயர்குடியில் வளர்ந்து வெண்ணெய் அருந்தி முறைமா தரையணைந்து தீயன் கொடிய கஞ்சனையும் திருக்கி யறுத்து அசுரரையும் உபாய முடனே கொலையடக்கி உருப்பிணி முதலாய்ப் பெண்களையும் தேயம் புகழ மணமுகித்துத் துவரம் பதியிலி ருந்தளரே  உருப்பிணி முதலாய் ஒத்துவந்த பெண்களையும் திருப்பொருத்தம் பூட்டிச் செகலதுக்குள் வீற்றிருந்தார் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(70,'பாண்டவர் வரலாறு','கஞ்ச னிடுக்கம் கழித்தந்தக் காரணரும் பஞ்சவர்க்கு நன்மைசெய்யப் பார்த்தனர்கா ணம்மானை பிறந்த துரியோதனனும் பிறவியொரு நூற்றுவரும் சிறந்தபுக ழைபேரும் தேசமதி லேவாழ்ந்து அவரவர்க்குத் தக்க ஆர்க்கமுள்ள வித்தைகற்று எவரெவரு மெய்க்க இவர்வளர்ந்தா ரம்மானை வளர்ந்து நிமிர்ந்து வரும்வேளை யானதிலே இழந்துருகி வாடும் இசைகெட்ட மாபாவி துடியாய் மனுவழக்குச் சொல்லித்துரி யோதனனும் முடிய வினைசூடி உலகாண்டா னம்மானை பாவி யிருந்து பாராண்டச் சீமையிலே கோவுகட்கு  நீர் குடிக்கக் கிடையாது தன்ம ரவ்வீமன் சகாதே வன்விசயன் நன்மை பரிநகுலன் நாடான நாடதுதான் குருநா டெனவே கூறுவா ரந்நகரு திருநாடு தன்னுடைய சிறப்புக்கே ளம்மானை துரியா தனாதி செலுத்துமஸ்தி னாபுரத்தில்  பரியொட் டகமும் பலிமிருக மானதுவும்  பசியால் தகையால் பட்சி பறவைகளும் விசியாய்க் குருநகரில் மேவித்தகை யாறிவரும் அத்தினா புரத்தில் அரதேசி யாவரெல்லாம் பத்தியா யுள்ள பஞ்சவர்க ளாண்டிருக்கும் குருநாடு தன்னில் குழாங்கொண் டிருந்தனராம் திருநாட்டுக் கொவ்வும் சிறந்த குருநாடு ஒருநாடு மந்தக் குருநாட்டுக் கொவ்வாது பருநாடு பத்தியுள்ள பஞ்சவர்கள் தந்நாடு தேவரும் வானவரும் தெய்வத் திருமாலும் மூவரும் நன்றாய் உகந்த குருநாடு ஆளியொடு சிங்கம் ஆனையிறாஞ் சிப்புள்ளும் வேளிசமா யுள்ள வெகுவைந் தலையரவும் வெள்ளானை வெள்ளை மிகுசாரை யானதுவும் துள்ளாடி நித்தம் துலங்கிவரும் நன்னாடு அந்நாடு நாடு அரன்நாட்டுக் கீடாகும் பொன்னாடு நாடு புத்தியுள்ளோர் தந்நாடு அரனருளைப் பெற்றிருக்கும் ஐவருட நன்னாடு இரவலர்க்கு ஈயும் ஏற்றதர்மர் தன்னாடு மாயனருள் பெற்ற மன்னவர்கள் தந்நாடு தாய்நாடு ஆனத் தமிழ்க்குரு நன்னாட்டில் மெய்யில்லா மன்னனுக்கு மேதினியில் பேர்பாதி பொய்யில்லாத் தர்மருக்குப் பேர்பாதி யாகவேதான் ஆண்டார் சிலநாள் ஆளுக்கோர் பங்காகத் தாண்டவ ராயர் தண்மையா லம்மானை ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(71,'பாண்டவர் வனவாசம்','இப்படியே ஆண்டு இருக்குமந்த நாளையிலே முப்படியே விட்டகுறை முடிவாகும் நாளையிலே வணங்கா முடியுடைய மன்னன்துரி யோதனனும் இணங்காமல் பிணங்கி ஏதுசெய்தா னம்மானை தலைவீதம் பங்கு தான்வையா வண்ணமுந்ததான் நிலைபகிர்ந்து விட்டோமே நினைவுசற்று மில்லாமல் இனியவன் பூமிதனை யாம்பறித்து ஐவரையும் தனியே வனத்தில் தானனுப்பி இராச்சயத்தை அடக்கி யரசாள அவன்நினைத்து மாபாபி உடக்கிச் சூதுபொருத்தி ஒட்டிவைத்தான் ஐவரையும் பாவிதுரி யோதனனும் பஞ்சவரைத் தான்விரட்டி சோவிதமாய் நாட்டைச் சுற்றியர சாண்டிருந்தான் வனவாசந் தன்னில் வந்திருந் தைபேரும் இனமா னதுபோல் இருந்தார் குகையதிலே அப்போது வேத வியாச ரவறிந்து செப்போடு வொத்தத் திருமா லருகேகி           மாயவரே பஞ்சவர்க்கு வாரமதாய்த் தானிருந்து தீயதுரி யோதனனைச் செயிக்கவந்த பெம்மானே பஞ்சவரை மாபாவி பழுதுசூ தாடிவென்று வஞ்சக மாய்ப்பாவி வனத்தில் துரத்திவிட்டான் ஐபேரும் பத்தினியும் அன்னை பிதாவுடனே பைப்போ லலறிப் பசித்திருந்து வாடுகிறார் அன்றுமகா மேர்வில் அடியே னுரைத்தபடி இன்றுபா ரதமுடிக்க எழுந்தருளும் நாளாச்சு என்று முனிதான் எடுத்துரைக்க மாயவரும் அன்று திருமால் ஐவ ரிடம்நடந்தார் வேத வியாகரரும் வேயூதும் வாயானும் சீதக்குணத் தர்மர்முன்னே சென்றனர்கா ணம்மானை நாரா யணர்வரவே நல்லதர் மாதிகளும் பாரா னதையளந்தோன் பதம்பூண்டா ரம்மானை கால்பிடித்துத் தர்மர் கண்ணர் பதந்தொழவே மால்பிடித்துத் தர்மரையும் மார்போ டுறவணைத்துப் பதறாதே பாண்டவரே பத்தியுள்ள பஞ்சவரே கதறாதே ஐவரையும் காத்தருள்வோ மென்றுரைத்தார் அப்போது தர்மம் அச்சுதரை யும்போற்றி இப்பெழுது எங்களுக்கு ஏற்ற பசிதீர  சூரிய பாண்டம் தனையழைத்துச் சுத்தமனே ஆரிய மான அன்ன மருளுமென்றார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(72,'பாண்டவர் வனவாசம்','அப்போது மாயவனார் ஆதிதனை நினைத்து மெய்ப்பான பாண்டம் மிகவருத்தி ஓரரியும் கையா லெடுத்துக் கனத்ததர்மர் கைக்கொடுத்து ஐயா யிரங்கோடி ஆட்கள்மிக வந்தாலும் என்பே ரரிதான் எனைநினைந்து பாண்டமதில் அன்பரே நீரை அதுநிறைய விட்டவுடன் என்பே ரரியை எடுத்ததி லிட்டதுண்டால் அன்பாக எல்லோர்க்கும் அமுதாய் வளருமென்றார் இத்தனையுஞ் சொல்லி ஈந்தாரே யன்பருக்கு சித்திரம்போல் வேண்டித் தெளிந்திருந்தா ரம்மானை மாயனுஞ் சொல்லி மண்டபத்தில் போயின்பின் தூயவியா கரரும் சொல்லுவார் தர்மருடன் பஞ்சவரே உங்களுக்குப் பச்சைமா லின்றுமுதல் தஞ்சமென்று சொல்லித் தான்போனார் மாமுனியும் மாமுனியும் போக வனத்திலந்த ஐபேரும் ஓமுனியே தஞ்சமென்று உகந்திருந்தா ரம்மானை பாவிதுரி யோதனனும் பஞ்சவரைக் கொல்லவென்று ஆவி யவன்செய்த அநியாய மத்தனையும்  ஒக்க வொருமிக்க உரைக்கக்கே ளொண்ணுதலே சிக்கெனவே நஞ்சைத் தீபாவி யிட்டனனே நஞ்சைக் கலந்து நல்லதயி ரென்றீந்தான் அஞ்சலென்று மாயன் அதுகாத்தா ரம்மானை பாதாளம் வெட்டிப் பார்வீ மனையோட்டி நீதாள மான நெடியோ னதுகாத்தார் கன்னிதனிற் பாவி கழுநாட்டி ஐவரையும் கொன்றுவிட வைத்ததையும் குன்றெடுத்தார் காத்தனரே அரவதையும் விட்டு அருள்வீம னைவதைத்தான் விரைவுடனே மாயன் விசந்தீர்த்துக் காத்தனரே தண்ணீரில் நஞ்சைவிட்டுச் சதித்தானே ஐவரையும் மண்ணீரேழு மளந்த மாயனது காத்தார் பூதத்தை யேவிப் புல்லிசெய்தான் மாபாவி நீதத் திருமால் நிலைநிறுத்தி யாண்டனரே இப்படியே பாவி இடறுசெய்த தோசமெல்லாம் அப்படியே மாயன்காத்து ஆண்டனரே ஐவரையும் பாவியவன் செய்ததெல்லாம் பலியாம லைபேர்க்கும் சோவிதமாய் மாயன் துணைசெய்தா ரம்மானை ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(73,'துரியோதனன் பாடு','பின்னுமந்தப் பஞ்சவர்க்குப் பெரும்பாவி சொன்னபடி பன்னிரண் டாண்டு பரிவாய்க் கழிந்தபின்பு  மாயன் தூதுபோனார் வஞ்சமில்லாப் பஞ்சவர்க்குத் தீயதுரி யோதனனும் திருமாலைப் பாராமல் தாள்போல் புத்தி தானுரைத்துப் பஞ்சவர்க்கு வாழ்வுபெறப் பூமி வாரென்றார் வாமனுமே எள்போ லிடங்கள் ஈயேனென வுரைத்தான் மாயனையும் பாவி வாபோ வெனப்பேசி ஈயேனெனச் சொன்ன இயல்புகேட் டெம்பெருமாள் கன்னன் பிலமும் கடியவிது ரன்பிலமும் மன்னன் சிறுபீஷ்மர் வாழுந்துரோ ணர்பிலமும் தென்னன் துரியோ தனன்பிலமுந் தானழித்து வன்ன விசயனுக்கு வாளி பலகொடுத்து வீமனுக்கு நல்ல விசைதண்டா யுதங்கொடுத்துத் தாமன்சகா தேவனுக்குச் சத்திசூலங் கொடுத்து நகுலனுக்கு ஆயுதமும் நல்லபரி கொடுத்துப் புகலான தர்மருக்குப் பொறுமை அரிகொடுத்து மங்கை துரோபதைக்கு வாய்த்தக் கனல்கொடுத்துச் செங்கையிலே பாரதத்தைச் சேவித்தார் மாயவரும் மாயருட கையில் வளர்ந்த திருமுடியில் ஓசையிட வொன்று ஒத்துதே பாரதப்போர் ஐபேருட படையும் அரவக்கொடி யோன்படையும்  கைப்போரு விற்போரும் கணைப்போரும் வாள்போரும் அம்புப்போ ருங்கரியின் அச்சுப்போரும் பொருது கன்னன் சகுனி கனத்தவலுச் சல்லியனும் மன்னன் சிறுபீஷ்மர் வாய்த்த துரோணர்முதல் சராசந் தன்வரையும் சத்தியக் கீசகனும் பூராச வீமன் பெலியிட்டா னம்மானை இத்தனைப்பேர் மாண்டால் இருப்பாரோ நூற்றுவரும் அத்தனைபே ரையும் அறுத்தா னருச்சுனனும் துரியோ தனன்படைகள் சேர மடிந்தபின்பு விரிமாறு தூவி வெளியில்வந்தான் மாபாவி தம்பி படைகள் தலைவர் புதல்வர்முதல் வம்பி லிறந்தாச்சே வாழ்வெதே னென்றுசொல்லி எல்லா ரிறந்திடிலும் எண்ணமில்லை யென்றிடலாம் வெல்லாரும் வெல்லா விசகர்ணன் மாண்டதினால்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(74,'துரியோதனன் பாடு','இருப்பதோ பூவுலகில் இறப்பதுவே நன்றெனவே விருப்பமுள்ள கர்ணனைத்தான் வெற்றிகொண்ட அர்ச்சுனனை இன்றுகொல்ல வென்று எழுந்தான் படைக்கெனவே அன்றுமால் தானறிந்து அருச்சுனனைத் தானழைத்து இத்தனை நாளும் என்னபோர் செய்தாய்நீ அத்தினா இன்றுன்மேல் அரவக் கொடியோனும்  வாறான் படைக்கெனவே வாள்வீமனை யழைத்து உன்றனக்கு நல்ல உறுவேட்டை யின்றடவா என்றனக்கு இன்றுமுதல் இளைப்பாற லாமடவா வண்ண மகள்தனக்கு மயிர்முடித்த லின்றடவா எண்ணமற்றுத் தர்மர் இருப்பதுவு மின்றடவா என்றந்த வீமனுக்கு இசைந்தபோர்க் கோலமிட்டு வண்டுசுற்று மார்பனுக்கு வரிசைமிகக் கொடுத்து வீமனுட தண்டதுக்கு விசைமால் விசைகொடுத்துப் போமெனவே வீமனுக்குப் போர்க்கு விடைகொடுத்து விசையன் பரிநகுலன் வெற்றிச்சகா தேவனையும் இசையொத்த தர்மரையும் இன்றகல நில்லமென்று வீமனை யுங்கூட்டி வெளியிலரி வந்திடவே காமக் கனல்மீறிக் கரியோ னெழுந்ததுபோல் வந்தானே பாவி வணங்கா முடியோனும் சந்தான மான தமிழ்வீமன் தான்மாறி எதிர்த்தா ரிருவர் எனக்கெனக் கென்றேதான் செதுத்தான் வலுமை செய்வதுகே ளம்மானை மண்விண் ணதிரும் வானமது தானதிரும் திண்திண் ணெனப்பூமி தொந்தொ மெனக்குலுங்கும் மலைக ளசைந்து மடமடென ஓசையிடும்  கலைகள் கரிகள் கதறி மிகஓடும் அலைகள் சுவறி அங்குமிங் கோடிடவே இலைக ளுதிர்ந்து இடிந்துவிழு மாமரங்கள் தூளெழும்பிச் சூரியனைத் தோன்றாம லேமறைக்கும் வேöழுழும்பிக் காட்டில் விழுந்தலறி ஓடிடவே தவமுனிவர் நெட்டை தானெகிழ்ந்து தட்டழிந்து திசைமாறித் திக்கில் திரிந்தலைந்து போயினரே வேதா சிவனும் வெம்மருண்டு தாம்பதற நாதாந்த மோதும் நன்மறையோர் தாம்பதற துரியோ தனன்போரும் செயவீமன் றன்போரும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(75,'துரியோதனன் பாடு','எரியோ டெரிதான் எதிர்த்துப் பொருதாப்போல் ஒண்ணுக்கு வொண்ணு ஒல்கிப்புறஞ் சாயாமல் மண்ணும்விண் ணுமதிர மண்டி யுத்தமிட்டார் துரியோ தனனடிக்கத் துடிவீமன் சாயாமல் மரியாதை வீமன் மாறிய வனடிக்க அடிக்க அவன்பிடிப்பான் அப்படியே சண்டையிட்டுச் சாயா விதத்தைத் தானறிந் திருபேரும் வாயால் சபதம் வகுத்தே பொருவோமென்று கட்டான கள்ளன் கசடன் வெகுகெடும்பன் துட்டாள னான துரியோதன னுரைப்பான்  இத்தனை நேரம் இருபேருஞ் சண்டையிட்டுப் புத்தியில்லா வண்ணம் பொருதோமே வம்பாலே உன்பெலமு முன்னுடைய உயிர்ப்பெலமும் நீயுரைத்தால் என்பெலமு முன்னோடே இயம்புகிறே னென்றுரைத்தான் அப்போது வீமன் அவனிலைக ளத்தனையும் தப்பாமல் தானுரைத்தான் சகலோ ரறிந்திடவே கேட்டுத்துரி யோதனனும் கேளுநீ யென்பெலங்கள் தீட்டுகிறே னென்னிடது செய்யபுற மென்றுரைத்தான் கள்ளக் கௌசலமாய்க் கபடுரைத்த ஞாயமெல்லாம் எவ்வளவு போலே இசையாத வீமனுக்கு மெய்யுரைத்தா னென்று மேலான போர்வீமன் கையாரத் தண்டால் கனக்க அடித்தனனே அடிக்கவே வீமன் அசையாமல் மற்றோனும் திடத்த முடனே சென்றவ் வீமன்பேரில் மாறி யவனிடிக்க வாட்டமுற்றுப் போர்வீமன் தேறியே மாயவரைச் சிந்தை தனில்நினைக்கத் திலர்தமுள்ள மாயன் செயவீ மனைநோக்கி வலது துடைதனிலே மாயன் கண்காட்டிடவே அடித்தானே வீமன் அதுதான் குறியெனவே துடித்தான் கிடாய்போல் துரியோதனன் விழுந்து  மூடற்ற மாமரம்போல் முறிந்துகீழ் தான்விழுந்தான் கூடற்ற உயிர்போல் குலைகுலைந்து வீழ்ந்தனனே அப்போது மாயன் அரவக்கொடி யோனிடத்தில் தப்பாமல் வார்த்தையொன்று தான்கேட்கப் போயினரே முன்னே பிறப்பில் முடியிலங்கை யாண்டிருந்தாய் தென்ன னிராவணனாய்ச் சிரசுபத்தாய்த் தானிருந்தாய்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(76,'துரியோதனன் பாடு','அப்போது நீதான் அநியாயஞ் செய்ததினால் செப்போடு வொத்த ஸ்ரீராமனாய் நான்தோன்றி பத்துச் சிரசறுத்துப் பார்மீதிலே கிடத்தி உற்று வொருவசனம் உரைத்தேனா னுன்னிடத்தில் தம்பியா லென்னை சரமறுத்தா யல்லாது எம்பிரா ணன்வதைக்க ஏலாது என்றனையே தம்பியொரு நூறோடே தான்படைத் துன்னையுந்தான் கொம்பிலொரு ஆளைவிட்டுக் கொன்றேனே உன்னையென்றார் என்றுரைக்கப் பாவி இகழ்த்துவா னப்போது தண்டுகொண் டேயடித்த தமிழ்வீ மனல்லாது இன்றுன்னா லேலாது இடையாபோ வென்றனனே அப்போது மாலும் அதிகக்போ பம்வெகுண்டு துப்புரைகள் கெட்ட தீயனுக்கங் கேதுரைப்பார் உன்னையின்ன மிந்த உலகி லொருபிறவி  சின்னவன்ன மாகச் சிரசொன்றாய்த் தான்படைத்து அறிவுபுத்தி யோடும் ஆணுவங்கள் தன்னோடும் செறியுங் கலையோடும் சிறப்போடுந் தான்படைத்து என்பேரி லன்பு இருக்கவெகு சாஸ்திரமும் தன்போத மறியத் தான்படைப்பேன் கண்டாயே முன்னே வுனக்கு உற்றபிறப் பாறதிலும் என்னை நினைப்பு எள்ளளவும் நம்பவில்லை ஏழாம் பிறப்பதிலும் என்னைநினை யாதிருந்தால் பாழாவாய் மேலும் பகையில்லை யென்றனக்கு என்று திருமால் இயம்பித்துரி யோதனனை அன்றவனைக் கொன்று ஐவரையுந் தான்வருத்திக் கர்மச் சடங்கு கழிக்க விடைகொடுத்தார் தர்மமுள்ள கர்ணனுக்கு சாஸ்திரத்தி லுள்ளமுறை எல்லாச் சடங்கும் இவருக்கும் நூற்றுவர்க்கும் உல்லாச முள்ளதர்மர் ஒக்கமுறை செய்தனராம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(77,'கர்ணனுக்கு முத்தியருளல்','முறைசெய்து கர்ணனுக்கு முத்திமோக்ஷங் கொடுக்க மறைதேர்ந்த மாயன் வந்தா ரவனருகே அப்போது வேத வியாசரவ ரங்குவந்து செப்போடு வொத்த திருமாலோ டேதுரைப்பார் பாவியோடே கூடிப் படைசெய்த கர்ணனுட  ஆவிக்கு மோக்ஷம் அருளுவதோ மாயவரே என்றுரைக்க வியாசர் எடுத்துரைப்பா ரெம்பெருமாள் நன்றுநன்று மாமுனியே நானுரைக்கக் கேட்டருள்வாய் பண்டு இலங்கைப் பாரழிக்கவே நினைந்து தொண்டுபண்ணி நின்ற துய்யவா னரங்களிலே வல்லபெல முள்ள வாலியிவன் முற்பிறப்பில் நல்லவனாய் முன்னே நாட்டி லிருக்கையிலே இராவணனோ டேகூடி இராமசரத் தாலிறந்தான் சிராமனாய் நானிருந்து செயித்த விதமறிந்து வந்து பணிந்தானே வாலியவ னென்காலில் நன்றியுள்ள மாலே நானுமுன் னேவலனாய் முன்னேநீ ரமிழ்தம் உவரிதனிற் கடைய என்னை யொருபுறமாய் ஏவல்கொண்ட மாயவரே பத்துத் தலையுள்ள பாவியந்த ராவணனைக் கொத்திச் சிரசறுத்துக் கொல்லேனோ நானடியேன் என்றந்த வாலி இறைஞ்சிநின்றா னென்னையுமே அன்றுவா லிதனக்கு அருளினது நீர்கேளும் எனக்கேவ லாக இப்பிறவி நீபிறந்து தனக்கேராப் பாவிதனைச் சங்காத்தங் கொண்டதினால் இனிமே லவனோடிருந்து என்சொல் கேட்கவைத்துக் கனியான மோஷக் கயிலாச மேதருவேன் என்றவனை யழித்து இப்பிறவி ஐவருடன் முந்தி உதித்து முதற்பிறவி செய்தவனை');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(78,'கர்ணனுக்கு முத்தியருளல்','அரவக் கொடியோன் இடத்தி லனுப்பிவைத்து இரவலர்க்கு மீந்து என்புத்தி யுள்ளிருத்தி அன்னை பிதாசொல் அசராம லைபேர்க்கு ஒன்னே வொருகணைமேல் விடேனென்ற உத்தமன்காண் ஆனதால் முன்னே அருளிவைத்த சொற்படிக்கு மானமாய் மோக்ஷம் வகுத்தே னிவனுக்கென்றார் நல்லதுதா னென்று நன்முனிவன் தான்மகிழ்ந்தான் எல்லைவைத்தா பாரதப்போர் இன்று முடிந்ததென்று கொண்டாடி ஐபேரும் குருமுனியைத் தெண்டனிட்டு வண்டாடும் வண்ணமகள் மயிர்முடித்து நீராடி ஐபேரும் பத்தினியும் அச்சுதரை யும்போற்றி மெய்போக மான வியாசரை யுங்குவித்து ஆண்டார்கள் சீமை அச்சுதனா ருண்டெனவே பாண்டவர்கள் நன்றாய்ப் பாராளும் நாளையிலே வைகுண்ட மேக மனதிலுற்று எம்பெருமாள் பொய்கொண்ட வேசம் பொருந்திப் பொருப்பேறி ஆங்கார மோகத்(து) அம்புக் கணுவாலே  ஓங்கார மாமுனிவன் விடையேற்றுத் தான்மெலிந்து பஞ்சவர்க்கு உள்ள பாரப் பெலங்களையும் துஞ்சிவிட வாங்கித் தோற்றமுள்ள ஐவருக்கு மேல்நடப் புள்ள விசளமெல்லாந் தானுரைத்து நூல்நடந்து வாருமென்று மோக்ஷத் திறவோனும் சீரங்க மாபதியில் செல்கின்ற அப்பொழுது சாரங்கர் செய்த தன்மைகே ளம்மானை');");


