        db.execSQL("INSERT INTO akilathirattu2 VALUES(101,'கலியன் பிறப்பு','சீரங்க மானதிலே சீரங்க நாதருந்தான் பாரெங்கு மெய்க்கப் பள்ளிகொண் டிருக்கையிலே நாட்டுக்குக் கேடு நாட்பிடித்த செய்திதன்னை கூட்டுக் கிளியே கூர்மையுடனே கேளு முன்னே குறோணி முடிந்ததுண்ட மாறதிலே அன்னேகே ளஞ்சுதுண்டம் அவனி தனிலேபிறந்து ஆதி தனைநினைத்து ஆளாம லவ்வுயிர்கள் நீதிகெட்ட மோச நீசனைப்போல் தானாகி முப்பிறவி யோடாறும் முதலோனைப் போற்றாமல் அப்பிறவி யாறும் அழித்ததின்மே லாயிழையே குருமுனிவ னான கூர்மையுள்ள மாமுனிவன் அருமுனிவ னான ஆதிமுனி யைநோக்கி சிவனெனவே போற்றிச் சொல்லுவான் மாமுனிவன் தவமே தவப்பொருளே தாண்டவசங் காரவனே முன்னே பிறந்த குறோணிதனை மூவிரண் டாக வுடல்பிளந்து தன்னே வுயிரோ டஞ்சுசெய்து சுவாமி யுனைநினை யாத்ததினால் வன்னத் திருமால் கொலையடக்கி வந்தார் சடல மிழந்துகுண்டம் இன்னம் பிறப்பொன் றுண்டல்லவோ இறந்த குறோணி யவன்தனக்கே வாசமுள்ள நேசா மற்றொப்பில் லாதவனே  ஈசனே நானுமொன்று இயம்புகிறேன் கேளுமையா குறோணி யவனுடலைக் கூர்மையுட னேபிளந்து சுறோணித மாயன் தொல்புவியில் விட்டெறிய அஞ்சுதர முயிரு அவனிதனி லேபிறந்து பிஞ்சுமதி சூடும் பிஞ்ஞகனைப் போற்றாமல் மாயனையு மெண்ணாமல் மதமா யிருந்ததினால் ஆயனந்தச் சூரரையும் அழித்தார்கா ணீசுரரே முச்சூர னோடாறு யுகமு முயிரழித்து அச்சூரக் குடும்பம் அறுத்தந்த மாயவரும் கொண்டிருந்த பொய்ச்சடலக் கூட்டை மிகக்களைந்து ஆண்டிருந்த குண்டம் அவரடைந்தா ரம்மானை முச்சூரன் துண்டம் ஒன்றுண்டு ஈசுரரே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(102,'கலியன் பிறப்பு','அச்சூரன் பிறவி ஆறொன் றீரரையாய் ஆறாம் யுகத்தில் அமையுமென்று மாமுனியும் வீறாக ஈசுரரை விண்ணப்பஞ் செய்துநின்றான் நல்லதுதா னென்று நாட்டமுற்று ஈசுரரும் வல்லவனே மாமுனியே மாயன்வர வேண்டாமோ என்றுநல்ல ஈசர் இயம்பமுனி யேதுசொல்வான் நன்றுகாண் மாயவர்க்கு நாலுரண்டோ ராம்பிறவி பிறக்கத் தவசு புரியப்போய்க் கீழுலகில்  உறக்க மெய்ஞ்ஞான ஒளியிருக்கி றாரெனவே தேவமுனி சொல்ல சிவனா ரகமகிழ்ந்து கோவுகத்தி லுள்ள குருவசிஷ்டர் தங்களையும் தெய்வலோ கத்திலுள்ள தேவரை யும்வருத்தி வைந்தலோ கத்திலுள்ள வாய்த்ததர்மி தங்களையும் கிணநாதர் கிம்புருடர் கிங்கிலியரை யும்வருத்தி இணையாக மேலோகத்(து) எல்லோரை யும்வருத்தி சத்தி மறையும் சாஸ்திரத்தை யும்வருத்தி அத்தி முகவனையும் ஆனவ கும்பனையும் சங்கமறையை வருத்தித் தான்கேட்பா ரீசுரரும் இங்குண்டோ மாலும் எங்கிருப்ப துண்டனவே எல்லோ ருங்கூடி இதமித்துச் சொல்லுமென்றார் அல்லோ ருங்கேட்க அரன்சொன்னா ரம்மானை வேழமொத்த தேவரெல்லாம் மேகவண்ண ரிங்கேயில்லை கீழுலகில் மாண்டு கிடக்கிறா ரென்றுசொல்லி ஒன்பதாம் பிறவி ஒடுங்கி யவர்பிறக்க இன்பமறக் கீழுலகில் இறந்துகிடக்கி றாரெனவே எல்லோருஞ் சொல்ல ஈசுரரும் நல்லதென்று அல்லோருங் கேட்க அவரேது பின்சொல்லுவார் முன்னே பிறந்த முண்டசுரன் தன்னுயிரைத்  தன்னோ டாறுபிறவி தான்செய்தோ மசுரரென ஆறு பிறவியிலும் அசுரரென வேபிறந்து வீறுடனே நம்மை விரும்பவும்நாம் கண்டிலமே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(103,'கலியன் பிறப்பு','இப்போ தவன்தனக்கு ஏழாம் பிறவியிது அப்போதும் நம்மை அவன்நினை யாதிருந்தால் இல்லை மேற்பிறவி இறப்புமுடி வாகுமல்லோ நல்ல பிறவியதாய் நமைத்துதிக்கப் புத்தியதும் அழகு சவுந்தரியம் அலங்கார மெய்யறிவும் குழவு மிகப்புத்தி கூர்மையலங் காரமுடன் மேலு மாலாறு முடத்த யுகங்களுக்கு நாலுமுழ மொன்றுதலை நல்லோ ரசுரர்களை கொன்றுதா னவ்வசுரர் கூறுதற்குச் சொல்லுமுண்டே இன்னமிந்த மாயன் எடுத்த வுருப்போலே படைத்துநாம் வைத்தால் பகருமொழி வேறில்லையே நடத்துவ தேதெனவே நவிலுமென்றார் தேவர்களை அப்போது தேவர்களும் ஆனமறை வேதியரும் எப்போது மிறவா(து) இருக்கும் முனிவர்களும் நல்லதுதா னென்று நாடி யகமகிழ்ந்து வல்லப் பிறவி வகுக்கத் துணிந்தனராம் பிறவி வகுக்கவென்று பெரியோ னகமகிழ்ந்து திறவி முதலோன் தெளிந்துநிற்கும் வேளையிலே துண்டமது பிண்டமதைத் துளைத்துக்கால் மேல்நீட்டி மண்டைகீழ் கால்மேலாய் வந்துதித்தா னம்மானை எல்லோருங் கண்டு இதுகண் மாயமெனவே அல்லோரும் வந்து ஆதியோ டேவுரைத்தார் உடனே சிவமும் உள்ள மிகக்களித்து இடமே தான்பேர்ந்து எழுந்தருளி ஈசுரரும் அதிசயத்தைப் பார்ப்போமென்று அவரேகும் வேளையிலே எதிராக நின்றுநந்தி ஏதுரைப்பா ரம்மானை மூவர்களுந் தேவர்களும் மூர்த்திகளுங் காணாத தேவரீ ரெழுந்தருளி திசைநோக்கி வந்ததென்ன அப்போது நந்தியுடன் ஆதி மிகவுரைப்பார் இப்போ தோரற்புதமாய் இருக்குதென்றா ரெல்லோரும் வானோக்கிக் காலும் மலர்நோக்கிச் சிரசும் தானமது மாறி தலைகீழாய் நிற்பதுவும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(104,'கலியன் பிறப்பு','பார்த்துவர லாமெனவே பையநடை கொண்டேனென்றார் சாற்றுமொழி கேட்டுநந்தி தானுரைப்பா ரம்மானை அங்கல்லத் தானம் அழிந்ததுகா ணீசுரரே இங்கு நான்கண்டேன் எழுந்தருளி வந்ததினால் தலந்தா னிளகி தான்வந்த தாலேதான் குலந்தா னழியும் குசல்பிறக்கும் ராச்சியத்தில் வரம்பழியு மாரி மனுநீதங் குன்றுமையா பரம்பெரிய வேதம் பழுதுவருங் கண்டீரே என்றந்த மாமுனியும் ஈசுரரை யுங்கூட்டி குன்றுபொன் னான கிரிமேவித் தானிருந்தார் நடந்த புதுமையென்ன நாம்வருத்திப் பார்க்கவென்று திடந்தெளிந்த மாமுனிவன் செப்பவே ஈசுரரும் சாத்திர வேதச் சதுர்மறையைத் தான்வருத்தி சித்திர புத்திரரைச் சீக்கிரத்தி லேயழைத்து வந்த புதுமை வகையென்ன நம்கணக்கா இந்தக் கணக்கை எடுத்துரைநீ யென்றனராம் அப்போது ஈசுரரை அன்போ டுறவணங்கி செப்போடு வொத்த சித்திரருஞ் செப்பலுற்றார் சாத்திரத்தி லுற்ற தன்மை மிகக்கேளும் சீத்துவ மாகச் சித்திரரும் செப்பலுற்றார் முன்பிறந்த குறோணி உடலாறு துண்டதிலே தன்பிறவி யோடாறு தான்பிறந்தான் சூத்திரமாய் மண்தா னுடம்பு வந்துதித் தோன்றனக்கு விண்தா னுடம்பு விலாசக் குருவோடு சலந்தா னுடம்புக்(கு) உறுதி தைரியங்கள் வலந்தா னிளகி வன்னியோ டுங்கூடி கலந்து திரண்ட கட்டைமுண்ட மானதுக்குப் பிலந்தூக்கும் வாயு பிராணன்கா ணீசுரரே முப்பழியோ டாறு உயிரழித்த மாயனுக்கு இப்போது வந்த இவன்தான் கொடிதுவையா மாயனாலு முழமாய் மனுச்சிங் கமுகமாய்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(105,'கலியன் பிறப்பு','வாயமாய் நம்மை வதைத்தானே யென்றுசொல்லி அன்றா யனெடுத்த அளவா யுருவெடுத்துச் சென்றா லவனுடைய திறத்தைநாம் பார்ப்போமென்று வேகத்தால் துண்டம் வெடித்ததுகா ணிப்புதுமை ஏகந்தா னாளும் ஈசனே யென்றுசொல்லி இப்படியே சித்திரரும் எடுத்துரைக்கவே ஈசர் அப்படியே வந்தவனுக்(கு) ஆயுசு மவன்பலமும் கரணமுதல் நடப்பும் கட்டாக நீதேர்ந்து மரணமுதல் நடப்பும் வகுத்துரைநீ நம்கணக்கா அப்போது சித்திரரும் ஆதியருள் நெஞ்சில்வைத்துப் பொற்பாத முண்டெனவே புகல்வா ரியல்கணக்கர் அப்பனு மம்மை அடங்கிமிகப் பெறாமல் கொப்பளித்துத் தானாய்க் குறுத்துவந்த நீசனுக்குத் தத்துவந் தொண்ணூற்(று) ஆறுந் தடிக்குணந்தான்  புத்தி புலனைந்தும் பொய்ப்பூண்ட பூதமுமாய் சத்துருக்கன் கண்கால் தலையும் வெறும்நீசம் உற்றுணர்ந்து பாராத உடலு மவன்காலும் மாயக் காயமதுக்கு வருசமொரு நூறிருப்பு தோயநாதத் துளிர் தொகைபத்துநூ றாயிரந்தான் வாழ்வுவந்து சேர்க்கை வருசம் பதினாலு தாழ்வு தசைநரம்பு சனித்தமுப்பத் தோராண்டில் ஆர்க்கை எலும்பு அடைக்கும் நரம்புடனே மூர்க்கத் தசையும் உதிரப் புனலுடம்பும் மண்தண்ணீ ரோடே வகைக்காகாப் பாண்டமிது விண்பரந்த வீடு வெளிவாயில் ஓட்டலுமாய் ஓட்டை மடத்துக்கு ஒன்பது கொந்துடனே வீட்டைப் பிரித்தால் விறகுக்கு மாகாது விசையிட்டு ஆட்டும் வித்தாரப் பாவையிலும் பசையில்லாப் பாவையிது பட்சியொன் றாடிவரும் பட்சிக்குக் காயம் பகைத்துக்கிட்ட முட்டுகையில் குச்சியைக் கீழ்ப்போட்டு குதித்தோடு மக்குருவி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(106,'கலியன் பிறப்பு','இக்குருவிக் கூடு இவன்மணிய மீசுரரே அக்குருவி யேகும் அவ்வளவு மிந்நீசன் பண்ணுகிற நீசம் பரமனுக்கு மேராது  கண்ணிமைக்கு முன்னே கனவுருட்டுச் செய்திடுவான் இப்படியே வுள்ள இயல்பு மிவன்நினைவும் அப்படியே நீசம் அடைந்த மனவீடும் உள்ளவனாய் வந்து உருவெடுத்தா னாகையினால் எள்ளளவும் நன்றி இருக்காது ஈசுரரே என்று கணக்கர் எடுத்துரைக்க ஈசுரரும் அன்று அவன்தனக்கு ஆளனுப்பித் தான்வருத்தி ஆரைவிடு வோமென்று ஆராய்ந்து ஈசுரரும் சூரமுள்ள காலன் தூத னெமன்தனையும் துட்டமுள்ள பூதக் கிங்கிலியர் துர்க்கையையும் மொட்டைக்குற ளிகளையும் முக்கோடிக் கூளியையும் விட்டழைத்து வாருமென்று விடைகொடுத்தா ரீசுரரும் துட்டக் குணத்தோர் துடியாய் மிகநடந்து கொட்டங் கீழாகக் குருத்துநின்ற பாதகனை தட்டு மிகமாறி தலைகீழாய் நின்றவனை ஈச ரழைத்தார் எழுந்திருந்து வாவெனவே நீசன் தனையழைக்க நெளிந்தே வுருண்டவனும் பிரண்டுருளப் பிண்டம் பிசைபிசைந்து தான்மலங்க மருண்ட விழியால் மாகம துகலங்கி மண்ணை விட்டுயர மண்டைவர மாட்டாமல்  விண்ணெல்லா மெத்த வெம்மருண்ட தம்மானை கயிலைக் கிடுகிடெங்கும் கண்டபேர் தாம்பதற அகிலங் கிடுகிடெங்கும் அழைக்கவந்தோர் தாம்பதற அரனா ரிடத்தில் அவர்கள்சில ரோடி சிரமா னதுவெளியில் செல்லவர மாட்டாமல் பூமி கலங்கிப் பொறுக்கமிகக் கூடாமல் சாமியந்த நீசன் தான்வரவே மாச்சலுண்டு என்றந்தத் தூதர் இப்படியே சொல்லிடவே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(107,'கலியன் பிறப்பு','அன்று சிவனார் அதற்கென்ன செய்வோமென்று தம்மாலே யாகும் தந்து தெளிந்தெடுத்துக் கம்மாளன் வேலையினால் கவ்வையொன் றுண்டாக்கி குத்திப் புறத்தே கோதிவையு மென்றுசொல்லி எத்திசையும் நந்தி ஈசுவரனார் கைக்கொடுத்து வேண்டியந்த நந்தி விரைவா யவர்நடந்து ஊண்டிநின்ற நீசனையும் ஒருகவ்வை யால்கோதி வெளியிலே வைக்க வெப்பெடுத்த பாதாளம் பளிரெனவே வந்து பொருந்தியதே யம்மானை பூமிதனில் வெடித்த பொல்லாத நீசனையும் சுவாமிமுன் பானதிலே சுரண்டியத னாலிழுத்து கொண்டுவந்து விட்டார் குருநாதர் முன்பதிலே  கண்டுஅந்த ஈசர் கனமாய் விசேசமுற்று உன்றனக்கு வேணுமென்று உகந்ததெல்லா மிப்போது என்றனிடங் கேளென்று ஈசுரனார் தானுரைக்க உடனே யவனும் உள்ள மிகக்களித்து விடமேதான் பூண்டு விரிகந்தைத் தானுடுத்து மேலெல்லாங் குப்பை மிகப்பூசி யானையுடத் தோலி லிருப்பவனோ சொன்னதெல்லாந் தாறதுதான் என்று களிப்பாய் ஈசுரரை யந்நீசன் அன்று மொழிய அமர ரதையறிந்து பொல்லாத நீசா பொருளறிய மாட்டாமல் எல்லாரைப் போலே ஏசாதே ஈசுரரை லோகம் படைத்தவர்காண் உறுபொருளாய் நிற்பவர்காண் ஏகம் நிறைந்தவர்காண் இறவா திருப்பவர்காண் பட்சிப் பறவை பலசீவ செந்துகட்கும் நிச்சயமாய்ப் பொசிப்பு நிதமு மளிப்பவர்காண் மாயவனும் நான்முகனும் மறையு மிகக்காணாமல் தேயவா றுங்காணாத் திட்டிக்க வல்லவர்காண் இத்தனையும் நீசனுக்கு இயம்ப அமரர்களும் புத்திக்கு நட்புப் போதாமல் பின்சொல்லுவான்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(108,'கலிச்சி தோற்றம்','ஆனால்தான் தேவர்களே அப்படிநீ ரொப்பினீரே  தானா யிருந்து சர்வமதுமுண் டாக்கிவைத்த ஆனா லெனதுடைய அளவி லளவாக மானா ரொருகுழலை வகுக்கச்சொல் பார்ப்போங்காண் என்றந்த நீசன் இழிவாகச் சொல்லிடவே அன்றந்த ஈசர் அவரறிந்து ஏதுரைப்பார் நல்லதுநீ கேட்டதுதான் நாம்படைத்துத் தாறோமென்று வல்லபர மேசுரனார் வகையே தெனப்பார்த்து உந்தனக்கு நேரே ஒத்த பலம்போலே அந்தமுடன் பிறந்தால் ஆகுமோ வென்றுரைத்தார் அப்போது நீசன் அரனார் தனைநோக்கி ஒப்பமாகா தென்பலத்தில் ஒன்றி லரைப்பலமாய் அழகி லதிகமுமாய் ஆங்காரம் பாதியுமாய் குழகிய வாயழகாய்க் குரும்பத் தனத்தழகாய் மேனி யழகாய் விழியழகாய் வீச்சழகாய் யோனி யழகாய் ஒடுங்கு மிடையழகாய் கரமழகாய்க் காலழகாய்க் கண்ணழகாய்ப் பல்லழகாய் சரக்கூடு முன்னழகாய்த் தலைகண்டம் பின்னழகாய் தொடையழகாய் விரலழகாய்ச் சொல்லழகாய்ப் பல்லழகாய் நடையழகாய் வீச்சழகாய் நல்ல குழலழகாய் இடையழகாய் மேனி இறுக்கத் துடனழகாய்  உடையழகாய்த் தேகம் ஓங்காரத் தொங்கலுமாய் அழகுக்கு ஏற்ற அஸ்தர் மணத்தோடு கழப கஸ்தூரி கம்மென்ற வாசனைபோல் மூவர்தே வர்களையும் மோக மாய்மயக்கச் சீவ னதுகொடுத்துத் திட்டித்துத் தாருமென்றான் அப்போது ஈசுரனார் அகமகிழ்ந்து கொண்டாடி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(109,'கலிச்சி தோற்றம்','இப்போது கேட்டதற்கு என்செய்வோ மென்றுசொல்லி அருகில் சிவனார் அகமதிலே வீற்றிருக்கும் திருவுக்கு நன்றாய்த் தெரிவித்தா ரம்மானை கேட்டமா நீசனுக்குக் கீர்த்தியென்ன மாமயிலே தேட்டமுட னிப்போ செப்பென் றெனவுரைத்தார் அப்போது சத்தி ஆதி யடிவணங்கி ஒப்பொன் றில்லாத உடைய பெருமானே பாதியாய் நீசனையும் பகுந்தே யவனுடம்பில் விதியாயிடது விலாவி லொருயெலும்பைத் தட்டிக் கழற்றி சச்சுவருந் தானாக்கி திட்டித்து நீசனுக்குச் சிணங்கொடுவு மீசுரரே என்று உமையாள் இப்படியே சொன்னவுடன் அன்றுஅந்த நீசனுட அரையிலோ ராக்கையிட்டு இருக்கக் கீழ்கட்டி எழுந்திருநீ யென்றவுடன் பொருக்கென வேயிடது புறத்தி லொருயெலும்பைத் தெறிக்க அதையும் சிவனார் மிகப்பார்த்து வெறிகொண்ட நீசன் விலாவி லொடித்ததினால் நீசன் பலத்தில் நேர்பாதி யாகுமென்று வாசமட வாய்வரவே மதனன் தனைநினைத்தார் மதனை யரன்நினைக்க மாநீசன் தன்னெலும்பு வதன மடமாதாய் வந்ததுவே யம்மானை நீசன் நினைத்த நினவுபோ லம்மடவை பாசக்கலை தூவி பகட்டிவிழித் தாள்மருட்டி மும்மடங்காய் மோகம் முருகுக் குழல்சரிய கம்மடங்காத் தோசம் கரைபுரளத் தான்பிறந்தாள் கண்டானே மாநீசன் கண்கொள்ளாக் காட்சியுடன் அண்டாளைச் சென்று ஆவிமுகத் தோடணைத்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(110,'கலிச்சி தோற்றம்','நன்றாகத் தர்மம் நாள்தோறுஞ் செய்துகுண்டம் கண்டாரைப் போலே கட்டினான் மங்கையரை மங்கையரை நீசன் வாயாரா முத்தமிட்டுக் கொங்கைதனைக் கண்டு கொண்டாடி ஆசையுற்று எடுப்பா னவளை இடுப்பிலே வைத்திடுவான் படுப்பமோ வென்று பாய்விரிக்க நின்றிடுவான் பெண்ணோ டுறவாடிப் பேதலித்து அந்நீசன்  மண்ணோடு மண்ணும் மருவுவது போல்மருவி ஆசை யவள்பேரில் அங்கமெல்லாந் தானுருகி நீச னவள்பேரில் நினைவாக நின்றதல்லால் படைத்தருளி வைத்த பரமனையு மெண்ணாமல் தடதடெனப் பெண்ணைத் தானெடுத்து முத்திடுவான் அப்போது தேவர் அமரர்களும் பார்த்தவனை இப்போது உன்விழிக்கு ஏற்கும் படியாகப் பெண்படைத்துத் தந்த பெரியோனைத் தான்வணங்கி மண்பரந்த மன்னா வரங்கேளு என்றனராம் என்ற பொழுது இயல்புகெட்ட மாநீசன் அன்றந்த ஈசர் அடிவணங்கி யேதுசொல்வான் என்றனக்கு ஏற்ற இளமயிலை யுண்டாக்கித் தந்தருளுங் கோவே சர்வ தயாபரனே இனியெனக்கு ஏற்ற இயல்வரங்க ளானதெல்லாம் கனியிதழும் வாயானே கையில்தர வேணுமென்றான்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(111,'கலியன் கேட்ட வரங்கள்','அப்படியே ஈசர் அவனை முகம்நோக்கி இப்படியே உன்றனக்கு ஏதுவரம் வேணுமென்றார் என்ற பொழுது இயல்புகெட்ட மாநீசன் தெண்டனிட்டு ஈசர் திருவடியைத் தான்பூண்டு கேட்பான் வரங்கள் கீழுமேலும் நடுங்க  வீழ்ப்பாரங் கெட்ட விசைகெட்ட மாநீசன் மாயவனார் தன்னுடைய வாய்த்த முடிதனையும் தூயவனார் சக்கரமும் சூதமுடன் தாருமென்றான் அரனுடைய வெண்ணீறும் அந்தணரின் தன்பிறப்பும் வரமுடைய சத்தி வலக்கூறுந் தாருமென்றான் சிவமூலஞ் சத்தித் திருமூல மானதுவும் தவமூலம் வேதாவின் தன்னுடைய மூலமதும் மாலுடைய மூலம் வாய்த்தலட்ச மிமூலம் மேலுடைய தெய்வ விதமூலமுந் தாரும் காலனின் மூலம் காமாட்சி தன்மூலம் வாலைச் சரசோதி மாகாளி தன்மூலம் கணபதி யின்மூலம் கிங்கிலியர் மூலம் துணையதிப னான சுப்பிரமணியர் மூலமதும் ஆயிரத் தெட்டு அண்டம்நிறை மூலமெல்லாம் வாயிதக் கண்ணே வரமாகத் தாருமென்றான் அல்லாமற் பின்னும் அந்நீசன் கேட்டவரம் பொல்லாத வித்தை புகலக்கே ளொண்ணுதலே கூடுவிட்டுக் கூடு குதிக்கக் கருவதுவும் நாடு பாழாக்கி நகரிகொள்ளை யாக்கிடவும் துயில்வோர் போலுலகம் துஞ்சவைத்துக் கொள்ளைகொண்டு  அயதிமோ கினிக்கருவும் அரனேநீர் தாருமென்றான் ஆவடக்கு மோகினியும் அழைக்கவெகு மோகினியும் நாவடக்கு மோகினியும் நருளழைக்கும் மோகினியும் ஆண்பெண் பிரிக்க அதிகவெகு மாரணமும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(112,'கலியன் கேட்ட வரங்கள்','கோண்பிரித்துக் கட்டிக் குடிகெடுக்கும் மாரணமும் கொல்ல உச்சாடனமும் குடிகெடுக்க மாரணமும் தொல்லை வருத்தி சோலிசெய் யுச்சாடனமும் லோகமது வாழாமல் உள்ளமுங்கித் தாழ்ந்திடவும் ஏகமாய்த் தம்பனமும் இதின்கரு வுந்தாரும் கொட்டிக் கலைக்க கூறுகெட்டச் சல்லியமும் ஒட்டியமுந் தாரும் உள்ளகரு வுந்தாரும் பூசை முறையும் புவனச்சக்க ரமுடனே தீட்சை விதிமுறையும் சிவவிதியுந் தாருமையா சலமேல் கனல்மேல் தானிருக்கு மோடிகளும் கலைமேல் குடைபிடிக்கக் கருவது வுந்தாரும் மிருக மதைவருத்தி வேலையது கொண்டிடவும் இறுக்கமுள்ள வாதை எனக்குவிட்டுத் தாருமையா அட்டகர்ம மெட்டும் அடக்கிவரந் தாருமையா  மொட்டக் குறள்களையும் முன்னேவலாய்த் தாரும் மந்திர சாலம் மாய்மாலத் தந்திரமும்  இந்திர சாலம் எனக்கருளு மென்றுரைத்தான் நோவுக் கிரகம் நுழையாம லென்றனக்குத் தாவுகெவுனக் குளிகை தாருமென்றான் மாநீசன் வந்த பிணிதீர்க்க வைத்திய வாகடமும் தந்துதந் தாகப்பல சாஸ்திர முந்தாரும் மூவருட வடிவும் உதித்துவந்த முற்பிறப்பும் தேவருட பிறப்பும் தெளிந்தெழுதித் தாருமென்றான் பறக்குங் குளிகை பரனையழைக் குங்குளிகை மறைக்குங் குளிகை மாலைவருத் துங்குளிகை சாலக் குளிகை சத்திவருத் துங்குளிகை வாலைக் குளிகை மறையைவருத் துங்குளிகை காளிதனை வருத்திக் கைக்குள் விடுங்குளிகை');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(113,'கலியன் கேட்ட வரங்கள்','கூளிகணத் தைவருத்திக் கொண்டுவரும் நற்குளிகை தேவரையும் வானவரைச் சென்றழைக் குங்குளிகை மூவரை யுமழைத்து மோடிசெய்யுங் நற்குளிகை பழிசெய்தால் வெல்லப் பாரத் தொழில்கள்முதல் சுழிவரைகள் தானறிய சூதானமாய்த் தாரும் மருட்டு விதமும் மாஞாலக் குண்டணியும் உருட்டுங் கொடிய உரம்பேசிய மதமும் வாள்வெடிக ளாயுதங்கள் வாய்தடுக்கத் தந்திரமும் வேழ்வருத்தி வேலைகொள்ள விசையடக்குந் தந்திரமும்    துட்ட மிருகம் தூறுவிசங் கொண்டதெல்லாம் கட்டுக்குள் கட்டிக் கீழ்ப்படியத் தந்திரமும் கட்டுச் சுருக்கும் கடியபல வித்தைகளும் நட்டு முட்டுள்ள நவநிதிய வஸ்துக்களும் தந்திந்தப் பெண்ணுடனே சார்ந்துவிளை யாடிருந்து விந்து சனங்கள் மிகுவாகத் தானுதித்துக் கிளையோடே வாழ்வு கெறுவித மாயாண்டு வளையான மாதை மறவாம லெப்போதும் புத்திக் கருத்தும் பெண்பேரி லென்றனக்குச் சற்றும் நெகிழாமல் தாரும்வர மென்றுரைத்தான் அப்படியே வுள்ளவரம் அந்நீச னுக்கருள எப்படித்தா னென்று எண்ணினா ரீசுரரும் எண்ணியே ஈசர் ஏந்திழையாள் சத்தியுடன் புண்ணிய நாதன் புகலலுற்றா ரம்மானை முப்பத்தி ரண்டறமும் முகித்திருந்த வொண்ணுதலே செப்புத் தனத்தழகு செவ்வே கருங்குயிலே வின்னோத நீசன் விபரீதமாய்க் கேட்டதற்கு இன்ன விதமென்று இயம்புநீ பெண்மயிலே அப்போது சத்தி ஆதி யடிவணங்கி நற்போது மறவா நல்லகன்னி யேதுரைப்பாள் சாத்திரமும் வித்தைகளும் தந்திரமுங் கேட்டதினால் சூத்திரச் சித்தனொன்று சுறுக்காயுண் டாக்குமென்றாள்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(114,'அகத்தீசர் பிறப்பு','உடனேதான் ஈசர் உள்ளங்களி கூர்ந்து திடமாய்த்தான் ஈசர் சிந்தைதனி லுத்தரித்து அகத்தீச னென்று ஆதிமன துள்ளிருந்து செகத்தோர்கள் காண சித்தாதி தானாகி சாத்திரமும் வித்தை சமூலக் கருவுடனே சூத்திரச் சித்தாதி தோன்றினன்கா ணம்மானை எல்லோருங் கண்டு இவராகு மென்றுசொல்லி அல்லோரு மெச்சி அகமகிழ்ந்து கொண்டாடி நல்லதென்று ஈசர் நன்முனியைத் தானோக்கி வல்ல கவியோடு வந்துதித்த சாஸ்திரியே என்னென்ன சாஸ்திரங்கள் ஏதேதுக் கானாலும் இன்னாநிற்கும் நீசனுக்கு ஈந்து கொடுவுமென்றார் அப்போ தகத்தீசர் ஆதிமறை முதலாய் மைப்போடும் வித்தை மாஞால அச்சரமும் கொடுத்தோ மெனவே கூறினார் மாமுனியும் கடுத்தமுள்ள நீசன் கையதிலே தான்வேண்டி நிற்கவே பின்னும் நிகழ்த்துவார் மாமுனியும்  அக்கறுகு சூடும் ஆதிமுத லந்தம்வரை வசமாகும் வித்தை மரணம்வரா வித்தைகளும் நிசமாகும் வித்தையெல்லாம் நீசனுக் கேகொடுத்தோம் கொல்லவே மெத்தக் கோபத்தால் நீசனையும் வெல்லவகை யில்லையல்லோ விடையேறு மீசுரரே இந்நீசன் லோகமதில் இருந்தாளும் நாளையிலே அன்னீத மல்லால் அறமறிய மாட்டானே அல்லாமற் பின்னும் அச்சுதனார் சக்கரமும் பொல்லாதான் கொண்டு போறான்மால் கிரீடமும் இவ்வரிசை ரண்டும் இவனிடத்தி லேயிருந்தால் எவ்வொருவ ரால்வதைக்க ஏலாது நீசனையும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(115,'கலியரசன் சத்தியம்','என்று அகத்தீசர் இப்படியே தான்கூற அன்றுஸ்ரீ ரங்கர் ஆண்டியுரு தானாகித் தலைவிரித்துக் கந்தை சற்றே கலைபூண்டு சிலையில்லா வெங்கையால் சென்றாரே நீசனிடம் நீச னிடத்தில் நெடுமால்தான் முன்னேகி வாசமுடன் வார்த்தை வழுத்தினா ரம்மானை ஈச னிடத்தில் இறைஞ்சிவரம் பெற்றதிலே தேச இரப்பனுக்குச் சிறுகா யென்றுரைத்தார் தாரா தேபோனால் சாபமிடு வேனுனக்குப் பாராய்நீ யென்று பகட்டினா ரெம்பெருமாள் அப்போது நீசன் ஆண்டி தனைப்பார்த்து இப்போது போடா இரப்பனோ டேதுசண்டை மாயன் வரவேணும் வலுப்பார்த்து விட்டிடுவேன் பேய னுடனெனக்குப் பேச்சென்ன நீபோடா என்று அந்தநீசன் இவ்வளமை கூறிடவே அன்று பரதேசி அவனோடங் கேதுரைப்பார் பிச்சைக் காரன்தனக்குப் பெலமில்லை யென்றோநீ அச்சம தில்லாமல் அடமா யிதுவுரைத்தாய் பண்டாரந் தன்பலமும் பழிநீசா வுன்பலமும் சண்டையிட்டுப் பார்த்தால் தான்தெரியும் மாநீசா என்று பண்டாரம் இதுவுரைக்க அந்நீசன் குன்று கரத்தெடுத்த கோபால ரோடுரைப்பான் ஆள்படை களில்லாமல் ஆயுதங்க ளில்லாமல் வேழ்படை களில்லை வெட்டவா ளிங்குமில்லை தடியில்லை சக்கரமும் தானில்லை உன்கையிலே முடிவிரித்துக் கந்தை உடுத்தவனோ டுயுத்தமிட்டால் ஆண்மையில்லை யென்றனக்கு ஆயிழைமா ரேசுவரே தாண்மைமொழி பேசாதே தலைவிரித்துப் பேயாநீ மூவர் முதலாய் முப்பத்து முக்கோடித்  தேவரையும் வேலைகொள்ள சிவமூலம் பெற்றிருக்க ஒன்றில்லாப் பேயனோடு யுத்தமிட்டா லென்றனையும் நன்றினியப் பெண்கள் நகைப்பார்நீ யப்புறம்போ என்று அந்தநீசன் இயம்பத் திருமாலும் நன்றுநன்று நீசா நானுரைக்க நீகேளு பண்டாரத் தோடே படையெடுத்தா லாண்மையில்லை');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(116,'கலியரசன் சத்தியம்','என்றேதா னிப்போ(து) இயம்பினையே மாநீசா பண்டார மென்றும் பயித்தியக் காரனென்றும் ஒண்டியாய் வந்தவனொ(டு) யுத்தமிட மாட்டேனென்றும் பிச்சைக் காரனெனவும் பெரிய இரப்பனென்றும் கச்சையில்லா னென்றும் கணைகம்பில் லாதானென்றும் இப்படியே பண்டாரம் என்றிருக்கும் பேர்களுக்கு எப்படியும் வம்புசெய்வ(து) இல்லையென் றுண்மையுடன் சத்திய மாகத் தானுரைநீ பார்ப்போமென்றார் புத்தியில்லா நீசன் புகலுவான் பின்னாலே பண்டார மென்றும் பரதேசி யானவரைத் தண்டரளக் கந்தைத் தலைவிரித்த ஆண்டிகளை அட்டியது செய்யேன் அவரோடு சண்டையிடேன் ஒட்டியே வம்பு ஒருநாளுஞ் செய்வதில்லை என்றான்காண் நீசன் எம்பெருமாள் தானுரைப்பார்  நன்றாக இப்படியே நட்டிசெய்ய மாட்டேனென்று ஆணையிட்டுத் தாவென்று அருளினா ரெம்பெருமாள் வீணமட நீசன் விளம்புவா னப்போது ஆரார்கள் பேரில் ஆணையிட வேணுமென்று பேராகச் சொல்லு பிச்சையெடுப் போனேயென்றான் அப்போது பண்டாரம் அகமகிழ்ந்து கொண்டாடி இப்போது நீதான் ஈசன்தனை வணங்கி வேண்டியே போற வெகுவரங்கள் பேரதிலும் பெண்டிவள் பேரதிலும் பெலமாக இப்போது ஆண்டி பரதேசி ஆகிவந்த பேர்களையும் வீண்டிடறு செய்யேன் எனவேநீ யாணையிட்டால் ஆணைக் கிடறுசெய்து ஆண்டிகளைச் சில்லமிட்டால் வீணேபோ மென்வரங்கள் வீட்டுப்பெண் ணார்கள்முதல் பெண்தோற்று நானும் பெற்றவர முந்தோற்று மண்தோற்று வாழ்வும் மக்கள்கிளை தோற்றுச் சேனைத் தளந்தோற்று சீமையரசுந் தோற்று ஆனைப் படைதோற்று அரசுமே டைதோற்று என்னுயிருந் தோற்று என்கிளையோ டேநானும் வன்னரகில் போவேன் என்றேவாக் குரைத்திடுநீ அப்போது நீசன் ஆண்டி யுரைத்தபடி  எப்போதும் மறவேன் இதைமறந்து வம்புசெய்தால் சொன்னபடி யெல்லாம் தோற்றிறந் தென்னுயிரும் வன்னரகில் போவேனென்று ஆணையிட்டான் மாநீசன்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(117,'சக்கராயுதம் பணமாதல்','நல்லதுதா னென்று நாட்டமுற்றுச் சன்னாசி வல்ல பெலமுள்ள மாநீசா நீகேளு மந்திரங்க ளாலே மகாகோடி ஆயுதங்கள் தந்திரங்க ளாலே தான்வருத்த லாகிடுமே அப்படியே யொத்த அச்சரங்கள் தானிருக்க இப்படியே ஆயுதத்தை எடுத்துச் சுமப்பதென்ன நீகேட்ட சக்கரத்தை நிமலனவர் தாராமல் தொனிகெட்ட வெற்றிரும்பைச் சுமவென்று தந்தார்காண் இரும்பைச் சுமந்தால் இல்லைகா ணொன்றுபயன் தரும்பொருள்போ லுள்ள சம்பாத்தியந் தாறேன் மண்டலங்கள் தேசம் வாழ்வுக ளுண்டாவதற்கும் கண்டார்கள் மெய்க்கக் கனக திரவியம்போல் தருகிறே னுன்றனக்குத் தான்வேண்டு நீயெனவே பருமுறுக் காயாண்டி பகர்ந்தாரே நீசனுக்கு அப்போது நீசன் அகமகிழ்ந்து கொண்டாடி இப்படியே தாருமென்று ஈந்தானே சக்கரத்தை சக்கரத்தை வேண்டி சங்குசரத் தாமன்  மிக்கப் பணமாக்கி மிகுத்தசக்க ராயுதத்தைச் சபிக்கவே மாயன் சக்கரா யுதங்கேட்கும் நீச னிடத்தில் என்னைப் பணமாக்கி ஈயுறீரே சுவாமி இப்போது சாபமிட்டு இப்போ திடுஞ்சாபம் எப்போது தீருமென்று அப்போது சக்கரமும் ஆண்டியடி போற்றிடவே கலிமா றும்போது கடருமென்றா ருன்சாபம் வலியான சக்கரமும் வாய்த்தபண மாகியதே பணமாகிக் கீழே பறந்து குதித்திடவே இணமான நீசன் எட்டி யவன்பிடித்துக் கண்ணில்மிக வொற்றிக் காரிகையாள் கைகொடுப்பான் பெண்ணே நமக்குப் பெலங்கள்வந்து வாய்த்துதடி என்று பிரியமுற்று ஈசுரரைத் தான்வணங்கி மன்று தனில்போக வரந்தாரு மென்றுரைத்தான் அப்போது தன்னில் ஆண்டியவ ரங்குசென்று இப்போது இங்கேவைத்து இவன்தான் மொழிந்ததெல்லாம் தப்பாம லாகமத்தில் தானெழுதி வையுமென்றார் முப்போது வுள்ள முறைபோலே மாயவரும் ஆகமத்திற் பதித்து ஆண்டார் துரிதமுடன் நாகரீக நாதன் நடந்தார்ஸ்ரீ ரங்கமதில் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(118,'கலியுகம்','நீசனையு மூரேபோ என்று நிமலனுந்தான் ஈயுகிற போது ஏதுரைப்பாள் சத்தியுமே வன்னச் சிவனாரே மாபாவி கேட்டவரம் என்னென்ன வாயமதாய் இருக்குதுகா ணுத்தமரே வலியான மாதே மாநீசன் கேட்டதுதான் கலியுகம் போலிருக்கு கண்ணமுதே யென்றுரைத்தார் சிவம்வாய் திறந்து செப்பக் கலியுகமாய் இதமான தேவர் எழுதினா ரகமத்தில் கலியுக மெனச்சிவம் கருதிடத் தேவர்கள் பொலிவுடன் சேர்த்தனர் புராண மீதினில் சலிவுடன் நீசனும் தரணியில் போந்திட வலியுள்ள மாயன் ஸ்ரீரங்க மேவினார் மாயனுக்கு ஆணை மாநீச னிட்டதெல்லாம் தூய பெருங்கணக்கில் தொகுத்தெகுதி வைத்தனராம் நீசக் கலியன் நிறைவோன் பதம்போற்றித் தேச மதில்வரவே சென்றான்கா ணம்மானை கலியன் கலிச்சி கட்டாய் வருகையிலே சிலிரெனவே லோகம் திணுக்கிடவே யம்மானை ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(119,'நல்லவை மறைதல்','தர்மமாய்ப் பூமி தானிருக்கும் நாளையிலே வர்மமாய் நீசன் வரவேகண் டம்புவியில்  நல்ல பறவை நல்மிருக ஊர்வனமும் வல்ல வகைநீதம் மாற்றங்கே ளன்போரே வெள்ளானை வெள்ளை வேங்கையொடு வெண்கடுவாய் தள்ளாத சற்பம் தலையைந்து கொண்டதுவும் வெள்ளன்னம் வெண்குயில்கள் வெண்புறா வெள்ளைமயில் கள்ளமில்லா வெண்சாரை கடியபெல வெண்கருடன் நல்ல அனுமன் நாடுங்காண் டாமிருகம் வல்லவெண் நரிகள் வளர்ந்துவரும்வெண் காகம் ஆளியொடு சிங்கம் ஆனையி றாஞ்சிப்புள்ளும் வேளிசை வெண்கலைமான் வெண்புள்ளு வெண்ணணில்கள் மிருகமதில் வெள்ளைகொண்ட மேல்மிருக மானதெல்லாம் அறியவே குண்டம் அதுநோக்கிப் போயிடவே பால்நிற மான பட்சிப் பறவைகளும் மேல்பரனார் குண்டம் மிகநோக்கி நாடிடவே வெண்சாரை யைந்துதலை விசஅரவ மானதெல்லாம் துஞ்சாத நாதன் துணைதேடி நாடிடுமாம் முன்னிருந்த சாஸ்திரமும் முறையும்பஞ் சாங்கமதும் பின்வந்த நீசனினால் போகவழி தேடிடுமாம் நீசனுக்கு முன்னிருந்த நீதநெறி மானுபமும் போயகல்வோ மென்று புத்திதனி லெண்ணிடுமாம் முன்னிருந்த ரத்தினங்கள் முத்துவை டூரியங்கள் பன்னுமறை சாஸ்திரமும் பாதையது கொண்டிடுமாம் சங்கு சமூலம் சலக்கண் சலத்தில்விழ பொங்குநவ ரத்தினமும் போகவழி தேடிடுமாம் கடல் விளைவெல்லாம் கண்காணா தோடிடுமாம் தடவரையி லுள்ள தங்கமது மண்கோர்க்க சிலைகள் பதிகள் தெய்வத்திருச் சமூலம் அலையுள்ளும் பூமி அதனுள்ளும் போய்மறைய மாரி மறைய மலர்க்கண் மிகமறைய ஏரி பாழாக எண்ணினதே யம்மானை கர்மக் கலியதனால் கடல்கோபித் தேயடிக்க');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(120,'நல்லவை மறைதல்','தர்மந் தலைகவிழ்ந்து தானிருந்து தம்மானை தருமமெய் நீதமதும் தாரணியில் உர்வனமும் பொறுமை யுடைய பெரிய மிருகமதும் நாகமணி தங்கமணி நவரத்தின மாமணியும் தாகமுள்ள முத்து சாஸ்திர மாமறையும் நீதத்தோ ரெல்லாம் நீலவண்ணர் சங்குடனே பாதத்திருக் குண்டம் பாதைவழி தேடிடுமாம் நடந்தோர்க ளெல்லாம் நாடி வழிவரவே கடந்தார்கள் வல்ல கலியைவிட்டுக் காடதிலே காடதிலே செல்ல கனத்தபஞ்ச பாண்டவர்கள் வாட லுடவாய் வைகுண்டந் தானேக கண்டாரே தர்மர் கடந்துநின்ற நீதமதைப் பண்டாரத் தர்மர் பகர்ந்தா ரதினோடு நாட்டிலுள்ள நீதமெல்லாம் நடந்துகா ணாதேசென்றால் வீட்டி லுறுதியுள்ள மேல்வீடு காண்பதற்குப் போவேனோ தர்மம் போனதினால் நாயடியேன் சேர்வேனோ குண்டம் செய்தர்ம மில்லையென்றால் என்றுரைக்கப் பாண்டவர்கள் இயல்தர்ம மேதுரைக்கும் பொன்றுவந்து லோகம் பொய்ப்பூண்ட தானதினால் எங்களுக் கங்கே இருந்தா லாகாதெனவே செங்கண்மா லாயனையும் தேடியே போறோமென்றார் அப்போது பாண்டவர்கள் அன்பாய்ப் பதம்பணிந்து இப்போது உங்களையும் இழந்துவிட் டிங்கிருந்தால் கர்மக் கலிமூழ்கிக் காண்பதில்லை குண்டமது தர்மத் தோடங்கே தான்வருவோம் கண்டீரே தர்மமும் நீதமதும் தாட்டீகப் பாண்டவரும் வர்மமில்லாக் குண்டம் வழிநோக்கிச் செல்லுகையில் மேலான மாமிருகம் வேதக் காராவுகளும் நாலா மொருதலையும் நல்ல அரவமதும்   வெண்பட்சி யான மேலான பட்சிகளும் இன்பட்சி யெல்லாம் எங்கினிப் போவோமென்று கதறி யழுது கனைத்துநின்றார் காடதிலே பதறி யழுது பறவை மிருகமொடு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(121,'நல்லவை மறைதல்','நிற்கு மளவில் நீலவண்ணர் தானறிந்து பக்குவப் பிராயப் பண்டார மாகிவந்து ஏதுகா ணீங்கள் இந்தவன வாசமதில் ஓதிக் கரைய உங்கள்விதி யானதென்ன செப்போடு ஒத்த திருமால் தனைப்பார்த்து அப்போது எல்லாம் அழுதழு தேதுசொல்லும் நீசக் கலியன் நீணிலத்தில் வந்ததினால் தேசமதி லெங்களுக்குச் செல்லவிருப் பில்லையையா என்றுரைக்கச் சன்னாசி ஏதுரைப்பா ரன்போரே நன்றுநன்று கேளும் நான்சொல்ல நீங்களுந்தான் பாண்டவரைத் தர்மமதைப் பாரநீ தமதையும் ஆண்ட வைகுண்டம் அடையப்போ மென்றுரைத்தார் தெய்வக் காராவைச் சிறந்தவெள் ளானைகளை மெய்வரம்பு கொண்ட மிகுத்த மிருகமெல்லாம் கும்பக் குருமலையில் கூடியே நீங்களெல்லாம் சம்பு சதாசிவத்தைத் தான்போற்றி யுள்ளிருத்தி  நீசன் தனையறுத்து நீதயுகந் தானாள வாசமுட னேவரவே வரம்வேண்ட நில்லுமென்று ஐந்தலை நாகமதை அந்தமுனி தானழைத்து உன்றனக்குச் செய்தி உரைக்கிறேன் கேளெனவே அலைமுகத்தில் நூற்றொன்று ஆழம் வரைக்கீழே நிலைதனிலே நின்று நெடியோனை உள்ளிருத்தி கலியை யழித்துக் கடியதர்ம மாகவேதான் வலியயுக மாளுதற்கு வைகுண்டா வாவெனவே நிற்பாய்த் தவசு நீபோவென அனுப்பி நற்பாக நீங்கள் நற்றவசு செய்யுமென்று சற்பமதுக் கரசு சங்குவெள்ளைச் சாரைதன்னை பற்பக் கிரிதனிலே பைம்பொன்னிறப் பொய்கையிலே வம்பை யழித்து வாய்த்ததர்ம ராச்சியத்தில் அன்பரோ டேயரசு ஆளத்திரு மால்வரவே வேணுமென்று நிட்டை விரும்பிச்செய் யென்றேதான் ஆணுவ நாதன் அதற்கு விடைகொடுத்தார் அன்னப்பட்சி யான அதிகப் பறவைகளும் பொன்னம்பல கிரியில் போய்த்தவசு பண்ணுமென்றார் சாத்திர மாமறையைத் தானெடுத்து மாமுனியும் சூத்திர நெஞ்சத்து உள்ளிருத்தி வைத்தனராம்  ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(122,'கலைமுனி ஞானமுனி தவசு','இருத்திஸ்ரீ ரங்கம் ஏகவென் றெம்பெருமாள் கருத்தி லுறயிருத்திக் கட்டாய் வருகையிலே ஞான முனியினொடு நல்ல கலைமுனிவன் தானமுள்ள மாமுனிவர் தலைகவிழ்ந் தேதவசு சடைத்து முகம்வாடித் தானிருக்கு மப்போது திடத்தமுடன் நல்ல திருமா லருகேகி நன்றியுள்ள மாமுனியே நல்லகயி லாசமதில் கண்டதுண் டுங்களையும் காட்டில்வந்த வாறேது அப்போது மாமுனிவர் ஆதி பதம்பணிந்து செப்போடு வொத்தத் திருமாலே நாங்களுந்தான் கயிலாச மீதில் கறைக்கண்டர் பாதமதை ஒயிலாகப் போற்றி ஒழுங்கா யிருந்தவர்காண் இருக்குமந்த நாளையிலே எங்களிரு பேர்களையும் தருக்குகந்த ஈசர் தன்னையவர் வருத்தி மாயன் மேல்பிறக்க மண்ணுலோ கந்தனிலே காய மிழந்து காணா துருவெடுத்து மறைந்திருக் கிறாரெனவே வானோர்கள் சொல்வதினால் சிறந்தபுகழ் மாயவனுக்குத் தேருஞ் சிங்காசனமும் மண்டபங்கள் மேடை வாய்த்தநீ ராவிகளும் பண்டையுள்ள நல்ல பைம்பொன்னிறப் பொற்பதியும் உண்டுகாண் துவாரகா யுகத்திலே யென்றுசொல்லி கண்டுகொண்டா னானால் கலியனதை யாண்டுகொள்வான் முன்போய்த் தானீங்கள் உவரிதனை வருத்தி இன்பமுள் ளவகைகள் எல்லாமதி னுள்ளேவைத்து வைகுண்ட ரங்கே வந்தா லிதைக்காட்டி மெய்கொண்ட வுங்கள் இடத்திலே போவுமென்று அல்லாதே போனால் அக்கினியா லுங்களையும் இல்லாதே செய்வார் என்றுசொல்லி வாருமென்று அனுப்பினா ரெங்களையும் அதற்காக இங்குவந்தோம் துனுப்பாக வாரிதனைச் சொல்லியே சட்டம்வைத்து ஏகினோங் கயிலை இடைவழியில் மாநீசன் தாவி வரக்கண்டு தவலோக மேமறைந்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(123,'கலைமுனி ஞானமுனி தவசு','கர்ம விதிப்பயனால் கலக்க மிகவடைந்து தர்மச்சிறப் பில்லாது தலைகவிழ்ந் திருந்தோமென்றார் அப்போது அச்சுதரும் அந்தமுனி தங்களையும் இப்போது கூட்டி ஏகின்ற அப்பொழுது கருங்குரங்கு காரானை கரியகடு வாய்புலியும் இருங்கருட னூர்வனமும் எறும்புஈ மூட்டைகளும் கள்ளக் கசடுள்ள கரிய மிருகமதும் கொள்ளைகொண்ட ஊர்வனமும் குசலான பட்சிகளும்    தரந்தரமாய்க் கூடித் தாமே திரளாக வரவேகண் டெம்பெருமாள் மாமுனியைத் தான்பார்த்து ஏதேது மாமுனியே இந்த வழிதனிலே ஈதே தெனவே எனக்கேட்க மாமுனிவர் நீச னுதிரம் நீதிகெட்ட இம்மிருகம் வாசமுள்ள பட்சிகளும் மானிலத்தி லூர்வனமும் பிறந்துதித்து வந்து பேருலகி லம்மானை சிறந்தறிந்து எம்பெருமாள் செவியிலே கையறைந்து விலகியே போக வேணுமென் றெம்பெருமாள் மலகிய மாமுனிவர் மாயனடி போற்றி எங்களுக் கெங்கே இருக்கவே சொல்வீரென்று அங்கந்த மாயவரின் அடிதொழுதா ரம்மானை அப்பொழு தெம்பெருமாள் அந்தமுனிக் கேதுரைப்பார் இப்பொழுது நீங்கள் ஏகுங்கோ செந்தூரில் செந்தூர்ப் பதியில் சிறந்தவா ரிக்கரையில் நந்த கோபால நாராய ணாவெனவே மாயநீ சக்கலியை வதைசெய்து தர்மமதாய் ஞாயமுடன் வைந்தர் நாட்டையொரு சொல்லதுக்குள் ஆளவர வேணுமென்று அருந்தவசு பண்ணுமென வேழமொத்த மாமுனிக்கு விடைகொடுத்தார் மாயவரும்     விடைவேண்டி மாமுனிவர் வேல னுறைந்திருக்கும் கடலான தற்கரையில் கடுந்தவசு பண்ணினரே அப்படியே மாமுனிவர் அங்கே தவசிருக்க இப்படியே மாயன் இசைந்தஸ்ரீ ரங்கமதில் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(124,'போகமுனி தவசு','போகின்ற வேளையிலே போகனென்ற மாமுனிவன் தாவு மொருமலையில் தலைசிதறிச் சாகவென்று வேகித்து மாமுனிவன் விசையாக முட்டுகையில் ஆகமுற்று எம்பெருமாள் அவனோடங் கேதுரைப்பார் மலைதனிலே முட்டி மாளவென்று மாமுனிநீ குலைகுலைந்து நின்றவிதம் கூறுநீ யென்றனராம் அப்போது மாயவனார் அடிபோற்றி யேதுரைப்பான் செப்பமுள்ள மாயவரே சீமைதனை யளந்த மாயவரேநானுமினி மாள்வதல்லா லிங்கிருக்க ஞாயமில்லை அய்யா நாரா யணப்பொருளே என்றுரைக்க மாமுனியும் ஏதுரைப்பா ரெம்பெருமாள் உன்று வசனம் உரையென்றார் மாமுனியை என்றெம் பெருமாள் இவையுரைக்க மாமுனியும் அன்று முனிசொன்ன வசனங்கே ளன்போரே தர்மநீ தமதுவும் தரணிதனில் மானுபமும் கர்மக் கலியால் கட்டழிந்து போச்சுதையா  நன்மையெல்லாம் நாடுவிட்டு நடந்து மறைந்ததினால் வன்மம்வந்து வையகத்தில் வளர்ந்த படியாலும் நானிருந்த நிட்டை நாலிரண் டாகையிலே மானிபங்கள் தர்மம் மறையத்தான் போனதினால் என்தவந்தான் வம்பில் இழந்தேனே யென்றுசொல்லித் தன்தலையைத் தான்சிதறிச் சாகவே ணுமெனவே மலைதனிலே முட்டி மாண்டுவிட வேணுமென்று அலைமேல் துயின்ற ஆண்டியோ டேயுரைத்தான் நல்லதென்று யுன்றனக்கு மாளவென்றால் வேறில்லையோ கல்லிலே முட்டிக் கண்தவர்ந்து சாவதென்ன சொல்லுநீ யென்று திருமா லிதுவுரைக்க ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(125,'மலைப் புதுமை','அப்போது மாமுனியும் அந்தமலைப் புதுமை இப்போது சொல்லுகிறேன் என்றே வுரைக்கலுற்றான் வேதா விதித்த மேலான ஆகமம்போல் நாதாந்த வேதம் நாலுண்டு கண்டீரே கலியன் பிறந்தபின்பு கட்டான வேதமொன்று சலிவாகி வேதம் தான்விளங்கா வண்ணமுந்தான் இளகாமல் கற்போல் இருந்ததுகாண் வேதமது குழைவான வேதா குறியா யதையறிந்து  கல்வி யிளகாமல் கற்போ லிருந்ததினால் பல்லுயிர்கள் வாழும் பாரிலொரு பங்காக மலையாய் வளர்ந்து வைந்தர் வருகையிலே அலைபோலிளகி அவனிதனில் பாயவென்று சபித்தார் காண்வேதா தானறிந்து வேதமது குபிந்தான மான குன்றுபோலே வளர்ந்து அன்றந்த வேதம் ஆனதினால் நானடியேன் இன்றென் சிரசை இதிலிடறி மாண்டதுண்டால் வைகுண்டங் கிட்டுமென்று மாளவந்தே னிம்மலையில் கைகண்ட வேதக் கருணாகரக் கடலே என்றுரைக்க மாமுனிவன் எம்பெருமாள் நல்லதென்று அன்று முனியை அவரோ டுடன்கூட்டிப் போகின்ற வேளையிலே போகனென்ற மாமுனிவன் ஏகின்ற வேளை எம்பெருமா ளேதுசொல்வார் மாமுனியே நீகேளு மாச்சலா யென்னுடம்பு தாமுனியே யென்றனக்குச் சடைவா யிருக்குதுகாண் ஆனதால் கங்கைபார்த்(து) அதிலே குளித்துமிகப் போனால் சுகமெனவே போகனோ டேயுரைத்தார்.');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(126,'சலந்தவசு','அப்போது நல்ல அருள்கொண்ட மாமுனிவன் இப்போது மாயவரே இதோ தெரியுகின்ற  அருணகிரி மலையில் அருவியொன் றுண்டுமையா கருணை யிரங்கினதால் காட்டியே தாறேனென்றான் நல்லதுதா னென்று நல்ல முனியோடே வல்ல திருமேனி வாவிதனைக் கண்டார்காண் கண்டு திருமேனி கனத்தநீ ராவிதனில் சென்றிறங்கி மேனிதனில் செலங்கோரி விட்டிடவே ஆவி தனிலிறங்கி அச்சுதனார் நின்றிடவே தேவி யொருபெண் செலமெழுந்து மாயனையும் வந்து பணிந்திடவே மாயனதைப் பார்த்துச் சந்துபயில் மாமுனியே தண்ணீரைக் காணாமல் பெண்ணாக வந்த புதுமையென்ன மாமுனியே கண்ணான மாமுனியும் கட்டுரைப்பா னம்மானை எண்ணாத தெண்ணி ஏதுரைப்பான் மாமுனியும் பெண்ணோடு கேட்டால் புதுமை யதுதெரியும் என்றுரைக்க மாமுனியும் ஏந்திழையோ டேதுரைப்பான் நன்றுநன்று பெண்ணேநீ நதியா யிருந்ததுவும் பெண்ணாக வந்ததுவும் பேசுநீ ஒண்ணுதலே தண்ணீராய் நின்ற தார்குழலா ளேதுரைப்பாள் அய்யாவே நானும் அதிகயி லாசமதில் மெய்யாக ஈசுரர்க்கு வேலையது செய்திருந்து  வருகின்ற வேளையிலே மனங்குழறி நானடியாள் அரையில் கலைபூணா(து) ஆவியிலே சென்றிறங்கிக் குளித்தே னதையும் கொன்றையணி வோனறிந்து அழித்தே யெனையும் ஆவிபோலே படைத்தார் படைக்கும் பொழுதில் பரமனோ டிச்சாபம் கடருவதெப் போதெனவே கறைக்கண்ட ரோடுரைத்தேன் ஆவியாய் நீகிடந்தால் அச்சுதனா ரங்குவந்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(127,'சலந்தவசு','தாவிக் குளிக்க சாபமது மாறுமென்றார் என்றுரைக்கப் பெண்ணாள் எம்பெருமா ளேதுரைப்பார் அன்றந்தப் பெண்ணதையும் ஆகமப் போகனையும் நானாண் டிருந்த நல்லதுவா பரத்தில் தானாக ஆண்டு தானிருக்கு மப்பொழுதில் சூரிய வொளிகாந்தம் துலங்கும்வை டூரியத்தில் வீரிய மான வெற்றிவிரு துக்குடையும் தங்கப்பொன் னாயுதமும் தளிருநிறச் சக்கரமும் பங்கமில்லா முத்துப் பவள நிறக்குடையும் கட்டான வைரக் கற்கோட்டை வாவிகளும் மட்டான செம்பவள வைரக்கால் மண்டபமும் உண்டுகா ணத்தனையும் உடனேபோய் நீங்களுந்தான் என்றுநான் வருவேன் இதுவரைக்குங் காத்திருங்கோ            நான்வந்த போது நல்லகதி யுங்களுக்குத் தான்தருவேன் போகுமெனத் தங்களிரு பேர்களுக்கும் விடைகொடுத்து ஸ்ரீரங்கம் மேவி யவரிருக்கத் திடமுடனே போகமுனி தேவி சலமடவும் வந்தந்த மாயவரின் வாழ்வெல்லாங் காத்திருந்தார் சென்றந்த மாயவனார் ஸ்ரீரங்கமே யிருந்தார் மாயன் ஸ்ரீரங்கம் வாழ்ந்திருக்கு மப்போது மாயக்கலி செய்த மாயங்கே ளன்போரே சாத்திரத்தை யாறாய்த் தான்வகுத்து வேதமதை மாத்திரமே நாலாய் வகுத்தனன்கா ணம்மானை நட்சேத் திரத்தை நல்லிருபத் தேழாகப் பொய்ச்சேத் திரமாய்ப் பிரித்தான்கா ணம்மானை கோளொன் பதுக்குக் கூடுபன்னி ரண்டாக நாளேழு வாரம் நாட்டிவைத்தா னம்மானை பக்கமது பத்தஞ்சு ஆகப் பவம்பிரித்து தக்கமது லோகம் தானாண் டிருக்கையிலே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(128,'சலந்தவசு','மாந்திரத்தால் செய்த வறுமைகே ளன்போரே பாந்தள்வாய்க் கட்டி பரிசுகெட ஆட்டிடுவான் சந்திரனை மங்கவைப்பான் சமுத்திரத்தைப் பொங்கவைப்பான் மந்திரத்தால் தெய்வமதை மாடுபோல் வேலைகொள்வான்      கற்பைக் கலக்கிடுவான் கன்னி யழித்திடுவான் வெற்பைத் தகர்த்திடுவான் வெடியைத் தடுத்திடுவான் ஆண்பெண்கள் தம்மை அலைச்சலது செய்திடுவான் கோண்பிரித்துக் கட்டிக் குடிகெடுப்பான் மானிடரை பசுவை யடைத்துப் பட்டினிகள் போட்டிடுவான் கசுவை மறைத்திடுவான் கண்ணைக் கெடுத்திடுவான் மிருகமதைக் கட்டி இரையிடாக் கொன்றிடுவான் கருவை யுருவழிப்பான் கன்னி சிறைவைப்பான் மானிடர்க்குக் குட்டம் வருத்தியே கொன்றிடுவான் வீணிலே மானிடரை விசமடக்கிக் கொன்றிடுவான் மாரிதனை மறைப்பான் வருத்தவென் றாலழைப்பான் ஏரிதனை வெட்டி இயலழியச் செய்திடுவான் அடப்பமிடத் தாயை ஆளாக்கி வைத்திடுவான் குடைப்பிடிக்கப் பிதாவைக் கொண்டிடுவான் வேலையது பேயை வருத்திப் பூசை மிகக்கொடுத்து தேயமதில் மானிடரைச் செத்திறக்க வைத்திடுவான் கடலதுக்கும் மாமலைக்கும் கற்கோட்டை தாண்டவிட்டுத் தடவரைக ளெல்லாம் தகர்த்துப் பொடிப்படுத்திப் பூலோகத் தேசம் புவனமெல் லாம்பரந்து மேலோக மெல்லாம் மிகப்பரந்தா னம்மானை  கயிலை யெமலோகம் கரியதெய்வ லோகமெல்லாம் அகிலமே ழும்புரண்ட அதிசயங்கே ளன்போரே சிவந்தா னினைத்தால் செல்லார்கள் தேவர்களும் தலந்தா னிளகித் தான்வந்த தாலேதான் மூவர்க ளூரும் முறைமை மிகத்தவறி தேவர்க ளூரும் தியங்கிமிகத் தான்புரள');");


