        db.execSQL("INSERT INTO akilathirattu2 VALUES(129,'நந்திவரவு','தெய்வலோ கமுறைமை திசைமயங்கித் தான்புரள மெய்கொண்ட லோகமெல்லாம் மிகமயக்க மானதினால் ஈசுரனா ரப்போ ஏதெனவே தான்வெகுண்டு வீசுபுகழ் நந்திதனை விரைவாய் வரவழைத்து மாமுனி யேகயிலை வரம்பழியக் காரணமேன் ஓமுனியே நீயும் உள்ளதெல்லாஞ் சொல்லெனவே என்றந்த நந்தியோடு ஈசுரனார் தான்கேட்க அன்றந்த மாமுனியும் அரனா ரடிதொழுது நல்லதுகா ணீசுரரே நம்மள்தனக் கிங்கேதான் செல்லாது இங்கிருந்தால் சிவனே யெனத்தொழுது பூலோக மேழும் பொன்னுலோக மேழும் தாலோக முதலாய்த் தவலோ கம்வரையும் ஈரேழு லோகம் இருபுத்தி யானதெல்லாம் பாரெல்லா மிப்படியே பரந்து இருப்பதெல்லாம்  சொல்லவே ணுமென்றால் சிவகயிலை விட்டேநாம் நல்லபர லோகமதில் நாம்போவோ மீசுரரே என்றந்த ஈசுரரை இறைஞ்சி முனிதான்கூட்டி அன்றந்த மாமுனியும் அரனாருந் தானேகி சென்றார் பரலோகச் சீமையிலே வந்திருந்து வண்டாடுஞ் சோலை வைடூரியக் கோயில் கோவிலவர் புக்கிக் குருவை மிகப்போற்றி ஆவலுடன் மாமுனியும் அருளுவா ரம்மானை நல்லபர மேசுரரே நாடுதடு மாறினதை எல்லாம் நீர்கேட்க எடுத்துரைப்பே னென்றுமுனி மாமுனியு மீசுரரை வணங்கிப் பதங்குவித்து ஓமுனியும் நன்றாய் உரைப்பார்கா ணம்மானை கேட்டீரோ ஈசுரரே கேடுவந்த செய்தியெல்லாம் தீட்டுகிறே னென்று செப்புவா ரன்போரே மாயன் வைகுண்டம் மறையவென்று பொய்ச்சடலக் காய மிழந்து காணா துருவெடுத்து ஏகி வரும்வழியில் ஏந்திழையாள் தெய்வகன்னி தாவிச் சுனையாடித் தாங்கள்நிற்கும் வேளையிலே கண்டந்த மாயன் கன்னியர்மே லிச்சைகொண்டு பண்டந்த யோகமுனி பச்சைமால் தன்றனக்கு மேலோகத் தார்களெல்லாம் விஷ்ணுவின் நாதமதில்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(130,'நந்திவரவு','பூலோகந் தன்னில் பிதிராகு வாரெனவும் பிதிரில் தவமிருந்து பிறந்தவழி தன்னையெல்லாம் சதிராக வந்தெடுத்துத் தர்மயுக மாள்வாரென்று யோகமுனி யிட்ட உறுசாப மத்தனையும் போகமதில் மேலோகப் பிறப்பெல்லாந் தோணவைத்துக் கன்னி வயிற்றிலுறக் காட்டில் மிகப்பிறந்து தன்னிக ரில்லாத சான்ரோ ரெனவளர்ந்தார் வளர்ந்தவர்கள் ராச்சியத்தை வகையாக ஆண்டிருக்க இழந்த கலிதோன்றி இவன்சென்றா னவ்வுகத்தில் கலிய னவன்செல்லக் கைமறந் தவ்வுலகில் பொலிவுள்ள தர்மம் பொன்றிச்சே நீதமதும் முன்னிருந்த சாஸ்திரமும் முறையும் மிகத்தவறி பின்னுதித்த நீசன் பிரித்தான்காண் வெவ்வேறாய் ஆனதால் பூலோகம் அழிந்து மிகத்தவறி ஊன மடைந்தார் உலகிலுள்ள சான்றோர்கள் தந்திர மந்திரத்தால் சான்றோரைத் தான்மயக்கிப் பந்தி யழித்ததினால் பத்தினியாள் பெற்றமக்கள் ஒண்ணுக்கொண் ணெதிர்த்து ஒத்துமிக வாழாமல் பெண்ணுக்கு ஆணும் பிரிவு முறிவாகித்  தாய்க்குத்தான் பிள்ளை தாழாம லேபேசும் வாய்க்குவாய்ச் சொன்னாலும் மங்கைநல்லா ளென்றிடுவான் பிதாவை மிகநம்பார் புண்ணியமென் றேபாரார் இதாகவெல்லாம் நீசன் இடறுவரச் செய்ததினால் ஆனதா லிந்த வழிவழியே யங்குளது போனதால் நீசம் பொன்னுலோ கம்வரையும் ஏகிச்சு தையா இடறுநீ சக்கலியும் கோவிச்சு தையா கொடுங்கலியு மங்கேகி ஈதல்லால் பின்னும் இன்னுஞ் சிலநாளில் ஏதெல்லா மாகுதென்று ஈசுரரே பார்த்திருவும் மாயனை யும்வருத்தி வார்த்தைகே ளாதிருந்தால் தேய மிருள்மூடிச் சென்றிடுங்கா ணீசுரரே அப்போது ஈசுரரும் அதற்கேது செய்வோமென்று செப்பேடு வொத்த சிவனாரும் நொந்திருந்தார் மாமுனியே மாயவனை வருத்தவென்ன செய்திடுவோம் ஓமுனியே நீயும் உரையென்றா ரன்போரே ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(131,'பேய்ப்பிறப்பு','அப்போது மாமுனியும் அரனா ரடிவணங்கி இப்போது தானதவம் இல்லையே லோகமதில் தவஞ்செய்து மாயவரைத் தான்வருத்த வென்றாலும் பவஞ்செய்த காலம் படுமோ தவமிருக்க  என்றுரைத்து மாமுனியும் இன்னமொன்று சொல்லிடுவான் நன்றுநன்று ஈசுரரே நானுரைக்கக் கேட்டருளும் இருள்கொண்ட காலமதால் ஏற்றதர்மஞ் செல்லாது மருள்கொண்ட காலமதால் வம்புக்கேநே ராகுமையா ஆணுவஞ்சேர் பேய்க்கணங்க ரம்பைக்கோர் மாலைதன்னைப் பூணும்நீர் தவசு புரிந்தா லரிவருவார் என்று முனிசொல்ல ஈசுரனார் சம்மதித்து அன்று பேய்க்கணங்க அசுமாலை யையணிந்து இருந்தா ரரிதான் இப்போவர வேணுமென்று தருந்தார மார்பன் தானறிந் தேதெனவே இருக்க வகையில்லாது எழுந்திருந்தா காயமதில் சுறுக்கா யுதித்துத் தோன்றினார் ஈசர்முன்னே அப்போது மாயவரை ஆதி யாவிப்பிடித்து இப்போ திதுவரையும் எங்கேநீர் போனீர்காண் என்றுதான் ஈசர் எய்த்திளைத்துச் சொல்லிடவே அன்றுதான் ஈசுரரை அரியாவித் தான்சேர்த்து பாண்டவர்க் குபகாரம் பண்ணி யிருந்ததுவும் வீண்டதுரி யோதனனை வெற்றிகொண்ட செய்தியதும் பொருப்பேறிப் பொய்ச்சடலம் போட்டுஸ்ரீ ரங்கமதில் இருப்பதுவுஞ் சொல்லி எனைவருத்துவா னேனென்றார்          அப்போ தரனார் ஆதிமா லோடுரைப்பார் இப்போது கலியன் இராச்சியத்தி லேபிறந்து பதிநாலு லோகமதும் பாரஇருள் மூடினதால் இதுநாள் வரையும் யானுமும்மைக் காணாமல் மனங்கலங்கிப் பேய்க்கணங்க மாலையிட்டு நானிருந்தேன் கனங்கொண்ட மாயவரே கயிலையிருள் மாற்றுமென்றார் நல்லா யிருக்குதுகாண் நவின்றதெல்லாங் கேட்பதற்கு எல்லா மிருக்கட்டென்(று) ஈசுரரே உம்முடைய கழுத்தில் தரித்ததையும் கழற்றி யெறியுமென்றார் வழுத்திடவே ஈசர் மாலை தனைக்கழற்றி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(132,'பேய்ப்பிறப்பு','கோபத்தா லக்கினியில் குறியா யெறிந்தனரே வேகத்தால் மாலை வெடித்ததுகாண் பேய்க்கணம்போல் கொள்ளைகொண்ட பேய்கள் கோடாமுக் கோடியுமாய்த் துள்ளிக் குதித்துச் சுற்றிக் குதித்தாடும் அப்போது பேய்கள் அரனா ரடிவணங்கி இப்போது ஈசுரரே எங்கள்பசி தீர்த்து மந்திர சாலம் மாய்மால மாரணமும் தந்திர சாலம் சர்வதுமே தாருமையா கொல்லச்சாவு சங்கிலியும் கொன்றவரைத் தானெழுப்ப வெல்லப்பிழை சங்கிலியும் வெடிப்பாக வேதாரும்  ஈசுரரும் தேவர்களும் எங்கபடத் துள்ளாக வீசுபல வரங்கள் விடையாகத் தாருமையா அப்போது பேய்கள் அதுகேட்ட தத்தனையும் இப்போது கொடுக்க ஈசுரருஞ் சம்மதித்து மாலோடும் வேதா மறையவனோடுங் கேட்பார் கேட்டிருந்த நாரணரும் கிருபையுட னீசுரரைத் தேட்டமுடன் பார்த்துச் செப்புவா ரம்மானை ஈசுரரே பேய்களுக்கு இவ்வரங்க ளீந்தாக்கால் வீசுங்காண் லோகமதை விழுங்குங்கா ணிப்பேய்கள் தூயவரே பேய்கேட்ட சுத்தவர மத்தனையும் ஞாயமில்லை யென்று நானுரைக்கப் போகாது ஆனாலும் பேய்களுக்கு அருள்வீரா லிவ்வரங்கள் ஞானாந்திர மான நாரத மாமுனியை வருத்தியே பேய்களுக்கு வரங்கொடுத் தவன்கையிலே இருத்தி யணைவைத்து ஏழ்ப்பியும் பேய்களைத்தான் பேய்களுட பேரில் பிழைத்ததுதான் குற்றமென்றால் தேயக்கலி மாளுகையில் சிவவடகை லாசமதில் முனிசிறையும் பேய்களுக்கு உயிரழிவு மென்றுசொல்லித் துனிவான முனியுடனே சொல்லி யனுப்புமென்றார் அப்போது ஈசுரரும் அந்தமுனியை வருத்தி  இப்படியே சொல்லி ஏழ்ப்பித் தனுப்பிடவே மாமுனியுஞ் சம்மதித்து வரம்வேண்டிப் பேய்களுந்தான் தாமுனிந்து பூலோகம் தன்னிலே வந்திடுமாம் பேய்ப்பூ லோகமதில் போயிடவே ஈசுரரும் ஆயனா ரோடே அருளுவா ரன்போரே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(133,'பேய்ப்பிறப்பு','கயிலை யிருளைக் கடத்திவைக் காதிருந்தால் அகில மதிலிருப்பு அல்லவே அச்சுதரே அப்போது மாயவனார் ஆதி தனைநோக்கி இப்போது நீங்கள் எல்லோரு மிக்கவேதாம் நன்றான ஆண்டிகள்போல் நற்றா வடங்களிட்டுப் பண்டார வேசமதாய்ப் பதிந்திருங்கோ வென்றுசொல்லி நடந்தார் சீரங்க நகரிதனி லெம்பெருமாள் மாயனுரை மாறாமல் வாய்த்தமே லோகமுள்ளோர் ஈசர்முத லாண்டியென இருந்தார்கா ணம்மானை ஆனதால் கலியன் அங்கே யனுவிலகி மான மனுப்போலே வாழ்ந்திருந்தா ரம்மானை பூலோக மெல்லாம் பொய்யான மாகலியன் மாலோ சனையிழந்து மாறியே மானிடவர் சாதி யினம்பிரித்துத் தடுமாறி மானிடவர் ஊதி னங்களாக ஒன்றுக்கொன் றேயெளிதாய் பிரட்டுருட்டாய் மானிடரைப் பிலமுள்ளோர் தானடித்து மருட்டும் புரட்டுடனே மாநீச னாளுகையில் மாரி யதுபொழிந்து வைகை யதுவுடைத்து ஏரி வழிந்து இராச்சியத்தைத் தானெடுக்க வெள்ளம் பெருகி வெம்மருண்ட தம்மானை உள்ளமகிழ்ச் சோழனுக்கு உடனேதா னாளோடி மனுநீதிக் காவலவா வைகையடை யாதிருந்தால் இனித்தேசந் தன்னை யாம்தேட ஞாயமில்லை ஊரை யரித்து உவரிதனில் கொண்டேகும் பாரை மிகஆளும் பத்தியுள்ள சோழமன்னா என்றுகுடி யானவர்கள் இராசனுக் கேவுரைக்க அன்றுதான் வைகை அணையடக்க வேணுமென்று எல்லா வகைச்சாதி இப்போ வரவழைத்து வல்லபுகழ் மன்னன் வைகை யணையில்வந்து மண்ணோடு கல்லும் மரங்கள்மிகு வைக்கோலும் எண்ணெண்ணக் கூடாத ஏதுவகை யானதெல்லாம்  கொண்டுவந் தேயணையில் கூறிட்டுத் தானடைக்க அன்றுஅடை படாமல் அறிவழிந்து சோழமன்னன் அய்யோ பாழாக அவனிதான் போகுதென்று மெய்யோடு மெய்குழறி வெம்மருண்டு நிற்கையிலே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(134,'சோழன் வினை','கோளனென்ற மாநீசக் குலத்தி லுதித்துவந்த ஈழனொரு பொல்லாதான் என்சொல்வா னம்மானை மன்னவனே இந்தவைகை மலையெடுத்து வைத்தாலும் இன்னமிது கேளாது ஏலாது நம்மாலே தெய்வகுலச் சான்றோராய்ச் சித்திர மாகாளி கையதுக்குள் பிள்ளையெனக் கட்டாய் வளருகிறார் அந்தச் சான்றோரை அழைத்திங்கே கொண்டுவந்து இந்த அணையடைக்க ஏலுமென்றா னம்மானை அப்போது சோழன் அந்நீசக் கலியதினால் இப்போது மயங்கி என்சொல்வான் மன்னவனும் மந்திரியே நம்முடைய வாய்த்த படைத்தலைவா தந்திரியே நீங்கள்சென்று சான்றோரைத் தான்கூட்டி வாருமென்று சொல்லி மன்னவ னேவிடவே சேருமென்று சொல்லச் செவுகர் தாம்விரைந்து அழைத்துவந்தார் சான்றோரை அரசன்மிகக் கொண்டாடித் தழைத்தபுகழ் சான்றோரே சந்தோசமாக இப்போ  வைகை தனையடைக்க வழிபாரு மென்றுரைத்தான் செய்கை முடிச்சான்றோர் தேசமன்ன னோடுரைப்பார் நல்லதல்ல மன்னவனே நம்மோ டிதுவுரைக்க இல்லை யிந்தவேலை இதற்குமுன் கேட்டிலையே வெட்டாப் படையை வெற்றிகொண்டோ மும்மாலே பட்டாங்கு எல்லாம் பகர்ந்தாரே சோழனுடன் மாயக் கலியதனால் மன்னவனுங் கேளாமல் ஞாயமொன்றும் போகாது நளிமொழிகள் பேசாதே குட்டையினால் மண்ணெடுத்துக் குளக்கரையைத் தானடைக்கக் கெட்டியல்லாமல் வேறு கெறுவிதங்கள் பேசாதே என்றுரைக்கச் சான்றோர் இயம்புவா ரம்மானை நன்றுநன்று மன்னவரே நமக்கு அழகல்லவே இவ்வேலை யொன்றும் எங்களோ டீயாமல்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(135,'சோழன் வினை','எவ்வேலை சொல்வீரோ யாமதற்குள் ளதென்றார் கேட்டந்த மன்னன் கிறுக்க முடனிறுக்கித் திட்டினான் சான்றோரைச் சினத்தான்கா ணம்மானை அப்போது சான்றோர் அதற்கிசையாமல் நின்றார் இப்போது சோழன் ஏதுசொல்வா னம்மானை நான்வேலை சொன்னால் நகட்டுவதோ உங்களுக்கு தான்பாரு மென்று தன்தள கர்த்தருடன்  வேலையது கொள்ளுமென்று விசைகாட்டினான் கெடுவான் தூல மறியாமல் துள்ளியே சேவுகர்கள் சூழ வளைந்து துய்யசான் றோர்களையும் வேழம் பலதை விட்டுப் பிடித்திடவே சான்றோ ரடுக்கல் சாரவகை யில்லாமல் மீண்டகலத் தோற்று வெளியிலே நின்றிடவே அப்போது சோழன் அவனானை கொண்டுவந்து இப்போது சான்றோரை ஏறிப் பிடித்தனனாம் சான்றோர்கள் சோழனையும் தட்டப் படாதெனவே நன்றியுடன் நின்றார் நாடி யவன்பிடித்துக் குட்டையெ டென்றிடவே கூடா தெனவுரைக்கத் தட்டினான் வைகையிலே தலையைச் சாணான்றனக்கு ஆனைதனை விட்டு அரசனந்தச் சோழமன்னன் சேனைத் தலைவர் சிறந்தசான் றோர்கள்தம்மில் கொன்றா னொருவனையும் குளக்கரையி லம்மானை பின்னா லொருவனையும் பிடித்துக்கொடு வாருமென்றான் குட்டை யெடென்று கூறினான் மாபாவி திட்டமுடன் நாடாண்ட தெய்வ குலச்சான்றோர் முன்னிறந்த மன்னனிலும் மோசமோ நாங்களுந்தான் இன்னமிந்தக் குட்டை யாங்கள்தொடோ மென்றனராம் பின்னுமந்தச் சோழன் பிடித்தொரு வன்தனையும் கொன்றான்காண் வைகைதனில் குஞ்சரத்தை விட்டிடறி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(136,'சோழன் வினை','நன்றி மறந்து நாடாண்ட சோழமன்னன் கொன்றான் காண்ரண்டு குலதெய்வச் சான்றோரை அப்போது வித்யா தரமுனிவர் தானறிந்து செப்போடு வொத்த திருமா லருகேகி மாயவரே உம்முடைய மதலையேழு பேரில் காய மழித்தான் கரிகாலச் சோழனவன் என்ற பொழுது எம்பெருமா ளப்போது அன்றுபுட் டேயருந்தி அவளாட்போல் கோலமது கொன்று குமாரர்களைக் குசல்செய்த தும்பார்த்து அன்றுவை கையடைத்து அடிகள்மிகப் பட்டவரும் ஸ்ரீரங் கந்தன்னில் சிறந்தகோ பத்தோடே சாரங்கர் வந்து தானிருந்தா ரம்மானை மாகாளி தானறிந்து மக்களைத்தான் கொன்றதினால் ஓகாளி சோழன் ஊர்வறுமை யாகிடவும் பன்னிரண் டாண்டு பாரில்மழை பெய்யாமல் உன்னினாள் மனதில் உடனே மழைசுவறிப் பெய்யாமல் சோழன் பேருலகம் பஞ்சமதால் அய்யமது ஈயாமல் அறிவழிந்து வாடினனே  இறந்தசான் றோர்களுட ஏந்திழைமா ரெல்லோரும் சிறந்த தவம்புரிய சென்றனர்கா ணம்மானை சென்ற தவத்தின் செய்திகே ளன்போரே அழுக்குக் கலையணிந்து அணிந்தபொன் தாளாமல் இழுக்குச் சொல்லீந்த ஈழன் பழிகொள்ளவும் இறந்த மன்னவர்கள் எழுந்திருந் தெங்களையும் சிறந்த மணத்தோடு உடன்சேர வந்திடவும் பழிசெய்த சோழனுசர் பகலநரி ஓடிடவும் அழிவாகிச் சோழன் அவன்மாண்டுப் போயிடவும் வரந்தாரு மென்று மாயவரை நெஞ்சில்வைத்துப் பரமானப் பெண்கள் பாரத் தவசுநின்றார் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(137,'ஸ்ரீரங்கம் விட்டுச் சுவாமி அனந்தபுரம் ஏகல்','இப்படியே மாதர் இங்கே தவசிருக்க அப்படியே மாயன் அவர்ஸ்ரீ ரங்கமதில் இருக்கும் பொழுது இரணியநீ சக்கலியன் கருக்காக லோகம் கண்டவிட மேபரந்து ஸ்ரீரங்க மானதிலே சேர்ந்திருக்கும் வேதியர்கள் நிதங் குளறி நெறிதவறிப் போனதினால் முறைவைத்த பூசை முந்தி முறையெனவே மறையவனும் பூசை வந்தே முகித்திடவே பொறாமல் மற்றொருவன் பூசையெனக் கென்றுசொல்லி       மறவாத மாயவரை வணங்கிநிட்டை செய்யவென்று முப்பது வேள்வி மொகுமொகெனத் தான்வளர்த்து இப்போ நான்வீழ்வேன் இதில்மாயன் வாராட்டால் என்றவ னெண்ணி ஏற்றவோ மம்வளர்த்து அன்றவன் வீழ ஆர்ப்பரிக்கு மவ்வளவில் மறையவனை மாயன் வந்தெடுத்துப் புத்திசொல்லி நிறையொத்த மாயன் நெடுமறையோ னைக்கூட்டி ஸ்ரீரங்கம் விட்டுத் திருவனந் தம்நோக்கிச் சாரங்கர் போகத் தானேகும் வேளையிலே நன்றான கேத்திரனும் நல்லமறை வேதியனும் அன்றிளகிச் சென்று அரனார் திருக்கோவில் வந்த பொழுது வானமதி லுள்ளோரும் நந்நகோ பால நாரா யணரிடத்தில் சென்று தொழுது தேவரெல்லாந் தெண்டனிட்டு இன்று பெருமாள் இங்கே யெழுந்தருளி வந்த வகையேது மாயவரே யென்றுசொல்லி அந்தமுனி தேவர்களும் அச்சுதரோ டீதுரைக்க நல்லதென்று அய்யா நாரா யணர்மகிழ்ந்து வல்ல பரமே சுரரை மிகவாழ்த்தி ஈசுரரே யென்றனக்கு இங்கிருக்கக் கூடாமல் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(138,'ஸ்ரீரங்கம் விட்டுச் சுவாமி அனந்தபுரம் ஏகல்','தேச மதைப்பார்த்துத் திருவ னந்தமேகி இருக்கவே யென்று எழுந்தருளி வந்தேன்காண் மருக்கிதழு மீசுரரும் மாமுனிவர் தேவர்களும் நீசனாய்த் தோன்றி நிரந்து பரந்திருந்த தேசத் திருவனந்தம் செல்லவகை யேதுவையா அல்லாமல் நீசன் அவனிடத்தில் போயிருந்தால் எல்லா மவன்றனக்கு ஈடாகிப் போகுமல்லோ கைவாய்த்து தென்று கலியனவன் கொண்டாடி மெய்வாய்த்து தென்று மேலாக மாநீசன் பரிகாசங் செய்வானே பார்முழுது மாநீசன் ஆனதா லங்கேக அச்சுதரே ஞாயமில்லை மான மழிந்தாச்சே மாகலியன் வந்ததினால் எல்லாங் கழியை ஈடழிக்கப் பாருமையா இல்லையே யானால் எங்களுக் கிங்கேதான் சென்ற இடமெல்லாம் சிறைபோ லிருக்குதையா என்றுதான் தேவர் ஈசர்முதல் சொல்லிடவே அன்று அவர்களுக்கு அச்சுதரு மேதுரைப்பார் நன்றுநன்று வானவரே நல்லபர மேசுரரே கலியேது நீசம் காணேது வையகத்தில் சலிவேது ராச்சியத்தில் தானேதும் நானறியேன்  அனந்தபுரம் போக ஆதியி லென்றனக்குத் தனந்தசுக முனிவன் சாபமுண் டானதினால் கொஞ்சநா ளானாலும் குடியிருக்க வேணுமங்கே வஞ்சகங்க ளில்லாத மாயன்வழி கொண்டனராம் ஈசர்தனை யனுப்பி எம்பெருமா ளச்சுதரும் வாசமுள்ள சேத்திரனும் மறையவனுந் தேவர்களும் சங்க மதுகூடித் தத்திதத்தி யாய்வரவே வங்கத் திருவனந்தம் வழிநோக்கித் தான்வரவே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(139,'பிரம்ம ரிஷிகள்','வழியிலோ ரற்புதந்தான் மாயவனார் கண்டுமிகக் களிகூர்ந் தவருடனே கட்டாக ஏதுரைப்பார் மாமுனியே தேவர்களோ வழியி லொழுங்கொழுங்காய் ஓமுனியே நின்றதையும் உரைப்பீர்கா ணென்றுரைத்தார் அப்போது மாமுனியில் அருண முனிவனொன்று செப்போடு வொத்த திருமாலை யும்பணிந்து மாயவரே நான்முகனும் வாழும் பிரம்மமதில் ஆய கலையிருஷி ஐம்பத் தொருநான்கோர் பிரமன் பிறப்பைப் புகுந்தெடுத் திவ்விருஷி வரமான புத்தகத்தை மாறாட்டஞ் செய்ததினால் அறிந்தந்த வேதாவும் அவர்கள் தமையழைத்துச் செறிந்த இருஷிகளைச் சிலைக்கல்லாய்ச் சாபமிட்டார் அப்பொழு திவ்விருஷி அயனைத்துதித் திச்சாபம் எப்பொழு திச்சாபம் ஏகுமென்றார் மாயவரே வேதா தெளிந்து விஷ்ணுஸ்ரீ ரங்கம்விட்டுத் தீதோர் திருவனந்தம் செல்லவரும் வேளையிலே வந்து சிலைதனையும் மாயவனார் தொட்டிடுவார் சிந்து திருக்கைதான் சிலைமேலே பட்டவுடன் தீருமுங்கள் சாபமென்று சிவயிருஷி யானோர்க்குப் பேருல கம்படைத்த பிரமன் விடைகொடுத்தார் அந்தப் பொழுதில் ஐம்பத்தொரு நான்குரிசி இந்தக் கற்சிலையாய் இவரிருந் தாரெனவே மாமுனிவன் சொல்ல மாயவரும் நல்லதென்று தாமுனிந்து கற்சிலையைத் தான்தொட்டா ரம்மானை உடனே இருஷிகளாய் உருவெடுத்து மாலடியைத் தடமேலே வீழ்ந்து தானாவி யேகுவித்து அன்று பிரமா அடியார்க்கு இட்டசாபம் இன்றகல வைத்து இரட்சிக்க வந்தவரே எங்களுக்கு நல்லகதி ஈந்துதா ருமெனவே திங்கள்முக மாயருட திருப்பாதம் போற்றிநின்றார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(140,'பிரம்ம ரிஷிகள்','நல்லதுதா னென்று நாரா யணர்மகிழ்ந்து வல்ல இருஷிகளே வாழ்வெங்கே வேணுமென்றார்  மேலோக வாழ்வு வேணுமோ அல்லவென்றால் பூலோக வாழ்வு போதுமோ வென்றுரைத்தார் அப்போ திருஷியெல்லாம் அவனியி லெங்களுக்கு இப்போ வரங்கள்தந்து ஏகவைத்தால் போதுமையா நல்லதுதா னென்று நல்ல இருஷிகட்கு வல்லத் திருமால் வரங்கொடுப்பா ரம்மானை பிச்சையது வாங்கிப் பெருமை யதாயருந்தி மிச்சமது வைக்காமல் விழிபரந்து பாராமல் சீமைக்கொரு இருஷி செல்லுங்கோ ஆண்டியெனத் தாண்மை பரதேசி தானாகி வீற்றிருந்து பூசை பெலிகள் பீடமிட் டேராதிருந்து ஆசைக் கருத்தை அறுத்து வொருநினைவாய் மாசணு காமல் மனதில் நமைத்துதித்து ஓசை யுடனே உலக மதில்நீங்கள் வைகுண்ட மென்று வையகத்தே வாழுமென்று பொய்குண்டம் நீக்கிப் பூலோக மேயிருங்கோ தந்த வரத்தில் தப்பி நடந்ததுண்டால் வந்தங் கிருந்து வருத்தியுங்கள் தம்மையெல்லாம் அவரவர்கள் செய்த அக்குற்றந் தான்கேட்டு எவரெவர்க்குந் தக்க இயல்பே தருவோமென்றார்  நல்லதுதா னென்று நாடி இருஷியெல்லாம் செல்லப்பர தேசிகளாய்ச் சென்றாரே சீமையிலே இருஷி களையனுப்பி எம்பெருமாள் தான்மகிழ்ந்து துரிச முடனனந்த சீமைநோக் கிநடக்கத் தேவர்களும் வானவரும் ஜேஜே யெனநடக்கத் தாவமுட னனந்தம் தானோக்கி மால்நடக்க அனந்த புரம்நோக்கி அச்சுதனா ரேகுகையில்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(141,'எக்காள துர்க்கை','புனந்தனிலோர் பொருப்புப் பூவையுருப் போல்கிடக்கக் கண்டெம் பெருமாள் கால்கொண் டுதைத்திடவே குன்றுபோ லேயுடம்பும் குஞ்சரம்போல் கைகாலும் துண்டு மலைபோல் துய்யமூக்கு முலையும் வாய்கண் ணொருமலையாம் வயிறுமூணு மலையாம் கொண்டை பன்னிரண்டு குறுக்கமுண் டன்போரே பண்டே திருமால் பம்பழித்தத் தாடகைபோல் நின்றாளே யண்டபிண்டம் நிறைந்த சொரூபமதாய் கண்டாரே எம்பெருமாள் கனத்தவி சேடமென்று தேவர்களே வானவரே சேத்திரனே வேதியனே பாவலரே கல்தான் பரும்பெண்ணாய் வந்ததென்ன சொல்லுவீ ரென்று திருமா லுரைத்திடவே வெல்லும் புகழ்தேவர் விளம்புவா ரம்மானை  ஏம னிணையான எக்காளத் துர்க்கையிவள் சாமி சிவனார்தன் சொல்லையிவள் தட்டினதால் கோபித் திவளைக் குன்றுபோல் சாபமிட்டார் ஏகி வரும்போது இவள்தானு மீசுரரை வணங்கியிச் சாபமெப்போ மாறுமென்றாள் மாயவரே அணங்குக்கு ஈசர் அருளினது கேளுமையா அனந்த புரமதிலே ஆனநதி மேலே வனந்தமால் பள்ளிகொள்ள வருகின்ற அவ்வழியில் உன்சாபந் தீர்த்து உன்னை உலகதிலே பின்சாப மிட்டுப் போக விடைதருவார் என்று சிவமுரைக்க இப்படியே வந்தவளும் குன்றுபோ லேகிடந்தாள் குருவேயுன் பாதமதால் அவள்சாபந் தீர்ந்து ஆயிழை போல்வடிவாய் இவள்தானும் வந்தாள் எனச்சொன்னார் தேவர்களும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(142,'எக்காள துர்க்கை','நல்லதென்று நாரணரும் நாரிதனைக் கொண்டாடி வல்லவளே யெக்காள மடந்தையே யுன்றனக்கு ஏதுனக்கு வேணுமென்று என்னோடு கேளுஎன்றார் வாதுக்கு வல்லகியாள் மாய ருடன்கேட்பாள் நாரணரே அய்யாவே நான்தான் முற்காலமதில் பாரமுள்ள செந்திருஷி பாரியாய் நானிருந்தேன்  அப்போது என்றனக்கு அழகிது இல்லையையா செப்போடு வொத்த சிவகாமி போலழகு நன்றாக என்னுடைய நல்லபர்த் தாவுடனே ஒன்றாக வாழ்ந்து உறவா டிருக்கையிலே என்பேரி லிச்சை ஏமன்மிகக் கொண்டாடி வம்புசெய் தென்னுடைய மன்னவனைக் கொன்றான்காண் ஆனதா லேமனுக்கு அழிவுவர வேணுமென்று மானத் தவமிருந்து வருந்தினே னீசுரரை அப்போது ஈசுரரும் அடியாள் மனந்திருத்தி இப்போது அந்தகனை இறக்கவைத்தால் ராச்சியத்தில் நருட்பெருத்துப் பூலோக நாடுதரியா தெனவே பொறுத்துக்கோ கொஞ்சம் பூவையே யென்றுரைத்தார் என்னை மனந்திருத்தி ஏமனோடே சேர்த்தாலும் மன்னனை வதைத்ததுதான் மறவாம லெப்போதும் திவசமொரு நேரம் சிவனாரை யானோக்கிப் பவத இயமனுக்குப் பகையாகக் கேட்டிருந்தேன் ஆனதா லீசர் அறிந்தே யெனைநோக்கி ஈன முடன்பேசி இகழ்த்தினா ரீசுரரும் கொஞ்சம் பொறுக்கவென்று கூறினேன் பெண்கொடியே மிஞ்சவல்லோ செய்தாய் எனவெகுண் டாதிபரன் மலைபோ லுடம்பும் வயிறு மிகப்பெருத்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(143,'எக்காள துர்க்கை','அலைபோற் பரந்த அங்கம் பெரும்புடமாய்க் கல்லது போற்கிடந்து காலனைக் கொல்லும்வகை வல்ல வகையாலும் வருந்திக்கோ என்றுசொல்லிக் கோபித்தார் முன்னே குன்றுபோ லேகிடக்கச் சாபித்தா ரென்னை சாமியுன் பாதமதால் தோன்றினே னென்சாபத் துயரறுத்தேன் மாயவரே வீன்றிய அந்தகனை வேரோ டறுத்திடவே வரந்தா ருமையா மாயவரே யென்றுசொல்லி சிரமுரத்தப் பெண்கொடியாள் தெண்டனிட்டாள் மாயவரை அப்போது மாயவரும் அவளை முகம்நோக்கி இப்போது பெண்கொடியே யான்சொல்லக் கேட்டிடுநீ அந்தகனைக் கொல்லவென்று அருந்தவசு பண்ணிடுநீ வந்த யுகமாறி வலியபெலத் தர்மயுகம் உதிக்கும் பொழுதில் உன்தவ சின்படியே சதிக்குகந்து தானால் சண்டனழி வாகுமென்றார் அப்போது பெண்கொடியும் மானத் தவம்வளர இப்போ விடையருளும் என்றுகேட்டு வேண்டியவள் நின்றாள் தவத்தில் நீலியாய்ச் சண்டனுக்கு வண்டூறிக் கன்னி மன்னன் பழிவாங்க ஆதியைப் போற்றி அருந்தவசு பண்ணிடவே சோதித் திருமால் திருவனந் தம்நோக்கி நடக்கத் திருமால் நளின முடன்மகிழ்ந்து வடக்குக் கயிலாச வழியே வருகையிலே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(144,'வானோர்க்கு அருளல்','தெய்வலோகத் திலுள்ள தேவாதி தேவர்களும் வைகுண்ட லோகமதில் வாழுகின்ற தர்மிகளும் சிவலோகம் வாழும் சிட்டர்முதல் வானவரும் தவமான வேதச் சதுர்மறையோன் தன்னுகத்தில் கருதி ரிஷிமுதலாய்க் கட்டாக நாற்புவியில் இருக்கின்ற வானவர்கள் எல்லோரு மெண்ணமுற்று மான வரம்பு மகிமைகெட் டேகுமுன்னே வான மதுவிட்டு வடகயிலை போவோம்நாம் கயிலை வரம்பழிந்து கட்டுமிகத் தப்பினதால் அகில மழிவதற்கு அடையாள மித்தனைதான் அல்லாமல் குறோணிமுதல் அந்நீசக் கலியன்வரை எல்லாஞ் சரியாகி எவ்வோர்க ளானாலும் கல்லாத பொல்லாக் கலியனுட மாய்கையினால் நல்லோராய் மேற்பிறந்து நளினமுற்று வாழ்வரென்று வேதா கணக்கில் விதித்துரைத்தார் முன்னாளில் நாதாந்த வேதம் நழுவிமிகப் போகாதே  இப்படியே தேவர் எண்ணமுற்று வாடினரே முப்படியே யுள்ள ஊழிவி தியெனவே கடைச்சாதி யான கலிச்சாதி யானதிலே படையாமல் நம்முடைய பங்குவம்மி சத்தோராய் ஒண்ணா மதுகுலந்தான் உயர்தெய்வச் சான்றோரில் வண்ணமுள்ள வேதா மனுவாய்ப் பிறவிசெய்ய ஒன்றாக நாமளெல்லாம் உவந்துதவஞ் செய்யவென்று நின்றார் தவத்தில் நிறைவோன் பதம்போற்றி தங்கள் குலமான சான்றோர்கள் தங்குலத்தில் எங்கள் தமைப்பிறவி இப்போசெய்ய வேணுமென்று பிறந்திறந்த போதும் பின்னுமந் தப்பிதிரில் மறந்திடா வண்ணம் மனுவி லுதித்திடவும் ஆதிமகா மூலத்து ஆதிநா ராயணரே நாதியா யெங்களையும் நாடிமிக வந்தெடுத்து எங்கள் துயரம் எல்லா மவர்மாற்றி சங்கடங்க ளில்லாத தர்ம பதியருளிக் கிரீடமுஞ் செங்கோலும் கீர்த்தியுள்ள முத்திரியும் வீரியமாய்த் தந்து மேலோக முமகிழ எண்ணுஞ் சாகாமல் இருக்கும் பதவிதந்து மண்விண் புகழ வரந்தாரு மென்றுசொல்லி நின்றா ரதையும் நெடியோன தையறிந்து கொண்டாடி யேதெனவே கூறினார் தேவருடன் நின்ற நினைவை நெடுமால் தனக்குரைக்க அன்றந்தத் தேவருக்கு அலையாத புத்திசொல்லி நல்லதுதான் தேவர்களே நாடுவது காரியந்தான் வல்ல தவசு மனம்பிரியாச் செய்யுமென்றார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(145,'பசுவும் பெண்ணும்','அச்சுதரும் தானடந்து அனந்தபுர மேகுகையில் பச்சுடம்பாய் நின்ற பசுவுமோ ரேந்திழையும் நாராய ணாவெனவே நாடித் தவமிருக்கச் சீராய்த் திருமால் சிறந்ததவங் கண்டருளி ஏது பசுவே ஏந்திழையே வுங்களுக்கு நேதுவில்லா வண்ணம் நெடுந்தவசு பண்ணினதேன் அப்போ துபசுவும் அச்சுதருக் கேதுரைக்கும் இப்போது மாயவரே என்னுடைய புத்திரனைக் கொல்லும் படியாய்க் கோதை யிவள்தவசு செல்லும் படியாய்ச் சிந்தை மிகக்கலங்கி புத்திரனு மாண்டால் பிள்ளையா யென்றனக்கு உத்திரக் கன்றாய் உடன்பிறக்க வேணுமென்று நின்றேன் தவசு நீலவண்ண ருண்டெனவே என்றே பசுவும் ஈதுரைக்க ஏந்திழையும்  நன்றாகப் பார்த்து நாரா யணருரைப்பார் ஒண்டொடியே உன்றன் உற்றவழக் கேவுரைநீ அப்போது பெண்கொடியும் அச்சுதரைத் தானோக்கிச் செப்போடு வொத்த தேவியுஞ் சொல்லலுற்றாள் அய்யாவே யெனக்கு ஆளான வீரனைப்போல் மெய்யா யொருமதலை விமல னருளினர்காண் மதலை வளர்ந்து வயதுபதினா லாகுகையில் குதலை மொழிகேட்டுக் கொண்டாடி நான்மகிழ்ந்து இருக்குமந் தநாளில் இப்பசுவின் புத்தினர்தான் உருக்கமுட னென்பேரில் உள்ளாசை கொண்டான்காண் அதட்டினே னானதற்கு ஆகட் டெனவுறுக்கி மதட்டி மதலைதனை மாளவைத்தான் மாபாவி ஆனதி னாலடியாள் அறமெலிந்து தான்வாடி போனேன் பிரமா பூசாந்திரக் கணக்கில் உள்ள விதியோ ஊழி விதிப்படியோ கள்ளக் கணக்கன் கண்மாயமோ எனவே பார்த்தேன் மகன்தான் படவிதி யங்குமில்லை ஆர்த்தேன் நான்கோபம் அக்கினிபோ லெமீறி சிவனுக் கபயம் செவியறிய விட்டேனான் இவளுக்கு வேண்வடி ஏதுசெய்வோ மென்றுசொல்லி ஒருவரு மென்னுடைய ஊழிவிதி கேட்கவில்லை வருவது வரட்டெனவே வந்தே தவசுநின்று மகன்பழிதான் வாங்க மாயவரே உம்மருளை அகமிருத்தி நானும் அருந்தவசு செய்தேனான் என்றுரைக்க ஏந்திழையும் எம்பெருமா ளேதுரைப்பார் நன்றுநன்று பெண்கொடியே நல்ல தவம்புரிந்தால் நினைத்த படியே நிறைவேறு மென்றுரைத்துக் கனத்த பசுவேநீ கட்டாய்த் தவமிருந்தால் உன்றனக்குச் சித்திரனும் ஒருகன்றாய்த் தான்பிறந்து உன்றன் மகனார்க்கு ஏவல்செய்ய வைத்திடுவேன் என்று பசுவதுக்கும் ஏந்திழைக்குந் தானுரைத்து அன்று திருவனந்தம் அவர்நோக்கித் தானடந்து ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(146,'சேத்திர பாலனுக்குச் சொன்னது','கேத்திரனும் வேதியனும் கிருஷ்ண ரடிபணிந்து மூர்த்திகளுங் காணாத முதலே முதற்பொருளே உன்மகவாய் வந்துதித்த உயர்ந்த குலச்சான்றோர் தன்கமவோ ரங்கே தவித்துமுகம் வாடிருக்க நீசனுட குடியில் நீர்போகக் காரியமோ தேசமெல்லாம் நீசனுட செய்திகேட் டேயிருந்தும் போவதோ தேவரீர் பொல்லா தவன்குடியில் தேவரீ ரும்முடைய சிந்தையெள் போலறியேன்  என்றுரைக்கக் கேத்திரனும் ஏதுரைப்பா ரெம்பெருமாள் நன்றுநன்று கேத்திரனே நான்சொல்லக் கேட்டிடுநீ சான்றோர்க் குபகாரம் தான்செய்யு முன்னாகப் பொன்றுகலி நீசனுக்குப் புத்திசொல்ல வேணுமென்றும் அல்லாமற் குறோணி அவன்மாய யிவன்வரையும் எல்லாந்தான் சொல்லுக்கு இடறுவை யாதபடி சான்றோர்கள் தம்மிடத்தில் சாங்கமாய்ப் போயிருந்து ஆன்றோர்தா னெங்களையும் அழியவைத்தீ ரென்றுரைப்பான் ஆனதால் நானும் அதற்கிடைகள் வையாமல் ஈன முறுநீசன் இடம்போறே னென்றுரைத்தார் அப்போது கேத்திரனும் அன்பா யகமகிழ்ந்து செப்போடு வொத்த திருமாலைத் தெண்டனிட்டு மாயனே உன்றன் மகிமையதை யாரறிவார் ஆயனே வும்முடைய அளவறியக் கூடாது என்று கேத்திரனும் இயம்புவான் பின்னுமொன்று மன்று தனையளந்த மாயப் பெருமாளே பூசை புனக்காரம் பெரியதீ பத்துடனே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(147,'சேத்திர பாலனுக்குச் சொன்னது','நீச னுமக்கு நினைத்துநிதஞ் செய்வானே எந்தனக் கென்ன இலக்குக் குறியெனவே சிந்தை தெளிந்து செப்பி விடைதாரும்  என்றுரைக்கக் கேத்திரனும் ஏதுரைப்பா ரெம்பெருமாள் இன்றுநீ கேட்டதற்கு இயல்புரைக் கக்கேளு கலிநீசன் மாநிலத்தில் கால்வைத்து அன்றுமுதல் சலிவாகி யென்மேனி தண்ணீ ரறியாது எண்ணை யறியேன் இலட்சுமியை நானறியேன் வண்ணத் துகிலறியேன் மறுபுடவை தானறியேன் பூசை யறியேன் பொசிப்பறியேன் பூவறியேன் ஆசை யறியேன் அக்கக் கிளையறியேன் மெத்தை யறியேன் மேவுஞ் சொகுசறியேன் ஒற்றைபோ லானேன் உட்கார்ந் திருக்கறியேன் மேடை யறியேன் மகிழுஞ் சிரிப்பறியேன் தோழமையை நானறியேன் சுருதி மொழியறியேன் ஆளவை குண்டம் அரசுமே டையறியேன் இத்தனைகள் தானறியா(து) எண்ணமது நானறியேன் புத்தியுள்ள கேத்திரனே பூசைபூ ஏற்பதெல்லாம் மாயக் கலியறுத்து வாய்த்தநா டாள்வார்க்கு ஞாயமுள்ள பட்டம் நான்சூட்டித் தர்மமதாய் இராச்சியத்தை யாளவைத்து இத்தனையு மேற்பதல்லால் அதற்குமுன் பூசை புனக்காரமுத லேற்பதில்லை இதற்குமுன் னேற்பவர்கள் எனக்காகா தேபோவார்  என்றந்தக் கேத்திரனுக்(கு) இத்தனையுந் தான்கூறி அன்றந்த நீசன் அவ்வூரி லெம்பெருமாள்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(148,'புலச்சிக்கு அருளியது','நீச னிடத்தில் நீலவண்ணர் வந்ததுதான் தேச மறிய செச்சை புலச்சிகண்டு ஒளித்தா ரொருஇடத்தில் ஊர்தேச முமறிய விழித்தாள் புலச்சி விதமறிந் தெல்லோரும் நீச னறிந்து நெருங்கமணி மேடைவைத்து வீசை முறுக்கி விசையாக அந்நீசன் கோவில் சிவாலயங்கள் கொந்துகொந் தாயமைத்துப் பாவித்துப் பூசை பண்ணத் துணிந்தனனே மேடைக்குக் கால்கள் மிகுத்தத்தங்கத் தால்நிறுத்தி வாடைக் கமகமென வாத்தியங்கள் நின்றதிர தீபம் புலச்சி தீவட்டித் தான்கொடுக்கப் பாவக் குடும்பத்தோர் பண்ணினார் பூசையது நம்பூரி வேதியர்கள் நாடி யகமகிழ்ந்து பம்பைப் பரத்தை பகட்டுக்கை காட்டலோடு நாடி மகிழ்ந்து நல்ல அனந்தபுரம் கோடிநூ றாயிரம்போல் கூண்டரிய தீபமொடு தேடரிய மாமறையோர் சிந்தைகளித் தேயிருக்க நாடதிக மாகி நல்ல அனந்தபுரம்  மாத மும்மாரி வருசிக்கத் தான்பொழிந்து வாரமுடன் செந்நெல் மாறாம லேவிளைந்து தென்னங் குலைசிதறித் தேர்ப்போ லலங்கரித்து என்னென்ன பவிசு எல்லா மிகப்பெருத்து ஒப்பில்லாத் தேசம் உற்ற அனந்தபுரம் செப்பத் தொலையாது சிறந்தப் பெருமையது நன்றாகச் சீமை நாடோறு மேவாழ்க அன்றுஸ்ரீ ரங்கர் அங்கே யகமகிழ்ந்து நீசன் நினைத்த நினைவுபோ லெம்பெருமாள் தேசப் பவிசும் சிறப்பு மிகக்கொடுத்து நீசன் தனக்கெதிரி நீணிலத்தி லில்லையென்று தோசக் கலிப்புவியைச் சூட்டியர சாளுகையில்');");

              //8th Day

        db.execSQL("INSERT INTO akilathirattu2 VALUES(149,'கலியரசன் தவம்','மதலைய தில்லாமல் மனஞ்சலித்து மாநீசன் குதலையி னேதுவினால் கொஞ்சஞ் சடைவாகி ஆதித் திருமால் அடியை மிகப்போற்றிச் சோதியே யென்றனக்குச் சிறுவனொன்று தாருமென்றான் அப்போது நாராயணர் அகமகிழ்ந்து கொண்டாடி இப்போது உனக்கு இங்கிருந் தால்மதலை கிட்டாது காசிதனில் கீர்ந்த நதிக்கரையில் திட்ட முடன்தவசு செய்யப்போ யங்கிருந்தால்  மதலையுட துயரம் மாறுங்கா ணுன்றனக்குப் பதலைமொழி நீசனுக்குப் பகர்ந்தார்கா ணம்மானை கேட்டந்த நீசன் கெட்டிதா னென்றுசொல்லி நாட்டுக்குச் சீட்டெழுதி நருட்களையுந் தான்வருத்திப் போனானே நீசன் புகழ்பெரிய காசிதனில் சேனா பதிகள் திக்கெங்குஞ் சூழ்ந்துநிற்க நீசன் தவசு நிற்க வொருமறையோன் வாசக் குழலோடு மருவினன்கா ணம்மானை கண்டந்த நீசன் காமம் பொறுக்காமல் தண்டுமீ றிக்காமம் சலத்தில் விழுந்ததுவே இரைநமக் கென்று எட்டியொரு கொக்காவி விரைவாய் விழுங்க மிகுத்தகர்ப்ப முண்டாகித் தண்ணீரில் பிள்ளை தான்பெற்று தம்மானை வெண்ணிற மான மிகுத்தபிள்ளை தான்மிதந்து போகும் பொழுதில் புனலரிஷி மாமுனிவன் தாப முடன்சிசுவை தானெடுத்தா னம்மானை மதலை தனையெடுத்து வளர்த்துப் பருவமதில் குதலை தனையாற்றில் குளியோட நீச்சலதும் தோணியே றுந்தொழிலும் சுருக்குக்கப் பலேற்தொழிலும் ஆணிப்பொன் முத்து அதுவளருந் தலமும்  வெள்ளித் தலமும் மிகுத்தபொன் னுத்தலமும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(150,'கலியரசன் தவம்','உள்ளவித்தை யான உற்றரச வாதமுதல் கள்ள உபாயக் கபடுபலத் தந்திரமும் வெள்ளை நீசன்தனக்கு விதமா யவர்வருத்திச் செங்கோ மட்டியெனத் தேசநசு ராணிகளில் பெண்கள் ரண்டுபேரைப் பேறாய் மணமகித்து இருத்தினான் மாமுனிவன் ஏற்றசெங் கோமட்டியில் பருத்த வெள்ளைநீசன் பண்பாக அங்கிருக்க நின்ற தவசு நெறியழிந்து மாநீசன் அன்றந்தப் பாவி அனந்தபுரம் வந்தனனே வந்து சடைவாய் மாநீசன் தானிருக்கச் சந்துபயில் மாயவரும் தானறிந் தேதுரைப்பார் மன்னவனே யுன்றனக்கு மதலையது கிட்டினதோ என்ன விதங்காண் ஏகிவந்த தென்றுரைத்தார் அப்போது நீசன் அவனேது சொல்லலுற்றான் இப்போது மாயவரே யான்தவசு நிற்கையிலே மறையவனுந் தேவியோடு மருவினதைக் கண்டாவி இறையவரே யென்றனக்கு இந்திரியந் தானிளகி ஆனதால் தவசு அழிந்தே னதினாலே ஈனமுடன் மதலை இல்லையென்றா ரீசுரரும்  மதலையில் லாதிருந்தால் வையகத்துக் கேராது குதலையல்லோ வேணும் குவலயத்தை யாளுதற்குச் செங்கோ லரசு செலுத்தியர சாளுதற்கு முன்கை சிரைத்து முறைகர்மஞ் செய்திடவும் பிள்ளையில்லா தேயிருந்தால் போதுமோ புண்ணியரே வள்ளலந்த மாலும் மறுத்துரைப்பா ரம்மானை சொந்தமுள்ள சோதிரியைச் சுறுக்காய் வரவழைத்து உந்தனக்குப் பிள்ளை உண்டோதா னில்லையென்று வருத்திக்கே ளப்போ வகைசொல்வான் சோதிரிசி பொருத்தமா யாயன் புகன்றாரே நீசனுக்கு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(151,'மருமக்கள் வழி','நல்லதென்று வேதியனை நாடி யவனழைத்துச் சொல்லவென்று நீசன் தொகுத்தவனோ டேகேட்க அப்போது சோதிரிசி அந்நீ சனைப்பார்த்து இப்போது கேள்நீ ஆகமத்தி லுள்ளமுறை பெற்றாலு முன்மகவு பேருலகம் ஆளாது மற்றோர்பெற் றுன்மருகன் ஆளுவான் வையகத்தை என்றேதான் சாஸ்திரமும் இசையுதுகாண் மன்னவனே அன்றேதான் சோதிரியும் அந்நீசனுக் குரைத்தான் நல்லதென்று நீசன் நாடி யகமகிழ்ந்து வல்ல வகையாலும் வைத்தபங்குக் கிட்டுமென்று  சோதிரியைத் தானனுப்பித் துயர்நீங்கி யேயிருந்தான் ஆதி முறைப்படியே அவன்மரு கன்றனக்குப் பட்டந்தா னென்று பறைசாற்றி யாண்டிருந்தான் திட்டமுடன் நீசன் தேசமதை யாளுகையில் முன்னீசன் விந்தில் உதித்துவளர்ந் தேயிருந்த அந்நீச னான அசுபக் கிரிவளமை');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(152,'வெண்ணீசன்','சொல்லவே நாதன் தொகுத்துக்கே ளன்போரே வல்லசெங் கோமட்டி வையகத்தி லந்நீசன் வெள்ளி மலையும் மிகுத்ததங்கப் பொன்மலையும் கள்ளமில்லா வாதம் கைகண்ட வித்தையதாய் நிதியிற் பெருத்து நெருங்கப் படைகூட்டிப் பரியானை ஒட்டகங்கள் பலபடைகள் சேகரித்து ஆளும் படையும் ஆயுதங்க ளஸ்திரமும் வேழும் பெரிய வெகுவாணு வங்கூட்டித் தன்னோ டினங்கள் சதாகோடி யாய்ப்பெருத்து என்னோ டெதிர்க்க யாதொருவ ரில்லையென்று அவன்தா னொருவேதம் ஆகமங்கள் தான்பிரித்து எவர்தா டெதிர்க்க யாதொருவ ரில்லையென்று அவன்தா னொருவேதம் ஆகமங்கள் தான்பிரித்து எவர்தா னெதிரியென்று எண்ணமுற்று மாநீசன் நட்சேத் திரத்தை நகட்டிருபத் தெட்டாக்கி வைத்தானே மாதம் வருசமது மாறாய்  முன்னீசன் வைத்த முறைமை யிலுங்கூட்டிப் பின்வந்த நீசன் பிரித்தானொன் றேற்றமுடன் அரிநமா வென்ற அட்சரத்தை விட்டவனும் விரியாய் அனாதியென விளம்பினான் வையகத்தில் இப்படியே வைத்த இவன்வேத மானதுக்குள் அப்படியே மற்றோரை அகப்படுத்த வேணுமென்று பணமா னதைக்கொடுத்துப் பகட்டினான் மானிடரைச் சிணமாக மானிடவர் சேர்ந்தா ரவன்வேதமதில் இப்படியே வேதமொன்று இவன்பலத்தா லுண்டாக்கி அப்படியே தானிருக்க அவனேது தானினைப்பான் ஆணுவங்கள் சேனை ஆயுதங் கள்வெகுவாய் வாணுவங்கள் ரெம்ப வம்மிசத்தோ ரெம்பரெம்ப படையாலு மற்றொருவர் பணத்தாலும் நம்மையுந்தான் தடைசெய்து நம்மைத் தடுப்பவரா ரென்றுசொல்லி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(153,'வெண்ணீசன்','ஆரா ரெதிரியென்று அவன்பார்த் திருக்கையிலே பூராய மாயொருவன் போதித்தா னவன்றனக்குச் சோழனென்றும் சேரனென்றும் துய்யபாண் டியனென்றும் வேழமுடி மன்னர் விபரீதமா யாண்டிருந்த  தேச மைம்பத்தாறு உண்டுகாண் செந்துரையே வாசமுட னாண்டு வகையா யிருக்கையிலே  வேசையொரு தாசி வழிநுதலாள் தன்வயிற்றில் பேசரிய வோர்மதலை பிறந்ததுகாண் மாயமுடன் மதலை பிறந்து வையகத்தி லேயிருந்து குதலை வளர்ந்து குடுமி வளர்க்கையிலே சேரனுக்குஞ் சோழனுக்கும் சிறந்தபாண் டியனுக்கும் வாரமுள்ள தெய்வ மாதருட சாபமதால் அவர்கள் கிளையிறந்து ஆணுவங்கள் தானழிந்து இவர்களும் போய்க்கடலில் இருந்தார்கள் கல்லெனவே ஆனதினால் முன்னம் அவனிதனை யாளுதற்கு மானமுள்ள பேர்கள் மறுத்தேதா னில்லாமல் மாயமுடன் தாசி மகன்தானும் சீமைதன்னை ஞாயமில்லா வண்ணம் நாடாண் டிருந்தனனே சென்றால்தா னந்தச் சீமைநமக் காகுமென்று அன்றேதான் சொல்ல அவன்கோபத் தால்வெகுண்டு வந்தானே யந்த மாநீசன் தன்பேரில் செந்தார மாயன் சேனை வருவதையும் அறிந்தே நீசன்தனக்கு அச்சுதருஞ் சொல்லலுற்றார் வெறிந்தமுள்ள நீசன் வேண்டும் படைகூட்டி நசுறாணி யான நல்லவெண் ணீசனுக்குத் துசுவான மாநீசன் திருமாலின் தன்னருளால்  எற்றுக் கொடாமல் இவன்வெற்றி கொண்டனனே மற்றுமந்த நீசன் மாறியவன் போயிருந்தான் அப்போது அந்த அன்னீத மாநீசன் இப்போது படையை யாம்வெற்றி கொண்டோமென்று');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(154,'வெண்ணீசன்','கோட்டைபின்னு மிட்டுக் கொடிய விருதுகட்டித் தாட்டிமையா யுலகில் சட்டமது வைத்தனனே வைக்கும் பொழுதில் மாயவரைத் தானோக்கிச் செய்க்கும் பெரிய திருமாலே யென்றனக்குக் கைக்குள்ளே ஏவல் கருத்தாகச் செய்வதற்கு மெய்க்குணம்போ லுள்ள விதமான சாதியொன்று வருவித்து என்றனுட மணிவாசல் காத்திருக்கத் தருவித்து நல்ல சாதியொன் றென்றுரைத்தான் அப்போது மாயன் அதற்கேது சொல்லலுற்றார் இப்போது உன்றனக்கு ஏவல் தொழில்கள்செய்ய ஆகின்ற பேரை அழைத்துக்கோ என்றுரைத்தார் வேகுன்ற நீசன் விளம்புவா னமைச்சருடன் ஆரைக் கொடுவரலாம் அருகில்விட்டு வேலைசெய்ய ஏரையொத்த மந்திரியே இயம்பு மெனக்கேட்டான் அப்போது மந்திரிகள் அந்நீசனுக் குரைப்பார் இப்போது வேறொருவர் இருந்தாலா காதெனவே  நல்லவகை யான நாடுஞ்சான் றோர்களைத்தான் வல்லவகை யாலும் வருவித்து வைப்பீரால் ஆகுமந்தச் சாதியென்று அந்நீசனுக் குரைத்தார் வேகும் பொழுதில் வெற்றிசான் றோர்களுக்கு ஆள்விட்டு வருத்தி அதிக நிதிகொடுத்து  வாள்கொடுத்து ஆயுத பாணி மிகக்கொடுத்துப் பட்டயமுங் கொடுத்துப் பாரயிறை கூலிவிட்டுச் சட்டைக்குல் லாகொடுத்துத் தலைப்பா மிகக்கொடுத்து வாசல் மணிமேடை வகையாகக் காரெனவே வாசமொழிக் கலியன் மாய்கையால் சான்றோர்கள் ஊழி விதியால் உடையோனை நெஞ்சில்வைத்துக் காளி வளர்த்த கண்மணிகள் காத்திருந்தார் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(155,'கலிநீசன் சாபம்','அப்படியே காத்திருந்து அவர்வருகும் வேளையிலே முப்படியே வந்த முழுக்கலியன் தோசமதால் அந்தநீ சன்தனக்கு அழிவுவரும் வேளையிலே இந்தச் சான்றோர்கள் இவர்தூக்கம் வைத்திடவே நீசனுட வம்மிசத்தோர் நேரக்கூறே யறிந்து வாசமிட்டு நீசனையும் வதைத்தாரே யவ்வினத்தோர் சீவன்போ கும்வேளைச் செப்புவான் சாபமொன்று காவலாய் நீரிருந்து காட்டிக்கொடுத் தீரேயென்று  கோபித்தான் சான்றோரைக் கூறழியப் பேசியவன் சாபித்தான் சான்றோர்க்குச் சத்தியில் லாநீசன் என்குடும்பத் தோர்கள் இராச்சியத்தை யாளுமட்டும் உன்குடும்பத் தோர்கள் ஊழியங்கள் செய்துமிக அழுந்தப்படு வாரெனவும் அந்நீசன் சான்றோர்க்கு விழுந்த மொழியாய் விரைந்துரைத்தா னேநீசன் சொல்லியந்த நீசன் சோர்ந்திறந்தா னவன்றன் வல்லி யுடன்பிறந்த மகன்தேசம் ஆண்டிருந்தான் நீசனுட சாபம் நீணிலத்தில் சான்றோர்க்கு மாய வலையாய் வளைந்ததுகா ணன்போரே தேவ ரதையறிந்து திருமா லிடமேகி மேவலர்கள் வந்து விளம்பினா ரப்போது மாயவரே எங்கள் வழியில்வாழ் சான்றோர்க்குப் பாவிநீ சன்சபித்த பழிசாபங் கேட்டீரோ சாபத்துல்ப மெல்லாம் தானுரைத்தா ரச்சுதற்குத் தாமத் திருமேனி தானுரைப்பார் தேவருக்கு மன்னதியத் தேவர்களே வாய்த்தபுகழ் சான்றோர்க்கு அந்நீசன் சாபம் அதுவுங் குறிதான்காண்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(156,'கலிநீசன் சாபம்','வம்புள்ள நீசன் வழக்கெல்லா மேற்பதற்கு அன்புள்ள சான்றோர்க்கு அவனிட்ட சாபமது  எத்தனை குற்றம் இவர்செய்து போட்டாலும் அத்தனையும் நீசன் அவனேற்கக் காரியந்தான் நல்லதுகாண் தேவர்களே நாடுஞ்சான் றோர்களுக்குத் தொல்லைவந்தா லுந்தீர தொகைவைத்த லக்குமுண்டு சோழனுக்குச் சான்றோர் செய்தநன்றி மெத்தவுண்டு வேழமிட் டிருப்பேரை வெட்டினான் பாரறிய நீசனுக்குச் சான்றோர் நித்திரைக ளில்லாமல் வாசல்நடை காத்ததற்கோ வலுதுயரச் சாபமிட்டான் எல்லா மறிவார் ஈசர்முதல் லோகம்வரை வல்லாண் மையான வாயத்ததே வாதிகளே நாமென்ன செய்வோம் நாட்டில்விதி வந்ததற்குப் போமென்னத் தேவருக்குப் பெரியோன் விடைகொடுத்து மாயவரு மங்கே மனஞ்சடைத்துத் தானிருந்தார் வாயக் கலிநீசன் வையகத்தை யாண்டிருக்க நீசனிட்ட சாபம் நீதிச்சான் றோர்களுக்கு மாயவினை போலே வளைந்ததுகா ணன்போரே தம்பி தமையனுக்குச் சத்துருப்போல் தானாகி வம்புக்குங் கோளு மாநீச னோடுரைத்து அடிக்கவே கைக்கூலி அவனுக்கே தான்கொடுத்து முடிக்கும் வரையும் முறைமுறைக்கோள் சொல்லிடுவான் இப்படியே சான்றோர் இவர்கள்நிரப் பில்லாமல் அப்படியே நீசனுட அன்னீதத்தால் வேறாய் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(157,'கலிநீசன் கொடுமை','பிரிந்துதான் சான்றோர் பெருத்த கிலேசமுற்றார் அறிந்துதான் நீசர் அவர்கள்தொக் காச்செனவே தாலிக்கு ஆயம் சருகு முதலாயம் காலிக்கு ஆயம் கம்பு தடிக்காயம் தாலம தேறும் சான்றோ ருக்காயம் தூலமுட னரிவாள் தூருவட் டிக்காயம் தாலமதுக் காயம் தரணிதனிலே வளர்ந்த ஆலமரம் வரைக்கும் அதிகஇறை வைத்தனனே வட்டிக்கும் ஆயம் வலங்கைசான் றோர்கருப்புக் கட்டிக்கும் ஆயம் கடுநீசன் வைத்தனனே பாழிலே சான்றோர்க்குப் படுநீசன் கொள்ளுகின்ற ஊழியங்க ளெல்லாம் உரைக்கக்கே ளன்போரே பனைகேட் டடிப்பான் பதனீர்கேட் டேயடிப்பான் கனத்த கற்கண்டு கருப்புக்கட்டிக் கேட்டடிப்பான் நாருவட்டி யோலை நாள்தோறுங் கேட்டடிப்பான் வாதுக்கு நொங்கு வாய்கொண்டு கேட்டடிப்பான் நெடுமட்டைக் கேட்பான் நெட்டோலை தான்கேட்பான் கொடுவா வெனவே கூழ்பதனீர் கேட்டடிப்பான் சில்லுக் கருப்புக்கட்டி சீரகமிட்டே வூற்றிக் கொல்லைதனில் சான்றோரைக் கொண்டுவா என்றடிப்பான் மீச்சுக் கருப்புக்கட்டி மிளகுபல காரமிட்டு வீச்சுடனே கொண்டு வீட்டில்வா வென்றடிப்பான் வட்டிக் கருப்புக்கட்டி மணற்கருப்புக் கட்டியொடு வெட்டக் கருப்புக்கட்டி வெண்கருப்புக் கட்டியொடு தோண்டிக்கும் பாய்க்கும் சுமடதுக் குமோலை வேண்டியதெல் லாமெடுத்து விரைவில்வா என்றடிப்பான் காலைப் பதனீர் கண்முற்றா நொங்குகளும் மாலைப் பதனீர் வற்றக்கா யும்பதனீர்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(158,'கலிநீசன் கொடுமை','கொதிக்கும் பதனீர் கொண்டுவா என்றடிப்பான் விதிக்குகந்த சான்றோர் விரைவாய்க் கொடுத்திடவே இத்தனையும் வேண்டி இவன்கொண்டு போனாலும் பத்தியுள்ள சான்றோர்க்குப் படுந்துயர மாறாதே பனையிலுள்ள வஸ்து பலநாளு மிப்படியே வினைகொண்ட பாவி வேண்டியவன் போனாலும் சான்றோர்க்கு நன்மை சற்றுசெய்ய வேணுமென்று மாண்டோர் கணீசன் மனதில்வைக்க மாட்டானே உய்கொண்ட சான்றோர் உடம்புருகுந் தேட்டையெல்லாம் நொய்கொண்ட நீசன் நோகப் பறித்தானே இப்படியே சான்றோர் இவர்தேடுந் தேட்டையெல்லாம் அப்படியே நீசன் அவன்பறித்துத் தின்றாலும் ஞாயமுள்ளச் சான்றோர் நாமமது கேட்டதுண்டால் நீசக்குலத் தோர்விரட்டி நெடுந்தூரங் கொண்டடிப்பார் பின்னுமந்தச் சான்றோரைப் பொல்லாதான் கொள்ளுகின்ற அன்னீத மெல்லாம் அருளக்கே ளாயிளையே உப்பா லுவரா உபய மெடுத்தவரை ஒப்பமுள்ள சான்றோரை ஊழியங்கள் கொண்டடிப்பான் குளம்வெட்டச் சொல்லிக் கூலி கொடுக்காமல் களம்பெரிய சான்றோரைக் கைக்குட்டை போட்டடிப்பான் சாணார்கள் தம்பனையில் தான்முளைத்த ஓலையெல்லாம் வீணாக நீசன் வெட்டித்தா என்றடிப்பான் வெட்டிக் கொடுத்தாலும் வெற்றியுள்ளச் சான்றோரைக் கட்டிக் சுமவெனவே கைக்குட்டை போட்டடித்து சுமந்து போனாலும் தொகையெண் ணமுங்கேட்டுப் பவந்து மொழிபேசிப் பணமுமிகக் கேட்டடிப்பான் ஐயையோ சான்றோரை அந்நீசன் செய்ததெல்லாம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(159,'கலிநீசன் கொடுமை','வையமது கொள்ளாதே மாதேவுன் னோடுரைத்தால் ஈசருக்கும் வேதா இவர்களுக்குந் தேவாதி வாச முடன்பதறி வணங்கிநின்ற வேலையைப்போல்      நேசமுள்ள சான்றோர் நெடுநாளு மேபதறி நீசனுக்குச் செய்த நிசவேலை யொக்குமல்லோ ஈப்புலிபோல் நீசன் ஈயைப்போ லேசான்றோர் நாய்ப்புலிபோல் நீசன் நல்லாடு போல்சான்றோர் கீரியைப்போல் நீசன் கிராணம்போ லேசான்றோர் பாரியைப்போல் நீசன் படுத்தினான் சான்றோரை பண்டுநீ சன்பித்த படுசாபந் தன்னாலே விண்டுரையா வண்ணம் விசையடக்கித் தாழ்ந்திருந்தார் இப்படியே ஊழியங்கள் எண்ணலக்கில் லாதபடி அப்படியே சான்றோர் அவனூழி யங்கள்செய்து அல்லாமல் நீசன் ஆர்க்கமுள்ள சான்றோர்க்கு வல்லாண்மை யான வரிசை யிறைகள்வைத்துக் கரிவிறை பாட்டஇறை கண்டபாட் டஇறையும் தரிசிறை காணாத தரைப்பாட் டஇறையும் ஆமிசங்க ளில்லாத அன்னீத வம்பிறையும் நேமித்து வைத்து நிலையுள்ளச் சான்றோரை அடித்துக் கைகெட்டி ஆண்பெண் வரைக்குமிட்டு இடித்தடைத்துப் பட்டினிகள் இரவுபகல் போட்டுப் பெண்ணா ணுடைய பெருமை மிகக்குலைத்து மண்ணாண்ட  வரம்பழித்து மாநீசன் சாணாரைக் கண்ணில் தான்காண வொட்டாமல் வீணாட்டஞ் செய்து விரட்டி யடித்துமிகப் பம்பழித்துச் சாணாரைப் பலசாதி யின்கீழாய்த் தும்பழித்து வேலை தூறுபடக் கொண்டனனே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(160,'கலிநீசன் கொடுமை','பறையன் புலையன் பகல்வரான் போகுமிடம் மறையொத்த சான்றோர் வந்தால் பிழையெனவே முக்காலி கட்டி முதுகி லடித்துமிக மிக்கான பொன்பணங்கள் வேண்டினான் பிழையெனவே சாணான்தன் வஸ்து தரணி தனக்குயிராம் ஆனாலுஞ் சான்றோர்க்கு அடியொருநாள் மாறாது கோவில் சிவாலயங்கள் கூடங்கள்சிங் காசனங்கள் நாவுலகுங் கள்ளாய் நாடி யிருந்தாலும் சாணான்கள் ளேறியெனச் சண்டாள நீசனெல்லாம் வீணாகச் சாணாரை விரட்டி யடிப்பான்காண் சாணுடம்பு கொண்டு தரணிமிக ஆண்டாலும் வீணுடம்பு கொண்டோர் விரித்துரைத் தோராமல் சாணான்சா ணானெனவே சண்டாள நீசனெல்லாம் கோணா துளத்தோரைக் கோட்டிசெய் தேயடித்தான் தரணிதனில் வந்து தலையெடுத்த யாவருக்கும் மரணம் வரைக்கும் வந்துதித்த அன்றுமுதல்  சேனைமிக வூட்டுதற்கும் தெய்வச்சான் றோரமிர்தம் ஈனம தில்லாமல் யாபேர்க்கு மீந்தாலும் பொல்லாத நீசன் பொறுதியுள்ளச் சான்றோரை கல்லாதான் கூடிக் காணவிடா தேயடித்தான் நீசன் குடியிருக்க நிறைந்தமணி மேடையெல்லாம் வாசமுடன் சான்றோர் வஸ்துவல்லா லாகாது அப்படியே சான்றோர் அவருதவி செய்திடினும் எப்படியுஞ் சான்றோர் இடுக்கமது மாறவில்லை எவரெவர்க்குஞ் சான்றோர் ஈந்துமிக வந்தாலும் அவர்களுக்கு நீசன் அனுப்போ லுறவுமில்லை பாலரியச் சான்றோர் படுந்துயரங் கண்டிருந்து மாலதிகக் கோபமுடன் மாநீ சனைப்பார்த்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(161,'ஸ்ரீபத்ம நாபர் கலியரசனுக்கு உபதேசித்தல்','கேளடா சூத்திராவுன் கிளையோடே மாளுதற்கு வாளடா சான்றோரை வம்புசெய்து வாறதுதான் உன்றனக்கு முன்கிளைகள் உள்ளோர்க்கும் நாள்தோறும் என்றனக்கும் நன்மை இன்பமுடன் செய்துவரும் சாணாரை நீயும் தடிமுறண்டு செய்கிறது வாணாள்க் கிடறு வருமடா மாநீசா முன்னுகத்தில் கேளு முகமைந்து கொண்டோனும் உன்னோடும் பிறவி ஒருயேழு உண்டுமடா  ஒண்ணாம் யுகத்துக்கு உற்ற குறோணியடா மண்ணெல்லாங் குறோணி வந்தெடுத்து விழுங்குகையில் பூதக் குருமுனிவன் புத்திசொன்னா னவ்வுகத்தில் நீதமுடன் கேளாமல் நீசனவன் மாண்டான்காண் அடுத்த யுகமதிலே அக்குண்டோம சாலினுக்குக் கடுத்தமுள்ள கோவிரிஷி கடியபுத்தி சொன்னான்காண் கேளா தேமாண்டான் கிளையோடே யந்நீசன் பாழாகிப் பின்னும் பதிந்தமூன் றாம்யுகத்தில் மல்லோசி வாகனென்று வந்த இருவருக்கும் நல்லபெல ரோமரிஷி நாடிமிகப் புத்திசொன்னான் கேளாதே மாண்டான் கிரேதா யுகந்தனிலே தாழாத சூரபற்பன் தம்பி யவன்றனக்கும் வீரவா குதேவர் விரைந்துமிகப் புத்திசொன்னார் தாராமல் சூரன் தன்கிளையோ டேமாண்டான் அவ்வுகத்தில் வந்த அசுர னிரணியற்குச் செவ்வுகந்த சிங்கம் செப்பினதே புத்தியது சற்றுமவன் கேளாமல் தான்மாண்டா னவ்வசுரன் பத்தத் தலையான பார அரக்கனுக்குத் தம்பிவி பீஷணனும் தான்சொன்னான் புத்தியது வம்பிலவன் கேளாமல் மாண்டான் கிளையோடே  பின்னுந்துரி யோதனனாய்ப் பிறந்தான் மறுயுகத்தில் மன்னுகந்த பீஷ்மரும் வாழ்த்திமிகப் புத்திசொன்னார் கேளாமல் மாண்டான் கேடுகெட்ட மாபாவி தாழம லுன்றனக்கு தற்சொரூபத்தோ டிருந்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(162,'ஸ்ரீபத்ம நாபர் கலியரசனுக்கு உபதேசித்தல்','நாரா யணராய் நானுதித்து உன்றனக்குச் சீரான புத்தி செப்புகிறேன் கேளடவா உன்கிளையும் நீயும் உற்றார்பெற் றார்களுடன் தன்கிளையோ டெநீயும் தரணியர சாளவென்றால் சாதி தனிலுயர்ந்த சான்றோ ரவர்களுக்கு நீதி யுடனிறைகள் இல்லாமல் நீக்கிவைத்துக் காளி வளர்த்தெடுத்த கண்மணிக ளானோர்க்கு ஊழியமுந் தவிருநீ உலகாள வேணுமென்றால் அல்லாமல் சான்றோரை அன்னீத மாயடித்தால் பொல்லாத நீசா புழுக்குழிக் குள்ளாவாய் கற்புள்ள சாணாத்தி கதறியுன்னைச் சாபமிட்டால் அற்படியும் உன்கோட்டை அழிந்துபொடி யாகுமடா சாணாத்தி யுன்னைச் சாங்கமுடன் சபித்தால் வாணாளழியு முன்றன் வம்மிசங்கள் தாமுடியும் தீத்தழலில் விந்து சிக்கி மிகப்பிறந்த பார்த்தன் வழிக்குலங்கள் பகைந்துநிந் தித்துண்டால்  கோட்டை யிடியுமடா கோத்திரங்கள் தாமுடியும் நாட்டை முடிக்குமடா நல்லசா ணாத்திகற்பு சேனை யழியுமடாவுன் செல்வமது குன்றுமடா வான மிடிந்துன் வம்மிசத்தைக் கொல்லுமடா திடம்பெரிய சாணாத்தி தினமுனைநிந் தித்ததுண்டால் கடல்வந்துன் சீமைதனை கட்டா யழிக்குமடா ஊக்கமுள்ள சான்றோர்க்கு ஊழியங்க ளில்லையென்று ஆக்கமுடன் பறைதான் அடித்தவனி தானறிய அல்லாதே போனால் அரசாள மாட்டாய்நீ நல்லான சான்றோர்க்கு நாட்டு மிறைதவிர்த்து இறைகூலி தானம் இட்டுக் கொடுத்தவர்க்குத் தரைமீது நன்றாய்த் தழைத்திருக்க வைக்காட்டால் குஞ்சரமு முன்னுடைய கொத்தளமுந் தானிடித்து வஞ்சகமா யுன்றனக்கு வலியகர்மஞ் சுற்றுமடா கர்ம வியாதிகளாய்க் கண்டமா லையுடனே வர்மம்வந்து சிக்குமடா மாநீசா நீகேளு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(163,'ஸ்ரீபத்ம நாபர் கலியரசனுக்கு உபதேசித்தல்','தெய்வச் சாணாத்தி தினமுனைநிந் தித்ததுண்டால் பொய்வகையால் கர்ம போகத்தால் நீமடிவாய் என்றுநா ராயணரும் ஏற்றபுத்தி சொல்லிடவே அன்றுஅந்த நீசன் அதற்கேது சொல்லலுற்றான்  மாயவரே நீர்கேளும் வாய்த்தசான் றோர்களைத்தான் ஆயரே நீரும் அபுருவமாய்ச் சொன்னீரே சாதிகட் கீழான சாணார்கள் தங்களுக்கு வீரியமா யித்தனையும் விவரித்துச் சொன்னீரே சாணார்க்கு வைத்த தலைவீத முள்ளஇறை - என் வாணா ளழிந்திடினும் மாற்றிவைக்கப் போறதில்லை என்சீவ னுள்ளளவும் ஏற்றசா ணான்தனக்கு வன்பான ஊழியங்கள் மாற்றிநான் வைப்பதில்லை நீர்தா னுமிந்த நிலைபேர்ந்து சாணாரின் ஊரா னதிலே உறைந்திருக்கப் போனாலும் கேட்பதில்லை சாணாரின் கேள்விநான் கேட்பதில்லை தாட்பதல்லால் சாணாரின் சங்கடங்கள் கேட்பதில்லை என்றந்த நீசன் இத்தனையு மாயருடன் தாவியு ரைக்கச் சாற்றுவா ரச்சுதரும் பாவி யுனது பவிசெல்லாந் தான்மாறி ஆவிக்கு நரகம் ஆவதற்கோ என்னோடே இந்த மொழியுரைத்தாய் ஏனடா மாநீசா உன்றனுட ஊருவிட்டு ஓடிப்போ வென்றனையே ஆனாலுஞ் சாணார்க்கு அடியும்பல ஊழியமும் மானாந்திர இறையும் மாற்றிவை யென்றுரைத்தார்  அப்போது நீசன் அச்சுதரைத் தான்பார்த்து வெப்போடு கோப வெகுளியாய்த் தானுரைப்பான் மாலைகா லைநேரம் மாயவனே உன்றனக்குச் சீலமுள்ளப் பூசை செய்துவ ருவதற்கு நித்தமொரு நூறுபொன் நினக்குச் செலவுமுண்டே அத்தனையுங் கிட்டிடுமோ அவனிறைகள் தான்தடுத்தால் அல்லாம லென்றனக்கு ஆயிரத்தி நூறுபொன்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(164,'ஸ்ரீபத்ம நாபர் கலியரசனுக்கு உபதேசித்தல்','எல்லாத் திருப்பதிக்கும் என்றனக்கும் வேணுமல்லோ என் வேலையாக இருக்கின்ற பேர்களுக்குப் பொன்பதி னாயிரந்தான் போடணுமே சம்பளங்கள் இப்பொன்னுக் கெல்லாம் யானெங்கே போவேனடா அப்பொன்னுக் கெல்லாம் அவனை யடித்தல்லவோ வேண்டித்தா னித்தனையும் விதானிக்க வேணுமல்லோ ஆண்டியுன் சொல்லை யான்கேட்க ஞாயமுண்டோ என்னோடு தானிருந்து இந்தமொழி சொன்னாயே உன்னோடு யென்றனக்கு உறவென்ன நீபோடா முன்னெல்லாம் நீதான் உதவியோ யென்றனக்கு இந்நிலத்தை விட்டு எங்கானா லும்போடா அதுக்கந்த நீசன் அடடா என்றிடவே பொதுக்கென்ற கோபமதைப் புந்திதனி லடக்கிச்  சொல்லுவார் பின்னும் சீவனம்போ லுள்ளபுத்தி பல்லுயிருக் கெல்லாம் படியளக்கு மாலோனும் இத்தனையும் வேண்டி என்றனக்குப் பூசையது நித்தமும்நீ செய்யெனவே நின்னோடு கேட்டேனோ பூசைசெய்தாய் நீயும் பிராமண நம்பூரிகட்குத் தேச மறியாதோ செப்பாதோ சாட்சியது ஏற்கா திருப்பதற்கு ஏற்ற அடையாளம் தெற்கே தலைவைத்துச் சென்றதுவுங் காணலையோ பின்னுமந்தப் பூசை புனக்கார மானதெல்லாம் பின்னுங் கடைச்சாதி புலச்சிகை யெச்சித்தீதான் அறியலையோ நான்தான் அமுதேற் றிருப்பதுதான் வெறிகொண்ட நீசா மேதினிகள் சொல்லாதோ என்றந்த மாலும் இத்தனையுஞ் சொல்லிடவே அன்றந்த நீசன் அதற்கேது சொல்லலுற்றான் நான்தந்த தொன்றும் நாவில்வைக்க வில்லையென்றால் என்தொந் தமாக என்னோ டிருப்பதென்ன போபோ நீதானும் போகு மிடந்தனிலே நீபோ வெனவே நிகழ்த்தினான் மாயவரை அப்போது மாயன் அதிகக் கோபத்துடனே இப்போது நீசனைப்பார்த்(து) ஏதுரைப்பா ரம்மானை ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(165,'அனந்தபுரம் விட்டுச் சுவாமி திருச்செந்தூர் ஏகல்','போறேன் நானுன்னுடைய புரத்தைவிட்டுச் செந்தூரில் வாறேன் நானுன்னை வதைக்கவொரு கோலமிட்டு எளியசா ணானெனவே எண்ணம்வைத் தென்றனையும் நளியாகப் பேசி நகைத்தாயே மாநீசா எளியோர் வலியோர் எவெர்க்கும்வெகு நன்மைசெய்து வெளியாக உன்றனக்கு விபரம்போ தித்தருளி நீயறியத் தர்மம் நீணிலத்தி லேநடத்திப் பேய்வெறியைக் கொன்று பேருலக மத்தனையும் நாடாள் வார்தமக்கு நான்பட்டமுஞ் சூட்டித் தாடாண்மை யான சத்தியமா யென்றனையும் ஒருபுத்தி யாகி உள்ளென்னைக் கொண்டோர்க்குப் புதுப்புத்தி யீந்து பூலோகம் ஆளவைப்பேன் வருவேன் நானென்று வருமுன் னறிவதற்கு நிருப மதுவெழுதி நீணிலத்தி லேயனுப்பி மரமறிய சீவசெந்து மலையு மிகவறிய திரமான வாயு சேடன் முதலறிய வருண னறிய மதியு மிகவறிய தருணம் வரும்போது சாணா ரிடம்வருவேன் அறிந்துபல சாதிமுதல் அன்பொன்றுக் குள்ளானால் பிரிந்துமிக வாழாமல் பெரியோராய் வாழ்ந்திருப்பார்         என்று அந்தநீசனுக்கு எடுத்துரைத்து எம்பெருமான் அன்று திருவனந்தம் அவனிவிட் டெழுந்தருளி நீசனுட கோட்டை நெடுநெரெனத் தானிடிய தேசமெல்லாம் நித்திரா தேவி யிருள்மூட ஏழுமேக முங்கூடி இராச்சியத்தைத் தான்மூடி வெளுவே ளெனமாரி வெண்டூளி போல்தொளிய வாயு வதுதிரண்டு வையகத்தைத் தானரிக்க வீசுவீ சென்று உலாவி யதுவீச வாரியது கோபமுற்று வையகத்தை தான்முழுங்க மூரிபோல் மூச்செறிந்து மொகுமொகென கோபமுற்று மாநீச னிட்டிருந்த வாய்த்தகற் கோட்டையெல்லாம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(166,'அனந்தபுரம் விட்டுச் சுவாமி திருச்செந்தூர் ஏகல்','தேனியீன் கூடதுபோல் செகலிடிந்த தம்மானை கோட்டைத் தளமிடிந்து குஞ்சரங்கள் தானிறந்து பூட்டை மிகப்பூட்டிப் போட்டிருந்த காவலெல்லாம் உழைந்து மிகவெருவி ஓகோவென வுளறி கழைந்து அவரோடக் கைமறந்து நின்றனனே சிப்பாயி யோட சுபேதாருந் தானோட அப்பப்பா வென்று அந்நீசன் தானோட பிராமண நம்பூரி புலம்பிமிகத் தானழுது ஸ்ரீராமனையுங் காணலையே தேசமிரு ளாகுதல்லோ            அய்யோ கெடுத்தானே அரசன்நம்மை யென்றுசொல்லி மெய்யோடே குத்தி விழுந்தழுவார் வேதியர்கள் மாய னனந்த புரத்திலே வாழுமட்டும் நீசனுட சட்டம் நின்றுதல்லோ ராச்சியத்தில் ஸ்ரீபத்ம நாபரிந்தச் சீமைவிட்டுப் போனவுடன் பிறிவாக நவ்வா பிடித்தானே சீமையெல்லாம் என்று பலபேர்கள் இப்படியே சொல்லிமிக அன்று புலம்பி அழுவார் சிலபேர்கள் நம்பூரி வேதியர்கள் நாம்கெட்டோ மென்றுசொல்லி வெம்பிடா வண்ணம் வெளியிலுரை யாதிருந்தார் இப்படியே பூலோகம் எல்லாந் திணுக்கிடவே அப்படியே மாயவரும் ஆனதெய் வாருடனே இன்றுமுதல் யானிருக்கும் இடங்களிலே சாதியெல்லாம் ஒன்றுபோ லென்னிடத்தில் ஒத்துமிக வாருமென்று சொல்லித் திருச்சம்பதி சென்றிடவே தானடக்கப் பல்லுயிரும் வந்து படிந்ததுகே ளன்போரே திருச்சம் பதியதிலே சென்றவர் தானிருக்கப் பொருச்சமது பார்த்தான் புகழுமொரு நம்பூரி அப்போது சாஸ்திரத்தில் அவனிபல சாதியெல்லாம் இப்போது சும்மா இங்குவர லாமெனவே  அன்றந்த சாஸ்திரத்தில் அவனிபல சாதியெல்லாம் இப்போது சும்மா இங்குவர லாமெனவே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(167,'அனந்தபுரம் விட்டுச் சுவாமி திருச்செந்தூர் ஏகல்','அன்றந்த சாஸ்திரத்தில் அதுகண்டு மல்லாமல் நன்றந்தச் சாணார்கள் நல்லதே ருண்டுபண்ணித் தொட்டுக் கொடுத்துத் தேர்நடத்த வேணுமென்றும் மட்டும் வெகுதானம் வலங்கையுயர் கொண்டோர்க்குக் கொடுக்கவே ணுமென்று கூறினார் சாஸ்திரத்தை வெடுக்காக அந்த விபரிப்பெல் லாம்நடத்தி வாரிக்கரை யாண்டியென வாய்த்தநா மம்விளங்க நேரியர்கள் சூழ நெடியோ னங்கேயிருந்தார் திருச்செந்தூர் தன்னில் திருமா லங்கேயிருக்க விருச்சமுள்ள நீசன் வேசைநசு ராணியவன் வையங்க ளெல்லாம் வரம்பழித்து மாநீசன் நெய்யதியச் சான்றோர்கள் நெறியெல் லாங்குலைத்துப் பேரழித்துத் தர்மம் பெருமையெல்லாந் தானழித்தான் மார்வரை யேகூடும் மைப்புரசு சஞ்சுவம்போல் தான மழித்துச் சான்றோரின் கட்டழித்து ஈனகுலச் சாதிகட்கு ஈடாக்கித் தான்கொடுத்துப் பள்பறைய நீசனுக்குப் பவளத்தார் தான்கொடுத்துக் கள்பறைய சாதிகட்குக் காலமிகக் கொடுத்துச் சாதி வரம்பு தானழித்து மாநீசன் மூதி முன்னீசன் மும்முடி யுந்தவிர்த்து நவ்வா முடியெனவே நாடிவன்நா டாகவேதான் எவ்வோ ரறிய இவன்தேசந் தானாக்கி ஆளாகமுன் னீசனையும் அவனைநா டாளவைத்துப் பாழாக நீசன் பழையசட்ட முமாற்றி நீசன் நவ்வாவின் நினைவுபோல் சட்டமிட்டுத் தேசமெல்லாம் நவ்வா செய்தானே சட்டமது மானம் வரம்பு மகிமைகெட்டுச் சான்றோர்கள் ஈன மடைந்து இருக்கின்ற வேளையிலே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(168,'தேவர் முறையம்','தேவர்க ளெல்லாம் திருச்செந்தூர் சென்றேகி மூவரொரு மித்தனுக்கு முறையிட்டா ரம்மானை ஆதி முதற்பொருளே அய்யாநா ராயணரே சோதியே யெங்கள் துயரமெல்லாந் தீருமையா இத்தனை நாளும் இருந்தோமொரு மானுவமாய்க் கொற்றவரே நாங்களினிக் குடியிருக்கப் போகாது சாதிக்கட்டை யெல்லாம் தலையழித்து மாநீசன் மேதினிக ளெல்லாம் மேவினா னையாவே தான மழிந்தாச்சே தம்பியர்கள் சான்றோரின் மான மழிந்தாச்சே வரம்பெல்லாங் கெட்டாச்சே பூப்பியமுங் குலைத்துப் புரசியோ டொப்பமிட்டுக் காப்பிலிய னேதுவினால் கட்டழிந்தார் சான்றோர்கள் இத்தனை நாளும் யாங்கள்முறை யிட்டதுபோல் புத்தி தனில்வைத்தால் பொறுக்கஇனிக் கூடாதே தம்பி சான்றோர்கள் சங்கடத்தைக் கேளாமல் சம்பி முகம்வாடித் தலைகவிழ்ந் திருப்பதென்ன மக்களு டதுயரம் மனதிரங்கிப் பாராமல் பக்கமாய் நீரும் பாரா திருப்பதென்ன சான்றோர் படுந்துயரம் தானிரங்கிப் பாராமல்  ஆண்டோரே நீரும் அயர்ந்தே யிருப்பதென்ன  இதெல்லா மெங்களைநீர் ஏற்றசான் றோர்பிறப்பாய் முதலெல்லாஞ் சான்றோருள் முடிந்துவைத்த கண்ணியினால் பாவியந் தநீசன் படுத்துந் துயரமெல்லாம் தாவிக் கயிலை சத்தி சிவன்வரைக்கும் பொறுக்கமிகக் கூடலையே புண்ணிய அய்யாவே மறுக்க மதைப்பாரும் மனதிரங்கி யெங்களுக்கு உகத்துக் குகங்கள் ஊழியங்கள் செய்ததெல்லாம் அகற்றி யருள்தந்த அச்சுதரும் நீரல்லவோ மக்களா யெங்களையும் வலங்கைவுய்யோர் தாமாக ஒக்குறவு கெட்டோன் உலகில் படைத்திருந்தால் இத்தனை பாடும் எங்களுக் கென்றோதான் புத்திரரா யெங்களையும் பூமிதனில் பெற்றுவைத்து நாங்கள் படும்பாடு நாரணரே கண்டிலையோ தாங்களுக்கு நெஞ்சம் சற்று மிரங்கலையோ இப்படியே தேவர் எல்லோரு முறையமிட அப்படியே முறையமிட்டு அவரங்கே நிற்கையிலே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(169,'அம்மை உமை இரங்கல்','நல்ல உமைதிருவும் நன்றா யெழுந்தருளி வல்ல பொருளான வாய்த்தநா ராயணரின் அடியி லவள்வீழ்ந்து அழுதுகரைந் தேதுரைப்பாள் முடியுமடி யில்லா முதலே முதற்பொருள அரிஹரிநா ராயணரே அண்ணரே அச்சுதரே கரிஹரிநா ராயணரே கண்ணரே கார்வண்ணரே இலச்சைகெட்ட பாவி ஏமாளிக் கலியதினால் அலச்சல்செய் தெங்களையும் அகற்றிவைத்துப் போனீரோ பாவிக் கலியனுட பழிசாபச் சூட்சியினால் பூவில் மண்டூகம் பொசித்துதே காரணரே கலியனுட ஏதுவினால் கபாலியும் மைத்துனரும் சலிவாகி மேனி சடல மிகத்திமிர்த்து இருள்மூடிக் கண்கவிழ்ந்து இருக்கிறா ரீசுரரும் உருவு சுவடில்லை உம்முடைய மைத்துனரும் நின்னயமில் லாக்கலியன் நீசன் பிறந்ததினால் என்னோடே பேச்சு இல்லையும் மைத்துனரும் வானுறவு கெட்ட மாநீசன்வந்த நாள்முதலாய் நானும்பர மேசுரரும் நலநஷ்டமுந் தெரியோம் பேசிப் பழக்கமிட்டுப் பெருத்தநா ளுண்டுமண்ணே தோசிக் கலியனுட சூட்சியினால் நாங்கள்படும் பாட்டைவந்து பாராமல் பரிகாசம் பார்ப்பதென்ன நாட்டைக் கெடுத்தானே நன்றிகெட்ட மாநீசன் அல்லாமல் நம்முடைய அருமைச்சான் றோர்கள்படும் பொல்லாங்கை யெல்லாம் போய்ப்பார்க்க எழுந்தருளும் வரமீறியக் கலியன் மாய்கையி னேதுவினால் பிரமன் பிறப்புப் பிசகித் தலைமாறி முண்டம்போல் பிறப்பு முகங்கண்ணில் லாப்பிறவி பிண்டப் பிறவி பேருறுப் பில்லாப்பிறவி தலையுடம் பில்லாத சடலப் பிறவியைப்போல் நிலைதவறி வேதா நினைவுதடு மாறினனே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(170,'அம்மை உமை இரங்கல்','சீவன் கொடுக்கும் சிவனுக் கிருநினைவாய்ப் பாவக் குணவுயிராய்ப் பகர்ந்தார் சிவனாரும் படியளக்க நீரும் பண்புற் றிருக்காமல் குடிகேடு மாச்சே குடிக்கக் கிடையாமல் இத்தனை யுங்கலியன் ஏதுவால் வம்பாச்சே அத்தனையும் நீர்தான் அறியாதவர் போலே எங்கள்மேல் பகைபோல் இருப்பதென்ன அச்சுதரே சங்கடங்க ளெல்லாம் தான்சொல்லக் கூடாது கயிலை சிவனார் காணாம லும்மையுந்தான் அகிலமதைப் பாராமல் அயர்ந்திருக்கி றாரெனவே ஆனதா லென்னுடைய அண்ணரே அங்கேகி மானமுள்ள புத்தியும் மைத்துனர்க்குச் சொல்வாரும் என்றுமையா ளிப்படியே இன்பமாய்ச் சொல்லிடவே அன்று பெருமாள் அவள்முகம்பார்த் தேதுரைப்பார்  சிவனாரும் நீயும் தேசமதி லில்லாட்டால் எவனுக்கு மலைச்சல் இல்லையே ராச்சியத்தில் உங்களால் யானும் உகத்துக் குகங்கிடந்து சங்கடங்க ளுற்றுத் தவிக்கத் தலைவிதிதான் நீங்களில்லை யானால் எனக்கும் அலைச்சலில்லை ஏனுங்க ளோடே இப்பாடு யான்படத்தான் அயோத்தியா பட்டணத்தில் அரசுண்டு மென்றனக்குக் கையேற்று வந்த கன்னி திருவுமுண்டு ஏனிந்தப் பாடு யான்படக் காரணந்தான் மானழுதாற் போலே மறுகுவாள் லட்சுமியும் கைப்பிடித்த நாள்முதலாய்க் கலந்துவிளை யாடறியேன் மெய்ப்பிடித்த மான மெல்லியரு மங்கிருக்க இரப்பனைப் போல்லோகம் எல்லாந் திரிந்தலையப் பரப்பிரம னம்மைப் படைத்ததுமி தற்கோவென எல்லையில்லாப் பாடு யான்பட்ட துபோதும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(171,'அம்மை உமை இரங்கல்','நல்லகுலச் சான்றோரை நான்பெற்று வைத்தேனே வைத்த தெல்லோரும் மனதறியப் பார்த்திருக்க பொய்த்தலைவ னீசனையும் பிறவிசெய்ய ஞாயமுண்டோ நீசனுட கையதிலே நின்மக்கள் தங்களையும் பாசக் கயிறிட்டுப் பந்தடிக்கச் செய்தாரே  தொட்டாலும் நீசம் தொடருமதைக் கண்டாலும் வெட்டா வெளியாய் விரட்டிப் பிடிக்குமல்லோ காதிலதைக் கேட்டாலும் கர்மம்வந்து சிக்குமல்லோ ஆரிந்த நீசனையும் அழிக்கவகை யாரறிவார் மாய்கை நினைவொழிய மறுநினைவு வாராதே மோசக் கலியனையும் முடிக்கவகை யாரறிவார் வாளா யுதத்தாலே மாளானே மாகலியன் தூளாம் பிறவி சொல்லொணா வல்லமைதான் முப்பிறவி யாறும் முடித்த வலுமையெல்லாம் இப்பிறவி தன்னில் எட்டிலொன்று பாரமில்லை உகத்துக் குகம்பிறந்து ஓடித்திரிந் தவனென்றே அகத்துக்கொஞ்சம் வைய்யாமல் அவர்மூப்பாய்ச் செய்தாரே ஆனதா லின்னமினி அங்குவர வேணுமோகாண் தானம்வைத்துப் பாராமல் சங்கை யழித்தாரே எங்கே யானாலும் இறந்திடுவே னானிதற்குச் சங்கையுள்ள சாணார்க்குத் தங்குமிடம் வேறிலையே தாய்தந்தை யில்லாமல் தயங்குகின்ற பிள்ளையைப்போல் மாயக் கலியனினால் வாடுவரே சான்றோர்கள் தகப்பனில்லாப் பிள்ளை சாணார்க ளென்றுசொல்லி அகப்படுத்திச் சான்றோரை அடிப்பானே மாநீசன்  தாயுந் தவமிருந்து சடைப்பாளெனைக் காணாமல் ஆயுங் கலைதெரிந்த ஆயிளையும் வாடுவளே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(172,'அம்மை உமை இரங்கல்','இப்படியே ஞாயம் இருப்பதா லென்றனக்கு அப்படியே வேறு வழியல்லவே யானதினால் எங்கினிப் போவேன் என்றெண்ணுது என்மனது உங்களுட ஊருக்கு ஒண்ணுதலே போநீயெனக் கோபத்தால் தங்கையுடன் கூறினா ரித்தனையும் வேகத்தா லம்மை விழியிட் டழுதனளே கயிலைக்கு நீர்தான் கால்வைக்கா தேயிருந்தால் அகிலம தொன்றாய் அழியு மொருநொடியில் நல்லகுலச் சான்றோர் நாடுங் கலியனினால் தொல்லைமிகப் பட்டுத் துயருற்று வாடுவரே நானுமென்றன் ஈசுரரும் நல்ல கயிலையிலே மானுங் கலைபோல் மறுகி யிருப்போமே கயிலை முனிதேவர் கட்டழிந்து வாடுவரே மயிலனைய ஈசர்சொல்லை மறந்து மிருந்தாரே கன்னிப்பெண் ணார்களென் கற்பனையைத் தான்மறந்து உன்னுதலாய்ப் பெண்கள் ஒருவர்க்கோர் மூப்பாச்சே ஐயோ கயிலை அங்கே வரம்பழிந்து பையரவம் பூணும் பரமன்மௌ னமாச்சே  அண்ணரே கோவே அடியாள் தனக்கிரங்கி எண்ணமில்லாக் கயிலைக்கு எழுந்தருள வேணுமையா என்று அறம்வளர்த்தாள் இறங்கியண்ணர் கால்தனையும் சென்று பிடித்துச் செய்யத்தாள் தான்வணங்கி மனதிரங்கி எம்பெருமாள் மார்போடு தங்கையரைத் தனதில் பிரியமுற்றுத் தங்கையரோ டேதுரைப்பார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(173,'திருமால் திருக்கயிலை ஏகல்','உன்னோடு பிறந்த ஊழிவிதி யானதினால் நன்னாடோ டேவாழ நமக்குவிதி யில்லையே உன்றனக் காகவல்லோ ஊருக்கூரே திரிந்து சந்தோச மற்று சடைக்க விதியாச்சே என்று பெருமாள் ஏற்ற பிறப்போடு இன்று கயிலைக்கு எழுந்தருள வேணுமென்று தேவர் முனிவரோடு திருக்கனனி மாரோடு மூவருட நடுவன் மகமலர்ந் தேகூடி நடக்கவே ணுமெனவே நளினமுற் றெம்பெருமாள் கடற்கரையில் வந்து காலமே தெனப்பார்த்தார் இந்தச் சொரூபமதாய் இங்குவிட் டெழுந்தருளிச் சிந்தர் குடியிருக்கும் சீமையி லேகுமுன்னே கலிமூழ்கி நாமள் கரையேறப் போறதில்லை பொலிவாக வேசம் புதுப்பிக்க வேணுமென்று  என்ன சொரூபம் எடுப்போம்நா மென்றுசொல்லி அன்னப் பெருமாள் ஆலோ சனையாகி மேலெல்லாம் வெண்ணீற்றை மிகஅணிந் தெம்பெருமாள் பாலொக்கும் நெஞ்சம் பரம னொருநினைவாய் மாமோக ஆசை மயக்க வெறியறுத்துத் தாமோ தரனார் சடைக்கோல மேபுனைந்து காலில் சிவநினைவைப் கழராமல் தானிறைத்து மார்பிலைந்து பூமணியை வகிர்ந்தார வேடமிட்டுக் கண்ணில் மனோன்மணியைக் கண்ணாடி யாய்ப்பதித்து எண்ணரிய தங்கை இன்பமுட னேவளர்த்த முப்பத்தி ரண்டறத்தை உடலெல்லா மேபொதிந்து செப்பமுடன் கந்தை செய்யவெண் கலையணிந்து தண்டரள மானத் தடியொன்று கைப்பிடித்து அண்டர்களும் போற்றி அரகரா என்றுவர தேவர்களுங் கூடிச் சிவசிவா சிவனேயென்று தாவமுள்ள தேவரெல்லாம் தாரமி டேத்திவர');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(174,'திருமால் திருக்கயிலை ஏகல்','அம்மை உமையாளும் அவளுஞ் சடைவிரித்துச் செம்மையுள்ள மாதும் சிவசிவா என்றேத்தி எல்லோ ருடனே எம்பெருமாள் தானடந்து வல்லோர் புகழும் வாழ்கயிலை வந்தனராம்  கயிலை தனில்வரவே காரிருளுந் தான்மறைய மயிலையொத்த ஈசர் மாயவரைக் கண்டாவிச் சந்ததியைக் கண்டு தாயாவல் கொண்டதுபோல் வந்தந்த ஈசுரரும் மார்போ டுறவணைத்து இத்தனை நாளும் யானும்மைக் காணாமல் புத்திரனுந் தாயைப் போகவிட்டாற் போலிருந்தேன் மாரி காணாத வாய்த்த பயிர்போலும் ஏரி காணாத ஏற்றநீர் போலிருந்தேன் இனம்பிரிந்த மான்போல் இருந்தே னுமைத்தேடித் துணையில்லார் போலே துணையற்று நானிருந்தேன் என்று மகாமாலை எடுத்தாவி ஈசுரரும் சென்றாவிக் கொண்டு சிணுங்கி யழுதுசொல்வார் இணையும் புறாவதுபோல் இருந்தோமே மைத்துனரே துணையு மெனைமறந்து தூரநீர் போனதென்ன என்னைவிட்டு நீரும் எழுந்தருளு மன்றுமுதல் வன்ன உமையாளின் வடிவை மிகஅறியேன் துணையிழந்த அன்றிலைப்போல் தினமு முமைத்தேடிக் கணவனில்லாப் பெண்போல் கலங்கினேன் காரணரே உயிர்த்தோ ழமைபோல் உறவுகொண்ட நாரணரே மெய்த்தோ ழமையை விட்டுப் பிரிந்தமுதல் பிரிந்து மலைத்தேன் பச்சை நிறமாலே சரிந்துபள்ளி கொண்டு சதாவருட மாச்சுதல்லலோ இத்தனைநாளும் என்னைமிகப் பாராமல் மத்திப மந்திரியே மறந்தென்னை வைத்ததென்ன கயிலைக்கு மென்றனக்கும் கட்டான மந்திரிதான் அகில மீரேழ்க்கும் அடக்கமுள்ள மந்திரிதான்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(175,'திருமால் திருக்கயிலை ஏகல்','நீயல்லாமல் வேறுளதோ நெடிய திருமாலே தானீ தமான சர்வபர நாரணரே இந்த விதமாய் இகபர விதத்தோடும் உந்தன் தனைப்பெறவே உயர்ந்ததவஞ் செய்தனல்லோ அப்படியே நீரும் ஆதிநா ராயணராய் முப்படியே வந்து உருவெடுத்த மாயவரே கயிலைத் தேவாதிகளும் கண்ணான மாதிருவும் ஒயிலான மாமறையோர் உற்ற இருஷிகளும் கின்னரர்கள் வேத கிம்புருடர் கேதாரர் மன்னர் முனிவர்களும் மறையும்பல சாஸ்திரமும் வேதாவும் நந்தி விதரூபச் சித்தர்களும் நாதாந்த வேத நல்ல ரிஷிமாரும் இப்படியே ஓர்வசனம் அவர்களோ டேகேட்டுக் கல்லை வலித்திழுத்துக் காலில்மிகப் போட்டவர்போல்          சொல்லை மிகக்கேட்டுச் சுமந்தேன் மிகுபாரம் எல்லா விதப்பாடும் யான்பட்ட துபோதும் வல்லவனே யுன்னுடைய வாக்கைக்கே ளாதபடிச் செய்த விதத்தாலே செப்புதற்கு நாணமுண்டு மெய்யிசையு மாலே மெத்தகோ பிக்கரிது எல்லா முமது இச்சையது போல்நடத்தும் சொல்லொன்றுக் குள்ளே சூட்டிநீ ராளுமென்று மாலுக்கு மனது மகிழ்ச்சை வரும்படிக்குப் பாலுக்கு மோரான் பகர்ந்தார்கா ணம்மானை அப்போது அச்சுதரும் அவரேது சொல்லலுற்றார் எப்போது முமக்கு இப்படியே யுள்ளமுறை நானொருவன் வேடனைப்போல் நாட்டில் திரிந்தலைய ஏனோகா ணுங்களுக்கு இல்லையே சங்கடங்கள் முன்னுதித்தப் பாவி முழுநீசக் குறோணியினால் பின்னுந்துரி யோதனனாய்ப் பிறந்தவன் மாளும்வரை உகமா றானதிலும் உதித்தக்குறோ ணியுயிரைப் பகையாக்கிக் கொல்ல பட்டபா டெத்தனைகாண்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(176,'திருமால் திருக்கயிலை ஏகல்','யான்மிகப் பட்டபாடு ஆரும் படவரிது தான்பட்ட தெல்லாம் தான்போது மென்றுசொல்லி இனிமேல் பகைதான் இல்லாமல் செய்வதற்கு  மனுவாய்ப் பிறக்க மக்களே ழுபேரை சேர்த்தெடுத்து விந்தில் சேர்ந்துசெங் காவுதன்னில் சார்ந்து விளையாடி தான்பெற்ற பாலருக்கு எல்லோருங் கூடி ஏற்றதிரு நாமமிட்டு வல்லோராய்ப் பூமிதனில் வாழுமென்று வைத்தோமே அலுவ லொழிந்ததென்றும் அலைச்சல்மிக இல்லையென்றும் மெலிவெல்லாந் தீர்ந்து மேவிஸ்ரீ ரங்கமதில் இனிதிருக்கும் போது என்னை நினையாமல் மனிதனையும் பூமியிலே வகுத்ததென்ன சொல்லுமென்றார் ஆறு யுகமதிலும் அதிகபலக் காரர்களாய் வீறுடனே பிறவி விதவிதமாய்ச் செய்தீரே முன்பிறந்த குறோணி முழுங்கினான் கயிலைமுதல் வன்பிறவிச் சூரனைத்தான் வதைக்கவே றாருமுண்டோ இரண்டாம் யுகத்தில் நன்றிகெட்ட மாபாவி குண்டோம சாலியனாய் குதித்தான் மகாபாவி வானலோ கமுழுங்க வாய்விட் டலறினதால் கோனகிரி வேந்தே கொன்றதுவே றாருமுண்டோ மூன்றாம் யுகத்தில் முளைத்தானே யப்பாவி வான்றான மல்லோசி வாகனென்ற மாபாவி தேவரையுந் தெய்வ ஸ்திரிகளையு மூவரையும் ஏவலது கொண்டு இடுக்கமது செய்தனனே முறையமது ஆற்றாமல் ஓடிப்போ வென்றுரைத்தீர் சிறைப்பிடித்தப் பாவிகளைச் செயித்ததுவே றாருமுண்டோ நாலாம் யுகத்தில் நன்றிகெட்டச் சூரபற்பன் ஆலா விருச்சம் அதுபோல் கிளையுடனே நாட்ட முடனே நாடும் படையோடு கோட்டை யதிட்டுக் கொடியமா பாவியனாய்க் கேட்ட வரங்களெல்லாம் கெட்டியாய் நீர்கொடுத்துத்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(177,'திருமால் திருக்கயிலை ஏகல்','தேட்டமுடன் கயிலை சீமையது வுங்கொடுத்து இருக்க இடமற்று எழுந்திருந்து நீரோடி உருக்கமுடன் நானிருந்த உவரியி லேகிவந்தீர் தேவர்களைப் பாவி செய்த இடுக்கமதால் மூவர்முத லும்மிடத்தில் முறையிட்ட ஏதுவினால் என்னை வருத்தி ஏற்ற மொழிகள்சொல்லிச் சென்றவனைக் கொல்லுமென்று செய்திசொல்லி விட்டீரே விட்டவுட னவனோடே வெகுநாளா யுத்தமிட்டுப் பட்டபா டெல்லாம் பகர எளிதாமோ உயுத்தமிட் டவனை ஒக்கத் தரமறுத்துச் செயித்தேன் பின்னவனும் செத்த யுகமதிலே இரணிய சூரனென்று எனக்கெதிரி யாய்ப்பிறந்து தரணியி லவன்பேரைச் சாற்றி வழங்கலுற்றான் பின்னு மவன்றனக்குப் பிள்ளையாய் நான்பிறந்து கொன்று தரமறுத்தேன் குதித்ததுகாண் மற்றயுகம் பத்துத் தலையோடு பாவியவன் பிறந்து உற்றுஅவன் கேட்டவரம் ஒக்கநீர் தான்கொடுத்து அருகில்மிக வாழ்ந்திருந்த ஆயிளையை நீர்கொடுத்துக் குருவிருந்த சோதி குன்றையும் நீர்கொடுத்துப் பாவியவன் மூன்று பாரடக்கி ஆண்டனனே தேவியும் நானும் தேசமதி லேபிறந்து அவனை யழித்து அந்தயுக முங்கருக்கிச் சிவனேயுன் பாரிதனைச் சிறைமீட்டித் தந்ததாரு சதியாந்துரி யோதனனாய்த் தான்திரும்ப வெபிறந்து மதியாம லென்றனையும் மறையுமிக நம்பாமல் அன்போரை யெல்லாம் அகப்படுத்தி மாபாவி வம்பான கள்ளன் மாய்மாலஞ் செய்ததினால் கொன்றவகை சொல்லக் கூடுமோ காரணரே என்னென்ன பாடு யான்பட்ட மாய்மாலம் கஞ்சனென்ற பாவிக்குக் கடியவரம் நீர்கொடுத்து வஞ்சகன்தான் செய்த மாய்மாலம் மெத்தவுண்டு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(178,'திருமால் திருக்கயிலை ஏகல்','அவனை வதைக்குமுன்னே அடியெத்த னைகளுண்டு சிவனே யுமக்குச் சூடெண்ணஞ் சற்றுமில்லை இருப்பி லிருந்துகொண்டு எனக்கலைச்சல் செய்வதுண்டு எதிர்ப்பவரார் சொல்லும் இந்தமாய் மாலமொடு கலியென்ற சொல்லு காதில்மிகக் கேட்டதுண்டால் சலிவாகி மேனி சடலமிரு ளாகுமல்லோ மெய்ப்பேச வேணுமென்று மேவி யிருந்தாலும் பொய்ப்பேச புத்திப் பொலியும் பொடுபொடென வாளா யுதத்தாலே வாய்த்தகணை யம்பாலே பாழாக்க வென்றாலும் படாதே மகாலிதான் மற்றப் பிறவி யாக வகுத்தாலும் இத்தனை யெண்ணம் இல்லையே யென்றனக்கு அய்யோயென் மக்கள் அதற்குள்ளாகப் பட்டாரே மெய்யோ யினிமக்கள் விளங்குவ தெக்காலம் தீண்டிப் பிடித்தானே செத்தகலி மாய்மாலன் வீண்டிடறு தீர்ந்துமக்கள் விளங்குவது எக்காலம் ஆறு பிறவி அழித்ததுவு மென்றனக்கு நீறுபோ லெண்ணுதுகாண் நிற்கலியைப் பார்க்கையிலே என்ன விதத்தால் இவனை யழிப்போமென்று வன்னச் சிவனாரே மனதுவேறே ண்ணுதுகாண் நாடாச்சு தேசம் நகரியுங்கள் பாடாச்சு வீடாச்சு குண்டம் மேதினிக ளங்காச்சு தேச வழிபோறேன் செல்லாது இங்கேதான் மாய்கை வலையைவிட்டு மறையவே போறேன்காண் பலபேர்க ளாட்டும் பாம்புபோ லென்னையுந்தான் அலைவிதமா யென்னை அழைத்ததென்ன சொல்லுமென்றார் அப்போது ஈசுரனார் அச்சுதரைத் தான்பார்த்து இப்போது நானும் இயம்புகிறேன் கேளுமென்று முற்பிறவி துயரம் எல்லாம் வரும்படிக்குப் பண்டுவொரு சாபம் பகர்ந்தேன்நான் மைத்துனரே அன்றுசா பம்பகர்ந்த அதுகேட்டுக் கொண்டருளும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(179,'திருமால் திருக்கயிலை ஏகல்','குறோணி யொருசூரக் கொடும்பாவி தான்பிறந்து சுறோணித மாயவரே தொல்புவியெல் லாமுழுங்கி என்னோடு ஈஸ்வரியாள் எல்லோரை யும்விழுங்க நின்னோடு மாயவரே நீயொருவன் தப்பிமிக என்னை நினைத்துத்தவம் இருந்தாய்நீ கீழுலகில் தென்னவனே நீயும் செய்தத் தவமதினால் எழுந்தருளி நானும் என்னவரம் வேணுமென்றேன் குளிர்ந்த குணமான கோவேந்தே நீமாறி கேட்டாயே சூரனுடல் கீறித்துண் டாறாக்கித் தாட்டாண்மை யாகத் தரணிதனில் விட்டெறிய  வரந்தாரு மென்று வாகாகக் கேட்டவுடன் தரந்தரமே துண்டம் தரணிக்கெல் லாம்பிறந்து மாற்றா னாயுனக்கு வருமாறு யுகம்வரைக்கும் சீற்றத் துடனே செடமெடுக்கு மென்றுசொல்லித் தந்த வரத்தின் தன்மையா லிந்தமுறை எந்தன்மேல் கோபம் ஏன்சொல்லப் போறீர்காண் என்றந் தஈசர் இப்படியே சொன்னவுடன் அன்று மகாமால் ஆகங் களிகூர்ந்து ஆனாலும் நான்கேட்ட அவ்வரங்க ளானாலும் மானாபர ஞாய வரம்பழித்துப் போட்டீரே இதற்குமுன் னேதாலும் என்னையறி யாதவண்ணம் ஒதுக்கிலிருந் தோர்வளமை உங்களால் செய்ததுண்டோ மும்முறைபோ லென்னை உவந்துக் கெணியாமல் அம்முறையை நீரும் அழித்ததேன் ஈசுரரே என்னைக் கெணியாமல் இக்கலியைச் செய்ததினால் நன்னமிர்த ஈசுரரே நம்மாலே கூடாது அழிப்பதுவுங் கலியை அங்கல்லால் கூடாது விழிப்பதென்ன நம்மாலே விசாரமிடக் கூடாது நம்மாலே யொன்றும் நடவாது ஈசுரரே சும்மாவென் னோடிருந்து சோலிபண்ண வேண்டாங்காண் உகத்துக் குகங்கள் ஓடித்திரிந் தலைந்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(180,'திருமால் திருக்கயிலை ஏகல்','மகத்துவமாய்ச் சந்து சதைமுறிந்து நோகுதையா தூங்கி விழியாமல் திமிர்ப்போல் சரீரமது ஏங்கிமிக வாடுதப்பா என்திருவைக் காணாமல் பாவிகள் பண்ணிவிட்ட பாட்டைமிக எண்ணுகையில் ஆவி மிகவாடி அங்கமெல்லாஞ் சோருதையா அந்தமுழுப் பாவிகளில் அதிகமுழுப் பாவியிவன் சந்ததிக்குத் தாய்தகப்பன் தாழ்ந்துநின் றேவல்செய்தால் கொடியவனோ பாவி கொஞ்சமிஞ்ச மோயிவன்தான் முடியுமோ பாவி யுத்தமிடச் சென்றதுண்டால் தன்ம மறியாத சண்டித்தடி மூடர்கையில் நம்மைப் பிடித்தங்கே நகட்டிவிட வேண்டாங்காண் பட்ட அடிவூறப் படுத்திருக்கப் போறேனான் ஒட்டொழிய என்னை ஒடுக்கிவிட வேண்டாங்காண் என்றாதி நாரா யணர்தானு மிப்படியே அன்றாதி நாதன் அவரோ டிதுவுரைக்கச் சிவனு மிகவயர்ந்து செப்புவார் மாயருடன் புவன மதற்குடைய பொன்னே யென்மாதவமே இப்படித்தான் மைத்துனரே ஈதுரைத்தீ ரானாக்கால் எப்படித்தா னானும் ஏகம தாளுவது  சுவரல்லோ மைத்துனரே சித்திரம் நாங்களல்லோ கவரல்லோ நாங்கள் கயமல்லோ மைத்துனரே மாயனல்லோ எங்களுக்கு மலையல்லோ மைத்துனரே ஆயனல்லோ எங்களுக்கு அணையல்லோ மைத்துனரே கொப்பல்லோ நாங்கள் குருவல்லோ மைத்துனரே செப்பல்லோ மைத்துனரே சிமிள்கூடு நாங்களெல்லாம் நீர்தானு மிப்படியே நெகிழ்ந்தமொழி சொன்னதுண்டால் ஆர்தானு மெங்களுக்கு அணைவுமொழி சொல்லுவது மூடிப்படிக் குலுங்க முயங்காதோ கொப்பதெல்லாம் கூடிப்படிக் குலுங்க குலையாதோ சீவனது தேவர்முனி சாஸ்திரிகள் சிறுபுத்தி யைக்கேட்டு மூவருன்னைப் பாராமல் முனிந்துசெய்த ஞாயமெல்லாம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(181,'திருமால் திருக்கயிலை ஏகல்','பொறுத்தருளு மென்னுடைய புண்ணிய மைத்துனரே மறுத்துரைத்தால் நானுமினி வகுத்துரைக்க ஞாயமில்லை எல்லாம் பொறுத்து இனியுமக்குத் தேர்ந்தபடிச் சொல்லொன் றுக்குள்ளே செலுத்தியர சாளுமென்று ஈசுரனார் சொல்ல எம்பெருமா ளேதுசொல்வார் வீசுபுக ழீசுரரே விரித்துரைக்க நீர்கேளும் என்னையுமோ கீழுலகில் இறந்துகிடந் தாரெனவே சொன்னவரை யெல்லாம் சுறுக்காகத் தானழையும்  என்றந்த மாயவனார் இப்படியே சொன்னவுடன் அன்றந்த ஈசுரனார் அழைத்தா ரவர்களையும் உடனேதான் தேவர்முனி ஒக்கப் பயமடைந்து தடதடென ஓடிவந்து தாழ்ந்துநமஸ் காரமிட்டு அப்போது மாயவனார் அவர்கள் தமைப்பார்த்துச் செப்புகிறா ரையா திருக்கண் பொறிபறக்கக் கண்கள் சிவந்து கருமேனி தான்விறைத்து விண்க ளதிர வெடுவெடெனக் கோபமுற்றுத் தேவர் திணுக்கிடவே தெய்வமுனி தான்பதற மூவர் திணுக்கிடவே உரைக்கிறா ரெம்பெருமாள் மண்திண் ணெனவே மலைகள் பொடிபடவே திண்திண் ணெனவே தேவர் மிகப்பதறச் சிவனுங் கிடுகிடெனச் சிவனுமை யும்பதற நமனும் பதற நாரா யணருரைப்பார் தேவரே வானவரே தெய்வமுனி சாஸ்திரியே மூவரே நானும் ஒளித்ததெங்கே சொல்லுமென்றார் ஆளுக்கொரு மூப்பாய் அரியிங்கே யில்லையென்று கோளுரைத்த ரீசர் குருமுன்னே நீங்களெல்லாம் சாத்திரத்தைப் பார்த்துச் சரியாகச் சொன்னதிட்டச் சூத்திரத்தை யெல்லாம் சொல்லுமென்றா ரெம்பெருமாள்  சொல்லநா வில்லாமல் சோர்ந்துமிகத் தேவரெல்லாம் பல்வாய் சுண்டொல்கிப் பதைபதைத்து நின்றனரே கண்தலை கவிழ்ந்து கால்பேர்த்து வைக்காமல்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(182,'திருமால் திருக்கயிலை ஏகல்','விண்கவிழ்ந்த யோகமுனி வெட்கிநின்றா ரன்போரே நின்ற நிலையறிந்து நெடியபர மேசுரரும் அன்றெந் தனோடே அறம்பாடிச் சொன்னதெல்லாம் இன்றெங்கே போச்சுதுகாண் இரண்டும்மிகச் சொல்லாமல் நின்றிங்கே நீங்கள் நினைவயர்ந்து நிற்பதென்ன பேச்சுப்பே சுங்கிளிக்குப் பின்பூனை நாட்டமுற்றால் சீச்சுக்கீச் சென்றுகிளி கீழ்ப்பதுங்க வேண்டியதேன் அன்றுநீர் சொன்னமொழி அயர்த்துமிகப் போனதென்ன இன்றுநான் கண்டிலனே இதற்குமுன் னுள்ளதிறம் இப்படியே ஈசர் எடுத்துரைக்கத் தேவரெல்லாம் எப்படிநாம் சொல்வோம் என்றே திகைக்கலுற்றார் எல்லா மறிந்து எடுத்துரைப்பா ரெம்பெருமாள் பொல்லாதார்க் கெல்லாம் பிறப்பொன் றிசைந்திருக்கு சொல்லுகிறேன் நானுமொன்று சொல்லுவதைக் கேட்பீரோ நல்லதுதா னென்று நாட்டமுற் றெல்லோரும் அப்போது எம்பெருமாள் ஆதிசிவ மோடுரைப்பார் இப்போது கேட்பீரோ யான்சொல்லும் நியாயமது');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(183,'சிவன் திருமால் ஐக்கியம்','என்றுதிரு மாலுரைக்க எடுத்துரைப்பா ரீசுரரும் மன்று தனையளந்த மாலேயென் மைத்துனரே வாரு மெனஅழைத்து மாலோன் தனையிருத்திச் சீருடனே வேதாவும் சிவனும் உமையாளும் எல்லோருங் கூடி இருந்தந்த மாயவரின் நல்லா யருகிருத்தி நவிலுவார் சத்தியமாய் இதற்குமுன் செய்த இழிவெல்லாம் நீர்பொறுத்துக் கதுக்காக வும்முடைய கருத்தின் படியாலே நடக்கும்படிச் சட்டமெல்லாம் நாரணர்க்குத் தந்தோங்காண் அடக்குடக் கெல்லாம் ஆதிக்குத் தந்தோங்காண் இன்றுமுத லும்முடைய இச்சைபோ லேநடத்தி என்றும் நடத்திக்கொள்ளும் என்றே விடைகொடுத்து சட்டமெல் லாமுமக்குத் தந்தோ மினிநீர்தான் இட்டசட்ட மீறி இனியொருவர் செய்யோங்காண் உம்மை நினையாமல் உக்கலியைச் செய்ததினால் நம்மை வரைநீர்தான் நடத்தும் படிநடத்தும் என்றந்த ஈசுரரும் ஏற்ற உமையாளும் அன்றந்த வேதாவும் அமரர்முனி தேவர்களும் சத்திய மாகத் தானுரைத்தார் நாரணர்க்கு மத்திய நாதன் மனமகிழ்ந் தேதுசொல்வார்  நல்லதுதா னென்று நாட்டமுற் றெம்பெருமாள் வல்ல பரமே சுரரோ டேவகிர்வார் கன்மம்போ லொத்தக் கலிப்பிறப் பானதினால் நம்மள் முதல்மாறிப் பிறக்கநா ளாகுதுகாண் ஆனதா லீசுரரே அருளுகிறேன் நீர்கேளும் ஈனமுள்ள பாவி இயன்றதுரி யோதனனைக் கொன்றே னவனைக் குருநாடை வர்க்கீந்து முன்னே மறையோன் மொழிந்தசா பத்தாலே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(184,'சிவன் திருமால் ஐக்கியம்','தேவர்முதல் வானோர் தேவமுனி தான்வரையும் மூவ ருறையும் உற்றதெய்வ லோகமேழும் மேலோர்க ளெல்லாம் மேதினியி லென்றனக்குப் பூலோகந் தன்னில் பிள்ளையென வேபிறக்க வேணுமென்று மாமுனிவன் விட்டசா பத்தாலே தாணுவே நீரறியத் தாம்பிறந்தார் சாணாராய் ஏழுலோ கமதிலும் ஏற்றமுள்ள வித்தெடுத்துக் கீழுலகில் பெற்றேன் கீர்த்தியுள்ள சாணாராய்ப் பெற்றுவைத்து ஸ்ரீரங்கப் பூமியிலே போயிருந்தேன் மற்றுஞ்சில நாட்கழித்து வாழுந் தியதிதனில் அனந்த புரம்நோக்கி யானேகும் வேளையிலே புனந்தனிலே நின்று புலம்பலுற்றார் தேவர்களும்  தெய்வலோ கத்திலுள்ள தேவதே வாதிகளும் வைகுண்ட லோகமதில் வாழுகின்ற தர்மிகளும் சிவலோகம் வாழும் சிட்டர்முதல் வானவரும் தவமான வேதாவின் தன்னுகத்தில் வாழ்பவரும் வடகயி லாசமதில் வந்திருந்தார் வாட்டமதாய் நடக்கும் வழியில் நானவரைக் கண்டேதான் தாது கரமணிந்த தானவரே தர்மிகளே ஏதுகா ணீங்கள் இங்குவரக் காரணமேன் என்றவரோ டேகேட்டேன் ஈசுரரே கேட்டருளும் அன்றவர்க ளென்னோடே அருளினதை நீர்கேளும் கலியன் பிறந்ததினால் கட்டழிந்து மானுபங்கள் சலிவாகி எங்களுட தானதவங் குன்றினதால் இனியிருந்தா லென்னபலன் என்றுமிக எண்ணமுற்றுக் கனிந்து வுமைத்தேடிக் காணவந்தோ மென்றுரைத்தார் அப்போது வுங்களுக்கு ஆகவேண் டியவளம் இப்போதே சொல்லும் என்றேகேட்டேன் தேவருடன்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(185,'சிவன் திருமால் ஐக்கியம்','சொல்லுகிறா ரந்தத் தேவர்கள்தா மீசுரரே நல்லதுதா னிந்த நாட்டில்கலி வந்ததினால் கலிக்குமுன் னுள்ளதுவும் கலியில்மிகக் கண்டதுவும் அலிக்கிய மானதினால் அக்கலிமா ளும்போது சிவசத்தி தான்முதலாய்ச் சீவனுள்ள செந்துகளும் தவமுனிவ ராகிடினும் தரணி புற்பூண்டுவரை மட்டை மருந்திலையும் மலைகடலும் வாசுகியும் திட்டமுடன் நாரணர்க்குச் சிந்தையொத்த பேர்களெல்லாம் மேனி யழுக்கறுத்து மேன்மூ டிருளறுத்து யோனிப் பிறப்பும் உற்றலிங் கப்பிறப்பும் அவரவர்க் குள்ள அதிகப் பிறப்போடும் எவரெவர்க்கும் பூமியொன்றில் இனிப்பிறக்க வேணுமல்லோ ஆகும் பிறப்பும் ஆகுவது மிந்நாளில் சாகும் பிறப்புத் தவறுவது மிந்நாளில் அப்படியே நீசன் அவன்பிறந்தத் தோசமதால் எப்படியும் ரண்டிலொன்று ஆகுவது திட்டமுண்டே ஆனதா லெங்களுட அக்கமதி லிப்போது மானமாய்ச் சான்றோராய் வகிருமென்றா ரீசுரரே கொஞ்சம் பொறுவுமென்று கூறிவந் தேன்முன்னமே இஞ்சொல் பார்க்கும்போது இவ்வுலகி லுள்ளோரைப் பிறவிசெய்ய ஞாயமுண்டு பிள்ளைமொழி சொன்னதினால் திறவி முதற்பொருளே செய்தியென்ன சொல்லுமென்றார் அப்போது ஈசுரரும் அச்சுதரைத் தான்பார்த்து இப்போது மைத்துனரே யானென்ன சொல்வதுதான்  நீர்நினைத்த துபோலே நிச்சயித்துக் கொள்ளுமென்றார் பாரனைத்துங் காக்கும் பரமே சுரருரைத்தார் அப்போது பாரளந்த அய்யாநா ராயணரும் முப்பொருளு மொன்றாய் ஒத்திருந் தேதுசொல்வார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(186,'தேவலோகத்தார் மனுப்பிறப்பு','தேவரையுந் தான்வருத்திச் செப்புவா ரையாவும் பாவலரே நீரும் பார்த்துணர்ந் தோராமல் மாலிங்கே யில்லையென்று மாறாட்டஞ் சொன்னதற்கு சூலின் கருத்திரண்டு தொல்புவியி லேபிறக்கும் உங்களி னமாக ஒருபிள்ளை பெற்றதுண்டு அங்குபோய்ச் சாணாராய் அதில்பிறக்கப் போவுமென்று சொல்லவே தேவரெல்லாம் சிரித்துமனங் கூடி நல்லதுகாண் நாரணரே நாங்கள்பிழைத் தோமெனவே பிறக்கவே போவோம் பிறந்தா லடியார்க்கு இறக்கவிதி யாகுமல்லோ இழிகலிய னேதுவினால் இறந்தால் பின்னுமந்த இனத்திலடி யார்பிறக்க வரந்தாரு மென்று வணங்கிநின்றா ரையாவை அப்போது அய்யா அரிநமோ நாரணரும் செப்பரிய நல்லகுலத் தேவர்களுக் கேதுரைப்பார் மக்களே நீங்கள் வையகத்தி லேபிறந்தால் வக்கலிய னேதுவினால் மாயமுங்க ளைச்சூழ்ந்து  என்பேரு மீசர் ஏற்றஉமை யாள்பேரும் தன்பேருஞ் சொல்லாதே என்று தடுத்தடிப்பான் ஆனதால் சாவுவரும் ஆனாலு முங்களுயிர் மானமது மாறாமல் மாறியவ் வினத்திலுறும் இப்படியே வுங்களைநான் இரட்சித்துக் காக்குமட்டும் எப்படியு முங்களுயிர் இதில்விட் டகலாதென்றார் நல்லதுகா ணென்று நாட்டமுற்றுத் தேவரெல்லாம் வல்ல வகையான வாய்த்தசா ணாரினத்தில் தேவ ருயிர்பார்த்துச் சென்றுதித்தார் தேவரெல்லாம் மூவருரை மாறாமல் மோகமுள்ள தேவரெல்லாம்');");


        //9th Day

        db.execSQL("INSERT INTO akilathirattu2 VALUES(187,'சம்பூர்ணத்தேவன் - பரதேவதை தவசு','போகும் பொழுதில் பொன்னுலகத் தேவர்தன்னில் தாவும்பெரிய வொரு சம்பூரணத் தேவன் பரதே வதையான பார்மறலி தன்னுகத்தில் உரமான தேவியவள் உடையமன்ன னைநீக்கித் தெய்வச்சம் பூரணனும் சேர்ந்தவளோ டேநடப்பாய் மாயவளை மாய்கையினால் மாறியவன் பேசினனே நான்பிறக்கப் போணுமென்றால் நன்னுதலை யென்னோடு தான்பிறக்கச் சொல்லித் தாரம்போ லாக்குவீரால் நான்பிறக்கப் போவேன் நாரணரே யல்லாது தான்பிறக்கப் போவதற்குச் சங்கடங்க ளுண்டுமையா          என்றுரைக்கத் தேவன் எடுத்துரைப்பா ரெம்பெருமாள் நன்றுநன்று தேவாநீ நாணமென்ன பேசுகிறாய் உன்பிறப்பு உயர்பிறப்பு ஓவியத்தின் தன்பிறப்பு பின்பிறப்பு ஏமன் பூமிப் பிறப்பல்லவோ அப்பிறவிக் கிப்பிறவி அடுக்குமோ தேவாநீ இப்பிறவி ஞாயம் ஏனுரைத்தாய் மாதேவா ஆனையொடு பூனை அணைந்துநலஞ் செய்திடுமோ பூனையொடு ஆனை புல்குமோ மாதேவா ஆனை மதமா யடர்ந்து மிகத்தேவா பூனை யொடுவந்து புல்குமோ - பூனை நாயோடு புல்குமோ நல்லறிவில் லாத்தேவா ஈயோடு சேர்மோ இசல் ஏனடா தேவாநீ இந்தமுறை சொன்னதென்ன வீணடா இவ்வாசை விட்டுவிடு நீதேவா கீரிக்குப் பாம்பு கிளைவருமோ வையகத்தில் ஓரிக்குச் சிங்கம் ஒக்குமோ மாதேவா இப்படியே மாயன் எடுத்துரைக்கத் தேவாதி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(188,'சம்பூர்ணத்தேவன் - பரதேவதை தவசு','எப்படியும் புத்தி இசையாம லேயுரைப்பான் காவலரே மாமோகம் கண்டஇட மன்றல்லவோ ஆவலது கொண்டஇடம் அச்சுதரே சம்மதங்காண்  எந்தவித மாகிடினும் எனக்கந்தப் பெண்கொடியைத் தொந்தமா யென்றனக்குத் தொலையாத ஆசையதும் தந்தருளி வைத்தால் தர்மமுண் டுங்களுக்கு என்றன் பிரானே இனிமாற்றிச் சொல்லவேண்டாம் என்றுரைக்கத் தேவன் எடுத்துரைப்பா ரெம்பெருமாள் ஒன்றுநீ கேட்டிலையே உன்மனது ஒத்திலையே கீழுள்ள பெண்ணை மேலாக்க வேணுமென்றால் வேழமொத்த தேவா மிகுத்ததவஞ் செய்திடுநீ மேலாக வேணுமென்று மெல்லியரும் நற்றவசு காலால் கனலெழுப்பிக் கடுந்தவசு செய்திடச்சொல் தவசு இருபேரும் தாற்பரிய மாகநின்று சிவசுவா சம்பெருக்கிச் சிறந்ததவஞ் செய்திடச்சொல் நின்ற தவத்தில் நிலையாய் நினைத்ததெல்லாம் அன்றுங் களுக்கு அருளுவே னானுமென்று சொல்லிடவே தேவன் சிரித்து மனமகிழ்ந்து நல்லதுதா னென்று நாட்டமுற்றுத் தேவனுந்தான் பரதே வதையான பைங்கிளியைத் தான்கூட்டி விரைவா கத்தேவன் விறுமா பதஞ்சேவித்து நின்றான் தவசு நெடியோ னுறுதியென்று நன்றான நன்னுதலும் நல்லசம் பூரணனும்  நினைத்துத் தவசு நிற்கநிலை தேடிநின்றார் அனைத்துயி ருங்காக்கும் அய்யாநா ராயணரும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(189,'எமலோகத்தார் மனுப்பிறப்பு','ஏம னுலகமதில் இருக்குந் தபோதனரைத் தாமனந்தப் பேர்கள் சர்வது மேயழைத்து வாருங்கோ பிள்ளாய் வாய்த்த தபோதனரே ஏதுங்கள் ஞாயம் என்னோடே சொல்லுமென்றார் உங்கள் குடும்பம் உடையோன் பதம்வணங்கி எங்களுட மோட்சமதில் ஏகி இருப்பதினால் ஆனதா லவர்கள் அனுக்கிரகந் தன்னாலே மானமுட னேமனுட வையகத்தில் வாழ்ந்திருந்தீர் இன்ன முன்வழிகள் என்மகவா யங்கிருக்க வன்னமுட னவ்வழிதான் வாய்த்தசா ணாரினத்தில் போய்ப்பிறந்து நன்றாய்ப் புவிமீதி லேவளர்ந்து மேற்பிறவி வந்திடினும் மேவுவ தவ்வழியில் இப்படியே பிறந்து என்று மிடறுசெய்து எப்படியு மென்குடும்பத் தாலே யிருளகன்று உன்பிறப்போர் தம்மால் உதவிபெற்று வாழுமென்று எம்பெருமாள் சொல்லி இனத்தில் பிறவிசெய்தார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(190,'சொர்க்க லோகத்தார் மனுப்பிறப்பு','சொர்க்கலோ கத்தாரைச் சுறுக்காய் வரவழைத்து மிக்கவுங்கள் செய்தியென்ன விடுத்துரையு மேவலரே      நான்பிறவிக் கெல்லாம் நம்பிகூ டப்பிறந்து என்பிறகே வந்து எனக்கேவல் செய்ததினால் இன்னம் பிறவி இனத்திலுயி ரென்மகவாய் வன்னமுள்ள சான்றோர் வழியி லிருப்பதினால் காலக் கலிதான் கட்டழித் தென்மகவு மேலுக மாள விடைநிச்சித் திருப்பதினால் நீங்களும்போ யங்கே நிங்ஙளுட தன்வழியில் மங்களமாய்த் தோன்றி வாழுங்கோ என்மகவாய் வாழுகின்ற நாளையிலே வருவே னானுங்களிடம் நாளும்பல ஊழியங்கள் நமக்குமிகச் செய்திருங்கோ ஏவல்கண் டுங்களைநாம் இரட்சித்து ஆண்டுகொள்வோம் போவெனவே சொல்லிப் புகன்றா ரவர்களுக்கு ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(191,'பிரம்மலோகத்தார் மனுப்பிறப்பு','பிரமலோ கத்திலுள்ள பிலத்த இருஷிகளை வரவழைத்து அய்யா நாராயணர் வகிர்வார் வாருங்கோ பிள்ளாய் வாய்த்த ரிஷிமாரே ஏதுங்கள் ஞாயம் என்னோ டுரையுமென்றார் அப்போது நல்ல அந்தரிஷி யேதுரைப்பார் இப்போது அய்யாவே எங்களைப்பூ லோகமதில் படைக்கவே ணுமெனவே பகர்ந்தமொழி மாறாமல் நடக்கக் கருமமிது நாரணரே பொய்யாது  முன்னமே யெங்கள் முறைவழியி லோருயிரை வன்னமுள்ளச் சாணாராய் வகுத்தீரே யும்மகவாய் அல்லாம லெங்கள்வழி ஆனரிஷி தங்களிலே பொல்லாத குற்றம் பிரமனுக்குச் செய்ததினால்  அன்பத்தினா லொன்றிரிஷி அவனிதனில் போகவென்று இன்புற்ற நாரணரே ஈந்தீரவர் கேட்டவரம் ஆனதா லவர்கள் அவனிதனி லேதிரிய ஏனையா எங்களைநீர் இப்போ பிறவிசெய்தால் இனம்பிரிந்து நாங்கள் இருப்போமே பூமிதனில் கனம்பொருந்தும் நாரணரே கட்டுரைக்க வேணுமென்றார் நல்லதுதா னென்று நாரா யணருரைப்பார் வல்ல ரிஷிமாரே வகையாகக் கேட்டீரே அதற்கு விபரம் அருளுவேன் கேளுமென்று மதுக்குகந்த மன்னன் வழுத்துவா ரன்போரே சென்ற ரிஷியெல்லாம் செடமெடுத்துப் பூமிதனில் மண்டலங்கள் தோறும் வாழுவார் கண்டீரே என்ன விதமாய் இருப்பாரென் றேயினமாய் துன்னயமாய் நீங்கள் துணிந்துநன்றாய்க் கேட்டிங்கோ மான்தோ லிலேயிருப்பான் வானமதைத் தான்பார்த்துத் தீன்சோ றருந்தாமல் செலங்குடித்து நாடோறும்  கற்பமுண்டோ மென்று கலியில்மிகப் பட்டுழன்று அற்பமுடன் கொஞ்சி அவன்சிலநாள் தானிருந்து பிரம்ம வைகுண்டம் பிறப்பெடுத்து நான்தானும் வரம்வைத் தவனை வதைத்துப்பின் னுன்னினத்தில் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(192,'பிரம்மலோகத்தார் மனுப்பிறப்பு','படைத்துத் தருவேன் பார்த்துக்கோ லெக்கெனவே அடர்த்தியா யைம்பத்து ஐந்து இருஷிகளும் ஆளுக் கொருவிதமாய் அருந்துவ துமிருப்பும் நாளு முறையாய் நடத்துவதும் நீகேளு ஒக்க விபரமதாய் உரைக்கநே ரம்பெருகும் மிக்கத் திரட்டாய் விடுகிறேன் நீகேளு நல்ல மிளகுதின்று நவகண்டி தான்பூண்டு கொல்ல மிளகுதின்று குப்பைமே லேபுனைந்து வெள்ளித் தடுக்கில் வீற்றிருப்பா ரேசிலர்கள் ஆனைத்தோ லிட்டு அருந்தாமல் நாடோறும் கானகத்தில் வாழ்ந்து கண்மூ டாரேசிலர்கள் மரத்தைமிகக் காலிலிட்டு வார்சிலந்திக் கோர்வையிட்டுச் சரத்தையுள்ளே கொண்டு தானிருப்பா ரேசிலர்கள் மீட்டைக் கொடியும் மிளகுவெற்றி லைக்கொடியும் இட்டமுடன் நிஷ்டை இருப்பார் சிலபேர்கள் உப்பில்லா தன்னம் ஒருபோது தான்குடித்து  அப்பிலிட்டுத் தின்று அக்கினியி லேகாய்ந்து இருப்பார் சிலபேர் இன்னமும்நன் றாய்க்கேளு பொருப்பி லிருப்பார் பூமியில் வரோமென்பார் கள்ளிப்பா லுண்டு கவிழ்ந்திருப்பா ரேசிலபேர் கொள்ளித் தழலும் கொள்ளைகொண்டக் கஞ்சாவும் உண்டோங்காண் லோகமதை விழுங்குவோ மென்பார்சிலர் புல்லை யருந்திப் புலித்தோலின் மேலிருந்து தில்லைப்பா லுண்டு திரிவோமென் பார்சிலபேர் புகையிலைச் சாறு புகட்டுவோ மென்பார்சிலர் தகையில்லாக் கற்பம் தானுண்டோ மென்பார்சிலர் பூவில் படுப்போம் புகட்டுவோ மாவின்பால் காவி லுறைந்து கலைதரி யாதிருப்பார் உடையுடா தேகழுத்தில் உத்திராட்ச மேபுனைந்து நடையொற்றைக் காலால் நடப்பார் சிலபேர்கள் இப்படியே நன்றாய் இவரிவர்க் கோர்விதமாய்  அப்படியே தேசமைம்பத் தாறதி லுந்திரிவார் சோழக் குருநாடு ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(193,'பிரம்மலோகத்தார் மனுப்பிறப்பு','தேசமது நன்னாடு ஆளவை குண்டர் அவர்வளரும் நன்னாடு இந்நாட்டில் வாழும் இராச ருடவளமை சொன்னால் தொலையாது சொல்ல எளிதல்லவே  இப்புவியில் வாழும் என்றன்வை குண்டரிடம் எப்புவியில் வாழும் இருஷிகன் னாசியெல்லாம் வரம்வைக்கச் சொல்லி வதைக்கவந்த வைகுண்டர்காண் பரம்பெரிய வைகுண்டப் பதியாள வந்தவர்காண் சன்னாசி யெல்லாம் தலைவீதம் வாழுகின்ற மின்னான சீமை விரிக்கக்கேள் நீங்களெல்லாம் பாண்டிய னான பரிகொங்கை நன்னாட்டில் ஆண்டிருப்பான் சன்னாசி அவனொருவன் கேட்டிருநீ வருஷ மொருநேரம் மாறிப் பிறந்தேனென்று புருஷனென வந்தெடுத்தால் பிள்ளையுண் டென்றுசொல்லி இருப்பான் சிலநாள் இவனொரு சன்னாசி சிங்கள நன்னாட்டில் சீகண்டன் ராச்சியத்தில் புங்கம்பா லுண்டு பூவையரைப் பாரோமென்று ஆடை யுடுக்காமல் அவனிருப் பானொருவன் கோரக்க நாடு குருக்கேத்திரன் ராச்சியத்தில் சூரக்கோல் கைப்பிடித்துச் சூலா யுதமேந்தி தன்மதத்தால் பேசித் தானிருப்பா னேசிலநாள் கன்மத்தால் சாவான் கடியமூன் றாம்பேர்தான் இப்படியே யைம்பத் தாறூ ரிவைகளுக்கும் அப்படியே தானிருக்கும் ஆனசன் னாசிகட்கு  ஒண்ணொண் ணடையாளம் உரைக்கிறேன் சீக்கிரமாய் துண்ணெனவே நீங்கள் துணிந்துமிகக் கேளுமென்று நாலாஞ் சன்னாசி நயன மிமையாதிருப்பான் காலைக் கைகொண்டு கட்டிறுக்கி யேயிருப்பான் அஞ்சாஞ் சன்னாசி அன்னங் குடியாமல் பிஞ்சிரு மிச்சியிலை பிசைந்துதின் றேயிருப்பான் ஆறாஞ் சன்னாசி ஆனகஞ் சாகுடித்து நீறாக்கித் தேகமதை நிஷ்டைபோல் தானிருப்பான் ஏழாம் சன்னாசி இடுப்பிலொரு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(194,'பிரம்மலோகத்தார் மனுப்பிறப்பு','சீலைகட்டிப் பாழாகப் பட்சி பறவைதின்றே யிருப்பான் எட்டாஞ் சன்னாசி இருந்துமிகக் கண்மூடி கட்டாய்ச் சுவாசமதைக் கவர்ந்துகவர்ந் தேயிருப்பான் ஒன்பதாஞ் சன்னாசி உமிழ்நீ ரிறக்காமல் இன்ப விறுவிறுத்து ஈரவம்போல் கண்வெளுத்துத் தூங்கினாற் போலே சூழச்சூழ விழித்துப் பாங்குகள் தேடிப் பதிந்திருப்பான் கண்டீரே பத்தாஞ் சன்னாசி பதிவாக நாள்தோறும் சிற்றா மணக்கிலையில் துயிலுவான் கண்டீரே பதினொராஞ் சன்னாசி பவளமணிக் கோர்வையிட்டுத் துதியா யொருமூலம் சொல்லித்தினம் சேவித்து   உரிய உளுந்தும் ஓயாமல் கஞ்சாவும் சதமென்று நம்பி தானிருப்பான் கண்டீரே பன்னிரண்டாஞ் சன்னாசி பாரக்கல் கோர்வையிட்டு உந்திக்(கு) இலுப்பையிலை உழக்குச்சா றுகுடித்து இருப்பான் பதிமூன்றான் இவனினத்தைக் கேட்டிருநீ பருப்பா னவலும் பழமுந் தினமருந்தி கோவை யிலையும் கொடுப்பையிலை யும்புசித்துச் சேவைபண்ணி நித்தம் செய்திருப்பான் கண்டாயே பதினாலாஞ் சன்னாசி பண்ணு முறைகேளு துதியா யொருமூலம் சொல்லித் தினஞ்சேவித்துக் கையிறை கொண்டேதான் கட்டிக்கொண் டேயிருப்பான் பதினைந்தாஞ் சன்னாசி பார்க்கெந்த மாலையிட்டு மதியகத்தே வுண்டு மலர்ந்துதுயின் றேகிடப்பான் பதினாறாஞ் சன்னாசி பண்ணு முறைகேளு ஆடை யுடுக்க அறிவுசற்று மில்லாமல் கோடை குடித்துக் குலைவான் பதினாறான் கள்ளிதனைத் தின்று காய்க ளிலையருந்தி கொள்ளித் தழல்காய்வான் கொடிய பதினேழான் நல்லத்திக்  காயருந்தி நாமமணி யும்பூண்டு கொல்லத்தி போலே குலைந்துகுலைந் தேதிரிவான்  இவன்பதி னெட்டான் என்றே யினமறிநீ எவனு மறிவான்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(195,'பிரம்மலோகத்தார் மனுப்பிறப்பு','ஈரெட்டு மூன்றானை மான்தோலின் மேலிருப்பான் மற்றிருபான் மீசையுள்ளான் தான்தெரியு மற்றிருபத் தொன்றா னினங்கேளு நாகத்தின் முள்ளை நல்லதலை மேலணிந்து கூகத்தைச் சுற்றிக் கும்பிட்டுத் தான்திரிவான் இருபத்தி ரண்டான் இனங்கேளு நன்றான மதுவைத்து நித்தம் வணங்குவான் மந்திரத்தை இருபத்தி மூன்றான் இருப்பான் மயிர்வளர்த்துச் சருவைத்துப் பால்பழத்தில் தந்திரத்தை யோதிடுவான் இருபத்தி னாலான் எங்கு மிகத்திரிந்து உருவேற்றி நித்தம் உடல்வாட லாயிருப்பான் இருபத்தியைந் தாமிருஷி எருக்கலம் பாலருந்தி பருவமாய் ஓவியஞ்செய் ததிலே படுத்திருப்பான் கள்ளுக் குடித்துக் கறியுப்புக் கூட்டாமல் புள்ளித்தோல் மேலிருப்பான் புகழிருபத் தாறானும் நீரைத் தியானமிட்டு நித்திரைக்குத் தானோதி பாதை வழியேகான் பத்துரண் டேழானும் கோண முடிமுடித்து குறுத்தடியுங் கைப்பிடித்து நாணமில்லா தேதிரிவான் நல்லிருபத் தெட்டானும்  சூலம் பிடித்துச் சுழியெழுத்தை மேலூன்றி கோலமிட் டேதிரிவான் கூறிருபத் தொன்பானும் முப்பதாஞ் சன்னாசி முச்சந்தி தான்பார்த்து அப்புச் சிரங்குபற்றி அவன்கிடப்பான் சந்தியிலே ஓர்முப்பத் தொன்றான் ஊமையென வேதிரிந்து கூர்மையுள்ள காது கொஞ்சமுங்கேளாத் திரிவான் முப்பத்தி ரண்டான் உயர்ந்ததிடில் தான்பார்த்து சர்ப்பத்தின் நஞ்சு தானுண்டோ மென்றிருப்பான் நன்றா யறிந்திடுநீ நல்லமுப்பான் மூன்றானையும் கண்டா லறிந்திடலாம் கண்ணில் வெளுப்புமுண்டு தொண்டையில் சடம்புகட்டித் தொல்புவியி லேதிரிவான் மண்டை வகுப்புமுண்டு வாய்த்தமுப்பத் தினாலான் முப்பத் தைந்தானும் முயலகன்போ லுண்டாகி எய்ப்பிளைப்பார் போலே ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(196,'பிரம்மலோகத்தார் மனுப்பிறப்பு','இவன்திரிவான் கண்டாயே செப்பமுள்ள முப்பத்(து) ஆறான் செய்தியைக்கேள் புஸ்பமீதிற் படுத்து பூவையர்கள் தாலாட்ட அற்பமுட னாடை அணியாமல் தானிருப்பான் முப்பத்தி யேழாஞ் சன்னாசி யானவனும் அகத்தி யிலையருந்தி ஆனைத்தோல் மேலிருந்து மகத்துவமாய்ப் பேசி மாள்வான் முப்பத்தேழான்  கொல்ல மிளகு குறுணியொரு நாளருந்தி பல்லைமினுக் காதிருப்பான் பார்முப்பத் தெட்டானும் சோறு குழையவைத்துச் செவ்வலரிப் பூவிலிட்டு ஆறுமுன் பூவோடு அருந்துவான் முப்பத்தொன்பான் நாக்கிலோ ராணிதனை நல்லவிந்தை யாய்க்கொருத்து மூக்கிலே கட்டி முனங்குவான் நாற்பதானும் சிறுபயறை மாவாக்கித் தேங்கா யதனிலிட்டு முறுக்கா யதையருந்தி முழுங்குவான் நாற்பத்தொன்றான் அறைக்கீரை வித்தை அருந்தித் தினந்தோறும் சுரைக்கூடிட் டேதிரிவான் சுத்தநாற் பத்திரண்டான் நாற்பத்தி மூன்றான் நத்தைச்சூரி விதையை தாற்பத்தி யமெனவே தானிருப்பான் கண்டாயே விராலி யிலையும் விளாங்காய் வெறுந்தோடும் நிராதனமாய் வைத்து நித்த மருந்துவன்காண் நாற்பத்தி நாலான் என்றே யிவனையறி நாற்பத்தைந் தானுடைய நல்விபரம் நீகேளு பூப்பறித்துத் தின்று பூனைமொழி பேசிடுவான் பேய்க்குமட்டிக் காயைப் பிசைந்து விரைதானெடுத்து  நாய்க்குணம்போல் தின்பான் நாற்பத்தி ஆறானும் எருமை யுடமோரும் எள்ளெண்ணெ யுங்குடித்து  நருநரெனப் பேசிடுவான் நாற்பத்தி யேழானும்  மருளான பெண்ணை மனதில்வை யோமெனவே இருளான போது இந்தநினை வாயிருப்பான் காமத்தைத் தன்னால் கழியவிட் டேவாடி நாமத்தைப் பேசான் நாற்பத்தி யெட்டானும் புகையிலையைத்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(197,'பிரம்மலோகத்தார் மனுப்பிறப்பு','தீயில்வைத்துப் புகையு மிகக்குடித்து நகம்வளர்த் தேயிருப்பான் நாற்பத்தொன் பான்தானும் அன்பதாஞ் சன்னாசி அரசியிலை மேலிருப்பான் மண்புரளத் தான்கிடந்து மாஜலங் குடித்திருப்பான் அன்பத் தொன்றானும் அலைவாய்க் கரைதோறும் புண்ணியத் தீர்த்தமெனப் போயாடி யேதிரிவான் நீல மவரி நித்தஞ்சா றேகுடித்து ஆலம் பலகையிலே ஐம்பத்திரண் டானிருப்பான் சுக்குப் பொடியைத் தினந் தினமெயருந்தி அக்குமறு கணிவான் ஐம்பத்தி மூன்றானும் ஐம்பத்தி நாலான் அசுரக்கா வடிவைத்துக் கெம்பித் திரிவான் கீரித்தோல் காலிலிட்டு விளியிட் டேயாடி வெற்றிகொண் டேதிரிவான் வழிவிட்டு விலகி வழிநடப்பா னைம்பத்தஞ்சான் இப்படியே யைம்பத்(து) அஞ்சு இருஷிகளும்  அப்படியே அவரவர்கள் அற்புதங்கள் வேறுசெய்து இருப்பா ரவர்கள் இராச்சியத்துக் கொன்றாக பருப்பா ரவர்கள் பழுதுகை வாய்த்துதென்று இருக்குமந்த நாளையிலே யான்வருவேன் தெச்சணத்தில் ஒதுக்கிச் சன்னாசிகளை ஒன்றொன்றாய்க் கொண்டுவந்து வைக்கச்செய் வேன்வரத்தை மனுவறி யச்சிலரைச் செயிப்பேன் பலபேரைச் செகமெல்லா மறிய வதைத்து ஒவ்வொன்றாய் மனுவில் வரத்தாக்கி நிரைத்துங்கள் தன்னோடு நிலைநிறுத்தி யேதருவேன் ஆனதால் நீங்கள் அவனியி லென்மகவாய் ஈனமில்லா துங்கள் இனத்தில் பிறந்திருங்கோ வாழுங்கோ கலியன் மாய்கையால் சாவுவந்தால் பாழுபோ காதேவுயிர் பதியுமுங்கள் தம்வழியில் நான்வந் துங்களையும் நாட்ட முடனெடுத்து மானொத்த தர்ம வையகத்தில் வாழவைப்பேன் என்று பிரம இராச்சியத் தோர்களையும் அன்று பிறவி அமைத்து அனுப்பினர்காண் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(198,'வைகுண்டலோகத்தார் மனுப்பிறப்பு','அந்தப் பிறவி அவர்செய் தனுப்பிவைத்துச் சொந்தப்பிறவி சிவவைகுண் டத்தோரை வாருங்கோவென்று வரவழைத் தெம்பெருமாள்  ஏதுங்கள் ஞாயம் இயம்புமென்றார் தர்மிகளை அப்போது தர்மியெல்லாம் அரியோ னடிவணங்கி எப்போது மெங்களைநீர் இரட்சித்துக் கொண்டவரே ஆண்டவை குண்டம் ஆண்டிருந்தோ மித்தனைநாள் தாண்டவரே யினியுமது தயவி னருளாலே என்னபடி நிச்சித்து இருக்குதோ அவ்வழியில் வன்னத் திருமாலே வகுத்தா லதுமனதாம் என்றுதர்மி யெல்லாம் இசைய ஆதிநாரணரும் நன்றுபிள்ளா யுங்களுட நல்வளமை சொல்லுகிறோம் இல்லா தெளியோர்க்கு எனைநினைந்து தர்மமிட்டுப் பல்லுயிர்க் குன்னயிராய்ப் பார்த்தீ ரொருப்போலே நீத நிலையை நிறுத்தி யெனைநினைந்து சீதமாய்ப் பூமி செலுத்தியர சாண்டிருந்து வந்தீர்கள் கூட்டோடு வைகுண்ட மானதிலே சேர்ந்தீர்க ளாகிடினும் செய்தநன்றி தான்பார்த்து உங்களுக்கின் னாள்வரையும் உதவிதர வில்லையல்லோ தங்களுக்கு நன்மை தரவேணு மானாக்கால் இன்னமொரு பிறவி என்மகவி லேபிறவும் வன்னப் பிறவியென் மகவழியில் தான்பிறந்தால் ஏழு யுகக்கணக்கும் யான்கேட் டுங்களுக்கு  மாளுவ தில்லாமல் மறுபிறப் புமறுத்து நம்முடைய சொத்தும் நாடியுங் களுக்கீந்து மும்மடங்காய்த் தர்மம் ஓங்கிவளர்ந் தேயிருப்பீர் என்றுதர்மி தங்களுக்கு எம்பெருமா ளீதுரைக்க');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(199,'வைகுண்டலோகத்தார் மனுப்பிறப்பு','அன்றந்தத் தர்மிகளில் ஐபேர்க ளேதுரைப்பார் ஆயரே எங்களுட ஆதிநா ராயணரே மாயரே எங்களைநீர் மறந்தெங்கே போனீர்காண் காணாம லும்மையெங்கள் கண்கள் மிகத்தவித்து வாணா ளயர்ந்து மறுகிநொந்து வாடினோமே என்றுதர்ம பாண்டவர்கள் இப்படியே சொல்லிடவே நன்றுநன் றென்று நாரா யணர்மகிழ்ந்து முன்னமே நீங்கள் உகத்துக் குகங்கூட என்னைவிட் டகலாது இருந்தீர்க ளென்னுயிர்போல் ஆனதா லிப்பிறவி அய்யாநா ராயணராய் மானமாய்த் தர்மம் வளர்ந்தோங்க எந்நாளும் நிச்சித்து ஒன்றாய் நிலைநிறுத்தித் தற்சொரூபம் மெச்சித்து நானினித்தான் மேல்பிறக்கப் போவதினால் என்மகவாய் நீங்கள் ஏழி லொருவனுமாய்க் கண்மணிபோல் நீங்கள் கலியுகத்தில் போய்ப்பிறங்கோ என்றேதான் தர்மிகட்கு எம்பெருமா ளீதுரைக்க அன்றேதான் தர்மி எல்லோரு மேதுரைப்பார் தானதவஞ் செய்தல்லவோ தர்மிகள் தானாகி மான வைகுண்ட வாசலிலே வந்திருந்தோம் மானுவங்க ளில்லா மாளுங் கலியுகத்தில் பேய்நீச வையகத்தில் பிறக்கப்போ வென்றீரே நாங்கள் முன்செய்த ஞாயநடுப் போய்விடுமே ஏங்கக் கலியனுட இருளினா லையாவே நாயநடுக் கேட்டு நடுக்கம் மிகப்பிடித்து தேயமதில் நாங்கள் செய்தமுறை தப்பிடுமே ஆனதால் நீரும் அன்றுநடுக் கேட்கையிலே மானமில்லை யானால் வழக்கென்ன சொல்லிடுவோம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(200,'வைகுண்டலோகத்தார் மனுப்பிறப்பு','என்றுரைக்கத் தர்மி ஏதுரைப்பா ரெம்பெருமாள் நன்றுநன்று பிள்ளைகளே நானதற்குச் சொல்வதுகேள் என்மக்க ளேழும் இயல்கலியில் பட்டுழன்று வன்மக் கலியதனால் மாறிமிகச் செய்ததெல்லாம் நான்பொறுத் துங்களுக்காய் நானே தவசுபண்ணி வான்சிவனுக் கேற்க வாய்த்த தவமிருந்து மக்களேழு பேர்கள் வம்மிசங்க ளுள்ளதெல்லாம் ஒக்கவொன்று போலே உகந்தெடுத் துண்மையுடன் நாடாள வைப்பேன் நல்லமக்க ளேழ்வரையும்  தாடாண்மை யான சத்தியுமை தன்னாணை நீங்களென்ன குற்றம் நிலையில்லாச் செய்தாலும் தாங்கிப் பொறுப்பேன் தருவே னானல்லபுத்தி உங்கள்கர்ம மெல்லாம் ஒக்கத் தொலைப்பதற்கு மங்களமாய்ச் சிவனை வருந்தித் தவசுபண்ணி நானுங்க ளாலே நல்ல தவசிருந்து மானுபங்கள் கெட்ட மாகலிய னுகத்தில் தாயும் தகப்பனையும் தான்பழித்த துற்கலியில் பேயுங் கலிநீசப் பிறப்பைச் சோதித்தறுத்து தாடாண்மை தர்மம் தழைக்கவைத் துங்களையும் நாடாள வைப்பேன் நம்மாணை தப்பாது என்றுரைக்க அய்யா எல்லோருஞ் சம்மதித்து அன்று பிறவி அமையுமென்றா ரப்படியே நன்மையுள்ள தர்மியெல்லாம் நல்லசான் றோர்குலத்தில் மேன்மையோ ரெல்லாம் மேதினியி லேபிறந்தார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(201,'சிவலோகத்தார் மனுப்பிறப்பு','அந்த யுகப்பிறப்பு ஆனபின் எம்பெருமாள் நந்த சிவனுகத்தோர் நாடும்வா னோர்களையும் வாருங்கோ பிள்ளாய் வானோரே என்றுசொல்லி ஏதுங்கள் ஞாயம் இயம்புமென்றா ரெம்பெருமாள் அப்போது வானோர் எல்லோரு மேமகிழ்ந்து செப்புகிறா ரந்தச் சிவனாதி தன்னோடு மாயவரே தூயவரே மற்றொப்பில் லாதவரே ஆயரே உம்முடைய அறிவின் படியாலே அமையு மமைத்தால் அதுமனதா மெங்களுக்கு சமயம் வழுவாத சாதித் திருமாலே என்றேதான் வானோர் இதுவுரைக்க அய்யாவும் நன்றென வேமனது நன்றா யகமகிழ்ந்து காலக் கலிதான் கலிமூழ்கி வையகமும் மேல யுகம்வரையும் மூழ்கியது கண்டீரே ஆனதா லீரேழு அவனி இருள்மூடி மான மழிந்து வரம்பழிந்து மானிடரும் ஆதி முதல்சாதி அடங்கல்வரை தேவரெல்லாம் நீதி நிலைமாறி நின்றதுவுங் கண்டீரே ஆனதா லிந்த அன்னீத இருள்தனையும் ஏனமென்ன பார்த்தேன் இதையறுக்க வேணுமென்று            பார்க்கும்போ திந்தப் படூரநீ சக்கலியை ஆர்க்கும் செயிக்க அடங்காது கண்டீரே ஆனதால் நாமள் எல்லோரு மின்னமொரு மானமாய்ப் பிறந்தால் மாளுங்கலி யல்லாது கலிக்குள்ளகப் பட்டசடம் கலியழிக்கக் கூடாது பெலிக்குக் கலியையிட பிறந்தல்லா லேலாது ஆனதால் நாங்கள் அம்புவி யில்வருவோம் வானவரே யுங்களுட வளப்பமென்ன சொல்லுமென்றார் அப்போது வானோர் எல்லோரு மேபயந்து செப்புவ தேதோ திருமாலே நாங்களினி சிவன்முதலே திருமால் திருவுக் கிதுவானால் எவனையாப் பூமியிலே இனிப்பிறக்கோ மென்பதுதான் ஆனதால் பின்னும் அடியார்க கொருவிதனம் ஈனமே கண்டு இரங்குதையா எங்கள்மனம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(202,'சிவலோகத்தார் மனுப்பிறப்பு','அவ்விதத்தைச் சொல்வோம் அடியார்க் கின்னபடி எவ்விதத்தி லேயும் இசைந்தனுப்பு மெங்கோவே என்றுவா னோர்பணிந்து இயம்புவா ரன்போரே அன்றுமுத லின்றுவரை அடியார்க ளெல்லோரும் சிவனுக் கடிமை செய்திந்தப் பூமியிலே எவனுக்கும் பதறாது இருந்தோமே இவ்வுலகில்  மாய்கையில்லை எங்களுக்கு மக்கள்பெண்டீர் தானுமில்லை சாய்கைத் துயிலுமில்லை தகையுமில்லை எங்களுக்கு இளைப்பில்லை தாகம் இல்லை பசிதாகமதும் வளப்பில்லை தேகம் மறுவில்லை நோவுமில்லை அலந்தா சையில்லை ஆணில்லை பெண்ணுமில்லை கலந்தா சையில்லை காமக் கழிவுமில்லை இத்தனையு மில்லாது ஈசன ருட்செயலால் நித்திய மனப்பூவால் நிமலன் பதம்போற்றி இன்பதுன்ப மற்று இருவினைகள் தானுமற்று வன்பத்து சம்பத்து வாக்குடனே போக்குமற்று மலமற்றுச் சலமற்று மயமும் சயமுமற்றுப் பெலன்பொறி யற்றுப் பேரற்று ஊருமற்று இத்தனையு மற்று இருந்தசிவத் தொண்டரிடம் புத்திகெட்ட மாகலியில் போய்ப்பிறக்கச் சொன்னீரே சொன்னமொழி மாறாமல் சுவாமிவிண் ணப்பமென்றார் என்னவித மாகிடினும் இரட்சிக்க வேணுமையா என்றந்த வானோர் இறைஞ்சித் தொழுதிடவே நன்றென்று கேட்டாதி நாரா யணருரைப்பார் நல்லதுகாண் வானவரே நானினிமேல் செய்பிறவி இல்லறத்தை விட்டுத்தவம் இல்லைகாண் வேறொன்று         ஆனதால் பேடென்றும் அருளியிருளி யென்றும் மானமில்லா ரென்றும் வாராது மேற்பிறவி ஆணுக்கொரு பெண் அன்றூழி காலமெல்லாம் தோணுதலா யொன்றாய்த் தொல்லூழி காலம்வரை வாழ்ந்திருப்பா ரென்றும் மக்கள் கிளையோடும் தாழ்ந்திருப்பார் பேராய்த் தழைத்தோங்க எந்நாளும் பேரு தழைக்க பெருமையாய் வாழ்ந்திருப்பார் ஊரொன் றுகமொன்று உரையொன்றுக் குள்ளாக்கி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(203,'சிவலோகத்தார் மனுப்பிறப்பு','இறவாமல் பெண்ணோடு இருந்துமிக வாழ்வதல்லால் பிறவியற்று வாழ்வார் பெண்ணுடனே யல்லாது ஊர்மறந்து பேர்மறந்து உற்றக் கிளைமறந்து பார்மறந்து தேசப் பவிசு மிகமறந்து ஆடல் மிகமறந்து ஆயிழையைத் தான்மறந்து பாடல் மறந்து பக்கத் துணைமறந்து அன்ன மறந்து அதிக மணமறந்து சொர்ண மறந்து சுகசோ பனமறந்து நலமறந்து தேக நளினமிக மறந்து மலசல மறந்து வளருந்தவப் பேறுமில்லை இத்தனை நன்மைகள் எல்லாமிகக் கொண்டாடிச் சித்திரமாகத் திரும்பிப் பிறப்பற் றவராய் என்பிள்ளை ஏழும் எடுத்தவழி ஓரினமாய் அன்புள்ள பேராய் அரசாள்வோம் கண்டாயே என்று மகாமால் இப்படியே சொல்லிடவே அன்றந்த வானோர் எல்லோரு மேயிரங்கி நன்மை பலதோடும் நன்னுதலாள் தன்னோடும் மேன்மை பலபெறவே மேற்பிறக்கச் செய்வோமென்று சொன்னீரே அய்யா சுகம்பெற்றோம் நாமளென்று பன்னீர்க் குணம்போல் பச்சம்வந்து வானோர்கள் ஆதியே எங்களையும் அதிலமைக்க வேணுமென்று சோதியே என்று தொழுநின்றார் வானோர்கள் நல்லதுதா னெனவே நாட்டமுற் றெம்பெருமாள் வல்லமக்க ளான வாய்த்தசா ணார்குலத்தில் ஏழு மக்களிலும் ஏற்றவா னோர்வழியில் மாளுவந்தா லுமதிலே மாறாப் பிறக்கவென்றும் நாரா யணர்புவியில் நல்லமக்க ளேழ்வரையும் சீராக வந்தேற்றுச் செய்தவினை யும்நீக்கி ஊழூழி காலம் உயிரழியா வண்ணமும்தான் ஆழிக்கரை யான்மகவு அரசாள்வா ரென்றுசொல்லி சொல்லிப் பிறவி செய்தார்காண் சாணாராய் நல்லதென்று வானோர் நாடி யகமகிழ்ந்து  சாணா ராய்வந்து தானுதித்தார் வையகத்தில் வாணா ளறிந்து வானோர் மிகப்பிறந்தார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(204,'பரலோகத்தார் மனுப்பிறப்பு','அந்த யுகப்பிறப்பு ஆனபின் எம்பெருமாள் சொந்தப் பரலோகச் சுத்தமுனி வோர்களையும் எல்லோ ரையுமழைத்து எம்பெருமாள் ஏதுரைப்பார் வல்லோரே உங்கள் வளப்பமென்னச் சொல்லுமென்றார் நீங்க ளெனைமறந்து நீணிலத்தி லில்லையென்று நாங்கள் மிகவறிய நன்றிகெட்டுச் சொன்னீரே ஆனதா லுங்களுக்கு அதிகப்பிழை யாச்சுதல்லோ ஏனமென்ன பார்த்து இயம்பு முனிவோரே அப்போது மாமுனிவர் எல்லோரும் தாம்பயந்து செப்புவ தேதோ சிவனுலகோர் செய்திகண்டால் கற்பனைக்குள் ளல்லால் கடருவமோ நாங்களினி அற்பரல்லோ கற்பனைக்கு அல்லவென்று சொல்வதுகாண் என்றுமுனி சொல்லி இயம்புவார் பின்னுமுனி எல்லா வுரையும் எடுத்துரைத்தீர் வானோர்க்கு நல்லா யறிந்தோம் நாதனேயவ்வகைபோல் படைத்தனுப்பு மும்முடைய பால ருடவழியில் நடத்தை யதிகமுள்ள நாராயணப் பொருளே என்று முனிவோர் இப்படியே சொல்லிடவே  அன்று பெருமாள் அவர்க்கேது சொல்லிடுவார் எல்லோரு மென்மகவாய் இனத்தில் பிறந்திருங்கோ வல்லோரே யுங்களைநாம் வந்தெடுப்போ மஞ்சாதே என்மக்க ளேழும் ஈன்றவழிச் சான்றோரை பொன்மக்க ளான பூமக்கள் தங்களுக்கு என்சொத்து மீந்து என்பேரையுங் கொடுத்துத் தன்சொத்தோ டேயிருந்து தற்சொரூபங் கொண்டிருப்பேன் இறப்பு பிறப்பு இல்லாம லென்மகவைப் பிறப்பிறப் பில்லாமல் பெரும்புவியை யாளவைப்பேன் ஆனதால் தர்மயுக அரசுங்க ளுக்கருளி மானமாய்த் தந்தோம் மாயாண்டி தன்னாணை என்றாணை யாதி நாரா யணர்கூற நன்றாக மாமுனிவர் நல்லதென்று சம்மதித்து அய்யா வுமது அருளின் படியாலே மெய்யா வுமது விந்து வழிக்குலத்தில் படையுமையா சாணாராய்ப் பரமனே யென்றுநின்றார் சடையு மணிந்தமுனி தானுரைக்க அய்யாவும் நல்லதுதான் பிள்ளாய் நாடுவது கருமம் வல்லவரே சான்றோர் வழியிலுங்கள் தம்மினத்தில் பிறக்கப்போ மென்று பெருமுனிவரை யனுப்பிச்  சிறக்கச் சிவனோடு செப்புவா ரெம்பெருமாள்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(205,'திருமால் சிவனிடம் மேல்நடப்புரைத்தல்','ஆதியே நாதி ஆனந்த மைத்துனரே சோதியே சான்றோராய்த் தொல்புவி ஏழிலுள்ளே சங்கமுதல் தெய்வ சத்திபர லோகம்வரை அங்கவரைச் சாணாராய் அவதரித்தார் பூமியிலே என்மக்க ளான இப்பூமி ஏழிலுள்ள பொன்மக்க ளெல்லாம் பொறுமையுள்ள சாணாராய்ப் பிறந்தார் புவிதனிலே புகலுமையா நம்மள்செய்தி கலிக்குள் ளகப்பட்டோர் கடந்துகரை யேறவென்றால் வலிக்கு வலுவான வகையல்லோ வேணுமையா தீட்டுக்குள் சென்றால் திரும்பக் குளியாமல் வீட்டுக்குள் போவாரோ மேதினியோர் நாமறிய இனிமேல் செய்தி இன்னதென் றீசுரரே கனியான மைத்துனரே கட்டுரைக்க வேணுமென்றார் அப்போது ஈசர் அச்சுதரைத் தான்பார்த்துச் செப்புவது நாமறியோம் செய்கரும மங்கேயல்லால் நம்மோடே சொல்ல நாரணரே ஞாயமில்லை தம்மோடே சொன்ன சத்தியத் தின்படியே நடத்தும் நீரென்று நவின்றாரங் கீசுரரும் திடத்திருமா லானோர் செப்புவார் பின்னாலே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(206,'தர்மயுகச் சிறப்பு','இவ்வுகமும் நீசன் இனக்குலமும் நாமழித்துச் செவ்வுகத்த நம்மனுவோர் சிறந்ததர்மத் தாரணியும் புதுயுகம் புதுவிருச்சம் புதுமனு புதுநினைவும் நெதிவுசற்று மில்லாது நின்றிலங் கும்பதியும் புதுவாயு புதுவருணன் புதுமுகில் புதுமதியும் புதுமிருகம் புதுப்பறவை புதுவூர் வனங்கள்முதல் தர்ம யுகத்துக்குத் தானேற்ற வஸ்துவெல்லாம் நன்மையினித் திட்டித்து நடத்திடவும் வேணுமல்லோ இப்படியே ஞாயம் இருப்பதுவு மல்லாமல் முப்படித்தா னுள்ள முறைகேளு மீசுரரே துரியோ தனனைத் தொலைத்துக் கலிவருமுன் பதிரங்க மேவிப் பள்ளிகொண்டு நானிருந்தேன் ஸ்ரீரங்க மீதில் சிறந்திருக்கு மப்பொழுது நீதங்கள் கெட்டகலி நீணிலத்தில் வந்ததினால் நீதநெறி மானுபமும் நிசமான தர்மமதும் சாதமுள்ள வெள்ளானை தலையைந்து கொண்டதுவும் கள்ளமில்லா வெண்சாரை கடியபெல வெண்கருடன் வெள்ளன்னம் வெண்குயில்கள் வெண்புறா வெள்ளைமயில் நல்ல அனுமன் நாடுங்காண் டாமிருகம் வல்ல வெண்ணரிகள் வளரும்வெண் காக்கைகளும்  ஆழியோடு சிங்கம் ஆனையிறாஞ் சிப்புள்ளும் வேளிசை வெண்கலைமான் வெண்புள்ளு வெண்ணணில்கள் வெண்கற்றா வெண்போத்து வெண்தோகை வெண்பறவை பண்புற்ற பட்சி பலமிருக மூர்வனமும் இத்தனையும் நீசனுக்கு எற்றுக் கொடாதபடி புத்தி யறிவோடு புவனமதை விட்டேகிக் கானகமே நடந்து கதறி யெனைவருந்தி மானுவமாய் நின்று வருந்தித் தவம்புரிந்தார் ஆனதா லூர்வனத்தில் ஐந்துதலை வெண்சாரை ஈனமில்லா மிருகம் ஏற்றவெள் ளானைகற்றா வெண்ணாடை வெண்ஞாளி வெண்முத்தி வெண்மிருகம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(207,'தர்மயுகச் சிறப்பு','வெண்பட்சி யான மேல்பட்சி யானதிலே பொன்பட்சி வெண்பட்சி பூணுநிறத் தானபட்சி தென்பட்சி அன்பட்சி செந்தா மரையின்பட்சி இப்பட்சி யோடு இம்மிருக மூர்வனமும் அப்பட்சி யோடே அனைத்து மனுகூலமுமாய்க் கூடிக் குழையும் கொடிப்பிதிரு மொன்றதுபோல் தேடரிய அந்தத் தேதிதனில் தோன்றிவரும் விருச்சம் படைக்க வேணு மதுவிபரம் புரச்ச மதுபார்த்துப் புகலுது ஆகமந்தான் என்பிள்ளை ஏழும் இயாபிக்க நாமீந்த மின்விருச்ச மான மிகவிருச்சம் நிலையாய் வன்னமது பூகம் வாசமது விருச்சம் தென்னை விருச்சம் செறியும்புன் னைவிருச்சம் ஆதி விருச்சம் அடர்ந்த பெருவிருச்சம் சோதி விருச்சம் சுபசோபன விருச்சம் தேச விருச்சம் செம்பொன் னிறவிருச்சம் ஆகமத்தி லுள்ள அனேக விருச்சமதும் வந்து மிகத்தோன்றும் வைத்திருக்குந் தேதிதனில் சிந்து பயில்வானம் சிறந்தவொரு வெண்வானம் வான மதிலுறையும் வளர்நிலவும் நேராக ஈனமில் லாததுதான் இலங்குமெந் நேரம்வெளியாய் சேத்திரங்க ளொன்று தினமு மதுவிளக்காய் சாத்திர வேதம் சமயமொன்றாய் நின்றிலங்கும் வாயுவே பூப்போல் மலரெடுத்து வீசிநிற்கும் ஆயுங் கலைதமிழும் அறிவொன்று போல்பரவும் நம்மனுவோர் தர்மபதி நாளுமிகத் தழைக்கும் தம்மனுவோர் போலே தழைத்திருந்து வாழ்வார்கள் ஒக்க ஒருஇனம்போல் உவந்திருந்து வாழ்வார்கள் மிக்கத் திருச்சாதி மேற்சாதி சான்றோராய்  இருப்பார் வயது எண்ணிக்கையில் லாதபடி பருப்பார் பழமும் பாலு மிகஅருந்திப்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(208,'தர்மயுகச் சிறப்பு','புண்ணிய முள்ளோராய்ப் பூமிதனை யாண்டிருப்பார் மண்ணெல்லாந் தர்ம வயல்போல் விளைந்திருக்கும் தர்ம புவியிலுள்ள தண்ணீர் கலைக்கியானம் நன்மைவெள்ளங் காட்ட நாடுங் கருணைவெள்ளம் இப்படியே தர்மபதி இராச்சியமொன் றுண்டாக்கிச் செப்படிய பொன்பதிகள் திட்டித்து வைக்கவென்றும் முன்னமே யென்றனக்கு உகந்ததுவ ராபதியில் பொன்னம் பதியும் பெரியதங் கப்பதியும் உண்டுகா ணப்பதியும் உவரியதுக் குள்ளிருக்கு கண்டு கொள்ளார்கள் கலிமாசு கொண்டவர்கள் ஆனதா லப்பதிதான் ஆகுவது மக்களுக்குத் தான முடனமைக்கத் தர்மசாஸ்திரத்தி லுற்றிருக்கு தர்ம புவிகண்டு தானிருக்கு மக்களுக்கு வர்மமில்லை நோவுமில்லை மறலிவினை தானுமில்லை தர்மபுவி வாழ்வார்க்குச் சத்தி விதிக்கணக்கும் நம்மிடத்தி லல்லால் நாட்டில் கணக்குமில்லை சாத்திர வேதம் சமயம்போ தித்தருளி சேத்திரவிப ரிப்புரைக்கச் செல்லநம் மனுவோரும்  காலனெனுஞ் சித்திரக் கணக்கனெனும் பேர்களுக்கு ஏலமே தவசு இடறாக நிற்கிறார்காண் ஆனதா லவர்கள் அழிவாகிப் போகுமையா ஊனக் கிரகம் ஒன்பது பேர்களையும் அழித்துவே றொன்றுக்குள் அருள்கொண் டிருக்குதுகாண் முன்னூலின் சாத்திர முறையெல்லாந் தப்பவைத்துப் பின்னூல்பு ராணமொன்று போதிப் பொருவனுமாய் இந்தப் படியேயான் நிச்சித் திருப்பதினால் அந்தப்படி யுள்ளதெல்லாம் ஆகுமந்தத் தேதிதனில் அதுக்குமுன்னே நாம் அமைப்பதெல் லாமமைத்துக் கதுக்கெனவே பார்ப்போம் கலியை யழிப்பதற்கு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(209,'அம்மைமார் தவநிறைவு','என்று விசாரித்து இருக்கின்ற நாளையிலே சான்றவரைப் பெற்ற தையல்தெய்வக் கன்னியர்கள் தவத்தி லவர்நின்று தலைவரையுங் கால்வரையும் உபத்திர மால்விருச்சம் உடலெல் லாமேமூடி கன்னியர்கள் ரோமம் கனத்தபுவி யில்வேராய் உன்னியே லோகமதில் ஓங்கிப் பரந்ததுவே நின்ற தவந்தான் நிலைத்து நிறைவேறி நன்றினியக் கன்னியர்கள் நாடுந் தவத்தருமை பார்த்தன் தவமதிலும் பத்துரெட்டிக் கூடவுண்டே ஏற்றரிய சீதை இருந்த தவமதிலும்  ஒட்டிரெட்டிக் கூடவுண்டே ஓவியத்தார் தந்தவங்கள் ஏட்டி லடங்காது இவர்தவசைச் சொல்லவென்றால் பொறுத்து முகியாதப் பெரிய தவத்தருமை மறுத்துரைக்கக் கூடாமல் வளர்ந்துதவ முற்றியதே சத்தி உமையாட்கும் சதாசிவ மானதுக்கும் முத்திக்கும் மாலுக்கும் முகுந்தன் பிரமனுக்கும் மனதில் மிகஅறிந்து மாதுசத்தி யேதுரைப்பாள் காட்டிலேழு பெண்கள் கனநாள் தவசுபண்ணி தாட்டிமையாய்ப் பெண்கள் சடைத்தார்காண் ஈசுரரே பெண்கள் தவசியல்லோ பொறுக்கமிகக் கூடலையே அங்கேபோய் நாமள் அவர்தவசு பார்ப்போமென்றாள் என்று உமையிரங்கி இப்படியே சொல்லிடவே அன்று அறம்வளர்த்தாள் அவள்பங்க ரேதுரைப்பார் நல்ல உமையே நாடும்பெண் ணார்தவத்தில் செல்லவென்றால் நாரணரும் தேனேவர வேணுமென்றார் அப்போது நாரணரை அடிவணங்கி மாதுமையும் ஒப்போ டுறவணங்கி உரைக்கிறா ளன்போரே அண்ணரே கோவே ஆதிநா ராயணரே கண்ணரே கார்வண்ணரே காட்டிலேழு கன்னியர்கள் தவசுதனி லிருந்து சடைத்தார் கனநாளாய் சிவனே அவர்கள்தவம் செப்பப் பொறுக்காதுநாமள்போய்ப் பார்த்து நல்லபெண்க ளேழ்வரையும் ஆமான பதவி அருளவே வேணுமையா');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(210,'அம்மைமார் தவநிறைவு','என்று நா ராயணரை எழுந்தருளச் செய்துஉமை அன்று சிவனாரும் ஆதிநா ராயணரும் அம்மை உமையாளும் அரம்பை ஸ்திரிமாரும் செம்மையுட னடந்து சென்றார் வனமதிலே வனத்தி லவர்கள்வர மாதரேழு கன்னியர்கள் இனத்திலுள்ள மாதுமையை எல்லோருங் கண்டாவி தாயே வுனது தாள்கண்டு எத்தனைநாள் வாயே புவிக்குடைய மாதாவென் தாயாரே எனதேர மெங்களைநீர் இகழ்ந்து கடத்தாமல் தனதாக எங்கள் தயவில் குடியிருந்து கற்பகலாக் காத்த கண்ணெங்கள் மாதாவே உற்பனமா யெங்களைநீர் உலகில் தவறவிட்டு இருந்தாயே தாயே எங்கள்விதி நாயகமே அருந்தாமல் வாடினோமே அறமில்லார் தங்களைப்போல் காத்திருந்தா யென்றிருந்தோம் எங்களுட கட்டழியப் பார்த்திருந்த ஞாயமென்ன எங்களுட பார்வதியே எந்நேர மீசுரர்க்கும் என்னம்மை யுங்களுக்கும்  சொன்ன பணிமறந்து சொல்தப்பி நின்றோமோ ஈசருட சட்டம் யாங்கள் மிகமறந்து தேச மதில்வரவே சிந்தை யிச்சித்ததுண்டோ கற்பினை மறந்தோமோ காராவைக் கொன்றோமோ அற்புதவேள் தாயே அடியார் தமக்கருளும் என்றுகன்னி யெல்லாம் இப்படியே தானுரைக்க அன்று அறம்வளர்த்த அம்மை மனதிரங்கி நல்லதுகாண் பெண்ணேயுங்கள் ஞாயமெல் லாமுரைப்போம் வல்ல சிவனாரே வார்த்தையொன்று நீர்கேளும் தவத்தை நிறைவேற்றித் தார்குழலா ளேழ்வரையும் அகத்தே வரவழையும் ஆதியே யென்றுரைத்தாள் அப்போ சிவனார் ஆனகன்னி ஏழ்வரையும் கொப்போடு சூழ்ந்துநின்ற குழையெல்லாமே யுதிர்த்தி நிறைவேற்றித் தவத்தை நேரிழைமா ரேழ்வரையும்  சிறைமாற்றிப் பெண்களையும் சிவனார் வரவழைத்து வந்து மடவார் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(211,'அம்மைமார் தவநிறைவு','மறையொத்த மூவரையும் சந்துஷ்டி யாகத் தாழ்ந்து நமஸ்காரமிட்டுக் கனகத் திரவியக் கப்பல்கரை சேர்ந்தாற்போல் மனமகிழ்ந்து மாதுமையை மாமடவார் முத்திமுத்திக் கொண்டாடிக் கொண்டாடிக் கூறுவார் கன்னியர்கள்  சென்றாடிச் சென்றாடிச் சிவனை மிகப்போற்றி வந்தனே வந்தாய் போற்றி மாதுமைக் கணவா போற்றி சந்தன மயிலே போற்றி சண்முகன் தாயே போற்றி நந்தகோ வேந்தே போற்றி நாதனே நீதா போற்றி சிந்தரென் கணவா போற்றி சிவசிவா போற்றிப் போற்றி மாதவா போற்றிப் போற்றி மறைமூடி காணா வல்ல நீதவா போற்றிப் போற்றி நிசரூபச் சித்தா போற்றி சீதவா ளுமையே போற்றித் தெய்வநன் மணியே போற்றி பாதவா வனத்தில் தொட்ட பாங்கனே போற்றிப் போற்றி மூவர் தேடியு முற்றாத முதலே யுனதுபதம் போற்றி தேவர்க் கரியத் திரவியமே தெய்வ மணியே சிதம்பரமே காவக் கானக வனமதிலே கற்பை யழித்துக் கைவிட்டகன்ற தாவத் துணையே யென்கணவா தவமே யுனதுபதம் போற்றி மாட்டி லேறும் மகாபரனே மாது உமையாள் பங்காளா காட்டி லடியா ரேழ்பேரும் கற்றா விழந்த பசுவதுபோல் ஊட்டி உறக்கா ரில்லாமல் ஊமை கண்டக் கனாவதுபோல் வாட்ட மறிந்து மனதிரங்கி வந்தாய்க் கவலை தீர்ந்தோமே தீர்ந்தோமினி எங்களைத் தானும் திரும்பக் கயிலைக் கழையாமல் ஈந்தோர் பிள்ளை ஏழ்வரையும் இனமு மேழுங் குறையாமல் சாந்தோ ரெங்கள் கணவரையும் தந்தே தரணி யரசாண்டு  வாழ்ந்தே யிருக்க வரமருளும் மாயா திருக்கு மறைமுதலே அய்யா முதற்பொருளே ஆனந்த மானவரே மெய்யான மூல மேல்வீட்டி ருப்போனே தூணாக அண்டபிண்டத் தூரைக் கடந்தோனே கோணவட்டத் துள்ளே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(212,'அம்மைமார் தவநிறைவு','குடியிருக்குங் கோவே ஈசரே யெங்களுட இளைப்பெல்லாம் நீர்மாற்றித் தேசமதில் வாழச் செல்ல வரந்தாரும் பெற்றமக்க ளேழும் பிள்ளை யதுவழியும் கொத்துக் கொடியும் குலவம்மி சமுழுதும் சான்றோர் வழிமுழுதும் தற்காத்து எங்களையும் பண்டூசல் செய்த பாரமுனி வன்பதத்தில் சேர்ந்திருக்க வுமவரின் திருப்பாத முந்தொழுது தாழ்ந்திருக்க வுஞ்சிவனே தாரும்வர மென்றுரைத்தார் அப்போது நல்ல ஆதிசிவ மேதுரைக்கும் இப்போது நாரணரே இக்கன்னி கேட்டதற்கு இன்னவித மென்று இசைந்து வரங்கொடுவும் சொன்னவிதங் கேட்டுச் சொல்லுவார் நாரணரும் கேட்ட வரமதுதான் கெட்டிதான் ஈசுரரே நாட்டமிந்தப் பெண்களுக்கு நான்சொல்ல நீர்துணையாய் மடமாதே பெண்ணரசே மடந்தைக் கிளிமொழியே  தடமான பொய்கையிலே தண்ணீர் தனைச்சுருட்டி ஈசருட முடியில் இட்டுக் கரங்குவித்து வாசமுடன் நாடோறும் வருகின்ற வேளையிலே இன்றேநீர் கொண்டு ஏகின்ற நீரையெல்லாம் இன்றென் தலையில் இயல்பாய் விடுவுமென்றேன் இரந்து திரியும் ஈசருக் கல்லாது பரந்தபுவி மாயனுக்குப் படாதே எனவுரைத்தீர் அப்போது உங்களைநான் ஆகட் டெனவுறுக்கி இப்போது பாரென்று இத்தனையுஞ் செய்தனல்லோ வண்ணத் திருமாலிவ் வகையுரைக்க மாதரெல்லாம் கண்ணையவர் பொத்திக் கவிழ்ந்துமிக நாணிநின்றார் அப்போது எம்பெருமாள் அவர்களுட னேதுரைப்பார் ஒப்போடு வொத்த ஓவியங்க ளேகேளும் நீர்பெற்ற பிள்ளைகள்தாம் நீணிலத்தி லேபெருகி வானத் தமிர்த மதுபா லமுதருந்தி வாழ்ந்தார் சிலநாள் மன்னரெனக் கோட்டையிட்டார்  ஆழ்ந்ததொரு சோழன் அவன்பழிகள் ரண்டேற்றான் பின்னுள்ள சான்றோர்க்குப் பிறகுவந்த மாநீசன் அந்நீசன் சாபமிட்டு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(213,'அம்மைமார் தவநிறைவு','அவர்கள் துயரமுற்றார் துயரமுற்றுச் சான்றோர் துற்கலிக் குள்ளாகி  அயர்ந்து மிகவாடி ஆட்போலே நீசனுக்குத் தாழ்ந்து பணிவிடைகள் சான்றோர்கள் செய்தாலும் வாழ்ந்துமிகக் கெட்டகலி வைது மிகஅடித்து ஆருமற்றார் போலே அலையுகிறார் சான்றோர்கள் சேருமிட மில்லாமல் தியங்குகிறார் சான்றோர்கள் அப்ப னானிருக்க அவரம்மை நீயிருக்க அப்பனம்மை யில்லையென்று அடிக்கிறான் நீசனெல்லாம் நீரு மித்தனைநாள் நின்றுவிட்டீ ரித்தலத்தில் நானும் பரதேசம் நடந்துவந்தே னித்தனைநாள் சான்றோர் துயரம் தான்கேட்பா ராருமில்லை மீண்டே நாம்கேட்கவென்று மேதினியில் போகவென்றால் இச்சொரூபங் கொண்டு ஏகினால் மாகலியன் பொய்ச்சொரூபத் துள்ளே புக்கிடுவோ மல்லாது என்றையா நாதன் எடுத்துரைக்கக் கன்னியர்கள் அன்றவர்கள் தலையில் அடித்துக்கீழ் வீழ்ந்தழுதார் அழுதாரே பெண்கள் அருவரைகள் தானிளக ஒழுகான பெண்கள் ஓலிமிட் டேயழுதார் அய்யோயெம் மக்கள் ஐந்திரண் டானதிலே பொய்யோமெய் யோசோழன் பொன்றிவிட்ட ஞாயமது கற்பழியாக் கன்னியெங்கள் கற்பையெல்லாம் நீரழித்து          உற்பனமாய்ப் பெற்றபிள்ளை ஒன்றுபோல் நீர்பார்த்து வளர்க்காமல் மக்களையும் மாளக் கொடுத்தீரே பழவினையோ யெங்களுட பாவக் குறைச்சலிதோ கற்பிழந்தோ மென்றும் கௌவையில்லா தேயிருக்க இப்பிழையோ வந்து எமக்குத் தலைவிதிதான் மக்க ளிருபேரை வதைத்தகொடுஞ் சோழனுட அக்கபக்க மெல்லாம் அறாதோ எம்சிவனே மாபாவிச் சோழனுட வம்மிசங்க ளானதெல்லாம் தீயாவிக் கொண்டு செத்திடா தோசிவனே எம்மக்கள் தம்மை இடுக்கஞ்செய் தேயடித்த வன்மக்கலி நீசனெல்லாம் வாழ்விழந்து சாகானோ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(214,'அம்மைமார் தவநிறைவு','பழிசெய்த சோழனூர் பகலநரி ஓடாதோ வழிசோழ வம்மிசங்கள் வன்னரகில் மாளாதோ நீசக் குலங்கள் நெருநெரெனத் தானொடிந்து தோசப் புழுக்குழியில் தோயாதோ யெம்சிவனே பெற்றநா ளன்றுமுதல் பிள்ளைகளைக் காணாமல் நித்தம் பால்சுரந்து நெகிழுதே யெம்சிவனே பெற்றபிள்ளை ஏழுடைய பேருடம்பு தன்னிறத்தைச் சற்று மறிந்திலமே தலையிலேழு துஞ்சிவனே கொதிக்குதே யெங்கள் கும்பிமிகக் குமுறிக்  கதிக்குதே யெங்களுட கண்மணியைக் காணாமல் ஏழுகன்னி மாரில் யார்பெற்ற கண்மணியோ கோளுரைத்துப் பாவி கொன்றானோ யெம்சிவனே கொன்னவன்தா னின்னம் கொடுநரகில் வீழாமல் இன்ன மிருப்பானோ இறந்தானோ யெம்சிவனே பாவியவன் செத்துப் பஸ்பமாய்ப் போனாலும் ஆவியைக் கண்டாலும் ஆக்கினைகள் செய்திடுவோம் மக்கள்ரண்டு பேரும் மணஞ்செய்து வாழ்ந்தவரோ பக்குவ வயசதிலோ பாழறுவான் செய்தவினை கலியாணஞ் செய்த கண்மணிக ளானாக்கால் வலியான மங்கையர்கள் மனஞ்சலித்து வாடுவரே இட்ட வுடைமை இறக்கா திருப்பாரோ கட்டின மங்கிலியம் கழற்றா திருப்பாரோ இத்தனையுங் கழற்றாது இருக்கச் சிவன்செயலால் புத்தி தனைக்கொடுத்துப் பெண்ணரசைக் காப்பாரோ ஏதென் றறிந்திலமே எங்களுட தாயாரே கோதண்ட மாதாவே கோவேயெந் தாயாரே வனத்தில்வந் தெங்களுட வாட்டமெல்லாம் தீர்த்தீரென்று புனத்தில்கனி கண்டதுபோல் பிரியமுற்றோ மாதாவே கைப்பிடித்த பண்டாரம் கட்டுரைத்த சொற்கேட்டு மெய்ப்பிடித்த மெல்லாம் மிகவுழறு மாதாவே கன்னிப் பருவமதில் கைமோச மானதினால் உன்னி மனதில் உளறி யுளங்கலங்கிப் பாராமல் மக்களையும் பார்மீதி லேகிடத்திச் சீரா கவனத்தில் சென்றோ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(215,'அம்மைமார் தவநிறைவு','மிகநாணி பாவி கெடுவான் பழிசெய்வா னென்றுசொல்லித் தாவியே யெங்கள்மனம் சற்று மறியாதே எங்களையு மீடழித்து எம்மக்க ளேழ்வரையும் சங்கை யழிக்கத் தலையெடுத்தா ரிக்கூத்தர் நச்சுக் களையாய் நாங்கள்வா ழுமிடத்தில் சச்சுருவ மிட்டுவந்து சதித்தாரே பண்டாரம் எங்களுக் கென்றிவரைப் படைத்தா ரோபிரமன் கங்கை யணிசிவனார் கேட்டு மகிழ்ந்தாரோ இத்தனைக் கூத்தும் இக்கூத்தெல் லாம்பார்க்கக் கூத்துவன்போல் தோன்றி கோலங்கொண்ட பண்டாரம் இனியெங்க ளையேற்று ஈன்றபிள்ளை ஏழ்வரையும் மனுவேழ் குறையாமல் மக்களேழு வழியும் குறையாமல் தந்து குவலயத்தை மக்களுக்குத் திறவானத் தங்கத் திருமுடியுஞ் செங்கோலும் கொடுத்தெங்கள் மக்கள் குவலயத்தை யாண்டிருக்க   அடுத்துச் சிலநாளில் ஆக்கிவைக்கா திருந்தால் ஏழுபெண் பாவம் ஏற்பீர்காண் பண்டாரம் நாளு கடத்தாமல் நடத்தி மிகத்தாரும் எங்களையுங் கற்பழித்து இந்த வனந்தனிலே மங்கள சோபனமும் மறந்து மயிர்விரித்துத் தூங்காமல் வாடி தினமும் மிகவுணர்ந்து ஆங்கார மேமறந்து அவனியா சையறுத்துத் தலைவிரித்துக் கலையைச் சதமென்று எண்ணாமல் உலையில் மெழுகதுபோல் உறுவனத்தி லேநிலையாய் வெயில்பனியிலு மேகத் துளிர்விழிக் குள்ளாகிக் குயில்கூவும் வனத்தில் கோதையேழு பேரும் நின்றோமே காட்டில் நீர்செய்த மாயமதால் இனியெங்கள் மக்கள் ஏழ்வரையு மேழ்வழியும் அநியாய முமடக்கி ஆனமக்கள் வம்மிசத்தைக் கொத்தோடே சேர்த்தெடுத்துக் குறுங்கலியை யடக்கி அரசாள மக்களுக்கு ஆனபதி ஈயும்வரைக்கும் எங்களைப்போல் சுகமற்று இருப்பீர்காண் பண்டாரம் மங்களமல் லால்கலி மாளும்வரை வாராது என்று சபித்தார் ஏற்றகன்னி ஏழ்பேரும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(216,'அம்மைமார் தவநிறைவு','அன்றுநா ராயணரும் அவர்கேட்டுத் துக்கமுற்று  என்னசொல்லப் போறோம் யாம்தா மினியெனவே வன்னத் திருமேனி மனதுநொந் தேதுசொல்வார் பெண்ணேநீ ரேழ்பேர்க்கும் பிரமா அமைத்தபடி எண்ணம் வந்ததல்லால் யானென்ன செய்தேனடி ஆனால் கலியை அழிந்துமக்கள் தம்வழியை நானாகச் சென்றெடுத்து நலங்கொடுக் கும்வரைக்கும் துயர மெனக்குத் தொடுக்குமெனச் சாபமிட்டீர் அசுரக் குடும்பம் அறுக்கும்வரை முன்னாளில் ஈசரிட்ட சாபம் ஈதோடே வாய்த்துதடி மாசங் கடந்து வரும்வரைக்கும் நீங்களுந்தான் எங்கே போயிருக்க ஏழ்பேர்க்குஞ் சம்மதங்காண் கொங்கை யினியழகக் கோதையரே சொல்லுமென்றார் அப்போது கன்னி அவரெல்லா மாராய்ந்து செப்புகிறா ரந்தச் சிவபத்தர் தன்னுடனே மக்கள்ரண்டு பேர்கள் மாண்டாரவ ருயிரை அக்கமது செய்தீரோ அயலோவது ஞாயமென்ன என்றுகன்னி ஏழ்பேரும் இரங்கித் தொழுதிடவே அன்றுநா ராயணரும் ஆதிசிவ னுமையும் தாதா மனமகிழ்ந்து சதுர்முகனைத் தானழைத்து வேதாவே மக்கள்ரண்டை வெற்றியுள்ள வைகையிலே     கொன்றானே சோழன் குருநன்றி யைமறந்து சென்றாரே மக்கள் சென்றவுயி ரெவ்விடங்காண் அப்போது வேதா அவர்தான் மிகமகிழ்ந்து செப்புகிறோ மென்று செப்பினா ரன்போரே இறந்தபிள்ளை ரண்டின் ஏற்றவுயி ரானதையும் அறந்தழைக்கு மாகயிலை அறைக்குள் ளடைத்திருக்கு மால்மக்க ளென்று மனமகிழ்ந்து நான்பதறி வாலைமுனி யுயிரும் வானவர்கள் தன்னுயிரும் ஆனதினால் பிறவி அமைக்கப் படாதெனவே நானிதற் கஞ்சி நற்பதியில் வைத்திருக்கு என்றுவே தாவுரைக்க எம்பெருமா ளேதுரைப்பார் அன்று படைத்திலையே அநேகமனு வாகுமல்லோ அஞ்சுமக்கள் பிள்ளை அவனியைம்பத்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(217,'அம்மைமார் தவநிறைவு','தாறதிலும் மிஞ்சிப் பரந்து மேல்சான் றோர்பெருக்காய் இப்பிள்ளை ரண்டும் ஏலமே நீர்படைத்தால் கொப்புநூ றாயிரம்போல் கூடிப்பெ ருக்குமல்லோ பாவி கெடுத்தான் படையாமல் நீர்கெடுத்தீர் தாவிக் கெடுத்தான் சளக்கலியன் மாபாவி இனியென் மக்களுயிர் இன்பமுடன் நீர்காத்து மனுவராய்ப் பூமியிலே வைகைக்கூ டவ்வோடு  எழுப்ப வேணுமென்று யாம்நிச்சித்தி ருப்பதினால் வெளுப்பாக அவ்வழியில் மேலுகத்தோர் தாம்பிறந்து இன்பமுள்ள வானவரும் இவ்வழியில் வாழ்வதினால் அவ்வழியி லவ்வழிகள் அநேகம் பெருகிடவே செவ்வாக நிருமிப்போ திடீர்திடீ ரெனப்படையும் என்றுசொல்ல மூலம் இசைந்துநல்ல வேதாவும் நன்று நன்றென்று நருள்பிறவி செய்தனராம் இப்படி யேபிறவி இவர்செய்வோ மென்றுசொல்லி அப்பிறவி வேதா அமைத்தார் மனுப்பெருக உடனேநா ராயணரும் உள்ளங் களிகூர்ந்து திடமான கன்னியர்கள் செய்முகம்பார்த் தேதுரைப்பார் நீர்கேட் டதற்கு நிண்ணயங்கள் கண்டீரே இனிநான் கேட்பதற்கு இன்னதென்று சொல்லிடுவீர் உங்களைப்பூ லோகமதில் உடைய வழிக்குலத்தில் நீங்களும் போய்ப்பிறக்க நிச்சித் திருப்பதினால் ஏதுபெண்கா ளுங்கள்மனம் ஏதென்று டனேசொல்லும் மாதுக ளெல்லாம் மனமகிழ்ந்து கொண்டாடி எங்களுட நாயகமே எமையாளும் ரத்தினமே செங்கருட வாகனவா தேவி மணவாளா நீர் நிச்சித்த நினைவெள்ளுப் போலளவும்  சீர்பரன் முதலாய்த் தெரியாத சூட்சியதே எங்களைப்பூ லோகமதில் எங்கள் வழிக்குலத்தில் மங்களமாய்ப் பிறவி வகுப்போ மெனவுரைத்தீர் பிறவிய துநாங்கள் பெண்மனுப் போல்பிறந்தால் இறவி யாகாமல் இருக்க அருள்வீரோ அல்லாமல் பின்னும் அடியார் மிகப்பிறந்தால் வல்லாத நாயகமே வந்துநீ ரேற்குமட்டும் மாதவிடை மாமணங்கள்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(218,'அம்மைமார் தவநிறைவு','மலர்ந்துறக்க மில்லாமல் சீதமாய் மனுவோடு உறவாடிச் சேராமல் மூடாம லாடை முகமினுக்கிச் சேராமல் பாடாம லந்திசந்தி படுத்துத் துயிலாமல் நன்மை யறியாமல் நளிப்பேச்சுக் கேளாமல் தின்மை யறியாமல் தீன்ரசத்தைத் தேடாமல் கொய்து புடவை குக்குளித்துச் சூடாமல் மயிரு வளர்க்க மனதுவே றெண்ணாமல் கொங்கை திரளாமல் கூறுடம்பு வீசாமல் செங்கனிவாய்த் தேமல் தேகமதில் வீழாமல் பக்குவ ஞாயப் பருவம்வந்து வாய்க்காமல் மிக்குவ மான மிகுவாழ்வு சேராமல் சுற்றுக் கிளைகள் தொடுத்தன்பு கொள்ளாமல் ஒற்றுப் பிதற்றாமல் ஒருவர்முகம் பாராமல்  அல்லல்நோய் பிணிகள் அனுப்போலும் வாராமல் தொல்லை வாராமல் சுகமுமிக வாராமல் இந்த விதிப்படியே எங்களையும் நீர்படைத்துச் சொந்தமுடன் வந்துநீர் தொட்டெடுத்து நன்மைதந்து இரச்சிப்போ மென்று எமக்கு உறுதிபண்ணி நிச்சித்துத் தர்மகுலம் நீர்பார்த்து தான்படையும் என்றுகன்னி ஏழ்பேரும் இப்படியே சொல்லிடவே அன்றுஆ திநாதன் அகமகிழ்ந்து கொண்டாடி நல்லதுதான் பெண்ணேநீர் நம்மோடே கேட்டபடி வல்லவித மானாலும் மாறாதென வுரைத்தார் வாக்குரைக்க கன்னியர்கள் மனமகிழ்ந்து கொண்டாடி நாக்குரைப்பார் பின்னும் நாரா யணரோடு நாங்கள்போய்ப் பிறந்தால் நம்முடைய நாயகமே தாங்கள்வரு மென்றதற்குத் தருணமே தென்றுரைத்தார் அப்பொழுது அய்யா நாராய ணருரைப்பார் செப்புகிறார் கன்னியர்க்குச் சிறந்த தருணமது ஆயிரத்தெட் டம்பலமும ஆன திருப்பதியில் வாயிதமோ ரம்பலமும் வளர்பதியி லொன்றதுவும் கெங்கையுட கண்ணும் கேள்விமன்னர் தஞ்சாவும் சங்கை யழிந்து தலையழிந்த தவ்வாண்டு வருவேன் தென்சுவர்க்க வடமேற்கு மூலையிலே லிங்கமொன்றி லேமூன்று');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(219,'அம்மைமார் தவநிறைவு','இணையாகத் தோன்றினவென்(று) எங்கும் பிரகாசம் இட்டவ்வாண் டேவருவோம் கண்டிடநீர் நான்வருகக் காரண மநேகமுண்டு விண்டுரைக்கக் கூடாது மெல்லியரே யித்தருணம் சொன்னத் தருணம் செவிகேட்டு நீங்களெல்லாம் என்னை நினைந்து இருங்கோ வொருநினைவாய் இத்தருணங் கேட்ட இப்பொழு தேமுதலாய் வடமேற்கு மேற்கும் வடக்குங்கால் நீட்டாதுங்கோ என்று தருணம் இதுவுரைத்தார் கன்னிகட்கு அன்று மடவார் அகமகிழ்ந்து கொண்டாடி நல்லதுகா ணெங்களையும் நாயகமே நீர்படையும் வல்லப் பொருளே மறைமுதலே யென்றுரைத்தார் உடனையா நாதன் ஓவியத்தா ரேழ்வரையும் திடமான பூமியிலே செய்தர்ம அவ்வழியில் பிறவிசெய்தார் மூவர் பிறந்தார்கள் கன்னியர்கள் திறவி யொளிமாதர் தேசம தில்பிறக்க நாரா யணரும் நல்லசிவ னுமையும் சீராரும் நல்ல தெய்வத் திருமாதும் கயிலை தனிலேகிக் கட்டான மண்டபத்தில்  ஒயிலாகக் கூடி உவந்திருக்கு மப்போது ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(220,'காளி சிறை','மாகாளி யென்ற வடபத்தி ரகாளி ஓகாளி யென்ற உயர்ந்த பலக்காரி சென்றாள் கயிலை சிவஅய்யா நாதனிடம் நன்றான கன்னி நாரா யணரிடத்தில் வந்து விழுந்து மண்ணி லவள்புரண்டு சந்துபயில் மாயவரே தான்பிழைத்தேன் நானுமக்கு உன்மக்கள் சான்றோர் உற்றமக்க ளேழதிலே நன்மையற்ற சோழன் நாடும்பழி ரண்டேற்றான் கண்டு அடியாள் கரிகாலச் சோழனுட மன்னுதனில் பன்னிரண்டு ஆண்டுமழை பெய்யாமல் சாபித்தேன் சோழனூர் தட்டழியப் பட்டுழற பாவியவ னூரைப் பகலநரி ஓடவைத்தேன் அல்லாமல் சோழனுட அக்கமறச் சாபமிட்டேன் பொல்லாத சோழன்வழி பொடிப்படவே சாபமிட்டேன் இத்தனையுஞ் சொல்லி ஈடழியச் செய்துவிட்டுப் புத்திரரின் செய்திசொல்லப் புண்ணியரே வந்தேனென்றாள் அப்போத னாதி அய்யாநா ராயணரும் செப்போடு வொத்தச் சிவனோடு சொல்லலுற்றார் கேட்கலையோ யென்றன் கிருபைச் சிவனாரே  ஏற்கலையே யிந்த ஏந்திழையாள் சொன்னதுதான் பிள்ளைக்கோர் தீங்கு பிழையாம லெப்போதும் வள்ளல்களை நன்றாய் வளர்ப்பேனா னென்றுசொல்லி மருட்டி விழித்து வாங்கினாள் மக்களையும் திருட்டுமொழி பேசும் செய்தியைநீர் கேட்டீரோ நான்தனிமை யல்லவே நால்பேரு முண்டல்லவோ தான்தனிமை யாகிடினும் தப்பிதமென் றேபுகல்வார் வானவருந் தானவரும் மறையவரும் சாட்சியதாய் நானவளோ டேவாக்கு நவின்றல்லோ தான்கொடுத்தேன் மக்களுக்கோர் தீங்கு வந்ததே யுண்டானால் மிக்கச் சிறையுனக்கு மேவு மெனவுரைத்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(221,'காளி சிறை','ஈந்தோ மதலை இவள்கையில் ஈசுரரே மாய்ந்தவுட னாகிடினும் வந்துரைத்தா ளோசிவனே சற்றும் பதறாமல் தானிருந்து இத்தனைநாள் புத்திமேல் நெஞ்சரிப்பாய் போவோமென் றேகிவந்தாள் தன்மதலை யென்றால் தலைவைத் திருப்பாளோ என்மதலைக் கிவள்தான் இடறி விழுவாளோ ஒருவர்பிள்ளைக் கொருவர் உடைமை யிடுவாரோ கருதல் விருப்பம் காணுமோ மற்றோர்க்குப் பெற்றகும்பி யல்லோ பெருங்கனல்போல் மீறுவது மற்றோர்கள் கும்பி வருந்திக் குமிறிடுமோ பாவியவன் கொன்று பன்னிரண் டாண்டுவரை ஆவி யறிந்திலையே ஆரும்வந்து சொல்லலையே தாய்தகப்ப னில்லார்போல் தயங்கினது கண்டோமோ சேய்பரனுக் கேராத செய்த பழவினையோ என்றாதி நாதன் ஏந்திழையைத் தான்பார்த்துச் சென்றாதி வேந்தர் செடத்தோ டுயிர்திரும்பி எழுந்திருக்கு மட்டும் இருநீ சிறைதனிலே குளிர்ந்த திருமேனி கூறினா ரந்தரிக்கு கேட்டுமா காளி கிலேச மிகவடைந்து தீட்டும்வட வாமுகத்தில் செய்யவன்னி மண்டபத்தில் இருந்தாள் தவசு ஈசன் செயலெனவே வருந்தாத கூளிகணம் வாதைவிடு பேய்களெல்லாம் நாச்சியார்க் கிச்சிறையால் நமக்கென்ன கேடோகாண் கேச்சியாய்த் தேசமதுக் கென்னகேடோ அறியோம் என்று சிலபேய்கள் எண்ணியெண்ணி யேதிரியும் மூன்று முறுக்குள்ள மூளிப்பே யேதுசொல்லும் கைவாய்த்து மாகாளி கவிழ்ந்திருந்த ஏதுவினால் மெய்வாய்த்து தென்று விளியிட்டுக் கொண்டோடும் இப்படியே பேய்கள் எண்ணஞ்சில திண்ணமுமாய்  அப்படியே காளி அவள்சிறையி லேயிருக்க');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(222,'காளி சிறை','நல்லநா ராயணரும் நாடுஞ் சிவனாரை வல்லப் பொருளே மறைகாணா தோவியமே காளி சிறைதான் கவிழ்ந்திருக்கும் ஞாயம்வந்தால் ஆழிவளை வையகத்தில் ஆர்க்குச் சுகம்வாய்க்கும் முதற்றான் கலியை முடிக்கப் பரகாளி விதத்தமுள்ள அக்கினியில் மிகவே சிறையிருக்கப் பார்த்துநா மிங்கிருக்கப் படுமோகா ணீசுரரே சாற்றும்நீ ரின்னதென்று சத்திகொண்ட ஈசுரரே உடனேதான் ஈசர் உரைக்கிறா ரன்போரே கடனோகா ணென்னோடு கலங்குமொழி பேசுவது நானோ தடுத்தேன் நாட்டுக்கலி தீட்டறுக்க ஏனோகாண் மைத்துனரே என்னோடு பேசுவது எப்போ கலியழித்து எங்களுக்கு நற்பேறு எப்போ தருவீரென்று எண்ணிமிக வாடுறோமே தீட்டை மிகக்கழித்துச் சிவஞான முத்திதந்து வீட்டையெப்போ கயிலை விளக்குவீ ரென்றுமிக தவித்து முகம்வாடித் தானிருக்கும் ஞாயமதும் குவித்து முகம்மலர்ந்து கொள்ளுவதுங் காணலையோ என்றே காபரமும் எடுத்துரைக்க எம்பெருமாள்  நன்றென்றா கட்டெனவே நாரணருங் கொண்டாடி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(223,'மகாலெட்சுமி மகரமாதல்','லட்சுமியைத் தானழைத்து ஏதுரைப்பா ராதிமுதல் சச்சுடராய் நின்ற தருணத் திரவியமே இதுமுன் பிறப்பு இருந்தா யுருப்பிணியாய் மதுவினியப் பெண்ணே மாயக் கலியறுக்க நான்நிச்சித் திருப்பதினால் நாயகியே நீயுமொரு பூனிரைச்சப் பொன்மகரப் பெண்மயில்போல் நீவளர அலையி லறுமுகவன் அவன்பதியி னாழிதனில் நிலையிலே நின்று நீவளரு கண்மணியே நீவளரச் சூழ நிறைந்ததங்க மாமணியால் பவளக்கால் மண்டபமும் பவளமணி மேடைகளும் தேனினிய மண்டபமும் தேரும்பூங் காவனமும் சூழ அரங்குவைத்துத் தெய்வரம்பை சூழவைத்து காளமொடு பேரிகையுங் கண்ணாளர் சூழ்ந்துநிற்க அகரத் தெருவும் அரம்பையர்கள் தந்தெருவும் சிகரத் தெருவும் சேர்த்துவைப்பே னுன்சூழ நித்தம் வலம்புரிகள் நின்றுவுனைச் சூழ்ந்து தித்தியென வேமுழங்கி சேவிக்க வைத்திடுவேன் கோட்டைகள் சூழக் கோடிமண்டப முகித்து வீட்டலங் காரம்போல் வேலையிலுந் தருவேன் உந்தனுட மேனி உரைக்கெளிதாத் தங்கமெனச்  செந்தழல்போல் மகரச் சிலைபோல் வளர்ந்திருநீ கலியுகத்தை முடிக்கக் கண்ணான ஒருயிரை வலியுடனே யுன்னுடைய வயிற்றில் பிறவிசெய்து நாட்டு நருளின் நகர்சோ தனைபார்த்துத் தீட்டுக் கலியறுத்துச் செவ்வுமனு தானெடுத்துத் நாடுன் மகன்றனக்கு நல்லமுடியுங் கொடுத்துத் தேடுந் தர்மச்சீமை செலுத்தசெங் கோல்கொடுத்து அரசாள வைக்கவொரு ஆண்பிள்ளை நீபெறவே துரைசானி போவெனவே சொன்னார்கா ணம்மையுடன் கேட்டுமிக லட்சுமியும் கிளிமொழிவாய் தான்திறந்து நாட்டுக் குடையவரே நாரா யணப்பொருளே மகரச் சிலையாய் வாரிதனில் நான்வளர்ந்தால் சிகரநற் கோபுரமே திருவுளமே நீர்தானும் வருகுவ தென்னவித மாக வுருவெடுத்து அருகில் நீர்வருக');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(224,'மகாலெட்சுமி மகரமாதல்','அடையாள மென்னவென்றாள் அப்போது அய்யா ஆனந்த மேபெருகி செப்புகிறோ மென்று திருவோ டிதுவு ரைக்க மூன்று சடையில் முறுக்குச்சடை யொன்றெனவும் பண்டுள்ள சூரியர்கள் பதிந்திருக்கு மென்விழியில் நெற்றி தனில்பிறையும் நெடியவட அக்கினியும்  சுற்றிக் கனல்மேனி துய்யமாற்றெண் ணாயிரமாம் பளிரெனவே லோகம் பதினாலுக் கோருருவாய்த் துளிரெனவே வாரிச் சூழல வெகுகனலாய்க் காதம் பன்னிரண்டில் கண்டகலி யன்றெரிய தீதக் கனலாய்ச் செயகுண்டமும் பதித்துக் காலில் தர்மமணியைக் கலிரெனவே தண்டையிட்டு மேலில்பல வேதமதை மின்னுடம்பாக விரித்துப் பெண்ணரசே நான்வருகும் பெருமைக் கடையாளம் கண்ணரசே நீயுமென்மேல் கருத்திருந்தால் பிழைப்பாய் அப்படித்தா னென்பிறவி அருகே வருகையிலே இப்படியே வந்து இருபேரு மோர்பிள்ளைபெற்று நாட்டுச் சோதனைக்கு நாமனுப்பித் தாம்பார்த்துக் கோட்டிசெய்த பாவி குறுங்கலியைத் தானெரித்து அதின்மே லுனக்கும் எனக்குமிகு ஆனந்தம் மதுவாக ஈன்ற மதலைநா டாளுமடி என்று பிறவிக்கு ஏற்றஅடையா ளமுரைத்து நன்றினிய மாதே நடப்பதற்குச் சங்கடமேன் உடனேதான் லட்சுமியும் உரைக்கிறா ளாதியுடன் திடமான கோவே செய்ய மணிவிளக்கே என்னை மகரச் சிலைபோல் பிறவிசெய்ய  என்ன விதமாய் இசைவீர்கா ணென்கோவே என்றுரைக்க அம்மை ஏதுரைப்பா ரெம்பெருமாள் நன்றுநன்று பெண்ணே நமக்குத்தொழி லோகடிது அலையி லுனையும் ஆமதியப் பொன்மகரச் சிலைபோல் வளருவெனச் சிந்தித்தா ரெம்பெருமாள் வடகயிலை வாசலிலே வந்தலைந்த வாரிதிரை மடமடென வந்து வாரியெடுத் தாயிழையைச் செந்தூ ரலைவாரி திரைமே லெடுத்திருத்தி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(225,'மகாலெட்சுமி மகரமாதல்','அந்தூரு வாரி அம்மை தனையெடுத்துச் சந்தன வாரி சமுத்திரத்தி னுள்ளிருத்திப் பந்தியாய் வைத்த பரமமணிக் கோட்டையதுள் தெய்வரம்பை சூழத் தேவரெல்லா மோலமிட மெய்பரமக் கன்னியர்கள் மிகுவாய்க் குரவையிட இடம்மான மெக்காளம் டகுடகா வென்றிடவே மடம்மான வாத்தியங்கள் மடமடென வேயதிர வலம்புரிக ளெல்லாம் மாதுவந்தா ளென்றூத சிலம்புனைந்த ரத்தினங்கள் சில்விளக் கேந்திநிற்க முத்துகள் சிப்பி முன்வந் தொளிவீச கொத்துக் கொத்தாகக் கோமேதகஞ் சூழ கன்னிமா ரெல்லாம் கால்கவரி வீசிநிற்க  தன்னிகரில் லாமறையோர் சாஸ்திரங்க ளோதிநிற்க மகரச் சிலையாள் மாதுதிரு லட்சுமியாள் சிகரகோ புரத்தாள் திருவுருவந் தான்வளர்ந்தாள் பார்பதி மாதுமையும் பரமேஸ்வரித் தாயும் சீர்பதியு மங்கே தினமூன்று நேரம்வர ஈஸ்வரனும் வேதா இவர்கள் தினம்வரவே வாசவனும் வானவரும் வந்துநிதம் போற்றிசெய்ய செய்ய அமரரெல்லாம் சிவசிவா வென்றுநிற்க வெய்யவனுஞ் சந்திரனும் மெல்லிபதம் போற்றிநிற்க புஷ்ப மலர்தூவிப் பொன்னுலகத் தோர்சூழ செப்பமுள்ள மகரச் சிலைபோல் திருவளர்ந்தாள் மாதுதிரு லட்சுமியாள் மகரச் சிலைபோலே ஒது மணியலைக்குள் ஒவியம்போ லேவளர மாதை மகரச் சிலையாய் வளரவிட்டுத் தாதணியும் நாதன் சங்கரரோ டேதுரைப்பார் கூட்டுக்கிளி போலிருந்த கொடியிடையைப் போகவிட்டு நாட்டி லிருப்பேனோ நம்முடைய மைத்துனரே சீதை மகரச் சிலைபோ லங்கேவளரப் பாதையது தேடாமல் பதிந்திருக்க ஞாயமுண்டோ அப்போ துசிவனார் அவரேது சொல்லலுற்றார் செப்போடு வொத்த திருமாலே நீர்கேளும் நடத்தும்படி யுள்ள ஞாயமெல்லா மிப்போது சடைத்து இருக்காமல் தானடத்து மென்றுரைத்தார்');");


