        db.execSQL("INSERT INTO akilathirattu2 VALUES(226,'சம்பூர்ணத் தேவன் பரதேவதை மனுப்பிறப்பு','அந்தப் பொழுதில் ஆதிநா ராயணரும் சிந்தை மகிழ்ந்து செப்புவா ராதியோடு நல்லதுகா ணீசுரரே நாடுந்தே வாதிகளில் வல்லசம் பூரணனும் வாய்த்த எமலோகமதில் பரதே வதையான பைங்கிளியுந் தேவனுமாய் மருவனைய மாதும் மன்னவனு மாய்ப்பிறக்க உருவேற்றி நம்மை உயர்ந்ததவஞ் செய்திடவே ஆனதா லவரை அவனிதனி லேபிறவி ஈனமுட னமைக்க இதுநா ளாகுவதால் நின்றந்தத் தேவனுட நிஷ்டைநிறை வேறினதோ என்றெனவே பார்க்க எழுந்தருளு மீசுரரே அப்படித்தா னீசுரரும் ஆதிநா ராயணரும் இப்படியே தேவனிடம் ஏகின்ற வேளையிலே தேவ ரெதிரே தெய்வேந் திரன்தானும் மூவர் நடுவன் முத்தனரி நாரணரின் சங்கு சரம்போல் தங்கநவ ரத்தினத்தால் எங்கு மொளிவீசும் இரத்தினத் திருமுடியைச் சூடித்தெய் வேந்திரனும் சிவனெதிரே போகவென்று நாடி யகமகிழ்ந்து நாரா யணருடனே சந்தோச மாகத் தான்வருகும் வேளையிலே கண்டுசம் பூரணனும் கைமறந்து நிஷ்டையது பண்டு மையலாய்ப் பாவையோ டேதுரைப்பான் மானே கேளிந்த வானவர்கோன் தன்சிரசில் தானாகிய மான சங்குசரத் தங்கமுடி என்தலையி லேவைத்து ஏந்திழையே உன்னோடு தென்தலைவ ரத்தினச் சிங்கா சனமீதில் இருபேரும் வாழ்ந்து இராச துரைத்தனமாய்ப் பெருக்கான தெய்வகன்னி பெற்ற மனுவழியை எல்லா மருகிருத்தி ஏற்றவொரு சொல்லதுக்குள்  வல்லான நீத வையகத்தை யாண்டிருந்தால்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(227,'சம்பூர்ணத் தேவன் பரதேவதை மனுப்பிறப்பு','எத்தனையோ நாமள் இலங்கு மகிழ்ச்சையுடன் வர்த்தனையாய் வாழ்வதற்கு வாய்த்தமுடி ஈதல்லவோ என்றுசொல்லத் தேவன் இச்சைகொண்டு பெண்ணாளும் நன்றுநீர் சொன்னமொழி நல்லா யிருக்குதுகாண் அப்படித்தா னம்மை ஆதிசிவன் படைத்தால் இப்படியும் படைத்தோர் இறவா திருப்பாரோ என்றுபெண் ணாளுரைக்க ஏற்றசம் பூரணனும் அன்று பெருமூச்சு அலைபோ லெறியலுற்றான் அந்தப் பொழுதில் ஆதி சிவமுதலும் வந்தங்கு நின்றார் மாகிருஷ்ண மாலவரும் கண்டந்தத் தேவன் கைமறந்த நிஷ்டையோடு தண்டமிழ்சேர் பதத்தைத் தையலும்வந் தேவணங்கி இத்தனைநாள் யாங்கள் நின்றதவம் கண்டிரங்கிச் சித்த மிரங்கிச் சிவனேநீர் வந்தீரோ எந்தன் பெருமானே இறையவரே வந்தீரோ நந்தகோ பால நாரணரே வந்தீரோ குருவே அரிநாதா கோபாலா வந்தீரோ முருகக் குருபரனே முத்தாநீர் வந்தீரோ செல்வக் கடலே சீமானே வந்தீரோ   கல்விக் கடலான கறைக்கண்டா வந்தீரோ அரவணிந் தத்திவுரித்(து) அணிந்தோரே வந்தீரோ பரமசிவ மான பச்சைமால் வந்தீரோ இப்படியே மானாளும் ஏற்றசம் பூரணனும் அப்படியே வந்தோர் அடிபணிந்து போற்றிடவே மாய பரனும் மனமகிழ்ந்து கொண்டாடி ஆயனைப் பார்த்து அருளுவா ராதியுமே மாயவரே கேட்டீரோ வாய்த்தஇத் தேவனுந்தான் தூயவரே நின்றதவம் துலைத்தறுத்தா னில்லையிவன் முற்றுந் தவமும் முழுதும்நிறை வேறுகையில் சிற்றின்ப மாகித் திருமுடிமே லிச்சைகொண்டு வாடி யயர்ந்தான் மங்கையருந் தேவனுமே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(228,'சம்பூர்ணத் தேவன் பரதேவதை மனுப்பிறப்பு','நாடி யிவன்தனக்கு நல்வளமை யேதுசொல்வீர் அப்போது நாரணரும் அகமகிழ்ந்து கொண்டாடிச் செப்புகிறா ரந்தச் சிவனோடு எம்பெருமாள் ஏது விதமாய் இருந்ததவ மேகுறைய தீதுவந்த ஞாயம் செப்பிடீ ரென்றுரைத்தார் அப்போ சிவனார் அகமகிழ்ந்து தேவனோடு இப்போ துன்சிந்தை எண்ணமெல்லாஞ் சொல்லுவென்றார் தேவ னதைக்கேட்டுச் சிந்தை மிகக்கலங்கி  ஆவி மறுகி அவனேது சொல்லலுற்றான் காரணரே நாங்கள் கருத்தில் நினைத்ததெல்லாம் பூரணமா யங்கே புகுந்துதோ யென்சிவனே ஐயோநான் சொன்னேனென் ஆயிழையோ டல்லாது கையோ கண்ணாலே காணேனே மற்றொருவர் ஊமை மொழிபோல் ஒதுக்கி லுரைத்தாற்போல் நாமறியப் பெண்ணறிவாள் நாடறியா தென்றிருந்தோம் இவர்க ளறிவதற்கு யார்சொல்லிப் போட்டாரோ எவர்களும் நாம்பேசுகையில் இங்குவரக் காணோமே ஆர்சொல்லிப் போட்டாரோ அறியோமே யென்சிவனே தார்சிறந்த என்சிவனே சர்வ தயாபரனே பூரணனே வாசவனே பொறுத்தினிக் கொள்ளுமையா நாரணரே என்சிவனே நாடிப் பொறுவுமையா என்றுசொல்லித் தேவன் ஏற்ற விருத்தமதாய் நன்றினிய முப்பொருள்மேல் நற்போற்ற லேபடிக்கப் பரதே வதையான பைங்கிளியு மப்போது திரமான தாயார் சிவசக்தி பேரதிலும் சிவநாமப் பேரதிலும் ஸ்ரீகிருஷ்ணர் பேரதிலும் விவமான ஆசிரிய விருத்தம்போல் போற்றலுற்றாள்         காரணா அரிநாரணா கவிபூரணா வெகுதோரணா கவிஞோர் தொழும் வாரணா கருணகரத் தருணாவெகு தருணா');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(229,'சம்பூர்ணத் தேவன் பரதேவதை மனுப்பிறப்பு','கவிவருணா கவிமால் சிவாபோற்றி காயாம்பூவின் மேனிநிறமாயா சிவசிவாயா கருவா யுருவுருவா யுனைத் தினமே யருள் நியாயா குறிமத்தகா கருணாகரா போற்றி குணசீரா வெகுதீரா நலிவீரா புவிநாதா திருவோடுறு மார்பா சிறியார்மிக அறியாச்செய்த வெறியானதைச் சிறிதாக்கியே சேர்ப்பாய் முகம்பார்ப்பாய் முடிவானதை யறியாமலே மோகமாய் வெகுதாகமாய் உளறித் தவங்குளறியே உன்சொற்பதம் அஞ்சல்லென ஒஞ்சிப்பத மஞ்சினோம் உன் செயல்தந் தருள்வாயே இப்படியே கன்னி ஏற்றபர தேவதையும் அப்படியே ஆசிரிய விருத்தமொன்று பாடிடவே கேட்டு அரிநாரணரும் கிளிமொழியைத் தான்பார்த்து மெட்டுக் கொடியிடையே மின்னிடையே நீகேளு உன்புருஷ னங்கே உற்றஎம லோகமதில் இன்புருகி வாடி ஏங்கித் தவிப்பதினால் அவனைநீ மறந்து ஆனதெய்வப் பூரணனை இவனை மிகத்தாவி இருந்த தவமதிலும் நிறைவேற மாட்டாமல் நினைத்தகே டித்தனையால் பிறபோநீ பூமிதனில் பெரியசான் றோர்வழியில் எமவழியி லோர்பிறவி என்மகவா யங்கிருக்க அமைப்பா னதற்கு அவ்வழிதா னாயிழையே  இந்தத் தவக்குறைக்கு இன்னமங்கே நீபிறந்து முந்தப் புருஷனுக்கு முன்வேலையுங் கழித்து அழுந்தத் துயரப்பட்டு அவனூழியங்கள் செய்து குளிர்ந்த மொழியற்றுக் கூடுஞ் சரசமற்று எந்நேர முன்றனக்கு ஏக்கமுட னழுந்தி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(230,'சம்பூர்ணத் தேவன் பரதேவதை மனுப்பிறப்பு','மின்னேகே ளிந்த மிகத்தேவ னுன்வழியில் கொண்டபச்சத் தாலே கோதைய ரேயுனையும் கண்டிச்சை கூர்ந்து கர்மக் கடுவினையால் கருவா லுருவைக் கைக்குள் வசமாக்கிப் பருவா பருவமதில் பாவையேநீ யிவனோடு இருவ ரொருவரென இச்சைமிகக் கூர்ந்து குருவை மறந்து கொண்டவனைத் தான்மறந்து தாய்தகப்பன் மாமன் தமையன் தனைமறந்து வாயிற் படிமறந்து வாழ்வை மிகமறந்து சொந்த மனைமறந்து சுகசோபன மறந்து சந்தோச மற்று சகலோர்க்கும் நாணமுற்று முற்புரு ஷன்தனக்கு மூலக்கருக் கேடணைத்து இப்புருஷன் தானெனவே எவ்வுலகுந் தானறியக் கட்டிய மங்கிலியம் காசினியோர் தாமறியக் கெட்டினாற் போலே கீர்த்தி மிகப்பிறந்து  மேலும்பல தொந்தரவு மிகுநாள் வழிதோறும் கேலி மிகவாகிக் கிலேச மிகவடைந்து உதிர வெளியாகி உட்செருமல் விக்கலுமாய்த் தீனறா மந்தச் செடியேற்பச் செய்கையினால் வீட்டை விரித்து மேலேதெய்வ வழியாய் நாட்டை யரசாண்டு நாச்சியார் போலிருப்பாய் என்று சிவனாரும் எம்பெருமாளு முரைத்து அன்று ஏமச்சண்டன் அவ்வழியி லேயமைத்தார் அமைக்க அணங்கும் அவனிதனி லேபிறக்க எமக்குலத்தில் வந்து இயல்பாய்ப் பிறந்தனளாம் மாது புவியில் வளரப் பிறந்தபின்பு தாது கரமணிந்த சங்கு சரத்தாமன் தேவன் தனைப்பார்த்துச் செப்புவா ரெம்பெருமாள் பூவுலகி லுன்றனுட பெண்பிறக்கப் போனதல்லோ ஏதுநீ தேவா இனியுனது செய்தியென்ன சீரொத்தத் தேவன் திருப்பாட்டு ஓதலுற்றான்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(231,'சம்பூர்ணத் தேவன் பரதேவதை மனுப்பிறப்பு','ஆறுசெஞ்சடை சூடிய ஐயனே அலையிலேதுயி லாதி வராகவா நீறுமேனி நிரந்தரம் பூசிவா நீசிவாசிவ மைத்துன ராகவா வீறுசத்தி மணவாள ரானவா வீரலட்சுமி மன்னரரி ராகவா ஏறுமீதினி லேறிடு மீஸ்வரா எமையாட்கொள் ளும்நாரணா போற்றியே  சீதமாங்குணச் செல்வனே போற்றி சிவசிவா சிவனே போற்றி நீதவா நட்பெய்துவா போற்றி நீசிவா சிவராகவா போற்றி மாதவா அரிகேசவா போற்றி வல்லனே அரிசெல்லனே போற்றி ஆதவா அரிநாரணா போற்றி அனாதியேயுன் றன்போற்றிப் போற்றியே அய்யனேதவம் யானிற்கும் போதிலே அந்திராணி மன்னனிந்திர னானவன் பொய்யின் மாய்கைநினை வதினாலவன் பொற்பக்கிரீடமீ துற்பனத் தாசையால் மெய்யின்ஞா னத்தவம் விட்டுவாடினேன் வித்தகாவுன் சித்த மிரங்கியே செய்யும் பாவவினை தவிர்த்தாண்டருள் சிவசிவா சிவசிவா சிவாபோற்றியே போற்றியென் றேத்தித் தேவன் பொற்பதம் வணங்கி நிற்க ஆத்தியே சூடும் வேதன் ஆகமே மகிழ்ச்சை கூர்ந்து தீத்திய ஆய னோடு செய்தியே தெனவே கேட்க நாற்றிசை புகழுந் தெய்வ நாரண ருரைக்கின் றாரே தேவன் வணங்கித் திருப்பாதம் போற்றிடவே ஆயனோ டந்த ஆதிசிவ மேதுரைக்கும் அச்சுதரே தேவனுக்கு ஆன வளமையது');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(232,'சம்பூர்ணத் தேவன் பரதேவதை மனுப்பிறப்பு','நிச்சயமாய்ச் சொல்லி நீரனுப்பு மென்றுரைத்தார் அப்போது நாரா யணரு மகமகிழ்ந்து செப்புகிறோ மென்று தேவனுக்கங் கேதுரைப்பார் கேளாய்நீ தேவா கீழான மங்கையரைத் தாழாத மேல்வகையில் சரியாக்க வேணுமென்று  நின்றாய் தவத்தில் நினைவொன்றைத் தானாட்டி சண்டாளா நீநினைத்த நினைவுதவத் தொத்திடுமோ சூழ வினையறுத்துச் சுருதிநே ருள்ளாக்கி நீளநூ லிட்டு நீதவசு நிற்கையிலே ஆசை நினைவாமோ அறிவுகெட்ட மாதேவா பாச மறுத்தல்லவோ பரகெதியைக் காணுவது ஏதுகா ணுன்றனக்கு இந்நினைவு வந்ததினால் மாது பிறந்ததுபோல் வையகத்தில் நீபிறந்து இந்த நினைவதற்கு இனிப்பிறந்து நீசிலநாள் தொந்துயர மெல்லாம் தொல்புவியி லேதொலைத்துக் கண்டந்த மங்கையரைக் கருவால் வசமாக்கிக் கொண்டு திரிந்து கோதையரின் தன்னாலே நஞ்சுதின் றானாலும் நாண்டுகொண் டானாலும் உன்சீவன் மாய்க்க உனக்கு மனதாகிப் பின்னுஞ்சில நாள்கழித்துப் பெண்ணோ டுறவாடித் தன்னோவியம் போல் தானிருக்கும் நாளையிலே உன்துயரந் தீர்த்து உன்னையெடுத் தென்மகவாய் என்துயர மெல்லாம் யானேநீ மாற்றல்செய்து என்வரிசை தானம் எல்லாமுனக் களித்துப் பொன்னரசு தந்து புவியாள வைத்திடுவேன்  என்றுமா கிருஷ்ணர் இப்படியே சொல்லிடவே அன்றந்தத் தேவன் அவரடியைத் தான்போற்றி அய்யாவே பூமிதனில் அவதரிபோ என்றீரே பொய்யா னகலியன் பூமிதனி லேபிறந்தால் பிறந்த வழியும் பிதிருமந் தக்குலமும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(233,'சம்பூர்ணத் தேவன் பரதேவதை மனுப்பிறப்பு','அறந்தழைக்கு மாலே அதுவுமென் னாற்கெதிதான் பெற்றுக்கொள் ளும்படியாய்ப் புத்தி கொடுத்தருளிப் பற்றுத லாகப் பலன்கொடுப்போ மென்றுசொல்லும் அல்லாம லென்னைவந்து ஆட்கொள்ளும் நாளதுதான் எல்லாம் விவரமதாய் இயம்பி யனுப்புமையா அப்பொழு தந்த ஆதிநா ராயணரும் செப்புகிறா ரந்தத் தேவன் தனக்குத்திடம் தெச்சணா பூமிதனில் தேசபர சோதனைக்கு நிச்சயமாய் நான்வருகும் நிசத்துக் கடையாளம் பூமியி லோர்திக்குப் பொருந்தி மிகவாழும் சாமிவே தமறந்து சுழல்வேத முண்டாகும் சாதி வரம்பு தப்பி நிலைமாறும் பாரிகட்கு மூப்புப் பார்மீதி லுண்டாகும் நீசக் குலங்கள் நெளுநெளெனப் பூமிதனில் தூசியிடத் தெண்டம் தொடர்ந்து பிடிப்பார்கள்  வானத் திடிகள் வருடவரு டந்தோறும் நீணிலத்தில் வீழும் நின்று மலைமுழங்கும் சீமை தென்சோழன் சீர்பயிரு மேவிவரும் இலைகள் கருகும் இருவேதம் பொய்யாகும் தலைக்கண் படலம் தட்டழிய ஓடிநிற்கும் நச்சேத் திரமுதிரும் நடக்கும் வழிகுறுகும் பொய்ச்சேத் திரக்குருக்கள் பூமிதனில் மேவிவரும் நவ்வா துலுக்கு நாட்டில் கலசல்வரும் சிவ்வா லயங்கள் தேய்ந்து சுவரிடியும் கிணறு பாழாகும் கீழூற்றுப் பொய்யாகும் இணறு பெருத்து ஏங்குவாரே மனுக்கள் மனுக்கள் தங்களுக்கு மாகலச லுண்டாகும் மாமோக ஆசையினால் மாசண்டை யாகிவரும் காமோக வெறியால் கனபழிக ளுண்டாகும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(234,'சம்பூர்ணத் தேவன் பரதேவதை மனுப்பிறப்பு','மாதாவைப் பிள்ளை வைப்பு நினைத்துநிற்கும் தாதாவை வேலை சந்ததிகள் கொண்டிடுவார் மாடாடு தன்வயிற்றில் மனிதர்போல் தான்பிறக்கும் கோடா னதுபெருத்துக் கொல்வார் சிலபேரை காணிக்கை வேண்டல் கைக்கூலி தான்மீறும்  மாணிக்கத் தங்கம் வையிர மிகமறையும் நீணிலத்தில் பேய்கள் நிரந்துமிகக் கோட்டிசெய்யும் மாநிலத்தில் வேளாண்மை மழையில் மிகக்கேடுவரும் பேதையர்கள் பிள்ளை பிறக்கு மதிசயம்போல் கோதையர்கள் கொங்கை கூடாம லேவாழ்வார் கூடப் பிறப்பைக் கொடும்பகைபோ லெண்ணிடுவார் வான முறுமும் மழைகீழ்ச் சொரியாது தான மழியும் சாஸ்திரங்கள் பொய்யாகும் ஈனருக்குக் காலம் ரெம்பரெம்ப வுண்டாகும் வீணர் பெருப்பார் வீடதனைக் கெடுப்பார் தாணருட வேதம் தலையழிய விட்டிடுவார் பொய்வேதம் பூமிதனில் பெருத்துமிக வுண்டாகும் மெய்வேதந் தன்னை விரும்பா திகழ்ச்சிசெய்வார் கள்ளர் பெருப்பார் கறியுப்பு மேமலியும் கொள்ளையாய்ச் சம்பை கோடாமுக் கோடியுமாய் நாணி யிறப்பார் நஞ்சித்தின் றேயிறப்பார் கேணிக்குள் ளேயிறப்பார் கீறிக்கொண் டேசாவார் வயிர முழுங்கி மாள்வார் சிலபேர்கள் துயரம் பொறுக்காமல் தோயமதி லேவிழுவார் ஈக்கள் பெருகும் எறும்பு மிகப்பெருகும்  காய்கள் பெருகும் கஞ்சா அபின்பெருகும் சன்னாசி நிஷ்டை தவறி யலைவார்கள் ஒன்னாமது சாதி உலைமெழுகு போலலைவார் கட்டழிந்து பெண்கள் கற்பு மிகத்தவறும் சட்ட மழியும் சந்தியம்பல மிடியும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(235,'சம்பூர்ணத் தேவன் பரதேவதை மனுப்பிறப்பு','கூட்டங் குலையும் குடும்பம் பகையாகும் நாட்டை யரசாங்கம் நாலுரண்டு மூன்றாகும் கோட்டை யழியும் குச்சிக்கட் டைமதிலாம் சீவசெந் தெல்லாம் தீனில்லாத் தட்டழியும் சூவை பெருக்கும் சூறா வளிமீறும் அக்கினியால் தண்ணீரால் அநேக சீமையழியும் முக்கியமாய் லோகமதில் வாந்தி மிகமீறும் இப்படித்தா னல்லாமல் இன்னு மநேகவளம் செப்பத் தொலையாது செப்புவேன் பின்னுனக்கு நீகேட் டதற்கு நிலையாக இத்தனையும் நான்தேட் டமாக நாடியுன் னோடுரைத்தேன் உன்பேரால் நானும் உள்ளாசை கொண்டிருந்துன் தன்பேரா லென்றனக்குத் தலைச்சிறைதான் மாறுமென்று சொன்னே னுனக்குச் சொல்லுவே னின்னமுமே என்ன இனிப்பிறக்க ஏதுனக்குச் சங்கடங்கள்  என்றுரைக்க நாதன் இசைந்ததே வனுரைப்பான் அய்யாவே யென்னுடைய ஆதித் திருவுளமே பொய்யா னகலியில் போய்ப்பிறக்கச் சொன்னீரே சாதி பதினெண்ணாய்த் தான்பிரித்தா னேநீசன் ஆதித்தா திதனிலே அமையுமையா என்கோவே அல்லாம லுன்றனக்கு ஆதிவரை யெந்நாளும் நல்லாகத் தொண்டுபண்ணி நவ்வியிருக்கு மனுவில் படையுமையா யென்றன் பிதாவே யெனப்பணிந்தான் குடையொன் றாற்பெரிய குருவு மகிழ்ந்துரைப்பார் நல்லதுகாண் தேவாநீ நம்மக்கள் ஏழ்பேரில் வெல்லமர் கோன்வாழும் வெற்றிதெய்வ லோகவித்தாய் அவ்வழியி லம்மனுவில் ஆனசான் றோர்குலத்தில் மெய்வழியி லங்கே மிகப்பிறக்கப் போநீயென்றார் நல்லதுதா னென்று நவிலுவான் தேவனுமே வல்லவரே மூவருக்கும் மைத்துனராய் நிற்போரே என்னைப் பிறவி இப்போது செய்யுமென்று வன்னத் தேவாதி வணங்கிநின்றான் மாயவரை');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(236,'ஆத்ம ஜெயம்','அப்பொழுது அய்யா நாராயணர் மகிழ்ந்து முப்பொழுது வுள்ள முறைமையது கூறலுற்றார் ஆறுதரம் நம்மை அகம்வைத்துக் கொண்டசடம்  பேதுபெற்ற தில்லையே பேறுநா மீந்திலையே அச்சடல மம்மலையில் அருந்தவசு பண்ணினதே இச்சடல மீடேற இந்தநாள் வந்துதென்று தேவன் சடலமதைத் திக்கென் றழித்துவிட்டு மூவர் நடுவருட முச்சடலக் கூடதையும் வரவழைத்து வாருமென்று மறையோரைத் தானேவி விரைவாக வாருமென்று விடைகொடுத்தா ரெல்லோரும் நடந்து மறையோர் நல்ல மலைபார்த்துச் சடம் வீற்றிருந்த தங்கமலை மீதில்வந்தார் மலைமீதில் வந்து மலைப்புதுமை தான் பார்த்து அலைமீதில் வாழ்துவரா ஆனபதி தன்னுடைய வடவாசல் நேரே வாழு மலையிதுதான் தடவரை யிம்மலையில் தானிருக்கும் நதிகள் தங்கக்கிணர் வங்கக்கிணர் தாம்பிரக் கிணறுடனே சங்கமகி ழிம்மலையின் தடாகமிது நன்றெனவே கரைகாணாத் தேவர் காட்சியிடு மிம்மலைதான் விரைவான மாமுனிவர் வீற்றிருக்கு மிம்மலைதான் மரித்தோரைத் தானெழுப்பும் வாய்த்தசஞ்சீ விவளரும் பதித்தான மிம்மலையின் பகுத்துரைக்கக் கூடாது என்றுசொல்லித் தேவர் ஏற்றமலைக் குள்ளேகி  நன்றுநன் றென்று நாடியவர் பார்க்குகையில் கண்டாரே காயாம்பூ கண்ணர்முக் கூடதையும் கொண்டாடிக் கூடதையும் குளிர்ந்தரத மீதேற்றி நடந்தார் கயிலை நாதர்திருக் கூட்டமதில் வடந்தார மார்பன் வாய்த்த அரிநாரணரும் பார்த்துச் சடலமதைப் பதிந்தமுகத் தோடணைத்துத் தேற்றி உயிர்க்கொடுத்துச் செப்புவார் கூடுடனே எடுத்த பிறப்புக்கு எல்லாமெனைச் சுமந்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(237,'ஆத்ம ஜெயம்','அடுத்து மூவுலகும் அங்கங்கே கொண்டேகிப் பூமி கடலும் பொருப்புப்பூ லோகமெல்லாம் நேமித் தெனைச்சுமந்து நேட்டமுடன் திரிந்தாய் நீசெய்த நன்றி நினக்குரைக்கக் கூடாது நானுனக்குச் செய்யும் நன்றி மிகக்கேளு இத்தனை நாளும் எனைச்சுமந்து பாடுபட்டாய் புத்திரனாய் நீயெனக்குப் பெரியதெய்வ உயிரில் பிறந்து புவிமீதில் பின்னுஞ்சில நாட்கழித்துச் சிறந்த முகூர்த்தமதில் சேர்த்தே உனையெடுத்து உன்றனக்குப் புத்தி உபதேசங் களுரைத்து என்றன் துயரமாற்ற யுகசோதனைக் கனுப்பி உன்றன்துயர மாற்றி உகத்துயர மும்மாற்றி  என்றன்துயர மாற்றி ஈசர்துயரம் மாற்றி கலியை யறுத்துக் கனாப்பயங்கள் தானறுத்துச் சலிவில்லாத் தர்ம தங்கநவ ரத்னபதி முத்துநவ ரத்தின முடியுனக்கு நானருளி எத்திசையு மெய்க்க ஏற்றமனு அன்போர்கள் கட்டியங் கூறிக் காட்சியிட்டு வுன்றனக்கு எட்டுத்திக்கும் நின்றேத்த ஏற்றகுடை யொன்றுதந்து அரசாண்டு நீயும் அழியாத் திருவுளம்போல் வீரசான்றோர் சூழ வீரக் கொடிநிறுத்திச் செங்கோற்கு தர்ம சிவசெங்கோ லுமருளி எங்கோ லரசு என்நாமச் சக்கரத்தால் சீமையைம்பத் தாறுமுன் சொல்லொன்றுக் குள்ளாக்கிப் பூமுகச்சிங் காசனமும் பொன்முகச்சிங் காசனமும் கோமுகச்சிங் காசனமும் குணமுகச்சிங் காசனமும் மாமுகச்சிங் காசனமும் மயில்முகச்சிங் காசனமும் அன்னமுகச் சிங்கா சனமீதோ டாகவேதான் பொன்னம் பலம்போல் பெரியசிங் காசனந்தான் முப்பத்தி ரண்டும் முழுது முனக்கருளி செப்பொத்த மாணாக்கர் சேவிக்க வுன்றனக்குத்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(238,'ஆத்ம ஜெயம்','தருவேனா னுன்னுடைய தாயார்பே ராணையதாய்  என்றையா நாதன் எடுத்தவர்க் கேவுரைக்க நன்றெனவே வானோர் நற்பூரணனைக் கொண்டாடிப் பேறுபெற்ற சென்மம் பிராணனிது நன்றெனவே பூருவத்தி லுள்ள பூசாந்திர மிவ்வளமை என்னதவஞ் செய்தாரோ இத்தேவன் தாய்தகப்பன் பொன்னப்ப நாரணர்க்கு பிள்ளையென வந்துதிக்க நாரண ரைப்பெறவே நல்ல தசரதரும் பூரணமாய் நின்றதவம் புகன்றிடவே கூடாது பன்னீரா யிரம்வருசம் பாரத் தவசுபண்ணி முன்னூறு வாரமதாய் உலகளந்தோர் ராமருரு கண்ணாளர் தந்தமக்கு கற்பகம்போ லித்தேவன் மதலையாகப் பிளந்து வையகத்தை ஆள்வானென்று கதலிவாய் நாதன் கற்பித்தா ரிப்படியே ஆருபெற்ற பேறும் அல்லப்பே றிப்பலன்தான் பேறுபெற்ற வரிவர்தான் பிறந்த இனமதிலே நாம்பிறந்தா லும்பெரிய நலங்கிட்டு மென்றுசொல்லித் தாம் பிறப்போமென்று சான்றோராய்த் தான்பிறந்தார் அவர்கள் பிறக்க ஆனதெய்வப் பூரணனை திவசமது பார்த்துச் செய்தார் பிறவியது செய்யச் சிவமும் சிவவேதனும் மகிழ்ந்து வைய மளந்த மாலும்பிறவி செய்தார் கர்மக்கடன் கழிக்க கலியுகத்திலே பிறக்கத் தர்மச்சம் பூரணனைத் தரணிப் பிறவிசெய்தார் செய்த சடமதையும் சுமக்கச் சடம்பார்த்து மெய்யிசையும் நாதன் மேதினியில் செய்தனராம் சடம்பிறக்க லோகம் தட்டுமிக மாறி உடன்பிறக்கச் செய்தார் உலகளந்தோர் கற்பினையால் அன்று பிறவிசெய்தார் அவனிசோ தனைக்கெனவே எண்ணமில்லை யென்று ஈசுரரு மாயவரும் கொண்டாடி ஈசுரரைக் கூறுவா ரச்சுதரும் கெண்டையக்கண் ணீசுரரே கேட்டீரோ யென்றனெண்ணம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(239,'திருமால் கலைமுனிக்கு அருளல்','பஞ்சவர்க்கு அஞ்சல்செய்து பார்மீதில் நானிருக்க வஞ்சகநீ சக்கலியன் வந்ததுகண் டேபதறி நடுநடுங்கி நானோட நல்லகலைக் கோட்டுமுனி பொடுபொடென வந்தான் போற வழிதனிலே ஏன்காணும் மாயவரே எய்த்திளைத் தோடுவதேன் கண்காணாப் போறேனென கலைக்கோட்டு டனுரைத்தேன் என்ன விதத்தாலே என்றே யவனிசைய சொன்னதுறு கலியன் சூட்சியினா லென்றவுடன்  அப்போ முனியும் அவனேது சொல்லலுற்றான் இப்போநீ ரோடுகிறீர் இவனை யழிப்பதற்கு எப்போ வருவீர் இசையுமென்றான் மாமுனிவன் அப்போ முனியோடு அருளினது நீர்கேளும் மாமுனியே நீகேளு வஞ்சகநீ சக்கலியை நாமுன்னின்று கொல்ல ஞாயமில்லை கேட்டிடுநீ கலியனுட கண்ணு கண்டால் பவஞ்சூடும் திலிய அவனுயிரைச் செயிக்கயெவ ரால்முடியும் முன்னின்று கொல்ல மூவரா லுமரிது பின்னின் றவனவனால் பேசாதே மாழவைப்பேன் வல்லமையுங் காட்டேன் மாநீசன் கண்ணின்முன்னே உற்பனமுங் கொடேன் ஒன்றறியா நின்றிடுவேன் வந்தேனெனச் சொல்வார் வரவில்லை யென்றிடுவார் என்றன்பே ரோகாணும் யாரோஎனச் சொல்வார் இதோவந்தா னென்பார் இவனில்லை யென்றிடுவார் அதோவந்தா னென்பார் அவனில்லை யென்றிடுவார் இப்படியே சூட்சமொன்று எடுப்போம்நாம் மாமுனியே எப்படியு முள்ளறிவோர் எனையறிவார் மாமுனியே அறிந்தோ ரறிவார் அறியாதார் நீறாவார் செறிந்தோர்கள் வாழ்வார் சேராதார் போய்மாழ்வார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(240,'திருமால் கலைமுனிக்கு அருளல்','சாத்திரத்தி லுந்தோன்றேன் சதுர்மறையைத் தாண்டிநிற்பேன் சேத்திரத்தி லுமடங்கேன் செய்தவத்தி லுமடங்கேன் அன்பிலு மடங்கேன் அறத்திலு மேயடங்கேன் வம்பிலு மடங்கேன் வணங்கிடி லுமடங்கேன் கும்பிடி லுமடங்கேன் குவித்திடி லுமடங்கேன் நம்பிடி லுமடங்கேன் ஞானத்தி லுமடங்கேன் யோகக் கிரியை உறுசரிதை யிலடங்கேன் விற்பனத்தி லடங்கேன் வினோத மதிலடங்கேன் சொற்பனத்தி லடங்கேன் தெரிசனத் திலடங்கேன் கனாவி லடங்கேன் கைகாட்ட லிலடங்கேன் அனாவி லடங்கேன் அச்சரத்திலு மடங்கேன் இத்தனையிலு மடங்காது இருந்து பகைமுடிப்பேன் என்றுநான் சொல்ல ஏற்றகலைக் கோட்டுமுனி மன்றுதனில் வீழ்ந்து மறுகியழு தேபுலம்பி மண்ணிலு மடங்கா மரத்திலு மடங்கா மறையிலு மடங்கா பலவசத்திலு மடங்கா கண்ணிலு மடங்கா கருத்திலு மடங்கா கவியிலு மடங்கா பலவிதசெபிப் பிலுமடங்கா  எண்ணிலு மடங்கா இகத்திலு மடங்கா இறையினி லடங்கா இரங்கிலு மடங்கா ஒண்ணிலு மடங்காத உனைவந் தடைந்திட உரைத்திட மருளென பதத்தடிமிசவிழுந்தான் மறையினி லடங்கா இறøயினி லடங்கா வணங்கிலு மடங்கா பலவகையிலு மடங்கா துறையினி லடங்கா தொல்வியி லடங்கா சுகயினி லடங்கா சுருதியி லடங்கா உறவிலு மடங்கா ஒளியிலு மடங்கா உகத்திலு மடங்கா ஒருவிதத்திலு மடங்கா புறத்திலு மடங்கா அகத்திலு மடங்கா');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(241,'திருமால் கலைமுனிக்கு அருளல்','புகழ்ந்துனை யடைந்திட வகுத்துரையென பதத்தடிவிழுந்தான் 4 சரியையி லடங்கா கிரியையி லடங்கா சையோகத்தி லடங்கா ஞானத்தி லடங்கா கருத்தினி லடங்கா கலைக்கியானத் திலடங்கா கனாவிலு மடங்கா கருவிலு மடங்கா உருவிலு மடங்கா உருகினு மடங்கா ஒன்றிலு மடங்காத உனைவந் தடியாருன தருகினி லடங்கிட அருளுரைத் திடுவென அருள்முனி பதத்தடிமிசை விழுந்தான் உகத்திலு மடங்கா ஓதிலு மடங்கா உணர்விலு மடங்கா உற்பனத் திலுமடங்கா தவத்திலு மடங்கா தழுவிலு மடங்கா தனத்திலு மடங்கா தயவிலு மடங்கா அகத்திலு மடங்கா புறத்திலு மடங்கா அசத்திலு மடங்கா புசத்திலு மடங்கா - மனத் தகத் தடக்கியுன் பதத்தடி பணிந்திட வகுத்துரை யருளென பதத்தடிமிசை விழுந்தான் ஓரெழுத்து மீரெழுத்தும் உற்றபர மூன்றெழுத்தும் ஆறெழுத்து மஞ்செழுத்தும் ஆனா அரியெழுத்தும்  மடங்காது இருந்துபகை முடிப்பேனென ஓதினீரே அய்யா உலகளந்த பெம்மானே எட்டாப் பொருளே எங்கும் நிறைந்தோனே கட்டாக் கலியைக் கருவறுக்க வந்தீரால் நாங்களுமைக் கண்டு நலம்பெறுவதெப்படித்தான் தாங்கள் மனதிரங்கித் தான்சொல்ல வேணுமென்றான் அப்போ முனிக்கு அருளினது நீர்கேளும் இப்போது மாமுனியே இன்றுநீ கேட்டதுதான் ஒருவ ரறியாத உபதேசங் கண்டாயே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(242,'திருமால் கலைமுனிக்கு அருளல்','கருதி வருந்தினதால் கட்டுரைப்பேன் கேட்டிடுநீ 46 எனக்கா கும்பேர்கள் இனங்கேளு மாமுனியே புனக்கார மில்லை பூசை முறையுமில்லை கோவில்கள் வைத்துக் குருபூசை செய்யார்கள் பூவதுகள் போட்டுப் போற்றியே நில்லார்கள் ஆடு கிடாய்கோழி அறுத்துபலி யிடார்கள் மாடு மண்ணுருவை வணங்கித் திரியார்கள் என்பேரு சொல்லி யாரொருவர் வந்தாலும் அன்போ டவரை ஆதரிக்கும் பேராகும் இரப்போர் முகம்பார்த்து ஈவதுவே நன்றாகும் பரப்போரைக் கைசேர்த்துப் பணிவதுவே நன்றாகும் என்பேரால் முத்திரிகள் இட்டோரே நன்றாகும் அன்போராய் வாழ்ந்தாலும் அறிவுநினை வொன்றானால் நாம்வந்தோ மென்ற நாமமது கேட்டவுடன் தாம்வந்து வேடமிட்டோர் சாதியது நன்றாகும் அல்லாமற் கேளு அரிய முனியேநீ எல்லா அமைப்பும் ஏலமே விட்டகுறை எச்சாதிக் காச்சோ அச்சாதி நன்றாகும் எவர்க்கு மிகஈந்து இருப்போரே நன்றாகும் அவர்க்குதவி செய்வோர் அவ்வினமும் நன்றாகும் தாழ்மையாய் வாழ்ந்த சாதியது நன்றாகும்  ஏழ்மையாய் வருவோம் இரப்பன் பரப்பனென இரப்பனைக் கைகொண்டோர் எனையேற்றார் மாமுனியே பரப்பனைக் கைகொண்டோர் பரமேற்றார் மாமுனியே சினமு மறந்து செடமுழிக்கும் பேர்மனுக்கள் நினைவுக்குள்ளே நாம் நிற்போங்காண் மாமுனியே இனத்தோடே வொத்து இருந்துமிக வாழ்ந்தாலும் சொன்ன நினைவதுக்குள் துயிலாமல் வாழ்வோங்காண்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(243,'திருமால் கலைமுனிக்கு அருளல்','மாசுக் கலியை மனதூடா டாதேயறுத்து வாசு நினைவில் வந்துநிற்போம் மாமுனியே பின்னுமொன்று கேளு ஒருவர்க்கோர் வாரமுமாய் நின்றோ முனியேயினி நீசக் கலியறுக்கச் சென்றோமே யானால் செய்யுந் தொழில்கேளு காலுக்குள் ளேதிரியாம் கையதுக்குள் ளேதிரியாம் மேலுக்குள் ளேதிரியாம் விழியதுக்குள் ளேதிரியாம் கண்டெடுத்தார் வாழ்வார் காணாதார் வீணாவார் பண்டைப் பழமொழிபோல் பார்மீதி லாட்டுவிப்பேன் கண்டெடுத்துக் கொண்டோர் கரையேறு வாரெனவும் தொண்டனெனக் கண்டு சொன்னேனா னுன்னோடு தாய்தமர்க ளென்றும் தனது கிளைகளென்றும்  வாயுரமாய்ச் சொல்லும் வம்பரென்றும் பார்ப்பதில்லை அன்பாகி வந்தவரை அலைச்சல்செய் தேற்பதுண்டு வம்பான மாற்றானை வளர்த்தே யறுப்பதுண்டு எட்டியு மெட்டாத எழைக் குணம்போலும் கட்டியுங் கட்டாத கடிய சொரூபமதும் வேளைக்கு வேளை விதக்கோல முமணிந்து நாளுக்கு நாளாய் நடக்கு மதிசயமாய் இரப்ப வடிவாய் இருந்து கலிதனையும் சங்கி லிரந்து தான்வளர்ப்பே னன்போரை என்றுரைக்க நாதன் எடுத்துமுனி யேதுரைப்பான் மன்று தனையளந்த மாயத் திருமாலே இந்த விதமாய் எழுந்தருளி வந்திருப்ப(து) எந்தச் சொரூபம் எடுத்தணிவீ ரெம்பொருமாள் கேட்டான் முனியும் கீர்த்தியுள்ள ஈசுரரே நாட்டான மாமுனிக்கு நானுரைத்தது கேளும் எகாபரத்தை நானினைத்து ஏழுபிள்ளை ஈன்றெடுத்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(244,'திருமால் கலைமுனிக்கு அருளல்','மகாபரனார் செயலால் மக்களுக்குச் சொத்தீய நானுப தேசித்து நாட்டில் மிகவிருத்தி மானுவஞ் சோதிக்க மகற்கு அருள்கொடுத்து மாசுக் கலியறுத்து மக்களொரு மித்தவுடன்  பாசு பதங்கொடுத்துப் பாலன்கை யால்விளக்கிக் கூப்பிடுத லொன்றில் குவலயத்தைத் தான்கிறக்கிச் சாப்பிடுவே னஞ்சைத் தன்பாலை யாளவைப்பேன் என்று கலைமுனிக்கு இயம்பிவந்தே னீசுரரே அன்று மொழிந்ததுக்கு அமைத்ததுகா ணிம்மகவு சந்தோச மாச்சுதுகாண் சங்கரரே நீருமிப்போ வெந்தோசந் தீர்ந்துதென்று வெள்ளிக் கயிலையிலே ஒருவிடத்தி லெக்கியத்தை ஊட்டிக் கனலெழுப்பித் திருநடன மாடுமென்று செப்பினா ரொப்புடனே அப்படியே நல்லதென்று அரனு முமையாளும் முப்படியே மாலினிக்கு ஒன்பதாம் பேறெனவும் வேதனுக்கோ ராயிரம் பிறவி யாச்சுதென்றும் மாது உமைக்கு வளர்பிறவி ஏழெனவும் திதுவுக்கோர் பத்தாம் செய்யப் பிறவியென்றும் சதுர்மறைக் கேழெனவும் சாஸ்திரத்துக் கோர்நான்கும் வானவர்கோன் றனக்கு வளர்பிறவி மூன்றெனவும் தானவர்க் கேழெனவும் சரசுபதிக் காயிரமாம் தொண்டர் தமக்கு சூல்பிறவி யொன்றெனவும் அண்டர்கள் மெய்க்க அரனடன மாடலுற்றார் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(245,'திருநடன உலா','நாதன் முறையார்க்கு நற்பிறவி யொன்றுபத்தாம்  வேதன் றனக்குப்பிறவி ஆயிரமாம் - வெண்டரள மாதுக்கு மாயிரமாம் மாதுமைக் கோரேழாம் சீதுக்குப் பத்தெனவே செப்பு இந்திரற்கு மூன்றெனவும் இறையவருக் கேழெனவும் சந்திரற் கோரேழாம் சாற்று - நன்றியுள்ள மறையதுக்கு மோரேழாம் மானமுள்ள தொண்டருக்கு இறப்பிறப்பில் லாப்பேறென் றியம்பு என்றிவகை ளெல்லா மிப்படியே வந்துதென்று ஒன்றியுள்ள சித்தாதி ஓதினான் - பண்டையுள்ள தாவடத்தை மேவிடத்திக் கோவிடத்தி லோமிடத்தி நாவிடத்திப் பூண்டாடி னார் தன்னாலே முளைத்த சற்கணையின் றண்வாங்கிப் பின்னாலே யோர்கணைப் பீறி - பின்னாளே பீறுங் கணையதினால் பெரும்புவியெல் லாந்தோன்றிச் சீறுங் கலிவயதால் சென்றுதோ வானத்தளவில் வளர்ந்த கம்பத் தண்ணருளால் சேனைச் செகம்படைத்த செல்வமோ - மானத்த மாகலிய னேதுவினால் வாழ்விழந் திவ்வுகமும் போகத்தருணம் வந்து பூட்டினதிப் போது நல்லாறு சாஸ்திரமும் நாலுமறை வேதமதும்  பொல்லாக் கலியினால் பொய்ச்சூடி - பொல்லாப் பொடியக் கலியோடு பொன்றியடி வேரறவே இடியத்தான் வந்தொத்த தின்று பக்கம் பதினைந்தும் பார்மேடம் பன்னிரண்டும் வக்கணைக் கோளொன்பதுவு மங்கி - அக்கிகொண்(டு) அழிந்த கலியோ டலமாந் தழிந்திடவே சுழிந்தகன லாறுவந்து சுற்றிச்சோ மாந்திர தந்திரத்தை வைத்திய வாகடத்தைச் சூழ்ந்திருந்த அட்சரத்தைச் சோதித்து ஏய்ந்திருந்த இழகு கலியோ டிம்முறையெல் லாமொடுக்கிக் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(246,'திருநடன உலா','களவறுக்கச் சோதியொன்று காணுதோ அய்யமிட் டுண்ணாத அரும்பாவி யாவரையும் பொய்யரையும் வெட்டிப் பெலியாக்கி - மெய்யிழந்து நையுங் கலியோடு அனலாவிக் கொண்டெரிக்க வெய்யவன்போல் சோதியொன்று மேவுதோ பத்திசோ தித்தே பலநாளுங் காத்திருந்த வித்தகனை வந்து வேண்டார்மேல் - வீடிழந்து செத்திறந்து தீநரகச் சீக்கூட்டி லேயடையக் கொத்தியருந் தப்புழுக்கள் கொஞ்சுதோ பாங்கலிய னேதுவினால் பண்டுண்டு செய்ததெல்லாம்        மூங்கிக் கலியதனுள் மூடி - மூச்சுவிட்டு ஓங்குவ தோங்கி உறங்குவது தானுறங்கி மூங்கிக் குளிப்பதுநாள் முற்றுதோ இலச்சைகெட்ட பாவி யென்றுவந்தா னன்றுமுதல் நற்சடலங்கள் நல்வகைகள் நாடிழந்து - நாணமுற்றுப் பட்சிமுதல் மாமிருகம் பால்நரிகள் கற்றாவும் அச்சமற்று வாழ்புவியொன் றாகுதோ பொல்லாத் துரங்கள்கொண்ட பேய்ச்செடிகள் தானொழிந்து கல்லாத புல்லர் கருவொழிந்து - எல்லாம் நல்லோராய்ச் சாகாமல்நாளும் நகரொன் றானதுக்குள் சொல்லொன்றாலாள சோதியொன்று தோணுதோ தாணுமால் வேதன் தற்பொருளாய் முப்பொருளும் ஆணுவமா யொன்றி லடங்குதோ - அஞ்செழுத்தும் ஆனா அரியெழுத்தும் ஆங்கார மூன்றெழுத்தும் ஓநமோ வென்றதுக்குள் ளொடுங்குதோ ஆகாத்த வஸ்துவெல்லாம் அழித்துநர கத்திலிட்டு வாகாய்க் குழிமூட வந்துதோ  சாகாத சனங்கள்பல வஸ்துவையும் தர்மபதி ராச்சியத்தில் இனங்களொன்றாயாள ராசாவொன் றாகுதோ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(247,'திருநடன உலா','பொன்னூற்றுத் தன்னூற்றுப் புரவுதன்னால் விளையூற்று        முன்னூற்று யோசனை யுலாவுசுழி - பன்னூற்றுப் பாலூற்று மேலூற்றுச் சேலூற்று வாலூற்று மாலூற்று மாபதியு மாகுதோ செப்பொத்த பொன்னும் சிவமேடைசிங் காசனமும் முப்பத்தி ரண்டறமு மோங்குதோ - ஒப்பற்ற ஊர்தெருவு மொன்றதுக்குள் ஓர்யோசனைத் தெருக்கள் சீர்பதினா யிரத்தெண் சேருதோ தெருக்கள்பதி னாயிரத்தெண் செந்திருமால் வாழ்பதிக்குக் குருக்களொன்று விஞ்சையொன்று கூறுதலோ - மருக்கள் மாறாமல் வாழ்பதிக்கு மணங்கொடுத்து நிற்பதல்லால் வேறார்க ளும்பறிக்க வேண்டுமோ பொற்மைப் பதியில் பொன்வாச லொன்றதிலே தர்மமணி யொன்று தாங்குதோ - தருமமனு மணியினது கூறறிய மணிகணீரென் றதல்லால் இனியிரு ளில்லா தேகுதோ அலைந்தலைந்த சூரியனும் அவனலையச் சாயாமல் நிலந்தெரிய எப்போதும் நேரே - குலைந்தலைந்த கார்மேகத் தட்டதெல்லாம் கூண்டுடைந்து வானமதில் ஊர்மேகமெல்லம் ஒருமேக மாகுதோ தோணிக்கச் சிந்தை துலங்கிவெளி காட்டுமல்லால்  காணிக்கை யென்றயிறை காணாதோ - மாணிக்கக் கல்லால் வளர்ந்தபதி காணுமொளி யல்லாது பொல்லாரெனப் பேச்சுப் போச்சுதோ ஈசர் நடன மிருபத்து ஒன்றதுக்குள் தேசமது தீதுநலஞ் செப்பினார் - வாசமுடன் வன்னி யமர்த்தி  மாதுமையைத் தானோக்கி உன்னிச் சிவமுரைப் பார் ஈசர்  மகிழ்ந்து ஏந்திழையைத் தானோக்கி வாச முடனீசர் வகுப்பா ரம்மாதோடு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(248,'திருநடன உலா','முன்னே மொழிந்த முற்றாண்ட வமதுக்குப் பொன்னே யிந்நேரம் புரிந்ததுநீ கண்டாயே அப்பொழுது ஈஸ்வரியும் அரனை மிகப்போற்றி இப்பொழுது என்னுடைய இராச மகாபரனே செப்போடு வொத்தச் சிவனே சிவபரனே ஆடினீரே தாண்டவந்தான் அநேக வளங்கள்சொல்லிப் பாடினீரே அய்யா பாட்டு வழுகாதே தாண்டவ மாடிப்புரிந்தால் சத்திசிவம் வரைக்கும் மாண்டாரைப் போற்கிடந்து மறுத்தெழும்ப வேணுமல்லோ அல்லாம லென்னுடைய அண்ணர் நினைப்பதிலே நல்லவரே கொஞ்சம் நானறிந்தது கேளும்  காரணமா யண்ணர் கலியுகத் தைமுடிக்க நாரணர்தான் பூமியிலே நடப்பது நிசமானால் நம்மையு மிங்கிருக்க ஓட்டார்காண் நாரணரும் எம்மழையி லானாலும் எவ்வெயிலி லானாலும் கொந்தலி லானாலும் கூர்பனியி லானாலும் கந்தத் துணிகள்தந்து கையில்திரு வோடுதந்து என்றனக்கு நல்ல இளங்குறத்தி வட்டிதந்து மைந்தன் சரவணற்கு மறிப்பு மிகக்கொடுத்துப் பிரமனுக்குக் கோலமிட்டுப் பிசாசுத னைவிரட்டி வரமுள்ள தேவதைக்கு மாற்றி யுருக்கொடுத்து லோகமதில் நம்மையும் உகக்கோலங் களிட்டி வேகத்துடனே மேதினியெல் லாந்திரிந்து கலிக ளகலக் காண்டங் கழிக்கவென்று பலிகாண வைப்பார்  பரசோ தனைக்கனுப்பி இப்படியே தோணுதுகாண் என்னுடைய  சிந்தையிலே எப்படியும் வந்து இதுசமையு மீசுரரே என்று  உமையாள் ஈசர்தனை வணங்கி அன்று மொழிந்து அவள்நிற்கும் வேளையிலே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(249,'திருநடன உலா','நாரா யணரும் நல்ல சிவனிடத்தில் சீராக வந்து தெண்டனிட்டுத் தானிருந்தார்  இருந்து மாலோனும் இருதயத்தி னுள்மகிழ்ந்து பொருந்துவிழி தங்கையோடு புகலுவா ரம்மானை என்னுடைய தங்கையரே எனைமேனி யாக்குவளே உன்னுடைய தன்பொருட்டால் உலகளந்தோ னானாகி நாட்டி லென்பேரு நடத்திவைத்த நன்னுதலே கோட்டு வரையாளே கோவே யென்தங்கையரே நீயும் நம்மீசுரரும் நீணிலத்தில் போகவென்று நானும் நினைத்திருந்தேன் என்று நவின்றாயே ஈஸ்வரிக்கு மீசுரர்க்கும் இந்த நினைவானால் தேசமதில் போக வேண்டாமென்று சொல்வாரோ நானுங் களைவலிய நாட்டிற்போ மென்றிலனே தானுங்கள் தம்நினைவில் தரித்ததுபோ லேபோவும் நீங்கள் நடப்பதென்றால் எனக்குவெகு சந்தோசம் தாங்கள் மனதிரங்கி சாற்றிமிகப் போவுமென்றார் அப்போது ஈசுரனார் அச்சுதரைத் தானோக்கி மைப்போடுங் கண்ணினிய மைத்துனரே யென்றாவி சொல்லுச் சொல்லாதபடி சொல்லுதற்கு உம்மையல்லால் அல்லும் பகலும் அலைந்தாலுங் கிட்டாது போங்களென்று சொல்லாமல் புத்திசொன்னாப் போலேநீர் தாங்கி யுரைத்தீரோ  சத்தியுள்ள மைத்துனரே  புத்தியிது நன்று புண்ணியநா ராயணரே தத்தியுட னடக்க  தருணமெப்போ தென்றுரைத்தார் நடக்கக் கருமமிது  நல்லதுதா னென்றுசொல்லி அடக்க முடனே அய்யாநா ராயணரும் ஒத்திருந்து தங்கள் உற்பனமா யாராய்ந்து புத்திபோல் வாசகங்கள்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(250,'திருநடன உலா','பூத்தான மாயெழுதி நாட்டு நருளறிய நடைசீவ செந்தறிய காட்டு மரமறிய கல்லப்பு தானறிய புற்பூ டறிய பூமிதெய் வாரறிய நற்பறிந்து பேய்கள் நடுங்கி மிகஅறிய சேட னறிய சிறுவாய்வு தானறிய மேக மறிய மேலோர்கள் தாமறிய பூக மறிய பொழுதுசந் திரனறிய நட்சேத் திரமறிய நமனறிய கொளறிய பொய்ச்சரியை கிரியை பேர்நா லுந்தானறிய வேத மறிய விளங்கு மறையறிய வாதை யறிய மன்றுபதி னாலறிய சங்கறிய முத்தறிய சகலமச்ச முமறிய அங்கறிய நாம்படைத்த எல்லோருந் தானறிய எழுதி விடுவோங்காண் ஏற்றரிய புத்திவைத்து  பழுதுவைத்தா ரென்று பார்பலதுஞ் சொல்லாமல் நம்பேரில் குற்றம் நகத்தளவு மில்லாமல் பம்முதலாய் நாமும் பரிந்தெழுதி தான்விடுவோம் என்று திருமாலும் இறவாத ஈசுரரும் மன்று படைத்த மறையோனும் பார்வதியும் சரசு பதியும் சத்தியுள்ள லட்சுமியும் விரசு முனிமாரும் வேத மறையோரும் சாஸ்திரரும் கின்னரரும்  சங்கத் துறைவோரும் பார்த்திவனுந் தெய்வ பாவாணர் தாமுதலாய் வானவருந் தேவர்களும் மற்றுஞ்சித் தாதிகளும் தானவரும் வேதத் தம்புருக் காரர்களும் சங்கம் வரையும் தாண்டவர்கள் தாமுதலாய் எங்கெங்கு  முள்ள இருஷிமுதல் தான்வருத்தி எல்லோருங் கயிலை இடம்விட் டெழுந்தருளி வல்லோர்கள் வாழும்  வைகுண்ட மீதில்வந்து கங்கைநதி கண்டு கறைக்கண்டர் தேவர்வரை கங்கை தீர்த்தமாடி கிருஷ்ணர்  பதிபுகுந்து மேடை விதானமிட்டு மிகுமேடை ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(251,'திருநடன உலா','பொன்னழுத்தி வாடை கமழவிட்டு வானவர்கள் பாடவிட்டு எக்காள டம்மானம் எங்கு முழங்கவிட்டு  மிக்க புராணக்கலை மிகுமறையோர் போற்றவிட்டு ஆகமங்கள் போற்றவிட்டு அரம்பையர்கை காட்டவிட்டுப் போகமுனி சித்தரெல்லாம் புராணகவி யோசையிட்டு மும்முதற்குத் தேவரெல்லாம் முறையிட்டு வாழ்த்தவிட்டுச் சங்குத் தொனிகளிட்டு சகலோரும் போற்றவிட்டு இத்தனை நற்சிறப்பும் இதமா யலங்கரித்து வித்தையருள் தண்டரள விழியாளைத் தான்வருத்தி அப்போ சரசுபதி ஆனந்த மேபெருகி இப்போ எழுந்தருளி இன்பமுடன் போகவென்று கங்கை நதிமூழ்கி கிரணப்பச்சை யாடைபெய்து சங்கையுட னாபரணம் சரசு பதியணிந்து குழல்முடித்து குழையில் குவிந்ததங்கத் தோடணிந்து வளையல் முறுக்குமிட்டு மாதுசந் தோசமுடன் பச்சைநிறக் காப்பணிந்து பவளமணித் தண்டையிட்டு மச்சமதில் மையெழுதி மகிழ்ந்து மூக்குத்தியிட்டுக் கொண்டைதனில் தேமலரைக் குவிய மிகச்சூடி பொற்சரிகைச் சேலை பெரியயேத் தாப்புமிட்டு அச்சரக்கைத் தோழி அரம்பையர்முன் னாடிவர அன்ன நடைநடந்து அனல்கொழுந் திட்டாப்போல் வன்ன அழகுடனே வந்தாள்திரு முன்பதிலே  மாது வரவே மறையோனும் மாயோனும்  ஏதுகோ லமெனவே எண்ணமதி லுன்னினராம் உன்னித் திடமாய் உளமகிழ்ந்து மாயவனும் கன்னி தனைப்பார்த்துக் கறைக்கண்டர் தானுரைப்பார் வன்னத் திருவடிவே மறையோனி னோவியமே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(252,'திருநடன உலா','அன்ன மடமாதே அரசுக் குகந்தவளே கருத்துடைய சோதி கவியுடையீ ருந்தனையும் வருத்தின காரியந்தான் வகுக்கக்கே ளொண்ணுதலே நாட்டிலொரு நீசன் நன்றியறியாப் பிறந்து மேட்டிமையாய் லோகமெல்லாம் மெய்யழித்தான் கண்டாயே கோட்டிப் பவம்பிறந்து குருநீதி யைக்கெடுத்தான் கேட்டிருக்கக் கூடாது கெடுநீசன் பொல்லாங்கு நம்மை நினையாததுதான் நமக்குசற் றெண்ணமில்லை செம்மையுள்ள சான்றோரைச் செய்தபங்க மேற்கலையே தெய்வப் பிறவியல்லொ திசைவென்ற சான்றோர்கள் மெய்வரம்பை யெல்லாம் மிகக்கெடுத்தான்மாபாவி கற்பகலாப் பெண்களுட கற்புக்கிரங் காதிருந்தால் உற்பனமோ நாமள் உடையபர னென்றாமோ எளியவனை வம்பால் இடுக்கஞ்செய் தேயடித்தால் விளியிட் டழுதாலும் மேதினியிற் கேட்பதில்லை  கேளாத காரணத்தால் கீர்த்திகொண்ட பேரெளியோர் நாளான நாள்வழியாய் நம்பிநம்பேரில் வைத்தார் அல்லாமல் நம்முடைய அரிதன்மகா நாமமதை இல்லாம லேமறைத்தான் இயல்புகெட்ட மாநீசன் ஆனாலும் பாரமில்லை அன்னீதம் பொறுக்கரிது மானாபர ஞாயமதை வரம்பழித்தான் மாநீசன் ஆகமத்தைக் கூறழித்தான் அன்னீத மாபாவி தாக மடைந்தார்கள் தவசுமுனி மார்களெல்லாம் நீசப் பாசாசு நீணிலத்தைக்  கொள்ளைகொண்டு தோசப் பயலோடு துணையாய் மிகக்கூடிக் கெடுக்குதுகாண் பசாசு கேட்பார்க ளில்லாமல் கொடுக்கவொன் றில்லா நருட்பார்த்துக் கொல்லுதுகாண்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(253,'திருநடன உலா','அதுவே யொருகொடுமை அநேகம் பொறுக்கரிது எதுவும் வரம்புதம்பி இருக்குதுகாண் வையகத்தில் ஆனதால் நாங்கள் அவனிதனிற் போயிருந்து மானமுறை சோதித்து மனுவிடுக்கந் தான்தீர்த்து நாட்டிலுள்ள குற்றம் நாங்கள்பார்த் துத்தீர்த்து ஓட்டுவதை யோட்டி உறைப்பதை யுறைப்பித்து நாடையொரு சொல்லதுக்குள் நலமாக வாழ்விக்கவே         பேடையன்ன மாமயிலே பெண்ணரசே இப்போது  நாங்கள் நடப்பதற்கு நாளான நாளிதுவே ஆனதால் பாவி அவனிழுக்குச் சொல்லாமல் மானமுள்ள புத்தியைப்போல் வாசகமொன் றேயெழுதி வருவோமென அயச்சால் மாபாவி தானறிந்து கருவி குழறி கற்பினைக்குள் ளாகுவதும் என்னவோ வென்று இடும்புசெய்வ துமறிய அவனறிய விட்டதுபோல் அவனிபதி னாலறிய எவரும்புற் பூண்டுவரை எவ்வகையுந் தானறிய எழுதியொரு வாசகந்தான் இப்போவிட வேணுமென்று முழுது முன்னிங்கே ஓடிவா வென்றதுதான் அப்போ சரசுபதி ஆனஅயன் மாமயிலும் இப்போ திவ்வகைக்கு யானென்ன செய்கருமம் எல்லாங் கிருபை எம்பரனே யுன்செயலு அல்லாம லென்றனக்கு ஆகுமோ ஏதாலும் ஆட்டுவதும் நீயல்லவோ ஆடுவானும் நீயல்லவோ ஓட்டுவதும் நீயல்லவோ ஓடுவானும்நீயல்லவோ  சொல்லுவதும் நீயல்லவோ சொல்லவைப்பானும் நீயல்லவோ அல்லும்பகல் நீயல்லவோ  அழியாத வனும்நீயல்லவோ எல்லா முமது கிருபை யெனக்கல்லாது சொல்லீது வந்ததென்ன சுவாமியெனப் பணிந்தான்  ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(254,'திருநடன உலா','மாது திருமால் மலர்ப்பாதம் போற்றிடவே ஆதித் திருமால் அவரேது சொல்லலுற்றார் நீயுரைக்க நானெழுதி நீணிலத்தில் விட்டதுண்டால் வாயுரைக்கக் கூடாது மற்றோர்க ளாராலும் வம்புரைக்கக் கூடாது வம்புரைத்தால் வன்னரகம் உம்பதுவே யல்லால் உறுகெதிகள் காணார்கள் ஆனதா லுன்வாக்கும் அரியச்சரமுங் காண்பித்தால் மானமென்ன வாகுமென்று வகுத்துரைத்துப் பார்ப்போம்நாம் அப்போ சரசுபதி அன்போ டுளமகிழ்ந்து செப்புகிறாள் நல்ல திருவாசகந்தா னம்மானை கலையொடு கலையைத் தாக்கிக் கண்ணின்மேல் கருணை நாட்டி மலையொடு மலையைத் தாண்டி வளர்ந்தவன் பதமே கண்டு சிலையொடு சிலையி னாளும் சிவபரா கிருபை யென்றே அலைதலை முழக்கம் போலே அவளுரை கூற லுற்றாள் கூறிய வாசகத்தை யிந்தக் குவலயத் தோர்கள் காண மீறிய மொழிகள் சொல்லி விளம்புவர் நாச மாகும் தேறிய பூதத் தோடு தேர்ந்துணர்ந் தவரே வாழ்வார் கூறிய வாசகத்தை யுள்ளங் கொண்டவர் குருவைக் காண்பார் ஒருதிரு விபூதி யுண்டை ஒருயிரு மிச்ச மாகும் ஓருதிடத் தேங்காய்ப் போலும் ஒருமனத் திரணை போட்டுக்   கருதிட வெற்றிலை பாக்கும் கனிந்திடு வோர்க்கு மூவர் வருகிட ஞாய மெய்யின் வழியிது வாகுந் தானே மனுமொழி யிதுவா மென்று மதத்துடன் பேசு வோர்க்கு இனிதல்ல வீண்தா னென்று இயம்பிய பகைஞர் தம்மை குனிதவள் துர்க்கை சென்று கொன்றவள் நரகம் பூத்தி கனிதுடன் துர்க்கை வாரி கடல் தீர்த்த மாடுவாளே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(255,'வேறு','எறும்புகடை யானைமுதல் எண்பத்துநான் குயிர்கள் எழுகடல் பதினாலு புவிகளும் இரதிமதி சூரியர்கள் பருதி பாலாழியும் இயல்வானம் வாயு முதலாய் தெறும்பு மாமலை மாமரச் சோலையும் சேடனுந் தலைமோடனு மறியவே தென்கீழத் தேவரு மிங்குள்ள மூவரும் தேசதெய் வேந்திர னறியவே வாச முனிவோர்களும் வேதசன் னாசியும் மறையாறு சாஸ்திர மறியவே மண்டல மளந்தகை கொண்டெழுதும் வாசகம் மண்டலர்க ளெவரு மறியவே வறும்பகல் தொளாயிரத்து தொண்ணூற் றெட்டாண்டினில் வளர்ஸ்ரீ சாம்பசிவ மூர்த்தியும் பராபர ஸ்ரீராமரும் பதியேறும் மூர்த்தியும் பார்த்திந் தக்கலி யுகத்தில் படூரநீ சக்கலியால் வரும்வாறு வண்மையாய்ப் பகர்ந்து திருவாசக மெழுதிப் பலநூ லறிந்தவ ரெவர்க ளாயினும் பக்தியுட னெக் காலமும் பணிந்துதிரு வாசகத்தை முத்தியணைந் தோர்க்கு மிகுபல னுண்டாம் பகரக்கேளு நத்தியுடன் பூலோகக் கலியுகா தேசத்தில் நடக்கு முறைதானுங் கேளீர் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(256,'திருவாசம் - 1','நல்ல வீரபுரந்தரத் தர்மராசா வங்கிசத்திலே நாடும் ஒரு மதலை பிறந்து வந்தவுடன் நல்ல அருணாசலத்திலே வாலிபப் பிள்ளையாயிருக்கிறார். அந்தப் பூலோகக் கலியுகத்திலே ஆசாரமாயிருக்கிற பேர்களும் அழுக்கான புத்திமதியா யிருக்கிற பேர்களும் அவரவர் ஆங்காரமாகியே அழிந்து போவார்கள். அதின்மேல் பதினெட்டுத் துர்க்கையம்மாள் பிறந்து அந்தக் கலியுகப் பஞ்சமிர்த ராச்சியத்திலே வருகிறார்கள். வந்தவுடனே மூன்றுநாள் இருள்மூடி ஆனைத் துதிக்கைபோல் மழைபெய்யும். அதிலே சில துர்ச்சனர்களெல்லாம் மாண்டு போவார்கள். அதின்மேல் பக்தியாயிருக்கிற பேர்களுக்கும் பிள்ளை யில்லாமலிருக்கிற பேர்களுக்கும் கண்ணில்லாமலிருக்கிற பேர்களுக்கும் தனமில்லாமலிருக்கிற பேர்களுக்கும் பராபர மூர்த்தியும் சாம்பசிவ மூர்த்தியும் ஸ்ரீராமச்சேயரும் தீட்சையாகித் திருமனதிரங்கிப் பக்திகாரணங்களைச் சோதித்துப் பகிர்ந்து அவரவர் கேட்ட வரங்களைக் கொடுப்பார்கள். இன்னுஞ்சிறிது நாளையிலே சிலபேர்கள் தெய்வீகமாய்ப் போவார்கள். முன்னுக்கு மழைதட்டும் உலகிற்பல பல வஸ்துவும் பலிக்குமென வகுத்தார். சிலநீசர் மிகக் கறுப்பை நினைவில்லாத்தொட்டு மிகப் பாசமடைந்து அலைந்து அழிந்து போவார்கள். வாலி சுக்ரீபனும் பண்டாரமாகிப் போவார்கள். வாச்சி கொழு கலப்பையெல்லாம் நாசமாகிப் போய்விடும். முன்னாலே துலுக்கர் தம்மை நாசம் பண்ணுகிறதற்காகவே துர்க்கையம்மாளைப் பிறவி செய்தனுப்புகிறோம். பிராமணர் நன்றாய் சுகத்துடன் வாழ்வார்கள். புவியில் முகத்துலிங்க மில்லாத பேர்க்குப் பிரமதேவரை யனுப்புகிறோம். பிரமதேவர் புவிமீதில் வந்து பக்தி காரணங்களைச் சோதித்துப் பொல்லாத பேரைத் தெரிந்து பிடித்துப் புதுக்கிராம தேசத்தில் வாழுந் தேவதைக்குப் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(257,'திருவாசம் - 1','பூசைப்பண்ணிப் போடுவாரெனப் புகட்டினார். காவேரியாற்றுக்குள்ளே மூன்று பொதி மங்கிலியம் கவிழ்ந்து அடையவேணுமென்றும் காசினியில் ஒரு ஏழு பெண்பிள்ளைகள் ஒரு ஆண்பிள்ளையை அடர்ந்து பிடிப்பார் களென்றும்,  அறுத்த மங்கிலியத்தை மிகுத்துக் கட்டுவார்களென்றும், இன்னுஞ்சிறிது நாளையிலே கேள்விகேளாமலிருக்கிற பேர்களும் துர்ச்சனராயிருக் கிறபேர்களும் வர்த்தகனாயிருக்கிற பேர்களும் தம்மில் ஒருவருக்கொருவர் சண்டைபோட்டு மாண்டுபோவார்கள், போனபேர்கள் போக இருக்கிற பேர்கள் புண்ணிய புருஷரைத் தெரிசனம் பண்ணிக் கொண்டு சர்வபாக்கியத்திலே யடைவார்கள். மூன்று லோகத்துக்கு ஒரு வால்வெள்ளி யுண்டாக்கி நெருப்புப் போட்டுக் கொண்டு வருகிறோம். அதினால் மானிடரெல்லாம் உறழ்ந்து உட்கொள்ளைப் பிறக்கொள்ளை யடிப்பார்களென்று நமக்கு நன்றாய்த் தெரியும். நாமதற்குமேல் பூலோகக் கலியுகப் பஞ்சமிர்த ராச்சியத்திலே பண்டார வேசமாய் வருகிறோம். நாம் வருகிறபோது மண்ணெல்லாம் கிடுகிடென்றாடும், மலையும் வானமும் முழங்கித் திடுக்கிடும், அப்போது அதிலே அநேக துர்ச்சனர்களெல்லாம் மாண்டு போவார்கள். போனபேர்கள் போக இருக்கிறபேர்கள் புண்ணிய புருஷராய் இருப்பார்கள். மந்திர தந்திர வைத்தியங்களெல்லாம் மறைந்து போய்விடும். வாதை பேய்களெல்லாம் வட கயிலாசத்திலே போய்ச் சட்டுத்தீர்ந்து போவார்கள். ஏழு சமுத்திரத்திலே மூன்று சமுத்திரம் நீருள்வாங்கிப் போய்விடும். ஒரு சேர்வை விபூதி ஆறு சக்கரத்திற்கு விற்கவும், ஒரு லிங்கம் மூன்று வராகனுக்கு விற்கவும், ஒரு கழஞ்சிச் சலமெடுக்கவும், இரண்டு நாழிகை வழிக்கு  ஒரு தண்ணீர் பந்தலும், மூன்று நாழிகை வழிக்கு ஒரு தர்மசாலை மடமும் நன்றாய் முடியும். ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(258,'திருவாசம் - 1','தர்மங்கொடுக்கிற பேர்களுண்டு வாங்கிறபேர்களில்லை யென்று சொல்லுகிற வெயிலாள் மடக்கொடியாள் வயிற்றிலே நாடுமொரு மதலை பிறந்து பத்து வருசமாச்சுது. நாங்களும் மகா அருணா சலத்திலே வாலிபப் பிள்ளையா யிருக்கிறோம். அந்த வாலிபப் பிள்ளை என்ன சொல்கிறா ரென்றால் வீர புரந்தர தர்மராசா வழியிலே விகிநாராயணர் இங்கே வந்து மூன்று மாதத்துக்குத் தவசுபண்ணி இரண்டு மாதத்துக்கு மேல் வருகிறார். வந்தவுடனே அந்நியோன் னியக் கலகமாகும்.  அந்தக் கலகத்திலே ஒருவருக்கொருவர் சத்தியமாய்ப் போவார்கள். போன பேர்கள் போக இருக்கிற பேர்கள் புண்ணிய புருஷராய் இருப்பார்கள். அவர்களுக்கு வேண்டிய பாக்கியத்தைக் கொடுப்போம். பாக்கியங் கொடுப்போம் நாமும் பலனுடன் வாழு வோர்க்கு நோக்கிய கருணை யுண்டாம் நோயில்லா திருந்து வாழ்வார்     தாக்கிய வாச கத்தின் தன்மையை நம்பு வோர்க்கு வாக்கிய வைகுண்ட வீடு வந்தவர் வாழ்வார் தாமே தினமொரு நேர மெந்தன் திருமொழி யதனைக் கேட்டால் பனிவெள்ளம் போலே பாவம் பறந்திடும் நிசமே சொன்னோம் கனிமொழி சோதி வாக்கும் கையெழுத் தாதி நோக்கும் துணிவுடன் கேட்டோ ருற்றோர் தொலைத்தனர் பிறவி தானே வாசித்தோர் கேட்டோ ருற்றோர் மனதினி லுணர்ந்து கற்றோர் ஆசித்தன் பதமே கண்டு அவ்வழி முறையே நின்றோர் கோசித்தன்  பதமே கண்டு கோமலைப் புவியின் வாழ்வும் தேசத்தின் செல்வத் தோடும் சிறப்புட னிருந்து வாழ்வார் திருமொழி வாசகந் தன்னைத் தேசத்தில் வருமுன் னாக வருவது திடனா மென்று வழுத்தினோம் தொளாயிருத்து 98ல் ஒருதிருக் கூட்ட மாக ஓராயிரத் தெட்டா மாண்டில் வருகென வந்து நாங்கள் அம்மானையில் வருத்தி னோமே வருத்தினோ மம்மானை ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(259,'திருவாசம் - 1','தன்னில் மானமாய்ப் புதிய தாக்கிக் கருத்தினுள் ளகமே கொண்டு கவனித்தோ ரவர்க்கே தக்கும் உருத்தில்லாக் கேட்போ ரெல்லாம் ஓருரை வெளியே காணார் சிரித்துரைக் கேட்போ ரெல்லாம் சிவபொருள் வெளியே காண்பார் உலகில் மனுக்கள் தமிழாலே உவமை யுரைத்து விட்டாப்போல் கலக முடனே யென்மொழியைக் கண்டு பழித்து நகைத்தோரை  அலகைத் துளைத்து நரகதிலே ஆணி யறைந்து அவனிதனில் குலையக் குலைத்துத் தீநரகில் கொண்டே போடச் சொல்வேனே எந்தன் மொழியு மென்னெழுத்தும் ஏடாய்ச் சேர்த்து இவ்வுலகில் சிந்தை மகிழ்ந்த அன்பருக்குத் தெரியத் திறமா யெழுதி வைத்தேன் எந்தன் பெருமான் திருமொழியை எடுத்தே வாசித் துரைத்தோரும் சந்த முடனே வாழ்ந்துமிகத் தர்ம பதியுங் காண்பாரே காண்பார் தர்மக் கண்காட்சை கண்டே மரண மில்லாமல் காண்பா ரென்றுங் களிகூர்ந்து கண்ணோன் பதத்தைக் கண்ணாடி காண்பார் நீதக் கண்ணாலே கருணா கரராய்க் கவ்வையற்றுக் காண்பா ரென்றுங் கயிலாசம் கண்டே நன்றாய் வாழ்வாரே இந்த மொழியைத் தூஷணித்த இடும்பர் படும்பா டதுகேளு கந்த உலகுக் கலிபிடித்துக் கண்ணு முருகிக் காலுழன்று குந்தக் குடலும் புறம்பூற கொப்புள் சிலந்தி யுண்டாகி எந்த இடமும் அலைந்தழிவார்   என்னாணை இது தப்பாதே தப்பா தெனவே சாபமிட்டேன் சத்திபேரி லுண்மை யதாய் எப்பா ரெல்லா மறிந்திடவே இந்த மொழியை எழுதிவைத்தேன்  ஒப்பா ரொருவ ரெழுதார்கள் உலகில் மனுக்கள் தன்னாலே அப்பா நாத னெழுதிவைத்த அகிலத் திரட்டம் மானையிதே என்றே யிந்தத் திருவாசகம்  இயம்பச் சரசு பதிமாது கன்றே மேய்த்தோ னெழுதிமிகக் கலியுக மதிலே விட்டிடவே நன்றோர்  மறையோ னிடமேகி நாட்டி லறியச் செய்யெனவே அன்றே அவனி தானறிய அனுப்பி மகிழ்ந்து இருந்தனரே ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(260,'திருவாசம் - 1','நாடறிய எம்பெருமாள் நல்லதிரு வாசமிட்டு லாட மதிலிருந்து வாவுவா ரீசரொடு வாரமில்லை நமக்கு வஞ்சகங்க ளில்லாமல்  சார முடனே தரணிதனி லோதிவைத்தோம் அகப்பே ரெனவே அவனிசொல்வ தில்லையினி உகப்பாரந் தீர்க்க உரைப்பீர்கா ணீசுரரே லட்சுமியை முன்னம் இருத்தினோஞ்செந் தூர்க்கடலில் பச்சணியுங் காளிதனைப் பாரச் சிறையில்வைத்தோம் என்மகவாய்ப் பெறவே இராச்சியத்தி லோர்சடலம் தன்மகவா யங்கே தான்பிறந்து நல்லவயசு இருபத்து நாலு என்றாச்சே மாநிலத்தில் கருவுற்ற முத்தோசம் கழிவதும் நாளின்றாச்சே விட்டதிரு வாசகந்தான் மேதினியோர் கொள்ளாமல் கட்டங் கொடிதாய்க் காணுதே தொல்புவியில் புத்திவரு மென்றிருந்தோம் பிழையாத நீசனுக்கு  சற்று மனதில் தங்குமோ நல்வசனம் பார்த்திருந்தால் போராது பார்மீதில் மக்களுந்தான் காத்திருந்து வாடுகிறார் கௌவையுற்று நல்மனுக்கள் மனுவிடுக்கந் தீர்த்து வாய்த்தசொல் லொன்றதுக்குள் தோணியேவை குண்டர் சுத்தயுக மாளுதற்கு  நாளுண்டோ போவதற்கு நல்வேதச் சொல்லுமுண்டோ கோளுண்டோ வெற்றியுண்டோ கூறும்நீர் வேதவுரை அப்போது ஈசர் அகமகிழ்ந்து கொண்டாடி முப்போது வுள்ள முறைகேட்க வேணுமென்றால் ஆதி வியாகரரை அழைப்பித்துக் கேளுமென்றார் சோதி யுரைக்க திருமா லகமகிழ்ந்து அழைத்தார் வியாகரரை அதுகேட்டு மாமுனியும் பிழைத்தார்கள் போலே  பொடுபொடென ஓடிவந்து தெண்டனிட்டு மூவரையும் சுவாமிவிண் ணப்பமென்றார் கண்டிருந்(த) அய்யா கரியமுனி யைப்பார்த்து முன்பிறந்த துமகிலம் முடிந்ததுவுஞ் கொல்லிமிகப் பின்பிறக்கப் போவதுவும் பிசகாமல் சொல்லுமென்றார் அப்போ வியாசர் ஆதி யருளாலே செப்புகிறோ மென்று தெண்டனிட்டுச் சொல்லலுற்றார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(261,'வேதவியாசர் முன்னாகமம் கூறல்','குறோணி முதலாய்க் கூறுகெட்ட நீசன்வரை தரணியுக மாறுஞ் சாற்றியே-சத்தியுடன் தன்மபுவி தோன்றுவதுஞ் சாணாரை வைந்தர்வந்து நன்மையுட னாடுவதும் நாட்டினார் குறோணியொடு நீசன் கோள்பிறவி ஏழ்பிறந்து தரணியுக மாறோடு தன்னால ழிந்ததுவும்  சொல்லி விரித்துச் சுத்த வியாகரரும்  நல்லியல்பு கொண்ட நாரணரோ டேதுரைப்பார் கொற்றவரும் மாண்டு குறும்பு மிகப்பெருத்து உற்ற நசுறாணி உடன்வந் துடனோடி  மற்றொரு பத்தாண்டில் ஆனவை குண்டராசர் உற்றொருவர் வந்து உலகாள வேணுமென்றும் முன்னே பரந்தான் மொழிந் ததின்படியே  தர்ம புவியாள சுவாமிநீ ராகவென்றும் துர்மக் கடன்கழித்தோர் சுகமாக வாழ்வரென்றும் ஆகமத்தைப் பார்த்து ஆதிவி யாசுரரும் நாகத்தணை கிடந்தோன் நாட்டம தாயுரைத்தார் உரைத்திட வியாசர் தானும் உண்மையாய் மொழிந்த தெல்லாம் கரைத்திடாக் கயிலை மீது கல்லிலே எழுதி வையும் நரைத்திடா முனிவன் சொல்லும் நாள்வழி தோறு முற்ற உரைத்திட முனிக்கு மேலும் உதவிகள் செய்ய வென்றார் என்று சொல்லிவேத வியாசர் இசைந்ததெல்லாம் கண்டுகயி லயங்கிரியில் கறைக்கண்டர் நாட்டிவைத்தார் மாமுனியை யுமனுப்பி மாயத் திருமாலும் தாமுனிந் தீசுரரைத் தழுவியுறத் தானணைத்துக் கேதாரம் விட்டுக் கிரிகோவில் வந்திருந்து  கோதார மாயன் கூறுவார் சங்கமதில் செந்தூர்க் கடலில் சென்றுபள்ளி கொண்டிருந்து விந்து வழிசெய்து வித்தகனை ஈன்றெடுத்துத்  தேசபர சோதனைக்கு தெச்சணத் திலேயனுப்பி வாசமுட னிங்கே வருவோ மெனவுரைத்தார் அப்போதீ சுவரியும் ஆனசங்கத் தோரர்களெல்லாம் இப்போது நாங்கள் ஏகுவோ மும்மோடே ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(262,'வேதவியாசர் முன்னாகமம் கூறல்','என்றுரைக்கச் சங்கம் எடுத்துரைப்பா ரெம்பெருமாள் ஒன்றுமறி யாகாதே உற்றவுப தேசமதைத் தேவன் திருமுதலாய்த் தெய்வகன்னி மார்முதலாய் மூவர் முதலாய் உள்ளறியா விஞ்சையது நடந்தா லறிவார் நடக்குமுன் னேயொருவர் அடக்க மறியாத அருவிஞ்சை யீதல்லவோ அந்தவகை யானதினால் எல்லோரு மென்கூட வந்தால் சரியல்லவே வரவேண்டாமென்றால் நோவீர்களே என்றுரைக்க நாதன் எல்லோரு மேதுரைப்பார் ஒன்றுமறி யாதிடினும் உம்முடனே வாறொமென்றார் உடனே திருமால் உற்றலட்சு மிநினைவால் நடைமேல் நடையெனவே நல்லசெந்தூர் தானோக்கி உன்னி சிவமும் உற்றசத்தி நல்மாதும் வன்னத் தேவர்முதலாய் மாமுனிவர் கின்னரரும் சங்கமு மெங்குளோரும் சகலகலை வாணர்களும் மங்களக் காரர்களும் மாதுசத்தி யைச்சூழ்ந்து திரைதிரையாய்க் கன்னியர்கள் சேவித்து ஏத்திவர ஏழு வாச்சியமும் இமலோக ரேற்றிவர தொழுவார் சிலபேர் தொம்தொ மெனஆடி கடலிலோர் பிள்ளை கரியமா லீன்றெடுத்து நடமாடுந் தெச்சணத்தில் நகர்சோதனைக் கனுப்பி கலியை யறுத்துக் கனாப்பயங்கள் தானறுத்துச் சலிவில்லாத் தர்மபதி சத்திமக்க ளையாள வாறாரைய்யா நாதன் வைகுண்ட மும்மூர்த்தி  பேறானோர் காண்பார் பெரியதர் மப்புவியை என்றுசில வாத்தியங்கள் இசைந்திசைந் தூதிவர கன்றுதிரை மேய்த்தோரும் கயிலையங் கிரிகடந்தார் கடந்து திருச்செந்தூர் கடல்காண வேணுமென்று நடந்தாதி நாதன் நல்லீசர் சத்தியோடு கூடிக் குணமாய்க் கொலுவார பாரமுடன் தேடித் திருவைச் சிணமாய் வழிநடந்தார் நடக்க மறையோர் நாற்றிசையும் போற்றிநிற்க  கடற்கரையை நோக்கிக் கண்ணோன் வழிநடந்தார் வழிநடந்து மாயவரும் வல்லபர மேசுரரும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(263,'வேதவியாசர் முன்னாகமம் கூறல்','களிகூர்ந்து மாதுமையும் கந்தன்செந் தூர்கடலின் அருகேயொரு காதம் அவர்வரக்கண் டாறுமுகன் கருவி குழறி கடற்கரையோன் தான்கலங்கி  ஆறு முகனும் அங்குள்ள தேவர்களும் வீறுமயில் வாகனனும் வெற்றிரத மேறாமல் என்னவித மாமோ என்று மனம்பதறி மன்ன னறுமுகனும் மனமயங்கித் தான்பதறி வந்துமா மன்தனையும் மாதா பிதாவையும் சந்துஷ்டி யாகித் தாழ்ந்துநமஸ் காரமிட்டு இம்மூ வரையும் யானெப்போ காண்பேனென்று எம்முதலே நாயடியேன் எத்தனைநாள் காத்திருந்தேன் காத்திருந்த நாளும் கரையெண்ணக் கூடாது பார்த்திருந்த கண்ணின் பாவந் தொலைந்ததின்று என்று வேல்முருகன் ஈசுரரை யுந்தழுவி கன்றுதிரை மேய்த்த கண்ணரை யுந்தழுவி மாதாவை யுந்தழுவி மனமகிழ்ந்து கொண்டாடிச்  சீரான தேவரையும் சிறப்பித்துக் கொண்டாடிச் சீரான தேவரையும் சிறப்பித்துக் கொண்டாடி வாருங்கோ அய்யாஎன் மண்டபச்சிங் காசனத்தில்  பாருங்கோ அய்யாஎன் பதியி னலங்காரம் நீங்க ளிருக்க நிறைந்ததங்க மேடையுண்டு  தாங்கள் கிரிபோலே தங்கமலை யிங்குமுண்டு பாடப் படிக்கப் பாவாண ரிங்குமுண்டு ஆடக் கைகாட்ட அரம்பையர்க் ளிங்குமுண்டு  பாலேக்க நல்ல பாலு பழங்களுண்டு மாலேக்க நல்ல மாதுகன்னி மார்களுண்டு கண்டு களித்திருக்கக் கனக நிதிகளுண்டு உண்டு சுகித்திருக்க உற்றவகை தானுமுண்டு பல்லாக்கு முண்டு பதிபோகி மாருமுண்டு குல்லாக்க ளுண்டு குளிக்கத்தாம்பி ராழியுண்டு சதுரங்க மேடையுண்டு சண்முக விலாசமுண்டு பதிரங்க மானப் பாலாழி யுண்டுமையா மாதமொன்று தன்னில் வருங்கோடி பொன்னதிகம் போத வருங்காண் பொற்சவடி யாபரணம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(264,'வேதவியாசர் முன்னாகமம் கூறல்','வருசமொன் றானதிலே மாலைவட மாயிரந்தான் கருவலங்க ளின்னதென்று காணாத் தொகையதுதான் காவடி கோடி காணிக்கை முக்கோடி பார்க்கோடி கூடி பலசாதி யெண்கோடி இப்படியே யென்றனக்கு இருக்குதுகாண் பாக்கியங்கள்  எப்படியும் நீங்கள் இங்கிருக்க வேணுமென்றான் கேட்டுநா ராயணரும் கீழ்ச்சுண் டசையாமல் நாட்டமுட னுள்ளத்(து) அடக்கிமறுத் தேதுரைப்பார் நல்லதுவே மருகன் நலமா யிருப்பதுதான் பொல்லாது செய்யாமல் புரிந்தாள் வதேபோதும்  என்றுரைத்து அய்யா ஈசரோ டேதுரைப்பார் பண்டுவிட்ட வாசகச்சொல் பற்றிச்சோ பூமியிலே  வல்லாண்மைக் காரருக்கு மதமிப்ப டியிருக்கும் இல்லாதெளி மைகட்கு இருக்குமது மேல்தயவு என்று இருபேரும் இயம்பி மனதடக்கி நன்றெனவே கந்தனொடு நல்வார்த்தை யும்பேசி அந்த முடனே எல்லோருந் தானடந்து செந்தூ ரலைக்கரையில் சேர்ந்தனர்கா ணம்மானை கடற்கரை தனிலே வந்து கரியமா லீச ரோடும் மடக்கொடி உமையா ளோடும் மறைமுனி தேவ ரோடும் குடக்கலை பொருந்தும் வேதக் கூர்முனி ரிஷிக ளோடும் கடற்கரை தனிலே வந்து கண்டனர் கடலைத் தானே வந்தவ ரெல்லாப் பேரும் வட்டமிட் டதிலே நிற்கச் சந்தன வாரி யோரம் தன்னிலே  நின்று ரண்டு சுந்தர முனிவர் வந்து சுவாமிதன் பதமே பூண்டு  எந்தனின் பிரானே யென்று இருவரும் வணங்கிச் சொல்வார்  ஆதியே யெங்கள் அய்யாநா ராயணரே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(265,'கலைமுனி ஞானமுனி வரவு','சோதியே யெங்கள்தவம் சுறுக்கிட் டுருவளர்ந்து கண்டுகொண் டோமையா கமலப் பொருளேநீர் பண்டு மொழிந்தபடிப் பார்த்துவரந் தாருமையா என்று முனியிருபேர் இறைஞ்சித் தொழுதிடவே மன்று தனையளந்தோர் மறுத்துரைப்பா ரன்போரே ஏதுகாண் மாமுனியே இங்குநின்ற வாறேது தாது கரமணிந்த சன்னாசி சொல்லுமென்றார் அப்போது மாமுனிவர் அவரேது சொல்லலுற்றார் முப்போது நாங்கள் மும்முதற்சொல் மாறாமல்  மேலோகம் விட்டு மேகவண்ணா வுன்செயலால் பூலோக மீதிறங்கிப் பொங்குகடல் கண்டணுகிச் சோதி யுரைத்த சொல்லுரைத்து வாரியிடம் நீதியாய் நில்லுமென்று நினைவாகச் சட்டமிட்டுக்  கயிலை யதேக கண்ணோக்கும் வேளையிலே அகில மதிற்கலியன் அனைமிகக் கண்டேதான் மேலோக மேற மேல்வழிகள் காணாமல் பூலோக மானதிலே போயிருந்தோங் கண்கவிழ்ந்து அப்போது ஆயனேநீர் அங்குவந்து எங்களிடம்  இப்போது போய்நீங்கள் என்னுடைய செந்தூரில் வாரிக் கரையதிலே வாய்த்ததவ மாயிருந்து சூரியப் பிரகாச சுத்தவை குண்டராசர்  இக்கட லில்பிறந்து எங்கள் தமைக்கூட்டிப்  பக்கமதில் வைத்துப் பதவிதர வேணுமென்று நில்லுங்கோ தவசு நினைவாக வென்றுசொல்லி  பல்லுயி ரும்வளர்த்த பாக்கியவா னேநீரும் உரைத்தமொழி அய்யாவுன் உள்ள மறியாதோ கறைக்கண்டர் மைத்துனரே கண்ணா எனத்தொழுதார் அப்போது அய்யா நாரா யணர்மகிழ்ந்து செப்போடு வொத்தாற்போல் சிரித்து மனமகிழ்ந்து நல்லதுகாண் பிள்ளாய் நாடுந் தவம்புரிந்தீர் வல்லவர்தாம் நீங்கள் வாருங்கோ வென்றழைத்து அருகிலே நில்லுமென்று அகமழிந் தாரன்போரே  குருவான ஈசரொடு கூறுவார் பின்னாலே நம்முடைய ஈசுரரே நாம்வந்த காரியங்கள் எம்முதலே யின்னதென்று இயம்புவீ ரீசுரரே கட்டான ஈசுரரும் காரியத்தைப் பாருமென்று மட்டான வாரிக் கரையிலே வந்திருந்து எல்லோருஞ் செந்தூர் இடமெல்லாங் கண்பார்த்து  வல்லோர்க ளெல்லாம் வந்திருந்தார் செந்தூரில்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(266,'சம்பூர்ணத் தேவனுக்கு நற்பேறு அருளல்','அங்கு சிலநாள் அமர்ந்திருக்கும் வேளைதனில் சங்குவண்ணர் நேமித்த சடல மதின்பெருமை உரைக்கிறார் அன்பர் உள்ள மகிழ்வதற்கு  தரையீரேழு மளந்த சுவாமி யுரைக்கலுற்றார் சீரான நல்ல தெட்சணா புமியிலே பாரான நல்ல பதிதா மரையூரில் தவசிக் குகந்த தாமரையூர் நற்பதியில் சிவசிவா வளரும் தெட்சணா புரிநாடு  பாண்டவரில் விசையன் பாசுபதம் வேண்டுதற்கு ஆண்டபர மேசுரரை அகம்வைத்துப் போர்விசையன் நின்று தவமுடித்து நெடியரனை மாதுவையும் கண்டு பதம்வாங்கிக் கைகண்ட திவ்வூரு அதியரசன் முன்னாள் அதிகத் தவமிருந்து பதியரசி யானப் பார்வதியை ஈன்றதுதான்  இவ்வூரு தெச்சணந்தான் ஏற்றதா மரையூரு செவ்வூரு நல்ல சிறந்தமண வைப்பதிதான் வானலோ கம்வாழும் வாய்த்ததெய் வேந்திரனும் தானவரைக் காணத் தவசிருந்த திவ்வூரு பஞ்சவர்க்கு முன்னம் பசுவா னுதவிசெய்ய  அஞ்சல்செய்து நாதன் அமர்ந்திருந்த திவ்வூரு சாம்பு சிவசான்றோர் தழைத்திருந்த திவ்வூரு தாம்பிரவர்ணி யாவி தழைத்திருந்த திவ்வூரு பரராச முனிவன் பாரத் தவசுபண்ணி விரமான வியாகரரை மிகஈன்ற திவ்வூரு  தவம்புரிய வென்று தானினைத்த பேர்களெல்லாம் பவமற்ற தாமரையூர் பதியாகு மென்றுரைப்பார் தவசுக் குகந்த சந்தமுற்ற பேரூரு பவிசுக் குகந்த பாலதியத் தாமரையூர் அவ்வூரு தன்னில் ஆதி யருளாலே செவ்வுமகா விஷ்ணுவும் செய்தசட மேபிறந்து பிறந்து வளர்ந்து பெருமைப் புகழ்காட்டி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(267,'சம்பூர்ணத் தேவனுக்கு நற்பேறு அருளல்','மறந்திடா முன்னமைந்த மாதை யுறவாடிப் பூலோக மனுக்கள் பிள்ளைபோ லேவளர்ந்து மாலேற்கப் பூசை மனையில் மறவாமல் விட்டிணுவைப் போற்றி விளங்கவொரு பீடமிட்டுக் கட்டுத்தீர்க் காகக் கண்வளர்ந் தார்கலியில் ஒருவர்க்கோர் பொல்லாங்கு உலகில்மிகச் செய்யாமல் குருவைக் குருகண்டு கொக்கரித் தேவளர்ந்தார் எதிர்த் தோரையடக்கி எல்லோர்க்கும் நல்லவராய்  உருத்துக் கரியவராய் உலகில் மிகவளர்ந்தார் எச்சா தியார்க்கும் இவர்நல்ல வரெனவே அச்சாதி யெல்லாம் அகமகிழத் தான்வளர்ந்தார் முன்னுதித் துடன்பிறந்தோர் ஒருவர்மூப் பாடாமல் பின்னுதித்தும் பெரியோராய்ப் பெருமையுட னேவளர்ந்தார் தாய்தகப் பன்மாமன் சந்தமிந்தத் தம்பியென்றும் ஞாயவா னென்றும் நாடி மிகவளர்த்தார் ஊருக்குந் தலைவன் உடையவழிக்குந் தலைவன் ஆருக்குந் தலைவனென்று அன்னை பிதாவளர்த்தார் ஞாய மிருப்பதனால் நாடாள்வா னென்றுசொல்லித் தாய்தமர்க ளெல்லாம் தாங்கி மிகவளர்த்தார் சொல்லுக்கும் வல்லவனாய் சூராதி சூரனிவன் மல்லுக்கும் வல்லவனாய் உபாயத் திலும்பெரியோன் மங்கையர்க்கு மேற்றவனாய் மாமோகக் காமீகன் எங்கும்பேர் கேட்கவைப்பான் இவன்கீர்த்தி நல்வளமை பல்லார்க்கும் ஏறிடுவான் பார்முழுதும் ஆண்டிடுவான் எல்லார்க்கும் நல்லவனாய் இவன்சமைவா னென்றுசொல்லி எவ்வோருங் கொண்டாட இன்பமுட னேவளர்ந்தார் அவ்வோருங் கொண்டாட அவர்வளரும் நாளையிலே பத்து வயது பண்போ டிருபதிலே   மற்று நிகரொவ்வா மன்னவர்போ லேவளர்ந்தார் எல்லாத் தொழிலும் இதமிதமாய்க் கற்றுமிகப்  பொல்லா தாரோடு பெரும்பகைபோ லெசீறி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(268,'சம்பூர்ணத் தேவனுக்கு நற்பேறு அருளல்','நல்லாரை யுள்ளில் நாளு மறவாமல் கல்லாரை யெல்லாம் கண்டுகழித் தேயிருந்தார் ஈவதற்குத் தர்மனென எளியோரைக் கண்பார்த்து ஆய்வதற்கு நல்லான் என்றே மிகவளர்ந்தார் பதினே ழுவயதில் பண்டமைத்த பெண்ணோடு விதியா னபடியால் மெல்லியர்மே லிச்சைகொண்டு கூடிக் குணமாய்க் கொண்டவனை யுமகற்றித் தேடி யிவரோடு செய்யரச மாயிருந்தாள் வானுபர மேசுரனார் மாயவனா ராணையிட்டு நானும்நீ யுமாக நலமாக வாழ்வோமென்று ஆணையிட் டிருபேரும் அகமகிழ்ந் தன்றுமுதல் நாண மில்லாமல் நாயகன்போ லெவாழ்ந்தார் மங்கை காணாமல் மறுவூரு தங்கறியாள் அங்கவளைக் காணாமல் அயலூரு தங்கறியார் இந்தப் படியாய் இவர்வாழும் நாளையிலே எந்தநருளுங்கண்டு இவர்க்கிவளை யமைத்ததென்பார் முன்னாள் அமைத்த ஊழி விதியெனவே  சொன்னா ரெவரும் சுவாமி யருளாலே அன்றைக் கமைத்ததுவே அவளையிவர்க் கல்லாது என்று நருளெல்லாம் இயம்பி மிகவுரைத்தார் மன்றீ ரேழுமறிய மங்கையொடு வாழ்கையிலே  உற்ற வயசு ஓரிருபான் ரண்டதிலே  சத்துராதி யோர்நீசன் தானே பிழையேற்க ஏற்கவே நீசன் இடறுசெய்த ஏதுவினால் ஆர்க்கம் அடக்கி அமர்ந்து பிணியெனவே எல்லோரு மறிய இவரிருந்தா ரம்மானை வெல்லாரு மில்லா விசையடக்கித் தானிருந்தார் நொம்பலங்க ளென்று நொந்து மிகவுழைந்து தம்பிலங்க ளடக்கித் தருணம் புலம்பலுற்றார் கருவுற்ற தோசம் கழிந்து சிவஞானத்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(269,'சம்பூர்ணத் தேவனுக்கு நற்பேறு அருளல்','திருவுள மாகி சிவமய மாய்ப் பெறவே மனதில் மிகவுற்று மாயவரை நெஞ்சில்வைத்துத் தினமும் வருந்தித் தீனமென வேயிருந்தார் அப்படி யோர்வருசம் அங்கமதி லூறலெல்லாம் முப்படி ஞாயமதால் உலகில் கழியவிட்டு எகாபரா தஞ்சமென்று இருக்குமந்த நாளையிலே மகாபர னார்செயலால் மாதாவின் கண்ணதிலே  சொற்பனம்போல் சுவாமிவந்து சொன்னாரே வுத்தரவு உற்பனம்போல் கொண்டு உற்றஅவர் மாதாவும் மகனே யின்றிரவில் மனஞ்சலித்து நான்வாடி அகம்வைத்த எண்ணம் அறிந்துசிவ நாரணரும் வந்துசொன்ன வுத்தரவை மகனேநீ கேளுவென்று விந்து வழிக்கிளையோர் மிகுவாகக் கேட்டிருக்கச் சொல்லுகிறா ளந்தச் சொற்பனத்தை யன்போரே நல்லுறவாய்க் கேளுமென்று நவிலுவா ளன்போரே ஆயிரத் தெட்டு ஆண்டிதுவா மிவ்வருசம் மாசியென்ற மாதமிது வாய்த்ததேதி பத்தொன்பது இம்மாத மித்தேதி ஏற்றதிருச் செந்தூரில்  நம்மாணை கொடியேறி நல்லதிரு நாள்நடக்கு அங்குன் மகனை அழைத்துவ ருவாயானால் எங்குள்ளோ ருமறிய இப்பிணி யுந்தீர்த்து நல்லபே றுங்கொடுப்போம் நம்மாணை தப்பாதெனச் சொல்லவே கண்டேன் சுவாமிகரு மேனிநிறம் நான்கண்ட சொற்பனந்தான் நழுவிமிகப் போகாது தேன்கண்டாற் போலே சிரித்து மனமகிழ்ந்து இந்தக் கனாவதற்கு இங்கிருந் தங்கேபோய் வந்தல்லா தேபிணியும் மாறவகை யில்லையல்லோ  என்றுரைக்க எல்லோரும் இன்பமுட னேமகிழ்ந்து நன்றுநன்று போவதற்கு நடைக்கோப்பு கூட்டுமென்றார் வேண்டும் பலகாரம் விதவிதமாய்ச் சேகரித்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(270,'சம்பூர்ணத் தேவனுக்கு நற்பேறு அருளல்','ஆண்ட கருப்புக்கட்டி ஆனதெல்லாஞ் சேகரித்துத் தானதர்மஞ் செய்ய தனங்கள் மிகவெடுத்துத் தீன மானவரைத் திடமாகக் கூட்டிவந்து ஆளுமிகச் சேகரித்து அகலநல்ல தொட்டில்வைத்து நாளு கடத்தாமல் நடக்கவழி கொண்டனராம் மாதாவும் மகனும் வாய்த்த நருளுடனே நிதாவின் பாதாரம் நெஞ்சில் மிகநிறுத்தி இருந்த பதியும்விட்டு ஏற்றவொரு காதம்விட்டு வருந்த நருளோடே வழிகொண்டா ரன்போரே  கூடங் குளமும்விட்டுக் குளிர்ந்தசுக்குப் பாரும்விட்டுத்  தோடவழி யாறுங்கண்டு சூறாவழிக் காடும்விட்டு  நடந்து வொருவனத்தில் நல்லதண்ணீ ராவிகண்டு கொடர்ந்த பலகாரம் கொண்டுதண்ணீர் தான்குடித்து தகையாறிக் கொண்டு தானிருக்கும் வேளையிலே  வகையான நல்ல வாய்த்தமகா விட்டிணுவும் பிறவிக்கு ஏற்ற பிள்ளை வருகுதென்று திறவி முதலோன் தெளிந்துமிகக் கொண்டாடி  எதிரே ஆள்விட்டு இங்கழைக்க வேணுமென்று அருகே தானின்ற ஆதி முனியான நல்ல முனிவரையும் நாரா யணரழைத்து வல்லவர்தாம் நீங்கள் வாரு மென அழைத்து வாருங்கோ பிள்ளாய் வாய்த்த முனிமாரே நேருங்க ளோடு நிகழ்த்துகிறே னோர்வசனம் நானே மித்த நல்லவுயி ரானதிங்கே தானே வருகுதுகாண் எந்தன் தவத்தாலே எதிரேபோய் நீங்கள் இங்கழைத்து வாருமென்று பதியேறும் பெருமாள் பகர்ந்து முனியயச்சார் அயச்ச முனிமார் அவ்வாயு போல்விரைவாய்ப்  பயபட்ச முடனே பகர்ந்த இருமுனியும் எந்த வழியாய் இவர்கள்வரு வாரெனவே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(271,'சம்பூர்ணத் தேவனுக்கு நற்பேறு அருளல்','அந்தந்த வழிக்கு ஆலோட்டம் பார்த்துவந்தார் வந்து ஒருவழியில் வரவேகண் டம்முனிவர் சந்துஷ்டி யாகித் தாழ்ந்துநமஸ் காரமிட்டு கண்டு குவித்துக் கனக முனிமார்கள்  வண்டுசுற்று மார்பனுட வாய்த்தகரம் ரண்டதையும் முனியிருபேர் தோளில் உயர்த்தி மிகவேந்தி துணிவுடனே மாமுனிவர் தோளின்மே லேயிருத்திப்  பதியி லிருந்தாற்போல் பார்மன்னனை யிருத்திக் குதிரைநடை கொண்டாற்போல் கொண்டோடி மாமுனிவர்  கூட நடப்போர் குதித்துக்குதித் தோடிவர வாடி யிடைந்து மனுநருட்க ளோடிவர நல்ல பிறவியைத்தான் நன்முனிவர் கொண்டோடி வல்லசெந் தூர்ப்பதியில் வந்தனர்கா ணன்போரே வந்த முனிமார்கள் வாழ்த்தியந்த நல்லுயிரைச் சந்தனா வீதியிலே சடலந்தனை நிறுத்தி மாமுனிவர் தாமும் மாயோனை வந்துகண்டு சாமிநீர் கொண்டுவரத் தானுரைத்த நற்சடலம் அடியா ரெதிரேபோய் அழைத்துவந்தோ மையாவே திடீரெனவே நாதன் சொன்னமுனி யோடுரைப்பார் கொண்டுவந்தோ மென்றீரே கூர்மையுள்ள நற்சடலம் பண்டுமுறை யெல்லாம் பகருவீர் மாமுனியே அப்போது மாமுனிவர் ஆதி யருளாலே செப்புகிறோ மென்று செப்பலுற்றா ரன்போரே நல்ல சடலமிது நாடுமுற் காலமதில் வெல்லமர்கோன் வாழும் வெற்றிதெய்வ கோலகவுயிர் பெருசம் பூரணன்தான் பெரிய திறவான்காண் ஆரொவ்வா ரேயிவர்க்கு ஆதி கிருபையுள்ளோன்  அப்படியே தெய்ட லோகமதி லேயிருக்க இப்படியே யிவர்க்கு எமலோக மானதிலே இருக்கின்ற பெண்ணதிலே இசைந்தபர தேவதையென்(று)');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(272,'சம்பூர்ணத் தேவனுக்கு நற்பேறு அருளல்','தன்மேல் உள்ளாசை யாயிவரும் அவளு மிவர்பேரில் ஆசையாய்த் தானிருந்து இவளு மிவரும் இருந்துமிக வாழ்கையிலே தேவருக்குந் தெய்வ லோகமே ழுள்ளவர்க்கும் யாவருக்கு மோர்பிறவி யாகுகின்ற நாளதுவாம் இவரையும் வருத்தி என்னவுன் செய்தியென்றார் அவளுடைய ஆசையினால் அல்லவென்று தான்மறுத்து இவளையு மென்னோடு இயல்பாய்ப் பிறவிசெய்தால் குவளையணி மாயவரே குணமெனக் காகுமென்றார் அப்போது நாதன் அந்தத்தே வன்றனக்குச் செப்பமுள்ள புத்தி செப்பிமிகப் பார்த்தனரே அப்போதிவர் கேளாமல் அதுதானது தானென்றார் மாயனுக்குக் கோபம் மனதில் மிகவாகி ஆயனப்போ திவர்க்கு அதிகப் பலனுரைத்தார் நல்லதுநீ கேட்ட ஞாயமீட் டேற்றமதாய் வல்லவனை நினைந்து மாதுவும் நீதானும் எங்க ளிருபேர்க்கும் இன்னம் பிறவியிலே  மங்கையும் புருசனென மறவாம லாவதற்கு வரந்தாரு மென்று மனதில் நினைவேற்றிப்  பரந்தாண்டி கண்டு பலன்பெறுங்கோ வென்றுசொல்லி தவசிருக்க விட்டீர்காண் சங்கரரே நீர்கேளும் சிவசிவா வென்றுதவம் செய்தவர்கள் நிற்கையிலே  தவம்பார்க்க ஈசுரரும் சன்னாசி நீதனுமாய் அவட மெழுந்தருளி அங்கேகும் வேளையிலே  தெய்வேந் திரனும் திருமுடி யுஞ்சூடி மையேந்திர னுடைய மலர்பாதங் காணவென்று அவனுமிக வந்தான் அரனெதிரே அய்யாவே தவத்துக் கிடறு தான்வருவ தோராமல் தேவன் மதிமயங்கித் திருமுடிமே லிச்சைகொண்டு பாவையுட னுரைத்துப் பற்கடித்தான் தேவனுமே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(273,'சம்பூர்ணத் தேவனுக்கு நற்பேறு அருளல்','ஒருகுழலி அதையறிந் திசுரருள் அன்றும்மு டனுரைத்தார் இதையறிந் துநீரும் ஏற்றதேவ னோடுரைத்தீர் வாய்த்த தவங்குளறி வாய்க்காமல் நின்றதினால் ஏற்றகீ ழுலகில் என்மகவு சான்றோரில் நல்லதர் மகுலத்தில் நன்றாக நீபிறந்து தொல்லையெல் லாந்தீர்த்துச் சூலினழுக் கறுத்து வளரும் பருவமதில் வந்துன்னை நானெடுத்து  இளவரசா யுன்னை ஈன்றெடுத் தேவளர்த்து ஆளு மரசு அழகாக வுன்றனக்கு நாளு மிகத்தருவேன் நம்மானை தப்பாதென உறுதிசொல்லித் தேவனையும் உற்ற சான்றோர் குலத்தில் பொறுதியுள்ள தர்மப் பிதிரில் பிறவிசெய்தீர் மாதைப் பிறவிசெய்தீர் மக்கள்சான் றோர்குலத்தில் சூதஎமக் குலத்தில் தோகையரைத் தோன்றவைத்தீர் அப்படியே முன்னம் அய்யாவே யிவ்வகைக்கு இப்படியே பிறந்து இச்சடல மென்றுரைத்தார் என்றிந்த விவரமெல்லாம் இயல்முனி வோர்கள் சொல்ல நன்றிந்த விவர மென்றே நாரணர் தயவு கூர்ந்து சென்றிந்தச் சடலந் தன்னைச் செந்திலம் பதியி லெங்கும் கொண்டெந்தத் தெருவுங் காட்டிக் குளிர்ப்பாட்டி வாருமென்றார் மேலுள்ள சடலந் தன்னை மிகுமுனி மாரே நீங்கள் நாலுள்ள தெருக்கள் தோறும் நடத்தியே தரையி லூட்டிப் பாலுள்ள பதத்தில் கொண்டு பழவிளை தீரக் காட்டி மாலுள்ளம் புகுத நாட்டி வாருங்கோ சிணமே யென்றார் அப்படி முனிமா ரேகி அங்கங்கே கொண்டு காட்டி முப்படித் தோசம்போக முனைபத மதிலேமூழ்கி இப்படி யிவரைக் கொண்டு இவர்வரு முன்னேயாகச்  செப்படி வித்தை நாதன் செகலினுள் ளகமே சென்றார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(274,'வைகுண்டர் உதயம்','வானவர் தேவர்போற்ற மறைமுனி வோர்கள் பாடத் தானவர் ரிஷிகளோடு தமிழ்மறை வாணர் போற்ற ஞானமாம் வீணை தம்பூர் நாற்றிசை யதிர வோசை ஓநமோ வென்று தேவர் ஓலமிட் டாடி னாரே மத்தளத் தொனிகள் வீணை மடமடென் றேற்ற வானோர் தித்திதெய் தித்தி யென்ன தேவியர் பாடி யாடத்  தத்தியாய்ச் சங்க மெல்லாம் சதுர்மறை கூறி நிற்க முத்திசேர் மாயன் தானும் மூழ்கினர் கடலி னுள்ளே கடலினுள் ளகமே போந்து கனபதி மேடை கண்டு  மடவிபொன் மகரந் தன்னை வாகுடன் பூசித் தேய்த்து நிடபதி மாயன் தானும் நிறைந்தபொன் னிறம்போல் வன்னி  வடவனல் போலே வீசி வந்தனர் மகர முன்னே வந்தனர் மகர முன்னே மாதுபொன் மகரங் கண்டு செந்தழ லெரியோ வென்று செல்லிடப் பதறி நொந்து எந்தனின் மான வானோ எரிவட வாச மேர்வோ கந்தனின் மாய மாமோ என்றவள் கலங்கி னாளே  கலங்கியே மகரந் தானும் கருத்தறிந் தேதோ சொல்லும் இலங்கியே வருவோ மென்ற என்மன்னர் தானோ யாரோ  சலங்கியே யதிரப் பூமி சதிரெனக் கதிரு பாய துலங்கிய சுடரைப் பார்த்துச் சொல்லுவாள் மகரந் தானே சுடரே சுடரே துலங்கு மதிசுடரே கடலே கடலே கடலுட் கனலாரே அக்கினிக் கேயபயம் அனலே வுனக்கபயம் முக்கியமாய்க் காந்தல் முனையே யுனக்கபயம் தீயே யுனக்கபயம் திரிபுர மேயபயம் நீசுட்டத் தலங்கள் நினக்குரைக்கக் கூடாது மாசற்ற ஈசன் வல்லவனோ டுள்ளிருந்து காண்டா வனமெரித்தாய் கடியவில்லி கையிருந்து  ஆண்டஇலங் கையெரித்தாய்அனுமன்கையி லேயிருந்து முப்புரத்தைச் சுட்டழித்தாய் முதன்மைகையி லேயிருந்து ஒப்புரவாய் நீயெரித்த உவமைசொல்லக் கூடாது');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(275,'வைகுண்டர் உதயம்','அனலே வுனக்கபயம் ஆதி யுனக்கபயம் கனலே யமருமையா காந்தல்தனை மாற்றுமையா மன்னரரி நாரணர்க்காய் மாறுவீ ரக்கினியே இந்நியல்பை யெல்லாம் என்மன்ன ரிங்குவந்தால் சொல்லி யுனக்குச் சொக்கம்வேண் டித்தருவேன் வில்லிக்கு வல்லவனே விலகி யமருமையா என்னையென் மன்னவர்தான் இந்தக் கடலதிலே பொன்னனைய நன்மகரம் ஆகவே போகவிட்டார்  வருவோ மென்றநாளும் வந்ததுகா ணக்கினியே தருவே னுனக்குவரம் தாட்டீகன் வந்ததுண்டால் அமர்ந்துநீ போனால் அதிகப்பல னுண்டாகும் சுமந்த பறுவதத்தின் சுகம்பெற்று நீவாழ்வாய் என்றுமக ரமாது எரியைமிகக் கண்டுளறி நின்று மயங்குவதை நெடியதிரு மாலறிந்து அனலைமிக வுள்ளடக்கி ஆதி யுருக்காட்டி மனைவியேநீ வாவெனவே மாய னருகழைத்தார் உடனே பொன்மாது உள்ளம் மிகநாணி தடதடென வந்து சுவாமியடி யில்வீழ்ந்து எந்தன் பெருமானோ இப்படித்தா னின்றதுவோ சிந்தை மயக்குதற்கோ தீப்போலே வந்ததுதான் என்றுமிகப் பொன்மகர இளமாது ஸ்தோத்தரித்து மன்று தனையளந்தோர் வாநீ யென அழைத்து அருகில் மிகநிறுத்தி அங்குள்ள தேர்பதியும் கருதி யிருபேரும் கண்கொண் டுறப்பார்த்து வச்சிர மேடை மரகதப் பொன்மேடை எச்சரிக்கை மேடைஎல்லா மிகபார்த்து மாதே வுனக்கு வளர்பிறவி பத்தெனவும் சீதே வுனக்குச் சிறந்தபேறு பத்ததிலே  பலம்பேறு பேறாய் பார்மகிழ வோர்பாலன் சிலம்பே றுடையவனாய் சிவனுக் குகந்தவனாய் எகாபர முமகிழ யானும் நீயுமாக ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(276,'வைகுண்டர் உதயம்','மகாகுரு வதாக மகிழ்ந்து மகிமையுடன்  தன்மச் சிறப்பதிகத் தலைவன் தனைப்பெறவே உண்மையாய் நானுனக்கு உபதேச மாயுரைத்தேன் என்றுரைக்க நாரணரும் இளமகர மேதுசொல்லும் பண்டு அமைத்திருந்தால் பக்கஞ்சொல் வதோ அடியாள் சித்தத்துக் கேற்ற செயலெனவே பொன்மகரக்  கற்றைக் குழலி கருதி மிகவுரைத்தாள் உடனே பொன்மகரம் உள்ளம் மயக்கமிட்டுத் திடமயக்கிச் சிங்கா-சனத்தில் மிகவிருத்தி மயங்கியே பொன்மகர மாது மிகவிருக்கத் தியங்குதே லோகமுதல் திரைவாயு சேடன்முதல் ஈசர்முத லாதிமுதல் ஏற்றதெய் வார்கள்முதல் சீதை முதல்மயங்க சீமையீரே ழுமயங்க மாமோகச் சக்கரத்தை மாயவனார் தான்வீசிச்  சீமை யெழுகடலும் சிணமே மயக்கினரே மயக்கியந்த மால்மகரம் மாமோக மாயிருக்க  தியக்கியந்த நாரணரும் சென்றார் மதுரமதுள்  மகரமுள் சென்று மகனை மிகநினைக்க உகரமுனி கூட்டிவந்த உற்ற சடலமது சிணமே கடலுள் சென்றதுகா ணன்போரே குணமே குணமெனவே கூண்டரியோன் தான்பார்த்து மகனை மிகவெடுத்து மகரமதி னுள்ளேகொண்டு அகரத் தெருவறையில் அலங்காரத் தட்டில்வைத்துக் காலொன்றாய் நிற்கும் கனத்தபொன் மேடையதுள் நாலொன்றாய்க் கூடும் நல்லசிங் காசனத்தில் தங்கவை டூரியத்தால் தானிறைந்த மண்டபத்துள் எங்கும் பிரகாசம் இலங்குகின்ற மண்டபத்துள் முத்துக்கள் சங்கு முழங்குகின்ற மண்டபத்துள் பத்தும் பெரியோர்கள் பாவித்த மண்டபத்துள் தங்கநீ ராவித் தான்வளரு மண்டபத்துள்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(277,'வைகுண்டர் உதயம்','சங்கு முழங்கிநிற்கும் சதுரமணி மண்டபத்துள் மூவாதி கர்த்தன் உகந்திருக்கு மண்டபத்தில் தேவாதி தேவர் சிறந்திருக்கு மண்டபத்தில் ஆயிரத் தெட்டு அண்டம்நிறை மண்டபத்தில் தாற்பிரிய மான தாமிரவர்ணி மண்டபத்தில் எண்ணக் கூடாத இயல்புநிறை மண்டபத்தில் மண்ணளந்தோர் மகரமதுள் மகனை மிக இருத்திக்  கண்ணான மகற்கு கனத்ததங்கச் சட்டையிட்டு எண்ணவொண்ணா ஆபரணம் எல்லா மெடுத்தணிந்து தங்கக் குல்லாவும் தலைமேலே தூக்கிவைத்துப்  பங்கமில்லாத் தைலப் பதத்தில் மிகமூழ்கி வைகுண்ட வெள்ளை மான்பட்டொன் றேயெடுத்துக் கைகொண்டு பாலனுக்குக் கண்ணர் மிகச்சூடி மேலுக ளொக்க மினுமினுக்கப் பச்சையிட்டுக்  காலுக ளுக்குத்தங்கக் கழராப் பணிகளிட்டு மகனை யெடுத்து மடிமீதி லேயிருத்தி உகமாள வந்தவனோ உடையவனோ என்றுசொல்லிக்  கண்ணைத் தடவி கரிய முகந்தடவி எண்ணும் வளர்வாயென்று இசைந்தமுகத் தோடணைத்துப்  பாலனைப் போல்காட்டிப் பதினா றுடன்காட்டி நாலுரண்டு காட்டி நல்ல மகவதுக்கு நடக்கக் கருமம் நவின்றிடவே நாரணரும் அடக்க முடனே அருளுவா ரன்போரே நல்ல வுபதேசம் நன்மகவுக் கேயருளி வல்லத் திருநாதன் வகுக்கிறா ரன்போரே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(278,'விஞ்சையருளல்','தேடிய மறைநூல் வேதன் தேவியர்க் கமல நாதன் நாடிய இறையோன் ஞானி நாச்சிமார் தேவ ரோடும்  கூடிய ரிஷியோர் வானோர் குவலய மறியா விஞ்சை தேடிய மகனார்க் கென்று செப்பினா ரொப்பில் லானே மகனே வுனது மனமறிய மறையோ ரறியா விஞ்சைசெய்து அகமே யருளித் தருவதெல்லாம் அனுப்போ லசலில் விலகாதே உகமே முடிந்த தின்பிறகு உதிக்குந் தர்மயுகத் தில்வந்தால் செகமே யறியச் சொல்லிமிகச் சிறந்தே வாழ்ந்து வாழ்வோமே ஆண்டா யிரத்து எட்டதிலே அதனே மாத மாசியிலே  நான்றாங் கடலின் கரையாண்டி நாரா யணனே பண்டாரம் கூண்டாந் தெச்ச ணாபுரியில் கொண்டோம் பள்ளி தர்மமுற்று ஒன்றாம் விஞ்சை யிதுமகனே உரைப்பேன் ரண்டாம் விஞ்சையதே வேண்டாம் வேண்டாங் காணிக்கையும் மிகவே வேண்டாங் கைக்கூலி ஆண்டார் நாரா யணன்தனக்கு அனுப்போல் வேண்டாங் காவடியும் வேண்டா மெனவே நிறுத்தல்செய்து வாய்த்த சிறையாய்க் கவிழ்ந்திருநீ இரண்டாம் விஞ்சை யிதுமகனே நவில்வேன் மூன்றாம் விஞ்சையதே கொற்றவர் தானு மாண்டு குறும்புகள் மிகவே தோன்றி உற்றதோர் துலுக்கன் வந்து உடனவன் விழுந்து வோடி மற்றதோ ராண்டு தன்னில் வருவோ மென்றா கமம்போல் முத்தலத் தோருங் காண உரைத்தனர் மூன்றாம் விஞ்சை நல்ல மகனே நாலாம் விஞ்சைகேளு வல்ல நடுஞான வாய்த்த வைகுண்டமது  பிறந்துகொண் டிருக்கெனவும் புதிய ந÷க்ஷத்திரத்தில் அறந்தழைக்க நன்றாய் அதுகுதிக்கு மென்றுசொல்லு மாழுவது மாண்டு மனதுகந்த தேமுழிக்கும் முழுவதை நீக்கி முழிப்பதுவே கண்டுகொள்ளும் ஒருநெல் லெடுத்து உடைக்கநாடு ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(279,'விஞ்சையருளல்','கேட்டுக்கொள்ளும் இருநெல் லெடுத்து உடைக்கநாடு தாங்காது மலைக ளுதிர்ந்துவிடும் வான மழிந்துவிடும் தலைநாட் சேத்திரமும் தானுதிரும் ஆலங்காய்போல் என்றுநா லாம்விஞ்சை இதுதானே என்மகனே சத்தமொன் றானதிலே தானிதெல்லா மாகுமென்று சித்த முடனே செப்பியிரு என்மகனே ஆயிரத் தெட்டு அதில்வாழுந் தேவருக்கு வாயிதமாய் விஞ்சை வகுப்பேன்கே ளென்மகனே நாட்டுக் குடைய நாரா யணர்தானும் ஆட்டுக் கொடைபூசை அனுப்போலும் வேண்டாமென்று சொல்லியே தர்மமுற்றுச் சிறையாகத் தானிருக்கத் தொல்லுலகி லாரேக்கார் பார்ப்போமென்று சொல்லிடுநீ தூறான பேய்களுக்குச் சொல்லு முறைகேளு வாறான நாரணர்தான் வாய்த்ததர்ம மேநினைத்துக்  கவிழ்ந்து சிறையிருக்குங் காரணத்தா லோகமெல்லாம்  உவந்து திரியும் உயிரிப்பிராணி யாதொன்றையும் இரத்த வெறிதீபம் தூபமிலைப் பட்டைமுதல் சற்றும் வெலிபாவம் தான்காண ஒட்டாதெனத் தர்மம் நினைத்துத் தவசிருக்கேன் நாரணனும் உற்பனமா யறிந்தோர் ஒதுங்கியிருங் கோவெனவே வீணப் பசாசறிய விளம்பியிரு என்மகனே நாண மறியாமல் நன்றிகெட்ட நீசர்குலம் மட்டை யெடுத்தடிப்பார் மண்கட்டி கல்லெறிவார் சட்ட மசையாதே தன்னளவு வந்தாலும் நாட்டைக் கெடுத்த நவின்றகலிச் சக்கரத்தில்  ஓட்டைக் கலத்ததிலும் உக்கு மிரும்பதிலும் முழியாதே யென்மகனே மும்முதற்கும் நாயகமே அழிவாகிப் போகும் அன்புகெட்ட நீசருடன் பொய்யரோ டன்பு பொருந்தி யிருக்காதே மெய்யரோ டன்பு மேவியிரு என்மகனே வர்மமதை வதைக்க வந்தேனெனச் சொல்லிமிகத் தர்ம மதைவளர்க்கத் தரணியில் வந்தேனென்று இம்முதலே யாறு இருப்பே தவசுபண்ணி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(280,'விஞ்சையருளல்','மும்முதலோ னாகி முறைநடத்து என்மகனே மூக்குச் சுழியும் முச்சுளியும் முத்திமுத்தி  நோக்குச் சுழியாய் நேர்நில்லு என்மகனே பரமான பட்டணத்தில் பார்வைகொண் டேநாட்டிச் சிரமானது விரித்துச் சிறையே யிருமகனே ஏற்பதுபால் பச்சரிசி இருப்பது பார்மேலாகும் காப்பதுமா தர்மம் கண்ணே திருமகனே சந்தன வாடை சாந்துசவ் வாதுபுஸ்பம் எந்தன் திருமகனே எள்ளளவும் பாராதே வாடைபூ தீபம் மகிழாக் கலியுகத்தில் நாடை யழித்து நல்லயுக மேபிறந்தால் சொல்ல வொண்ணாத சுகந்தருவே னென்மகனே கண்டாயோ மகனே கரிய பவிசுஎல்லாம் கொண்டாயோ விஞ்சை கூடுபிர காசமதாய் அப்போது தான்பிறந்த அந்தவைகுண் டருரைப்பார் இப்போ தென்பிதாவே என்னையீன்றே யெடுத்துப் பருவமதாய் வளர்த்துப் பகர்ந்தீரே ஞாயமெல்லாம் உருவுபிர காசமதாய் உபதேசஞ் செய்தீரே தங்க முடிவேந்தே தகப்பனே நீர்கேளும் பொங்கு கடல்துயின்ற புண்ணியரே நீர்கேளும் என்னுடைய முத்துயரம் எல்லா மிகக்கழித்து நன்னமுத மாக நன்மகவாய்ச் செய்தீரே  இன்னுங் கலியுகத்தில் இருக்கப் போச்சொல்லீரே உன்னை யறியாமல் ஊன்கலியி லுள்ளோர்கள் என்னை மிகப்பழித்து ஏசுவானே நீசனெல்லாம் அல்லாமல் முன்பெற்ற அவர்களுட பேரைச்சொல்லிப்  பொல்லாத நீசனெல்லாம் பேசி யடிப்பானே சூத்திர விஞ்சை தொழிலை யறியாமல்  யூத்திர நீசனெல்லாம் ஒழுங்குதப்பி மாழ்வானே நம்முடைய சாதி நம்மைமிகக் காணவந்தால் செம்மைகெட்ட நீசன் சிதற அடிப்பானே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(281,'விஞ்சையருளல்','ஆனதால் நம்சாதி அகலநின்று வாடிடுமே ஏனமென்ன சொல்லுகிறீர் என்னுடைய நாயகமே முன்னென்னைப் பெற்ற மொய்குழலார் வம்மிசங்கள் என்னேது வாலே இயல்புபெற வேணுமல்லோ அல்லாமல் முன்னம் அணங்கொரு பெண்ணிணங்க  நல்லோர் மனசு நல்லணங்குக் கேகொடுத்து மேலும் பதவி மிகக்கொடுக்க வேணுமல்லோ இப்படியே நல்ல இயல்பு பெறஅருளி  எப்படியு மென்னைவந்து ஏற்கவரும் நாளும் எல்லாம் விவரமதாய் இயம்பி யனுப்புமென்றார் பொல்லாத பேர்மாளப் போவதுவுஞ் சொல்லுமென்றார்  அந்த வுடனே ஆதிநா ராயணரும் சிந்தை மகிழ்ந்து திருமகனை யாவிமிகக்  கட்டி யெடுத்துக் கமலமுகத் தோடணைத்துத் திட்டித்த நாதன் சொல்வார் மகனு டனே எந்தனின் தவத்தா லேக இகபர முனக்குள் ளாகித் தந்திடு மகனே நானும் தனதுள்ள மமர்ந்தே னானும் உந்தனைக் கண்டால் மூவர் ஒஞ்சியே மகிழ்வர் கண்டாய் சிந்தர்க ளெவரும் போற்றச் செயல்பெற்ற மகனும் நீயே மகனேநீ கேட்டதற்கு வகைசொல்ல வேணுமென்றால் தவமூ ணுண்டேநீ தரணிதனில் போயிருந்தால் அல்லாமல் பின்னும் அதின்மேல் நடப்புவளம் எல்லா முனக்கு இயம்பித்தரு வேன்மகனே முதற்றான் தவசு யுகத்தவசு என்மகனே தத்தமுள்ள ரண்டாம் தவசுசா திக்காமே மூன்றாந் தவசு முன்னுரைத்த பெண்ணாள்க்கும் நன்றான முற்பிதிரின் நல்ல வழிகளுக்கும் இப்படியே மூவிரண்டு ஆறு வருசமதாய் எப்படியும் நீதவசு இருக்கவரு மென்மகனே ஓராறு ஆண்டு உற்றதவ மேயிருந்து பாராறுங் காண பார்கண்டு கண்டிருநீ ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(282,'விஞ்சையருளல்','வத்துவகை சொத்து மனைவீடு வாசல்முதல் சற்றுமன தெண்ணாமல் தானிருந் தென்மகனே நன்மையோடு தண்மை ஒன்றுமிகப் பாராமல் கந்தைத் துணியுடுத்து கைநிமிர்ந்து காட்டாமல் எந்தப் பேரோடும் இனிப்புமொழி பேசாமல் பாலல்லால் வேறு பண்டமிக ஏராமல் காலல்லால் சுத்தம் கழுவ நினையாமல் மூக்கு நடுவே மும்முதலு முத்திமுத்தி நாக்குச் சுழிதாங்கி நாரணன் நானெனவே எகாபரத்தைக் கண்டு இரண்டாண்டு ஓரிருப்பாய் மகாபரனை நெஞ்சில் மறவாம லெப்போதும் கண்டிரு என்மகனே கருத்தொன்று தானாட்டி கொண்டை யமுதுண்டு குவிந்திரு என்மகனே இப்படியே ஆறாண்டு இருநீ யுகத்தவசாய் அப்படியே நீயிருந்து ஆறாண் டேகழித்தால் நினைத்ததெல்லா முனக்கு நிசமாய் முடியுமப்பா உனைத்தள்ளி வேறே ஒருமூப் புகத்திலில்லை என்ன நினைத்தாலும் எத்தனைதான் செய்தாலும் வன்னத் திருமகனே வகையதுதா னென்மகனே  நீசெய்த தெல்லாம் நிரப்புத்தா னென்மகனே நானீயே யல்லால் நடப்பதுவும் வேறில்லையே தானீத னான சர்வபரா என்மகனே ஒக்க வுனது உள்ளம் நினைத்தபடி  மிக்க நடத்தி விரும்பியிரு என்மகனே எண்ணிறந்த கோடி இயல்பாவஞ் செய்தோர்க்கும் நண்ணியே மோட்சம் நவிலவென்றா லுன்மனந்தான் முக்கோடி தர்மம் உவந்தளித்த அன்போரைத்  தக்கோடி நரகமதில் தள்ளவென்றா லுன்மனந்தான் உனக்கேற்க நின்றவர்க்கு உதவிகொடு என்மகனே எனக்கேற்க நின்றவரை இரட்சிப்பது முன்மனந்தான் சிவனும்நீ நாதனும்நீ திருமாலும் நீமகனே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(283,'விஞ்சையருளல்','தவமும் வேதனும்நீ சங்கமுத லெங்கும்நீயே அப்படியே அய்யா அரிநாத னுமுரைக்க முப்பொருளோர் மெய்க்கும் முதலோன் மகிழ்ந்துரைப்பார் ஆறு வருசம் அதிகத் தவம்புரிந்தால் வாறு மேலென்ன வகுப்பீர்கா ணையாவே அப்போது நாதன் அன்பா யகமகிழ்ந்து செப்புகிறார் நல்ல திருமகற்கு அன்போரே அதிகத் தவசு ஆண்டாறு தான்கழித்தால்  இதின்மேல் நடப்பு இயம்பக்கே ளென்மகனே இம்மூணு தவமும் இயல்பாய் நிறைவேறி மும்மூவ ருக்கும் முதல்வனாய் வந்தாக்கால் தானா னாய்நீயும் தலைவனும்நீ என்மகனே சாணாரின் நாயகமே சாதித்தலை வன்நீயே எந்தப்படிநீ எண்ணி நினைத்த தெல்லாம் அந்தப்படி செய்து அவனிதனி லேசிலநாள் சொந்த விளையாட்டு தொல்புவியிற் கொண்டாடிச் சிந்தாத நன்மையோடு செகத்திலிரு என்மகனே அதின் முன்னாக அன்னீத மாபாவி ஏதுவினை செய்தாலும் எண்ணம்வையா தேமகனே தாழ்ந்திரு என்மகனே சட்டைக்குள் ளேபதுங்கிச்  சார்ந்திரு என்மகனே தனதில்மிக நினைத்துக் கோபமாய் விள்ளாதே குவியச் சிரியாதே பாவத்தைக் காணாதே பாராக்கிரமங் காட்டாதே ஆக்கிரம மெல்லாம் அடக்கியிரு என்மகனே தாக்கிரவா னாகிடினும் சற்றும் பகையாதே  எல்லா முன்னருகே இருந்துகேட்டுக் கொள்வேனான் பொல்லாதா ராகிடினும் போரப் பகையதே வாரஞ் சொல்லாதே வளக்கோரம் பேசாதே  சார மறிந்து தானுரைநீ சொல்லுரைகள் ஆய்ந்து தெளிந்து அருளுநீ யென்மகனே ஏய்ந்துநீ தர்மம் இடறு நினையாதே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(284,'விஞ்சையருளல்','ஈனமில்லாத் தேடு எமக்காகுஞ் சாதியின்மேல் மானமாய் வருந்தி மகிழ்ந்திரு என்மகனே அன்பான அவ்வினர்க்காய் அறிவில் வருந்தியிரு தன்பாலே வந்தவுடன் சரியாச்சு தென்மகனே மகனே நானுனது மனதுட் குடியிருந்து சிவமே பொருந்தி செப்புவது முத்தரவே துல்லிபமுங் காட்டேன் சூட்சமது வுங்காட்டேன் நில்லு நினைவில்நீ  சரித்துக்கொடு என்மகனே பேயன் பயித்தியக் காரனெனப் பேசியுன்னை நீசக் குலங்கள் நின்னையடிக் கவருவார் சரித்துக் கொடுமகனே சற்றுங் கலங்காதே ஒருவரோ டும்பிணங்கி உரையாதே என்மகனே எல்லாம் நான்கேட்டு ஆட்கொள்வே னென்மகனே வல்லாண்மை பேசாதே மாதிரி போடாதே ஏழையா யிருநீ என்னுடைய கண்மணியே ஆழ மனதுடைய அதிகமக னேவுனது விதங்க ளறியாமல் வீணாவார் வம்பரெல்லாம்  மதங்க ளடக்க வாக்கெனக்கே ளென்மகனே தந்தே னுனக்கு தரளமணி முத்திரியும் கந்தைத் துணியதிலே காட்டாதே வைத்துக்கொள்ளு மகனே வுனது மனறியக் காட்டினதை அகமேநீ வைத்து அகமகிழு என்மகனே பொறுதிதா னென்மகனே பெரியவ ராகுவது உறுதிமிக வுண்டாகும் உகநாதா என்மகனே தருமச் சிறப்புத் தான்கண்டா யோமகனே பொறுமை பெரிது புவியாள்வா யென்மகனே கண்டாயோ என்மகனே கரியமண் டபச்சிறப்புப்  பண்டையுள்ள தேரும் பதியுமிகக் கண்டாயோ தேட்ட முடனுனக்குச் செப்பும்விஞ்சை யானதிலே  நாட்ட மறவாதே நாரணா என்மகனே அப்போது ஆதியுடன் அம்மகவு ஏதுரைக்கும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(285,'விஞ்சையருளல்','இப்போது என்றனக்கு இத்தனையுஞ் சொன்னீரே கலியுகத் தைமுடித்துக் கட்டானத் தர்மபதி வலியுகத்தைக் காண்பதெப்போ மாதா பிதாவேயென்றார் அப்போது மகனை ஆவிமுகத் தோடணைத்து இப்போது சொல்லுகிறேன் என்மகனே கேட்டிடுநீ நீபோய்த் தவசு நீணிலத்தில் செய்திருக்க  நான்போய் நடத்தும் நல்வளமை கேட்டிருநீ ஆயிரத் தெட்டு ஆனதிரு மண்டபத்தில் வாயிதமா யாண்டொன்றில் வைத்தமண்ட பங்களிலே  நூற்றிப் பன்னிரண்டு நொடிப்பே னோராண்டதிலே தோற்றுவிப்பே னார்க்கும் தூது மிகநடந்து பத்தாண்டுக் குள்ளே பலசோதனை பார்த்து வித்தார மேலே விசாரிப்பேன் கண்டாயே அங்கங்கே நாட்டிவைத்த ஆணிவே ரத்தனையும் எங்கெங் குமொடுக்கி யானுன் னிடமிருந்து ஒக்க வொருகுடைக்குள் உன்கையிலே வேருதந்து துக்கக் களையறுத்துச் சுத்தமரை யாளாக்கி உன்மூப் புயர்மூப்பாய் உல்லாச மேதருவேன் பின்மூப் பில்லாமல் பெரியோனின் நாமமதாய் அடக்குடக் கெல்லாம் அன்றுனக்கு நான்தருவேன் வடக்கு வெளிவாசல் மலைமீதி லோர்புதுமை  உண்டாகு மகனே உனக்கு நற்காலமதே தண்டா யுதத்தோடே சத்தமொன் றேபிறக்கும் மேலக்கால் மண்டபமும் மேல்மூடி தான்கழன்று காலத்தால் காணும் கண்மணியே யுன்காலம்  தெச்சணா மூலை தென்பாற் கடலருகே  மெச்சும் பதிமூலம் விளக்கெரியக் கண்டுனது கால மிதுமகனே கன்னிதிரு தென்திசையில்  கோபுர மண்டபமும் கொடிமரமும் வீதிகளும் தாபரமாய்த் தன்னால் தலங்க ளதுதுலங்கும் கண்டதுவே காலம் கனத்தவலு சூரியனும் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(286,'விஞ்சையருளல்','பண்டு முறைதப்பிப் பருதிரத்தக் கண்வெறியாய் மகனே வுனக்காகா மாநீசப் பாவியெல்லாம் அகமே கனல்தாவி அன்றாடம் தானழிவார் உகமீதே தர்மம் உனக்கெனக்கென் றேகுதிக்கும் மகனே வுனது மாதவத்தோர் காலம்நன்று கால மிதுமகனே கண்மணியே கேட்டிடுநீ தூல முரையாதே துலங்கும்நா ளாகுமட்டும் எளியோரைக் கண்டு இரங்கியிரு என்மகனே வலியாரைக் கண்டு மகிழாதே என்மகனே தாழக் கிடப்பாரைத் தற்காப்ப தேதருமம் மாளக் கிடப்பார் மதத்தோர்க ளென்மகனே இவ்வகைக ளெல்லாம் என்மகனே யுன்காலம் அவ்வகைக ளெல்லாம் ஆராய்ந்து பார்த்திருநீ இனத்துக் கினங்கள் இருப்பேனான் சுவாமியென்று மனதில் வேறெனவே வையாதே யென்மகனே  நாலுவே தமதிலும் நான்வருவேன் கண்டாயே தூல வேசமிட்டு சுற்றுவேன் கேட்டிடுநீ தியங்காதே யென்மகனே சீருள்ள கண்மணியே  மயங்கா தேமகனே மாதவங்கள் பெற்றவனே அவரவர்கள் மனதில் ஆன படியிருந்து எவரெவரை யும்பார்த்து இருப்பேனா னுன்னிடமே  லிங்கமொன் றேதிக்கும் இணையாக ஓர்சோடாய் தங்கநவ ரெத்தினங்கள் சமுத்திரத்தி லேகாணும்  ஆனையொரு கன்றீனும் அதினா லுலகமெல்லாம் தானாக ஆளும் தலைவனும்நீ யல்லாது மலையடி வாரம் வளருமொரு விருச்சம் அலையதி லும்பெரிதாய் அதுதழையுங் கண்டாயே சிலையுள்ளே தோன்றி சிவதோத்தி ரமாக நிலையொன்றா யாகி நீயாள்வா யுன்காலம் வாரிக் கரையில் மண்டபமொன் றேவளர்ந்து நாரியர்கள் கூட நாடெங்கும் வாசமிட்டு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(287,'விஞ்சையருளல்','ஒருகுடைக்குள் ளாள்வாய் உன்கால மென்மகனே தருமமதாய்த் தாரணியில் தன்னந்தன் னால்வாழ்ந்து பொறுமைப் பெரியோராய்ப் பூதலமெல் லாம்வாழ்வார் அப்போது நீயரசு ஆளுவாய் யென்மகனே வாரிமூன்று கோதி வளைந்திருக்கு மோர்தீவை சாதியொரு நிரப்பாய்த் தானாள்வா யென்மகனே கோடுபல வாதும் கோள்நீசப் பாவிகளும் கேடு வருங்காலம் கிருஷ்ணா வுன்நற்காலம் வானமது வெள்ளி மாறிவெறும் வானமதாய் மேன் முகிலற்று வெறுவான மாய்த்தோன்றும் நல்ல மகனே உனக்குவரும் நற்காலம் சொல்லத் தொலையாது சூல்மகனே என்கணக்கு  ஒக்க அடக்கி உரைத்திருக்கு வுன்னிடத்தில் நிற்க நிலைக்க நினைத்ததெல்லா மங்காகும் பொறுமை பெரிது பெரிய திருமகனே தருமம் பெரிது தாங்கியிரு என்மகனே எல்லா முனது இச்சையது போல்நடக்கும் நல்லார்க்கு மோர்நினைவே நலமாகு மென்றுசொல்லி எல்லார்க்கும் விளம்பி இருநீ யிருமகனே பொல்லாதா ரோடும் பொறுமை யுரைமகனே தன்னாலே யாகுமட்டும் சாற்றியிரு என்மகனே  வந்தா ரறிவார் வராதார் நீறாவார் இன்ன முன்காலம் இயம்புவேன் கேள்மகனே தன்னந் தன்னாலே சாதிக்குச் சாதிமாளும் நல்ல நினைவோர்க்கு நாளெத்தனை யானாலும் பொல்லாது வாராமல் புவிமீதில் வாழ்ந்திருப்பார் நாட்டி லொருஅனுமன் நல்லவனை நானயச்சு கோட்டிசெய் யென்றுரைப்பேன் கோமகனே வுன்காலம் முறைதப்பி யாண்ட முகடன் வெறும்நீசன் குறைநோவு கொண்டு கூடிழப்பா னுன்காலம் அதிலே சிலபேர் அரசுனக் கெனக்கெனவே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(288,'விஞ்சையருளல்','விதியை யறியாமல் வெட்டிக்கொண் டேமாள்வார் அத்தருண முன்காலம் அதிகமக னேகேளு இத்தருண மல்லால் இன்ன மெடுத்துரைப்பேன் எல்லோருங் கைவிட்டு இருப்பாருனைத் தேடாமல் நல்லோர் மனதில் நாரணா என்றுரைப்பார் கோல விளையாட்டுக் கொஞ்ச மெடுப்பேனான் தூல மறியாமல் தொல்புவியெல் லாமயங்கும் அன்போர்க ளெல்லாம் ஆவியே தான்மறுகி இன்ப முடனே ஏங்கியேங்கி யழுவார் இத்தனை நாளும் இவரைநம்பி நாமிருந்து  புத்தியது கெட்டோம் என்றுமிகப் பேதலிப்பார் அப்போது ஆகா அன்னீத மாபாவி இப்போது கண்டுதென்பார் ஏலமே சொன்னதெல்லாம் எய்த்தானென்பா னன்போரை இடுக்கஞ்செய் தேயடிப்பான் சூட்ச முடனே சொரூபமொன்று நானெடுப்பேன் அதுதா னொருகாலம் அதிகமக னேகேளு இதுபெரிய சூட்சம் என்மகனே கேட்டிடுநீ அதின்மேலே அன்போர் அதிகப் பெரியோராய் இதின்மேலே தாண்டும் எல்லோரும் வல்லோரே பின்னுமொரு சூட்சம் பிரமாண மாயெடுப்பேன் பன்னு மணியே பராபரமே கேட்டிடுநீ குளத்தைத் தடதடெனக் கொந்துகொந் தாயுடைத்து சுழற்றக்கன லக்கினியும் துர்க்கைமா துர்க்கையையும் விட்டயச்சு மாநிலத்தில் விளையாடி யேதிரியும் கட்டணங்க ளாக கனமாய்க் குழுகுழென  ஓடுவார் பதறி விழுவா ரறமெலிந்து நாடும் பெரியோர் நலமாக வாழ்ந்திருப்பார் என்மகனே வுன்காலம் இன்னும் வகுப்பேன்கேள் பொன்மகனே பூமியிலே பின்னுமொரு காரணங்கேள் கெட்ட நசுறாணி கெம்பிக் கடைக்காலம்  மொட்டைத்தலை யன்வருவான் முளையனவ னோடிடுவான்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(289,'விஞ்சையருளல்','ஆடிக் கொண்டாடி அங்குமிங்குந் தானலைந்து ஓடிக் கொண்டோடி ஒன்றுபோ லேமாள்வான் மகனே யுன்காலம் மகிழ்ந்திரு என்மகனே உவமையொன்று சொல்லுகிறேன் உற்பனமாய்க் கேள்மகனே புத்திரனுக் கேகுருவும் புகன்றதெல்லாம் புகன்றாலும் சுற்றுமொரு சூட்சத் தொழிலுண்டு மாயானுள் அத்தனையு மென்னுள் அடக்கமில்லை யென்மகனே முத்தியெனை யீன்றோர் முதனாளோர் விஞ்சைவைத்தார் அவ்விஞ்சை மாத்திரமே யானுனக் கீயவில்லை  இவ்விஞ்சை யீவதுதான் எப்போதெனக் கேளு சீமை யரசு செலுத்தவரும் நாளையிலே மேன்மை முடிதரிக்கும் வேளையி லென்மகனே சொல்லுகிற விஞ்சை சுத்தமனே கேட்டிடுநீ பல்லுயிர் களுக்கும் படியளக்கும் விஞ்சையது எல்லா முனக்களித்தேன் என்னுடைய நாரணனே அல்லாமல் நானுனக்கு அருளுகிற விஞ்சையைக்கேள் பெரியோர்க்கு வாழ்வு பெருகிச் சிறந்தாலும் மரியாதை யேயிருக்கும் மகனேநீ கேட்டிடுநீ சற்றோலே வாழ்வு சகடருக் கேவருகில்  கற்றோரே யாகிடினும் கண்டறிவேன் போநீயென்பான் முள்முருக்கம் பூவு மினுக்குமூன் றுநாளை  கள்ளருக்கு வாழ்வு காணுமது போல்மினுக்கு நல்லோர்க்கு வாழ்வு நாளுங் குறையாமல் வல்லோர்க்கும் நல்லோராய் வாழ்ந்திருப்பார் கண்டிருப்பாய் விள்ளாத ஞாயம் மேல்ஞாய மென்மகனே துள்ளாத யானை துடியானை யென்மகனே அடக்கம் பெரிது அறிவுள்ள என்மகனே கடக்கக் கருதாதே கற்றோரைக் கைவிடாதே நன்றி மறவாதே நான்பெரிதென் றெண்ணாதே அண்டின பேரை அகற்ற நினையாதே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(290,'விஞ்சையருளல்','ஆபத்தைக் காத்து அகலநீ தள்ளாதே சாபத்தைக் கூறாதே தன்னளவு வந்தாலும் கோபத்தைக் காட்டாதே கோளோ டிணங்காதே பாவத்தைக் காணாதே பகட்டுமொழி பேசாதே பசுவை யடைத்துப் பட்டினிகள் போடாதே விசுவா சமதிலே விரோதம் நினையாதே எளியோரைக் கண்டு ஈந்து இரங்கிடுநீ அழிவென்ற பேச்சு அனுப்போல் நினையாதே தொட்டுப் பிடியாதே தோர்வைவைத்துக் காணாதே  விட்டுப் பிடியாதே வெட்கமதைக் காணாதே வரம்பு தப்பாதே வழிதவறி நில்லாதே பரம்பூமி கண்டு பாவித் திருமகனே இத்தனை புத்தி எடுத்துரைத்தே னென்மகனே புத்தி விபரீதமாய்ப் பின்னுமா ராயாதே சட்ட மறவாதே தன்னளவு வந்தாலும் நட்டங் காணாதே நாடாள்வா யென்மகனே என்மகனே நானுனக்கு இன்னமொரு விஞ்சைசொல்வேன் பொன்மகனே யுன்னுடைய புத்தியிலே வைத்திருநீ இச்சட்டந் தன்னில் எள்ளளவு தப்பினதால்  தீச்சட்டம் காய்க்கத் தேதிவரு மென்மகனே அனுப்போல் மறவாதே யான்வைத்த சட்டமதில் மனுப்போ லென்றிராதே மனதெண்ண மாயிருநீ பஞ்சமிர்தே யென்னுடைய பாதைதப்பி நீநடந்தால் கொஞ்சுங் கிளியே கொன்னெழுப்பு வேனுனையும் பதறியிரு என்மகனே பம்மியிரு கண்மணியே  பொறுதி மகனே பெரியோரா யாகுவது  உறுதி மகனே உலகமதை யாளுவது மகனேநா னெத்தனையோ மகாபெலங்கள் கற்றவன்காண் உகமீதே சொல்லித் தொலையுமோ வுத்தமனே  எவ்வுகங் களுக்கும் இப்படியே தர்மமில்லை செவ்வுமகா விஷ்ணுதான் என்றுசொல்லார் தேசமதில்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(291,'விஞ்சையருளல்','இப்போது என்மகனே இக்கலியன் மாய்மாலம் செப்ப எளிதல்லவே செயிக்க ஏலாதாராலும் இப்போதுன்னோ டுரைத்த இயல்விஞ்சைக் ககப்படுங்காண் அப்பனே பம்மலிலே அகப்படுங்கா ணிக்கலியும் கலியென்றால் எலியல்லவே கணையாளி வேண்டாமே  வலிமாய நினைவு மாய்மால மென்மகனே ஆனதா லாயுதங்கள் அம்புதடி வேண்டாமே  மானமாக இருந்தால் மாளுங்கலி தன்னாலே இன்னமொரு விஞ்சை இயம்புவேன் கேள்மகனே முன்னம்வகைக் காரனொடு மோதிப் பகையாதே பகைத்தா லிருக்க வொட்டான்கா ணவ்விடத்தில்  உகத்தடக்கு முன்னே ஒருவரையுஞ் சீறரிது தாழ்ந்திருக்க வென்றால் சர்வதுக்குந் தாழணுமே ஓர்ந்திருக்க வென்றால் ஒருவர்பகை யாகாதே ஆனதால் நீயும் அவனைப் பகையாதே நானவனைக் கேட்பேன் ஞாயக்கே டாகிடினும் வலியோர்க் கொருவழக்கு வைத்துநீ பேசாதே மெலியோர்க் கொருவழக்கு வீணாய்ப் பறையாதே  சொத்தாஸ்தி வஸ்து சுகமென்று எண்ணாதே வத்தாஸ்தி பெண்ணு வகையென்று எண்ணாதே நீயெண்ணா தேயிருந்தால் நீணிலங்க ளுமயங்கும் தானிதின்மே லெண்ணம் தங்குதங் காதேயிரு சத்துரு வோடும் சாந்தமுட னேயிருநீ புத்திர ரோடும் பேசியிரு என்மகனே அன்போர்க்கு மீந்திருநீ ஆகாப்பேர்க்கு மீந்திருநீ வன்போர்க்கு மீந்திருநீ வழிபோவார்க்கு மீந்திருநீ சகலோர்க்கு மீந்து தானிருநீ யென்மகனே வலியோர்க்கு மீயு வழிபோவார்க்கு மீயு மெலியோர்க்கு மீயு மேன்மையா யென்மகனே ஆர்க்கு மிகவீந்தால் அந்தத்தர்ம மேகொதிக்கும் போர்க்கு நினைத்தாரைப் பெலிகொடுக்குந் தர்மமது');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(292,'விஞ்சையருளல்','தன்மந்தான் வாளு சக்கரங்க ளல்லாது தின்மையது கேடு செப்பக்கே ளென்மகனே சாதி பதினெட்டும் தன்னாற்கே டாகுமட்டும் நீதி யழியாதே நீசாபங் கூறாதே மகனே வுனக்கு வைத்தவிஞ்சை மனதி லறிந்து கொண்டாயோ அகமே வைத்த சட்டமெல்லாம் அதுபோல் நடந்து அல்லாமல் உகமே யழியு முன்னாக ஒருசொல் லிதிலே குறைவானால்  சகமோ ரறியத் தீமூழ்கிச் சதியாய் மறுத்து முழிப்பாயே நல்லோர் மகனே சொல்வதுகேள் நானோ வுரைத்த விஞ்சையெல்லாம் வல்லோ னான திருமகனே மனதி லறிந்து அறிந்துநட எல்லாம் நமதுள் ளாச்சுதென்று எண்ணி நடப்பொன் றுன்னாலே சொல்லால் தாண்டி நடப்பீரால் தீயே குளித்து வருவாயே கொடுக்கும் வரங்களு னக்கீந்தேன் கொடுத்த வரத்தை பறிப்பதற்கு உடக்கு வரமுந் தந்தேனான் உன்ற னாணைத் திருவாணை அடக்கு முடக்குந் தந்தேனான் ஆதி சிவனா ரவராணை நடக்கும் படியே சட்டமெல்லாம் நாரா யணனே தந்தேனே தந்த வரங்கள் தவறாது தருணஞ் சடையுந் தரைமீது எந்தன் மீது மறவாதே என்னாண் டருளு மிறையோனே அந்தன் மீது மறவாதே ஆயா னெனவே அறிவில்வைத்துச்  சிந்தை மகிழ்ந்து முறைநடந்தால் சிவனு முனக்குள் வசமாமே தாயா ரோடு சிவமாது சரசு பதியே பொன்மாது நேயா மாதர் மடமாது நீணில மறிய வந்துனது பூசா பலன்கள் சொல்லிமிகப் புலம்பித் திரிவார் துயர்தீர மாயா திருக்கு மகனேவுன் மனமே மகிழ்ந்து மகிழ்ந்திருவே சொல்ல எளிதோ என்மகனே சொல்லா தனேகம் தோன்றுமினி வெல்ல எளிதோ என்மகனே மேலோரா ராரா லும் கொல்ல எளிதோ வுனைமகனே கோவேங் கிரியின் கோனாலும்  வெல்ல எளிதோ வுனைமகனே மேலாங் கண்ணே மிகவளராய் இந்தப் படியே நாரணரும் இயம்ப மகவு ஏதுரைக்கும் கந்த னுறுவே லென்றகப்பா கடியா வுனது படிநடக்க எந்தப் படியோ நான்றமியேன் ஏதோ அறியப் போறேனெனச்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(293,'விஞ்சையருளல்','சிந்தை மகிழ்ந்துத் தகப்பனுட திருத்தாள் பிடித்துச் செப்பலுற்றார் தன்னம் பெரிய தாட்டீக வைகுண்டரும் என்னசொல்வீ ரென்றனக்கு என்றகப்பா நீரெனவே வணங்கிப் பதம்பூண்டு மாதாவை யுந்தொழுது இணங்கி யிவர்கேட்க ஏதுசொல்வார் நாரணரும் நாராணா வைகுண்டா நன்மகனே என்னையினி வாரணமே நீவணங்கி மகனே கைசேராதே நீகை குவித்தால் எனக்குமிகத் தாங்கரிது தானீத னான சர்வபரா என்மகனே ஒருவருந் தாங்கரிது உன்கை குவித்தாக்கால் குருதெய்வம் நீயே கோவே குலக்கொழுந்தே இன்ன முனக்கு இயம்புகிற விஞ்சையதும் பொன்னம் மகனே பூராய மாய்க்கேளு சாதி பதினெட்டும் தலையாட்டிப் பேய்களையும் வாரி மலையதிலும் வன்னியிலுந் தள்ளிவிடு வரத்தை மிகவேண்டி வைத்துக்கொள் ளென்மகனே   சரத்தை மிகவாங்கித் தானனுப்புப் பேய்களையும் உட்கோள் கிரகம் உறவுகெட்ட பேர்முதலாய்க் கட்கோள் கருவைக் காணாதே சாபமிடு செல்வம் பொருந்திச் சிறந்திரு என்மகனே கொல்லென்ற பேச்சுக் கூறாதே என்மகனே ஒவ்வொன்றைப் பார்த்து ஊனு ஒருசாபம் செவ்வென்ற பேச்சுச் செப்பியிரு என்மகனே உரைத்த விஞ்சையெல்லாம் உனக்குப்போது மோமகனே விரைத்தமுள்ள வைகுண்டா விற்பனவா நீகேளு நீபோ யிருக்கும் இடங்களில் என்மகனே அன்பான பஞ்சவர்கள் அங்கேவுண் டென்மகனே முன்னே பிறந்த உகத்துக் குகங்களெல்லாம் என்னைவிட் டகலாது இருந்தார்கள் பஞ்சவர்கள் ஆனதா லிப்போது யானும்நீ யானதினால் மானமுள்ள பஞ்சவரை மகனேமுன் விட்டுக்கொள்ளு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(294,'விஞ்சையருளல்','அல்லாமற் கேளு அரிநாரணா என்மகனே நல்லோராய்ப் பெண்கள் நாடியெண்ணக் கூடாது வல்லோராய் நந்தன் வம்மிசத்தில் வந்திருந்தார் அப்பெண்ணார்க் கெல்லாம் அதிகப்பலன் தாறோமென்றேன் இப்பெண்ணார் சிலர்கள் இன்னமங்கே தோன்றிவந்தால்  ஆனதால் பெண்ணார்க்கு அழகுகொடு என்மகனே நின்ற தவசு நிறைவேறி னால்மகனே ஒண்டொடியார் சிலரை உன்பாரி தானாக்கி நன்மைகொடு என்மகனே நாரணா நாடாள்வாய் உன்னை நினைத்தோர்க்கு உதவிகொடு என்மகனே கட்டை விடாதே கருத்துள்ள என்மகனே சட்ட மறவாதே சாஸ்திரத்துக் குற்றவனே பெண்பாவம் பாராதே பேணியிரு என்மகனே கண்பாரு கண்மணியே கன்னி கணவருக்கு இன்னமொரு சூத்திரம் இயம்புகிறே னென்மகனே துன்ன மறியத் தோற்றுவிப்பேன் சாதிதோறும் அன்புவன்பு பார்க்க ஆரா ரிடத்திலும்போய்  உன்புதுமை சொல்லி உடலை மிகஆட்டி சாதிசா திதோறும் சக்கிலி புலச்சிவரை ஆதிச்சா திமுதலாய் ஆராதனை காட்டுவிப்பேன் காட்டுவிக்குஞ் சொரூபம் கண்டிரு என்மகனே ஆட்டுவிப்பேன் நால்வேதம் அதிலும்நான் மாயமிட்டு துலுக்கன்வீ டானதிலும் சூதாட்டுப் பார்த்திடுவேன் கிலுக்கமொன்று செய்வேன் கீழுமேலும் நடுவும் பதறித் தெளியும் பார்த்திரு என்மகனே   நீபெரிது நான்பெரிது நிச்சயங்கள் பார்ப்போமென்று வான்பெரி தறியாமல் மாள்வார்வீண் வேதமுள்ளோர் ஒருவேதந் தொப்பி உலகமெல்லாம் போடுஎன்பான் மறுத்தொருவே தஞ்சிலுவை வையமெல்லாம் போடுஎன்பான் அத்தறுதி வேதம் அவன்சவுக்கம் போடுஎன்பான் குற்ற முரைப்பான் கொடுவேதக் காரனவன்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(295,'விஞ்சையருளல்','ஒருவர்க் கொருவர் உனக்கெனக் கென்றேதான் உறுதி யழிந்து ஒன்றிலுங்கை காணாமல் குறுகி வழிமுட்டி குறைநோவு கொண்டுடைந்து மறுகித் தவித்து மாள்வார் சிலபேர்கள் ஓடுவார் சிபேர் ஒழிவார் சிலபேர்கள் கேடு வருமே கேள்விகேளாப் பேருக்கெல்லாம் அதன்மேல் மகனே யானுனக்குச் சொல்வதுகேள் இதின்மே லுனக்கு ஏகவியா ழம்வரும் சந்தோச மாகுதுகாண் உனைச்சார்ந்த அன்போர்க்கு எந்துயர மெல்லாம் ஏகுதுகா ணென்மகனே அதுவரையும் நீதான் அன்பா யிருமகனே எதுவந் தாகிடினும் எண்ணம்வையா தேமகனே சுறுக்கிட்டு யானும் ஒவ்வொன்றாய்த் தோன்றவைப்பேன் இறுக்கும் இறைகளெல்லாம் இல்லையென்று சொல்லிடுநீ  வேண்டா மிறைகள் வேண்டாமென்று சொல்லிடுநீ ஆண்டார் மகனே அதிகத் திருமகனே நல்ல மகனே நான்வைத்த விஞ்சையெல்லாம் செல்ல மகனேவுன் சிந்தையிலே பற்றினதோ போதுமோ விஞ்சை புகலணுமோ என்மகனே சாருமொரு விஞ்சை தான்சொல்வேன் கேள்மகனே முவ்விஞ்சை வைத்தேன் உலகமறி யாதவிஞ்சை இவ்விஞ்சை மாத்திரமே இனம்பிரித்துச் சொல்லாதே பெற்றோர்கள் கண்டுகொள்வார் பேசரிய என்மகனே கற்றோர்கள் கண்டுகொள்வார் கண்ணே திருமகனே கண்ணே மகனே திருமகனே கமலப் பூமா கரிமகனே எண்ணே யெழுத்தே என்மகனே இறையோர் தொழவே வருமகனே ஒண்ணே மகனே உயர்மகனே உடைய மகனே கண்மணியே தண்ணே மகனே தவமகனே சாகா திருக்குஞ் சலமகனே மகனே தவமே மரகதமே மாதவம் பெரிய மலரோனே தவமே யுனதுள் வைத்தவிஞ்சை தானே போது மோமகனே எகமேழ் மகிழ வந்தவனே என்றன் மகனே வளர்வையென');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(296,'விஞ்சையருளல்','உகமே ழளந்தோ ருரைத்திடவே உயர்ந்த மறையோர் வாழ்த்தினரே அய்யாநா ராயணரும் ஆதிவை குண்டமென மெய்யா யுரைத்து விளங்கவே வாழ்த்தலுற்றார்  மகனே வுன்தேகமதில் வைத்த நிறங்களெல்லாம் உகமழியு முன்னே ஒருவர்கண் காணாதென அகமே வைத்து அகமகிழு என்மகனே வைகுண்ட மென்றுமிக வாழ்த்தக் குருநாதன் மெய்குண்டத் தோர்கள் மேலோர்கள் வாழ்த்தலுற்றார் மகரவுந்தி விட்டு வைகுண்டந் தான்பிறந்து சிகரமுனி தேவருட சிறப்பதிலே வந்தவுடன் தேவர் திருவானோர் சென்றெடுத்து வைகுண்டரை மூவர்தே வர்மகிழ்ந்து முதலோனைக் கைகுவித்து ஏந்தி யெடுத்து இளந்தொட்டில் மீதில்வைத்துச் சாந்தி கழித்துத் தவலோகச் சட்டையிட்டுச் சீதை மயக்கமெல்லாம் தீர்ந்திடவே சாபமிட்டு வீரரையும் வந்து வீரலட்சு மியெடுத்து மடிமீதில் வைத்து மாதுசர சோதியுடன்  குடியான பேரும் கொண்டங் ககமகிழ்ந்து பொன்தொட்டி லிட்டுப் பொற்சீதை தாலாட்ட பைங்கொடி யாள்சரசு பதிமாது தாலாட்டத் தேவரிஷி வானோர் தேவரம்பை மாதரெல்லாம் சீவசெந் தெல்லாம் திருப்பாட்டுப் பின்பாட ஈசர்முதல் சங்கம் எண்ணி மனமகிழச்  சார்சர சுபதியாள் தாலாட்ட வேதுணிந்தாள் பொன்மணிப் பதியி னுள்ளே புனலரி கிருஷ்ணர் பெற்ற  தண்மணிப் பாலன் தன்னைத் தங்கமாந் தொட்டி லிட்டு மின்மணி கொடிசேர் கன்னி விளங்கிய சோதி மாது கண்மணி வைந்த ராசர் காணத்தா லாட்டி னாளே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(297,'தாலாட்டு','தார தரதர தாராரோ தார தரதர தாராரோ நாராயணர் தானோ தாராரோ நல்ல நாராயண வைகுண்டமோ  காரணர் தானோ வைகுண்டமோ கயிலாச நாதக் கண்மணியோ செல்வ முதலான சீமானோ சிவசிவ சிவ சிவமுதலோ அல்ல லகற்றியே அரசாளும் அரியோன் மிகப்பெற்ற அரிதானோ தெய்வப் பெருமாள்தான் பெற்றகன்றோ சீதை மாதுதான் பெற்றகன்றோ மெய்ய னரிநாதன் பெற்றகன்றோ விஷ்ணு மகாபரன் பெற்றகன்றோ ஈசர் மைத்துனர் பெற்றகன்றோ இறவா திருமாது பெற்றகன்றோ மாயத் திருவுளம் பெற்றகன்றோ மான வைகுண்ட ராசக்கன்றோ வேதக் குருநாதன் பெற்றகன்றோ வீர லட்சுமி ஈன்றகன்றோ சீதக் குருதாயா ரீன்றகன்றோ தெய்வ வைகுண்ட ராசக்கன்றோ கிருஷ்ண மகாநாதன் பெற்றகன்றோ கிருபைக் குருநாதக் கண்மணியோ  விஷ்ணு மகாநாதன் பெற்றகன்றோ வீர லட்சுமி ஈன்றகன்றோ நாட்டுக் குடையதோர் நாரணரின் நல்ல பாலனோ வைகுண்டனோ ஏட்டு முதலானோ தாராரோ ராச வைகுண்ட ராசர்தானோ  மூலச் சிவநாதன் பெற்றகன்றோ உலகை யொருகுடைக் காள்வானோ நாலு வேதமும் தாண்டிமுறை நடத்தி யொருகுடைக் காள்வானோ ஒருமை மனத்துடை யுத்தமர்க்கு உற்ற பதவிகள் கொடுப்பவனோ தருமத் திறவானோ தாராரோ தங்க வைகுண்டத் தாட்டீகனோ மகரச் சீதையாள் வயிற்றுலுற்ற வளர்ந்த வைகுண்ட மாமணியோ அகரச் சிவகோபுர மழகுபதி அதிகப் பதிகெண்டு வந்தவரோ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(298,'தாலாட்டு','தங்கக் கோபுரம் தளிர்மரமும் சதுர மேடைகள் கண்டவரோ சங்க மகிழவே வந்தவரோ சகல கலைதமி ழாய்ந்தவரோ உலகம் பதினாலு மொருகுடைக்குள் ஒருசொல் மொழிக்காள  வந்தவரோ இலகு பிரகாச சுவடுகொண்டு இலங்கும் பதியாளப் பிறந்தவரோ வம்புக் கலியுகக் குலமறுத்து வைந்தப் பதியாள வந்தவரோ அம்புக் கணையொன்று மில்லாமலே அறுக்க வந்தாரோ கலிதனையும் கூடப் படைகள்துணை யில்லாமல் குறும்பை யடக்கவே வந்தவரோ சாடத் தலையாரி யொன்றில்லாமல் சதைக்க வந்தாரோ கலியுகத்தை வாளு மாயுத மெடுக்காமலே வதைக்க வந்தாரோ கலியுகத்தைக்  கோளு பேய்களைக் கிரகங்களைக் கொல்ல வரம்பெற்ற வைகுண்டரோ சாணாக் குருநாத வைகுண்டரோ சாதி தற்காக்குந் தலையாரியோ காணாக் கருவான கருமூலமோ கர்த்தன் கர்த்தாதிக் கடவுள்தானோ நீசக் குலங்களைக் கருவறுக்க நெடிய நாரணர் பெற்றகன்றோ தோசப் புழுச்சாதி குலமறுக்கத் திருமால் நாரணர் பெற்றகன்றோ  சீமை மைம்பத்தாறு தேசமெல்லாம் சொல்லொன் றுள்ளாள வந்தவரோ நாமம் பெரியதோர் வைகுண்டரின் நாமம் பெறவோங்க வந்தவரோ தெய்வப் பாலர்கள் சிறந்துபோற்றச் சீமை யரசாள வந்தவரோ மெய்வ ரம்பதின் முறைநடத்தி மேன்மைச் செங்கோல் முடிதரித்து பொறுமைப் பெரியோனோ வைகுண்டனோ பெரிய பூமேடைக் கொலுவீரனோ தரும வரம்புகள் தவறாமலே தரணி யரசாளும் வைகுண்டரோ திராசு நிறையிலுந் துல்லியமாய்ச் செங்கோல் செலுத்தவே வந்தவரோ மிராசு மூவர்க்கு முதன்மைதானோ மூலச் சிவமணி குருநாதனோ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(299,'தாலாட்டு','சான்றோர் கைகட்டிச் சரணங்கூறத் தரணி யொருகுடைக் காள்வானோ ஆண்ட மணிநாதன் நாரணர்க்கு ஆன மதலையாய் வந்தவரோ வைய மளந்ததோர் நாரணர்க்கு மதலை யெனவந்த வைகுண்டரோ தர்மப் பதியாளும் வைகுண்டரோ சாணர்க் கனுகூல மானவரோ அலைந்து மலைந்ததோர் சான்றோர்க்கு அதிக விதிகொண்டுப் பிறந்தவரோ சாணாக் குடிகுல நாயகமோ தர்மந் தழைக்கவே பிறந்தவரோ தோணாத் துறைகொண்டு வந்தவரோ துவரா பதியாளப் பிறந்தவரோ ஏழு யுகபரக் கணக்கையெல்லாம் எடுத்து நடுத்தீர்க்கப் பிறந்தவரோ ஆளும் வைகுண்டப் பதியாளவே அரிய வைகுண்டம் பிறந்தவரோ வீர வீராதி வீரன்தானோ வீர சூரனா ரீன்றகண்ணோ சூர சூராதி சூரன்தானோ சூர சூரனார் பெற்றகண்ணோ நாத நாரணர் பெற்றகண்ணோ நாக மணிநாதன் பெற்றகண்ணோ  சீதக் குணநாதன் பெற்றகண்ணோ சீதை மணவாளன் பெற்றகண்ணோ மேக நிறத்தண்ண லீன்றகண்ணோ மேலோர் போற்றும்வை குண்டக்கண்ணோ ஏகச் சிவநாதன் பெற்றகண்ணோ இறவா திருமூர்த்தி பெற்றகண்ணோ');");

        //11th Day

        db.execSQL("INSERT INTO akilathirattu2 VALUES(300,'வேறு','சந்திர சுந்தர தந்திர மந்திர சாமிசீர் பாலகனோ தாயக மாயக நாயக மாகிய தாண்டவ சங்கரனோ சுந்தர சந்திர யந்திர குஞ்சித சூரியப்பிர பாலகனோ சூரனோ வீரனோ மாதனோ நீதனின் சூலகலா திபனோ தந்திர மந்திர இந்திர சுந்தரின் சந்ததி சந்ததியோ சகடவெலி சண்டரை அகடவெலி செய்திடும் சுக்கிர விக்கிரனோ கொந்தரி சந்ததின் தந்திர சுந்தர கோசல நாயகனோ கொடுகலி யறவொரு கடுமுனை கொண்டருள் குலவெங்கிட குண்டமோ சுத்தனோ கர்த்தனோ நித்தனோ அத்தனோ துலங்கும் தங்கரனோ சூரனோ வீரனோ தீரனோ நாதனோ சூரியப்பிரகாச வீதனோ விரனோ காரனோ தாரனோ சாரனோ வெங்கிட ரங்கிடனோ விகடக டகடக டகுட டடமருள் வேதவிக்கிர சுக்கிரனோ தீரனோ காரனோ சீரனோ வீரனோ செந்திலம்பதிவளர் சந்திரனோ திகதிக தரிகிட செகமுக மருள்புரித் திக்கிர விக்கிரனோ நீதனோ கீதனோ வீதனோ மாதனோ நீதவெங் கோகுலனோ நெகிடுகலி யகடற விகடுகுடல் கருவற நெக்கிர விக்கிரனோ இப்படி ஒளவை மாது இயல்புதா லாட்டக் கண்டு  செப்படி முனிமா ரெல்லாம் சிவவைந்தர் பதத்திற சென்று முப்படி முறைபோல் தேவர் முக்கந்தன் பள்ளி கொள்ள அப்படித் தேவ ரெல்லாம் அவரிசை கூற லுற்றார் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(301,'பள்ளி உணர்த்தல்','பல்லவி பள்ளியுணராய்-சிவமருகா நீ பள்ளியுணராய் சரணம்\n பள்ளியு ணராய்நீ தெள்ளிமை யாகவே பாற்கடல் மீதினில் தாக்குட னுதித்தவா வெள்ளி யுதித்திடப் பள்ளி யுணர்த்தவும் வேதநா ராயணர் சீதகோ பாலர்க்குக் கொள்ளி யெனவந்த வைகுண்டக் கோவனே கோவேங் கிரிவள ரீசர் மருகோனே வள்ளி மணவாளர் வளர்செந்தூர் வாரியில் வந்து பிறந்துநல் தெச்சணம் புகுந்தவா (பள்ளி)  வேக முடனொரு சந்தத்து ளாடியே மேதினி யோர்க்குப காரங்கள் செய்யவே ஆகாத்த பேர்களை அக்கினியில் தள்ளவும் ஆகின்ற பேர்க்குப காரங்கள் செய்யவும் தாகத்துக் கானதோர் தண்ணீர் கொடுத்துநீர் சஞ்சல நோய்பிணி யஞ்சற்குத் தீர்க்கவும் ஏகத்துட னாளும் நாரண வேந்தர்க்கு  எம்பி யெனவரும் தம்பிரா னானவா (பள்ளி) மூர்க்கன் கலியுக ராசனைத் தட்டியே முடுமுடுக்கஞ் செய்த மூர்க்கரை வெட்டியே ஆர்க்கமு டன்புவி யைம்பத்தாறு சீமையும் அடக்கியொரு சொல்லுக் குடைக்குள்ளே யாளவும் காக்காமகா தர்ம கற்பையுங் காக்கவே கர்த்தனரி கிருஷ்ணர் புத்திர ராய்வரும் தாக்கத் திறம்வளர் வைகுண்ட ராசரே தர்மமதி லுறையும் பொறுமைக் குலதீரா (பள்ளி) தம்பில மானகு றோணிகுண் டோமனைத்  தத்தியாத் தில்லைமல் லாலனைச் சூரனை வம்பு இரணிய ராவண சூரனை');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(302,'பள்ளி உணர்த்தல்','மகோதர னானது ரியோதனப் பாவியைக்  கொம்பிலுங் கெம்பிலும் அம்பினாற் கொன்றதோர் கோபால கிருஷ்ண குழந்தைவை குண்டரே நம்பின அன்பருக் குபகார சாலியே நாரணா சீமைக் கரிவரி யானவா (பள்ளி)  பள்ளிதா னுணர்த்தத் தேவர் பரிவுடன் கேட்டு வைந்தர் தெள்ளிவை குண்ட ராசர் சிந்தையி லன்பு கூர்ந்து நள்ளிய தேவ ரார்க்கும் நயமுடன் தயவ மீந்து துள்ளியே தகப்பன் பாதம் தொழுதவர் வணங்கிச் சொல்வார் தேவர் துயரமெல்லாம் தீர்ப்போங்கா ணென்றுசொல்லி மேவலர்கள் போற்றும் விசயவை குண்டராசர் தகப்ப னுடபதத்தில் சரண மிகப்பணிந்து உகப்பரனார் நோக்கி உரைக்கிறா ரன்போரே  என்னை பிதாவே மாதாவே யென்தாயே எல்லா விவரமதும் எடுத்துரைத்தீ ரென்றனக்கு நல்லோரே நானினித்தான் நடக்க விடைதாரும் பண்டுள்ள ஆகமம்போல் பால்வண்ணர் பெற்றிடும்வை குண்டர் தெச்சணமே கொள்வார்தான் பள்ளியென ஆதியா கமப்படியே அனுப்பிவையு மம்புவியில் சோதிமணியே சுவாமி யெனத்தொழுதார் உடனே மகனை உவந்துமுகத் தோடணைத்துக் கடலின் சிறப்பைக் கண்டாயோ என்னுசொல்லி மகனே வுனைவிட்டு மறுவூரு வாழ்வேனோ நகமுஞ் சதையும்போல் நான்வருவே னுன்கூட  எத்தனை கோடி இயல்தர்மஞ் செய்துவுன்னைப் பெற்றெடுத்தே னுன்கூட பிறகே வருவேனப்பா நீயொருவ னெனக்கு நிலையென்று காணுமுன்னே  தாயில்லாப் பிள்ளையைப்போல் தட்டழிந்தே னென்மகனே மகனே வுனைக்கண்டு மனச்சடவெல் லாந்தீர்ந்தேன் சுகமேபோய் வாவெனவே சொல்லி யனுப்புவேனோ உன்கூட நானும் ஓடி வருவேனப்பா');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(303,'பள்ளி உணர்த்தல்','என்கூட வுன்தாயும் ஏகி வருவாள்காண் என்று தாய்தகப்பன் இவர்கள் வழிகொள்ளவே கண்டு வைகுண்டர் கனமாய் மனமகிழ்ந்து நீங்கள் வருவதுதான் எனக்குவெகு சந்தோசம் தாங்கி வருவேன் தமியேனுங்கள் பாதமதை அப்போது நாரா யணர்மகனே யாவிமிக இப்போ தென்பாலகனே இயம்புகிற புத்தியைக்கேள் சீமை யதுகாணச் செல்லோங்கா ணுன்கூட வாமை மகனே வருவோமுன் கண்காண மகனே நீநடக்க வைகுண்டம்வை குண்டமென உகமீ ரேழுங்காண உரைத்துவிடு யாமமது முன்னடந்து யாமம் மொழிந்துநட என்மகனே பின்னடந்து நானும் யாமம் பிசகாமல்  நடத்திவைப் பேன்மகனே நல்லமணி மரகதமே அடத்திவிட்ட யாமமது அனுப்போல் பிசகாமல் காட்டுவிப்பேன் மகனே கண்ணேநீ முன்னடநீ தாட்டிமையா யுன்வாக்கால் தானுரைத்த யாமமெல்லாம் நடத்திவைக்க வாறேன் நன்மகனே வுன்பிறகே கடல்திரைவாய்ப் பதிகள் கண்டுசென்று தானிருநீ தண்ணீர் மண்ணீந்து தானிருபோ என்மகனே கண்ணி லுருக்காணும் கைக்குளொரு கிள்ளைவரும் பகைவன் மடிவன் பைங்கிள்ளை யங்காகும் உகவன் வருவன் ஒருவ னறிவான்காண் பகவான் சுழற்றும் பாருலக மும்பழுக்கும் அகங்காணும் பாந்தள் அஞ்சாட்சரங் காணும் மூசிர சீர்சிரசு முகாசிரசு முன்காணும் பார்சிரசு கண்டு வாறேனென் பாலகனே திகையாதே முன்னடநீ சந்தமுனி தன்கூட பகையாதே என்மகனே பஞ்சவரைக் காத்துக்கொள்ளு அஞ்சுபூக் கொண்டு ஆசாரஞ் செய்தோர்க்கு வஞ்சக மில்லாத வாழ்வுகொடு என்மகனே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(304,'பள்ளி உணர்த்தல்','மகனே நீவாற வழியின் பவிசுகண்டு தவமுடித்து நீயும் தாண்டியிரு என்மகனே  என்றுவை குண்டருடன் இசைந்தமுனி ரண்டுவிட்டு கொண்டயச்சுக் காத்திருங்கோ குண்டருட பக்கமதில் சாட்சியாய்ப் பார்த்திருங்கோ தரணி வளப்பமெல்லாம் காட்சி கொடுத்திடுங்கோ கண்ணரியோன் முன்னிருந்து கோபங்காட் டாதேயுங்கோ குணமுனிவ ரேகேளும் சாபங் கொடாதேயுங்கோ சாஸ்திர முனிவர்களே நாரா யணர்பெலங்கள் நன்றா யறிவீர்களே சீரான பொறுதி செய்வதுநீர் கண்டிருங்கோ வைகுண்ட மாமுனிதான் வாழுந் தலத்தருகே மெய்குண்டமாய் மேற்கு மேன்மூலை யோர்தனுக்கு வடகீழ் மூலை மாமுனியே ஓர்தனுக்கு இடமிதுவே சொன்னேன் யார்க்குந்தெரி யாதிருங்கோ இந்தப் படிகாத்து இருங்கோ தருணம்வரை சிந்த முனிவர்களே செல்லு மிவர்கூட என்று முனிகளுக்கு இயம்பச் சிவநாதன் நன்றுநன் றென்று நன்முனிவர் சம்மதித்தார் உடனேதான் வைகுண்டர் உள்ள மிகமகிழ்ந்து கடலை மிகத்தாண்டி கரையிலே செல்லுமுன்னே தேவாதி தேவரெல்லாம் திருமுறைய மிட்டனரே ஆவலா தியாக அபயமிட்டார் தேவரெல்லாம்  வைகுண்டருக் கேபயம் முறையமிட்டார் மாதேவர் கைகண்ட ராசருக்குக் காதிலுற அபயமிட்டார் நாரணருக் கேயபயம் நாதனுக் கேயபயம் தோரணருக் கேயபயம் சுவாமி யுமக்கபயம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(305,'தேவாரம்','நாராயண நாதாசிவ நாதா உமக்கபயம் நாடுங்கலி கேடுவரும் நாதா உமக்கபயம் சீராய்ப்புவி யாளவரு வாயே உமக்கபயம் சிவசிவனேசர சரணம்மகா சிவனே உமக்கபயம் பாராயேழை முகமேபரா பரமே உமக்கபயம் பாலர்தெய்வச் சான்றோர்படுந் துயரந் தனைமாற்றும்  நாராயண தீராநல்ல நாகந் தனில்துயில்வாய் நலமோவுனக் கிதுநாள்வரை நாங்கள் படும்பாடு கடல்மேல்துயில் கண்ணாகனி கண்டே கன்றையெறிந்தாய்க்  களமேபெலி கொளவேகூளி களிக்க லங்கையழித்தாய்க் குடைபோல்குன்றை யெடுத்தாய்முப்புர கோட்டை தனையெரித்தாய்க் கோனினிடை மாதரிணைக் கோனா கவேவளர்ந்தாய்க் காடேகாலி மேய்த்தாய்ப்புனல் களியன் தனைவதைத்தாய்க் கஞ்சன்தனை வதைத்தாய்பெலக் காரர்பெலம் வதைத்தாய் மாடேமேய்த்துத் திரிந்தாய்வலு மாபலியைச் சிறைவைத்தாய்க் கோசலாபுவி யாண்டாய்வலுக் குகனை மிகக்கண்டாய்க்  குறோணிதனை வதைத்தாய்வலு குண்டோமசா லியைக்கொன்றாய்க் கொல்லதில்லை மல்லன்கொடுஞ் சூரன் தனைவதைத்தாய் ஈடாய்வலு இரணியன்குடல் ஆறாயோடக் கொன்றாய் இரக்கமற்ற துரியோதனன் அரக்கர் குலமறுத்தாய் இன்னங்கலி நீசக்குலம் கொல்ல வரம்பெற்றாய் ஏழைக்குடி சாதிகட்கு மீள விடைகொடுத்தாய் எழுந்துதெட்ச ணாபுவியில் இரங்கி வரவேணும் மாளக்கலி தாழநரகம் வீழ வரம்பெற்றாய் வாய்த்தகுல சான்றோரவர்க் கேற்ற வரம்பெற்றாய் வரவேணுந் தெட்சணாபுரி மலைபோ லுயர்ந்துபோகு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(306,'தேவாரம்','தேசக்கலி தீதுவநி யாயம் பொறுக்கரிதே சிவனேவைந்த ராசேதெச்ச ணாபு வியில்போவோம் கேட்பாரில்லை யெனவேபேய்கள் கீழும் மேலுஞ்சாடுது கிருஷ்ணாமகா விஷ்ணுவேவுன் கிருபை யிரங்கவேணும் தாண்மைக்குலச் சான்றோருன்னைத் தவத்தால் தேடிவருந்துன்  தாயுமவர் தகப்பன்சுவாமி தானே யல்லாலுண்டோ சர்வபரா சுவாமிசிவ சுவாமி யுமக்கபயம் சாணார்படுந் துயரங்கண்டு சுவாமி யெழுந்தருள்வாய் ஆற்றிலலைக்கோரை யைப்போல் அலைவதுந் தெரியாதோ அணைப்பாருன்றன் துணையேயல்லால் அவர்க்கே ஏதுமுண்டோ  பலநாளவர் படும்பாட்டெல்லாம் பார்த்தே யிருந்தாயோ பயமோ அவரிடம் போகினுஞ் செயமோ சொல்லுமையா பாலரவ ருமக்கேயல்லா பாரா திருப்பாயோ ஆருமற்றார் போலேயவர் அலைவ துந்தெரியாதோ அரனேசிவ மருகாவுனக் கபயம் உனக்கபயம் அவரால்நாங்கள் படும்பாடெல்லாம் அறியல் லையோசுவாமி ஆதிமுறை யாகமத்தை அழித்துச் சொல்லுறோமோ அதுதான்பொய்யோ மெய்யோவென் றாகமந் தனைப்பாரும் அண்டரண்டந் தொழவேவரும் ஆதி யுமக்கபயம் அரகராசிவ அரனேசிவ அரசே யுமக்கபயம் ஆதிசிவ னாதிதவ மோதி யுமக்கபயம் ஐயுங்கிலி சவ்வுங்கிண அரனே யுமக்கபயம் அம்மாஆதி அய்யாசிவ மெய்யா உமக்கபயம் அரசேதெச்ச ணாபுவியில் வரவேணு முமக்கபயம் அந்தக்கலி திந்தெய்யென் றறவே வரவேணும் அத்திடக்கலி செத்திடக்கலி அறவே வரவேணும் அய்யக்கலி தீயாவியே அறவே வரவேணும் அவகடக்கலி சவகேடாக அழிக்க வரவேணும் ஐம்பத்தாறு சீமையொரு குடைக்குள் ஆள்வாயுமக்கபயம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(307,'தேவாரம்','அத்தாவுமக் கபயம்சிவ முத்தா வுமக்கபயம்  ஆதிநா ராயணர்க்குப் பால னானகுணதீரா ஆதியுமக் கபயம்சிவ சோதி யுமக்கபயம் அரனேசிவ னார்க்குமருக னான குணபாலா அலைவாய்க் கரைமடமாம்பதி ஆள்வா யுமக்கபயம் அய்யாவை குண்டாஅலை கண்டா யுமக்கபயம் தாணுமா லயனாய்ப்பேரு தழைக்க சிவமுளைக்க தானாயொரு குடைக்குள்தர்மத் தரணி யாளவந்தாய் சாணாக்குரு வேதக்குரு தானானா யுமக்கபயம் தாண்டும்பதி கூண்டப்பதி தானானா யுமக்கபயம் தவமேதெச்ச ணாபூமியில் தானே வருவாயே தெச்சண மீதே வந்து தெய்வமா யிருந்து கொண்டு மிச்சமாய் புதுமை காட்டி மேதினி யெவருங் காண உச்சமே சான்றோர்க் கீந்து உதவியுஞ் செய்வா யென்று பச்சமாய்த் தேவ ரெல்லாம் வைந்தரின் பதத்தில் வீழ்ந்தார் வீழ்ந்திடக் கண்டு வைந்தர் விடையுள்ள தேவர் தம்மைச்  சோர்ந்திட மொழிகள் சொல்லி தேவரே பதற வேண்டாம் ஓர்ந்திட எனக்கு முன்னே உள்ளது தானே யென்று கூர்ந்திட வுரைத்தீ ரெல்லாம் குணமுடன் மகிழ்ந்து சொல்வார் தெய்வ மாதர்கள் தாம்பெற்ற தேவரே சான்றோர்க் கெல்லாம் மெய்வரம் பதுபோல் ஞாயம் விளம்பிடப் பேறு பெற்றேன்  பொய்வரம் பசாசு எல்லாம் பொன்றுற வரங்கள் பெற்றேன் அய்வரைக் காத்துத் தர்ம அரசுக்கும் பேறு பெற்றேன் ஒருகுடை யதற்குள் ளாள ஒருவிஞ்சை யதிகம் பெற்றேன் கருவுடை யோர்க்குங் காண கனவரம் பெற்றே னானும் மறுகிநீர் தவிக்க வேண்டாம் வருவேனான் சான்றோ ரண்டை குறுகலி யதனைத் தாண்டிக் கொள்வேனென் குலத்தைத் தானே என்னையே கெணியா வண்ணம் ஏழையா யிருந்த சான்றோர்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(308,'தேவாரம்','தன்னையே பழித்தோ ரெல்லாம் சளமது துயரங் கொண்டு  அன்னீத நரகந் தன்னில் அகப்படத் தள்ளித் தள்ளிக்  கொன்றுநான் சான்றோர்க் கெல்லாம் கொடுப்பேன் மேற்பவிசு தானே எல்லாஞ் சான்றோர் கையாலே எள்ளும் நீரும் கலிக்கிறைத்து பொல்லாக் கலியை நரகமதில் புக்க அடித்துப் பேயோடு கொல்ல விடைகள் கொடுப்பேனான் கூண்ட சான்றோர் கையதிலே வல்லோர் புகழுந் தேவர்களே மனமே சடைக்க வேண்டாமே வேண்டா மெனவே தேவருக்கு விடைகள் கொடுத்து வைகுண்டரும் கூண்டாங் கடலின் கரைதாண்டி குதித்தே கரையி லோடிவந்து தாண்டாய் முன்னே பெற்றதொரு தாய்க்கோர் சடல வுருக்காட்டி ஆண்டா ராணை யொருவருக்கும் அகலா தெனவே யாணைகொண்டார் நானோ பிறந்த சமுத்திரத்தின் நடுமேற் கடலி லன்னாபார் ஏனோ பதியும் மேடைகளும் எண்ணிப் பாரு தேர்நிறமும்   மானோ பொன்னோ மண்டபமோ வறடே யுனக்குச் சாட்சியிது  யானோ காட்டுஞ் சொரூபமெல்லாம் யாதா யிருநீ மறவாதே பதமோ பதமோ பாற்கடலின் பாலை யகமே கொள்ளுவென இதமாய் இதமாய்ச் சொல்லிடவே இசையா வண்ணம் வீழ்ந்திடவே உதயம் உதயம் வெளித்திறந்த உருவை யொருவர்க் குரையாமல் இதையே யிதையே மறந்துஎன்னை இளப்பம் பேசிநெகிழா தென்றார் ஆண்டா யிரத்து எட்டுமுன்னே அன்னை யெனவே நீயிருந்தாய் கூண்டா மெட்டா மாசியிலே குணமாய் நாரா யணர்மகவாய் சான்றோர் கெதிகள் பெற்றிடவே தர்ம குண்டம் பிறந்துவொரு கூண்டாங் குடைக்கு ளரசாளக் கொண்டே போறேன் கண்டிருநீ வைகுண்டம் பிறந்து வார்த்தையொன் றோர்குடைக்குள் பொய்கொண்ட பேரைப் பெலிசெய் தரசாள கொண்டுநான் போறேன் குலக்கிழடே பாரெனவே அன்றுவை குண்டர் அவர்நடந்தார் தெச்சணத்தில்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(309,'தேவாரம்','கெங்கைக் குலத்தாயை கிருஷ்ணர் வரவழைத்து நங்கையே யென்னுடைய நாமமது கேட்டதுண்டால் பால்போல் பதம்போல் பாவந்தீ ரன்பருக்குச் சூல்புத்தி வம்பருக்குச் சூதுசெய் மங்கையரே  என்றுவை குண்டர் யாமம் கூறிக்கூறி இன்றுதெச்ச ணம்போக எழுந்தருளி யேவருக  சகல வுலகோரும் சந்தோசங் கொண்டாடப் புகல எளிதில்லாத பெரியநா ராயணர்க்கு வைகுண்டம் பிறந்து வார்த்தையொன் றோர்குடைக்குள் மெய்கொண்ட முப்பொருளோர் மேனியொன் றாய்த்திரண்டு எல்லா வரமும் இவர்க்கு மிகக்கொடுத்து நல்லோர்க்குக் காலம் நலமாகு மென்றுசொல்லி வானலோ கம்வாழும் வானவருந் தானவரும் தானே முன்னாகமங்கள் தப்பாதிடை முனிவர் வகுத்திருந்த ஆகமங்கள் நிறைவேறி வந்துதென்று மாமுனிவ ரெல்லோரும் மாசந்தோசங் கொண்டாடி நாமு மெதிரேபோய் நல்லவை குண்டர்பதம் கண்டுகொள்வோ மென்று கனமுனிவ ரெல்லோரும் கொண்டு மலர்ப்பூக்கள் கோமான்கால்மீ தேசொரிந்து கொற்றவரும் மாண்டு குறும்பு மிகப்பெருத்து உற்ற துலுக்கன் உடன்வந் துடனோடி மற்றொரு பத்தாண்டில் வருவோமென் றாகமத்தின் வர்த்தமா னம்போலே வந்தவை குண்டரென எல்லோரும் போற்றி எதிர்நின்று வாழ்த்திடவே நல்லோரா யெங்களையும் நாரா யணர்மகனே உன்கிருபை தந்து உதவிதரு வாய்மேலும்  முன்கிருபை யெல்லாம் உனக்குள்வச மாச்சுதல்லோ முப்பொருளும் நீதானாய் உறுவெடுத்தா யானதினால் எப்பொருளைக் கண்டு இயல்புபெறப் போறோங்காண் வைகுண்ட மாமுனியே மனதிரங்கிக் காக்கவேணும் மெய்குண்டர் பதத்தில் வீழ்ந்து நமஸ்கரித்தார் அப்போது வானோர்க்கு ஆதிவைகுண் டருரைப்பார் இப்போது வானோரே எனையறிந்த தெந்தவிதம் என்றுவை குண்டர் இயல்பறியச் சொல்லிடவே அன்று வானோர்கள் எல்லோரு மேதுசொல்வார் கயிலயங் கிரியிலுள்ள ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(310,'தேவாரம்','கணக்குமுத லுள்ளதெல்லாம் ஒயிலாகப் பிறப்பு உயர்கணக் கானதுவும் தத்துக் கணக்கும் சஞ்ச முதற்கணக்கும் பத்துக் கணக்கும் பலன்பெற்றோர் தங்கணக்கும் மோட்சக் கணக்கும் முன்னுள்ள யுகக்கணக்கும் வாச்சக் கணக்கும் வைகுண்ட நற்கணக்கும் நரகக் கணக்கும் நடுத்தீருவைக் கணக்கும் தருமக் கணக்கும் சகலக்கணக் குமுதலாய்ப் பொறுமைப் பிரமா பூசாந்திரக் கணக்கும் எல்லாக் கணக்கும் எடுத்துமிகத் தேவரெல்லாம் வல்லான கயிலைவிட்டு வருவதுகாண் டேயடியார்  தியங்கி மனங்கலங்கித் தேவரோ டேகேட்டோம் மயங்கிமிகக் கேட்டிடவே மாதேவ ரேதுரைப்பார் நாரா யணர்க்கு நல்லவைகுண் டம்பிறந்து சீரா யுலகமெல்லாம் சிறந்தே வொருகுடைக்குள் ஆள வருவதினால் அவர்கைக்குள் ளிக்கணக்கு நீள அரன்சிவனும் நெடிய மருகோனும் இந்நாள் முதலாய் ஏழ்ப்பிக்க வேணுமென்று சொன்னார்கா ணெங்களையும் சுருதிக் கணக்கையெல்லாம் பொன்னுலோ கம்புகுந்து பெட்டகத்தோ டேயெடுத்து வாரு மெனஅயச்சார் மகாபரனும் மாலோனும் சேரும் வைகுண்டம் சிணம்பிறந்தா ரென்றுசொல்லி ஆனதினா லிப்போ யாங்கள்கொடு போறோமென்றார் நானதுகள் கேட்டு நன்றா யறிந்தோமென்றார் அப்போது வைகுண்ட ராசர் மிகவுரைப்பார் இப்போது வானவரே எல்லோரு மென்கூட வாருங்கோ வென்று வைகுண்டர் சொல்லலுற்றார் சீருங் கோபால சிவனே செயலெனவே வானவர்கள் தேவர்முதல் மறைமுனிகள் சாஸ்திரிகள் தானவர்க ளுங்கூட சங்கீதம் பாடிவரச் சந்திர சூரியர்கள் தம்மானக் குடைபிடிக்கச்  சுந்தரஞ்சேர் வைகுண்டம் தோன்றிவந் தாரெனவே வேத மறையோரும் விசய முனிவர்களும் நாதாந்த வேதமதை நன்றாய்த் தெளிந்தெடுத்துக்  கோமான் வளரும் கோவேங் கிரிதனிலே நாமாது வாழும் நல்லதிரு மண்டபத்தில் தங்க நிறத்தூணில் சதுர்மறையோர் தாங்கூடி எங்கு மகிழ எழுதினா ரம்மானை ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(311,'திருவாசகம் 2','ஏகமொரு பரமானதும் இம்மென்றொரு வாயுவில் சத்து வளைந்ததும் சத்தில் சிவம் தோன்றியும் சிவத்தில் சத்தி தோன்றியும் சத்தியில் நாதம் தோன்றியும் நாதத்தில் விஷ்ணு தேன்றியும் ருத்திரர் மயேசுரர் உலகம் தோன்றியும் உலகில் அண்டபிண்டம் தோன்றியும் பிண்டத்தில் குறோணி அசுரன் தோன்றியும் குறோணி வானலோகம் விழுங்கவும் கோபத்தால் விஷ்ணு சென்று குறோணியைத் துண்டாறாய் வெட்டி உலகில் தள்ளவும், துண்டமென்றில் குண்டோமசாலி பிறக்கவும் சுகசோபன மண்டபமலைய விளித்ததும் திருமருகரது கேட்கவும் தேவாதிகளை நாங்கிலாக்கி வரையானதைத் தூண்டிலாக்கிக் கடலை ஓடையாக்கிக் காயாம்பு வண்ணர் ஓடமேறியே கறைக்கண்டர் ஓணிதள்ளவே கன்னியிலே தூண்டலுமிட்டு அக்குண்டோமசாலியையும் வதைத்து மறுயுகம் தோன்றியே தில்லைமலாலன் மல்லோசி வாகனென்ற இருபேருடைய அநியாயம் பொறுக்காமலே அவனிருவரையும் சங்காரம் செய்து அடுத்த யுகம் தோன்றியே, சூரன் சிங்கமுகா சூரன் செய்த அநியாயம் பொறுக்காமலே ஆதித்திருநெடுமால் ஆறுமுகக் கடவுளென நாமமுங் கூறியே அவனையும் சங்காரஞ் செய்து அவ்வுகத்திலே பிறந்த அசுரன் இரணியனையும் வதைத்து அந்த யுகமும் அழித்து அடுத்த யுகம் தோன்றியே, அரக்கன் இராவணசங்காரமுஞ் செய்து சீதாப்பிராட்டியினுடைய சிறையையு மீட்டித் தென்னிலங்காபுரி அரக்கரையும் கொன்று அக்கிளையொழிய அக்குலமும் அறுத்து அடுத்தயுகம் தோன்றியே பஞ்சவர்க்கு பகாரியாகியே பகைத்தத் துரியோதனனையும் வதைத்துப் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(312,'திருவாசகம் 2','பஞ்சவர்க்குக் குருநாடு பட்டமுஞ் சூட்டியே அரசாள வைத்து கலிவருவானெனக் காற்று வீசினதைக் காதிலே கேட்டுக் கானக வழிநடந்து பஞ்சவர்களுடைய பாரப்பெலன்களையும் வாங்கிப் பச்சை மாலை சுமந்திருந்த பயமாயக் கூட்டைப் பர்வதாமலை யுச்சியிலே கிடத்தி, கங்கையுங் கண்டு கங்கையிலே குளித்த கன்னிமார் பெண்களுடைய கற்பையுஞ் சோதித்துக் கயிலையங்கிரி சென்று ஏழுலோகம் புகுந்து ஏழு வித்துமெடுத்து இருதய கமலத்திலே இருத்தி எரியுங் கட்டையெனக் கிடந்து ஏந்திழைமாரைச் சூழ வளையும் படியாகக் கொந்தலையும் எழுப்பி ஏழுபெண்களுக்கும் ஏழு மதலையுங் கொடுத்துத் தவசுக்குக் கன்னிமாரையும் அனுப்பிப் பத்திர காளியிடத்தில் பாலரையுங் கொடுத்து ஸ்ரீரங்கம் போய் செகமறியும்படி பள்ளிகொண்டிருந்ததும்; தேவாதிகளுடைய வாக்கினாலே ஈசுரர் தாமே கலியனையும் பிறவிசெய்து இருநூற்று முப்பது நூறாயிரம் வருஷமாகக் கட்டங்கொடுமை செய்து அவனியரசாண்டு கன்னிமக்களாகிய சான்றோர் படுகிற மறுக்கம் பொறுக்காமலே, வியாகரிடை முனிவர் வகுத்த ஆகமத்தின் படியே மகாபர நாராயணர் தாமே வைகுண்டமாய்ப் பிறந்து ஒரு குடைக்குள் 1008 ஆம் ஆண்டு மாசியிலே அஞ்சி மூவஞ்சி தேதியிலே அவனிமனுவிலே ஆதிச்சாதியிலே மனுநிறமாக வந்திருந்து சாதி பதினெண்ணையும் ஒருதலத்தில் வருத்தியே தர்மமாய்த் தாரணி யாபேர்க்கும் சஞ்சல நோய்பிணி தண்ணீரால் தீர்க்கவும் சந்ததியில்லாத பேர்க்குச் சந்ததி கொடுக்கவும் தனமில்லாத பேர்க்குத் தனங்கொடுக்கவும் சாதைவாதை ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(313,'திருவாசகம் 2','பேய்களைச் சரிவிலே எரித்துக் கரியச் செய்யவும் தரணியேழு கணக்கையும் கேட்டு மிக சத்தியாய் அன்பு மனிதருக்காகவே தரணியில் நாலுதரம் சளங்கள் படவும் சாஸ்திரவேத நூலுக்குச் சரியொத்தி பேர்களுக்கு உபகாரங்கள் செய்யவும்; சண்டையாய்க் கலியனைத் தன்னாலே போக்கவும் தர்மமாகத் தரணியோர் குடைக்குள்ளாளவும் தங்கநவரத்தினத் திருமுடி சூடியே சகலமாபதி மேடைகள் பாவிக்கவும் தவசு கிருபை பெறவும், தங்கமணி சக்கராயுதக் கொடி ஒற்றைக்கொடி கட்டவும் சகலரும் புகழவரும் தர்மராசாவாகவும் சங்கவிருதுக் கொடி ஒத்தக் கொடி கட்டவும் அஷ்டதிக்குப் பதினாலு லோகமுமறிய அசையா மணியொன்று கட்டியே அரசு பாவிக்கவும், ஆதி சர்வேஸ்வரனாகிய அவனியைம்பத்தாறு சீமையும் அடக்கி அரசாளவும் ஆதிசர்வ காலமும் அழியாத் திருவுளமாயிருந்து அரசாள்வாரெனவும் ஆதிராமச் சந்திர சூரிய நாராயணர்க்கு ஆதியாகமத்தின் படியாகவே ஆதிவைகுண்டம் பிறந்தாரெனவே, அண்டர்களும் முனிவர்களும் தேவர்களும் அதிக சகல மாமுனிவர்களும் எல்லோருங் கூடியிருந்தாராய்ந்து தொண்டர்தமக் கென்றுவளர் கொன்றையணி கயிலையில் மண்டலம் புகழச் சிவனாருடைய கிருபையினாலே என்றவர் கயிலையங்கிரியில் எழுதினார்.');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(314,'வைகுண்டர் முருகனுக்கு அருளல்','அன்றந்தத் தேவர்முனி எல்லோருந் தாங்கூடி சென்றந்தக் கயிலை செகத்தூணி லேதரித்து வைகுண்டர் பாதமதை வாழ்த்திக் குவித்துவர மெய்கொண்ட நாதன் வேலவன்செந் தூரணுகி நடக்க அறுமுகனும் நடுங்கி மிகப்பதறி வடக்குமுக மாய்விழுந்து வைகுண்ட ரைப்போற்றி அப்போது வேலவனை ஆதிவைகுண் டர்பார்த்துச் செப்புகிறார் குண்ட சிவநாத கண்மணியும் நாடுகேட் கப்போறேன் நாரணன் நான்தானும் கேடு வருமுனக்குக் கேள்விகே ளாதிருந்தால் இத்தனை நாளும் என்னைக் கெணியாமல் புத்தியறி யாதவர்போல் புலம்பினீ ரித்தனைநாள் இனிவைகுண் டம்பிறந்து ஏகமொரு குடைக்குள் மனுவொரு சொல்லாள மகாதர்ம மேநினைத்து மாய்கை யறுத்து மாற்றான் கருவறுத்துத் தோயக் குழிமூடி தொல்புவியைத் தானாள நல்லோர்க ளெடுக்க நான்போறேன் கண்டாயே கல்லார் தமக்குக் கசப்பினிமேல் கண்டாயே தர்மவை குண்டம் தான்பிறந்தேன் இப்போது தர்மச் சிறப்புத் தானினிமேல் கண்டாயே  நன்மை யினிக்காணும் நாரணன்கண் ணல்லாது தின்மையென்ற சொல்லு ஆகாது கண்டாயே ஆனதா லுன்றனக்கு யானினிமேல் சொல்லும்புத்தி வானஞ்சூழ் வையகங்கள் வாழுகின்ற மண்டபங்கள் எத்தனையோ அத்தனையில் இருக்கின்ற தேவரெல்லாம் அத்தனை பேரும் அறியும் படிசொல்லுவேன் காணிக்கை வேண்டாதுங்கோ கைக்கூலி ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(315,'வைகுண்டர் முருகனுக்கு அருளல்','கேளாதுங்கோ மாணிக்க வைகுண்டம் வல்லாத்தான் கண்டிருங்கோ  பூசையே ராதிருங்கோ பெலிதீப மேராதுங்கோ ஆசைவை யாதிருங்கோ அவகடஞ் செய்யாதுங்கோ ஞாயமுறை தப்பி நன்றி மறவாதுங்கோ மாய நினைவு மனதில் நினையாதுங்கோ வைகுண்டா வென்று மனதில்  நினைத்திருங்கோ பொய்கொண்ட தேரோட்டம் புனக்கார மேராதுங்கோ தாதி கைகாட்டல் சப்பிரங்க ளேறாதுங்கோ மோதிப்பே சாதிருங்கோ மோகம்பாராட் டாதுங்கோ ஆலத்தி கைவிளக்கு ஆராட்டுப் பாராதுங்கோ சாலத்தீ பாராதுங்கோ சகலபூ ஏராதுங்கோ கொழுந்து மஞ்சணைமாலை குப்பையொடு சந்தனமும் விழுந்து நமஸ்காரமுதல் வேண்டாமென்று சொல்லிடுங்கோ  கூவென் றுரையாதுங்கோ கொக்கரித்துப் பேசாதுங்கோ ஓவென்றுரை யாதுங்கோ ஓமமுறை யேராதுங்கோ தீபரணை காணாதுங்கோ திருநாளைப் பாராதுங்கோ ஆபரணம் பூணாதுங்கோ அன்னீதஞ் செய்யாதுங்கோ எல்லாம் வெறுத்திருங்கோ இத்தனைபோ லுள்ளதெல்லாம் அல்லாமல் மீறி யாரொருவர் செய்ததுண்டால் வல்லாத்த கோபம் வரும்வை குண்டருக்கே நல்லோரே யாகவென்றால் நியாயமதி மேநில்லும் என்று வைகுண்டம் இயம்பக்கந் தனுரைப்பான் மன்று தனையளந்த வைகுண்ட நாரணரே நீருரைத்த சட்டமதில் நிலைதவறோம் நாங்கடியார் காரும்நீ ரென்று கந்தன்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(316,'வைகுண்டர் முருகனுக்கு அருளல்','அடிதொழுதான் அப்போது வைகுண்ட ராசர்மிக வுரைப்பார் இப்போது சொன்னதெல்லாம் எனக்குநிச மாகவென்றால் உன்கோ புரத்தில் உயர்ந்தவட மேல்மூலையில்  பின்கோ புரங்காணப் பிளந்துபோ டென்றுரைத்தார் அல்லாம லுன்னையென்று அவனியுண் டாக்கிவைத்த செல்லாச் சிலையைத் திருப்பிவிடு தெற்குமுகம் உலகோ ரறிய ஒருவாயி லுமடைத்துக் கலகமாய்க் கண்மூடிக் கவிழ்ந்திருப்பா யோவென்றார்  அல்லா தெனைமறந்து அழிச்சாட்ட மாய்நடந்தால் பொல்லா தவனே பெருவிலங்கு சிக்குமென்றார் அப்போது கந்தன் ஆவி மிகக்கலங்கி இப்போது சொன்னதெல்லாம் யானினி செய்வனென்று ஆதி யுரைத்ததுபோல் அடியார்நடப்போ மென்றார் சோதி வைகுண்டம் சொல்வா ரவரோடு நிலையழி யாதிருங்கோ நீதியாய் நின்றிடுங்கோ உலகறிய நானும் ஒருநெல் லுடைக்குமுன்னே பலசோ தனையும் பார்த்துநடுத் தீர்ப்புசெய்வேன் விடியும் பொழுது வேசம் பலதணிவேன் பிடியு மனுவுடனே பெரியயுக மாளவைப்பேன் வருவோ மொருநெல் மாறி யெடுக்குமுன்னே கருதி யிருங்கோ கருத்தயர்ந்து போகாதுங்கோ என்று வைகுண்டம் இத்தனையுங் கந்தனுக்கும் மன்றுக்கு மென்றும் மறவாதுங் கோவெனவே அதைவிட் டவர்நடக்க ஆகாயங் கொண்டனரே இதைவிட் டவர்நடந்து ஏகாய மாகிவர');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(317,'வானோர்கள் வைகுண்டரைப் போற்றுதல்','அண்டரொடு தெண்டனிட் டெண்டிசைகள் நின்றுவரும் ஆதவனைச் சூழ் கணம்போல் அரிஹரி அரஹரா சிவசிவா என்றுசிலர்  ஆடியே பாடி வரவே தொண்டரவர் கண்டுவை குண்டரடி கண்டுதொழ சூழவளைந் தேழியல் படர்தே  சூரர்பதி நாராயணர் வீரர்பத மோதிவரச் சூராதி சூர ரெனவே செண்டையொடு தண்டைமணி டண்டடம டண்டமெனத் தேவர்சே வித்து வரவே சிவநமசி வாயமெனும் ஓம்நம சிவாயமென்னும் சேவித்திரு புறமும் வரவே அண்டமுர செண்டுமணி டுண்டும டுண்டுமென ஆகாய மீதில் வரவே அனவரத கோலாக லாதிநாரா யணாவென அமரரிசை கூறி வரவே மத்தள முடுக்குபல வாத்திய மடடென வானமதில் நின்ற திரவே மலர்மாரி சலமாரி தினமாரி தூவியே வானவர்க ளிசை கூறவே தத்தளங் கிடகிட தொம்மெனத் தொம்மெனச் சங்கீதக் காரர் வரவே சகலகலைக் கியானவேத சாதிமுறை யோதிமிகு சாஸ்திரக் காரர் வரவே தித்தங்கிண தித்தங்கிண தித்தங்கிண தித்தியெனச்சில தேவர் கூறி வரவே சிவசிவ சிவசிவ சிவவென்று சிலதேவர் சேவித் தியல்கூறி வரவே நித்தங்கிண சித்தங்கிண உத்தங்கிண தித்தியென நேரியர் சீரியல் கூறவே நீலங்கிரி வாலங்குரு நீயென்குரு தாயென்குரு நீதென் குருவெனப் போற்றினார் பண்டைமுறை யின்றுவெனக் குண்டமெனக் கண்டுவரும் பலவாங் கிரி குண்டமே பசுவாமனே சிசுபாலனே பலமானனே தலமானனே பசுவா கிய நிசமே தொண்டர்தனக் கென்றுவரு குண்டவை குண்டமனாய்த் தெச்ச ணாபதி பூபனே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(318,'வானோர்கள் வைகுண்டரைப் போற்றுதல்','துளபமணி களபமேனி யழகொழுகு கிருபைமிகு துவாரகா பதிக் கரசே சண்டன்வலி துண்டப்பட கொண்டக்கணை விண்டத்தொடு துச்சா வில்லு வீரா சாமியுன் றாளோ எங்கள் தலையின்மீது தலையின்வழி தானே நடவாயே துண்டப்பட வண்டச்சரம் கண்டப்படி பொண்டத்தொடு துச்சா வில்லு தீரா    சுத்தா வுனக்கேற்றார்தமை வித்தானதில் வித்தாய் துவாரகாபதிக் கரசேயெனத் தொழுதார் தேவர் தொழுது திக்கெங்கும் போற்றிவரத் தாவமுள்ள வானோர் சங்கீதம் பாடிவர வேத மறையோர்கள் வேதக்கலை யோதிவர நாதமொடு சங்கு நகரா முழங்கிவர மத்தள மேலோர் மடமடென் றேற்றிவரத் தித்தி தித்தியெனத் தேவரெல்லாம் பாடிவரச்  சாத்திர வேத சாதிமுறை யோதிவர நாற்றிசையும் போற்றி நாற்சாதி யும்வரவே அரம்பையர்க ளாடி ஆலத்தி யேந்திவர வரம்பகலா மாமுனிவர் வரிசைமிகக் கூறிவர நாதாந்த வேதம் நவில்வோர் நெருங்கிவர மாதவர்க ளெல்லாம் மலர்மாரி தூவிவர கண்டங் ககமகிழ்ந்து கண்ணான நாரணரும் கொண்டாடித் தேவரொடு கூறுவா ரன்போரே மனுக்கண் காண வரவேண்டீ ரென்பிறகே தனுக்கான ஏகமதில் சகலத்தோ ருமறிய வாருங்கோ என்னுடனே வானவரே தேவர்களே வானமதில் நின்று வாத்தியங்க ளேற்றிவர தானவர்கள் முதலாய்ச் சங்க முதல்வரவே  ஆகாச மீதில்நின்று எல்லோரும் போற்றிவர வாகான சூரியனும் வந்து குடைநிழற்ற');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(319,'செந்தூர் விட்டு வைகுண்டர் தெச்சணம் எழுந்தருளல்','நன்றான வைகுண்டர் நல்லசெந்தூர் தானும்விட்டு வண்டாடுஞ் சோலை வாய்த்த வனங்கள்விட்டு சோலைத் தெருக்கள்விட்டுச் செந்தூர் தலங்கள்விட்டு ஆலைத் தெருக்கள்விட்டு அந்தூர் பதிகள்விட்டு மண்டப மேடைவிட்டு மடங்கள் மிகக்கடந்து தண்டமிழ்சேர் செந்தூர் தலத்தைவிட்டுப் போவதற்கு நிற்கின்ற போது நிலையுள்ள மாமுனிவர் நக்கன் மருகனுட நல்லடி யில்வீழ்ந்து செந்தூர் தலத்தைவிட்டுத் தெச்சணா பூமியிலே இந்த வேளைதனிலே எழுந்தருள வேண்டியதேன் இங்கே பகைத்ததென்ன என்னுடைய நாயகமே சங்க மகிழ்வேந்தே தானுரைக்க வேணுமென்றார் அப்போது நாரா யணர்மகிழ்ந் தேதுரைப்பார் இப்போது கேட்டதற்கு இயல்புரைக்கக் கேளுமினி கேள்விகே ளாநீசன் கெடுவ தறியாமல் நாள்வழியாய்ச்  சான்றோரை நியாயமில் லாதடித்தான் சொன்னேன்புத்தி நீசனுக்குத் திருவனந்த மேயிருந்து  என்னையும் பாராமல் இளப்பமிட்டான் சான்றோரை  ஆனதால் நீசனுக்கு யானதிகக் கோபமிட்டு நானவ் வூரும்விட்டு நாடிவந்தேன் செந்தூரு இங்கே யிருந்தேன் யான்சிறிது காலமெல்லாம் வெங்கப் பயல்சிலர்கள் வேசையுட னாசையினால் என்னைக் கெணியாமல் என்கோவி லுள்ளேதான் சன்னைசொல்லிப் பெண்களுடன் சரசமிட் டெச்சியிட்டான் இருபேரு மொத்து இருந்தாலும் பழுதல்லவோ ஒருவன் பெண்ணானை ஒருநம்பூ ரிபிடித்து ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(320,'செந்தூர் விட்டு வைகுண்டர் தெச்சணம் எழுந்தருளல்','எனக்கேவல் பண்ணி ஏந்திழையாள் போகுகையில்  மனக்குழலி தன்னுடைய மார்பின் கலைபிடித்து இழுத்து வலித்து இழிக்கேடு செய்யவென்று பழுத்துச் சழிந்த பருநம் பூரியவன்மேல்தலையி லிட்ட முத்திரி கழற்றாமல் மால் மயக்கத்தாலே மனங்கலங்கி நம்பூரி மங்கை மனங்கலங்க வாரிப் பிடித்திழுத்துக்  கொங்கைதனைப் பங்கமதாய்க் கூறழிய வேகிழித்து வேதனைகள் செய்ய மெல்லியவள் கோபமுற்று மோதி யென்பேரில் ஒருசாபங் கூறினளே நாரணா கந்தா நானுனக்கு ஏவல்பண்ணிக் காரணங்க ளாச்சு கடைசிநாள் தானாச்சு  உனக்குமிந் தப்பதிதான் உறவுகே டாச்சுதையா எனக்கு மொருபிறவி இன்றுவந்து வாச்சுதென்று செந்தூர் தலங்கள் சிலநாள் செல்லுமுன்னே மண்தூர்ந்து போகுமென்று மாதுமிகச் சாபமிட்டாள் சாபமிட்டு மங்கை தக்கென்று கீழ்விழுந்து சீவ னதுவிடவே சிவனைக்கண் ணோக்கினளே நம்பூரி பங்கத் தாலேயந்த நாயகியும் உம்பர்கோ னூரில் உயிர்விட்டாள் மாமுனியே தவறாத மங்கை தானுரைத்தச் சாபமதால் இதறா யெனக்கங்கு இருக்கமனங் கூடாமல் எங்கேயினிப் போவோமென்று இதைவிட் டெழுந்திருந்து மங்கைசொன்ன நாள்முதலாய்த் தெற்குவாரியிலே போயிருந்தேன் அல்லா மலிங்கே அழிமதிகள் ரெம்பரெம்ப வல்லாண்மை யாக வலுஞாயங் காணுதுகாண் முன்னடப்பு மங்கையர்கள் முங்கிக் குளித்துமிக என்னடையில் வந்து ஏந்திழையா ராடுவது ஆட வரும்போது அசுத்தத்தோ டுவாறாள்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(321,'செந்தூர் விட்டு வைகுண்டர் தெச்சணம் எழுந்தருளல்','பாடவரும்போது பண்ணுறா ளசுத்தமது கோவிலிலே பூசைசெய்யும் குறும்பர் மிகத்துணிந்து தேவியருக் கீயத் திருடுகிறா ரென்முதலை  கணக்கன்முதல் நம்பூரி கள்ளப்பெண் ணார்களுக்கு இணக்க மதாயிருந்து என்முதலைக் கொள்ளைகொண்டு எடுக்கிறார் பெண்கள் எச்சியாட்டு மாடிஒடுக்கிறார் பெண்கள் ஒண்ணுக்கொண் ணொத்திருந்து பம்பை பரத்தை பகட்டுக்கை காட்டலெல்லாம் எம்பரனுக் கேற்ற இயல்பல்ல மாமுனியே ஆனதா லிவ்வகைகள் யான்வேண் டாமெனவே மான மழியுமுன்னே மாமுனியே தெச்சணத்தில் பள்ளிகொண்டு நானிருந்து பார்த்துச்சில நாள்கழித்துக்  கள்ளியாட்டுக் காவடி கைக்கூலி தான்முதலாய் நிறுத்தல்செய்ய வேண்டிய தெல்லா மிகநிறுத்திப் பொறுத்தரசு தர்மப் புவியாளப் போறேனினி மாமுனிக்குச் சொல்லி வழிகொண்டார் தெச்சணமே தாமுனிந் தையா தவறாத மாமுனியும் தெச்சணத்துக் கேகவென்று திருச்சம் பதியிருந்து உச்சமது கொண்டு உகச்சாப முங்கூறி நடந்து வரவே நல்லசெந்தூர் தானும்விட்டுக் கடந்து தருவைக் கரைவழியே தேவரெல்லாம் சங்கீதங் கூறித் தாமே யவர்வரவே மங்கள நாதன் மனுச்சொரூப மேயெடுத்து  நருட்கள் மிகக்காண நாற்றிசைக்கு மேவிவந்தார் மருட்கள் மிகவந்து வாசமிட்டுக் கூடிவர');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(322,'பிசாசுகளின் பணிவிடையை மறுத்தல்','கீழநடை விடாமல் கிருபையுள்ள வைகுண்டரைத் தாழநடை விடாமல் சற்பூத மேந்திவர அப்போது பூதமதை அல்லகா ணென்றுசொல்லி மைப்போடுங் கண்ணர் மாபூத மதைவிலக்கி நீங்களெனைச் சுமந்தால் நீணிலத்துச் சோதனையில் தாங்கள் மீறிநடந்தால் தகுமோநான் கேட்பதற்கு வேண்டாங்காண் நாரணர்க்கு வேதாளா வுன்வேலை பாண்டவர்க ளானப் பஞ்சவர்க ளுண்டெனக்கு என்று வைகுண்டர் இசையத் திசைநடந்தார் தொண்டு செய்யாமல் சூல்பூத மாடிவரப் பூக்கொண்டு வந்து போட்டுத் தொழுவாரும் தீக்கொண்டு வந்து தீபரணை காட்டுவாரும் மலர்வீசிக் காற்று மரைவீசி நிற்பதுவும் சிலம்பொன் துளிபோல் சிறப்புடனே தூவிவர மதியுங் குடைபிடிக்க வாயு மரைவீசத் துதியும் வழிதீய்க்கத் தேவர்மிக வோலமிட இத்தனை நற்சிறப்பும் இயல்பாய் முழங்கிவரப் புத்தியள்ள நாரணரும் புறப்பட்டார் தெச்சணமே  நதியிற் பிறந்த நாரா யணமூர்த்தி பதியி னலங்காரம் பார்த்து வழிநடந்தார் கெங்கை தனிற்பிறந்த கிருஷ்ண மகாநாதன் கங்கை வழிநோக்கிக் கண்ணோன் வழிநடந்தார் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(323,'வெண்மை உயிரினங்கள் வைகுண்டரைப் பணிதல்','நடந்து வருகும் நல்லவை குண்டர்முன்னே கடந்து முன்போன காண்டா மிருகமொடு சிங்கமி யாளி செங்கருட னங்கனுமன் பங்கமிலா றாஞ்சி பால்நிறப் பட்சிகளும் ஐந்தலை நாகம் அஞ்சுபஞ் சாச்சாரை செந்தலைக் கருடன் செய்யகாண் டாமிருகம் கலியன்கண் காணாதே காடோடி வாழ்ந்திருந்த சலிவில்லா மாமிருகம் சந்தவர்ணப் பட்சிமுதல் தரும வைகுண்டர் தான்வந்தா ரென்றுசொல்லிப்  பொறுமைக் குலங்களெல்லாம் புறப்பட் டகமகிழ்ந்து வந்து வைகுண்டர் மலரடியைப் பூண்டுகொண்டு சிந்தர் குலமன்னர் தெய்வப் பெருமாளே வைகுண்ட ரெப்போ வருவீர் வருவீரென்று கைகண்ட நிதியும் காணாதார் போலிருந்தோம் கப்பல்கரை கண்டாற்போல் கண்டோமே யும்மையும்நாம் செப்பத் தொலையாத திருவடியைப் போற்றிசெய்ய  வாறோங்கா ணும்முடனே வைகுண்டப் பெம்மானே தாறோங்கா ணெம்முதுகு சாமியுன் பாதமதுள் என்றிவைக ளெல்லாம் இரங்கித் தொழுதிடவே நன்றென் றுஅந்த நாரா யணருரைப்பார் ஒருவிளி பொறுங்கோ உண்மையிது தப்பாது இருவிளிக் குள்ளே என்னிடத்தில் வந்திடுங்கோ இருங்கோ முன்னயச்ச இடத்திலே போயிருங்கோ கருதி யிருங்கோ கருத்தயர்ந்து போகாதுங்கோ அலைய விடாதிருங்கோ அஞ்சுபஞ் சமதையும் குலைய விடாதிருங்கோ குருநினைவை யுள்ளேற்றம் என்று அவைகளுக்கு எம்பெருமான் சட்டமிட்டு அன்று அவைகளையும் அனுப்பி மிக நடந்தார் நடந்துபல திக்கும்விட்டு நல்லவன வாசம்விட்டுக் கடந்துசில ஊரும்விட்டுக் கடல்வழியே தானடந்தார் இரசகனிக ளேற்று இரவும் பகல்கடந்தார் விசைகொண்ட ராசர் விசயா பதிகடந்தார் வாரிக் கரைவழியே வரவேணு மென்றுசொல்லி சூரிய புஸ்பத் துல்வக் குறிபார்த்து வீரிய நாதன் விரைவாய் வழிநடந்தார் கடலுட் பதிகள் கண்டுகண் டேநடந்தார்  மடமடென அட்டவணை வாரிதீர்த் தாமாடி கடற்பெம்மான் வாறார் காணுவோ மென்றுசொல்லித் திடமுடனே வாரி சென்றுகண்டு தான்தொழவே ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(324,'கடல் விளைவெல்லாம் வைகுண்டர் பாதம்பணிதல்','முத்துக்கள் சங்கு முன்வந்து தெண்டனிட்டு வத்துப் பெரிய வைகுண்ட மன்னவரே இத்தனை நாளும் இயல்கலிய னேதுவினால் சற்றும் வெளிகாணாதே சமுத்திரத்தி னுள்ளிருந்தோம் தர்மப் பெருமானே சாமிநீர் வந்ததினால் நன்மை யுடனாங்கள் நாட்டிலே வாறோங்காண் ஒருசொல் வரைக்கும் உவரியிலே வாழ்ந்திருங்கோ இருசொல் லாகுமட்டும் இங்கிருங்கோ என்றுரைத்து நடந்து வைகுண்டர் நாடி மிகவரவே கிடந்த நிதியும் கீழ்க்கிடந்த காசுகளும் ஒக்க உயரவந்து உளமகிழ்ந்து தானிருக்கும் திக்கென்று நாதன் சேடன் தனையழைத்து கயிலாசந் தன்னைக் கட்டாய் வரவழைத்து ஒயிலாக இத்தனையும் உள்ளேநீ கொண்டுசென்று காவல்செய்து கொள்ளு கயிலாச மென்றுரைத்தார் தேவன் சட்டமிட்டுத் தெச்சணம் போகவென்று நடந்தார் துரிதமுடன் நாரணனார் தெச்சணத்தில்  நல்லநா ராயணரும் நாடி வழிநடக்க வல்ல பெலமான மாமுனிவ ருங்கூடி எல்லோருங் கூடி இயல்வா ரிக்கரையே நல்லோர்க ளாக நடந்தார்கா ணம்மானை வாதையே யானதெல்லாம் வைகுண்டர் பாதமதை சீதமுடன் போற்றித் தீபரணைக் காட்டிவரக் காட்டுகின்ற தீபமெல்லாம் காணாத வர்போலே நாட்டுக் குடைய நாரா யணர்நடந்தார் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(325,'நல்ல குலதெய்வங்கள் மறைதல்','நல்ல குலதெய்வம் நாட்ட மதையறிந்து எல்லைக் குடையை ஈசர்வந் தாரெனவே இன்றுமுதல் வம்பருக்கு இயல்பகைதான் சூழ்ந்துதென்றும் நன்று மனத்தோர்க்கு நல்லநாள் வந்துதென்றும் முற்கலியன் சட்டம் முதன்மையின் றுமுதலாய்த் துற்கலிபோ லாகிச் சுற்றுக் குலைந்துதென்றும் கொண்டாடி நல்ல கூளி கணங்கள்சொல்லிக் கண்டா ருடனேயும் கைதொழுத மனுவோடும் இன்றுமுத லெங்களுக்கு எற்கும்வகை யில்லையேகாண் கண்டுகொள்ளுங் கோவெனவே கண்காட்டிப் போய்மறைந்தார் நானில மரசு ஆள நாரணர் பெற்று வாறார் தானித மான அன்பாய்ச் சகலருங் கேட்டுக் கொள்ளும் மாநிலத் தோரே யென்னை வருந்தியே தேவ வேண்டாம் நானினிச் செய்ய ஆகா நவின்றவர் தெய்வம் போனார் தேயவங்க ளுலகி லெல்லாம் தெரிசனங் காட்டிக் காட்டி மெய்வரம் புள்ளோ ரெல்லாம் மேதினி விட்டுப் போந்தார் பொய்வரம் பசாசு எல்லாம் பொன்றிய வகையா ராமல் மெய்மறந் துரைகள் சொல்லி மேதினி யொழித்தா ரென்றே');");

        //12th Day

        db.execSQL("INSERT INTO akilathirattu2 VALUES(326,'வைகுண்டர் பகவதிக்கு அருளல்','கண்ணான நாதன் கமலக் குருநாதன் வண்ணமுள்ள நாதன் வழிநடந்தா ரம்மானை நடந்து பகவதியாள் நல்லகட லும்பார்த்துக் கடந்து பகவதியைக் கண்காட்டித் தானழைத்து நன்மை யுடைய நாயகியே சுந்தரியே பொன்மோ கினியே பிள்ளாய்நான் சொல்வதுகேள் வைகுண்டம் பிறந்து வார்த்தையொன்றுக் குள்ளான உய்கொண்டோர் குலத்தை உருவேற்ற வந்தேனென எந்தன் திருச்சம் பதியு மிதுமுதலாய் எந்தெந்த நாட்கும் இனிக்காணிக் கைநிறுத்தல் ஆனதினால் நீயும் ஆதியி லென்னோடு தேனினியத் தங்கையராய்த் திட்டித்த ஏதுவினால் உன்னோடு இத்தனையும் உபதேச மாயுரைத்தேன் பொன்னாடு தெச்சணத்தில் போறேன் தவசிருக்க  என்று பகவதிக்கு இயம்பி வழிநடந்தார் அன்று பகவதியாள் அறைக்குள் ளடைத்திருந்தாள் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(327,'வைகுண்டர் மணவைப்பதி ஏகல்','நாரா யணரும் நல்லசங்கத் தாருடனே சீராய் மணவைப் பதிநோக்கித் தான்நடந்தார் தென்காசி யென்ற தெச்சணா பூமியிலே  கண்காட்சி சூழக் கண்ணர் மிகநடந்தார் நடந்தெம் பெருமாள் நல்ல கடல்வழியே திடந்தெளிந்த தேவர்களும் செயசெ யெனநடக்க கண்டார் துவரம் பதியின்கண் ணோட்டமெல்லாம் பண்டைப் பதியின் பவிசொல்லா மேபார்த்து ஆன பதியின் அகலநீ ளம்பார்த்து மான பதியின் வாசலெல்லாம் பார்த்தவரும் நாடுகுற்றங் கேட்க நல்லதவஞ் செய்வதற்குத் தேடும் வடவாசல் சீவிவளர் மலையின் நேரும்வா சல்தனக்கு நிகரில்லை யாமெனவே தவசுக் குகந்த தலங்களிது நன்றெனவே உபசீ வனம்வளரும் உகந்தபுவி யீதெனவே என்று மனதிலெண்ணி என்றன்பெரு மானும் அன்றுவே தமுனியை அழைத்துமொழி கேட்கவென்று வாநீ முனியே வல்லகலைக் கியானமொழி  தானீ கரமாய்ச் சத்தியாய்க் கற்றவனே மான முனியே மறைநாலுங் கற்றவனே ஓநமோ வேதம் ஓயாம லோதுவோனே பின்முன் நின்று இயம்புரைகேட் டேயுரைநீ நான்தவ சிருக்க நாடுரைநீ மாமுனியே மான்தவ சுக்குகந்த மாமுனியே என்றுரைத்தார் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(328,'கலைமுனி தவ இடத்தின் சிறப்புக் கூறல்','வெள்ளா சனத்தில் விரைவா சியைநிறுத்திக்  கள்ளமா னதையகலக் காடகற்றி-விள்ளரிய வெள்ளமாங் கருணைபெறு வேதமுக மாமுனியே உள்ளதெனக் கின்னதென் றுரை பூரணத்தி னாடி புகழ்ந்துமுனி கொண்டாடி வாரணத்தின் கோடுவரை தேர்ந்து-காரணத்தின் கட்டுரைத்து நாடுவளம் விட்டுரைப்பே னென்றுமுனி தொட்டுப் பதங்குவித்துச் சொல்லுவான் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(329,'கலைமுனி வேதவியாசர் பிறப்புரைத்தல்','அய்யாவே வேத ஆதிநா ராயணரே மெய்யா யுருவாய் விளங்குவோ னேகேளும்  பரராச மாமுனிவன் பாலனென முனிவன் விரைவாக வேபிறந்து வெள்ளிமலை நாதனிடம் தாதா வுடனே தான்வரக்கண் டீசுரரும்  வாராய் முனியே மதலையுனக் கிங்கேது அப்போ முனியும் அரனடியைத் தான்பூண்டு இப்போ திவ்வாண்டு இம்மாத மிந்நாளில் பஞ்சகரு ணாதி பன்னிரண் டொன்பதுவும் வஞ்சக மில்லாமல் வந்தா ரொருவீட்டில் பூரண நாளும் பிரிந்துறையும் நேரமதும் நாரணம் பிறந்த நல்ல நட்சேத்திரமும்  யோக பலன்கள் ஒத்திருக்கும் நேரமதும் ஆகமக் கூட்டம் அடங்கிருக்கும் நேரமதும் மதிசுழி போலாகி வந்துரத மேறுகையில் துதிமுக வன்சர சோதி பிறந்ததன்றும் இவ்வாறு கூட்டம் எல்லா மெழுந்தொருநாள் அவ்வாறு தான்கண்ட அந்நா ழிகைதனிலே துற்கந்த முலாவும் தோகையொரு பெண்ணிடமே நற்கந் தமுலாவி நான்சேர்ந்தே னப்பொழுது சேர்ந்த பொழுது திரண்டுநா தம்வளர்ந்து காந்தற் றழுப்பாய்க் கன்னி யுடலாகிப் பெற்றா ளிவனைப் பேரு வியாகரெனக் கர்த்தா அறியக் கண்டே னிவன்தனையும் ஆசு மதுரம் சித்திரம் விஸ்தாரமுதல்  வாசு நெறிதேசு வழியறிந்த மன்னவன்காண் ஏகச் சுழிமுனையும் இகமுகி வாம்வரையும் ஆக முடம்பறிவும் அண்டபிண் டத்தறிவும் முன்பின் னாராய்ந்து மூதுண ராகமங்கள் தன்னன் பிறப்பும் சாற்றத் திறவான்காண் கல்லாத கல்வி கலைக்கியா னக்காண்டம் எல்லா மறிந்த இயல்முனிவ னென்றுரைக்க நல்லதுதா னென்று நன்முனிவனை ஈசர் வல்லமையா மென்று மாமுனியைக் கொண்டாடி என்பிறப்பு என்மாது இதுமுன் பிறப்புமுதல் பின்பிறப்பு முதலாய்ப் பேசென்றா ரீசரரும் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(330,'வேதவியாசர் முக்காலம் உரைத்தல்','அப்போது வேத வியாச ரகமகிழ்ந்து செப்புகிறோ மென்று சிவனை மிகத்தொழுது மூல முதலை முத்தி முகந்துகொண்டு காலமே நின்ற கடவுளே தஞ்சமென்று  ஆதிமுத லந்தம் அடங்கல் மிகவுணர்ந்து சோதி பதம்போற்றிச் சொல்லுவான் மாமுனியும் மும்மூர்த்தி யான முதல்மூர்த்தி தோன்றியதும் அம்மூர்த்தி தன்னில் ஆயிளையாள் தோன்றியதும் சத்தியிலே நாதம் தான்தோன்றி வந்ததுவும்  வித்தியாய் நாதமதில் விந்துமிகத் தோன்றியதும் விந்திலே விட்டிணுவும் விரைவாகத் தோன்றியதும் அந்த முறைமுதலாய் அண்டபிண் டம்வரையும் சொல்லி யுகமதுதான் தோன்றும்வகை கூறலுற்றான் சல்லிக் கொடிய சண்டக் குறோணிமுதல் நீசன் பிறப்புவரை நிகழ்த்தினான் மாமுனியும் பாசனீ சன்வரைக்கும் பாருலக வாறதையும் சொல்லி விரித்து சுத்தஏ ழாம்யுகந்தான் நல்ல யுகமதுதான் நாடுவதுஞ் சொல்லிமிக வல்ல யுகத்தில் வளரும் மனுவளமும் எல்லாமே சொல்லி எற்றவை குண்டமுதல் அந்நாட்டைச் சொல் ஒன்றுள் ளரசாளப் பொன்னான நாரா யணர்ம கேந்திரனும் நல்ல வைகுண்டம் நற்பிறவி யாய்வளர்ந்து வல்ல கலியுகத்தில் வாய்த்ததெச்ச ணாபுவியில்  புதுமை மிகச்செய்து பொல்லாத வம்பர்கையால் அடிகல் லெறிகள் அவர்பட் டம்புவியில் குடிதாழ்ந்த பேர்க்காய்க் கோப மதையடக்கி நல்லோர்க்காய்ப் பாடுபட்டு நல்லஉப தேசமதாய்ப் பஞ்சகரு ணாதிகளைப் பம்மலாய்த் தானடத்திக்  கொஞ்சநா ளுங்கழித்து குதித்துதித் தேவளர்ந்து ஆடவராய்ச் சமைந்து ஆண்பெண்போ லேயிருந்துதேவ அறிவார்க்குத் திரவியம்போ லேயிருந்து பச்சைக் குழந்தை பருவமுன் னாகியவர் மெச்சக் கொடியாட்கு மேவும்பரு வம்போலும் சுத்தக் கிழவரைப்போல் சூரப்பி ராயமுமாய் மெத்தப் புலம்பி விழலும்பிரா யம்போலும்  நாலு பிராயம் நாளதுக்குள் ளேயெடுத்துக் கோலு ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(331,'வேதவியாசர் முக்காலம் உரைத்தல்','கையேந்திக் குன்னும்பிரா யம்போலும் பார்க்கப் பிராயமுமாய்ப் படுக்கக் கிழவனுமாய்த் தார்க்கத் திறவனுமாய்ச் சந்திக்கிசைந் தவனுமாய்க் காக்கக் கருத்தனுமாய்க் கர்த்தனின் கர்த்தனுமாய் ஏக்கத் திருத்தனுமாய் இவர்சமைந் திவ்வுகத்தில் தர்மத்தால் கலியைத் தன்னந்தன்னால் கரைத்துக் கர்மத்தை யீடழித்துக் காந்தக்கோ லுமெடுத்து நேரோரைக் காத்து நிசமாக வேயெழுப்பி ஏராரைக் கொன்று ஏழ்நரகத்தும் பூத்தி உகத்துக் குகம்வழக்கு எல்லா மொருதலத்தில் தொகுத்து நடுத்தீர்ப்புச் செய்து மிகத்தெளித்து ஆகாத்த பேரை எல்லாம் நரகமதில்   வாகாகத் தள்ளி வாசல்தனைப் பூட்டிச் சித்தத்துக் கேற்ற செடத்தோரைத் தானெழுப்பிப் பத்தரைமாத் துற்றப் பைம்பொன்னிறப் பொற்பதியில் சாகா வரங்கள் சனங்களுக் கேகொடுத்து வாகாக நல்ல வரிசை மிகக்கொடுத்து ஆண்பெண் ணுடனே அதிகவாழ் வுங்கொடுத்துக் காணக் காணக்காட்சிக் கனமாய் மிகக்கொடுத்துச் செல்ல வைகுண்டர் சீமையைம்பத் தாறதையும் அல்ல லகற்றி அரசாள்வா ரீசுரரே மும்மூர்த்தி யெல்லாம் ஒருமூர்த்தி யாயிருக்கும் எம்மூர்த்தி யெல்லாம் இயல்மூர்த்தி போலிருக்கும் வைகுண்டப் பெம்மான் வாய்த்தசெந் தூர்க்கடலில் மைகொண்ட நாதன் வந்து பிறப்பதுதான் வளர்ந்துதவம் செய்வதுவும் வாய்த்தபுவி தெச்சணமே குழந்தை வைகுண்டர் குமாரப் பிராயமட்டும் கோலா கலனாய்க் குருவாய்ச் சமையுமட்டும் நாலாஞ் சொரூபம் நடத்தும்வரை தெச்சணமே பொல்லாதெல் லாமழிந்து பூவர்கொலு வாகுமட்டும் எல்லா வழக்கும் இருப்புந்தெச்ச ணாபதயே ஆதியி லுதித்த வாறும் அரன்சிவன் பிறவி வாறும்  ஓதிய யுகங்கள் வாறும் ஒவ்வொரு அசுரன் வாறும் நீதிய மனுக்கள் வாறும் நீதமாங் குண்டர் வாறும் தீதிலாத் தர்ம ஞாயச் சிறப்புட வாறுஞ் சொன்னான்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(332,'தெச்சணாபூமி வளம்','இவ்வாறு எல்லாம் எடுத்துமிக மாமுனிவன் அவ்வாறெல் லாமுரைத்து அகன்றனன்காண் மாமுனிவன் துதிகொடு தன்னால் சிவகோ வேங்கிரியில் பதியச் சிவமும் பதியென்றா ரன்போரே நந்தீ சுரரும் நாட்டினா ரவ்வுரையை இந்தப் பழமொழிபோல் இசைந்தப் புவிமகிமை தெச்சணா பூமி சிவபூமி நல்லதுதான் மிச்சம் வியாகர் முன்மொழிந்த துவுமிது ஆனதா லிப்பூமி ஆகுந் தவசிருக்க கோனாங் குமரி குடியிருக்கு மிந்நகரு சங்குத் துறைமுகத்து சதாகோடி யற்புதங்கள் எங்கு மகிழ இயல்புபெற்ற திந்நகரு மங்கைப் பதிநகரு மணவைப் பதிநகரு கங்கைக் குலநகரு கண்ணாளர் தன்னகரு பஞ்சவர்க ளஞ்சு பார்மன்னர் தன்னகரு சஞ்சீவி தன்னகரு தவத்தோர்க் குருநகரு தெய்வகுல மன்னர் சிறந்தக் குருநகரு  சைவ முனிமார் தவத்துக் கிதுநகரு மேலோகக் காட்சி விளங்கிருக்கு மிந்நகரு சாலோக சாமி சார்ந்திருக்கு மிந்நகரு அரம்பை ஸ்திரீமார் ஆராடுஞ் சுனைநகரு பரம்பெரிய சேடன் பவிசுக் குருநகரு வாவி யுறைநகரு வைகைக் குருநகரு தாவிக் குலாவும் சந்தப்பட்சி தன்னகரு சொல்லஎளி தல்லகாண் தெச்சணா பூமிவளம் நல்லதிந்த நாடு நமக்குகந்த நன்னாடு பசுவும் புலியம் பாவித்திருந் தநகரு கசுவு மெந்நேரம் கரைபுரளும் நன்னாடு அந்நாடு நாடு அரனாட்டுக் கொப்பிடலாம் பொன்னாடு நாடு புரந்தரநாட் டுக்கீடாம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(333,'தெச்சணாபூமி வளம்','தவம்பெற்றோர் வாழ்ந்த தண்மைக் குருநாடு பவமற்றோ ராகி பாவித்த தின்னாடு நல்ல மலைவளரும் நாஞ்சி வளநாடு சொல்லுருசி யானச் சிறந்த குருநாடு ஒருநாடு மிந்நாட்டுக் கொவ்வாது வுத்தமரே திருநாடு ஈசர் தினமுறையும் நன்னாடு சம்பத்துக் கேற்ற சகல குருநாடு   இன்பம் வளரும் இசைந்த குருநாடு ஆதி யொருமுனிவன் அடவில் தவசிருக்க நீதி யறியவென்று நிலமலனுந் தேவியுமாய் கற்றாவுங் கன்றதுபோல் கறைக்கண்டர் வேசமிட்டுப் பற்றாண்மை பார்க்கப் பாரில் மிகமேய்ந்து அந்த முனியடுக்கல் அன்றிராப் போயடைந்தார் சிந்த னருளால் சீறி யொருகடுவாய் பசுவையுங் கன்றதையும் பார்த்து மனமகிழ்ந்து இசுவாக வந்தீர் இரையாய் நமக்கெனவே என்று கடுவாய் இயல்பசுவின் கன்றதையும் தின்றுவிட வென்று சென்றுப் பிடித்திடவே அப்போ பசுவும் அந்த முனியடுக்கல் இப்போ தென்கன்றை இந்தக்கடு வாய்பிடிக்கு மாமுனியே நீயும் வந்தொரு சத்தமிட்டால் தாம்பயந்து கடுவாய் தன்னா லொதுங்கிவிடும் வந்துசொல் லாதாலும் மாமுனியே யிங்கிருந்து உந்தித் தொனியால் ஒருசத்தங் கூறினையால் என்பிள்ளை யென்றனக்கு இப்போ துதவுமென்றார் உன்னுள்ள முமேற்று உடையோன் பதம்பெறுவாய் என்று பசுவும் ஈதுரைக்க மாமுனியும்  ஒன்றுமுரை யாடாது உள்ளங் கவிழ்ந்திருந்தான் கடுவாயுங் கன்றைக் கயிலையங் கிரிதனிலே வெடுவாகக் கொண்டு விட்டதுகா ணம்மானை');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(334,'தெச்சணாபூமி வளம்','பசுவும் வனமறைந்து பார்முனிவன் காணாமல் விசுவாச மாக மேலோகஞ் சேர்ந்ததுவே பின்னுஞ்சில நாள்கழித்துப் பேர்முனிவன் சிந்தையிலே முன்னும் பசுமுறையால் முகுந்தன்பதங் காணாமல் இத்தோசங் கழிக்க இன்னுஞ்சில நாள்வரையும் சித்தத்தோ டொத்தத் தெச்சணா பூமியிலே இரந்து குடித்து இத்தவ மேபுரிந்தால் பரந்தணியும் வேதன் பதமடைய லாமெனவே உத்தரித்து மாமுனிவன் ஒருநொடியி லேயிறங்கித் திர்ப்தியுட னேகித் தெச்சணா பூமிவந்தான் வந்து இரந்து வருசமொன் றானதின்பின் முந்துநின்ற காவில் உவந்து தவசுநின்று பரமன் பாதாரப் பவிசு மிகவடைந்து வரமருளப் பேறுபெற்று வாழ்ந்திருந் தானம்மானை அப்படியே தோசம் அகன்று அறம்வளரும் உற்பனம்போ லொத்த உகந்ததெச்ச ணாபதியே தெச்சணா புரியி னீதம் செப்பிடத் தொலையா தையா  மிச்சமாம் புவியி தாகும் மேவலர்க் குகந்த நாடு பச்சமால் மகனே நீரும் பண்ணுறத் தவசு ஏறும் அச்சமும் வாரா தையா என்றடிமிசைப் பணிந்து நின்றான் நல்லது தானே யென்று நாரணர் தயவு கூர்ந்து வெல்லமர் மணவை வாரி மேன்முக மதிலே நின்று அல்லல்நோய் பிணிகட் கெல்லாம் அறமதால் தண்ணீர் தன்னால் தொல்லைநோய்த் தீர்ப்போ மென்று தெச்சணா புவியில் வந்தார் வந்தவர் தலமும் பார்த்து வழியி னற்குலமும் பார்த்துச் சந்தமாய் மகிழ்ச்சை கூர்ந்து சாமியும் முனியைப் பார்த்து இந்தமா நகரில் வாழும் இராசனு மறிய வென்றே விந்தையா யறிவு வொன்று விதித்தெழுதி யனுப்பு மென்றார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(335,'திருவாசகம்-3','	கவியரசனுக்கு வைகுண்டர் வருகையை உணர்த்தும் நிருபம் \n வால ராமச்சந்திர சூரிய நாராயணர் தாமே வைகுண்டமாய்த் தோன்றி, தர்மம் நித்திச்சு, காணிக்கை கைக்கூலி காவடி என்றே திருச்சம்பதி முதல் வேண்ட ஆவசியமில்லையென்று நிறுத் தலாக்கியே, உகஞ்சோதித்து ஒரு குடைக்குள்ளான ஆயிரத்தெட் டாமாண்டு மாசியில் கடற்கரையாண்டி நாராயணம் பண்டாரமென நாமமுங் கூறி, எளிய கோலமெனப் பாவிச்சு தெச்சணம் பள்ளிகொண்டிருந்து, தர்மமாகத் தாரணி யாபேர்க்கும் தண்ணீ ரினாலே சஞ்சலநோய் கர்மம் வற்மம் வாதை கோதை பயங்களையும் பிறவி நாசமும் பொய்வினை சஞ்சாரமும், பீடை கோடை வாடை தீர்க்கவும், பிள்ளையில்லாத பேர்க்குப் பிள்ளை கொடுக்கவும், கண்ணில்லாதபேர்க்குக் கண் கொடுக்கவும், தனமில்லாத பேர்க்குத் தனங்கொடுக்கவும், சாம்பசதாசிவ சாமி மூவரும் சற்குணமாகியே தன்னாலொரு வேசமாகிச் சமைந்து, சாதி உயர்கொண்ட சத்திமாதர் வழியிலே சகலகுண நாராயண தீரசம்பன்னர் சாதி வைகுண்டமாய்ப் பிறந்திருக்கிறார், இனி நன்றாய்த் தெரியுமே. ஆனதினால் பூமியிலே அடிபிடி அநியாயம் இறை தெண்டம் கைக்கூலி அவகடம் பொய்ப்புரட்டு அவர் செவியில் கேட்க வொண்ணாதென்றும், மகாகோடி தர்ம பாக்கியசாலியாய்ப் பூமியிலே அதிகப் பாசமாய் விரித்து அவரருகிற் சூழ அலங்கார தர்மமணியாய் நிறுத்தி அந்தரவீடு லாடந் திறந்து அதன்வழி அரனடனம் திருநடனம் ஆடல்பாடல் அங்ஙனே கண்ணோக்கி சகலதும் பார்த்தாராய்ந்து இருப்பதால் அவரவர் நினைவிலிருக்கிற தெல்லாம் அவருக்குத் தெரியாம லிருக்கிறதல்லவே ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(336,'திருவாசகம்-3','	கவியரசனுக்கு வைகுண்டர் வருகையை உணர்த்தும் நிருபம் \n அதுகண்டு பதறி ஆரானாலும் அவரிட்டிருக்கிற சட்டம்போல் நடந்து கொள்வாராகவும். அங்ஙனே நடக்கிலென்னு வருகில் அவர் நிச்சித்திருக்கிற தேதியில் நடக்கு படியே வரும். அன்பாகிய மனுக்களுக்கு அனுகூலம். 1008 ஆமாண்டு மாசியில் தெச்சணம் பள்ளிகொண்ட அய்யா நாராயண அய்யா வைகுண்ட மாய் தர்மம் நித்திச்சு எழுதின அறிவென்று மாமுனி எழுதி அவனியறியும்படி அயச்சான்.ஆயிது வல்லாமல் அய்யா நாராயண அய்யா தர்மம் நித்திச்சுத் தவசிருக்கிற படியினாலே, இன்றுமுதல் அவர் நிச்சித்திருக்கிற நாள்வரையும் பூசை புனக்காரம் சேவித்தல் அர்ச்சனை ஆராடு நீராடு தீபரணை சாந்தி காளாஞ்சி கைவிளக்குக் காவடி காணிக்கை தெருமுகூர்த்தம் கோபுரமுகூர்த்தம் திருநாள் முகூர்த்தம் தேரோட்ட முகூர்த்தம் திருக்கொடி முகூர்த்தம் கொடிமர முகூர்த்தம் குருமுகூர்த்தம் குரவை குளாங்கூட்டம் கொலுவாரபாரம் ஆயுதம் அம்பு அச்சுநடை ஆனைநடை அலங்காரம் மஞ்சணைக் குளிநீராடல் இதுமுதலுள்ள நன்மை சுபசோபனம் வரையும் அவர்க்கானதல்லவே, ஆனதினால் நீங்கள் இதுவெல்லாம் இதுநாளைக்ககம் வீணில் செலவிடாமலும் விறுதாவில் நரகில் விழாமலுமிருக்கக் கடவுளிது நாராயண வைகுண்டசாமி திருவாக்குபதேசக் கருணையினால் மாமுனி எழுதி அயச்ச வாசகம் என்று எவரும் அறியவும். என்றிந்த விவர மெல்லாம் எழுதியே உலகில் விட்டு நன்றிந்த ஆழ்ச்சை வெள்ளி நற்கதி ருதிக்கும் வேளை ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(337,'திருவாசகம்-3','	கவியரசனுக்கு வைகுண்டர் வருகையை உணர்த்தும் நிருபம் \n பண்டிந்த மூலந் தன்னில் பஞ்சமி நேரந் தன்னே குண்டத்தின் அரசு கோமான குவலய மதிலே வந்தார் நாதன் குருநாதன் நாரா யணநாதன் மாதவனுந் தெச்சணத்தில் மாமருந்து மாவடியில் மணவைப் பதிமுகத்தில் மாதுகன்னிப் பார்வையிலும் இணையானப் பஞ்சவரில் ஏற்ற அரிச்சுனனும் மணமான நாதன் மகாபரனைத் தானாடி  வணங்குந் தவத்தால் வந்ததா மரைப்பதியில் தெச்சணா மூலை தென்வாரி யற்றமதில் மிச்சமுள்ள தேவர் முழங்கிக் குரவையிடத் தேவர் திசையெட்டும் செயசெய எனநெருங்க மூவ ரதிசயமாய் மோடுவழி தாள்திறந்து ஆரபா ரத்துடனே அவர்கள்வந்து பார்த்துநிற்க வீரநா ராயணரும் வித்தாரத் தெச்சணத்தில் பள்ளிகொண்டா ரென்று நாமம் பரந்திடவே துள்ளியே சொரூபம் சுற்றினா ரம்மானை வைகுண்ட மென்று மனுவோ ரறிந்திடவே மெய்கொண்ட நாதன் மேவிநின்றார் தெச்சணத்தில் யாம மிகக்கூறி அதிகத்திசை எட்டிலுள்ள ஓமப் பசாசுகளை ஒதுங்கவுப தேசித்தார் மேல்நடப்பை யெல்லாம் வித்தார நாரணரும்  தூல்நடப் பாகத் துறந்துதுறந் தேபடித்தார் கண்டவர்க ளெல்லாம் கருத்தோ டறியாமல் வண்டப் புலப்பமென வாக்குரைத் தேபோனார் பத்துமா தம்வரையும் பார்நடப் புள்ளதையும் முற்று மொருகுடைக்குள் உலகாள்வ தும்படித்தார் எல்லா நடப்பும் இவர்படித்த தின்பிறகு  வல்லாண்மை யான வைகுண்டப் பெம்மானும் உகஞ்சோ திக்க உற்றார்கா ணம்மானை தவமே தவமெனவே தானிருந்து வையகத்தில்  பொறுதி சதமாய்ப் புண்ணியனார் தானிருந்து  உறுதியுட னையா உகஞ்சோ திக்கலுற்றார் நாரா யணரும் நல்லவை குண்டமெனப் பேரா னதுநிறைந்து புண்ணியனார் தெச்சணத்தில் மனுநிறமாய் வந்து மனுவைச்சோ தித்தெடுக்கத் தனுவை யடக்கித் தவசிருக்கா ரம்மானை');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(338,'அய்யா வைகுண்டர் திருத்தவம்','உலகினில் மனுவாய்த் தோன்றி ஓர்இரு பதுநாலுக் கப்பால் தலைமுறை வினைகள் போக்கிச் சடலத்துள் ளூற லோட்டி மலைசெந்தூ ரலையி னுள்ளே மகரத்துள் ளிருந்து பெற்று அலைகடற் கரை நாரா யணரெனப் புவியில் வந்தார் வந்தந்த நாட்டி லுள்ள வன்குற்ற மதனைக் கேட்க நந்திகோன் விபூதி சாற்றி நாடிய தவங்க ளேற்றி முந்தநாள் மூவர்க் கெல்லாம் முதன்மையாய்ச் சாதித் தேற்றி சந்ததஞ் சாகா விஞ்சைத் தலைவனாய்ச் சமைய வென்றே நீதிய ரோமம் வீசி நினைவொன்றைக் கருணை வாசி சாதிக ளுரைக ளாற்றிச் சடத்துற வாசை யற்று வாதியாங் கார மற்று மலசல மதங்க ளற்று  ஆதியைக் கருணை நாட்டி அவர்தவம் புரிந்தா ரையா ஆசையாம் பாச மற்று அனுதாரக் குளாங்க ளற்று மாசதாம் வினைக ளற்று வாக்கலங் கார மற்று நீசமாம் கலியை யற்று நீணிலத் தாசை யற்று ஓசையாம் வெளியைத் தாண்டி ஒருவனைக்கண் டுகந்தா ரையா கண்ட வர்ப்பா லேற்று கண்சுழி முனையில் நாட்டிப் பண்டவர் செகலில் பெற்ற படிமுறை தவறா நாட்டிக்  கொண்டவர் லோகந் தன்னைக் குமியவோர் தலத்தி லாக்கி இரண்டது மறிய வென்றே இவர்தவம் செய்ய லுற்றார் மனுதவ தாரங் கொண்டு வந்தவர் பிறக்க லுற்றார் தனுமனு வோர்க ளெல்லாம் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(339,'அய்யா வைகுண்டர் திருத்தவம்','தழைத்துநீ டூழி வாழ்ந்து வனுதர்மப் புவியைக் கண்டு மாள்வரா வாழ்வு வாழ்ந்து துணிவுடன் மனதி லேற்றி சூரியத்தவசு நின்றார் முற்பிதிர் வழிக ளெல்லாம் முதன்மைபோ லாக வென்றும் கற்பினைப் படியே தோன்றிக் கலியுக மதிலே வந்தஅற்புத மடவா ரோடும் ஐவர்தம் குலங்க ளோடும் செப்பியச் சாதி யெல்லாம் செயல்பெறத் தவசி யானார் இத்தவ மதிலீ தெல்லாம் இயல்புடன் வசமே யாகக் குற்றமே செய்வோ னீசக் கொடுகலி வழிகள் சாகக் கர்த்தனார் கர்த்த னாகக் கலியுகத் தீர்வை யாக உற்றவை குண்ட சுவாமி ஒருகுடைக் கரசும் பெற்றார் நாரா யணரே நல்லவை குண்டமெனச் சீராக தெச்சணத்தில் சிறந்ததவஞ் செய்யலுற்றார் ஆறு வருசத் துள்ளே யவர்நினைத்து வாறு வகையெல்லாம் வந்து வழிப்படவே நின்றார் தவசு நெடியநா ராயணரும் குன்றாக் கடலில் கொண்ட வழிப்படியே  கடலில் மிகவே கண்டதெல்லாம் விள்ளாமல்  உடலுள் சூட்சம் ஒருவருக்குங் காணாமல்  அகமதுவே பூசித்து அதிகத் தவமிருந்தார் தவப்புதுமை சொல்லித் தானுரைக்கக் கூடாது ஈசர் முன்னாளில் இருந்தா ரொருதவசு வாசக் குழலுமையை மதலை யுருவாக்க வேட்டுவனுக் கீந்த விஞ்சைதனை நெஞ்சில்வைத்துக்  கூட்டுக் கிளியைக் குழந்தையுரு வாக்கவென்று அத்தவந்தான் போதாது அய்யா இவர்தவத்தே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(340,'அய்யா வைகுண்டர் திருத்தவம்','கற்றைக் குழலுமையாள் கரியமுற் காலமதில் சூர னெனவந்தத் தோசப்பொல் லாதவனை வீரத் தனமாய் வெட்டிச் சரமறுக்க ஆறு முகமாய் அரியதொரு ஆண்பிள்ளைதான் பேறுடனே நமக்குப் பிறக்கவே ணுமெனவே  சரவணப் பொய்கையிலே சாம்புவ னைநினைந்து அரகரா அவள்தான் அன்றுநின் றதவமும் இவர்தவத்துக் கொவ்வாது ஈசரரிலும் பெரிதாய் அவர்தவமு மொவ்வாது அய்யா இவர்தவத்தே சீதை தவசியதும் செப்பவொண்ணா தித்தவத்தே நாத அரிச்சுனனும் நாட்டத் தவத்ததிலும் எத்தனையோ கோடி இவர்தா னிருக்கும்தவம் வித்தரிக்க வென்றால் வெகுமணிகள் சென்றிடுங்காண் சொல்ல எளிதல்லவே சொன்னா லுலகிலுள்ளப்  பொல்லாத் பேரும் பொருந்தியிதைக் கேட்டாக்கால் பாவந் தொலைந்திடுமே பாவிகட்கு மோர்நினைப்பாய் நாமிருப் பதுவோ நாலு திரையதனுள் ஆமவரே கண்டு அறிந்தா லதுவீதம் இச்சொரூபத் துள்ளே இதுவிதிக்க வொண்ணாது வச்சுகந்து பார்ப்பீரால் வழிதெரியு மிந்தேட்டில் என்னமோ வென்று எண்ணம்வைத்துக் கேட்பீரால் அன்னமுத லற்று ஆக்கினைக்குள் ளாவீர் நல்லவர்கள் நல்லாவார் நானுரைக்கே னம்மானை செல்லத் திருகேட்கச் செப்புகிறார் நாரணரும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(341,'அய்யா மக்களுக்கு அருளல் ','சீமையைம்பத் தாறு தேச நருட்களையும்  நேர்மை யொழுங்காய் நிமைப்பொழுதில் வந்திடவே செல்லவை குண்டச் சீமானு மப்பொழுது வல்ல தவமும் மனதுளரா தேமுடித்து உலக நருட்கள் ஒக்கவொரு மிக்கவரத் தலமளந்த நாதன் தானினைத்தா ரம்மானை வடக்குக் கிழக்கு வடமேற்கு நேர்மேற்கு நடக்கும் படியே நல்லதிக்கு நாற்றிசையும் அடங்கல் குடியிருக்கும் ஆட்களெல் லாம்வரவே சாணார் முதலாய்ச் சாதிபதி னெட்டையுமே நாணாம லோடிவர நாரா யணர்நினைத்தார் நினைத்த வுடனே நிமிசம் பொறுக்காமல் தினந்தோறும் வந்து சேரும் நருட்களது எண்ணக் கூடாது எவராலு மன்போரே திண்ணமுள்ள சாதி செப்பொண்ணா தன்போரே சாணா ரிடையர் சாதி வணிகருடன் நாணாத காவேரி நல்லதுலுப் பட்டர்முதல் சூத்திரன் பிரமா தொல்வணி கன்பறையன் ஊத்திர நீசன் உழவ னுடன்குறவன் கம்மாள னீழன் கருமற வன்பரவன் வெம்மா நசுறாணி வேகவண்ட ரிடையர்  சக்கிலிய னோடு சாதிபதி னெட்டுகளும் முக்கிய மாக ஓடிவந்தா ரம்மானை ஆகாயங் காணாது அறைக்குள் ளடைத்திருந்த வாகா ஸ்திரீமாரும் வந்தார்கா ணன்போரே கிழவன் முடவன் கெற்ப ஸ்திரீமாரும் இளவயசு கொண்ட ஏந்துபச லைமுதலாய் ஒக்கவே வந்தார்காண் உடையவனார் தன்னிடத்தில் வந்த நருட்கெல்லாம் வாய்த்தவலு வியாதிமுதல் சிந்தையுடன் தன்னால் தீர்த்தாரே தர்மமதாய் ஐம்பது வயதாய் அரைதளர்ந்தப் பெண்ணாட்கும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(342,'அய்யா மக்களுக்கு அருளல் ','சம்பத்துக் கேற்ற சந்ததிகள் தான்கொடுத்தார் உடல்தன மில்லாது உருவழிந்த பெண்ணாட்கும் சடலமுருக் கொடுத்துச் சந்ததி யுங்கொடுத்தார் பிறவிக் குருடூமை பேச்சறா நாக்குழறல் திறவி யொளிபோல் திறமும் மொழிகொடுத்தார் தர்மமில்லாப் பாவிகட்குத் தாண்மை மிகக்கொடுத்தார் கர்ம வியாதிகளால் கால்கள்மொட்டிக் கைகள்மொட்டி அந்தமொட்டி யெல்லாம் அறத்தா லதைத்தீர்த்துச் சந்தமுடன் கைகால் தானாக்கித் தான்கொடுத்தார் உடலை யுருக்கும் உற்ற சயஇருமல்  குடலைப் புரட்டும் குன்மவாய் வுமுதலாய்த் தீர்த்துக் கெடுத்தார் தேசத்தோர் கண்டுமிகப்  பார்த்துப்பார்த் துமகிழ்ந்துப் பச்சமுற்று வந்தனர்காண் கர்த்தாதி கர்த்தன் கடவுளா ரென்றுசொல்லி என்தேசத் தோரும் இங்குவந்தா ரன்போரே பதிணென் சாதிகளும் பண்பா யொருதலத்தில் விதிவந்து தென்று மேவிக்குலா வியிருந்தார் மேவிக் குலாவி மிகவே யொருதலத்தில் ஆவிநீ ருண்டு அகமகிழ்ந்தா ரம்மானை நொம்பலங்க ளெல்லாம் நிசமாகத் தீருவதும் அன்பா யுலகில் ஆனதே சத்தோரும் ஒருதலமாய் வந்து ஒன்றாய்க் குவிகிறதும் கருதினமாய்ப் பார்த்து அவர்கள் மிகவுரைப்பார் முன்னாள் மொழிந்த முறையாக மம்போலே இந்நாள் சமைந்திருக்கு தென்று மகிழ்ந்துரைப்பார் வைகுண்ட ராசர் வருவார்தான் பூமியிலே மெய்கொண்ட ராசர் மேதினியில் வந்தவுடன் சாதிபதி னெண்ணும் தலமொன்றி லேகுவிந்து கோதிக் குளிக்கும் ஒருகிணற்றி லல்லாது கர்மவியா திசெவிடு கண்குரு டூமையெல்லாம்  தர்மத்தால் தீர்த்தார் சகலவுயி ரானதுக்கும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(343,'அய்யா மக்களுக்கு அருளல் ','செல்வ முண்டாகும் சீமானார் வந்தவுடன் அல்லல் மிகத்தீரும் அவரை யறிந்தவர்க்கு எளியோர் வலியோர் எல்லா மொருப்போலே பழியான பேச்சுப் பாரினில் கேட்கவிடார் தேசமெல்லாம் பரக்கும் சீமானார் வந்தசெய்தி தோசமெல்லா மகலும் சுத்தமாய்க் கண்டவர்க்கு சார்ந்தவர்க்கு சாந்தனுமாய்த் தர்மங்கொடுத் தருள்வார் சேர்ந்தவர்க்கு நல்ல செல்வமுண் டாகுமெனச்  சொல்லியிருந் தாகமத்தின் துல்யச் சுருதிப்படி நல்லவை குண்டர் நாட்டில்வந்தா ரென்றுசொல்லி சாதிபதி னெண்ணும் சடல வரவுமற்று ஆதிவை குண்டர் அடிவீழ்ந் தேத்தியவர் எல்லா மொருதலத்தில் இரண்டு மனுப்போலே அல்லோரும் வந்தோர் ஆவிநீ ருண்டிருந்தார் அல்லாமல் முன்னாள் ஆகமத் தின்படியே எல்லாமுன் வியாசர் எழுதின சொற்படியே தெய்வச்சான் றோர்களுக்குச் சேர்ந்த வியாழமது மெய்வகையாய் வந்து மிகத்தோன்றி யேயிருக்க இன்ன மிவர்வழியில் இயல்பாய் மணமுகித்து  மன்னர் வைகுண்டர் மகராச ராகியவர் சீமையைம்பத் தாறும் சொல்லொன்றுக் குள்ளாக்கி மேன்மைச் சான்றோர்க்கு முத்திரிசெங் கோல்கொடுத்துத் தாழ்வில்லாச் சான்றோரைத் தர்மபதி மீதில்வைத்து ஆள்வதுவும் நிசமே ஆதியா கமப்படியே நாரா யணர்க்கு நாள்பகையாய் வந்தவர்க்குப் போரா தெனக்காலம் புராண முரைக்கிதுகாண் சொல்லி யுலகோர் தொல்புவியில் கொண்டாடிப் பல்லுயிரு மறிந்து பண்பாய் மகிழ்ந்திருந்தார் மகிழ்ந்து சிலநாள் வைகுண்டர் பாதமதில் மிகுந்துபல சாதிமுதல் மிகவந் துரைகேட்டார் வந்த நருளறிய வைகுண்டர் வாய்திறந்து சொந்தமுடன் வசனம் சொல்லிமிகக் கூறலுற்றார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(344,'திருவாசகம் 4','1008 ஆம் ஆண்டு மாசியில் கடற்கரையாண்டி நாராயணம் பண்டாரம் தெச்சணம் பள்ளிகொண்டு காணிக்கை கைக்கூலி காவடி முதல் என்றனக்கு ஆவசியமில்லையென்று, தர்மம் நித்திச்சு, கொற்றவரும் மாண்டு குறும்பு மிகத்தோன்றி, உற்ற துலுக்கன் உடன் வந்துடனோடி, மற்றொரு பத்தாமாண்டில் வைகுண்டம் வருவோ மென்ற ஆகமத்தின்படி வந்து வைகுண்டம் பிறந்து கொண்டிருக்கும் நச்சேத்திரத்தில் குதிச்சிக் கொள்ளுந்தன்னே, மாளுவது மாண்டு கொள்ளுந் தன்னே, முழிக்கப்பட்டது முழிச்சி கொள்ளுந்தன்னே, முழிக்கப்பட்டது கண்டு கொள்ளுந்தன்னே, ஒரு நெல் எடுத்துடைக்க நாடு கேட்டுக் கொள்ளுந்தன்னே, இரு நெல் எடுத்துடைக்க நாடு தாங்காது தன்னே, வானமும் பூமியும் கிடுகிடென்றாடிடுந் தன்னே, வானத்திலிருக்கிற வெள்ளிகளெல்லாம் ஆலங்காய்போல் உதிர்ந்திடுந்தன்னே, மலைகள் இளகிடும் தன்னே, பதி எழும்பிடும் தன்னே, முழிக்கப்பட்டது கண்டுகொள்ளுந் தன்னே என்று நருட்களறியும்படி உபதேசித்துக் கொண்டிருந்தார்.அதை உலகோர் அறிந்தும் கேட்டும் முன்னுள்ள ஆகமமுஞ் சரி, இவர் சொல்கிறதுஞ் சரியென்று ஒத்துக்கொண்டு இவர்தான் வைகுண்ட சுவாமி யென்று கேட்டறிந்து, இவர் தலத்திலே போவோமென்று வந்தார்கள். ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(345,'திருவாசகம் 4',' பின்னும் நாராயண வைகுண்ட சுவாமிதானே, பேய் பல சீவசெந்து ஊர்வனம் புற்பூண்டு கற்காவே ரியறிய உபதேசித்தார். எப்படி யென்றால், வல்லாத்தான் வைகுண்டம் பிறந்து காணிக்கை கைக்கூலி காவடி ஆடுகிடாய் கோழி பன்றி இரத்தவெறி தீபதூபம் இலைப்பட்டை இது முதலானதென்றனக்கு வேண்டாம் ஆவசியமில்லையென்று, தர்மம் நித்திச்சு நாடு குற்றங்கேட்க நாராயணம் சிறையிருக்கும்போது, இனி ஆரேக்கார் என்று பார்க்க, அதையறிந்து நீங்களும் ஒதுங்கியிருங்கோ வென்று உபதேசித்தார், உடனதுகளெல்லாம் அய்யாவாணை நாங்களொன் றும் ஏற்கமாட்டோமென்று சொல்லிப் போனார். உடனே நாராயண வைகுண்ட சுவாமி தானே ஓராண்டு ஒன்னரையாண்டு கழித்து உகஞ்சோதித்து வரும்போது, பேய் செய்கிற அன்னீதம் பொறுக்காமல் மானிடர் வைகுண்ட சுவாமியிடம் வந்து ஆவலாதி வைத்தார். உடனே வைகுண்டராசரும் திட்டிச்சுப் பார்க்கும் போது பேய் செய்கிறது அன்னீதந்தான் என்றறிந்து பேய்களுக்குள்ள முன்னாகமக் கணக்கைச் சோதித்துப் பேயை எரிக்கவேணு மென்று நாராயண வைகுண்ட சுவாமி தானே மனதிலுத்தரித்தார்.');");

       // 13th Day

        db.execSQL("INSERT INTO akilathirattu2 VALUES(346,'பேய்களை எரித்தல்','பேய்கள்தான் பிறந்த வாறும் பெருவரம் பெற்ற நாளும் மாய்கையால் உலகில் பேய்கள் வந்ததோர் நாளும் பார்த்து ஞாயமாய் நடுவுங் கேட்டு நாமதை யெரிக்க வென்றே ஆயர்முன் னெழுத்துங் கொண்டு அருள்முனி வரவே யென்றார் வரவென வுரைத்த போது மறைமுதல் வேதன் வானோர் துரகத மீதோ டாவிச் சீக்கிரம் வந்தா ரங்கே பரமருள் வைந்த ராசர் பார்த்தவர் தன்னை நோக்கி  விரைவுடன் பேய்க்கு முன்னாள் விதிதனைப் பாரு மென்றார் விதிதனைப் பார்த்து வேதன் விளம்புவான் வைந்த ரோடு துதிகொடு எழுத்து மென்றன் சுருதியுங் கேண்மோ அய்யா ஆதிநாரா யணர்தா னிந்த அழிகலி யுகத்தி லேதான் பதினை குண்ட மென்று வந்தன்றே யவர்கள் போனார் பேயொடு பசாசு கூளி பொறாமையுங் கலியும் நீசம்  மாயொடு கபடு கள்ளம் மனக்கறுப் புகங்கள் தீர்ப்பு பொய்யொடு மிரட்டு வஞ்சம் பிழைபொல்லாப் பென்ற தெல்லாம் வாயொடு வாயால் கெட்டு மறுபிறப் பில்லாப் போனார் நாரணர் வைகுண்ட மாகி நாட்டினில் வந்த அன்றே காரண மெல்லா மாச்சு கலியுக மழிந்து போச்சு பூரண வேத நூலும் புராணமுன் னாக மங்கள் சாரமுங் கெட்டுப் போச்சு சதாசிவம் வைகுண்ட மாச்சே அழிவதை யழித்துப் போட்டு அவரொரு சொல்லுக் குள்ளாய் சுழிவரை யெழுத்தை யூனி தெய்வமா தவரு மாகி வழிதனில் வன்னி யாகி வகுத்திடும் மகவோ ராகி அழிவில்லாப் பதிதா ளாள்வார் ஆகமத் துரைதா னென்றார் ஆனதா லாகா தென்ற அவ்வகைக் கிதுநாள் சாக ஏனமு மிதுதா னென்று இயம்பிட வேதன் தானும் மானமாய்க் கேட்டு வைந்தர் வானவர் சாட்சி யென்று ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(347,'பேய்களை எரித்தல்','தானவர் கணக்கி லூனி சத்தியாய்ப் பதித்தா ரன்றே கணக்கிலே யெழுதிக் கொண்டு கருத்தினி லடக்கி வைத்து இணக்கியே யவரை யெல்லாம் இலக்குலக் கதிலே கொல்வோம் பிணக்கியே கொல்லே னானும் பிசகில்லா வழியே செய்வோம் குணக்கில்லாச் செய்வோம் பாரும் கோகிரித் தேவ ரெல்லாம் உகசிவா வானோர் வேதன் ஒருவரும் போக வேண்டாம்  வகையுடன் நான்தான் செய்யும் வழிதனைப் பார்த்துக் கொள்ளும் இகபரன் முதலா யிங்கே இருமெனச் சொல்லி வைத்துப்  பகைசெய்த கழிவை யெல்லாம் எரிக்கவே பரனங்குற்றார் ஈசர்முதல் வானோர் எல்லோரும் போகரிது வாச மலரோனும் மற்றோரும் போகரிது என்று இவர்களையும் இவர்சூ ழருகிருத்தி அன்றுபேய் கொண்டுவர அவர்நினைத்தா ரம்மானை உடனே யுலகமதில் ஊர்ந்துதிரி யும்பேய்கள் திடமே குளறி சிந்தை மிகக்கலங்கி பேய்க ளொருதலத்தில் பிரமாண மாய்க்கூடி மாய்கைத் தொழில்குளறி மனது மிகஅளறிச் சீல முடனுரைத்த சிவவைந்த ராசர்மொழி ஏலமேநாம் கேட்டு இருந்திலமே எல்லோரும் என்னவோ வென்று எண்ணி நினைத்ததெல்லாம்  சன்னைபோ லாகாமல் சாக விதியாச்சே அய்யோ கெடுத்தோம் அவர்பேரில் குற்றமில்லை பொய்யென் றிருந்தோமே பெரியோ ருரைத்ததெல்லாம் நம்பேரில் குற்றமல்லால் நாரணர்மேல் குற்றமில்லை தம்பேரில் குற்றமல்லாமல் சாமிமேல் குற்றமில்லை நமக்கு முன்னாளில் நாரத மாமுனிவர்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(348,'பேய்களை எரித்தல்','எமக்குயாமி யங்கொடுத்து அதிகவரம் வேண்டித்தந்தார் அவரிடத்தில் நாமள் எல்லோரும் போயுரைத்தால் சிவனிடத்தில் வந்து செய்திகேட் டேயுரைப்பார் போவோம் வாருங்கோ பேய்க ளொருப்போலே கோவேங் கிரிதனிலே குடியிருக்கும் மாமுனிவர் அண்டை யவரணுகி அபயம் முறையமென்றார் கண்டந்த மாமுனிவர் கழிவையெல் லாமமர்த்தி அபயமிட்ட தேது அலகைகளே சொல்லுமென்றார் கௌவை துயரமுற்ற கழிவெல்லாஞ் சொல்லலுற்று மனுவுருப்போல் வையகத்தில் வைகுண்டம் வந்திருந்து இனியுங்க ளையெல்லாம் எரிப்போ மிப்போதெனவே வரங்களையும் வைத்து மாண்டுபோங் கோவெனவே துரத்தனமா யெங்களையும் துடர்ந்து பிடிக்கிறார்காண் பதறியிங் கோடிவந்து பாட்டையும் மோடுரைத்தோம்  இதறு தனைத்தீர்த்து இரட்சிப்பீ ரென்குருவே என்றுபே யெல்லாம் இப்படியே சொன்னவுடன் அன்று தலையோடு அந்தமுனி தானடித்து வைகுண்டம் பூமியிலே வந்து பிறந்தாரென்றால் மெய்கொள் சிவன்முதலாய் மேல்பிறக்க வேணுமல்லோ  சத்தி சிவன்வரையும் சத்துப் பிறக்கணுமே பொல்லாப் பிசாசுகளே புத்திகெட்டுப் போனீர்களே  வல்லாத்தா னல்லோ வைகுண்ட மாபொருளும் ஆரவர்நா மங்கேட்டால் அல்லவென்று சொல்வதுதான் சீர்பரன் முதலாய்ச் சிந்தை பதறணுமே உங்களுட மூப்பு உகத்திலினி வாராது எங்களுட மூப்புவரை இல்லையென்று போகுதுகாண்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(349,'பேய்களை எரித்தல்','எல்லா மவர்மூப்புக் குள்ளே யடங்குதுகாண் பொல்லாப் பசாசுகளே பொன்றவிதி யுங்களுக்கு ஆனாலு மீசர் அமைப்புக் கணக்கெடுத்து நானே யிதுமேவித் தாறேனென் றிப்போது இந்தக் கணக்கும் ஏழு யுகக்கணக்கும் எந்தக் கணக்கும் இருப்பு மவரிடத்தில் ஆனால் கணக்கை அவரிடத்தில் நாமள்போய் நானாகச் சொல்லி நற்கணக் கும்பார்த்து  அறிந்துகொள்ள லாமெனவே எல்லோரும் போவோமென்று நாரத மாமுனியும் நாட்டில்பே யத்தனையும் காரண வைகுண்டர் காலில்வந்து தெண்டனிட்டார் தெண்டனிட்ட போது சொல்வார் முனியுடனே பண்டா கமப்படியே பசாசையெல் லாமெரிக்க என்மனதி லுற்றேன் இங்கேவந்து வாய்த்ததென்று நன்முனியே பேயை நகன்றிடாக் காருமென்றார் முன்னேநீ பேய்க்கு முதல்யா மியங்கொடுத்து இந்நேரங் கூட்டி என்னிடத்தில் கொண்டுவந்தாய் வந்தது நல்லதுதான் வாய்த்தமுனி யேயிப்போ இந்தாப் பேய்க்கணக்கை எடுத்துரைக்கேன் கேட்டிருநீ பொல்லாக் கலியன் பிறந்தநா ளன்றுமுதல் எல்லாம் பேய்களுக்கு ஈந்தவர மானதினால் நீசன் கொடுமை நீணிலமெல் லாம்பரந்து தேசச் சிறப்புச் சிதறிக் குலைந்ததெல்லாம் வாயநா ராயணரும் வைகுண்ட மாய்ப்பிறந்து ஞாய நடுகேட்டு நாட்டுக்குற்றந் தீர்த்து ஆகாத்த தெல்லாம் அழித்துநர கத்திலிட்டு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(350,'பேய்களை எரித்தல்','வாகாய் நரக வாசல் தனைப்பூட்டி நல்லோர்க்கு நாலு வரங்கொடுத் தேயெழுப்பி  பொல்லாரை நரகில் போட்டுக் கதவடைத்து வல்லோரைச் சொல்லொன்றுள் வைந்த ரரசாளப்  பிறந்துபூ மிதனிலே பேறாக வந்தவுடன் இறந்ததுகாண் பேய்கள் இவர்க்காகாப் பேர்களெல்லாம் என்று பேய்களுக்கு ஈந்துவரம் கொடுத்ததெல்லாம் அன்று பேய்க்கணக்கு வாசித்தா ராதியுமே இம்மணியி லித்திவசம் ஈந்தவர மல்லாது எம்மணியு மிப்பேய்க்கு எள்ளளவு மில்லையென்றார் உண்டோகாண் பேய்க்கு ஒருமணிதா னானாலும் கண்டோ முனியே கணக்குண்டோ சொல்லுஇனி கேட்டு முனியும் கெஞ்சிமிக வாய்புதைத்து நாட்டுக் குடைய நாரா யணருரைத்தால் எவர்தா னெதிர்த்து இல்லையென்று சொல்லுவது மூவரு மங்கே ஒடுங்கி யிருக்கையிலே  அல்லாமல் நீசன் அழிவாகு முன்னதியாய் எல்லாமவ னோடுளது ஏலமே சாகணுமே நீசன் வலுவெல்லாம் நேர்முன்னே தானறுத்து ஓசையொன்றுக் குள்ளே உகங்கேட்க நிச்சயித்தீர் நன்றிகெட்ட நீசன்வேர் நாள்வழியே நீரறுத்து ஒன்று விளிக்குள் உகமழித்துத் தீர்ப்புசெய்து  நல்லோரை யெழுப்பி நகரொருசொல் லுள்ளான வல்லோராய் நீரும் வரம்பெற்ற மாதவமே ஆனதினால் பேய்க ள் அந்நீசக் குலமதினால் வானக் கனலில் மாளுமிக் காலமிதாம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(351,'பேய்களை எரித்தல்','என்று முனியுரைக்க ஏற்றவை குண்டமுதல் நன்று முனியே நம்பேரில் குற்றமில்லை முன்னுள்ள ஆகம முறைநூற் படியாலே என்னூ லொழுங்கில் எரிக்கிறே னிப்பேயை மாமுனியே நீசாட்சி மாயாண்டி நீசாட்சி நாமுனியே சாட்சி நல்லவரே நீசாட்சி எல்லோருஞ் சாட்சி என்பேரில் குற்றமில்லை வல்லாத்தான் வைகுண்ட மாலவ ருஞ்சாட்சி என்றுநா ராயணரும் எரிக்கலுற்றார் பேய்களையும் பண்டு அந்தப்பேயைப் பார்மீதில் கொண்டாடும் நருட்கள் மிகப்பார்த்து நாலைந்து தான்தாக்கிக் கருக்கிவிட வென்று களிவைத் தலையாட்டி மனுக்க ளறிய மனுநருளைத் தானாட்டித் தனுக்கள் மிகவாங்கத் தானினைத்தா ரம்மானை அப்போ நருள்பேரில் ஆடும்பே யேதுசொல்லும் இப்போது சுவாமி எங்களொக்கத் தானெரித்துத்  தனியே யரசாள சாதியொன்று தானெடுத்துச் சொல்லொன்றுக் குள்ளான சுவாமி வரங்கள்பெற்றுக் கொல்லென் றெங்களையும் கொல்லத் துணிந்தீரே என்றுபே யெல்லாம் எண்ணியெண்ணித் தானழுது அன்று அழுது அவர்வரங்கள் வைக்கலுற்றார் காந்த லிடியும் கனத்தவலுச் சக்கரமும் சூழ்ந்து குத்தல்கண்டு சுருண்டு மிகப்பேய்கள் ஐயோ சுவாமி அடியார்க்குத் தந்தவரம் வையோ ரறிய வைக்கிறோ மென்றுரைத்தார் உடனே வைகுண்டர் உற்றநா ராயணரும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(352,'பேய்களை எரித்தல்','திடமுடனே பேயோடு உரைக்கிறார் சீமானும் அணியில்லாப் பேயே அன்றுதந்த வரத்திலொரு மணியிருக்கு தானால் மாறியென் னோடுரையும் அப்போது பேய்கள் எல்லோருந் தானழுது இப்போது நாழிகையும் இல்லைமணி நேரமதும் உம்முடைய தர்மம் உண்டானா லெங்களையும் தம்முடைய கருணை உண்டாற் பிழைப்போங்காண் என்று பேயுரைக்க எம்பெருமா ளேதுரைப்பார் அன்று நானுரைத்து ஐமூன்று மாதம்வரை பார்த்து இருந்தேன் பசாசுகளே யுங்களுக்காய்க்  காத்து இருந்தேனே ஆண்டொன்றொரு கால்வரையும் என்பேரில் குற்றமில்லை என்று சாட்சிவைத்து உன்பேரில் குற்றமதால் ஒடுக்கிறே னுங்களையும் என்று பேயோடு இப்போவரம் வையுமென்றார் அன்றுபே யெல்லாம் அறமெலிந்து தான்வாடி என்னமுறை சொல்லி இப்போவரம் வைக்கவென்று வன்னப் பொருளே வகையாகச் சொல்லுமென்றார் அந்தப் பொழுதில் அய்யாநா ராயணரும் இந்தப் பொழுதில் இம்மணியிந் நேரமதில் ஐயாவு மங்கே ஆகமத்தைத் தான்பார்த்து கைவாய்த்து தென்று கழிவோ டுரைபகர்வார் திட்டிச்ச அய்யாணை சேர்ந்து வரங்கள்வைத்து விட்டுருந்தச் சாவுபிழை சங்கிலிகள் தானும்வைத்து மலையேறி யொக்க மாண்டுபோ வோமெனவே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(353,'பேய்களை எரித்தல்','விலையாய்ப் பலகையிலே விரித்தடியுஞ் சத்தியமாய் அய்யா வுரைத்த ஆணை தவறாமல் மெய்யாகப் பேய்கள் விளம்பிவைத்து தம்மானை அய்யாணை அய்யாணை திட்டிச்ச அய்யாணை மெய்யாணை எங்கள் மிக்கவரங்கள் வைத்தோம் தந்தபிழை சாவு சங்கிலிக ளானதுவும்  எந்தன் குலமுழுதும் இப்போ வரமும்வைத்து அய்யாணை நாங்கள் அருமலைகள் தானேறி மெய்யாக அக்கினியில் விழுந்துசெத்துப் போவோமெனச் சொல்லிப் பேயெல்லாம் சுத்தவர மத்தனையும் பல்லுயி ருமறியப் படித்துவரம் வைத்ததுவே வைத்த உடனே வைகுண்டர் முறைப்படியே மெய்த்தபுகழ் சான்றோர் மெய்வாயுங் கையாலும் ஆகாத்த தெல்லாம் அழித்திடு வாரனவே வாகாக முன்னூல் ஞாய வழிப்படியே வாணாள் மறுகி வாதைவரம் வைத்தவுடன் சாணா னொருவிரலால் சற்றேதொட வைத்தனரே தொட்டிடவே பேயும் சுற்றுமனு நருளும் பட்டு விழுந்தாற்போல் பாரறிய தான்விழுந்தார் உடனே மனுவழியில் உடைய குலச்சனங்கள் படபடெனத் தூக்கிப் பதங்கோரி விட்டிடவே அசதி தீர்ந்தாற்போல் அந்த நருள்முழிக்கும் செய்திதனை யெல்லாம் சீமை நருளறியும் கண்டு கொண்டாடிக் காரணத்தின் ஞாயமென்று மன்று பதினாலும் கண்டு மகிழ்ந்திருந்தார் இப்படியே பேய்கள் எல்லாம் வரங்கள்வைத்து ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(354,'பேய்களை எரித்தல்','அப்படியே பேய்கள் அருமலைபோய் மாண்டதுவே மலைதனிலே வந்து மாண்டசெய்தி தானறிய இலையி லாடிட்ட இடையர்மிகக் கண்டுளறி அய்யோபேய் மலையில் அலறிக் கரிவதுதான் மெய்யாகக் கண்டு மிகப்பதறு தெங்கள்மனம் அழுதுமுறை யிட்டுப்பேய் அக்கினியில் சாடுவதும் முழுது மலைகள் முழக்கிக் கிடுகிடென ஆடு மிகப்பதறி அம்மலையில் மேயாமல் சாடுதுகாண் தரையில் சாரங்கள் சீமானே என்று இடையர் இயம்பக்கேட் டெம்பெருமாள் அன்று நருளறிய அவர்போ தித்தார்சாட்சி கேட்டு மனுநருட்கள் கெட்டிகெட்டி யிந்தமுறை நாட்டுக் குடைய நாரா யணரிவர்தான் மன்று தனையளந்த மாயத் திருநெடுமால்  என்று பலரும் இயல்பா யறிந்திருந்தார் ஆகமப் படியே பேய்கள் அதினுட வரங்கள் வாங்கி லோகங்கள் அறியக் காட்டி யுகபர சாட்சி நாட்டி வேகத்தில் மந்திர தந்திர விசையெல்லா மடக்க வென்று நாகத்தி லுண்டு வாழும் மலையரசனை வருத்த வென்றார் வருத்தவே வேணு மென்று மகாபரன் மனதி லுன்ன  விருத்தமாய் மலையில் வாழும் மிருகங்கள் கோப முற்றுத் துரத்தலைக் கண்டு மெத்தத் துயரமுற் றயர்ந்து மந்திர வருத்தலைச் செய்து பார்த்து மலைந்தன னரசன் தானே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(355,'மந்திரவாதிகளின் விசையடக்குதல்','மந்திர தந்திர மாமுனிவன் சாத்திரங்கள் விந்தைசெய்யு மாய்மால விசையடக்க வேணுமென்று நினைத்த வுடனே நெடுமலையில் தான்வாழும் அனர்த்த மிடும்மிருகம் ஆனை புலிகடுவாய் கடுகிக் கரடி கனத்தமந் திக்குரங்கும் முடுகி யெழுந்து மூச்சுவிட் டேவிரைவாய்ச் சீறி யெழுந்து சென்றுமலை யில்வாழும் கூறித் தொழில்கடிய குன்றரசர் தங்களையும் விரட்டத் தொடுத்ததுகாண் மிருக மிகக்கூடி அதட்டுப் பொறுக்காமல் அந்த மலையரசர் ஓமந் தனைவளர்த்து ஒருகோடி பூச்சொரிந்து ஆமா அரியெனவே அட்சரங்கள் தானெழுதி புவனைவலிச் சக்கரங்கள் பீட மதில்நிறுத்தி நமனை யழைக்கும் நல்லவலுச் சக்கரமும் சத்தி நிறுத்தி சதாகோடிப் பீடமிட்டு சுற்றிவர வோமம் சுழலக் கனலெழுப்பி வேண்டும் படைப்பு விதம்விதமாய்த் தான்படைத்துக்  கூண்டு மலையரசன் குறளிகளைத் தான்வருத்தச் சேவித்தான் மூன்று நாளாக மந்திரங்கள் கோபித்துப் பார்த்தான் குறளிகள் தன்பேரில் சற்றும் பலிக்காமல் சடைத்துமந்தி ரத்தோனும் கற்றத் தொழிலும் கருவுங் கலைகலைந்து என்ன விதமோ என்று மிகக்குளறி மன்னன் மலையரசன் மார்கள் மிகநடுங்கி எங்கே யினிப்போவோம் என்று மனம்பதறி சங்கை யழிந்து சருவில் மிகஇறங்கி தட்டழிந்து வாடி தவித்துநின் றேதுரைப்பார் பட்டபா டேதெனவே பண்பாகக் கேட்பதற்குக்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(356,'குறத்தி வருதல்','குறத்தியொன்று காண்கிலமே குறிகேட்டுப் பார்ப்பதற்கு உரைத்து அவரிருக்க உடையவ னாரருளால் குறத்தி யொருத்தி குறப்பெட்டி யுமிடுக்கி விறைத்துரைத்து வந்தாள் விசாரமிடு வோர்கள்முன்னே கண்டார் குறத்தியையும் கலிதீர்ந்தோ மென்றுசொல்லி வண்டாடுஞ் சோலை வனத்தில்வா என்றழைத்து அருகி லிருத்தி அவளைமிக ஆதரித்துச் சரிவிலுள்ள மாங்கனிதேன் தகையாற வேகொடுத்து பசிதீர்ந்து குறத்தி பகர்வா ளொருவசனம்  விசியா யெனதுடைய பசிவிசா ரந்தீர்த்தீர் உங்க ளுடதுயரம் ஓட வழிகேளீர் தங்களுக்கு மெங்களுக்கும் சகலவுயி ரானதுக்கும் நாயகமாய் வந்து நாட்டில் மிகஇருக்கார் வாச மணக்குதுகாண் வையகங்க ளீரேழும் பேரு வைகுண்டர் பெரிய திறவான்காண் நேரா யிருப்பவர்க்கு நிச்சமாய்க் கைகொடுப்பார் அவரைநீங் கள்கண்டு ஆனதுய ரமுரைத்தால் தவிருந் துயரமெல்லாம் சுவாமியவர் தன்னருளால் என்று குறத்தி ஈதுரைக்க நாகரசர் நன்று குறத்தியுடன் நம்துயரஞ் சொன்னோமோ சொல்லா திருந்திடினும் சொன்னாளே நம்துயரம் எல்லாத் துயரமதும் இதினாலே தீருமென்று சந்தோசங் கொண்டு சற்குறத்தி யையனுப்பி வந்தார்கள் தெச்சணத்தில் வைகுண்டர் வாழ்பதியில் கண்டுவை குண்டரையும் கரங்குவித் தேபணிந்து பண்டுபட்ட பாட்டைப் பகர்ந்தா ரவருடனே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(357,'குறத்தி வருதல்','கேட்டு வைகுண்டர் கிருபையுட னேதுரைப்பார் நாட்டுக் குடைய நாராயணர் பாலன் பிறந்து வைகுண்டம் பெரிய யுகமாளச்  சிறந்து தவசு செய்யு முறைமையினால் துக்கக் களையறுத்துச் சுத்தமரை யாளாக்கிப் பக்கக் களையறுத்துப் பகைஞரொன் றில்லாமல் ஒருசொல்லுக் குள்ளே யுகமழித் துகமெழுப்பி இருசொல்லுக் குள்ளே இராச்சியத்தை யாளுதற்குப் பிறந்துகொண் டிருக்கும் பெருமைக் கடையாளம் இறந்ததுகாண் பேய்கள் இப்போது தந்திரமும் போக நினைத்தேன் பெரியதெய்வ முந்தனைத்தான் வேகந் தனிலே விடுத்ததுகா ணென்றன்முன்னே நல்லது கைவாய்த்து நமக்குவிதி தானாச்சு பொல்லாத தெல்லாம் போக விதியாச்சு உந்தன் துயரம் ஒக்கஇப்போ தீரவென்றால் எந்தன் மொழிகேட்டு இடைவீரோ நீங்களெல்லாம் அப்போது மலையில் வாழு மலையரசர் இப்போது சுவாமி என்னநீர் சொல்வீரோ அதுக்கெல்லாம் நாங்கள் அல்லவென்று சொல்லோங்காண் பொதுக்கெனக் கேட்டுப் புகல்வார் வைகுண்டருமே மாந்திர தந்திர மாமுனிவன் சாஸ்திரமும் உபாந்திர வாகடமும் உபாயச் சமூலமதும் மாயக் கருவும் மாரண வித்தையதும்  உபாயக் கருவும் உயர்ந்தகரு மோடிகளும் தம்பனவுச் சாடனமும் சல்லியம் பேதனமும் வம்புசெய்யு மாஞால மந்திர மட்டகர்மம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(358,'குறத்தி வருதல்','எட்டு மடக்கி இதுமுதலாய் மந்திரமும் கட்டுடனே யிப்போ கருசமூ லத்துடனே ஒக்கவைத்தோ மென்று ஓருடக்கா யுடக்கி மிக்க என்முன்னே மேதினியீ ரேழறிய ஆணையிட்டு வையும் அருமலையில் வாழரசா நாணமிட்டுப் போகுமுன்னே நம்முன்னே வையுமென்றார் உடனே மலையரசர் ஒக்க மனதலைந்து குடலே மருண்டு குறுக முழிமுழித்து வம்பறுக்க வந்த மாயக்கூத் தனெனவே தம்பெலங்க ளற்றுத் தலைசாய்ந் துடனயர்ந்து மாட்டோமென்றோ மானால் மலைமிருகங் கேளாது வீட்டைப் பிரித்தெறிந்த மிருகம் விலகாது குடியிருப்பு நமக்குக் குன்றுமே லங்குமில்லை படியீரே ழளந்த பரமனம்மை யிங்குவிடார் அய்யாவுரைத்த ஆணை வழிப்படியே மெய்யாக வைத்து வீடுமட்டும் போயிருப்போம் அல்லாதே போனால் அம்மலையி லிருப்புமில்லை  எல்லா மறிந்து இனிப்போவ துமரிது என்று மலையரசர் எல்லோருஞ் சம்மதித்து அன்று வைப்போமென்று அடிபணிந்து தெண்டனிட்டு ஐயாவே யெங்களுட ஆதிநா ராயணரே கையார வைக்கக் கருவுகொண்டு வந்தோமில்லை ஏடுகொண்டு வந்தோ மில்லையே யெம்பரனே வீடுவரைப் போயெடுத்து விரைவாக வாறோம்நாம் என்றுரைக்க மந்திரத்தோர் எம்பெருமா ளேதுரைப்பார் நன்று அதுதூரம் நம்முடைய கைக்குள்ளொரு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(359,'குறத்தி வருதல்','சூட்ச முண்டு பிள்ளாய் சொல்லக்கேள் நீங்களெல்லாம் மாச்சல் படவேண்டாம் மலையரசா நீங்களெல்லாம் எல்லா விதத்தொழிலும் இந்திரமா ஞாலமுதல் எல்லாம் வைத்தோமென்று ஆணையிட் டாற்போதும் நல்லதுவே யென்று நாடி மலையரசர் வல்ல விதத்தொழிலும் மந்திரமா ஞாலமுதல் எல்லா மடக்கி இப்போது வைத்தோமென்றார் அய்யாணை யிட்டு அவர்முன்னே வைத்தார்காண் மெய்யாணை யிட்டப் பொழுதே மலையரசர் எங்கள் பிழைப்பு இப்போதே போச்சுதையா தங்கடங்க ளொன்று தானிருக்கு தையாவே  இதினால் பிழைப்பு என்றிருந்தோ மித்தனைநாள் விதியால் குறைந்ததுகாண் வித்தை பிழைப்பெல்லாம் மலையில் பயிரிட்டு வயிறு பிழைப்பதற்கு உலைவில்லா நொம்பலமும் உலாவுமிரு கச்சிறையும் இல்லாமலே விலக்கி இப்போ தரவேணும் நல்லாப் பயிர்விளைந்து நாங்கள் பிழைக்கவேணும் என்று இவர்கேட்க இதுதந்தோ மென்றுரைத்தார் அன்று அவரனுப்பி அவர்மலைக் கேகுகையில் வைத்த தொழிலை மலையரசா நீங்களெல்லாம் செயித்த தறியாமல் திருப்பியே செய்ததுண்டால் நரக மதுபிடித்து நாள்வழியே கொன்னுகொன்னு துரோக மடைந்து தீநரகில் மாள்வீர்கள் என்று சபித்தார் எம்பெருமா ளானவரும் அன்று அவர்கேட்டு அப்படியே வந்ததென்றால் சொன்ன நரகில் செத்திறந்து போவோமென');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(360,'குறத்தி வருதல்','அன்னப் பெருமாள்முன் ஆணையிட் டுப்போனார் போனா ருலகில் பிச்சைவேண் டிக்குடித்து வானோ ரறிய மந்திரமா ஞாலமற்று மந்திர வித்ø மாமுனிவன் சாஸ்திரமும் தந்திரமும் போச்சுதென்று சகலோருங் கொண்டாடி  வைகுண்ட சுவாமி வாய்த்த கணக்கதிலே மெய்கொண்ட வானோர் மிகஅறியத் தானெழுதி மேலோக மான வைகுண்ட ராச்சியமும் பூலோக முமறியப் பெரியோ னெழுதினரே மாந்திரங் கழிவு சூன்யம் மாரணக் கருவு கோளும் உபாந்திரக் கேடு தீதோ(டு) ஊறியப் பொய்பொல் லாங்கு ஏந்திய நினைவு பாசம் இதுமுதல் வினைக ளெல்லாம் சாந்தியி லெரித்து நீற்றித் தர்மத்தை வளர்க்க லுற்றார் நல்லநா ராயணரும் நாமமணி வைகுண்டராய்ச் செல்லத் திருவுளமாய்ச் சீமைக் குறுதியுமாய் மனிதவதா ரமெடுத்து வைகுண்ட வாசவனும் பனிதவழும் பத்தினியாள் பாலருட வங்கிசத்தில் உண்டாகி வாழ்ந்து உலக மறிவதற்கு குண்டணிப்பேய் பொய்யும் குறளிக்கரு வைத்தியமும் ஆகா தெனஎரித்து அப்பிறப் புமறுத்து வாகாகத் தர்மபதி வாழுங்கோ மானிடரே என்றுசொல்லித் தர்மம் எம்பெரு மாள்நினைத்து மன்றுபதி னாலறிய வாய்த்ததர்மங் கூறினரே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(361,'வைகுண்டர் தர்மம் கூறல்','இன்றுமுத லெல்லோரும் இகபரா தஞ்சமென்று ஒன்றுபோ லெல்லோரும் ஒருபுத்தி யாயிருங்கோ  காணிக்கை யிடாதுங்கோ காவடி தூக்காதுங்கோ மாணிக்க வைகுண்டம் வல்லாத்தான் கண்டிருங்கோ வீணுக்குத் தேடுமுதல் விறுதாவில் போடாதுங்கோ வைகுண்டருக் கேபதறி வாழுவ தல்லாமல் பொய்கொண்ட மற்றோர்க்குப் புத்தியயர்ந் தஞ்சாதுங்கோ அவனவன் தேடுமுதல் அவனவன்வைத் தாண்டிடுங்கோ எவனெவனுக் கும்பதறி இனிமலைய வேண்டாமே என்று வைகுண்டர் இராச்சியத்தி லுள்ளோர்க்குக் கன்றுக்குமா தாஇரங்கிக் கற்பித்தது போலுரைத்தார் எல்லோருங் கேட்டு எங்கள்வினை தீர்ந்துதென்று நல்லோர்கள் முன்னே நவின்றிருந்த சொற்படியே  வருவார் வைகுண்டர் வந்தா லிருப்பவர்க்குத் தருவார் கெதிகள் சாகாத வாழ்வுகளும் என்றுசொல்லி முன்னோர் எங்கள்கா லமதிலே நன்று திருமொழியை நாங்களுமே தான்கேட்க எங்கள் கலிதீர்ந்து எம்பரனே யென்றுசொல்லி சங்கை யுடனேபல சாதியெல் லாமறிந்தார் நருளறிய தர்மமிதை நாட்டியே யென்பெருமாள் துரித முடனே தொல்புவியில் வாழுகின்ற பட்சி பறவைகட்கும் பலசீவ செந்துகட்கும்  அச்சு அரிமுதற்கும் ஆனமிரு கங்களுக்கும் ஊர்வனங் களானதுக்கும் உதித்தபுற் பூண்டுகட்கும் கார்சேட னாறுகட்கும் கரடுகல் லானதுக்கும் உத்தரவாய்த் தர்மம் உரைக்கிறா ரன்போரே மிருகமொடு மிருகம் மிகமகிழ்ந்து வாழ்ந்திருங்கோ உங்களுக்குப் புற்பூண்டு உண்டு மதைப்புசித்துச் சங்கையுட னோர்தலத்தில் தண்ணீர் குடித்திருங்கோ ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(362,'வைகுண்டர் தர்மம் கூறல்','வாந்திருங்கோ நீங்கள் வைகுண்ட ருண்டெனவே தாழ்ந்திருங்கோ வென்று தங்கள் கிளைபோலே மிருக இனத்துக்கு இதுவுரைத்துப் பட்சிகட்கு உறுதி யுடனே உரைத்தார்வை குண்டருமே பட்சியெல்லா இனமும் பண்பா யொருஇனம்போல் கட்சி யுடனே களிகூர்ந்து வாழ்ந்திருங்கோ சிறிது பெரிது என்றுமிகச் சீறாமல் உறுதி பெரிது உடையோனே தஞ்சமென்று கூடிக் குலாவி குணமாய் மகிழ்ந்திருங்கோ நாடி யுரைத்தார் நல்லபட்சி தங்களுக்கு புல்பூ டானதுக்கும் பொறுதி மிகவுரைத்துக் கல்வாரி தனக்கும் கனத்தசே டன்தனக்கும்  தங்கள் தங்களுக்கு தாழ்மை மிகவுரைத்து மங்கள மாக மனமகிழ்ந்து வைந்தருமே  நாட்டுக்கு நம்முடைய நல்லதர்மத் தின்படியே கூட்டுக்கு ஏற்றக் குருவுபதே சம்போலே எய்யாமல் நாதன் எல்லோருந் தானறிய செய்தர் மஞாயச் செய்திகே ளும்நடுவே அன்னீதத் தாலே லோகம் அவனி யீரேழும் வாடி குன்னியே கலியில் மூழ்கிக் குறுகியே யலைவர் கண்டு கன்னிகள் மதலை யானக் கற்பகக் குலங்கள் தன்னில் மன்னிய மனுப்போல் தோன்றி மன்னுகத் துதித்தார் தானே உதித்தாயிரத் தெட்டாமாண்டில் உவரிசெந் தூரில்பெற்றுக் குதித்தாண் டருள்மாசியில் தெச்சணா குருநாட்டினில் பதித்தாமரை யூரினில் பள்ளிதான் கொண்டநாள் விதித்தாமிதைத் கருத்தாய் அருளுரைத்தா ரறவுரையே பொய்யில்லை பசாசு இல்லை பில்லியின் வினைக ளில்லை நொய்யில்லை நோவு மில்லை நொம்பலத் துன்ப மில்லை தொய்யில்லை யிறைக ளில்லை சுருட்டுமா ஞால மில்லை');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(363,'வைகுண்டர் தர்மம் கூறல்','மையில்லை உலகத் தோரே வாழுமோர் நினைவா யென்றார் இத்தனை யெல்லா மில்லை என்றரி நாதன் சொல்லப் புத்தியி லறிந்து மண்ணோர் புதுமையென் றன்பாய் கண்டு முத்தியி லிவரைக் கண்டு முயன்றவர் பேறு பெற்றார் பத்தியில் லாதா ரெல்லாம் பாழெனச் சொல்லி நின்றார் இப்படி மனுட ரெல்லாம் இவர்மொழி தர்மங் கண்டு ஒப்புடன் கூடி வந்து ஒருவனே தஞ்ச மென்று செப்பிடத் தொலையா தையா சீர்பதம் பதமே யென்று நற்புடன் மனுட ரெல்லாம் நாடியே மகிழ்ந் திருந்தார் கலியரசன் வைகுண்டரைப் பிடிக்க வருதல் நாடியே யுலகி லுள்ள நருடகளோர் தலத்தில் வந்து கூடியே நிற்கும் போது குறோணிதன் கொடியால் வந்த சாடிக ளதினால் நீசன் சாகிற தறியா மீறி பாடிய வைந்தர் தன்னைப் படையேவிப் பிடிக்க வந்தான் வந்தவன் சுசீந்திரந் தன்னில் வளைந்து கூடார மிட்டு மந்திரி மாரை நோக்கி வகையெனப் புகல்வா னீசன் இந்தமா நிலத்தி லென்னோ(டு) எதிரிதா னாரோ சொல்வீர் விந்தையா யவரைச் சென்று விருதிட்டு வருக என்றான் நாடுங் குறோணி நாதவழி யாய்பிறந்து மூட மடைந்த முழுநீச மாபாவி இருந்த நகர்விட்டு எழுந்திருந்து தானேகி வருந்தப் படையோடு வந்தான் சுசீந்திரத்தில்   ஆனைத் திரள்கோடி அதிகப் பரிகோடி சேனைத்திரள் கோடி சென்றார் சுசீந்திரத்தில் சேவுகக் காரர் திலுக்கர் துலுக்கருடன் ராவுத்தர் கோடி ராட்சதர்கள் முக்கோடி சரடன் கரடன் சவுனி கெவுனியுடன் முரட னுடனே மூர்க்கர் முழுமூடர்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(364,'வைகுண்டர் தர்மம் கூறல்','படைக ளலங்கரித்துப் பார வெடிதீர்த்துக் குடைக ளலங்கரித்துக் கொடிகள் மிகப்பிடித்து வாள்கா ரர்கோடி வல்லயக்கா ரர்கோடி தோள்கா ரர்கோடி சுரண்டிக்கா ரர்கோடி அம்புக்கா ரர்கோடி ஆயுதக்கா ரர்கோடி வம்புக்கா ரர்கோடி வந்தா ரவன்கூட வெடிக்கா ரர்கோடி வேல்கா ரர்கோடி அடிக்கா ரர்கோடி வந்தா ரவன்கோடி இத்தனை யார பாரத் துடன்நீசன் தத்தியுடன் நடந்து தமுக்கு மிகஅடித்து வந்து கூடாரம் இட்டான் சுசீந்திரத்தில் தந்துதந் தாகத் தங்கள் மிகக்கூடி கொந்துகொந் தாகக் கொடும்படைகள் கூடிவர  மந்திரி மாரோடு வந்திருந் தேதுசொல்வான் கேளாய்நீ மந்திரியே கிருபையுட னோர்வசனம் வாளாற் பெரிய மன்னர்களந் நாள்முதலாய்  நமக்கா யிருந்ததுகாண் நம்முடைய நாளையிலே துடுக்கான வெள்ளை நசுறாணி யவன்தனக்காய் ஆச்சுதே நாடு அவனுக்கே யாகிடினும் பேச்சுநா மல்லால் பின்னொருவர் காணாதே ஆனதாற் பூமியிலே ஆரொருவ ரானாலும் மானமற வேநமக்கு மாற்றானாய்க் கண்டதுண்டால் ஆய்ந்து விசாரித்து அறிவித்துச் சொல்லுமென்றான் தேர்ந்துநின்று மந்திரியும் தீநீசக் குலமதினால் பொல்லாத சூத்திரப் பிசாசுக் குலமதினால் கல்லாதான் கூடிக் கண்டுதேர்ந் தேதுரைப்பான் வேறொரு வரையும் மேதினியில் நாம்காணோம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(365,'வைகுண்டர் தர்மம் கூறல்','வாறோர் ஞாயமாற்றம் வையகத்தில் காணுதிப்போ நமக்கு இறையிறுத்து நாடூழியங் கள்செய்யும் குமுக்கா யினம்பெருத்த கூண்டசா ணாரினத்தில் வைகுண்ட மாக வந்து பிறந்தோமென்று மெய்கொண்ட பூமியெல்லாம் வேறொருவ ராளாமல் நான்தான் முடிசூடி நாடுகெட்டி ஆள்வேனெனத் தான்தா னுரைபகர்ந்து சாதிபல தும்வருத்தி ஒருதலத்தி லூட்டுவித்தான் உற்றநீ ரானதையும் அருகிருக்குஞ் சாணார்க்கு அதிகவிதி தோணுதுகாண்  ஆகாத்தப் பொல்லாரை அழித்துப்போடு வேனென்றும் சாகா வரங்கள் சாணாருக் கீவேனென்றும் சொல்லி யிருக்கிறான் சுவாமிவை குண்டமென்று தொல்லுலகி லிந்தச் சொற்கேட்டு ராசாவே என்றவன் மந்திரிகள் இத்தனையுஞ் சொன்னவுடன் ஒன்றுமுரை யாடாது உட்கார்ந் தயர்ந்துரைப்பான் ஏதுகாண் மாயம் இதுஞாயம் வந்ததென்ன வாதுக் கொருவர் வருவாரைக் காணோமென்று நினைத்ததற்கோ இந்த நேருசொல் வந்ததுகாண் மனதயர்ந்து பார்த்து மந்திரியைத் தானோக்கி ஏதுகாண் மந்திரியே எல்லாரிலு மெளியச் சாதியிலே வுள்ளவன்தான் சாற்றுவனோ இந்தமொழி ஏதோவொரு சூட்சம் இருக்குதுகா ணிம்மொழியில் ஆராய்ந்து பார்ப்போம் எல்லோருங் கூடிருந்து பாராய்நீ மந்திரியே பாரிலொரு சாஸ்திரியை என்றந்தப் பேர்கள் எல்லோரும் சம்மதித்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(366,'வைகுண்டர் தர்மம் கூறல்','அன்றந்த சாஸ்திரியை அவன்வருத்திக் கேட்கலுற்றான் சாஸ்திரியே யிந்தத் தர்மராட்சி யந்தனிலே வேற்றொருவ ரென்றனக்கு வீறிட்ட துண்டோகாண் எதிரியுண்டோ பாரு என்னுடையச் சோதிரியே  கருதி மிகப்பார்த்து கடியச்சோ திரிபுகல்வான் தர்ம முடிவேந்தே சாற்றக்கே ளுமரசே கர்மக் கலிதோசம் கமண்டலங்க ளேழ்வரையும் சுற்றிப் பரந்ததுகாண் தோசத்தால் பூமியெல்லாம் கர்த்தனரி கிருஷ்ணர் கடியகோ பம்வெகுண்டு பூமியிலே வந்து பிறந்திந்தத் தோசமதை சாமிதர்மத் தாலே தோச மதையழித்து நற்பூ மியாக்கி நாடெங்கு மோர்குடைக்குள் சொல்லொன்றுக் குள்ளான சுவாமிமகா விட்டிணுவும் மனுவாய்ப் பிறந்து வைகுண்ட மென்றுசொல்லித் தனுவை யடக்கித் தவசு மிகஇருந்து நல்லோரை யெல்லாம் நாடி மிகஎடுத்து வல்லோராய்ச் சாகாமல் வரங்கள் மிகக்கொடுத்து ஆள வருவார் அவர்வருகும் நாளிதுதான் என்றுமிகச் சோதிரியும் இந்தக் குறியுரைக்க நன்று நன்றென்று இராசன் மிகமகிழ்ந்து சொல்லக்கேள் சோதிரியே சொன்னதெல் லாஞ்சரிதான் எல்லையுண்டோ அவர் வந்திருக்கு மினங்களென்ன சொல்லி விரிநீ சுத்தமுள்ளச் சோதிரியே நல்லியல்பு பெற்ற நற்குறியோ னுரைப்பான்  இன்னவகைச் சாதியிலே இவர்வரு வாரெனவே சொன்னக் குறியே சூதாய்த் தியங்குதுகாண்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(367,'வைகுண்டர் தர்மம் கூறல்','தருமங்கொண்டு சுவாமி தரணியில் வந்ததினால் பொறுமைக்குல மாயிருக்கும் பெரியவழி யாயிருக்கும் நம்மாலே சொல்லி நவிலக்கூ டாதிதுவே சும்மாஎனை நீங்கள் சோலிபண்ண வேண்டாமே என்றந்தச் சோதிரியும் இணங்கி வணங்கிநின்றான் மன்று தனையாளும் மன்னன்மறுத் தேபுகல்வான் அப்படித்தா னானால் ஆதிமகா விட்டிணுவும் இப்படியே வந்து இவர்பிறக்க வேணுமென்றால் போற்றி நம்பூரி பிராமணசூத் திரக்குலத்தில் ஏற்றிப் பிறக்க இயல்வில்லா மலிந்தப் பிறர்தீண்டாச் சாணார் குலத்தி லவதரித்துப் பிறக்க வருவாரோ பெரியநா ராயணரும் சும்மாயிருந் தச்சாணான் சுவாமி சமைந்ததெல்லாம் பம்மாத்தாய்க் காணுதுகாண் பிராக்கிரம மந்திரியே இப்போ தொருநொடியில் இந்தச்சா ணான்தனையும் மெய்ப்பாகக் கொண்டு விடவேணு மென்றன்முன்னே என்றுமந் திரிமாரை ஏவுந் தறுவாயில் அன்று அருகிருந்த ஆவிடையர் தம்வழியில்  ஒருவ னெழுந்து உற்றரசனை நோக்கிக் கருதனம்போல் புத்தி கற்பித்தா னம்மானை ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(368,'கலியரசனுக்குப் பூவண்டர் உபதேசம்','கேட்டீரோ முன்னா ளுள்ளக் கெறுவித வளங்க ளெல்லாம் தாட்டிமை யாகச் சொல்வேன் தமியனும் வணங்கி நின்று பூட்டியே மனதில் வைத்துப் புத்தியென் றெண்ணி நீரும் நாட்டினில் நன்மை செய்து நலமுட னரசு செய்யும் மாயனோ உபாயத் தாலே மனுவது போலே யாகி ஆயனக் குலமுந் தோன்றி அவனியி லிருப்பன் கண்டீர் மாயத்தை யறியா நீரும் மாளவே போக வேண்டாம் ஞாயமா யுரைத்தே னையா நம்முட அரசுக் கேகும் எளிமையாங் குலங்க ளென்று எண்ணுற மனுவே யல்ல பழியென ஆதி நாதன் பார்க்கவே மாட்டா ரையா அழிவது யுகத்துக் காச்சு ஆனதால் வருவார் திட்டம் வழியிது இல்லை யையா மாறியே போவோம் வாரும் தசரதன் மகவாய் மாயோன் சீமையில் வந்து தோன்றி புசமுடப் பிறப்போர் கூடப் பிறந்ததுங் கேட்டி லீரோ வசையுடன் மனுத்தா னென்று வாதிட்ட அரக்கன் தன்னை விசையுட னறுத்த செய்தி விளம்பியுங் கேட்டி லீரோ தெய்வகி வயிற்றி லுற்று ஸ்ரீகிருஷ்ணன் எனவே தோன்றி வைவசு தேவன் பெற்ற மனுவென வளங்கள் நாட்டி  நெய்யிடை வழியிற் சேர்ந்து நெருட்டிமா ஞாலஞ் செய்து செய்வதுங் கஞ்சன் தன்னைச் செயித்ததுங் கேட்டி லீரோ ஐவர்க்குத் தூத னாகி அங்குமிங் கோடிச் சென்று மைக்குழல் சிலர்கள் தம்மை மணங்களு மிகவே செய்து மெய்ப்புடன் கிளைக ளோடு மேவியே குழாங்கள் செய்து  செய்துரி யோதனனைக் கொன்றச் செய்தியுங் கேட்டி லீரோ இப்படி மாயன் தானும் இருமூன்று யுகங்கள் தன்னில் ஒப்புடன் மனுப்போல் தோன்றி உவமைகள் பலதுஞ் செய்து அப்படி யுகங்கள் தன்னால் அழிந்திடா வரங்கள் பெற்ற');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(369,'கலியரசனுக்குப் பூவண்டர் உபதேசம்','செப்பிடக் கூடா மாயன் செய்தியுங் கேட்டி லீரோ சாணெனக் குலத்தில் மாயன் சார்வரோஎன் றெண்ண வேண்டாம் பாணனாய்த் தோன்றி நிற்பார் பறையனாய்த் தோன்றி நிற்பார் தூணெனத் தோன்றி நிற்பார் தோலனாய்த் தோன்றி நிற்பார் ஆணெனத் தோன்றி நிற்பார் அவருரு கேட்டி லீரோ குசவனின் குலத்தில் வந்தார் குறவனின் குலத்தில் வந்தார் மசவெனக் குலத்தில் வந்தார் மாடெனக் குலத்தில் வந்தார் விசுவெனக் குலத்தில் வந்தார் வேடனின் குலத்தில் வந்தார் அசுவெனக் குலத்தில் வந்தார் அவருரு கேட்டி லீரோ எவ்விடந் தானாய் மேவி இருப்பவர்க் கெந்தச் சாதி அவ்விட மாகா தென்று அவர்தள்ள மாட்டா ரையா  செவ்விட மாபோன் சூட்சம் செப்பிடத் தொலையா தையா இவ்விடம் விட்டு நாமள் ஏகுவோம் நம்மூ ரென்றான் என்றவன் சொன்ன போ இராசனு மிகவே கேட்டுப் பொன்றுற விதங்கள் சற்றும் புத்தியி லறியா வண்ணம் மன்றலத் தோர்க்கு மேற்கா வார்த்தையை யுரைத்தா யென்று கொன்றவன் தன்னைப் போடும் கோபமா யுரைத்தா னீசன் வேதமாங் கலைகள் தேர்ந்த மேற்குல மதிலே மாயன் சீதமாய்ப் பிறவா வண்ணம் சிறுகுலம் புக்கு வானோ ஆதர வில்லா வார்த்தை ஆர்க்குமே யேற்கா போடா பாதகா எனக்குப் புத்திப் பகரவே வந்தாய் மோடா நித்தமும் முன்னால் லட்சம் நேடிய பொன்க ளிட்டுச் சத்திரச் சாலை தோறும் தர்மமு மளித்து நாமள் சித்திரத் திருநாள் நித்தம் செய்கிற குலத்தைத் தள்ளி நித்தியம் வேலை செய்யும் இழிகுலம் புக்கு வானோ பிரமனை யொப்பாஞ் சாதிப் பிராமணக் குலத்தைத் தள்ளிச்  சிராமனு மற்றோர் தன்னில் சேர்வது திடனோ இல்லை வராகமா யுதித்த மாயன் மனுவெனப் பிறக்க மாட்டான்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(370,'கலியரசனுக்குப் பூவண்டர் உபதேசம்','இதாரடா இந்த வார்த்தை என்முன்னே சொல்லி நின்றாய் காரண மான மாயன் கலியுகம் வருகென் றெண்ணி நாரணன் பயந்து வோடி நல்மலை யேறி மானாய்  வாரண நாத னம்பால் வல்வேட னெய்து செத்தான் தாரணி வாறே னென்று தவலோகஞ் சேர்ந்தான் மாயன் அப்படிக் கொத்த மாயன் அவனியில் மனுப்போல் வாரான் இப்படி யிவன்தான் சொன்ன இகழ்ச்சியை எங்கே கண்டோம் கப்பிடிச் சாணான் கையில் கைக்கூலி வேண்டிக் கொண்டு இப்படி மழுப்ப வந்தாய் ஏதடா செய்தி யென்றான் மழுப்பில்லைக் கேளுங் கேளும் மாண்டுநான் போனா லென்ன வழுப்பில்லை யுனக்குத் தூண்டல் வளருதே யவரி டத்தில் கழுத்திலே யுனது கையால் கத்தியை வாங்கி வைப்பாய் முழுத்தில்லை யானா லென்சொல் மேல்வீசை யரிந்து தாறேன் ஆனையும் படையு முன்றன் அரண்மனைக் கோவில் காலும் சேனையுங் குடையும் வியாழச் செல்வமுஞ் சிறப்போ டெல்லாம் ஏனமு மிகவே தோற்று எரிந்துநீ கரிந்து சாவாய் நானிதைச் சொல்ல வில்லை நாரண ருரைத்தா ரென்றான் என்றிவன் சொன்ன போது ஏங்கியே வுளறிப் பின்னும் பொன்னுற வகையி னாலே புத்தியி லறியா வண்ணம் கொண்டிவன் தன்னைப் போடுங் கொடுவிலங் கதிலே சிங்கத் தண்டிகை யெடுத்து வாரும் சாணெனச் சாமி பார்க்க பார்க்கவே போவோ மென்று பார்முடி வேந்தன் சொல்லத் தார்க்கெனப் பாவி நீசச் சறடனு மேதோ சொல்வான்  ஆர்க்கிது ஏற்குஞ் சாணான் அவனிடம் போவ தென்ன கார்க்கதி கௌட சாஸ்திரி கடியனை யனுப்பு மென்றான் அனுப்புமென் றுரைத்த போது அரசனு மேதோ சொல்வான் துனுப்புடன் சாணான் தன்னைச் சூழ்ந்துநூற் கயிற்றாற் கட்டிக் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(371,'கலியரசனுக்குப் பூவண்டர் உபதேசம்','கனிப்புட னமது முன்னே கட்டுடன் கொண்டு வாரும் வனிப்புடன் போவா யென்று வரிசையுங் கொடுத்தா னீசன் கொடுத்திடும் வரிசை தன்னைக் குவித்தவன் வேண்டிக் கொண்டு கடுத்தமாய்க் கௌவுட சாஸ்திரி கடும்படைக் கோல மிட்டுத் தடுத்திடும் வீரர் தம்மைத் தானிங்கே வாரு மென்று நடுத்தர மதிலே நின்று நல்லணி வகுக்க லுற்றான் பரிநடைக் காரர் தம்மைப் பக்கமு முன்னே விட்டான் கரிநடைக் காரர் தம்மைக் காவலாய் முன்னே விட்டான் சரிபரி யேறிக் கௌவுட சாஸ்திரி முன்னே நின்று வரிபடைக் கவசம் போர்த்து வந்தனன் படைக்கு முன்னே அம்பெடுத் தெய்வே னென்று அவனொரு அணிகள் சாட கம்பெடுத் தடிப்பே னென்று காலசன் மிகவே சாட கொம்பெடுத் துதைப்பே னென்று குஞ்சரத் தலைவ ரோட பும்படக் கொல்வே னென்று போயினன் கௌவுடன் தானே இந்தநே ரந்தனிலே ஏகியந்தச் சாணானை குந்தமது கொண்டிடித்துக் குண்டிக் கயிறிறுக்கி  இங்கேகொடு வாறேன் என்று கௌடனுமே துங்க விருதுபெற்றுச் சூழப் படைவகுத்து ஆயுத மம்பு அணியீட்டி தான்பிடித்து வாயுதக் கணைகள் வகைவகை யாயெடுத்துப் பரியை விதானமிட்டுப் பாய்ந்துக் கௌடனுமே கரியைக்சூ ழநிறுத்தி கனபடைகள் பின்னிறுத்தி நீசன் விடைவேண்டி நீணில முமறிய பாசக் கயிறுடனே பாவியந் தக்கௌடன் வாற விதமதையும் வைந்தப் பொருளறிந்து காரணிக்க மொன்று கருத்தில் மிகநினைத்து இப்போ பதறி எழுந்திருந்து வோடவென்று அப்போது மாயன் அறிந்தேகும் வேளையிலே நன்மையாய்ச் சொல்லி நாடுவது மெச்சாதி தின்மையாய்ச் சொல்லிச் சிரிப்பதுவு மெச்சாதி என்றுநாம் பார்ப்போம் என்று மனதிலுற்றுப் பண்டுதாம் பிறந்த பாற்கட லைநோக்கி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(372,'சுவாமி பாற்கடல் நோக்கி ஒடுதல்','ஓடத் துணிந்தார் ஒருவேச மும்போட்டு ஆடத் துணிந்தார் ஆகாசத் தேதேவர் ஆடையணியாமல் அரைக்கயிறு மில்லாமல் நாடைக் கெணியாமல் நடந்தார் கடல்நோக்கி  நல்ல மனுச்சாதி நாரா யணாவெனவே செல்ல மக்களெல்லாம் சிவசிவா என்றுசொல்லிக் கூடத் தொடர்ந்துக் குவித்துப் பதம்போற்றித் தேடரிய நல்லோர் சேவித்துப் பின்வரவே ஆகாத நீச அநியாயச் சாதியெல்லாம் வாயாரப் பேசி வம்புரைத் தேநகைத்தார் பதறி யிவன்போறான் படைவருகு தென்றுசொல்லிக் கதறிமிக ஓடுகிறான் கள்ளச்சுவா மியிவனும் சாமியென்றால் நருட்குத் தோற்றுமிக ஓடுவானோ நாமிவனை நம்பி நடந்ததுவும் வீணாச்சே பேயனுட பேச்சைப் பிரமாண மாய்க்கேட்டு ஞாயமது கெட்டோம் நாமெல்லா மென்றுசொல்லி நீசக் குலத்தோர் நெடுந்தூரம் வைத்தார்காண் தேசமதை யாளும் செல்லவைந்தார் சூட்சமதை அறியாமல் நீசரெல்லாம் அப்போநன்றி யுமறந்து வெறிகொண்ட பேயனென மேதினியில் பேசினர்காண் நல்ல மனுவோர் நாரணா தஞ்சமெனச் செல்ல மனுவோர் திருப்பாத முந்தொழுது கூடி நடந்தார் குருவே குருவேயென நாடி நடந்தார் நல்லபதியேற் வரெனக்  கண்டுகொள்வோ மென்று காட்சை யுடன்நடந்தார் பண்டு அருள்பெற்றோர் பச்சைமா லுடன்நடந்தார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(373,'சுவாமி பாற்கடல் நோக்கி ஒடுதல்','அப்போ வைகுண்ட ராசர் மனமகிழ்ந்து எப்போதும் வாழ்வார் எனக்கான மக்களெல்லாம் வம்புரைத்த நீச வழிமுழுதுஞ் செத்திறந்து முன்பு விதியால் முழுநரக செத்திறந்து என்று சபித்து எம்பெருமாள் தானடந்து பண்டு பிறந்த பால்கடலுள் சென்றனரே சென்றனர் தேவ ரோடு செகல்கரை தனிலே நின்று நன்றினந் தன்னைப் பார்த்து நவிலுவார் வைந்த ராசர்  பண்டெனைப் பெற்ற தாதா பாற்கட லுள்ளே யென்னைச் சென்றிட அழைக்கிறார் நீங்கள் செகல்கரை தனிலே நில்லும் நில்லுமென் றினத்தை யெல்லாம் நிறுத்தியே கடலி னுள்ளே பல்லுயிர் யாவும் ஆளும் பரமனு மங்கே செல்ல வெல்லமர் தேவ ரெல்லாம் மேகனிற் குடைகள் போட வல்லவன் தகப்பன் பாதம் வணங்கியே வைந்தர் சொல்வார் அப்பனே வொப்பில் லாதா அலைகடல் துயின்ற மாயா செப்பவுந் தொலையா நாமம் சிமையில் விளங்கப் பெற்றாய் தப்பர வில்லா என்றன் தகப்பனே கேண்மோ வையா எப்போநான் விளங்கி யுன்றன் இரத்தின சிம்மாசன மேறுவேன்  ஆண்டு ரண்டாச்சே மாதம் அயிரு பதினாலாச்சே வேண்டுத லின்னங் காணேன் வெம்புறேன் கலியில் மூழ்கி கூண்டுநீர் முன்னே சொல்லிக் குருவுப தேசம் வைத்த ஆண்டின்னம் வருகி லையோ அப்பனே மெய்யுள் ளோனே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(374,'முட்டப்பதி விஞ்சை 1','இப்படி மகன்தான் சொல்ல இருகையால் மகனை யாவி முப்படித் தவத்தால் வந்த முதலெனத் தழுவிக் கூர்ந்து எப்படி யென்றோ நீயும் ஏங்கவே வேண்டா மப்பா ஒப்பில் லாச்சிங் காசனம் உனக்கென வளரு தப்பா ஏங்கியே பதற வேண்டாம் எனது மகனே நீகேளு மூங்கிக் கலியை விட்டகன்று முழித்துக் குதித்து வுதித்தஅன்று தாங்கி யுனைநான் வந்தெடுத்துத் தனது தாயை யிடம்நிறத்திப் பாங்கி லுதித்த மகனுனக்குப் பட்டந் தரிப்பேன் பதறாதே வண்ணம் பதியி னலங்காரம் வகைகளின்ன தென்று சொல்லி எண்ணத் தொலையா தென்மகனே இரத்தின சிங்கா சனப்பவிசு கண்ணைப் பறித்து நீசனுட கருவை யறுத்துக் கலியழித்து எண்ணும் வளர்ந்து வாழவைப்பேன் என்னா ணையிது நீசமகனே மண்ணி லுள்ளோர் தாமறிய மனுவோர் சீவ செந்தறிய கண்ணே யிவர்கள் கண்காணக் கலியிற் காட்சி மிகநடத்தி புண்ணிற் கதிரு பட்டாற்போல் பொல்லா நீசன் கண்டுழைந்து எண்ணியறியா நீசனெல்லாம் ஏங்கி மாள வைப்பேனே  மகனே நீயும் தவமிருக்கும் வாய்த்தஇடத் திலிப்போ சென்றால் உகமே யறியக் கலிநீசன் ஓடி வந்து உனைப்பிடித்துச் செகமே ழறிய உன்கையைத் திருக்கிப் பினனே கட்டிறுக்கிப் பகையே செய்து மிகஅடித்துப் பார விலங்கில் வைப்பானே வைப்பான் மூன்றே முக்கால்மாதம் வாய்த்த விலங்கில் நீயிருந்து செய்ப்பாய் கலியை யறுப்பதற்குத் தெய்தி இதுவே கைவாச்சு போய்ப்பா ரென்பான் பின்னுமவன் பெரிய குற்ற மிகவேற்று மெய்ப்பா யவனு முரைத்ததுபோல் மெள்ள வுரைத்து இருந்திடுநீ மனுவை யடித்த துபோலே வசைகள் சொல்லி மிகஅடிப்பான் தனுவை யடக்கிக் கொண்டிருநீ சற்றும் புதுமை காட்டாமல் இனிமே லறிவா யென்றுசொல்லி எண்ணி மனதி லெனைநினைந்து கனிபோல் மகிழ்ந்து நீயிருநீ கண்ணே யெனது கற்பகமே அன்பா யுன்னை யடுத்திருந்த ஆதிச் சாதி யவ்வினர்க்குத்  தன்பா லருந்துஞ் சாதிகட்குத் தவத்துக் குறுதி தான்கொடுத்து என்பாற் கடலின் கரைதனிலே ஏழு மணிக்கு விடைகொடுத்துப் பின்பா லவரை யருகழைத்துப் புசத்தி லடுக்க இருத்திடுநீ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(375,'முட்டப்பதி விஞ்சை 1','கண்டாய் கண்டா யுடன்புதுமை கண்ணே வுனக்கு நற்காலம் கொண்டாய் கொண்டாய் பத்தறையில் கோவில் காலும் சிவலாயமும் தண்டா யுதத்தால் வாகனமும் சத்தி மாத ரிருபுறமும் பண்டோர் காட்சி யுனக்குள்ளே பண்பாய்க் காணு தென்மகனே  காட்சிச் சிறப்புங் கலியாணம் கவரி வீசிக் கொலுவாரம் சாட்சிக் கணக்கு முன்வருத்திச் சந்திர வர்ணக் கொடிநிறுத்தி வாச்சி யுனக்குத் திருநாளும் வாய்த்த டம்மான முழங்க காட்சி யுனது கண்முன்னே காணு மகனே கலங்காதே பல்லக் கேறி தெருவீதி பகலத் தேரு நீநடத்திச் செல்லப் பதிகள் மிகமுகித்துத் திருநாள் கண்டு மகிழ்ந்திருநீ வல்லக் கொடிகள் மரம்நிறுத்தி வருவாய் நித்தம் வாகனத்தில் பொல்லாக் கலியன் கண்டுழைந்து பொடிவா னித்த மடிவானே இப்படிச் சிறப்பு எல்லாம் உனக்கிது மகனே மேலும் எப்படி மலங்கி யென்னோ(டு) இதுவுரைத் தேது பிள்ளாய் சொற்படி யெல்லா மந்தத் தேதியில் தோன்றுங் கண்டாய் அப்போநீ யறிந்து கொள்வாய் அப்பனும் நீதாய னானாய் நல்ல மகனே நான்வைத்த விஞ்சையெல்லாம் எல்லை தனில்வந்து இகனை நடத்துமப்பா என்னென்ன பவிசு இன்னம் நடக்குமப்பா உன்னையின்னங் காணார் உலகி லொருமணிக்குக் கெடுத்தானே யென்று கெடுவார் வெகுகோடி நடுத்தானம் நம்முடைய நம்மக்கள் குன்றாது புழுத்துச் சொரிவாய்ப் பூமி தனில்நீயும் கழுத்துவரை முன்னூன்றிக் கண்ணுந் தெரியாமல்  ஆவி யலுகாமல் ஆடையொன் றில்லாமல் பாவிப் பயல்கள் பரிகாசஞ் செய்யவென்று கிடப்பாய்த் தெருவில் கிழவன்வேச மெடுத்துத் துடைப்பார்கள் நம்மினத்தோர் தூக்குவார் நம்மினத்தோர் சாணாச் சுவாமி சாவாறு ஆகுதென்று வீணாக நீசரெல்லாம் வெகுளியாய்த் தானகைப்பார் பெண்சிலரைக் கட்டிப் பெரும்புவியோ ரறிய மண்சீமை யோரறிய மங்கையொடு சண்டையிட்டுத்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(375,'முட்டப்பதி விஞ்சை 1','தெருவிற் கரையேறித் தேவியரைத் தான்கூட்டி கருவி லுருத்திரண்டு கண்ணாளர் தாம்பிறந்து சீமை நருளறியத் தேசந் தனிலாண்டு தாண்மை பொறுதியுடன் சாணார் விருந்தழைத்து மனுக்கள் முறைபோல் வாழ்வாய்ப் புவிமீதில் தனுக்க ளுனக்குள்ளே சர்வது மேயடங்கும் எல்லா மடக்கி யானுன் னிடமிருந்து நல்லானே யுனக்கு நாட்டில் சிறப்புமெத்தக் காணு மகனே கலிநீசன் கண்ணுமுன்னே ஆணும் பெண்ணோடும் அன்புற்றி ருக்கையிலே நல்ல திருவாளி நற்பட்டு வாகனத்தில் செல்லத் திருவோடும் செம்பவள மாலையுடன்  வாகனத்  தேரும் வருமா ஞாலத்திருந்து கோகனக மாலே குணமாக வேகூடிக் காணுவோர் காண்பார் காணாதார் வீணாவார் தாணுவே நீயொருவன் தானாக ஆண்டிருப்பாய் இன்னம் பவிசு எண்ணத் தொலைந்திடுமோ வன்ன மகனே வாறதெல்லாங் கண்டிருநீ பதறாதே போயிருநீ பாரத் தவசியிலே குதறாதே போயிருநீ கூண்டத் தவசியிலே வாறநீ சன்தனக்கு மலங்காதே நின்றுகொள்ளு போறநீ சன்தனக்கு பொறுமையாய் நின்றுகொள்ளு என்று மகன்தனையும் எழுந்திருந்து போநீயென மன்று தனையளந்தோர் மகனைக் கடல்கடத்திக் கரையிலே கொண்டு கண்ண ருடனுறைந்தார் பிதாவை யனுப்பிப் பெரியவை குண்டப்பொருள் விதானித்து உச்சரித்து வேதவுல் லாசமிட்டுத் தெச்சணா பூமி சென்று தவத்ததிலே உச்சரித்து நாதன் உகந்து நடக்கலுற்றார் நடந்தாரே தெச்சணத்தில் நல்ல முனியுடனே படந்தார மாயன் பண்டு தவமிருந்த தலத்திலே வந்து சுவாமி தவமிருந்தார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(376,'கலி சோதனை','இருந்தனர் விஞ்சை பெற்று இருபுற முனிவர் சூழப் பொருந்திடும் கமல மாது பூரண மதுவாய் நிற்கக் கரிந்திடும் நீசப் பாவி கயிறுவாள் வெடிகள் சூலம் புரிந்தவன் கோட்டி செய்து பிடிக்கவே வந்தா னங்கே வருமுன்னே யருகில் நின்ற மக்களை வைந்த ராசர்  கருதின மாக நோக்கிக் கடக்கவே நில்லும் நீங்கள் பொருதிட நீசன் வாறான் பொறுத்துநாம் வந்த போது அருகிலே உங்கள் தம்மை அழைத்துநாம் கொள்வோ மென்றார் முப்படியே யுள்ள முறைநூற் படியாலே இப்போது நீசன் இங்கோடி வாறான்காண் மெய்ப்பான மக்களெல்லாம் விலகிநின்று வந்திடுங்கோ செய்கிறதைப் பார்த்துத் திரும்பியிங்கே வந்தவுடன் கையருகி லுங்களையும் கட்டாய் வரவழைப்போம் என்று மக்களுக்கு இயம்ப அவர்விலக முன்குறோணி விந்திலுள்ள மூடக் கௌடனவன் கூட்டப் படையோடு கொக்கரித் தேதுடிப்பாய்ச் சாட்டை வெறிபோலே சாடிவந் தான்கௌடன் சூழப் படையைச் சூதான மாய்நிறுத்தி வேழம் பலரேவி வெடியா யுதத்துடனே வந்து வளைந்தான் வைகுண்டர் வாழ்பதியில்  சிந்துக் குயிரான ஸ்ரீபல்ப நாபருமே கவிழ்ந்துபதி வாசலிலே கட்டிலின் மேலிருக்க அவிழ்ந்த துணியோடே அவரிருக்கு முபாயமதை அறியாமல் நீசன் அணிவகுத்துத் தன்படையைக் குறியாய்ப் பிடிக்கக் கூட்ட மதில்நுழைந்து சாணா ரினங்கள் சதாகோடி கண்டுளைந்து வாணாள் வதைப்பானோ என்று மனதுளைந்து குதிரை மேலேறிக் கொடுஞ்சாட்டை யால்வீசி சதிரு சதிராய்ச் சாணாரைத் தானடித்து அடிபட்ட போது அவர்கள்மிகக் கொக்கரித்து முடிபடவே யிந்த முழுநீச வங்கிசத்தை இப்போ தொருநொடியில் இவரையெல் லாமடித்து மெய்ப்பாகக் கொன்று விடுமோ மெனச்சினத்தார் ஆளுக் கொருவன் ஆவானோ நீசனெல்லாம் தூளுபோ லாக்கவென்று துடியா யெழுந்திருந்தார் சான்றோர் சினத்துத் தாறு மிகப்பாய்த்து ஆன்றோரை நெஞ்சில்வைத்து ஆடை மிகஇறுக்கி உடையிறுக்கிக் கட்டி உல்லாசத் தொங்கலிட்டுப் படைத்திரளாய்ச் சான்றோர் பண்பா யொருமுகமாய் எதிர்த்துநிற்கும் போது எம்பெருமாள் தானறிந்து  பொதுக்கென்ற கோபமதைப் புந்திதனி லடக்கிப்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(377,'சுவாமி பொறுமையுரைத்தல்','பொறுத்து இருந்தவரே பெரியோரே யாகுமக்கா அறுத்திட வென்றால் அபுருவமோ என்றனக்கு வம்புசெய்வதைப் பார்த்து வதைக்கவந்தே னக்குலத்தை அன்புக் குடிகொண்ட அதிகமக்கா நீங்களெல்லாம் பொறுத்து இருங்கோ பூலோகம் ஆளவைப்பேன் மறுத்துரை யாடாமல் மக்களென்ற சான்றோர்கள் என்னசெய்வோ மென்று இவர்களையைத் தாங்கடித்து பின்னே விலகிப் பெரியவனே யென்றுநின்றார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(378,'நீசன் கொடுமை','நீசன் மகிழ்ந்து எதிர்ப்பாரைக் காணோமென்று பாசக் கயிறுகொண்டு பரமவை குண்டரையும் கட்டி யிறுக்கிக் கைவெடியா லிடித்துக் கெட்டி யிறுக்கிக் கீழேபோட் டுமிதித்துத் தலைமுடியைத் தான்பிடித்து தாறுமா றாயிழுத்துக் குலையக் குலைத்ததுபோல் குண்டரைத் தானலைத்துக் குண்டியிலே குத்திக் குனியவிடு வானொருத்தன் நொண்டியோ வென்று நெளியிலே குத்திடுவான் வெடிப்புடங் கால்சுவாமி மேலெல்லாந் தானிடித்து அடிப்புடங்கு கொண்டு அடித்தடித்துத் தானிழுப்பான் சாணாருக் காகச் சமைந்தாயோ சுவாமியென்று  வாணாளை வைப்போமோ மண்டிப் பதனிக்காரா பனையேறி சுபாவம் பட்டுதில்லை யென்றுசொல்லி அனைவோரை யும்வருத்தி ஆபரணந் தேடவென்றோ சமைத்தாய் நீசுவாமியென்று சாணாப் பனையேறி பனைச்சிரங் கின்னம் பற்றித் தெளியலையே உனைச்சுவாமி யென்றால் ஒருவருக்கு மேராதே ஆளான ஆளோநீ ஆளில் சிறந்தவனோ தாழாய்க் கிடந்து சாமியென்று வந்தாய்நீ நாரண சுவாமி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(379,'நீசன் கொடுமை','நானென்ற தும்நீயோ  வாரணத்தின் காலில்கட்டி வாட்டுவோம் பாருஎன்பான் உன்னோடு நின்றவர்கள் உன்னை யடிக்கையிலே என்னோடு வந்து என்னவென்று கேட்கலையே நீசுவா மியென்றால் நெகிழுவ ரோஇவர்கள் பாசக் கயிறு பற்றுமோ வுன்கையிலே வகைதேட வென்று மனுச்சுவாமி யென்றாயே புகையோடு புகையாய்ப் போறாய்நீ வம்பாலே நருளை வருத்திவிட்ட நல்லமந்தி ரத்தாலே மருளச்செய் தெங்களையும் மண்ணில்விழ வைபார்ப்போம் அல்லாம லுன்கைக் கட்டு மறுவதற்கு வல்லாமை யுண்டானால் மந்திரத்தைப் பார்நீயும்  கூனைப் போலிருந்து குறுமுழி முழிப்பதென் நானன்னா வென்று நகைப்பா ரொருகோடி ஏசுவார் கோடி எறிவா ரொருகோடி பேசுவார் கோடி பேயன்வெறும் பேயனெனச் சொல்லி நகைப்பார் சுத்தமுள்ள நாரணரை பல்லுயி ரும்படைத்த பரமன்வாய் பேசாமல் கவிழ்ந்து கண்மூடிக் களிகூர்ந் தேமகிழ்ந்து சவந்தரிய நாதன் தானே நடக்கலுற்றார் வெடிகொண் டிடிப்பார் வெட்டவா ளோங்கிடுவார் அடித்தடித்துத் தள்ளி அங்குமிங் குமிழுத்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(380,'நீசன் கொடுமை','வேதனைகள் செய்வார் வேதநா ராயணரை சாதனைக ளுள்ள சர்வபர நாரணரும் எவ்வுகத் துக்கும் இப்பாடு என்பாடு இவ்வுகத் துக்கும் இப்பாடு என்பாடு அவ்வுகத்தி லுள்ள அநியாயப் பாவிகளை  இவ்வுகத்தைப் போலே எரியாமல் விடடேனோ என்று மனதில் இதமாய் மிகவடக்கி ஒன்று முரையாமல் ஊமைபோ லேநடந்தார் நடக்கவே மாயன் நல்லன்பு சான்றோர்கள் கடக்கவந்து நின்று கண்ணீர் மிகத்தூவி  ஏழைக்கா யிரங்கி எங்கள்குல மீதில்வந்தீர் கோழைக் குலநீசன் கொண்டடிக்க வந்தானே பாவிநீ சனாலே பட்ட துயரறிந்து ஆவுமேய்த் தநாதன் ஆளவந்தார் நம்மையுமே அய்யோஇனி நாமள் அலைந்துமிகப் போவோமென மெய்யோடே குத்தி விழுந்தழுவார் சான்றோர்கள் தாய்தகப்ப னில்லையென்று தானடித்த நீசனெல்லாம் வாயயர்ந் திருந்தானே இவர்வந்த நாள்முதலாய் இனியாரு நம்மை ஏற்றுக்கை தாறதுதான் தனியானோம் நாமளினித் தலையெடுப்ப தெக்காலம் என்று சான்றோர்கள் சொல்லி யழுதசத்தம் கன்றுக் கிரங்கும் கண்ணர் மிகக்கேட்டு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(381,'அய்யா சான்றோர்க்கு அருளல்','மலையாதுங் கோநீங்கள் மாமுனிவன் புத்திரரே அலையாதுங்கோ மக்காள் அய்யா திருவாணை இப்பூமி தன்னில்வந்து இத்தனை நாள்வரைக்கும் கைப்பொருளுக் கிச்சை கருத்தில் மிகநினைத்துக் கைக்கூலி வேண்டிக் கருமஞ்செய்தே னானாக்கால் இக்குவ லயத்தில் இனிவரேன் கண்டிருங்கோ தன்மமது நிச்சத்துத் தாரணியில் வந்துண்டால் நன்மைக் கடைப்பிடித்து நான்வருவேன் நானிலத்தில்  ஒன்றுக்கு மலைய வேண்டாங்கா ணுத்தமரே என்றைக்கும் நானிருப்பேன் என்மக்கள் தங்களிடம் ஆளுவே னோர்குடைக்குள் ஆனவை குண்டமனாய் ஆளுவோம் மக்கா வையகத்தை நாமாக ஒன்றுக்கு மலைய வேண்டாம் உகபர நாத னாணை என்றுக்கு மலையின் மீதில் ஏற்றின தீபம் போலே கன்றுக்குப் பாலு போலும் கண்ணுக்குப் புருவம் போலும் என்றுக்கு மக்கா வுங்கள் இடமிருந் தரசு ஆள்வேன் முன்முறை விதியா லிந்த முழுநீசப் பாவி கையால் என்விதிப் படவே வுண்டு இறப்பொன் றனக் கென்றதாலே பின்விதி யெனக்கு நன்றாம் பெரும்புவி யாள்வோ மக்கா  உன்விதி நல்ல தாகும் ஒளிவறா வாழ்வீர் தாமே');");

        //14th Day
        db.execSQL("INSERT INTO akilathirattu2 VALUES(382,'சுவாமி கைது','என்று மகிழ்ந்து எம்பெருமா ளுள்ளடக்கி மன்று தனையளந்த மாலோன் நடக்கலுற்றார் கெட்டினக் கெட்டைக் கிறுக்க முடனிறுக்கிக் கட்டினக் கட்டோடே கடுநீசன் தானடத்தி ஏசுவார் மாயவரை எறிவார்காண் மாயவரைப் பேசுவார் மாயவரைப் பேயனென்பார் மாயவரை இப்படியே நீசக் குலங்களெல்லா மாயவரை அப்படியே பேசி அடித்து மிகநடத்தி  நீசக் குலமிருக்கும் நெடுந்தெருக்க ளூர்வழியே பாசக் கயிறோடு பகற்கள்ள னைப்போலே தெருவுக்குத் தெருவு சிறுகுழந்தை நீசர்குலம் வருகின்ற மாயவரை மண்கட்டி பேர்த்தெறிவார் தலையைப் பிடித்திழுப்பார் சடைப்பேயன் பேயனென்பார் இலைசருகு போலே எரியுகின்ற சாதியெல்லாம் குண்டியிலே யொருவன் கோல்கொண்டு குத்திடுவான் நொண்டியோ வென்று நெளியிலே குத்திடுவான் நம்மள் குலத்தை நாணங் கெடுக்கவந்த பம்பக் குறும்பேயன் பம்பைதனைப் பின்னுமென்பார் அதிலுஞ் சிலபேர் அடடாபோ என்றிடுவார் மதிலேறி வந்து மாமட வார்சிலர்கள் இவன்தா னடியோ என்னையுமே ஆடவைத்துச் சவந்தனையே சுட்டதுபோல் தான்சுட்டப் பேயனிவன் தலைமயிரைப் பனையில் தான்பங்கு வைத்திறுக்கிக் குலையக் குலையக் கொன்னகொடும் பாவியிவன் என்று எறிவாள் இவள்சிலர்க ளம்மானை அன்றுதுகள் கண்டு அதிலுஞ் சிலமடவார் ஆமடியோவுங்களையும் அழைத்தானோ வீட்டில்வந்து ஓமடியோ இந்த இறுமாப்புப் பேசாதுங்கோ  அன்றைக் கவனை அடிபணிந்து நின்றுகொண்டு இன்றைக் கவனை எறியத் தொடர்ந்தாயோ என்று சிலபேர் எறியாதே யென்றுரைப்பார் நன்று நன்றென்று நாரணரு முள்ளடக்கி ஒருதிக் கொருதி உரைத்திடுகோவெனவே கருதிமுகம் பாராமல் கண்ணர் மிகநடந்தார் பாவிக் குலங்கள் பச்சைமால் நாரணரை தாவிச் சுசீந்திரத் தலம்நோக்கிக் கொண்டுசென்றார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(383,'சுசீந்திரத்தில் அய்யா','கொண்டுபோ கும்வேளை கொடும்பாவி நெட்டூரன் விண்டுரைக்கக் கூடாத வேதனைகள் செய்யலுற்றான் அய்யய்யோ மாயவரை அன்னீத மாபாவி செய்த வினையெல்லாம் சொல்ல முடியாதே கொண்டுவிட்டு மாயவரைக் கூண்டகலி ராசன்முன்னே கண்டு கலிராசன் கருத்தில் மிகத்தேர்ந்து பார்ப்போம் பரிட்சை பயித்தியக்கா ரனுடனே தாற்பரிய மாகத் தன்விரலின் மோதிரத்தைக்  கழற்றி யொருவர் காணாமல் கையடக்கிச் சழத்தி வருவது தானறியா நீசன்வன் ஏதடா என்றன் கைக்குள் ளிருப்பதையும் ஓதடா வுன்றன் உற்ற வலுவாலே   அப்போது மாயன் அடக்க மிக அறிந்து இப்போ துரைத்தால் இந்தநீ சக்குலங்கள் கண்டு பிடித்துக் கட்டுவார் நம்மையுமே தொண்டுசெய்யுஞ் சான்றோர் துயரமது மாறாது நாம்நினைத்தக் காரியமும் நடந்திடா தானதினால் ஏனிதைத்தான் சொல்லப் போறோ மெனஅடக்கி எல்லாம் படைத்த ஏகன் கிருபையென அல்லா நினைத்தபடி ஆகு மெனவுரைத்தார் அப்போது நீசன் அவன்வெகுளி யாயுரைப்பான் இப்போ தொருமருட்டாய் இவன்சமைந்தான் சுவாமியென்று மருளிதுதா னென்று மாநீசன் தான்மருண்டு கொண்டுபோங் கோசறடன் கூண்டவன் முன்பதிலே என்றுரைத்த போது ஏவல்சீவா யிகளும் நடத்தி யிழுத்து நாரா யணரையுமோ கடத்திச் சறடன் கண்டிருக்கக் கொண்டுவிட்டார் உடனே சறடன் உபாயமதைப் பார்ப்போமென்று தடதடென எழுந்து சாராய மானதிலே அஞ்சுவகை நஞ்சு அதில்கலந்தான் மாபாவி நஞ்சில்லை யென்று நல்லபா லென்றீந்தான் பாலென்ற போது பச்சைநா தன்மகிழ்ந்து  காலனைக்கா லாலுதைத்தக் கடவுளார் தாமகிழ்ந்து வேண்டி யகமேற்றார் வேதநா ராயணரும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(384,'சுசீந்திரத்தில் அய்யா','ஆண்டி யதனால் அஞ்சா திருப்பதையும் பார்த்துச் சறடன் பண்நா யகமகிழ்ந்து வேற்றொரு சூட்சம் வேறேயுண் டாகுமென என்னமோ என்று இவனுணர்ந்துப் பாராமல் பொன்னர் மயக்கம் பெரிய மயக்கமதால் கொடுபோங் கோவென்று கூறினான் சேவகநாடு வெடுவாகக் கொண்டு வெறுநீசன் டாணாவில் அடைத்துவைத் தானங்கே அன்னீத மாபாவி திடத்தமுடன் நாரணரும் சிந்தைமிக மகிழ்ந்து இருந்தா ரேயங்கே ஏழைக்கிரங்கி யவர் தருந்தார ருக்காகத் தடியிரும்பி லுமிருந்தார் மாண்டோ ரையுமெழுப்பும் மாய வலுவாண்டி அய்யோ அவரை அடைத்துவைத் தேயிருந்த பொய்யோ புரைக்குள் பொறுக்குமோ கண்ணுகண்டால் மோளுக் குழியும் மொய்த்தத்தெள் ளினங்களுமாய்த் தோளு வழியே சொரியுதே தெள்ளினங்கள் அட்டை மித்க அரியத்தேள் ஈமிதிக்க  விட்ட நரகு மிகுவாய்ப் புழுமிதக்க நாற்றத் துறைகள் நரகத் துறைபோலே பார்த்த இடமெல்லாம் பலபுழுக்க ளுஞ்சரிய ஐயோ அய்யாவை அடைத்தப் புரையதிலே கையாடி நிற்கக் காரணமோ மாயவர்க்கு சான்றோ ருக்காகத் தர்மக் குருநாதன் மீன்று முழியாமல் முகமலர்ந் தங்கிருந்தார் ஏழை களுக்காக ஈரொன்றொரு நாளாய் மீளாச் சிலுவையிலே முள்ளா லடிகள்பட்டு மரித்துப் பிறந்ததுபோல் மாயக் குருநாதன் இதுவுகத்துக் கிப்புரையில் இருந்து அழுந்தவென்று இருந்தாரே மாயவனார் ஏழைகளுக் காயிரங்கிக் கருந்தார மார்பன் கருத்தில் மிக அடக்கி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(385,'அய்யாவை அனந்தபுரம் கொண்டு செல்லல்','எல்லா மடக்கி இவரிருக்கும் நாளையிலே பொல்லாக் கலியன் புறப்பட்டான் மேற்கெனவே இப்பே யனையும் இங்கேகொடு வாருமென்று அப்போது சொல்லி அவனடந்தான் மேற்கெனவே உடனே சிவாயி உற்றநா ராயணரைத் திடமாகக் கூட்டிச் சிணமே நடக்கலுற்றான் கொண்டு போனாரே கோட்டாற்று ஊரோடே  கண்டுபெரும் பாவியெல்லாம் கட்டிகொண்டு தானெறிய எறிவார் சிலபேர் ஏசுவா ரேசிலபேர் வெறிப்பேய னென்று விரட்டி மிகஎறியார் எல்லாம் பொறுதியுடன் எம்பெருமா ளுமடக்கிக் கல்லாரை யெல்லாம் கமக்கியறுப் பேனென்று மனதி லடக்கி மாய நெடுமாலும் கனகத் திருமேனி கண்கவிழ்ந் தேநடந்தார் நடந்தாரே கோட்டாறு நல்ல பிடாகைவிட்டுக் கடந்தாரே சுங்கான் கடையெல்கைத் தானும்விட்டுப் பத்மனா புரத்தெல்கைப் பார்த்துத் தெருவோடே உற்பனமாய் மாயன் உள்தெருவோ டேநடக்க ஆகாத பாவியெல்லாம் அவரைக்கண் டேநடக்க ஆகாத பாவியெல்லாம் அவரைக்கண் டேபழித்து வாயாராப் பேசி வைதாரே நீசரெல்லாம் அதிலு மன்போர்கள் அவரைக்கண் டேபணிந்து இதுநாள் வரையும் இவர்நடந்த சட்டமதில் பொல்லாங்கு செய்தாரெனப் பேருநாம் கேட்டதில்லை எல்லாங் கடைதலைக்கு இட்டதர்ம மேகாக்கும் என்றன்போர் சொல்லி இவரழுது நின்றராம் அன்றந்த நாதன் அதுகடந்துப் போயினரே போனாரே தக்கலையின் புரைக்குள்ளே யுமிருந்து  மானான பொன்மேனி வாலரா மங்கடந்து வாரிக் கரையும் வாய்த்த நதிக்கரையும் சீரிய நாதன் சென்றகண் டேநடந்தார் நடந்துத் திருவனந்தம் நல்லபுரத் தெல்கைகண்டு அடர்ந்த மரச்சோலை அடவி வனங்கள் கண்டு கண்டுகொண் டெம்பெருமாள் கண்கொள்ளாக் காட்சியுடன் பண்டுநாம் சீரங்கத்தைப் பார்த்தகன் றேநடந்து இவ்வூரைக் கண்டு எத்தனைநா ளாச்சுதென்று செவ்வுமகா விட்டிணுவும் சிந்தைக்குள் ளேயடக்கி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(386,'சிங்காரத் தோப்பில் சுவாமி சிறை','அவ்வூரைப் பாராமல் அந்த வனமானதுக்குள் எவ்வுயிர்க்குந் தானாய் ஈயுகின்ற பெம்மானும் நாட்டுக் கலியனுக்காய் நற்சோத னையெனவே நீட்டின காலில் நீசனிட்ட விலங்கோடே ஏழைகளுக் காக இருந்தார்பா ராவதிலே கோழைக் குடும்பக் குறும்பர் தமைவதைக்க மனுப்பா ராவதிலே மாயாண்டி தானிருந்தார் தனுப்பா ரமடக்கித் தாழ்மையுட னேயிருக்க ஆகாத நீசன் அழியும் நினைவதினால் போகாத படிக்குப் பேயன் தனைவிலங்கில் போட்டு வையென்றும் புள்ளி பதனமென்றும்  கோட்டுக்கா ரர்தமக்குக் கொடுத்தானே வுத்தரவு அந்தக் கலிராசன் அவனுரைத்த வுத்தரம்போல் சந்தமுனி மாயனையும் தான்விலங்கி லிட்டுவைத்தான் அப்போது மாயன் அதிகசான் றோர்களுக்காய் இப்போ பொறுதிகொண்டு இருக்கே னெனஇருந்தார் நாட்டுச் சோதனைக்காய் நாராயணர் விலங்கில் நீட்டின காலோடே நிலம்பார்த் தேயிருந்தார் சாணான் பால்வைத்துச் சந்தோசப் பாலேற்று நாணாம லெம்பெருமாள் நற்சோலையி லிருந்தார் சோலையில் வாழ்பறவை சாமிவந்தா ரென்றுசொல்லி ஏலேல முங்கூறி எந்நேர முந்தொழுது நன்றான மாமுனியும் நற்பறவை யானதுவும் கொண்டாடிக் கொண்டாடிக் கொஞ்சிவிளை யாடிருக்கும் இந்தவனச் சோலையிலே எம்பெருமாள் தானிருக்க சந்த முடனருட்கள் சதாகோடி யாகவந்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(387,'சிங்காரத் தோப்பில் சுவாமி சிறை','தொழுது நமஸ்கரித்துத் தூயோன் பதம்பூண்டு முழுது முன்பாதமென மொய்குழ லார்சிலர்கள்  நாற்பது வயசாய் நான்சேயில் லாதிருந்தேன் காப்பதுன் பாதமெனக் கண்டு தொழுதபின்பு தந்தாரே யெங்களுக்குச் சந்ததி தழைத்தோங்க  வந்தாரே நாயடியார் வாழு மனையிடத்தில் என்ன உபகாரம் இவர்க்குநாம் செய்வோமென்று கன்னல் கதலிக் கனிகள் கொடுப்போமோ தர்ம மால்தீர்த்தச் சஞ்சலங்க ளானதையும் அம்மம்மா சொல்ல ஆராலு முடியுமோடி குட்டம் பதினெட்டும் குடித்தபத மொன்றாலே கட்டம் முதலாய்க் காணாப் பறந்ததுவே இப்புதுமை செய்தவர்க்கு இப்போ தொருபுதுமை கொப்பளிக்கு முன்னே கூடா தோஇவரால் ஏதோ வொருதொழிலாய் இவர்பம்ம லாயிருக்கார் சூதால் கொடுமைவந்து சுற்றுங்காண் நீசனுக்கு என்று சிலபெண்கள் ஏழைபங் கோனருளை கண்டு தொழுது கருத்தகலா நின்றுடுவார் அப்படியே நருட்கள் ஆதிநா ராயணரை செப்படிபோ லவர்க்குச் செய்தநன்றி சொல்லியவர் கூரைக்குப் போகக் கூறநினை வில்லாமல் நாரணரைச் சூழ்ந்து நமஸ்காரஞ் செய்துநிற்பார் சூழ்ந்து நருட்கள் தொழுதுமிக நிற்கையிலே தாழ்ந்துசா குங்கலியன் சறடன் தனைநோக்கிப் பேயனொரு சாணானைப் பிடித்துக்கொடு வந்தோமே  தூய இரும்பு விலங்கதிலே போட்டுவைத்தோம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(388,'கடுவாய் சோதனை','சாமியென்று பாவித்தச் சாணான்தனை நாமும் காமியத் தால்பெருத்தக் கடுவாய் தனைவருத்தி ஏவிவிட்டுப் பார்த்து இருவகையுந் தானறிந்து போவெனவே சொல்லிப் பேயன் தனையனுப்ப வேணுமே யிப்போ விவரமென்ன என்றுரைத்தான் ஆணுவ மூர்க்க அரச னிதுவுரைக்க நல்லதுதா னென்று நாட்டமுற் றுச்சறடன் வல்லபெலச் சேவுகரை வாவென் றருகழைத்து இப்போ தொருநொடியில் ஏழுமலை யுந்தேடி வெற்போடு வெற்பெல்லாம் வேக முடன்தேடி வேகம் பெரிய வேங்கைகடு வாய்பார்த்து நாகமதிலுங் கடிய நல்லகடு வாய்பார்த்து நாழிகை ஏழதுக்குள் நம்மிடத்தில் கொண்டுவரத் தூளி யதுபறக்கும் சூறா வளியதுபோல் கொண்டு வாவெனக் கூறிச் சறடனுமே விண்டுரைப்பான் பின்னும் வீரியமாய்ச் சேவுகர்க்கு நன்று மொழிகேளும் நல்வீரச் சேவுகரே இன்றேழு மணிக்கு இங்கேகொண்டு வராட்டால் தூக்கியே வுங்களையும் தூண்டலில் போட்டிடுவேன் ஆக்கினைகள் செய்துவுங்கள் ஆமிசத்தை வாங்கிடுவோம் என்று சொல்லிச் சேவுகரை இறுக்கமுட னிறுக்கி இன்றுபோ மெனவே ஏற்றவரி சைகொடுத்து அனுப்பினாள் சேவுகரை அந்தவே ளைதனிலே பனிப்பிசினும் வெயிலைக் கண்டு பதறினாற்போல் பதறியே சேவகர்கள் பண்பாகச் சட்டையிட்டுக் குதறியே வந்து கூண்டுரைப்பார் மாயவேனாடு நாரா யணரே நாங்கள்போ குங்கருமம் பேராய் நடக்கவேணும் பெரிய திருமாலே உம்மைச்சோ தனைபார்க்க குங்கருமம் ஆனதினால் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(389,'கடுவாய் சோதனை','செம்மையுள்ள ராசன் சிணமே யனுப்பினர்காண் ஆனதினால் கடுவாய் அடியேங்கள் கையதிலே போனவுடன் சிக்கிடவும் புண்ணியரே வந்தாரும் என்றுரைக்கச் சேவுகர்கள் எம்பெருமா ளுள்ளமதில் நன்று நன்றென்று நாரா யணர்சிரித்து விடைகொடுத் தார்கடுவாய் வேகமதில் சிக்கிடவும் படைச் சேவுக்கத்தார் பாரமலை தானேறி கடுவாய் கிடக்கும் கனமலைகள் தான்தேடி முடுகித் தலையாரி முடிச்சு வலைவளைந்து காடு கலைத்துக் கடுநாய்கள் விட்டேவி  வேடுவர்க ளெல்லாம் விரைந்து கலைத்தனரே கலைக்க வொருகடுவாய்க் கடுங்கோபங் கொண்டெழுந்து வலைக்குள் நுழைந்ததுகாண் மாயவனார் தன்செயலால் உடனேதான் சேவுகர்கள் ஓடிமிக வளைந்து அடவுடனே வலையை அமர்த்தி மிகப்போட்டு உபாயத்தாற் சென்று ஒருகூட்டுக் குள்ளடைத்துக் கபாடத்தால் கட்டிக் கனகூடு தானெடுத்துத் தாமரை குளத்துச் சன்னாசி பாதமதால் நாமளு மன்னனுக்கு நாடிப் பிழைத்தோமென்றார் நல்லவென்னி யுண்டு நற்சாணாச் சுவாமியிடம் வல்லவர் தானென்று மாதவாய்க் கொண்டாடித் தேடித் திரியாமல் திக்கெங்கும் வாடாமல் ஓடித் திரியாமல் உயர்சாணாச் சாமியினால் நாமும் பிழைத்தோம் நற்கடுவாய்க் கொண்டுவந்தோம் சீமையாளு மரசன் செப்பும் வாக்குப்படியே கடுவாயைக் கொண்டு காலமே போவோமென வெடுவாகக் கூடதுக்குள் வேங்கை தனையமர்த்திச் சுமந்துகொடு வந்தார் துடியான சேவுகர்கள் அமர்ந்த கடுவாய் அதறுகின்ற வோசையினால் நருட்கள் மிகப்பதறி நாற்கரைக்கு மாட்கள்விட்டு ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(390,'கடுவாய் சோதனை','வருகின்ற வேளை மாகோடி யாய்ச்சனங்கள் சாமியென்ற சாணானைச் சோதிக்க வேணுமென்று ஆமியமாய்க் கடுவாய் அதோகொண்டு வாறாரெனப் பார்க்க வருஞ்சனங்கள் பலசாதி யுங்கோடி போர்க்குத் திரள்போல் போற வகைபோலே எண்ணிறந்த நருட்கள் இதிற்கூடி வந்தனரே மண்ணளந்த நாதன் மனமகிழ்ந் தேயிருந்து சாமி யருகில் சூழ்ந்திருந்த சான்றோர்கள்  நாமினித்தான் செய்வதென்ன நாதனே யென்றுசொல்லி கேட்டுநா ராயணரும் கீழ்ச்சுண்டுக் குள்மகிழ்ந்து நாட்டுக் கரிவிரிநாள் நாரா யணனும்நான்  படசி பறவை பலசீவ செந்துக்களை நிச்சயமாய்ப் படைத்த நீலவண்ண நாதனும்நான் மண்ணே ழளந்த மாயப் பெருமாள்நான் விண்ணே ழளந்த விஷ்ணு திருவுளம்நான் ஏகம் படைத்தவன்நான் எங்கும் நிறைந்தவன்நான் ஆகப்பொருள் மூன்றும் அடக்கமொன் றானதினால் நாதக் கடல்துயின்ற நாகமணி நானல்லவோ சீவசெந்துக் கெல்லாம் சீவனும் நானல்லவோ இந்நீச னெல்லாம் என்னையறி யாதிருந்தால்  மின்னிலத்தில் நான்படைத்த மிருக மறியாதோ என்றே யடக்கி ஏகந் தனைநினைத்து ஒன்றுக்கு மஞ்சாதே உற்றமக்கள் சான்றோரே பதறாதே யென்றனுடப் பாலகரே யென்றுசொல்லி இதறாத மாயவனார் இருந்தார்கா ணம்மானை அப்போ தவரிருக்க அம்மிருக மானதையும்  வைப்போடு நெஞ்ச மாநீச ராசனிடம் கொண்டுவைத்தா ரந்தக் கோபக்கடு வாய்தனையும் கண்டு கலிராசன் கனகசந் தோசமுடன் அன்று சிப்பாயிகட்கு ஆனவரி சைகொடுத்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(391,'கடுவாய் சோதனை','இன்றுகடு வாய்தனையும் இப்போது கொண்டுசென்று கூட்டி லடைத்துவைத்துக் கோப மதுவருத்த ஈட்டிமை யாயதற்கு இரையொன்றும் போடாமல் இன்றைக்கு வைத்து இதுநாள் கழிந்ததன்பின் சென்றந்தப் பேயனிடம் செல்லவிட லாமெனவே அடைத்துவை போவெனவே அரசன் விடை கொடுக்கத் திடத்தமுடன் சேவுகர்கள் சென்றடைத்தார் கூடதிலே அன்றிரச் சென்று அடுத்தநாள் சென்றதற்பின் இன்றுகடு வாய்திறந்து இகழ்ச்சியது பார்பபோமென்று வந்தானே யந்த மாநீசப் பாதகனும்  தந்தாய்ச் சறடன் சௌனி கௌடனுமே கவுடன் வெகுடன் கீர்த்திதுரை சானிகளும் மகுடவர்த்தனர் முதலாய் மந்திரி மார்களுமே துலுப்பர் சலுப்பருடன் சிப்பாயி மார்களுமாய் அலுப்பர் பட்டாணிகளும் ஆனகரி காலாளும் காலாளும் வீராளும் கருமறவர் சேகரமும் சூலாளும் தோலாளும் சூழ்ந்தபடை யாளர்களும் ஒக்கத் திரண்டு ஒருமித்தாங் காரமுடன் மிக்கத் திரண்டு வெடியாயு தத்துடனே  ஆனைக்கா ரர்கோடி திரண்டுவந்தா ரம்மானை சேனைக்கா ரர்கோடி திரண்டுவந்தா ரம்மானை இந்தப் படையோடும் எச்சாதி தன்னோடும் வந்து வளைந்தார் மாகடுவாய்க் கூட்டையுமே அப்போது ராசன் அருகில்தலை யாடகளை இப்போது இந்த ஏற்றகாடு வாய்தனையும் குண்டியிலே கம்புகொண்டு குத்துவது கோபம்வரக் கண்டுகடு வாயதுவும் கம்பதுக் கேபதறி அதறி மிகமுழங்கி அதுகவிழ்ந்து தான்படுத்துப் பதறி யதுகிடந்து படுக்கை யிளகாமல் நீச னவன்குத்த நெடுஞ்சற டன்காமல் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(392,'கடுவாய் சோதனை','வாசப் பொடிவருத்தி வன்னப் பொடிநிறைத்து மட்டாய்க் கடுவாய் மாறி யிளகாமல் கிடந்ததுகா øணாய கிருபை யதினாலே  தடந்தெரியா வண்ணம் சனங்கள் மிகப்பார்க்க ஈட்டி யெடுத்து இனிக்குத்த வேணுமென்று மேட்டி யொருவன் விசையாகக் குத்திடவே எட்டிக் கடுவாய் ஈட்டி தனைப்பிடித்து விட்டிடவே யருகில் நின்றதொரு வேதியனைக்  குத்திச்சே யீட்டி குடல்பீற அம்மானை கத்தியும் பட்டுக் கதறியொரு வன்சாகப் பார்த்திருந்த நீசப் பாதக னேதுரைப்பான் நாற்றிசைக்கு மேராதே நல்மறையோன் பட்டதுதான் ஆயிரம் பசுவை அடித்துமிகக் கொன்றாலும் தோச மீதல்லவொரு வேதியனைக் கொன்றதுதான் என்னபோ லாச்சு யாம்நினைத்த காரியங்கள் மன்னன் கலிராசன் மாசறட னுமயர்ந்து ஞாயமீ தல்லவென்று நடந்தா னரண்மனைக்கு தோசமொன் றேற்றோமெனச் சொல்லி மிகப்போனான் அப்போது சனங்கள் அல்லோரு மேதுரைப்பார்  இப்போதிப் பேயனுக்கு இதுவுமொரு நல்லதுதான் என்றுசொல்லி நீசன் ஏற்ற சறடனுமே ஒன்றுமுரை யாடாமல் உள்ளபடை யத்தனையும் கூட்டிக்கொண்டு போனான் கோட்டையதுள் ளம்மானை வேட்டைப் பலித்துதில்லை வெற்றிதான் பேயனுக்கு பார்க்கவந் தசனங்கள் பலதிசைக்கு மிவ்விசளம் ஆர்க்கு மிகவே அறிவித் தகன்றனரே அப்போ சுவாமி அருகில் மிகவாழும் மெய்ப்பான சனங்கள் மெத்தசந் தோசமதாய் இண்ணத் தறுவாய் ஈசுரன் காத்ததுதான் அண்ணல் காயாம்பு அச்சுதனார் காத்ததுதான்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(393,'கடுவாய் சோதனை','என்று மனமகிழ்ந்து எம்பெருமாள் நாரணர்க்கு அன்று விவரம் அறிவித்தா ரன்போர்கள் கேட்டுநா ராயணரும் கீழ்ச்சுண்டுக் குள்மகிழ்ந்து ஆட்டுவோ மாட்டுவோங்காண் அல்லாமல் வேறுளதோ இப்படியே சொல்லி இருக்குமந்த நாளையிலே முப்படித்தான் ஈசர் மொழிந்தநா ளதுவரைக்கும் இருப்போ மெனச்சொல்லி இருந்தார்கா ணம்மானை அருப்பான நீசன் அவன்சிலநாள் சென்றதற்பின் என்ன விசாரம் இப்பேய னுக்குகெனவே தன்னே யிருந்து தானினைக்கும் வேளையிலே முன்னே யொருயுகத்தில் மூவரிய நாரணர்க்கு அன்னமொடு பாலும் அருந்த மிகக்கொடுத்த ஆயர் குடியில் அவதரித்தக் கோமானில் தூய வொருகோனும் துணிந்தங் கெழுந்திருந்து பத்தியுள்ள நாரணரைப் பாரா வதுஇளக்க உத்தரித்துக் கோனும் உலகமதை யாளுகின்ற மன்னவன் முன்பில்வந்து நின்றங்கு ஏதுரைப்பான் தன்னதிய மானத் தலைவனே கேட்டருளும் நம்முடைய ராச்சியத்தில் ஞங்ஙளிட தன்னினத்தில் எம்முடைய ஆளாய் இருக்கின்ற இச்சாணான் சாணானில் நல்ல தன்மைவெகு மானமுள்ளோன் கோணா மனதுடையோன் குணமுடைய நல்லவன்காண் நேர்மை யொழுங்கோன் நேர்சொல் லொருவாக்கன் ஓர்மை யுடையோன் உபகாரக் காரனிவன் ஆனதா லெங்களுக்காய் அரசேநீர் தாமுருகி ஈனமிலா தெமக்காய் இரங்கு மெனத்தொழுதான் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(394,'வைகுண்டர் விடுதலை','அப்போ தரசன் அவன்மனது தானிளகி இப்போ திவனை யாமனுப்பி விட்டிடுவோம் விட்டா லவனும் மேலு மிருக்குமுறை  கட்டாகச் சொல்லிக் கைச்சீட் டெழுதிவைத்துப்  போகச்சொல் லென்று போகண்ட னார்க்குரைக்க ஆகம் மகிழ்ந்து ஆயன்மறுத் தேதுசொல்வான் என்ன விதமாய் எழுதிவைக்கும் வாசகங்கள் மன்னவனே சொல்லுமென மாறி யவன்தொழுதான் அப்போ தரசன் எல்லோரு மேகேட்க இப்போ திவனினத்தில் எத்தனைபேர்க் கானாலும் ஒப்போ டுறவாய் ஒத்திருந்து வாழ்வதல்லால் சற்பம்போ லொத்த சகலசா திதனக்கும் உத்தரவு சொல்லாமல் உபாயமாய்த் தானிருந்து மற்றுமொரு சாதிகளை வாவென்று ரையாமல் தன்னொரு சாதி தன்னோ டிருப்பதல்லால் பின்னொரு சாதி பிதனம்வைத்தப் பாராமல் இனத்துடனே சேர்ந்து இருப்போம் நாமென்றுசொல்லிக் கனத்தோ டவனும் கைச்சீட் டெழுதிவைத்துப்  பின்னவன் எல்லையிலே போகச்சொல் லென்றுரைத்தான் மன்னன் கலியுரைத்த வாக்கின் படியெழுதி ஒப்பமிடச் சொன்னார் உலகளிந்த பெம்மானை செப்பமுடன் நாரணரும் சிரித்து மனமகிழ்ந்து நம்மாலே சொல்லி நகட்டப் படாதெனவே  சும்மா அவன்வாயால் சொல்லிச் சுமைசுமந்தான் இதல்லோ நல்ல இயல்கா ரியமெனவே அதல்லோ வென்று அரிமா லகமகிழ்ந்து கைச்சீட்டை சுவாமி கைகொண்டு தான்கீறி  வச்சுக்கோ வென்று மண்ணில் மிகப்போட்டார் கீறிவிட்ட ஓலைதனைக் கெடுநீசன் தானெடுத்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(395,'வைகுண்டர் விடுதலை','மாறிக் கொருத்து வைத்தான்கா ணம்மானை உன்னில் லிடத்தில் உடனேநீ போவெனவே குன்னுடைய நீசன் கூறினா னம்மானை அப்போது மாயவனார் அகட்டென வுறுக்கி இப்போது நீநினைத்த இலக்கதிலே போவேனோ தேவ னினைத்தத் தேதியுண் டவ்வேளைப் போவே னெனச்சொல்லிப் பெரியோ னகமகிழ்ந்து நல்லவரே மக்காள் நாம்நினைத்தத் தேதியது வல்லவரே வருகு மாசிபத் தொன்பதிலே கிழக்குநாம் போவோம் கிரணமாய் நீங்களெல்லாம் விளக்கொளி போலே வீரம தாயிருங்கோ என்றுரைக்க நாதன் இசைந்தமக்க ளெல்லோரும் நன்று நன்றென்று நாதன் பதந்தொழுது தொழுது அவரிருக்கத் தூயத் திருமாலும்  முழுது மனந்த மூதூரை விட்டுஇன்று பழுதில்லாத் தெச்சணத்தில் பரமனமக் கேயருளித் தந்தயச் சிருந்த தாமரையூர் நற்பதியில் சிந்தை மகிழ்நாதன் சீக்கிரம் போகவென நினைத்துத் திருமால் நிண்ணயமாய் உள்ளடக்கிக் கனத்த புகழ்சான்றோர் கைக்குள்ளே நிற்பவரைப் பய்யவே யின்று பயண மெனவுரைக்க உய்யமிகக் கொண்டோர் உல்லாச மேயடைந்து சந்தோசங் கொண்டு சங்கடங்கள் தீர்ந்துதென்று வந்தோர்க ளெல்லாம் மாயவரைத் தொட்டில்வைத்து ஏந்தி யெடுத்து இயல்வா கனம்போலே ஓர்ந்து வருக உற்ற அனந்தம்விட்டுக் கண்டுநீ சப்பாவிக் கர்த்தாவைத் தானிகழ்ப்பாய்க் கொண்டுபோஞ் சாணாரைக் கூடிமிகச் சிரிப்பார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(396,'வைகுண்டர் விடுதலை','சாமியென்றோன் பட்ட சளங்களெல்லாம் பார்த்திருந்தும் வாய்மதத்தால் பின்னும் வழுங்கல் சுமப்பதுபார் பிழைப்பில்லை யென்றோ புத்திகெட்டச் சாணார்கள் உழைக்க மதியற்று உளத்துகிறான் சுமந்து பேயனைச் சுமந்து புலம்பித் திரிவதற்கு நாயன் முன்னாளில் நாட்டில் படைத்ததுபார்  என்று தூசணிப்பார் இசைந்தபுகழ்ச் சான்றோரை அன்றதிலே சிலபேர் அல்லகா ணென்றுசொல்லி நம்மளுக் கென்ன நட்டம்வந்து போச்சுதெனச்  சும்மா அவனினத்தோர் சுமந்துகொண்டு போவதல்லால் ஏனிந்தப் பேச்சு யாம்சொல்லப் போறோமெனத் தானிந்த வார்த்தை சனங்கள்சிலர் சொல்லவே சொல்லன்பு வன்பு சுவாமி மிகப்பார்த்து நல்ல அனந்தம்விட்டு நாடும்வால ராமம்விட்டுச் செல்ல அரியும் சிறப்பா யகமகிழ்ந்து வல்ல வழியில் வாழுகின்ற ஊரும்விட்டுப் பள்ளிவா சல்விட்டுப் பார்வதி யகரம்விட்டுப் துள்ளிக் கோட்டாறு சுசீந்திரத் தலமும்விட்டு வேக மடக்கி விளியிட்டு வண்டுறுக்கி ஆகமத்தின் தேதி அடுத்தாலா கட்டெனவே சொல்லுவேன் வெள்ளித் தோன்றி வருகையிலே வெல்லுவே னென்று விசையடக்கி எம்பெருமாள் தொட்டிலி லிருந்து சுவாமி விளியுமிட்டு மட்டி லிருந்த வளர்தா மரைப்பதியில் வந்து பதிகண்டு வட்டமிட்டுத் தானாடி விந்து வழிகளுக்கு மேற்கெதிக ளாகுதென்று  இன்றுமே லெனக்கு ஏற்றவெகு சந்தோசம் பண்டு எனக்குப் பகர்ந்த மொழிபோலே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(397,'துவையல் பந்தி-வாகைப்பதி தவசு','சான்றோரை நல்ல தவத்துக் கனுப்பிடவும் பண்டோர் நமக்குப் பகர்ந்தபடி மாதர்களை நாடு மணங்கள் நல்லகலி யாணமுதல் வீடுவகை சொத்து விருதுவே டிக்கையுடன் தேரு திருநாள் திருக்கல்யாணக் கொலுவும் பாரு புகழப் பாவாணர் கீதமுடன் நாட்டில் மனுக்கள் நல்லோர்கள் வாழ்வதிலும் மேட்டிமைக ளாக மிகுவாழ்வு சேர்கையுடன் வேண்டும் பவிசு மிகுவாய்ப் பவிசுகளும் தாண்டும் பெரிய சனங்கள் மிகக்குழைவும் கொடியுமிகக் கட்டிக் கொக்கரித் திகனையுடன் வெடியெக் காளமுடன் வெற்றியி டம்மானமிட்டு நாட்டில் சிறப்பதிகம் நாமினிமேல் செய்யவென்று தாட்டிமையாய் நாதன் சான்றோர் தமக்குப்போ அறிவில் வினோதம் அதிகநே ருத்தமமாய்க் குறியாய் மனதில் குறிக்கும் படியருள அருளான விஞ்சை அருள்கொடுக்கு மாலோனும் மருளாமல் சாணார் மனதி லுருவாக  மூணுநேரத் துவைத்து உச்சி யொருநேரமதாய் வேணும் பச்சரிசி வெற்றிச் சிறுமணியும் வேகவைத்து நன்றாய் விரைவாய் மணலிலிட்டுத் தாக மில்லாமல் தவசிருக்க வேணுமென்று பெண்ணுடனே ஆணும் பிறந்தபிள்ளை தன்னோடும் கண்ணான கட்டில் கலருங் கிழவிவரை சூலி யிளம்பிள்ளை திரண்ட மடமாதும் மாலி னருளால் வஸ்துவகை தான்மறந்து வீடு மனைமறந்து விற்று விலைகள்செய்து ஒண்ணிலரைப் பாதியென ஒக்கவிற்றார் சொத்ததனை மண்ணு மறந்து மாடாடு தான்மறந்து ஆண்டபண்ட மெல்லாம் அகல மிகமறந்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(398,'துவையல் பந்தி-வாகைப்பதி தவசு','கூண்டபண்ட மெல்லாம் கூசாம லேமறந்து அனுபோக மற்று ஆண்பெண் ணிகழாமல்  இனிபோக மற்று இருந்தார் தவசெனவே எல்லோரு மிக்க இருக்கத் தலம்பார்த்து அல்லோரு மேக அவர்கள் மனதிலுற்று நாத னரிநாதன் நாரா யணநாதன்  சீதக்குரு நாதனிடம் சென்றா ரவர்பதத்தல் சென்றே யவருடைய சீர்பாதந் தெண்டனிட்டு  இன்றேக எங்களுக்கு இப்போ விடையருள்வீர் என்றே சனங்கள் எல்லோரும் போற்றிநிற்க அன்று பெருமாள் அகமகிழ்ந்து கொண்டாடி நாம்நினைத் ததுபோலே நடந்து காரியந்தான் தாம்நினைத் ததுபோலே தாநடந்து வீரெனவே உத்தரவு சொல்ல உடைய வழிச்சான்றோர் சித்தருட அருளால் சென்றார் தவமதுக்கே தவமது செய்ய வென்று சடுதியிற் சான்றோ ரெல்லாம் உகமது அளந்தோன் பாதம் உண்டென மனதி லாக்கி வகைபொரு ளாசை யற்று மனைவிகள் மக்கள் கூட திகையது நோக்கி வாரி துவரயம் பதியிற் சேர்ந்தார் மண்ணுடன் மனைக ளாசை மாடுடன் வீடு மாசை பெண்ணுடன் பொருளி னாசை பூதலப் பொன்னி னாசை எண்ணுடன் எழுத்தி னாசை இடறுபொல் லாசை வேசை ஒண்ணுடன் ஆசை நீக்கி உடையவ னாசை கொண்டார் கொண்டுநல் மனதிற் பூண்டு குருபரா தஞ்ச மென்று பண்டுநல் துவார கையின் பதிவாட வாசல் தன்னில் தெண்டிரை வாவை சூழ்ந்த செகற்கரை தனிலே வந்து கண்டுநற் பதியில் புக்கி கருத்துடன் இருந்தா ரன்றே இருந்தவர் தலமும் பார்த்து ஏகநல் வெளியுங் கண்டு  பொருந்திடும் ஞான வான்கள் புண்ணிய மனதி லெண்ணி வருந்திடக் கனிவே காணும் வாவைநற் பதியி தாகும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(399,'துவையல் பந்தி-வாகைப்பதி தவசு','தெரிந்திடக் கடலில் மூழ்கிச் சிவகலை யணிந்தார் தாமே அணிந்தவர் பெண்ணு மாணும் அன்புடன் மகிழ்ச்சை கூர்ந்து துணிந்தவர் தங்கள் தங்கள் துயரங்க ளறவே நீக்கப் பணிந்தவர் நாதன் தன்னைப் பரிவுடன் மனதுள் ளாக்கித் தணிந்தவர் கோப வேகத் தகுளியைத் தள்ளி வாழ்ந்தார் வாழ்ந்தே தவசு வாகைப் பதியதிலே தாழ்ந்தே சனங்கள் சந்தோச மாயிருக்க அந்திசந்தி யுச்சி ஆகமூ ணுநேரம் நந்தி யருளால் நல்ல துணிதுவைத்து உவரிநீ ரில்துவைத்து உவரிநீ ரைக்குடித்து உவரிநீர் தன்னில் உற்றன்ன மேசமைத்து உச்சி யொருநேரம் உணவு மிகஅருந்தி மெச்சிப் புகழ்ந்த விடிய வொருசாமம் உகப்பாட்டு மோதி உற்ற அபயமிட்டு வகையாய் விடிய வரும்நா ழிகையேழில் துணிகள் துவைத்துத் தோய மதிலிறங்கிக் கெணியாய்க் குளித்துக் கிருஷ்ணர் பதம்போற்றி இப்படியே நித்தம் இவர்மறவா வண்ணமேதான்  அப்படியே வாரி அலைவாய்க் கரையிருக்க வாரிப் புறமாய் வளர்காற்று வாடைமிக நீரிறைத்தாற் போலே நித்தமந் தத்தவத்தோர் பேரி லிறைத்துப் பெருவாடை யாய்வீசச் சீருகந்த நாதன் செயலா மெனஇருந்தார் இருந்தார் தவசு ஏற்றரிய சான்றோர்கள் அருந்தாமல் மற்றொன்றும் அன்னமொரு நேரமுமாய் வாரிநீ ரல்லால் மறுநீ ரறியாமல் சீரியல்பாய்ச் சான்றோர் செய்தார் தவமதுவே பவமா னதுநீக்கும் பச்சைநா ராயணரும் முன்னாறு வருசம் உவந்திருந்த நற்றவத்தைப் பின்னொரு வருசம் பேணி நடத்துமுன்னே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(400,'துவையல் பந்தி-வாகைப்பதி தவசு','நீச னிடையில் நிறடுசெய் தானதினால் தோச மவன்பேரில் சுமக்கச்சா பம்புரிந்து ஆறு வருசம் அதிற்குறைவு வராமல் வாறு தவசு வகுத்து முடிக்கவென்று புரிந்தார் தவசு இலக்குத் திகைவதுமுன் சான்றோர் தவத்தைத் தான்பார்க்க வேணுமென்று ஆன்றோர் மனதில் அன்புமிகக் கொண்டாடி  அலைவாய்க் கரையில் அமர்ந்திருந்த சான்றோரை நிலைபார்க்க வேணுமென்று நிச்சயித் தெம்பெருமாள் உகத்துக்குத் தக்க உபாயமினிச் செய்யவென்று மகத்துவ நாதன் மனதில்மிக வுத்தரித்துத் தெள்ளு தனக்குச் சிணமே விடைகொடுத்தார் விள்ளுறவே செய்து விடுநீ சிணமெனவே ஆதி விடையருள அதுவெல்லா மேசமைந்து மோதி யொருமித்து உற்றஅந்தத் தெள்ளினங்கள் வந்து வளைந்துதல்லோ வாய்த்ததவத் தோரருகில் பொந்து பொந்துள்ளும் போடுந் துணியதுள்ளும் வைத்தக் குடிலுள்ளும் வளைந்ததுகாண் தெள்ளினங்கள் மெய்த்தபுகழ்ச் சான்றோர் மெலிவாய் மிகச்சடைத்து மாடா டுகோழி வளர்ப்பில்லா நம்மிடத்தில் ஏடா விடமல்லோ இதுசெய்யு மாய்மாலம் இருக்கு மிடத்தில் எண்ணக்கூ டாதபடிப் பொருக்குப் பொருக்கெனவே பொன்றக் கடிக்குதென்ன மூணுநேரந் துவைக்கும் உற்றகலை யானதிலே கோணியலில் பற்றும் குறுந்தெள்ளு வந்ததென்ன படுக்க இருக்கப் பண்புசற்று மில்லாமல் முடுக்கமாய்த் தெள்ளு முடுக்கிமுடுக்கிக் கடிக்க  இதல்லால் சான்றோர்க்கு இன்னம்விட்டுப் பார்ப்போமென சதமில்லா மூட்டை தன்னினங்க ளைவருத்தித் தவத்தைக் குலைத்துச் சான்றோரைத் தான்விரட்டிப்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(401,'துவையல் பந்தி-வாகைப்பதி தவசு','பவத்துமாய் நீங்கள் பாவிப்பீ ரென்றுசொல்லி மூட்டைக் குரைகள் மொழிந்தார் முகுந்தனுமே சேட்டைசெய் வோமெனவே திரண்டு மிகச்சூழ்ந்து வந்து வளைந்தார் வாழரியச் சான்றோரை முந்து பரிந்த மூண்டதெள் ளினங்ளோடு கூடிக் குலாவிக் கூட்டமிட் டேதிரளாய்த் தேடிச் சான்றோரைச் செகலதுக்குள் ளேவிரட்ட வேணு மெனவே விசும்புகொண் டவ்வினங்கள் பூணு முடைமை போர்த்துந்துணி யிலிருந்து  நடையிற் கடிக்கும் கிடக்கவிடா தேயரிக்கும் குடிக்கின்ற வெள்ளமதில் கும்பல்கும்ப லாய்க்கிடக்கும் முடிக்குந் தலையில் முச்சிறங்கை போல்கிடக்கும் அன்னத்தி லேமிதக்கும் அண்டைக்கல்லி லேவாழும் முன்னெற்றி மயிரில் மிதந்துமிக முன்னுதிரும் இடைக்குள் ளிருந்து இடைவிடா தேயரிக்கும் உடையி லிருந்து ஓயாம லேயரிக்கும்  மேலெல்லா மிதந்து மிகச்சரியும் கீழ்வழியாய்க் காலெல்லாந் தெள்ளு கனமாய் மிகஅரிக்கும் மாறி யிவரெடுக்க மனதுசற்று மில்லாமல் ஆறிப் பதறி அசையாமல் தாமிருப்பார் வாரிக் கரையில் வளர்நண்டு சேகரமாய் மாரித் துளிபோல் வந்து வளைந்துமிகப் பாண்டத்துக் குள்ளே பதிந்திருந் தேவாழும் காண்டமாய்க் கண்டு கைநீட்ட லாகாதெனத் தடவி யெடுத்துத் தன்னாலே போநீயென வடவிப் போகாமல் வளர்ப்பார்கள் போலிருப்பார் இப்படியே தெள்ளு ஈமூட்டை நண்டினங்கள் அப்படியே கூடி அவரலைச்சல் செய்திடவே அல்லாமல் சான்றோர் அவரவர்க்குத் தேகமதில் பொல்லாத வங்குப் பெரிய சிரங்குடனே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(402,'துவையல் பந்தி-வாகைப்பதி தவசு','வெகுவாய்ப் பெருத்து மேலிலிடை காணாமல் தகுவாய்ச் சடைத்துச் சனங்கள்மிக எல்லோரும்  எங்கே யினிப்போவோம் எல்லோரு மென்றுசொல்லி சங்கை யுடன்கூடிச் சமுத்திரக் கரையருகில் போயிருக்கும் வேளை பொங்குகடல் கோபமதாய் வாயிதமாய்த் திரைதான் வந்துகோபித் தடித்து  பாலதியப் பெண்கள் பண்பா யுடுத்திருந்த  சீலைரண்டு தன்னைச் செகற்கொண்டு போயினதே அய்யோ கடல்தான் அங்குசெல்லக் கூடுதில்லை செய்ய விலகவென்றால் தெள்ளுநம்மை விட்டுதில்லை வீட்டுக்குள் செல்ல விட்டுதில்லை மூட்டையது ஊட்டுகின்ற அன்னமதில் உயரவந்து நண்டிருக்கும் தேகமதிற் சிரங்கு சொறிச்சல் பொறுக்குதில்லை தாகத்துக் கேற்ற தண்ணீர் கிடைக்குதில்லை பாகமுட னுண்ணப் பற்றுதில்லை தீனதுவும் இனியெங்கே போவோம் எல்லோரு மென்றுசொல்லி மனுப்புகழுஞ் சான்றோர் மாதமொரு ஆறாய் இருந்தா ரதிலே ஈசன் செயலெனவே திருந்தார மார்பன் திரும்ப வொருதலத்தில் கொண்டுபோய்ப் பார்ப்போம் குலதெய்வச் சான்றோரை பண்டு வொருசணான் படுத்திருக்கு மவ்வேளை சொர்ப்பனம்போல் உற்பனமாய் சுவாமி மிகவுரைத்தார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(403,'முட்டப்பதி தவசு','இதின்நேர் தெற்கு இருக்கு மொருபதிதான் அதின்நோக்குஞ் சொல்ல ஆராலு மேலாது துட்டர் தமைவென்று சுற்றுமதில் கோட்டையிட்டு  அலங்கார நற்பதியின் அழகுசொல்லக் கூடாது வலங்கார மான வாய்த்த கடலதினுள் தேரு பொன்பதிகள் சிங்கார மேடைகளும் நீருக்குள் ளேயிருக்கும் நிறங்கள்சொல்லக் கூடாது கன்னிமா ராடும் கரியநல்லப் பூஞ்சுனைகள் பொன்னினா லேபடிகள் பூஞ்சப்பிரக் கொலுவும் மாணிக்கக் கல்லால் வளர்ந்தமணி மண்டபமும் ஆணிப்பொன் தன்னால் அழகுபூம் பந்தல்களும் வகையின்ன தென்று வகுக்க முடியாது தொகையின் தென்று தொகுக்க முடியாது இப்பதியில் நீங்கள் இந்தநே ரம்போனால் அப்பதியி லுங்களுக்கு அதிகப்பலன் கிட்டுமென்று தூக்கமதில் சொர்ப்பனம்போல் சொன்னாரே வுத்தரவு ஆக்க முடனே அவனெழுந்து சொல்லிடவே கேட்டு எல்லோரும் கெட்டிதா னென்றுசொல்லி ஆட்டு மெனவே எல்லோருஞ் சம்மதித்த நடக்கத் துணிந்தார் நாரண ருண்டெனவே அடக்க முடனிதிலே ஆறுமா சம்வரைக்கும் இருந்தோமே நன்றாய் இனிநடப்போ மப்பதியில்  வருந்தாமற் போவோம் மகாபரனா ருண்டெனவே போவோ மெனவே பெண்ணா ணுடனெழுந்து தாவமுள்ள முட்டப் பதிதலத்தில் வந்தனரே வந்தனர் பதியின் கூறும் வாரியின் செயலுங் கண்டு சந்தன வாரி போலும் தலமிது நன்ற தாகும் இந்தநற் பதியில் நாமள் இருந்துதான் தவசு செய்தால்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(404,'முட்டப்பதி தவசு','செந்தமி ழாயன் பாதம் செயல்பெற வாழ்வோ மென்றார் வாழ்வ திற்குறை வராது மக்களுங் கிளைக ளோடு தாழ்வ தில்லை வண்ணம் தவமது வளர்வ தாகும் நாள்வழிப் பலனுண் டாகும் நாரண ரருளி னாலே ஆள்வது திடனா மென்று அதிலவ ரிருந்தா ரன்றே என்றே மிகமகிழ்ந்து எல்லோரு மோர்முகமாய் அன்றே மிகமகிழ்ந்து அதிலிருந்தா ரம்மானை சூழ அணியாய்ச் சுற்று மதில்போலே நீள அரங்குவைத்து நெருங்கப் புரைகள்வைத்து வைத்தோர் திசையில் வாசலொன் றாகவிட்டு மெய்த்தே தினமும் மிகவே துணிதுவைத்து அன்ன மதற்கு அவித்தநெல் லேயுடைத்து  வன்னமுள்ள காய்கனிகள் வகைவகையாய்த் தான்வகுத்து நல்லநீர் தன்னில் நாடி மிகத்துவைத்து  எல்லா வகைக்கும் இசைந்தநீ ரேயிருந்தி அந்திசந்தி உச்சி ஆதிதனைத் துதித்துத்  தந்திரம தாகத் தவசு மிகப்புரிந்தார் கண்டிருந்த மற்ற கலிநீசச் சாதியெல்லாம் பண்டி லமைத்ததுவோ பனையேறுஞ் சாணார்க்கு மூணுநே ரந்துவைத்து உச்சியொரு நேரமதாய் வேணும் பகுத்தாய் வெகுவா யலங்கரிக்க வேத னவர்க்கு விதித்த விதிப்படியோ நாதனவர் பங்கில் நாடிமிக வந்ததுவோ எப்போமீ னுண்டு என்றே மிகப்பார்த்து அப்போ ததைவேண்டி அரக்குரக் காயவித்து வாயிலெடுத் திட்டு வயிறுவளர்க்குஞ் சாணார்கள் புகையிலையு மறந்து பெண்ணாணு மோர்மனதாய் இந்தப் படியாய் இருக்க இவர்களுக்கு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(405,'முட்டப்பதி தவசு','முந்தை விதித்த விதிவந் தொத்ததுவோ அவர்க ளுடுக்கும் ஆடையழுக் கில்லாமல் இவருடுக்கச் சற்றும் எண்ணமொன் றில்லாதார் மூணுநே ரந்துவைக்கும் உற்றதுணி பொன்னிறம்போல் தோணுதலா யவர்கள் தேகம் பழபழென இந்த விதமாய் இவர்கள்பா வித்திடவே  சொந்தத் திருமால் சேர்ந்தா ரவரிடமே ஆதி மகாமால் அவர்பங்கி லில்லாட்டால் ஓதி யுணவருந்த ஒன்றுந்தெரி யாதிவர்க்கு பேற்றி நம்பூரி பிராமணர்கள் தஞ்சீலை மாற்றித் துவைப்பு மங்கலாய்க் காணுதுகாண் இந்தசா ணார்சீலை எரிசூரி யன்போலே பந்த வொளிபோலே பழபழெனத் தோணுதுகாண் பிராமணர் சூத்திரர்கள் பேணி யிவர்தமக்கு நிராதனமாய்க் காய்கனிகள் நித்தஞ் சுமக்கிறார்கள் பாக்கியங்கள் வந்துதென்று பனையேறுஞ் சாணார்க்கு நோக்கமது வந்துதென்று நீணலத் துள்ளோர்கள் எச்சாதியும் புகழ்ந்து இருந்தார்கா ணம்மானை அச்சாதி யெல்லாம் அப்படியே சொல்லிமிகச் சாதிமற்றோ ரெல்லாம் தலைகவிழ்ந் திருக்கையிலே நாதித் திருமால் நாடுகின்ற சான்றோரை இவர்க்கருளு மாண்டு இலக்குத் திகைந்துதல்லோ அவரவர்கள் வீட்டில் அனுப்பிவிட வேணுமென்று தோணித்தா ரவர்மனதில் சொர்ப்பனம்போ லெம்பெருமாள் மாணிக்கச் சான்றோர் மனமறியத் தாம்பார்த்து இனிநாம ளிப்போ இருந்த தலமும்விட்டுத்  தனியானோம் நாமளங்கே சார்ந்தோமெ யானாக்கால் நம்முடைய சாதி நகைத்திழிவு பேசுமல்லோ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(406,'முட்டப்பதி தவசு','எம்முடைய தவமும் இல்லையென்று போயிடுங்காண் முன்னிருந்த வீடும் மூண்டிருந்த பண்டமுதல் தன்னிருந்த சொத்தும் தலத்தை யிழந்தவந்தோம் இனியங்கே போனால் இருக்க இடமுமில்லை இனியங்கே போவதைக்கால் இந்தக் கடலதிலே விழுந்தோமே யானால் மேலும் பதவியுண்டு அழுந்த லுடன்நினைத்து எல்லோரு மேயிருந்தார் அப்போ அரிநாதன் ஆகட்டென வுறுக்கி இப்போநீர் போவும் என்றே விடைகொடுத்தும் இன்னமிவர் போகலையே என்றே மனதிலுற்று அன்ன மøக்குறைத்து ஆழாக் கரிசியிட்டார் ஆழாக் கரிசி அலர்மலர்ந்த கோவையுடன் தாழாமல் சனங்கள் சடையாம லேற்றனரே துவையல் முடங்கவில்லை தொழுதுநிதஞ் சேவிப்பதுவும் உகப்பாட் டோதுவதும் ஓயாமல் செய்தனரே இன்னம்போ னாரில்லையே இப்படியே செய்திடினும் இன்ன மொருவசனம் இப்போது செய்யவென்று உன்னித் திருமால் உற்றவை சூரியைத்தான்  ஏவினார் சான்றோர் ஏந்திழைக்கும் பிள்ளைகட்கும் மேவியே சான்றோர் மிகமெலிந்து தான்வாடிப் பற்றி யிருந்தப் படுசிரங்கு மாறலையே முற்றுங் கண்ணொளிவு முழுதுந் திறக்கலையே கும்பிக் குளிரக் குடிக்கக் கிடைக்கலையே வெம்பு மிலைகனிகள் மிகப்பறிக்கக் காணலையே ஐயோநா மெல்லாம் அலைந்து மலைந்திருக்க வையமதி லில்லாத வைசூரி வளைந்துதல்லோ என்று சனங்கள் எண்ணம் பெரிதாகிச் சென்ற சனத்தில் சிலபேர் மடிந்திடவே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(407,'முட்டப்பதி தவசு','பதறி நடுங்கி பரனேநீ தஞ்சமென்று குதறி யுலகில் கூச்ச மிகவடைந்து எங்கே யிருந்தாலும் ஈசன் செயலெனவே சங்கை வழுகாமல் தாமோ தரனுடைய நினைவு மயராமல் நிலைதவறிப் போகாமல் தன்னுடைய வீட்டில் தான்போவோம் நாமளென்று போகு முன்னாகப் பெரியோன் பதமணுகிப் பாகுரைத்து நாமள் பட்டசள முரைத்து உத்தரவு வேண்டி உற்றிடத்தில் போவோமெனப் புத்தி யுடனே பெரியோ னடிசேர்ந்தார்  சேர்ந்துவா கைப்பதியின் செய்தியெல்லா முரைத்துப் பேர்ந்துவந்து முட்டப் பதியின் பெருமைசொல்லி ஊணுகிடை யாததுவும் உயிரழிவு வந்ததுவும் கோணுத லெல்லாங் கூறி விடைகேட்டார் அப்போது நாதன் எல்லோரை யும்பார்த்து இப்போது உங்கள் இல்லிடத்தி லேபோனால் நாரா யணக்குருவை நாளு மறவாமல் பேராக வேயிருந்தால் பேறுங்களுக் கேகிடைக்கும் அன்ன மளக்கும் ஆதித் திருநெடுமால் முன்னவன் தன்பேரால் முத்திரிக ளிட்டதினால் சென்ற இடமெல்லாம் சிறப்பதிக முங்களுக்கு என்றைக்கும் நல்ல இயல்பே யருள்வோமென நல்ல விடைகொடுத்து நாரா யணரனுப்ப வல்ல வளர்சான்றோர் வாழ்த்தி யவர்போனார் அவரவர்கள் வீட்டில் அன்பாகப் போனவுடன் இவரிவர்க்கு நல்ல இயல்புசெய்ய வேணுமென்று ஏகபர நாதன் எம்பெருமா ளானவரும் லோகமதி லெம்பெருமாள் ஒருசொரூபங் கொள்ளலுற்றார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(408,'முட்டப்பதி தவசு','இரப்ப வடிவாய் எடுத்தா ரொருவேசம் பரப்ப வடிவைப் பாருள்ளோர் கண்டுகண்டு  கோட்டைக் கணக்காய்க் குளிர்ந்தநெல் லேயெடுத்துச் சாட்டமுடன் காய்கனிகள் சகல வகையுடனே பழங்கள் முதலாய்ப் பப்படமும் பாயசமும் வளங்க இலையிட்டு வற்றல்வகை சீனியுடன் அன்ன முதல்தானம் ஆடையொடு பொன்தானம் சொர்ணத் தானமுதலாய்த் துவையல்கா ரர்ககெனவே சான்றோ ரினத்தில் சார்ந்தன்பு கொண்டோர்கள் ஆண்டோ னருளால் அன்னவஸ்த்திரங் கொடுத்தார் நித்தம்நித்த மன்னம் நீங்களுண்ணு மென்றுசொல்லிச் சித்த மிரக்கமதால் துவையற்கா ரர்க்கெனவே இடைவிடா தபடியே ஈந்தாரே அன்னமது வடவெல்லாந் தீர்ந்து மகிழ்ந்திருந்தார் துவைத்திருந்தோர் நாலு திசையதிலும் நடந்துபிச்சை தான்குடித்துப் பாலு பவிசுடனே பாவித்தி ருந்தனராம் இப்படியே சான்றோர் இனமா யிருந்தவர்கள் அப்படியே பிச்சையிட்டு அன்பாய் மகிழ்ந்திருந்தார் பாங்கா னதுவையல் பண்டா ரங்களெல்லாம் பூங்கார மாகப் பிச்சையமு துண்டிருந்தார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(409,'அய்யாவை முட்டப்பதிக்கு அழைத்தல்','இந்தப் படியே இவர் வாழும் நாளையிலே சொந்தத் திருமால்க்கு சுவாமி யுரைத்தபடி ஆண்டாறு சென்று அதிகத் தவமதுதான் கூண்டாய் முடித்துக் குருநாதக் கண்மணியும் இனிநாம் செய்யும் இயல்பென்ன என்றுசொல்லி மனிதவ தாரன் மனதில் நினைத்திருக்க நன்றான முட்டப் பதிதலத்தில் நாரணரும் மன்றாடும் நல்ல வாய்த்தபர மேசுரரும் வேதப் பிரமன் விளங்கு மறையோனும் நாத ரிஷிமாரும் நல்லசங்கத் தோர்களுமாய்ச் சத்தி யுமையும் சரசு பதிமாதும் வித்தை முகத்து வேழ முகத்தோனும் எல்லோரும் வந்து இருந்தார் கடலருகே அல்லோரும் வந்து அங்கே யிருந்துகொண்டு ஏற்ற முனியில் இரண்டுபே ரைவருத்திச் சித்த முடனே திடீரெனவே நீங்கள்சென்று நம்முடைய பாலன் நல்லவை குண்டரையும் எம்முடைய அருகில் இப்போ கொடுவரவே சொல்லி யயைச்சார் சுவாமிதா னென்றுசொல்லி நல்லியல்பாய்ச் சொல்லி நாரணனை யிங்கழையும்  வாருங்கோ முனியே வாய்த்த தவத்தோரே சேருங்க ளங்கே திடீரெனவே யிப்போது என்று விடைகள் இருவருக் குங்கொடுக்க அன்று முனிகள் அவ்வாயு போல்விரைவாய்க் கடிதாய் நடந்து கரியமால் நற்றவசு முடிவாகச் செய்த முகுந்தன் பதியில்வந்தார் வந்தனர் வைந்தர் பாத மலரிணை முனிமார் கண்டு செந்தமிழ் ஆயன் பெற்றச் சேயிது கண்ணோ விண்ணோ கந்தரக் கலியை வென்றுக் கனதர்மப் புவியை யாள முந்தநல் தவங்கள் செய்து முடித்தவா பேற்றிப் பேற்றி பொல்லாக் கலியில் வந்திருந்து பெண்ணோ டவனிப் பொன்னாசை');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(410,'அய்யாவை முட்டப்பதிக்கு அழைத்தல்','மண்ணோ டுண்ணு மதத்தாசை மாய்கை விழியார் மருட்டாசை பண்ணோர் தவத்துள் ளணுகாமல் பவமே யறுத்துப் பார்வைவிழி கண்ணோ ஞானக் கருவிருத்திக் கண்டாய்ப் பொருளைக் கண்டாயே அய்யா வுமது தவமதுவால் அரம்பை மாத ரன்றுபெற்ற மெய்யாஞ் சான்றோர் கெதிகள்பெற்றார் மேவலோரு மிகப் புகழ்ந்தார் பொய்யாங் கலிய னுயிரிழந்து போனா னரக மீழாமல் அய்யா முட்டப் பதிதனிலே அழைத்தா ருந்த னப்பனென்றார் அழைத்தா ரெனவே சொன்னவுடன் ஆதி முனியை மிகநோக்கிப் பிழைத்தார் போலே மனமகிழ்ந்து பின்னு முனியோ டுரைபகர்வார்  இழைத்த கலியி னுள்ளிருத்தி என்னைக் கலியன் செய்தவலு சழத்த மறிந்து வந்தாரோ சமய மிதுவோ தானுரைப்பீர் நல்ல முனியே நம்மைக் கலியதனுள் செம்மைகெட்ட நீசத் தீபாவிக் கையதிலே கண்ணியிட்டு வைத்துக் கடக்கே நகண்டிருந்த புண்ணியவா னின்று பிழைத்துக் குதித்தாரோ நான்பட்ட பாடு நாடுபதி னாலறியும் ஏன்பட்டா யென்று இன்றுகேட்க வந்தாரோ ஏதோ தெரியலையே என்னுடைய நாயகமே பாதகன்தான் செய்த பாராத்தியங் களெல்லாம் பார்க்கும் படியாய்ப் பாரிக்கி றேனெனவே ஆர்க்க முடனே அந்நீசன் செய்தபடிக் கட்டுங் கயிறும் கையில் விழுந்திடத்தில் முட்டுக்கு மேலே ஊற்ற மிகநினைத்தார் வெடிப்புடங்கு தன்னால் விலாவி லிடித்திடத்தில் துடிப்புடனே ஊற்றம் தோன்றும் படியேவைத்தார் மேலில் பிரம்பால் மிக்க அடித்திடத்தில் சீல முடனே சிணந் தோணவைத்தனரே கன்னமதில் கம்பால் கனக்க அடித்திடத்தில் உன்னித மாக உடன்தோண வைத்தனரே  கூண்ட முடியைக் குலுக்கியசைத் திடத்தில் வேண்டு முடியை மிகக்கழிய வைத்தனரே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(411,'அய்யாவை முட்டப்பதிக்கு அழைத்தல்','இப்படியே நீசன் செய்த இடறதுபோல் அப்படியே தோணவைத்து ஆங்காரங் கொண்டனரே பால்மாட்டை நீசன் பற்றிக்கொண்டே யடைத்து மால்பா லேற்கும் மாதிரியைப் பார்ப்போமென இந்தநாள் வரைக்கும் இருந்ததொன் றானதுக்குள் அந்தநீ சன்தான் அநியாய மாகவேதான் கொண்டு அடைக்க கோவேங் கிரிநாதன் அன்றுமுத லிருபத் தொருநாள துவரைக்கும் கோல மெடுத்துக் குருச்சமைந்த நாரணரும் பாலுமுதல் வேறு பண்டமொன் றேராமல் இருக்குந் தறுவாய் இதுநல்ல தாகுமென அருக்காக நீசன் அவன்செய்த வேதனைபோல் எல்லா முறைபோல் எடுத்தார் சொரூபமது நல்ல முனிமார் நாரணா என்றுநிற்க கைக்குள்ளே நின்ற கரியசீ சன்மாரில் மெய்க்குள் ளறிய விளம்பினா ரெம்பெருமாள்அய்யாவை முட்டப்பதிக்கு அழைத்தல் இந்தப் படியே இவர் வாழும் நாளையிலே சொந்தத் திருமால்க்கு சுவாமி யுரைத்தபடி ஆண்டாறு சென்று அதிகத் தவமதுதான் கூண்டாய் முடித்துக் குருநாதக் கண்மணியும் இனிநாம் செய்யும் இயல்பென்ன என்றுசொல்லி மனிதவ தாரன் மனதில் நினைத்திருக்க நன்றான முட்டப் பதிதலத்தில் நாரணரும் மன்றாடும் நல்ல வாய்த்தபர மேசுரரும் வேதப் பிரமன் விளங்கு மறையோனும் நாத ரிஷிமாரும் நல்லசங்கத் தோர்களுமாய்ச் சத்தி யுமையும் சரசு பதிமாதும் வித்தை முகத்து வேழ முகத்தோனும் எல்லோரும் வந்து இருந்தார் கடலருகே அல்லோரும் வந்து அங்கே யிருந்துகொண்டு ஏற்ற முனியில் இரண்டுபே ரைவருத்திச்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(412,'அய்யாவை முட்டப்பதிக்கு அழைத்தல்','சித்த முடனே திடீரெனவே நீங்கள்சென்று நம்முடைய பாலன் நல்லவை குண்டரையும் எம்முடைய அருகில் இப்போ கொடுவரவே சொல்லி யயைச்சார் சுவாமிதா னென்றுசொல்லி நல்லியல்பாய்ச் சொல்லி நாரணனை யிங்கழையும்  வாருங்கோ முனியே வாய்த்த தவத்தோரே சேருங்க ளங்கே திடீரெனவே யிப்போது என்று விடைகள் இருவருக் குங்கொடுக்க அன்று முனிகள் அவ்வாயு போல்விரைவாய்க் கடிதாய் நடந்து கரியமால் நற்றவசு முடிவாகச் செய்த முகுந்தன் பதியில்வந்தார் வந்தனர் வைந்தர் பாத மலரிணை முனிமார் கண்டு செந்தமிழ் ஆயன் பெற்றச் சேயிது கண்ணோ விண்ணோ கந்தரக் கலியை வென்றுக் கனதர்மப் புவியை யாள முந்தநல் தவங்கள் செய்து முடித்தவா பேற்றிப் பேற்றி பொல்லாக் கலியில் வந்திருந்து பெண்ணோ டவனிப் பொன்னாசை மண்ணோ டுண்ணு மதத்தாசை மாய்கை விழியார் மருட்டாசை பண்ணோர் தவத்துள் ளணுகாமல் பவமே யறுத்துப் பார்வைவிழி கண்ணோ ஞானக் கருவிருத்திக் கண்டாய்ப் பொருளைக் கண்டாயே அய்யா வுமது தவமதுவால் அரம்பை மாத ரன்றுபெற்ற மெய்யாஞ் சான்றோர் கெதிகள்பெற்றார் மேவலோரு மிகப் புகழ்ந்தார் பொய்யாங் கலிய னுயிரிழந்து போனா னரக மீழாமல் அய்யா முட்டப் பதிதனிலே அழைத்தா ருந்த னப்பனென்றார் அழைத்தா ரெனவே சொன்னவுடன் ஆதி முனியை மிகநோக்கிப் பிழைத்தார் போலே மனமகிழ்ந்து பின்னு முனியோ டுரைபகர்வார்  இழைத்த கலியி னுள்ளிருத்தி என்னைக் கலியன் செய்தவலு சழத்த மறிந்து வந்தாரோ சமய மிதுவோ தானுரைப்பீர் நல்ல முனியே நம்மைக் கலியதனுள் செம்மைகெட்ட நீசத் தீபாவிக் கையதிலே கண்ணியிட்டு வைத்துக் கடக்கே நகண்டிருந்த புண்ணியவா னின்று பிழைத்துக் குதித்தாரோ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(413,'அய்யாவை முட்டப்பதிக்கு அழைத்தல்','நான்பட்ட பாடு நாடுபதி னாலறியும் ஏன்பட்டா யென்று இன்றுகேட்க வந்தாரோ ஏதோ தெரியலையே என்னுடைய நாயகமே பாதகன்தான் செய்த பாராத்தியங் களெல்லாம் பார்க்கும் படியாய்ப் பாரிக்கி றேனெனவே ஆர்க்க முடனே அந்நீசன் செய்தபடிக் கட்டுங் கயிறும் கையில் விழுந்திடத்தில் முட்டுக்கு மேலே ஊற்ற மிகநினைத்தார் வெடிப்புடங்கு தன்னால் விலாவி லிடித்திடத்தில் துடிப்புடனே ஊற்றம் தோன்றும் படியேவைத்தார் மேலில் பிரம்பால் மிக்க அடித்திடத்தில் சீல முடனே சிணந் தோணவைத்தனரே கன்னமதில் கம்பால் கனக்க அடித்திடத்தில் உன்னித மாக உடன்தோண வைத்தனரே  கூண்ட முடியைக் குலுக்கியசைத் திடத்தில் வேண்டு முடியை மிகக்கழிய வைத்தனரே இப்படியே நீசன் செய்த இடறதுபோல் அப்படியே தோணவைத்து ஆங்காரங் கொண்டனரே பால்மாட்டை நீசன் பற்றிக்கொண்டே யடைத்து மால்பா லேற்கும் மாதிரியைப் பார்ப்போமென இந்தநாள் வரைக்கும் இருந்ததொன் றானதுக்குள் அந்தநீ சன்தான் அநியாய மாகவேதான் கொண்டு அடைக்க கோவேங் கிரிநாதன் அன்றுமுத லிருபத் தொருநாள துவரைக்கும் கோல மெடுத்துக் குருச்சமைந்த நாரணரும் பாலுமுதல் வேறு பண்டமொன் றேராமல் இருக்குந் தறுவாய் இதுநல்ல தாகுமென அருக்காக நீசன் அவன்செய்த வேதனைபோல் எல்லா முறைபோல் எடுத்தார் சொரூபமது நல்ல முனிமார் நாரணா என்றுநிற்க கைக்குள்ளே நின்ற கரியசீ சன்மாரில் மெய்க்குள் ளறிய விளம்பினா ரெம்பெருமாள்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(414,'அய்யா முட்டப்பதி ஏகல்','இன்றைத் திவசம் இசைந்தமுட் டப்பதியில் நன்றிப்போ போக நல்மணிக ளாகுதெனச்  சொல்ல அறிந்து சீசன்மார் தாமகிழ்ந்து நல்லதெனச் சொல்லி நாடி யவரிருக்க அன்றிரா தன்னில் ஆரு மறியாமல் நன்றினிய சீசன் நாலுபேர் தாமறிய  வந்த முனியும் வளர்ந்த இருமுனியும் சொந்தமுடன் நாதன் தொட்டில் மிகஏறி  சீசன்மார் காவிச் சிணமாய் மிகநடக்க வாசமுள்ள மாமுனிவர் வலமிடமுஞ் சூழ்ந்துவரத் துடியாகத் தொட்டில் சுவாமி மிகஇருந்து வடிவான முட்டப் பதிதலத்தில் வந்தனரே முட்டப் பதியில் முகுந்த னரிநாதன் கிட்ட வருகும் கிருஷ்ணர்வந்து சேர்ந்திடவே மகனை மிகக்கண்டு மாவிருப்பத் தோடிளகி உகமாள வந்த உடையமக னேயுனத சடமெல்லாம் வாடி சடைப்பானே னென்மகனே வடவெல்லா மென்னோடு வகுத்துரைநீ யென்மகனே கண்ணே மணியே கருத்துள்ள நாயகமே விண்ணே வொளியே வேதத் திருவிளக்கே கலியில் மிஇருந்து கலக்க மிகஅடைந்த மெலிவெல்லாஞ் சொல்லி விளம்புநீ யென்மகனே  அடித்ததா ருன்னை அவனிதனில் பேயனென்று கடுத்தமது செய்தக் கலக்கமெல்லாஞ் சொல்லுவென்றார் சொல்லென்று மாலும் சிவமு மெடுத்துரைக்க வல்லபெல மான வைகுண்ட ரேதுரைப்பார் செந்தூர்க் கடலில் செகலதுக்குள் ளென்னைவைத்து முந்த எனக்கு மொழிந்த வுபதேசம் கப்பாம லாறு வருசந் தவசாக மெய்ப்பாவின் பாலருந்தி வீடுசொத் தெண்ணாமல்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(415,'அய்யா முட்டப்பதி ஏகல்','கலிக்காசி யாசை கனாவில் நினையாமல் அலிக்கியானப் பெண்ணின் ஆசை யணுகாமல் பக்கக் கிளையாசை பாவித் தினிதாசை அக்கத்தி னாசை அனுப்போல் நினையாமல் நல்ல துணியாசை நளின மொழியாசை சொல்லினிய ஆசை தீன்பண்டத் தினாசை தேய்ப்புக் குளிப்புச் சிறந்த பொருளாசை தாற்பரிய மானச் சந்தோசத் தினாசை பூமியா சைமுதலாய்ப் பேராசை யும்வெறுத்துச் சாமி யுமதருளால் தொல்புவியி லுள்ளோர்க்குத் தண்ணீரா லெத்த சர்வ வியாதிமுதல் மண்ணிலுள்ளோர் யார்க்கும் வாய்த்ததர்ம மாகவேதான்  நோய்தீர்த்து வைத்ததல்லால் நேட்டமொரு காசறியேன் வாழ்வில்லாப் பேர்க்கு வாழ்வு மிகக்கொடுத்தேன் தாழ்வடைந் தோர்க்குத் தாழ்வை விலக்கிவைத்தேன் பிள்ளை யில்லார்க்குப் பிள்ளை மிகக்கொடுத்தேன் கள்ளமெல்லாம் நீக்கிக் கழிவை வரத்தைவைத்தேன் வரம்வேண்டி நம்மிடத்தில் வைத்தந்தப் பேய்களையும் குரமாய் மலையில் கொண்டு எரியவைத்தேன் ஆயிரத் தெட்டு ஆன திருப்பதியில் வாயிதமாய் வாழும் வாய்த்ததே வர்தமக்கு ஞாயமில்லை பூசை நல்லாடு தீபவெலி தேயமதி லுங்களுக்குத் தேரோட்டமு முதலாய் ஏற்கப் படாதினிமேல் இராச்சியத்தி லுள்ளவர்தாம் மார்க்க வைகுண்ட வல்லாத்தான் வந்ததினால் பூசை படைப்புப் பெலிதீப மேராமல் வாச முடனே மறைந்திருங்கோ நீங்களெனச் சட்டமிட்டு வைத்தேன் தரணிபுற் பூண்டுவரை மட்டை மருந்திலையும் மலைகடலும் வாசுகியும் வையகத்தை யாளும் மாநீச னுமறிய');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(416,'அய்யா முட்டப்பதி ஏகல்','மெய்யாய் விரித்து விளம்பிமிகச் சட்டமிட்டேன் இத்தனை சட்டம் இட்டவர்த்த மானமெல்லாம்  மத்திய மாய்வைத்த மாமுனியோ டேகேளும் அல்லாமற் பூமிதனில் அவர்களறி யாதபடி வல்லாமை யாய்க்காசி வழங்குமிலை பாக்குவரை நானாசை கொண்டு நருட்களிடம் வாங்கினதை வானாசை யுள்ள மாமுனியோ டேகேளும் சதுராய் நருளறிய நல்லவெகு அற்புதங்கள்  இதுக்காய் கிணற்றில் ஏகாச்சிலை யுங்காட்டி இருந்தேன் தவசாய் என்னுடைய நாயகமே பொருந்தாத காரியங்கள் புத்திக் குளறிமிகச் செய்துமிகக் காணாது செய்ய மணிவிளக்கே நிசமிதுவோ என்று நீரவரோ டேகேளும் இத்தனையுந் தர்மமதாய் யான்செய் திருக்கையிலே புத்திகெட்ட நீசன் பொல்லாதான் வந்தெனையும் கட்டி யடித்துக் கைவெடி யாலிடித்து இட்டிறுக்கி வைத்தான் இரும்பு விலங்கதிலே நஞ்சிட்டுத் தந்தான் நாடுஞ்சா ராயமதில் கொஞ்சுங் கடுவாய்க் கூட்டி லடைக்கவென்றான் நீச னவன்சாதி நிசமாகத் தான்கூடி ஏசி யெனைப்பழித்த இடறுசொல்லக் கூடாது ஐயோ அவர்கள் அடித்த அடிகளெல்லாம்  வையம் பொறுக்காதே மாநீசன் செய்தவினை மாந்திர தந்திரத்தால் வஞ்சனைகள் செய்தெனையும் கோந்திர மாகக் கொல்லவகை செய்தார்கள் பாவிகள் செய்த பலவினைகள் சொல்லவென்றால் தாவு முலகில் தாலமதி லோலையில்லை என்னைத் தவத்துக்(கு) இருத்தியிது நாள்வரையும் இன்ன மிரங்கலையோ என்தவசு காணலையோ அன்றருளித் தந்த ஆண்டு திகையலையோ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(417,'அய்யா முட்டப்பதி ஏகல்','இன்று முதற்கலியில் யான்போக வில்லையையா நம்மாற் கலியில் நனின்றிருக்கக் கூடாது  சும்மா எனைநீங்கள் சோலிபண்ணக் கூடாது நன்றிசற்று மில்லா நாற வெறுங்கலியில் கொண்டென்னை வைத்துக் கோலமது பாராதேயும் இனியென்னாற் கூடாது இக்கலியி லேயிருக்கப் பனிதவழு மாயவரே பகர்ந்தமொழி மாறாமல் ஆறு வருசம் அவனித் தவசிருந்தேன் தாறுமா றுண்டானால் சாட்சியோ டேகேளும் என்று வைகுண்டர் இத்தனையுஞ் சொல்லிடவே நன்றினிய மகனை நல்லமுகத் தோடணைத்து கண்ணே மணியே கருவூலமே கனக மணியே ரத்தினமே  விண்ணே வொளியே கற்பகமே வேதச் சுடரே விளக்கொளியே மண்ணே ழளந்த மலர்பதத்தில் வந்தே குதித்த மலர்க்கொழுந்தே இண்ணே முதலா யுனக்குநல்ல இயல்பே யாகு தென்மகனே மகனே தவத்துள் ளிருக்கையிலே வருமோ செல்வ மாரார்க்கும் செகமே ழறியத் தவமுடித்துச் சென்றால் சிவனுக் ககமகிழ்ந்து தவமே முடித்த நினைவதுபோல் சகல கருமங் கைகூடும் அகமே யுனக்கு அருளினது அனுப்போல் தவறா தருள்மகனே செல்ல மகனே சீமானே நீகேளு நல்ல மகனே நாரணா நீகேளு தவவேடம் பூண்டு தவமா யிருக்கையிலே சிவஞான மல்லால் செல்வம்போல் காணாது தவசு நிறைவேறித் தமோதரர்க் கேற்று பவிசு மிகவேண்டிப் பலன்பெற்று வாழ்வார்கள் மகனே நீயிருந்த மகாதவத் தின்பெருமை செகமீதே சொல்லத் தொலையுமோ என்மகனே கள்ள யுகத்தைக் காணாம லேயறுத்து உள்ள நடுஞாயம் உகத்தீர்ப்புச் செய்துமிக ஆகாத்த தெல்லாம் அழித்து நரகிலிட்டு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(418,'அய்யா முட்டப்பதி ஏகல்','வாகாய்க் குழிமூடி வாசல் தனைப்பூட்டி மாறிய தில்லாமல் வங்கிஷமுந் தானொழித்து  வேறுவகை செய்து மேலுமொரு நல்லுகமாய்த் தரும யுகமாய்த் தாரணிக்க மாயருளிப் பொறுமை மனுவாய்ப் பெரியசான் றோர்வழியை அன்பாக வைத்து ஆள முடிசூடிப் பண்பாக வேண்டும் பவிசு மிகவாகச் சாகா வுலகத் தர்ம பதியாள வாகாய்த் தவசு வகுத்து முடிக்கையிலே  ஆங்கார மாமோ ஆணுவங்க ளங்காமோ ஓங்கார மாமோ உற்ற தவசதுக்கு தவசு நிறைவேறி தான்வந்த தாலேயினி பவிசு வுனக்குப் பகருகிறேன் கேளெனவே அருகில்வைத்த மாமுனியை அழைத்தார் திருமாலும் துரிதமுடன் மாமுனிகள் சுவாமி யெனத்தொழுதார் இந்தநாள் வரைக்கும் இவரிருந்த நற்றவசு  சந்தமோ வீணோ தாமுரைப்பீர் மாமுனியே  அப்போ முனிகள் ஆதி யடிபணிந்து தப்பெருநா ளில்லை தவத்துக் குறுதியுண்டு நூலொழுக்க மல்லாமல் நுண்ணிமைகள் தப்பவில்லை மாலொழுக்க மல்லாமல் மனதுவே றில்லையையா தப்பாத தவத்தில் சண்டித் தடிநீசன்  சிப்பாயி விட்டுச் சுவாமி தனைப்பிடித்து முடித்தலையைப் பின்னி முழுநீசப் பாவியவன்  அடித்து விலங்கில் அவன்கொண்டு வைத்தனனே வைத்துப்பல வேதனைகள் மாநீசன் செய்ததல்லால் மெய்த்தபுகழ் மாலே இவர்மேலே குற்றமில்லை என்று முனிமார் இசையத் திருமாலும் கொண்டுபோய் நீசன் கோட்டிசெய்த ஞாயமெல்லாமல்  இருக்கட்டு மந்த இழிநீசன் செய்ததெல்லாம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(419,'அய்யா முட்டப்பதி ஏகல்','குருக்கட்டு முறையின் குணத்தைமிகச் சொல்லுமென்றார் உடனே முனிகள் உள்ள மிகமகிழ்ந்து படபடென நின்று பணிந்து மிகப்போற்றித் தப்பில்லை யையா தவத்தி லொருபங்கமில்லை என்று முனிகள் இகபரத்துக் குத்தமமாய் அன்று உரைக்க அதகத் திருநெடுமால்  நல்ல தெனவே நன்மகனைத் தானாவி செல்ல மகனே செகலத்துகுள் வாநீயென மகனை மிகக்கூட்டி வாரிக்குள் ளேநடக்கத் தவமுனிவ ரெல்லாம் சங்கீதம் பாடிவர  பாவாணர் பாட பரமுனிவர் தாம்பாட  நாவாணர் போற்ற நாரா யணர்மகனை பாற்கடலி லுள்ள பாலமிர்தந் தான்வருத்தி ஆர்க்க முடனே அருமைமக னைமூழ்க்கி அழுக்கைத் துடைத்து அஞ்சாட்சர மருளி முழுக்காட்டி மகனை உகந்தமுகத் தோடணைத்து வேதப் புரோகி விளங்கி மிகவாழ்த்த நீதம் நிறைந்த நீலநிற மண்டபத்தில் தங்க மணியரங்கில் சமையக் கொலுவதிலே மங்காத ஈசுரரும் மாதவனுந் தேவர்களும் சத்தி யுமையும் சரசு பதிமாதும் முத்தியுள்ள தேவர் முனிமார் ரிஷிமாரும் எல்லோரும் நன்றாய் இறைஞ்சி முறையுமிட நால்லோர்க் ளெல்லாம் நாடி மிகஇருக்க');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(420,'முட்டப்பதி விஞ்சை 2','வல்ல திருமால் மகனை முகம்நோக்கி செல்ல மகனே சிறந்ததேதி வந்ததினால் திருநாள் நடத்தித் தெருவீதி தான்வரவும் ஒருவாரந் தன்னில் ஒருநா ளிடைவிடாமல் நித்தந் திருநாள் நீநடத்தி யென்மகனே சத்தகன்னி மாரைத் தான்வருத்தி மாலையிட்டுக் கலியாண மங்களங்கள் காட்சித் திருநாளும்  சலியாமற் காசுத் தந்தவர்க ளுண்டானால் வேண்டிநீ தர்மம் விரைவாய் நடத்தியிரு ஆண்டத் திருநாள் அதுமுடக்கம் வராமல் பிச்சிவெள்ளை பச்சரிசி மிளகிலை தேங்காயும் மிச்சம்பழஞ் சந்தனமும் மிகுபன்னீர் வாடைகளும் மேற்கட்டி கட்டி மிகுவா யலங்கரித்து மாற்கட்டுச் செய்து மகிழ்ச்சை டம்மானமிட்டு இந்தப் படியாய் ஏழுநாளுந் திருநாள் அந்தப் படிசெய்து அவனியி லாடிக்களித்து எல்லா முறையும் இயல்பாகக் கொண்டாடி நல்லாண்டு தேதி நல்லதிவ சம்பார்த்துத் தெய்வகன்னி மாரைத் தெய்வச்சான் றோரிடமே மெய்வரம் பான மனுமுறைபோல் தான்பேசிக் கொடுக்கல்வாங் கல்முறையும் கூறி மிகக்கேட்டுத் துடுக்கான நல்ல தோகைக்குண மும்பார்த்து பார்த்துன் னருகில் பணிவிடைகள் செய்யுகின்ற தோற்றுகின்ற சீசனுடத் தொல்முறை யும்பார்த்து பரிய முறையும் பாவையர்கள் தம்முறையும் சரியா யுலகோர் சங்கை யதுபோலே  தங்க நிறமானத் தர்மத் தலைவாநீ   மங்கள மதாக மாலையிடு என்மகனே என்னோடு கன்னி ஏலமே சொன்னபடி தன்னோ டினங்கள் தலைவன்மா ரேழ்வரையும் எடுத்துக் கொடுத்து இளமுலைப்பால் தானூட்டி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(421,'முட்டப்பதி விஞ்சை 2','கொடுத்து இகனைக் கூறிக்கோ என்மகனே இப்படியே பெண்கள் இன்னுஞ்சில பேர்களுண்டு  அப்படியே அவரவர்க்கு அந்தந்த வேடமிட்டு வகைவகையாய் வேசம் மாறி யுருவெடுத்துத் திகையாம லவர்க்குச் சொல்லு முறைபோலே கலியாணக் காட்சி கணக்குத்தீர் வைமுறையும் வலியானக் கலியில் வாழ்வைமிகக் கொண்டாடிச்  சாம்பசிவக் கோலம் சாருமஞ்ச ணைக்கோலம் ஆம்பக் குறக்கோலம் ஆடிமிகக் கழித்து முற்றுந் திருநாள் முழுது மிகநடத்திப் பத்தும் பெரிய பாலர்தங்கள் வீடேகி விருந்து மிகவும் விரைவாகத் தானருந்தி திருந்து புகழ்கிளைபோல் சிலவீட் டமுதருந்தி பிச்சைபோ லுமருந்தி பெண்வழிச்சோ றுமருந்தி மிச்சமாய்ப் புவியில் வேண்டும் பவிசுடனே வெற்றியி டம்மானம் விருதுக் கொடிகள்கட்டிப்  புத்திர ரானோர்க்குப் பிழைக்கும்வலு புத்திசொல்லிப் பாடித்தீர் வைதீர்த்துப் பரசோ தனைபார்த்து ஆடித்தீர் வைதீர்த்து ஆண்பெண் மிகவேகண்டு நல்லாண்டு கண்டு நாடும் மங்கையரைச் செல்லக் கலியாணம் செய்து சிறப்புடனே செந்தா மரையா ளுடனே சிறப்பிருந்து ஆனை கன்றீணி அதினா லுலகமெல்லாம் தானாக நீயும் தலைவ னெனச்சமைந்து முரணிக் கலியழிய முச்சுடரு மொன்றாவாய்த் தரணி யீரேழும் தடதடெனத் தானசைத்து நீயதின்மே லாளும் நிண்ணயத்தைக் கேள்மகனே தங்க முடிசூடிச் சான்றோர்க்கு நால்வரமும் அங்கவர்க்குத் தானருளி அழகு மிகக்கொடுத்து நல்லதர்ம மாக நாடும் புவியதிலே அல்லல்வினை யற்று அவனியதை நீயாள்வாய்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(422,'முட்டப்பதி விஞ்சை 2','அப்போ நீயரசு ஆளுகின்ற நாளையிலே செப்பொண்ணாச் செல்வம் சிறப்புமிக வுண்டாகும் பூமடந்தை வீட்டில் புகுந்து கொலுவிருந்து தாமுனிந்து நீயும் தரணிதனில் வந்தவுடன்  நான்வந்து உன்னை நாடி மிகஎடுத்துத் தான்மகிழச் செல்வத் தலைவ னுனையாக்கிச் சொல்ல வொண்ணாத சுகந்தருவே னென்மகனே எல்லைக் கணக்கும் ஏழுயுகக் கணக்கும் தீர்த்து உனக்குத் திருவம்ப லமருளித் தோற்றும் பதிகள் துலங்கும்வெகு ரத்தினமாய் எல்லா மருளி ஏழுமாதர் தாஞ்சூழ வல்லோனாய் நீயும் வாழுவா யென்மகனே மலங்காதே யென்மகனே மாதவங்கள் பெற்றவனே கலங்காதே யென்மகனே கண்ணேயென் கற்பகமே என்று மகனுக்கு ஈதுரைக்கு மிவ்வளவும் மன்று குலுங்காது வாரி யலுங்காது மேகங் குடைநிழற்ற மேவலர்கள் போற்றிநிற்க யோக முனிமார் ஓதித் தமிழ்கூற ஈசர் மகிழ்ந்து இரத்தின மதிலிருக்க வாசவனுந் தேவர்களும் வாய்பொற்றித் தாழ்ந்துநிற்க இப்படியே சங்கம் எல்லா மகிழ்ந்துநிற்க அப்படியே விஞ்சை யருளினா ராதியுமே விஞ்சையும் பெற்று வைந்தர் விரைவுடன் தகப்பன் பாதம் அஞ்சையும் புணர்ந்த சோதி அரனையும் வணங்கிப் போற்றி  நெஞ்சையு மொன்றுள் ளாக்கி நிர்மலத் தாயைப் போற்றி கொஞ்சையுங் குணத்தி னாதன் கூறுவார் குருவைப் பார்த்து என்னையாட் கொண்ட நாதா எங்குமாய் நிறைந்த நீதா உன்னையான் கண்டு போற்ற உன்னருள் கிருபை தந்தாய் இன்னமுங் கலியில் போய்நான் இருந்திந்த முறைகள் செய்தால்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(423,'முட்டப்பதி விஞ்சை 2','என்னையாட் கொண்டு ரத்தினக் கிரீட மெப்போ தீவீரோ கிரீட மெப்போ தீவிரென் றெனக் கேட்ட மைந்தா அரிவிரி கொண்டே சூடி அணியிடை மாரைத் தோய்ந்து தரிவிதி யான போதும் தாமரைக் கைநிகழ்ந்த போதும் பரிவலம் வந்த போதும் பார்மக ளடைந்தா யன்றே உன்விதி யதனால் முன்னூல் ஊறிய அமிர்தந் தன்னால் என்விதி தன்னால் வந்த இகபர முனக்குள் ளாகி மன்முறை தெளிந்த தன்றும் மான்கன்று ஈன்ற தன்றும் பொன்மக ளகன்ற தன்றும் பூமக ளடைந்தா யன்றே பூமக ளுன்னைச் சேர்ந்து பின்னாறு வரைக்கு மேலே நானுகந் தன்னாற் போக்கி நகரொரு பகற்குள் ளாக்கித் தானுனைத் தங்க மானத் தயிலமாம் பதத்தில் மூழ்க்கி வானுனை மகிழ ரத்தின மகிழ்கிரீட மருள்வே னென்றார் மகனே யுனக்கு மகாசெல்வ மாகிவரும் சுகமேபோ யிரெனவே சொல்லி யனுப்பலுற்றார்  அனுப்பத் திருமால் அமைமக வேதுரைக்கும் மனுப்புகழப் பெற்ற வைகுண்ட மேதுரைக்கும் பாவி வெறுநீசன் பண்ணிவைத்த பாட்டையெல்லாம் ஆவியறிந் தென்னுடைய அங்கமெல்லாஞ் சோருதையா என்று வைகுண்டர் இசையத் திருமாலும் அன்று மகனுக்கு அருளினது கேள்மாதே இந்நீசன் நமக்கு இந்த யுகம்வரையும் மன்னீதங் கெட்ட மாற்றானாய் வந்ததினால் செய்தா னதினால் திருமகனே யஞ்சாதே மைதான மான வாக்குநமக் காச்சுதெனக் கைவாய்த்து தென்மகனே கலிநீச னையறுக்க மெய்வாய்த்து நம்முடைய மேன்மைக் குலத்தோர்க்கு அன்பான் சீமை அரசாள நாளாச்சு தன்பா லடைய சரியாச்சு தென்மகனே மலங்காதே போநீ வாழுகின்ற நற்பதியில் கலங்காதே போயிருநீ கண்ணே திருமகனே');");


        ///15th Day

        db.execSQL("INSERT INTO akilathirattu2 VALUES(424,'முட்டப்பதியிலிருந்து அய்யா தோப்புப்பதி திரும்புதல்','என்று விடையருள ஏற்ற திருமாலும் அன்றுதிரு மாலும் அன்பாய் விடைவேண்டி அலைவாய்க் கரையில் ஆதி வருகுமுன்னே நிலையான சான்றோர் நிரம்பவந்து கூடினரே  கண்டந்தச் சான்றோரைக் கரியமா லுமகிழ்ந்து நன்றென்று சொல்லி நாராயணர் கூட்டிப் பண்டிருந்த நல்லப் பதியில்வந்து சேர்ந்தனரே கண்டெந்தப் பேரும் கனபிரிய மாய்மகிழ்ந்து தாமோ தரனார் தலத்தில் மிகச்சேர்ந்து நாமோ முனிகூட நாடி மிகஇருந்தார் இருந்து மிகப்பாலும் ஏற்றநல்ல பச்சரிசி திருந்து சிறுமணியும் தேங்காய்ப்பூ தன்னுடனே கொல்ல மிளகு கூண்டப் பொடியுடனே நல்லாக இவ்வகைகள் நாடியொரு நேரமதாய் காவி மிகப்புரிந்து கனத்திருத்தி ராட்சமிட்டுத் தாவமுடன் மாத்திரைக்கோல் தானிதுவோ டேயிருந்தார் கொஞ்சநாள் கழித்துக் கூண்டதிவ சம்பார்த்துப் பஞ்சகரு ணாதிகளைப் பண்பாகத் தானிறுத்திக் கந்தை மிகச்சூடிக் காவித்தொட்டில் மீதிருந்து விந்தை யுடனுகத்து விவரிப்பெல் லாமெடுத்துக் கூறினா ரந்தக் கூண்டரியக் காரணத்தை பேறிருக்கும் நல்லோர் பெரிய பெருமானும் நாடழியப் போறதுவும் நன்னாடு தோன்றுவதும் பாடழிய நீசன் படப்போற செய்தியதும்  நல்ல மனுவோர் நாடி முழிப்பதுவும் வல்லபுவி தர்மம் வாழ்வதுவுஞ் சிறப்பும் சொல்லி விரித்தார் சிறந்ததொட்டில் மீதிருந்து எல்லோருங் கேட்டு இப்போதோர் காரணந்தான் நடத்துகிறா ரென்று நல்லோர்கள் சொல்லிடுவார் சடத்தமுள்ள நீசச் சண்டாளப் பாவியெல்லாம் காசு மிகவேண்டக் கபடுசெய்கி றானெனவே பேசி யிவனும் பிதற்றுகிறா னென்றுசொல்லி நகைப்பார் சிரிப்பார் நன்றிகெட்ட நீசர்குலம் சிக்ப்பா னதுவால் தெய்வகுலச் சான்றோர்கள்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(425,'சான்றோர் பக்தி','கூடிக் குவித்து குணமாக வேகூடித் தேடிவைத்த பண்டம் சுவாமி தனக்கெனவே நம்முடைய சஞ்சலங்கள் நாடும்பிணி தீர்த்தவர்க்குத் தம்முடைய பாலு தாங்கொடுக்க வேணுமென்று பசுவைக் கொடுப்பார் பாலாடு தான்கொடுப்பார் கசுவிரக்க மாகக் காசு பணங்கொடுப்பார் கஸ்தி மிகத்தீர்த்த காயாம்பு வண்ணரென்று வஸ்திரங் கொடுப்பார் வழங்கு மிலைகொடுப்பார் நெல்லு சிறுமணிகள் நெய்த்தேங் காய்கனிகள் நல்லமைப்புச் சான்றோர் நாடி மிகக்கொடுத்தார்  கொடுக்க அன்பரெல்லாம் குணமாய் மிகவேண்டி அடுக்க அவரருகில் அன்பாக நின்வரில் பணிவிடைகள் செய்வோர்க்குப் பரிந்து மிகஈந்தார் மணியாம் பரனும் மகிழ்ந்துபா லேற்றனரே ஏற்று உகத்துக்கு உகத்தீர்ப் பும்புரிந்து சாற்று மொழிகூறித் தானிருக்கு மப்பொழுது மாயத் திருமால் மால்வேச மேயெடுத்துத்  தேயமதில் நம்முடைய தெய்வகுல மாதர்களை வருவிக்க வென்று மாஞால வித்தையினால் உருவிக்க வென்று உலகநரு னானதிலே தாக்கி உடலுள் தமனிய மாய்ப்பொதிந்து நோக்கு முறைகாண்டம் நேர்மை மொழிமொழிந்து பெண்ணாண் வரைக்கும் பெருங்கிழவி தான்வரைக்கும் கண்ணா னமதலை கைக்குழந்தை முதலாய்ப் பாட்டு வித்தேசம் படிப்புவித் தேசமுதல் ஆட்டுவித் தேசம் ஆட்டி மிகவருத்தி முன்னமைத்த பெண்ணை எல்லா மிகப்பார்த்துச் சொன்ன முறைபோல் சிணம்செய்ய லாமெனவே இட்டா ரொருசூட்சம் ஈசர்முத லெல்லோரும் மட்டாரும் சூட்சம் வையகத்தி லேயாவி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(426,'சான்றோர் பக்தி','சாதிபதி னெட்டும் ஆராட்டுப் பார்த்தனரே ஆதிச்சா திதனிலே அமைப்புபோ லுள்ளவர்கள் தானிறைந் தாடித் தண்மை மிகக்கூறி வானிறைச் சோதி மயமாய்க் குதித்தனரே இப்படியே சான்றோரில் ஏற்றபெண் ணாண்வரையும் அப்படியே நன்றாய் ஆராதனை யாகி ஊருக்கூ ரேயிருந்து உற்ற நருட்களெல்லாம் பாருகங் காணப் பண்பாகக் கொண்டாடி உமைபார் வதியெனவும் உற்றமுத் தாரெனவும் இமையோர் புகழும் இலட்சுமி நானெனவும் சத்திகன்னி மாதரென்றும் தார்குழலார் தோழியென்றும் பத்தியுள்ள நல்ல பரமே சொரியெனவும் மண்டைக்காட் டாளெனவும் மாதுநல்லாள் தோழியென்றும் பண்டையுள்ள ஈசொரியாள் பாக்கிய வாட்டியென்றும் வள்ளிதெய் வானை வாய்த்தபக வதியெனவும் பூம டந்தையென்றும் பெரிய பிராட்டியென்றும் பார்ம டந்தையென்றும் பத்திரமா காளியென்றும் நாகக் கன்னியென்றும் நல்லதெய்வக் கன்னியென்றும் பாகைக் கைகாட்டும் பைங்கிளிமார் நாங்களென்றும் இப்படியே நல்ல ஏந்திழைமா ரெல்லோரும்  அப்படியே நல்ல அவனிப்பெண் ணார்பேரில் ஆரா தனையாய் அங்கங்கு தான்கூடி சீராகக் கூடிச் சேர்ந்துவரு வார்தினமே ஆண்பிள்ளை கள்பேரில் ஆனசிவன் மாலெனவும் நான்பிரமா வென்றும் நல்ல அனுமனென்றும் காலனென்றும் ஏமனென்றும் கடியபெலக் கந்தனென்றும் மாலவனின் மக்களென்றும் மாமுனிமார் நாங்களென்றும் தேவ ரிஷியெனவும் சிமிள்கருட சேவனென்றும் தாவமுள்ள நல்ல சன்னாசி மார்களென்றும் ஆரா தனையாய் அவர்கள் மிகஆடிச்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(427,'சான்றோர் பக்தி','சீராகக் காண்டம் செப்பிமிகப் படிப்பார் வருவார் தினமே மாயோனிட மதிலே கருவா யுதித்த காண்டம் மிகப்படிப்பார் இப்படியே பெண்ணாணும் யாம முறைப்படியே அப்படியே வந்து ஆடி மிகப்படிக்க நல்லநா ராயணரும் நாட்ட மிகஅறிந்து வல்லப் பொருளும் மனது ளறிந்திருப்பார் அறிந்து சிலநாள் அகமகிழ்ந்து தான்பார்த்துச் செறிந்த குணநாதன் தேதி கணக்கதிலே இகனை நடத்த எண்ணமுற்றி ருக்கையிலே  உவமை பலசொல்லி உற்றதெய்வக் கன்னியர்கள் அனைவோ ரும்புகழும் அரன்கயிலை வாழ்ந்ததுவும் சுனையாடப் போனதுவும் தூயோ னடத்தினதும் பிள்ளைதனைப் பெற்றதுவும் பெருவனத்தில் சென்றதுவும் வள்ளல் சிவனை வருந்தித் தவம்புரிந்து உலகில்மிகத் தோன்றும் உள்ளவர்த்த மானமெல்லாம் சிலைநுதலிக் கன்னி சொல்லி மிகப்படிப்பார் அப்போ திருமால் அடக்கி மிகமனதில் செப்புவ தெல்லாம் சிந்தைதனில் கொண்டிருந்தார் உமைமண்டைக் காட்டாள் உற்ற பகவதியாள் இமையோர் புகழும் ஏற்றகுல பார்பதியாள் சீதை மடந்தை சேயிழைமா ரெல்லோரும் மாதவனை நோக்கி வளங்கூறி யேபடிப்பார் பாவிக் கலியன் பழிநீசன் தோன்றினதால் மேவிக் கலியில் மிகமூழ்கி மாயமதால் மாயக் கலியை வதைக்குமந்த நாளையிலே தேசமதி லெங்களையும் திருக்கல்யாண முகித்து எங்களுட நாயகனார் இங்குவரு வோமெனவும் எங்களையு மிங்கே இக்கோல மாய்வரவே சொல்லி யயைச்ச சுவாமிநீர் வந்தீரோ ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(428,'சான்றோர் பக்தி','பல்லுயிரும் பணியும் பரமனேநீர் வந்தீரோ என்று மடவார் இப்படி யேயிசையப் பண்டு முறைபோல் பிரப்பிரம மாயிருக்கும் எண்ணித் திருமால் இருதயத்தி லேயடக்கி மண்ணி லுள்ளோர்கள் வந்துமிகக் கூடிநிற்க மேலோகத் தார்கள் இறைஞ்சிமிகப் பார்த்துநிற்கப் பூலோக மெல்லாம் புதுமையெனப் பார்த்துநிற்க அப்போது மாயாதி ஆகமத் தின்படியே செப்போடு வொத்த தேசத் திருப்பதியில் வாழுகின்ற தேவரையும் வாகாய் வரவழைத்து நாளுக்கு நான்சொன்ன ஞாய முறைப்படியே நடத்தாம லிப்போ ஞாயமீறி நீங்கள் அடத்தமாய்ச் செய்வதென்ன எல்லோருஞ் சொல்லுமென்றார் உடனே யெல்லோரும் உள்ள மிகத்தளர்ந்து திடமே குளறிச் சொல்லவா யில்லாமல் புத்தி மயங்கிப் பொறியழிந்து தேவரெல்லாம் சத்திகெட்டார் போலே தானே விறுவிறுத்து நின்ற நிலையை நெடியதிரு மாலறிந்து குன்றுதனில் கொண்டு கொழுவிலங்கில் தான்சேர்த்து மன்று ஒருகுடைக்குள் வைகுண்டம் ஆளவரும்  இன்றுமுதல் லக்குவரை இருங்கோ பாராவதிலே வைத்தாரே நல்ல வடவா முகமதிலே செய்த்தான் விதியெனவே தேவரெல்லா மிருந்தார் அந்த அவதாரம் அதிக முடனடத்திச் சந்தான மான சப்தகன்னி மாரையினி மாமணங்கள் செய்து மாதரேழு பேர்களுக்கும் தாமதங்க ளின்றி சந்ததிகள் தாங்கொடுத்து இராச்சிய முங்கொடுத்து ஏற்றபவி சுங்கொடுத்(து) இராச்சிய மாளச் செய்யவே ணுமெனவே நினைத்துத் திருமால் நின்றிதயத் தேயடக்கித் தனத்தனங்கள் பாடித் தந்தனங்கள் போடலுற்றார் தினத்தினங்கள் பாடித் திருவேடம் போடலுற்றார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(429,'சத்தகன்னிமார் வருகை','முன்னே குருநா டைவருக்கு மீண்டு கொடுத்து மிகக்கலியால் பொன்னோர் மானா யுருவெடுத்துப் பொருப்பே யேறி முனிகையினால் சென்றோங் கூடு மிகப்போட்டு ஸ்ரீரங்க மேகச் செல்வழியில் மின்னா ரிவர்க ளேழ்வரையும் மேவிப் புணர்ந்தோ மவ்வனத்தில் வனத்தில் புணர்ந்து மாதர்களை மக்க ளேழும் பெறவருளிப் புனத்தில் காளி தனைவருத்திப் பிள்ளை யேழு மிகஈந்து இனத்தில் பிரிந்த மானதுபோல் இவர்க ளேழு மடவாரை வனத்தில் தவசு மிகப்புரிய மனதைக் கொடுத்து மீண்டோமே  மீண்டோங் கயிலைக் கிருந்துபின்னும் மெல்லி யிவர்கள் தவம்பார்க்க ஆண்டோர் சிவனா ருமையாளும் யாமுந் தவத்துக் கருள்புரிந்து சான்றோரிடமே பிறவிசெய் தரணி தனிலே நாம்வருவோம் என்றே விடைகள் கொடுத்தயைச்ச இளமா மாதர் வந்தனரே முற்பிறவி செய்த மொய்குழலார் வந்தாரென நற்பிறவி கொண்ட நாரா யணர்மகிழ்ந்து ஆடரம்பை மாரை அருமைமணஞ் சூட்டவென்று வேடம திட்டார் வீரநா ராயணராய் நாரா யணராய் நல்லதிரு வேடமிட்டுச் சீரான காவிச் சீலை மிகப்புரிந்து ஒருதோளில் பொக்கணமும் உத்திராட்ச மாலைகளும் துரித முடன்சிரசில் துளசிமா லைபுனைந்து கையிற் பிரம்பும் கனத்தசுரைக் கூடுடனே மெய்யில் வெண்பதமும் உத்திராட்ச மாயணிந்து மாலு நிறமாய் வாய்த்ததொட்டில் மீதிருந்து ஆகமத்தி லுள்ள அறிவுஞா னம்பேசிப் பாகமதில் நிற்கும் பாலதியச் சான்றோரைப் பார்த்து அருகழைத்துப் பச்சைமா லேதுரைப்பார் நாற்றிசை யுமறிய நல்லகுலச் சான்றோரே  உங்களுட தாய்மார் உற்றகன்னி மார்களைநான் எங்க ளுடவிதியால் இப்போமணஞ் சூட்டுதற்கு விதிவந் திருக்குதுகாண் வேளையி தானதினால்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(430,'சத்தகன்னிமார் வருகை','பெரியவுங்கள் தாய்மாரைப் பேயழைத்து வாருமென்றார் நால்திசையும் நீங்கள் நடந்துமிக சத்தமிட்டு மாலதியப் பெண்ணை வரவழைத்து வாருமென்றார் சீரான நூல்முறைக்கு தேனெயெங்கள் தாய்மாரே நாரா யணர்க்கு நல்லமணஞ் சூட்டுதற்கு நாளான நாளிதுவாம் நன்னுதலே தாய்மாரே தாழாமல் வாருமென்று சத்தமிட் டழையுமென்றார் இப்படியே சொல்லி ஏற்றபுகழ்ச் சான்றோர்கள் அப்படியே சொல்லி அழைத்தாரே சத்தமிட்டுப் பாலருட சத்தம் பாவையர்கள் கேட்டுமிகச் சீல முடனெழுந்து தெய்வமட மாதர்வந்தார் வந்துமிக நாரணரை வாழ்த்தி மிகவணங்கி சந்துஷ்டி யாகத் தங்கள்தங்கள் வாக்கில்நின்று முன்முறைகள் சொல்லி மொழிந்துமிகப் படித்தார் தென்பதிக ளான தெச்சணா பூமியிலே நான்வந் திருந்து நானிலத் துள்ளவர்க்குத் தீனம் பலதீர்த்து ஸ்ரீபண்டா ரமெனவே  நாம மிகக்கூறி நாட்டிலிருப் போமெனவே சொல்லி யெங்கள்தம்மைத் தொல்புவியில் வாருமென்று நல்லவராய்ச் சான்றோருள் நாடிப் பிறவிசெய்தீர் பிறந்து வளர்ந்து என்பேரு கேட்டவுடன் மறந்திடா வண்ணம் வாருங்கோ என்றுசொல்லிப் பிறவிசெய்த நாயகரே பெரியவரே வந்தீரோ கங்கை திரட்டி கறைகண்டர் தன்சிரசில் பெண்க ளேழுபேரும் பிரியமுடன் வாழ்ந்திருந்து எங்கள்கற் பெல்லாம் ஈடழிய வேதுணிந்து சங்கை யழித்த தலைவனேநீர் வந்தீரோ எங்களுட பிள்ளை ஏழு மினவழியும் அங்கங் குறையாமல் அவ்வழிகள் தாமுழுதும் கொத்தோ டேசேர்த்துக் குடும்பத்தோ டேயடக்கிப்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(431,'சத்தகன்னிமார் வருகை','பெற்றோர்க்கு எல்லாம் பெருவாழ்வு தான்வகுத்து எங்களையுஞ் சேர்த்து இரட்சிய முமருளி மங்களமாய் வாழ்வோமென்ற மாதவரே வந்தீரோ இனியிப்போ தேழ்வரையும் ஏற்ற மணம்புரிந்து வனிதவழு மாயவரே வைத்தாளு மென்றுசொல்லிப் பாடியே மாமடவார் பாதம் பணிந்துநிற்க நாடியே யெம்பெருமாள் நல்லதா கட்டெனவே அருகில் மிகநிற்கும் அன்பான சான்றோரை வருக அழைத்து வார்த்தைமிகக் கூறலுற்றார் எந்தனக்கு முன்னமைத்த இளங்குழலா ரேழ்பேரும் வந்தன ரேயிவர்கள் வளப்பமென்ன சொல்லுமென்றார் கன்னிதானோ யிவர்கள் கள்ளிகளோ பாருமென்று உன்னி மனதுள் உபாயமாய்த் தானுரைத்தார் நடையுடைகள் பேச்சு நாரியர்கள் தங்குணங்கள் மடமயிலின் சாடை மாதிரியைப் பாருமென்றார் அப்போது சான்றோர் எல்லோரு மேமகிழ்ந்து இப்போது எங்களுக்கு ஏதுந் தெரியாது படைத்தவர்க் கேதெரியும் பாவையரின் தங்குணங்கள் செடத்தமெல்லா மெங்களுக்குத் தெரியா தெனவுரைத்தார் மாயவர் தான்பார்த்து மாதுகளைத் தாநோக்கித்  தூயவர் தான்வார்த்தைச் சொல்லுவார் பெண்களுடன் நானெப்போ துங்களையும் நடுவனத்தில் கண்டதுதான் தானெப்போ வந்தேன் தார்குழலே நானறியேன் காட்டிலே வந்ததெப்போ கற்பை யழித்ததெப்போ பேட்டிசெய்து பிள்ளை பெற்றதெப்போ நானறியேன் ஒன்றுந் தெரியாது உங்களைநான் கண்டதில்லை பெண்டுகளே யிப்பேச்சுப் பேசாதே போய்விடுங்கோ  மாயமாய் நீங்கள் மருட்டி விழியாதுங்கோ பாயமா யுங்கள் பகட்டி லடங்கேனான் பண்டாரத் தோடே பழிமொழிகள் பேசாதே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(432,'சத்தகன்னிமார் வருகை','அண்டாது உங்கள்மொழி அகலநின்று போய்விடுங்கோ என்று அடாத்தியமாய் இவர்தான் மிகமொழிந்தார் நின்று நினைத்து நேரிழைமா ரெல்லோரும் அடந்து மடவார் ஆகத்திரண் டேகூடித் தொடர்ந்து நெருங்கித் தொட்டுப் பிடிக்கவென்று தார்குழலில் மூத்த சாத்திரத்தா ளேதுரைப்பாள் வாரணியுங் கூந்தல் மடவா ரென்தங்கையரே கள்ளக் கவுலாய்க் கண்மாயஞ் செய்துஅன்று மெள்ள நெகிழ்ந்த மெக்குவாய்த் தாதனையம் சென்று பிடித்துச் செய்தியென்ன கேளுமென்று தன்றுபுகழ் சாத்திரத்தாள் தங்கையரைக் கண்காட்ட மடவார்க் ளெல்லாம் மாகோப மாய்வெகுண்டு அடவாக மாயவரை அடர்ந்து பிடிக்கலுற்றார் தேன்மொழி மாரே யென்றன் சித்திரத் தங்கை மாரே கான்வன மதிலே நம்மைக் கைகலந் திழிவு செய்த மான்முனி தன்னைக் கண்டோம் வார்த்தையில் குழைக்கார் சோற்றை வான்முனி தன்னை நீங்கள் வளையுங்கோ சிணமே யென்றாள்  செப்பிட மூத்த கன்னி தேவிய ராறு பேரும் அப்படி ஒன்று போலே அவர்வளைந் தாதி தன்னை முப்படி யெங்கள் தம்மை மொய்வன மதிலே வந்து கற்பினை இகழச் செய்தக் கள்வரே யென்று சூழ்ந்தார் சூழ்ந்தே நமது துகிலைமுன்னம் சுழியஞ் செய்த மாமுனியைப்  பூந்தே நெருங்கித் துகிலுரியப் பிடித்தே யிழுத்து நாமீன்ற சாந்தோர் மக்க ளேழ்வரையும் தரவே யென்ன செய்தியென்று ஓர்ந்தே கேட்போ மென்றுசொல்லி ஒன்றுபோலே வளைய லுற்றார் வளைந்த போது மாயவரும் மனது ளுபாய மொன்றெடுத்து இளந்த மொழிகள் சொன்னாக்கால் இளப்ப மிதுவே யாகுமென்று குழைந்த வார்த்தை மிகக்கூறி குழந்தை யேழு மினவழியும் நுழைந்தே யெடுத்து நொடிப்பொழுதில் நேரேகொண்டு தாறோ மென்றார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(433,'சத்தகன்னிமார் வருகை','தரு (பல்லவி) பிள்ளையை நாம்தா றோம்-நீங்கள்பெற்ற பிள்ளையை நாம்தா றோம் அனுபல்லவி பிள்ளையை நாம்தாறோம் கிள்ளை மடவாரே கள்ள மொழியில்லை உள்ளதைச் சொல்லுகிறோம் (பிள்ளை) சரணம் காட்டு வனத்திலே கண்டுங்கள் தம்மையும் லோட்டுகள் பார்த்துநீ ருடுகலை தோற்றதும் மேட்டுக ளாயென்னை யெரிக்கத் துணிந்ததும் பூட்டுகள் சொல்லாமல் பெண்ணேநீர் பெற்றிடும் (பிள்ளை) அருவன மீதினி லமிர்தவாழ் கெங்கையில் சொரூபமாய் நீங்கள் சுனையாடும் வேளையில் வரும்வழி மீதுங்கள் வாய்மொழி மதங்கண்டு கருவது கொண்டுநீர் காட்டினி லீன்றிடும் (பிள்ளை) உறுவன மீதிலென் உபாயத்தை யறியாமல் முறுவல்செய் தென்னைநீர் மொழிந்தது கண்டுநான் இறுவான மேலோக எழுபுவி உயிரெனப் பெறுகநான் செய்திடப் பேணியே நீர்பெற்றப் (பிள்ளை) மடமயில் மாதே நீங்கள் வந்தென்னை நெருக்க வேண்டாம் தொடவும்நீர் ஞாய மில்லை தூரவே யகல நின்று அடவதில் பெற்ற பிள்ளைக்(கு) அடையாள மறியச் சொன்னால் உடனிப்போ மதலை தந்து உலகமு மருள்வோ மென்றார் அய்யனே குருவே யெங்கள் ஆதியே சோதி யான மெய்யனே வொப்பில் லாத வேதநன் மணியே கேள்மோ வெய்யநல் மதலை யீன்று வெட்கமு மிகவே யாகிக் கையது தப்பி யோடக் கண்டமோ மதலை யென்றார் தரு(பல்லவி) மதலையைத் தாருமையா-நாங்கள் பெற்ற மதலையைத் தாருமையா');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(434,'சத்தகன்னிமார் வருகை','அனுபல்லவி மதலையைத் தாருமெங்கள் வயிறெல்லாங் கொதிக்குது குதலைமொ ழிகள்கேட்டுக் கொஞ்சிக் குழைந்திருக்க (மதலை)  சரணம் பெற்றநா ளின்றுவரைப் பிள்ளைதனைக் காணாமலே மெத்தம னதுநொந்து மெய்யந்த ளருதையோ (மதலை) வாலைப்ப ருவத்திலே வனத்தில்ம யக்கமிட்டுக் கோலம ழித்தவேத கோலகால மாமுனியே (மதலை) பாலர்பெற்ற நாள்முதலாய்ப் பன்னிரண்டு ஆண்டுவரைக் காலதி ழகாமலே காட்டில்த வசிருந்தோம் (மதலை) ஊணுஉ றக்கமில்லை ஒருவரின் தஞ்சமில்லை மூணுநாலு காலம்வரை மூண்டத்த வசிருந்தோம் (மதலை) சிங்கங்க ரடிபுலி திரியும்வ னத்தினூடே நங்கைமா ரேழ்பேரும் நாடித்த வசிருந்தோம் (மதலை) இத்தனைநா ளுந்தவ மிருந்துபின் னாங்களெல்லாம் பெற்றநல் மனுவயிற்றில் பிறந்துவு தித்துவந்தோம் (மதலை) முன்னிருந்த செல்வமென்ன மூதூர்சி றப்புமென்ன மன்னுகந்த மாதுமையை மறந்துக லியில்வந்தோம் (மதலை) ஆண்டச்சி றப்புமென்ன அருஞ்சுனை யாடலென்ன காண்டம்நிறை வேறுதற்கோ கலியிலு தித்துவந்தோம் (மதலை) உண்டிருந்த செல்வமென்ன உடுத்ததுகில் ஞாயமென்ன கொண்டிருந்த விதிப்பயனோ கூறுகலி மீதில்வந்தோம் (மதலை) பந்தடிக்கும் வீரமென்ன பூத்தொடுக்கும் சாரமென்ன முன்னுரைத்து விட்டதுபோல் மூண்டகலி மிதில்வந்தோம் (மதலை)  கன்னிய ழியுமன்னே காதல்தனைப் பெற்றதினால் நின்னுபிள்ளைப் பாராமலே நெடுகந டந்துவிட்டோம் (மதலை) கண்டுபிள்ளைத் தானெடுத்துக் கமலமுகத் தோடணைத்துப் பெண்டேழும் பால்கொடாமல் பெருகவ னத்தில்சென்றோம் (மதலை) பாலிளகி நல்லமிர்தம் பாலாய்ச்சொ ரியுதையோ மாலழகா எங்களுட மனக்கவ லையைத்தீர்க்க (மதலை)');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(435,'சத்தகன்னிமார் வருகை','வர்ணநி றமதுவும் மக்களுட சாயலதும் கண்ணதிலே கண்டறியோம் கையதிலே தாருமையா (மதலை) அங்கமெல்லாஞ் சோருதையோ ஆவிமெத்த வாடுதையோ தங்கமணி நாங்கள்பெற்ற தவமணியை எங்கள்கையில் (மதலை) மங்காத தெய்வகுல மக்களேழு மேழ்வழியும் சிங்கார மாகஇப்போ சீக்கிரம்நீர் தாருமையா (மதலை) மதலையுந் தந்து எங்கள் மனச்சட வெல்லா மாற்றிக் குதலைகள் தமக்கு இந்தக் குருமுடி சூட்டித் தர்மப் பதியுக மதிலே யெங்கள் பார்முடி மன்ன ராக நிதமிருந் தரசே யாள நின்மக்கள் தருவீ ரென்றார் தருவீ ரெனவுரைக்கு மடமாதே நீங்கள் சந்ததியைப் பெற்றிடத்தில் தயவாய்ப் பாரும்  பெருகியே வாழ்ந்திருப்பா ரங்கே சென்று பெற்றிடத்தைப் பார்க்குகையி லுண்டு மங்கே மருவி யென்னைக் கேளாதே காட்டினூடே வாழ்ந்திருப்பார் கண்டுகொள்வீர் திட்டஞ் சொன்னோம் சருவியென்னை நெருங்காதே மடவீ ரெல்லாம் சந்ததியைத் தேடிவனஞ் செல்லு வீரே செல்லுவீ ரெனமொழிந்த தேனே கண்ணே சீமானே நாமாது புரக்கு மாலே பல்லுயிரு மிகப்படைத்தப் பாக்கிய வானே பருவனத்தி லெங்களைநீர் பற்றி வந்து தொல்லைவினை செய்துநாம் சுமந்து பெற்ற சுத்தகுல மானதெய்வச் சான்றோர் தம்மை இல்லிடத்தி லிப்போது தருகி லானால் இழுத்து வும்மைச் சந்தியிலே ஏற்றுவோமே சந்தியி லேற்றுவோ மென்ற மாதேகேளு தருவதுண்டோ முன்கடன்கள் தந்த துண்டோ பந்தியழித் துங்களுடப் பேச்சை நானும் பகருவே னுங்களைப் பார்பழித்துப் பேச');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(436,'சத்தகன்னிமார் வருகை','முந்தியிதை யுரைத்திட்டே னோடிப் போங்கோ உலகநருள் அறிந்தாக்கால் சிரிக்க லாகும் சிந்தனைக்கே டாகும்வரை நிற்க வேண்டாம் தேன்மொழியே மறுவிடம்போய்த் தேடு வீரே தேடுவீ ரெனமொழிந்த இந்திர ஜாலம் தெய்வமட மாதரொடு செலுத்த வேண்டாம் நாடுபதி னாலறியு முமது கள்ள ஞாயமது எங்களொடு நவில வேண்டாம் வீடுவழி தோறும்விருந் துண்டு முன்னே மெல்லிமட வார்சிலரைக் கொள்ளை கொண்டு பாடுபட்ட பாட்டையினி நாங்கள் சொன்னால் பாரிலுள்ளோ ருமைப்பேசிப் பழிப்பர் காணும் பழிப்ப ரெனச்சொன்ன இளம்பாவை மாரே பாரிலுள்ளோ ரறிவார்கள் பச்சை மாலின் களிப்பதுவுங் காண்டமுறை நூல்கள் தோறும் காரணத்தைச் சொல்வார்கள் கருதிக் கேளு சுழிப்பதுவு மமைப்பதுவும் நமக்குள் ளாகும் தோகையரே யிதுஎனக்குச் சொந்த வித்தை குளிப்பதுவில் சுனையாடி மதலை யீன்றக் கோலமதைச் சொன்னாக்கால் குறைவ தாமே குறைவதா மெனச்சொன்னக் குறவா கேளும் கோவேங் கிரிவாழுங் குலமா மாதர் மறைமுதல்வ னானசிவ னார்க்கு நாங்கள் வந்துசுனை யாடிகெங்கைத் திரட்டி யேகத்  துறையறியாக் களவாண்டுத் தேய்ந்த கள்வா தொல்புவிக ளறியாதோவுன் சுத்தக் கள்ளம் இறையளவு மெய்யில்லாப் பொய்யே சொல்லும் ஏமாளிக் கள்ளரென எவருஞ் சொல்வார் கள்ளரெனச் சொல்லிவந்த மாதே நீங்கள்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(437,'சத்தகன்னிமார் வருகை','களவாண்டீர் கயிலையதி லீசர் முன்பில் வெள்ளமது திரளாமல் கள்ளத் தாலே வெம்மருண்டு நின்றவித வெட்கங் கண்டு வள்ளல்சிவ னாரறிந்துக் கலியில் சான்றோர் மேல்வழியில் பிறக்கவென்று சபித்தார் முன்னே உள்ளதெல்லாஞ் சொல்லிடுவே னோடிப் போங்கோ ஓகோகோ வெனச்சொல்லி யுறுக்கி னாரே உறுக்கிநீ ருரைத்தசுணை யுமக்கே யல்லால் ஓவியங்க ளேழ்பேர்க்கு முண்டோ சொல்லும் இறுக்கிவைத்த நீரிளகச் சேய்த கள்ளம் ஈசர்முத லெல்லோருஞ் சொல்லு வார்கள் சிறுக்குமிடை மாதருட வீட்டில் வெண்ணெய்த் திருடியுண்டக் கள்வரெனச் சொல்வா ரும்மை பொறுக்கஇனி மாட்டோங்கா ணுமது ஞாயம் புகன்றிடுவோ மினியண்டம் பொடியத் தானே பிட்டுக்காய் மண்சுமந்து வைகை தன்னில் பிரம்படிகள் பட்டதுவும் போதா தென்றோ கெட்டுமிகப் பட்டீரே இடைச்சி கையால் கேள்வியில்லா முப்பரத்தில் கிழவன் போலக் குறுங்கண்ணி போட்டிழுத்துக் குனிய வைத்தப் பொட்டதுவுங் குலைத்திடுவோம் நாங்கள் பெற்றப் பிள்ளைதனை தந்துபுவி யான வைப்பீர் ஆடெடுத்தக் கள்வரல்லோ வுமக்குச் சூடு அணுவளவுந் தோற்றாம லலைந்து எந்த  நாடடுத்த இடமெல்லாம் வேசம் போட்டு நாணமதொன் றில்லாமல் நாரி மார்தம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(438,'சத்தகன்னிமார் வருகை','வீடடுத்த மனைதோறு மிரட்டிக் கொஞ்சி வேசியொடு கூடிவிளை யாடுங் கள்ளக் கூடெடுத்த மறையல்லோ எங்கள் தம்மைக் குவலயத்தில் கள்ளியெனக் கூறி னீரே இன்னமுண்டு வுமக்குவெகு பங்கந் தானும் எடுத்துரைத்தா லிப்போது இளப்ப மாகும் முன்னமைத்தப் பாலரையு மினத்திற் சேர்க்கை முழுவதையுந் தந்துபுவி யாள வைப்பீர்  இன்னிமிப்போ தாராம லிருந்தீ ரானால் ஈரேழு யுகமறிய இழுத்து உம்மை அன்னமது அருந்தாம லிழுத்துக் கொள்வோம் அதுவறிந்து பாலரைத் தந்தாளு வீரே பாலரைத்தந் தாளுவீ ரெனமொழிந்த பாவைமாரே பகற்பொழுது கழித்துவெள்ளி யுதிக்கும் நாளை காலமேநீ ரெல்லோரும் வந்தீ ரானால் கதிரவனும் போயடைந்து கங்கு லாகும் சீலமுடன் தென்றலுக்கு மழைக்கும் நானோர் சீட்டெழுதித் தேவரையும் வருத்தி யிங்கே கோலமண முங்களைநான் புரிந்து கொள்வேன் குடிலதுக்குப் பேயமுது குடித்து வாரும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(439,'திருக்கல்யாணம்','வாருமென மங்கையர்க்கு விடையருளி மாயன் வருணனுக்குந் தென்றலுக்கும் வான லோக ஊருமிக அறிவதற்கு ஓலை தானும் உடனெழுதி அழைச்சிடவே வந்தார் வானோர் பாருடனே சங்கமது திரண்டு கூடிப் பாவாணர் கீதமுறைப் பாடி நிற்க சீருடனே கதிரவன் போயடைந்து மீண்டு தினகரனு முதித்துவெள்ளி தோன்றிற் றன்றே தோன்றிய பொழுதே வானோர் ஈசர்மாது துதிமுகனும் நான்முகனுந் தொல்வி மாதும் கூன்றிருஷி தேவரிஷி வேத மானக் குணயிருஷி கிணயிருஷி குலமா மாது தான்றுசர சோதிபக வதிமா மாது சதாகோடி தேவரம்பை சங்க மாக மன்றுபுகழ் நாரணர்க்குந் தெய்வ கன்னி மடவார்க்கு முகூர்த்தமென வந்தா ரங்கே வானமதில் டம்மான முழக்கத் தேவர் மலர்மாரி சலமாரி வானோர் தூவ நான்முகனும் வேதமுறை முகூர்த்தங் கூற நாரிமார் குரவையிட நமனு மாற தானமுறை மாமடவார் சமனங் கூறத் தரணிதனில் நாரணர்க்குந் தைய லான மேனமுகில் மாதருக்கு மணமா மென்று மேலோகப் பந்தலது விண்ணோ ரிட்டார் பந்தலுக்கு நமனாதி கால தாகப் பக்கம்பதி னைந்தும்வளை பரப்ப தாகச் சந்தமுடன் மேற்கட்டி மறைய தாகச் சாருமேல் மேய்ந்ததுவே சமய மாக அந்தமுறை சூரியனும் விளக்க தாக');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(440,'திருக்கல்யாணம்','அலங்கிருதம் வானக்கா யதுவே யாகச் சொந்தமுள்ள சிவமதுவே பீட மாகத் தேவரெல்லாஞ் செய்துபந்தல் சிறப்பித் தாரே சிறப்பித்தோ மென்றுமகா தேவ ரெல்லாம் திருமாலி னடிபணிந்து சொல்வா ரப்போ பிறப்பித்தப் பெம்மானே யெவர்க்கு மாய்ந்துப் பேணியமு தளித்துவுயி ரனைத்துங் காக்கும் உறப்பித்த மாலோனே அய்யா வுந்தன் உதவியினால் பந்தலது விதானஞ் செய்தோம் நிறப்பித்த மணமதுக்கு நாங்கள் செய்யும் நிசவேலை யின்னதென நிகழ்த்து வீரே  தேவருரை யதனைமிகக் கேட்டு மாயன் திருமனது மகிழ்ந்துவாய் திறந்து சொல்வார் மூவருரை மாறம லெனக்கு முன்னே மொழிந்ததெய்வ மாதர்களை முகூர்த்தஞ் செய்ய நாவதுரை வருணனொடு வாயு தானும் நளினமலர் தூவியது குளிர வீச தேவருரை செய்துமிக நில்லு மென்று தெய்வமட வார்வரவே சிந்தத் தாரே சிந்தித்த வுடனே யுந்தத் தேன்மொழி மாத ரெல்லாம் வந்தவ ரடியைப் போற்றி வணங்கியே நாங்கள் பெற்ற சந்ததி யேழு பேரும் தன்னுட கிளைக ளொக்கத் தந்துநற் புவியை யாளத் தருவீரென் தலைவா என்றார் தருவீரென மொழிந்தகுல மாதே பெண்ணே தலைவருங்கள் மக்களையுந் தருக வுங்கள் மருவினியக் கருவதிலே யுதிக்க ஈன்ற மனுவழியை யென்முன்னே வருகச் செய்தால் தருமினிய சொத்தாஸ்தி பொன்னுங் காசு தாறதுவுங் கொடுப்பதுவுங் சபையிற் பேசி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(441,'திருக்கல்யாணம்','பெருகவுங்கள் தம்மை மணஞ் செய்துநீங்கள் பெற்றபிள்ளை தந்துபுவி யாள வைப்பேன் புவியாள வைப்பேனென வுரைத்தீ ரெங்கள் பொன்மானே கண்மணியே புரந்த மாலே கவிஞோர்கள் போற்றுமதக் கன்றே தேனே காயாம்பு மேனியரே கடவு ளாரே இவிலோகக் கலியதிலே பிறந்த தாலே இதுநாளும் வரையன்ன மீந்த பேர்கள் செவியாலுன் பதமடைந்தா லவர்க ளேது செய்வார்க ளெங்களொடு சேர்க்கை தானோ சேர்க்கையுண்டோ எனமொழிந்த மானே பொன்னே தேன்மொழியே யுங்களுக்குத் தெரியா வண்ணம் வார்த்தையிது சொல்லாதே நமது வாழ்வும் மக்களுட வாழ்வதுவு மொன்று போலே மார்க்கமுடன் வைத்தரசு ஆள நானும் மனமதிலே நினைத்திருக்கும் வளமை யாலே கோர்கையிது அவர்கொடுக்கல் வாங்கல் தன்னை கூறிமிகக் கேட்டுமணங் கூடு வேனே  மணங்கூடு னேனென வுரைக்க மாயன் மனுவழியி லுதித்தகுலச் சான்றோ ரெல்லாம் அணங்குஇன வழிபோலே யவர்கள் வந்து ஆயனடிப் போற்றிமிக அன்பாய் நிற்கக் குணங்குறிகள் கண்டுமிக அரியோன் மெச்சிக் கொடுப்பதென்ன இவர்களைநான் மணங்கள் செய்தால் பணங்கள்மிகப் பொன்காசு ரெம்ப ரெம்பப் பாவையர்க்குச் சீதனங்கள் பகரு வீரே பகருவீ ரெனமொழிந்த அய்யா நீரும் பரிசமென்ன எங்களுக்குப் பண்பா யீவீர் உகருமெனச் சொல்லிடவே சான்றோ ரெல்லாம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(442,'திருக்கல்யாணம்','உடையகுலச் சொக்கருமே உரைப்பார் பின்னும் தகருமோ நான்தருகுந் தங்கக் காசு சதாகோடி யெண்ணமது தாணோ இல்லை நிகருவீர் நீங்கள்தருஞ் சொத்தை யெல்லாம் நினக்கறியச் சொல்லிடுங்கோ நிசமா யென்றார் ஆண்டவரே யிப்பரிசந் தருவீ ரானால் யாமடியா ராளுகின்ற வஸ்து வெல்லாம் கூண்டபண்டம் பொன்னுடைமை யாடு மாடு குருபரனே யாங்கள்வரை யுமக்குச் சொந்தம் மீண்டடிமை முக்காலு முமக்கு நாங்கள் விலையடிமை யானோங்காண் விரைய மாலே பாண்டவரைக் காத்துரெட்சித் தாண்டாப் போலே பாவையரு மக்களுமும் பக்கந் தானே பக்கமது என்றவுடன் மாயன் தானும் பதறாதே உங்களையுங் காத்துக் கொள்வோம் ஒக்கவெனக் குங்களையும் ஆண்டு கொள்வோம் ஒன்றுபோ லிருந்துபுவி யாள்வோம் நாமும் தக்கமதொன் றில்லையப்பா இந்த வார்த்தை தந்ததுவு மீந்ததுவுந் தவறோ இல்லை மிக்கதெய்வக் கன்னியரை மணங்கள் செய்ய மெல்லிகையைப் பிடித்தெனக்கு விடுகு வீரே கைப்பிடித்துத் தாருமென வுரைத்தீ ரையா கணவரல்லோ முன்னவர்க்குக் கருணை மாலே மெய்ப்பிடித்த மெல்லியருந் தேவி யல்லோ மேதினிக ளறியஅவ தாரஞ் செய்தீர் எப்படித்தா னாங்கள்கையைப் பிடித்து ஈய இவ்வுலகி லவதார இகனை தானோ எப்படியோ அறியோங்கா ணென்று சொல்லி ஏந்திழைமார் கைப்பிடித்தங் கீய லுற்றார் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(443,'திருமுகூர்த்தம்','ஈந்திடவே மாயவருந் தங்கத் தாலே இரத்தின வொளி போல்வீசுந் தாலிதன்னை மாய்ந்துமிகப் போகாமல் தாலி வாழ மகாதர்ம யுகம்வாழ மாதும் வாழ ஏந்துபுவி தர்மமது தழைத்து வாழ ஈசர்முத லெல்லோரு மிருந்து வாழ சாந்தகுலச் சான்றோர்கள் தழைத்து வாழ தாலிமிக வாழவென்று தரித்தார் தாலி தாலிமிகத் தரிக்குகையில் மாத ரோடு தமனியநா தன்மகிழ்ந்து தானே சொல்வார் கேலிமிக வுரையாதீ ரெனக்கு இன்னம் கெணிமடவா ரநேகமுண்டு மணங்கள் செய்ய சோலியல்லோ ஆச்சுதென்று நினையா துங்கோ தொல்புவியை யரசாள்வீர் நிசமே சொன்னோம் மாலினுட சூட்சமகா கோடி யுண்டு மனதுசடை யாமல்மிக வாழுவீரே வாழுவீர் கூடுவிட்டுக் கூடு பாய்வேன் மறுவூரு பாய்ந்துவுங்கள் மனதில் வாழ்வேன் மாளுவேன் முழித்துப்பின் வருவே னுங்கள் மனதலைந்து எனைஇகழ்ந்து மாளா துங்கோ தோழிலே யிருந்துபல சூட்சஞ் செய்வேன் தோகையரே நீங்களெல்லா மெனது பாயம் நாளிலே யறிந்துமிகக் கண்டு கொள்ளும் நாரணரி னடப்பிதுவே ஞாயஞ் சொன்னோம் சொன்னமொழி தனைமறந்து நீங்க ளெல்லாம் தூசணித்து எனைக்கபட மெண்ணா துங்கோ என்னுடையத் தொழிலிதுவே உங்க ளோடு இருந்துதர்ம முடிசூடி ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(444,'சிந்து','யாளு மட்டும் பொன்னுடைய நாடதற்கு வானோர் தம்மைப் போகவிடை கொடுக்கஅவர் போனா ரங்கே கன்னிமட வார்கையைப் பிடித்துக் கொண்டு கருணைபதித் தெருவீதி வருகின் றாரே தெருவீதி நாம்வருவோம்-எந்தன் தேவியரே கன்னிநாயகமே மருவினிய கன்னியரே-பதி வலங்கள்சுற்றி நாம்வருவோம் கண்மணியே காரணரே-ஓகோ காயாம்பு மேனியரே மண்ணேழ ளந்தவரே-ஓகோ மாயவரே பதிவலம்வருவோம்  பெண்ணரசே மாமயிலே-நமது பொறுமைப்பதி வலம்வருவோம் தண்ணமுள்ளத் தேவியரே-நமது தருமபதி வலம்வருவோம் ஆண்டமணி நாயகமே-உலகு ஆண்டருளு மெங்கண்மணியே காண்டம்நிறை வேற்றவந்த-எங்கள் கணவனாரே நாம்வலம்வருவோம் பொறுமைப்பதி வலம்வருவோம்-கலிப் பொடியயாமம் போட்டிடுவோம் தருமமது தழைக்கச்செய்வோம்-பதி தானேவலம் நாம்வருவோம் பொல்லாத வகையழித்து-சுவாமி புதுப்பூமி தோணவைத்துக் கல்லாதார் கருவறுத்து-சுவாமி கதியபதி வலம்வருவோம் நாடும்பதி தலங்கள்வாழும்-பெண்ணே நம்முடைய இனங்கள்வாழும் கேடுகலி கோடுஅறும்-பெண்ணே கிளர்ந்தபதி வலம்வருவோம் ஆகாத பேரையெல்லாம்-சுவாமி அக்கினிக்கு விருந்தளித்து வாகாகத் தர்மபதி-சுவாமி வாழும்பதி வலம்வருவோம் முன்குறோணி உதிரமதால்-பெண்ணே உதித்துவந்தக் குலங்களெல்லாம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(445,'சிந்து','தன்குணத்தால் மாண்டுபோக-பெண்ணே தர்மபதி வலம்வருவோம் மாற்றானொ ழியவேணும்-சுவாமி மக்களெல்லாம் வாழவேணும் காத்தோரைக் கைவிடாமல்-நாமள் கருணைபதி வலம்வருவோம் தெண்டமிறை பொய்களவு-பெண்ணே செய்யும்வண்டக் குலங்களெல்லாம் கொண்டகலி கூடமாண்டு-குரு நாதர்பதி வலம்வருவோம் வீணான கலியுகத்தை-சுவாமி வெய்யோனுக் கமுதளித்துச் சாணாரை வைத்தாள-சுவாமி தர்மபதி வலம்வருவோம்   நாடும்பதி துலங்குதடி-பெண்ணே நல்லசிலை குதிக்குதடி ஆடுமாடு அருகுதடி-கர்த்தா ஆவினங்கள் தோணுதடி நல்லபதி துலங்கணுமே-சுவாமி நாடுதர்ம மாகணுமே பொல்லாப்பது ஒழியணுமே-சுவாமி புத்தியொன்றாய்க் குவியணுமே கோவில்தெரு துலங்கணுமே-நம்மள் கோட்டைவெளி யாகணுமே தேவருட நல்திருநாள்-பெண்ணே தினமும்வந்து கூடணுமே தங்கத்தொட்டில் தண்டாயமும்-சுவாமி தாண்டும்பதி வீதிகளும் சங்கமுடன் டம்மானமும்-சுவாமி தலத்தில்வந்து தோணணுமே கோட்டையிட்டுக் கொடியுங்கட்டி-பெண்ணே கொத்தளமாய் மேடைசெய்து நாட்டையெல்லாம் தான்கிலுக்கி-பெண்ணே நமக்குப்பதி யேறணுமே நம்பினோரைக் காக்கணுமே-சுவாமி நாடுகட்டி யாளணுமே அல்பலங்க ளேறணுமே-சுவாமி அவதாரங்கள் நடத்தணுமே சிங்காசன மேறணுமே-பெண்ணே தீவட்டிகள் போடணுமே கண்காட்சை காணணுமே-நானும் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(446,'சிந்து','கன்னியரைக் கைப்பிடித்தால் பாக்கியங்கள் பெருகணுமே-சுவாமி பாரிலுள்ளோர் காணுதற்கு ஆக்கிநாங்கள் படைக்கணுமே-சுவாமி அமுதருந்தி வாழணுமே மக்கள்பெற்று வாழணுமே-பெண்ணே வந்துவென்றான் கால்பிடித்து ஒக்கல் நின்றோ ராடணுமே-பெண்ணே உற்றபள்ளி மெத்தைசூழ பால்பவிசு பெருகணுமே-சுவாமி பஞ்சணையில் கொஞ்சணுமே தூலருமை யறியாமலே-கலி தொல்புவியு மயங்கணுமே   சமுசாரி யாகணுமே-பெண்ணே தலையிற்சோறு சுமக்கணுமே ஓவுதாரி யாகணுமே-பெண்ணே உங்களைநா னடிக்கணுமே அயலூரு போகணுமே-சுவாமி அழைக்கநீரும் வரவேணுமே கயல்விழிமா ரொருவர்க்கொரு-சுவாமி கத்துதல்கள் கொள்ளணுமே நானும்வந்து நிரத்தணுமே-பெண்ணே நல்லமொழி சொல்லணுமே தேனும்பாலும் போலநாமள்-பெண்ணே தேசமதில் வாழணுமே கண்டுஇந்த நீசர்குலம்-சுவாமி களிப்புச்சொல்லி யேசணுமே பெண்டுகட்கு விங்கியென்று-சுவாமி பொல்லாப்பய லேசணுமே காரணத்தை யறியாமலே-பெண்ணே கலிப்பயல்கள் தானகைத்தால் மாரணத்தின் தீர்வைதன்னில்-பெண்ணே மடுநரகம் பூத்திடுவேன் செம்பவள நற்பதியின்-சுவாமி தெருவலங்கள் சுற்றிவந்தோம் பொன்பதிக்குள் நாமள்புக்கி-மறு பொழுதுவந்தால் வருவோமையா');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(447,'சோபனம்','சோபனமே சோபனமே-சுவாமி திருநடன சோபனமே தேவர்குரு நாரணர்க்கும்-அவர் தேவியர்க்கும் சோபனமே பூமலர்ந்த ஈசுரர்க்கும்-அவர் பொன்தேவி மாமதுமைக்கும் காமனந்த நாரணர்க்கும்-தெய்வக் கன்னியர்க்கும் சோபனமே பூமடந்தை நாயகிக்கும்-நல்ல பொன்னுமண்டைக் காட்டாளுக்கும் பார்மடந்தை நாயகிக்கும்-சிவ பகவதிக்குஞ் சோபனமே தெய்வானை நாயகிக்கும்-நல்ல சிறந்தவள்ளி மடந்தையர்க்கும் அய்வர்குல நாரணர்க்கும்-கன்னி அரிவையர்க்குஞ் சோபனமே  துடியிடைக் கன்னி மாரைத் திருமணந் திருமால் செய்து குடிபுகழ்ச் சான்றோர் மக்கள் குரவைகள் முழக்கத் தோடு திடிரெனத் தெருக்கள் சுற்றித் தேவியு மன்ன ராகப் படிமிசைப் பதியி னுள்ளே பதிந்துவந் திருந்தா ரன்றே மாதுநல்லா ளேழ்வரையும் மணமுகித்து மாயவரும் தீதகலும் நற்பதியில் சிறந்தங்கினிதிருந்தார் கன்னிமா ரேழ்வரையும் கைப்படித்தோம் நாமுமினி பொன்னம் பதியில் புகழ்ந்ததிரு நாள்நடத்தி நித்தந் திருநாள் நிதம்நடந்த வேணுமென்று சித்தமதில் நாரணரும் சிந்தித்தா ரம்மானை எத்திசையு முள்ள ஏற்றபுகழ் சான்றோரும் முத்தி யடைந்தோம் மோட்சமது பெற்றோமென நம்முடைய தாயார் நல்லதெய்வக் கன்னியரைச் செம்மைத் திருமால் திருமணங்கள் செய்ததினால் குற்றமில்லை நம்முடைய குலத்துக் கினியெனவே சித்தமதில் நாரணனார் செயல்நமக் குண்டெனவே வந்துமிக எல்லோரும் வாழ்த்தி மிகப்பணிந்து சிந்துபுகழ் தெய்வ மடவா ரையுந்தொழுது நல்லபண்ட மானதுவும் நாடும்நிதி யானதுவும் வல்லகுலச் சான்றோர் மாதா பிதாவதுக்கும் கொடுத்தாரே நல்லக் குவலயத்தில் மக்களெல்லாம் விடுத்ததெல்லாம் வேண்டி மிகவேற்றார் மாயவரும் பலன்பெற்றோ மென்றுப் பாலதியச் சான்றோர்கள் குலமெல்லாம் வந்து கூடினார் மாயனிடம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(448,'அய்யா திருநாள் இகனை நடத்துதல்','அப்போது நாதன் ஆனந்த மேபுரிந்து இப்போ திருநாள் இகனைநடத்த வென்று நாளான நாளிதுதான் நாதன் பிறந்தநாள் தாழாத ஞாயிறுவே சுவாமி பிறந்ததினால் இந்நாள் முதல்திருநாள் இன்ப முடனடத்திப் பொன்னான நாரணரும் பூரித்தா ரம்மானை ஞாயிறாழ்ச்சைத் திருநாள் நாம்பார்க்கப் போவோமெனத் தேச மதிலுள்ளத் தெய்வகுலச் சான்றோர்கள் பாலுக்கு நெல்லும் பண்பாய்ச் சிறுமணியும் மாலுக்கு நல்ல மகிழ்ந்துபிச்சி மாலைகளும் கொழுந்துபன்னீர் சந்தமமும் குலுங்கா விடலைகளும் தென்னங்காய் மாம்பழமும் திகட்டாப் பலாக்கனியும் வாழைக் கனியிலையும் வகிர்ந்தகமு கின்குலையும் நாளைத் திருநாள் நமக்குமுந்திப் போவோமென இத்தனையுங் கொண்டு இசைந்ததிரு நாள்வகைக்குப் பத்துமூ ணேழு பணமு மிகஎடுத்துக் காதுக்கு நல்ல கனத்த நகைகளிட்டு மாதுக் கியல்வாய் வளர்கோர் வையுமணிந்து பெட்டிச் சீலையுடுத்துப் பெரியநாமப் பொட்டுமிட்டுக் கட்டன்ன மும்பொதிந்து கண்ணர்திரு நாட்கெனவே  மக்கள் மணவாளன் மனைவி கிளைகளுடன் சிக்கெனவே நாதன் திருநாட்கும் போவோமென வருவார் கிழமை மாதந் தவறாமல் திருமால் அகமகிழ்ந்து திருநாள் முறைநடத்தக் கொட்டகை யிட்டுக் குவிந்தமணி மேடையிட்டுப் பட்டால் மேற்கட்டி பழங்கனிகள் தாந்தூக்கிப் பிச்சிமா லைதூக்கிப் பெருவாடை தான்தொளித்து மிச்சம்பழந் தூக்கி மிகுவாடை தான்தொளித்து சந்தனங் கஸ்தூரி சவ்வாது புனுகுடனே சிந்தரெவ ரும்போற்றத் தேன்கனிகள் மாங்கனிகள் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(449,'அய்யா திருநாள் இகனை நடத்துதல்','வெற்றிலை பாக்கு மிகுவாய்ப் பழங்களுடன் தத்திதத்தி யாகச் சதாகோடி யாய்க்குவித்துப் பாலாயி தங்கள்வைத்துப் பாவித்து நற்பதிக்குள் மாலா யிருந்து மகிழ அலங்கரிப்பார் தெருவிற் காளாஞ்சித் தீவட்டி யும்பிடித்து மருவினிய மாதர் மகாகுரவை மலமலென டம்மானை நகாசுரம் நல்லவா ணவெடிகள் மும்மான முளக்கம் போலே மிகமுழங்க கன்னிமார்க் கெல்லாம் காவிப்பட் டாடைகொண்டு பின்னு மடவார்க்குப் பெரியருத்தி ராட்சமிட்டு  நாமப் பொட்டிட்டு நல்லஎத் தாப்புமிட்டு மாமடவா ரெல்லாம் மகிழ்ந்து தெருவில்வர எந்தன் பிரானும் எடுத்தா ரொருசொரூபம் கந்தைக்கா விபூண்டு கழுத்தில்தா வடம்பூண்டு கையதி லேமாத்திரைக்கோல் கமூக்கூட்டி லேபிரம்பும் மெய்யதிலே வெண்பதமும் மினுக்க முடனணிந்து உச்சிக்கொண்டை கோர்த்து உயர்ந்தசுரைக் கூடுமிட்டு மெச்சுந் துளசி மிகுமாலை யுமணிந்து பிச்சிமாலை யணிந்து பெண்கள்மிகச் சூழ்ந்துவரச் சச்சிசச்சி யாகச் சனங்கள்சான் றோர்கள்வரக் கைக்குள்நின்ற சீசர் காவிவஸ்தி ரமணிந்து மெய்க்குருவைப் போற்றி மேலில்ருத்தி ராட்சமிட்டுக் கையதிலே மாத்திரரைக்கோல் கனத்தசுரைக் கூடுமிட்டு மெய்யதியப் பொக்கணமும் மேலதிய நாமமிட்டு அய்யா குருவேயென்று அவர்கள் மிகப் போற்றிவரத் துய்ய துவையல் துவைத்தபண் டாரமெல்லாம் காவிருத்தி ராட்சம் கனத்தசெம்புக் கடுக்கனிட்டு நாவிற் சிவாவெனவே நாட்டமிக வாய்வரவே ஆரா தனையாய் அருள்கொண்டப் பெண்ணாணும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(450,'அய்யா திருநாள் இகனை நடத்துதல்','நாரா யணர்பேரில் நற்கீதம் பாடிவரத்  தங்கரத்தினத் தொட்டில் சுவாமி மிகஇருந்து பெண்கள் மிகநன்றாய்ப் பெருகக் குரவையிட சங்கு தொனிக்கச் சகலோர் மிகப்போற்ற சிங்கமுக லெட்சுமியும் திருமுகத்தில் நின்றிலங்க சான்றோர்கள் தொட்டில் தண்டாய முஞ்சுமந்து ஆன்றோர் பதியை அலங்கிருத மாகிவரக் கன்னிமார் நாதன் கைக்குளொழுங் காய்வரவே மின்னும் பதித்தெருவும் மிக்கஇந் தப்பவிசாய் உலாவி வரவே உற்றதெய்வ வானோர்கள் குலாவி மகிழ்ந்து கிருபையுள்ள நாரணர்க்கு பூமாரி பொன்மாரி பெரிய சலமாரி நாமாரி வானோர் நாடி மிகத்தொளிக்க எல்லோரும் பார்த்து இத்திருநாள் நல்லதென்று அல்லோரு மெச்சி அகமகிழ்ந்தா ரம்மானை நல்ல திருநாள் ஞாயிறாழ்ச்சை முழுதும் செல்ல மறுநாள் திங்க ளுதித்தவுடன் காட்சி நடத்திக் கரிய திருமாலும் நாச்சிமார் கூட நாதன் பதிபுகுந்தார் நாதன் பதிபுகுந்து நருட்கு விடைகொடுத்து மாதரோடு நாரணரும் மகிழ்ந்திருந்தா ரம்மானை  இப்படியே திருநாள் இகனை மிகநடத்தி நற்புடனே நாதன் நாயகிமார் தம்மோடு கூடி யிருந்து குலாவி யிருக்கையிலே வேடிக்கை யாக விதியின் படியாலே நல்லபுகழ் சீதா லட்சுமிக்கு நாரணரும் செல்லப்பட் டானதுவும் சிவந்ததங்கக் காறையதும் இட்டுக் கலியாணம் இலட்சுமியைச் செய்யவென்று மட்டநிக ரில்லா மாயன் மனதிலுற்றார் தங்கத்தால் காறை தானதியப் பட்டதுவும் அங்கே மிகவருத்தி ஆயிழைக்குத் தான்கொடுத்துத் திருநா ளிகனைத் திடிமன் முழக்கமொடு பெருமாளும் லட்சுமியைப் பேறாய் மணமுகித்துத்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(451,'அய்யா திருநாள் இகனை நடத்துதல்','தெருவலங்கள் சுற்றிச் சிறந்த பதிபுகுந்து மருவினிய நாதன் மாதரோடு வீற்றிருந்தார் நல்லரிய நாதன் நன்றா யிருக்கையிலே வல்ல திருதெய்வ மடவார்க ளேதுரைப்பார் ஐயாவே யெங்களையும் ஆண்டத் திருமாலே வைய மளந்த மாயத் திருமாலே எங்களைநீ ரிப்போ இவ்வுலகந் தானறிய மங்களமும் செய்தீரே மக்களையுந் தாருமென்றார்  அப்போது மாயன் அவர்களோ டேபுகல்வார் இப்போது மாதர்களே என்னோடு கேட்டதற்கு நல்லத் திவசம் நமக்குவரு மந்நாளில் வல்ல வகையாலும் மக்களையுந் தாறோமென்றார் அதுவரையும் நீங்கள் அதட்டாம லெயிருங்கோ இதுவரைக்க நல்ல ஏந்திழைமா ரெல்லோரும் சந்தோச மாக சுவாமி தனக்கவர்கள் வந்தே நிதமும் வாய்த்ததொண்டு செய்துமிக வாய்த்தபுகழ் லட்சுமியும் மாதுமட வாரேழும் ஏற்ற தொண்டுசெய்து எப்போதும் வீற்றிருந்தார் நாரணருந் தேவியரை நாடி யகமகிழ்ந்து காரணரு மெச்சிக் களிகூர்ந் தினிதிருந்தார் நன்றாக இப்படியே நாச்சிமா ரோடிருக்க எண்டிசையி லுள்ள ஏற்றபுகழ்ச் சான்றோர்கள் வந்து அவர்பதத்தை வாழ்த்தி மிகப்போற்றி சந்துஷ்டி யாகத் தாழ்ந்துநமஸ் காரமிட்டு மனதில் நினைத்ததெல்லாம் மாயவரோ டேவுரைத்துத்  தன்துள் விடைவேண்டி சங்கடங்கள் தீர்ந்திருந்தார் தெய்வமு மவராய்த் திருவுள முமவராய் வைய மகிழ மனுப்போலு மிருந்தார்  வத்துவகையும் மாடாடு சொத்துக்களும் ஒத்து மிகவாழ்ந்து உடைய பரனிருந்தார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(452,'அம்மை மார்களுக்கு மதலை ஈதல்','இப்படியே ஒத்து இவரிருக்கும் நாளையிலே நற்புடைய தெய்வ நாயகிமார் கேட்டபடி மதலை கொடுக்க மனதிலுற் றெம்பெருமாள் குதலை யினத்தைக் கூறி மிகவுரைத்தார் ஏழினமு மாதருக்கு இனமிட்டே தான்கொடுத்து நாளுவந் தபோதே நாரணருங் கொண்டாடி வாய்த்த திருநாள் மகிழ்ச்சையுட னடக்க ஏற்றகுலச் சான்றோர் எல்லோரும் வந்துநிற்க பிள்ளை தனையெடுத்துப் பெண்ணார்க ளேழ்பேர்க்கும் வள்ளலந்த மாலும் மனதுற்றி ருக்கையிலே கண்ணான தெய்வக் காரிகைமா ரெல்லோரும் விண்ணாண மாக வீரநா ராயணரை கண்ணரே கரிய மாலே காரணக் குருவே அய்யா இண்ணெங்கள் மதலை யேழும் எடுத்துநீ ரினம தாக விண்ணெங்கு மகிழத் தந்து வெற்றியா யாண்டு கொள்ளும் மண்ணெங்கு மளந்த மாலே மகாபரக் குருவே யென்றார் நாதக் குருவே நாடுமெங்கள் மன்னவரே மாதவரே யின்று வந்தநாள் நன்றெனவே  இப்போ மதலை எடுத்து மிகத்தாரும் செப்போடு வொத்த திருமாலே யென்றுரைத்தார் நல்லதுதான் பெண்ணே நாட்டு நருளறிய  முல்லை வனத்தில் முன்னேநீர் பெற்றுவைத்த மதலைக ளேழும் வளர்த்து இத்தனைநாளும் குதலைப் பிராயமதாய்க் கொண்டுதிரிந் தேவளர்த்தேன் பாலமு தூட்டாமல் பருவனத்தில் நீங்கள்விட்டுக் கோல வனத்தூடே குதித்தோடி மீண்டுவந்தீர் என்னென்ன பாடு யானிந்தப் பிள்ளையினால் பொன்னனயக் கன்னியரே புகலவுந்தான் கூடிடுமோ தெய்வேந் திரன்பசுவைத் திரையாகக் கொண்டுவந்து மொய்வனத்தில் பாலுகொண்டு மிகக்கறந்து மக்களுக்கு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(453,'அம்மை மார்களுக்கு மதலை ஈதல்','ஊட்டி வளர்த்தேன் உம்பர்கோ னுமறிந்து மாட்டிடவே யென்னை மாபடைக ளோடுவந்தான் தப்பி யவன்றனக்குத் தான்வளர்த்தேன் பிள்ளைகளை ஒப்பரியப் பாலமிர்தம் ஒருநா ளொழியாமல் மைப்பெரிய தாலம் வகுத்து மிகவளர்த்தேன் மக்களுக்காய்ப் பாடுபட்டு வைகைமண் ணுஞ்சுமந்து சொக்கரெனுஞ் சமைந்தேன் சொந்தமக்கள் வாழவென்று பின்னுமிப் பிள்ளைகள்தான் பெரும்புவியை யாளவைக்கத்  துன்னுகெட்ட கலியில் தோன்றிக் குதித்துவந்தேன் வந்தான்சா ணான்குலத்தில் மாயனென்று மாநீசன் பின்தாக்கிக் கையைப் பிடித்து மிகஇறுக்கிச் சாட்டைகொண் டேயடித்துத் தடியிரும்பி லிட்டுவைத்துக் கோட்டி மிகச்செய்து கூறி மிகஅடித்து அரங்கு மணிமேடை யானதிலே யென்னைவைத்துப் பரங்குவிய நித்தம் பாதம் பணிவேனென்றான் சாணா ரிடத்தில் தான்போகா தபடிக்குக் கோணாம லெங்களுடன் கூடியிரு என்றுடித்தான் பெற்றபிள்ளை யெல்லாம் பெருகலைந்து போவாரென இற்ற விலங்கில் இருந்தேன் மிகக்கவிழ்ந்து சாலமுட னீசன் தான்படுத்தும் பாட்டையெல்லாம் பாலருக்காய் வேண்டிப் பாரறியப் பட்டேனான் பெற்றது போதுமென்று பெண்ணேநீ ரேழ்பேரும் மெத்த தெளிந்து மேவிவந்தீ ரிப்போது என்றுரைக்க மாலும் ஏந்திழைமா ரேழ்பேரும் மன்று ள்ளோர்களறிய மாத ருரைக்கலுற்றார் பிள்ளைகள்தான் நன்றாய்ப் பெருகி மிகவாழ வள்ளலெங்கள் மாலே மாறாத் தவசிருந்த காரணத்தைச் சொன்னால் கணக்கி லடங்கிடுமோ  பூரணத்தின் ஞானப் பெரிய திருமாலே ஆரு மொருதர் ஆராட்டங் காணாத');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(454,'அம்மை மார்களுக்கு மதலை ஈதல்','மேரு நிறைவனத்தில் மெல்லிய ரேழ்பேரும் ஒருவர்முக மொருவருக்கு ஒருரூபங் காணாமல் கரிய அவரவர்கள் காடு வடவுகளில் உடுகலைகள் கொய்து உற்றமுடி தான்விரித்துக் கடுகெனவே வூசிதனில் கைவிரலைத் தானூன்றி ஆசை யுறவற்று அக்கபக்கத் தாசையற்று மாசைக் கருவறுத்து மயக்கவெறி தானறுத்து பாச மறுத்துப் பக்கத் துணைமறந்து வாச மணமும் வாக்குப்பேச் சுமறந்து காடு நினைவறுத்துக் கனாப்பயங்கள் தானறுத்து வீடு நினைவறுத்து வெளிவீடு தான்திறந்து மலசலங்க ளற்று வாயுவுடப் போக்குமற்றுக் குலதலமு மற்றுக் கூறுகின்ற பேச்சுமற்றுப் பேச்சற்று மூச்சற்றுப் பெருவனத்தி லும்மருளால் ஏச்சதூச்ச மற்று ஈராறு காலம்வரை நிற்க வுடம்பை நெடும்புற்று தான்மூடிப் பக்க மால்முளைத்து புறாவினங்கள் தான்கூடி முட்டையிட்டுக் குஞ்சு மூன்றுநே ரம்பொரித்து  வட்டணியாய்க் காடு வளைந்தெங் களைச்சூழ்ந்து கடுவாய் புலிகரடி கனத்தகுட்டி களீன்று கடுகிமிகப் புற்றுக் கரையில் மிகவாழும் ஆனையது குட்டி அதுபயின் றெங்களுடத் தானநிறை புற்றருகில் தன்னால் மிகவாழும் பிள்ளையால் நாங்கள் பெருகத் தவசிருந்த உள்ளமைக ளெல்லாம் உரைக்க எளிதாமோ நாணமற்று ஊணுமற்று நாலுமூணு ஆண்டுவரை தாணருட தஞ்சமெனத் தவசு மிகப்புரிந்து பிள்ளைக ளாசையினால் பேருல கில்வந்தோம் வள்ளலெங்கள் மக்கள்தம்மை மாயவ ரேதாரும் தந்து புவியாளத் தலைவரே யும்முடைய ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(455,'அம்மை மார்களுக்கு மதலை ஈதல்','விந்துவழி மக்களையும் விரைவாய் மிகத்தாரும் தாருமென மாதரெல்லாம் தாழ்மையுட னீதுரைக்க ஆருநிக ரொவ்வா அச்சுதரு மேதுரைப்பார் மாதேநா னிந்த வையகத்தில் வந்தவுடன் பாரேழில் நம்முடைய பஞ்சவர்கள் தம்வழியில் தேர்ந்துந்தன் மக்களிலே சீசனென நான்தெரிந்து கூர்ந்தெந்தன் கையதுக்குள் கொள்ளுகிறேன் வேலையது இன்னாபா ரென்று ஏழுபேரையு மெடுத்து  நன்னகரி பார்க்க நாத னிடுப்பில்வைத்து வட்டமிட்டு ஆடி மாதரொவ் வொருவர்க்கொரு கிட்டவிட்டுக் காட்டிக் கிளிமொழிமார் கைக்கொடுத்தார் பாலரையும் பார்த்துப் பாவையரைப் பார்த்தவுடன் சீலமுள்ள லெட்சணமும் சிற்றிடையு மொப்பனையும் ஒப்பமென் றெல்லோரும் உம்பருந் தாமகிழ்ந்தார் செப்பமுள்ள மாதர் சிரித்து மனமகிழ்ந்து அன்றுபெற்ற பிள்ளை ஐந்திரண் டானதிலே மன்றுதனை யாளும் மாபாவிச் சோழனவன் கொன்றானிரு பேரைக் கூடைதலை மீதில்வைத்து சொன்னீரே சுவாமி தோன்றியிப்போ வந்தாரோ தேன்மொழியே மாதே சொன்னபிள்ளை ரண்டுடைய மானதிய மக்களென மாயவருந் தானுரைக்க அப்போது பெண்கள் அச்சுதரைத் தான்பார்த்து இப்போ வரவில்லையே எப்போ வருவார்கள் மைப்பெரிய கண்ணே மாதரென் தேவியரே செப்பினீரே நீங்கள் சென்றமக்க ளெப்போவென்று பொல்லாத நாட்டுப் பொன்று கலியைமுடித்து நல்லதர்ம நாடு நான்தோன்ற வைக்குகையில் நடுஞாயங் கேட்டு நடுத்தீர்ப்புச் செய்யுகையில்  கெடுவேலை செய்த கேள்வியில்லாச் சோழனையும் ஆக்கினைகள் செய்து ஆழநர கமதிலே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(456,'அம்மை மார்களுக்கு மதலை ஈதல்','தாக்கியும் மக்களைநான் சடத்தோ டெழுப்பிமிகத் தருவேன் குருசுவாமி தன்னாணை மாதர்களே மருவினிய மாதே வாக்கிதுவே தப்பாது என்றாதி நாதன் ஈதுரைக்க மாதரெல்லாம் நன்றாய் மகிழ்ந்து நாடிமக்க ளையெடுத்து மடிமீதில் வைத்து மலர்ந்முகத் தோடணைத்துக் கண்ணோ மணியோ கரடரா சன்மகனோ விண்ணோர்கள் மெச்சும் வேதப் பரஞ்சுடரோ தோணார்க் கரியத் துய்யதிரு மாலீன்ற சாணாரோ நாயகமோ சகலகலை கற்றவரோ தெய்வ யுகக்கன்றோ திசைவென்ற சான்றோரோ வையம் புகழவந்த மக்களே என்றுசொல்லிச் சந்தோச மாகத் தார்குழலா ரேழ்பேரும் புந்தி மகிழ்ந்து பூரித் தகமகிழ்ந்தார் நாங்கள்செய் ததவத்தில் நாலிலரைப் பங்குவரம் வாங்கினோ மையா மாயவரே யின்னமுண்டு நாடாள வைக்க நல்லவரந் தந்ததுண்டு தாடாண்மை யுள்ள சங்கரரும் வந்திருந்து  என்றுரைக்க மாதர் எம்பெருமாள் நல்லதென்று இன்று வரைத்ததுவும் இலக்கதிலே ஆகுமெனச் சீதைக்கு நல்ல சிறந்தமக னையெடுத்து மாதுக்குத் தான்கொடுத்தார் வையகத்தோருங் காண மணமுகித்து மாதருக்கு முன்வகுத்த மைந்தரையும் குணமுடனே காட்டிக் கொடுத்தாரே யெம்பெருமாள் நல்ல திருநாள் நாள்கழித்து மற்றாம்நாள் வல்ல கதிரோனும் வந்ததுகா ணம்மானை கதிரோனுந் தோன்றிக் கங்குலவெளி யாகுகையில் திருநா ளிகனை செய்து நிறைவேற்றி மக்களெல்லாஞ் சூழ வந்து மிகப்பணிந்து மிக்கஅவர் வீட்டில் விடைவேண்டித் தாம்போனார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(457,'அம்மை மார்களுக்கு மதலை ஈதல்','கைக்குள்ளே நிற்கும் கரியச் சன்மாரும் மைக்குழல் மாரோடும் மகிழ்ந்திருந்தா ரம்மானை பின்னுஞ் சிலநாள் பெரியதிரு நாள்நடத்தி மன்னுகந்த நாரணரும் மாதரொடு வீற்றிருந்தார் பெண்க ளவரவர்க்குப் பெரிய அரங்குவைத்துத் தங்கள் தங்களுக்குச் சாமான்க ளுங்கொடுத்துப் பால்பவிசுங் கொடுத்துப் பாக்கியங்கள் மிகுவாய் மாலவர்கள் மனையில் வந்து அமுதருந்தி   இன்றொரு வீட்டில் ஏற்ற அமுதருந்தி அன்றிரா அங்கே அனந்தமால் பள்ளிகொண்டு பின்னுமொரு மாதருட பொன்னரங்கில் வந்திருந்து அன்று அமுதருந்தி அன்றிரா அம்மனையில் பள்ளிகொண்டு இப்படியே பாவையர்கள் வீடோறும் துள்ளியே மாயன் திருவிளையாட் டுமாடி மங்கையர்கள் காணாமல் மறுமனைக ளும்புகுந்து கொங்கைமட வாரோடு கூடிவிளை யாடிடுவார் மாயன் விளையாட்டை வகைப்படியே சொல்லவென்றால் வாய்ந்த வுலகில் வளர்ந்தபனை யோலையில்லை கூடி மடவாரோடு குவிந்துவிளை யாடுகையில் நாடி மடவார்கள் நாயகிமார் தங்களுக்குள் ஒருவர்க் கொருவர் உபாயமாய்ப் பார்த்திருந்து திருமருக ரோடே தெய்வமட வார்வெகுவாய்ச் சண்டை பிடிப்பார் தந்ததுபோ ராதெனவே கண்டமட வாரோடும் கனிவாகப் போறீரென அவள்வீட்டில் போறீர் அவளோ டதிகமுமாய் உபகாரஞ் செய்யுகிறீர் உள்ளாகத் தானிருந்து என்னோடு பேச்சு இப்படியே செய்வீரென மின்னோ வியங்கள் வெகுவாகக் கத்திடுவார்  சாமியொடு வெகுவாய்த் துரைத்தனங்க ள்தான்பேசி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(458,'அம்மை மார்களுக்கு மதலை ஈதல்','நாமகலப் போவோம் எனநடந்து மக்களுட வீட்டி லவர்போவார் வீரமால் தானறிந்து கூட்டி வரப்போவார் குளிர்ந்த மொழியுரைத்து அள்ளித் தருவேன் அனந்தவரா கன்பணமும் வெள்ளியும் பொன்னும் மேன்மையுள்ள சீலைகளும் உந்தனக்குப் போதும் உடைமை மிகத்தருவேன் அந்தப்பெண் ணார்களுக்கு அதுக்கதுபோ லேகொடுப்பேன் எழுந்திருநீ யென்று எட்டிமிகக் கைப்பிடித்துக் குளிர்ந்தநய வார்த்தைக் கோதைமின்னார் தாங்கேட்டுச் சிரித்து மகிழ்ந்து தேற்றலிது நன்றெனவே உருத்துமிகச் செய்வதெல்லாம் ஓவிய முமறிவேன் ஏன்காணு மும்முடைய இந்திரசால மெல்லாம் கோன் கிரிவேந்தே கொஞ்சமெனக் குத்தெரியும் மருட்டா தேயுங்காண் மாய்மால முந்தெரியும் உருட்டா தேயும்போம் உமதுதொழி லுந்தெரியும் எனக்கேற்ற வார்த்தை இதமதிமாய்ப் பேசிடுவீர் தனக்கேற்க நெஞ்சம் தானறியா தேயிருக்கும் கன்னி யுமதுடைய கருத்தெல்லாம் நானறிவேன் நின்னு சடையாதேயும் நீர்போவு மென்றுரைப்பாள்  அப்போது மாயன் அதுக்கினிய வார்த்தைசொல்லி இப்போது நீயும் எழுந்திருந்து வாவெனவே மாய னழைக்க மாது மெழுந்திருந்து நாயன் பிறகே நடந்து மிகவருவான் வந்து வளர்பதியில் வாழ்ந்துண் டினிதிருப்பார் சந்தோ சமாகத் தண்மை வெகுகுணமாய் இப்படியே பெண்களோடு இகனை மிகநடத்தி ஒப்புரவாய் மாயன் உகந்துண் டிருக்கையிலே செந்தூ ரலையில் தேவனுதித் தன்றுவந்து முந்துமூலப் பதியில் உவரிக் கரையிடத்தில் சென்றங் கிருந்து சிவலிங்கத் தானமைத்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(459,'அம்மை மார்களுக்கு மதலை ஈதல்','மன்றுக்குள் கொஞ்சம் மறைத்துவைத்து மாயவரும் நான்தவ சிருந்து நாட்கள்கொஞ்ச மேகழித்து வான்புதுமை யற்புதங்கள் மாநிலத்தோர் தாமறியச் செய்துதெய்வ மாதர்களைச் சிறந்த மணமுகித்து தெய்திதனி லிங்கே திருநாளைக் கொண்டாடி வருவே னெனச்சிலைக்கு வாக்குமிகக் கொடுத்துத் திருவேற்றி வைத்த தேதியின்று வந்ததென சும்மா இவரைத் திருப்பதியில் வாநீயென்று நம்மா லுரைத்தால் ஞாயமில்லை யென்றுசொல்லிக்  கொஞ்ச மொருசூத்திரம் கூர்மையாய்ச் செய்யவென்று அஞ்சலெனச் சாமளா தேவிதனை யழைத்துக் கன்னியிலே ரண்டு பேரைக் கலக்கமிட்டு என்னுடைய மக்களிலும் யாம முறைப்படியே கொஞ்சமது வாந்தி கொடுத்துயிரைத் தான்வாங்கி மஞ்சறை யில்வைத்து வாகாகக் காருமென்று விடைகொடுக்க நாதன் வேண்டினாள் தேவியவள் திடமுடனே சொன்ன சொல்வாக் குரைப்படியே கன்னியரில் ரண்டு கடத்தினாள் மாதேவி மின்னி னொளிமதலை வீரத் தனமாக நாலுமூ ணுடனே நகட்டினாள் மாதேவி உடனே சனங்கள் உடையவரே தஞ்சமெனத் தடமேலே வீழ்ந்து சுவாமி அபயமென்றார் அவதாரத் தின்படியே அன்றமைத்த பெண்கொடியை உபகார மாமணங்கள் உடையவரே நீரருளி வையகத்தோ ரெல்லாம் மனமகிழ்ந் திருக்கையிலே செய்யஇந்த மாயம் செய்வாரோ மாயவரே  ஆரார்க்கு ஏற்கும் அரிவையர்கள் மக்களுந்தான் போரா வழியானப் பொல்லாப்புச் செய்தாரோ ஆதியே யவர்கள் அநியாயஞ் செய்ததென்ன  சோதியே யென்று தொழுதார் மடவார்கள்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(460,'அம்மை மார்களுக்கு மதலை ஈதல்','அப்போ தொருமகன்றான் அஞ்சாறு நாளதுமுன் சொப்பனங்கள் கண்டதுவைச் சொல்லாமல் தட்டழிந்து அன்றைப் பொழுது அவன்வந் தடிதொழுது நன்றினிய எங்கள் நாரணரே நாயகமே பத்துநா ளுண்டும்நான் படுத்திருக்கு மவ்வளவில் பெற்றுச் சழிந்தவர்போல் பெரியபூவண் டர்வடிவாய் வந்தென் றனோடு வளப்பமென்ன சொல்லலுற்றார் சந்தமுடன் நானுரைத்தச் சட்டமற வாதபடி கொஞ்சநாள் தோப்பு குளிர்ந்தபதி யானதிலே அஞ்சிரண் டாண்டு அதில்வாழ்ந் திருந்துபின்னும் மூலகுண்டப் பதியில் மிகஇகனை செய்யெனவே ஏலமே சொன்னதெல்லாம் எண்ணமதி லில்லாமல் பெண்களைக் கண்டவுடன் பூத்தான மாய்மகிழ்ந்து கண்ணாட்ட மறந்து கலியை முடிப்பதற்கு நினைவு மயர்ந்து நிலைபேர்ந் திருக்குகிறான் நாட்டுக்குற்றங் கேட்க நானயைச்சு வைத்தற்கு கோட்டு மடவாரைக் குறிப்பாக எண்ணிமிக எண்ணி யிருக்குகிறான் இப்போது அங்கேசென்று நண்ணிமூ லப்பதியில் நாடிவரச் சொல்லிவிடு  வரவில்லை யானால் மாதருடன் மக்களையும் சரமதையும் வாங்கிடுவேன் சுவாமியுடன் போயுரைநீ என்றுசொல்லி நாளும் எட்டுரண் டாச்சுதையா அன்று வுரைக்க அய்யாமிகக் கோபமுற்று பாவிப் பயலே பகராம லித்தனைநாள் மேவி யிருந்தாயே மெல்லி யிழந்தேனே தீட்டிவைத்த சொற்பனத்தைச் சொல்லா திருந்ததினால் கூட்டில் மிகவாழ்ந்த கிளியைமிகத் தோற்றேனே முந்திவந்து நீயும் மொழிந்ததே யுண்டானால் என்ற னிளமயிலை இழந்துமிக வாடேனே பூவண்ட ரானால் பெரிய பிதாவல்லவோ மாவண்டப் பயலே வந்துசொல் லாதிருந்தாய்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(461,'அய்யா மூலகுண்டப்பதி எழுந்தருளல்','இப்போ பதிக்கு எழுந்தருள வேணுமென்று மைப்போடு வொத்த மாதரொடு மக்களுமே கூடி நடந்தார் கூண்டரிய பொற்பதியில் தேடிமூ லப்பதியின் சிறப்பெல்லாந் தான்பார்த்து ஏலமே இப்பதியில் எட்டுநாட் குள்ளேவந்தால் மாலவரின் மக்களுக்கு மாய்வுசற்றும் வாராதே கெடுத்தானே சொர்ப்பனந்தான் கெணித்தவுடன் சொல்லாமல் கொடுத்தோமே நம்முடைய குலமக்கள் மாதரையும்  இப்பதியில் வந்தால் எள்ளளவுந் தோசமில்லை எப்பதியு மிப்பதிக்கு ஒவ்வாது என்றுசொல்லி எல்லோ ருடனே எம்பெருமா ளுமகிழ்ந்து அல்லல் வினைதீர்ந்து அதில்வாழ்ந் திருந்தனராம் தோப்புப் பதிபோல் தொட்டிக்கட் டம்பலமும் கோப்புப் புரையும் குளிர்ந்த மணியரங்கும் பள்ளி யறையும் பார்சவுககை மண்டபமும் துள்ளி யிகனை சுகசோ பனம்வரவே பூம்பந்த லும்பெரிய புகுவீர மேடைகளும் ஊண்புரையும் நல்ல உகத்தீர்ப்பு மேடைகளும் சிணமாகச் செய்து சிறந்து மிகஇருந்து குணமாக மாயன் கோதையொடு வாழ்ந்திருந்தார் வாழ்ந்து திருநாள் வாரஞ்செவ் வாச்சைமுதல் ஆழ்ந்த திருமால் அவதாரக் காட்சியுடன் கும்மி யிகனை குவலயத் தீர்ப்புரைத்துத் தம்மியல்பு கொண்டு சுவாமிவெண் பட்டுடுத்தித் தங்கக்குல் லாஅணிந்து தாமரிய நீராளம் எங்கு மகிழ எம்பெருமாள் தானணிந்து ஆயிழைமா ரவர்க்கு அழகுவெண் பட்டுடுத்தி வாயிதமாய் மாயன் மகிழ்ந்துமிகக் கொண்டாடி  மங்களமா யிகனை மகாயிகனைத் தான்கூறி சங்க மகிழ சுவாமிதிரு நாள்நடத்தி பாக்கியங் களோடே பவிசா யிருக்கையிலே தாக்கமிக மாயன் சந்தோச மாய்மகிழ்ந்து பொல்லாக் கலிநாடு பொன்றிவருங் காலமதில் வல்லாமை யான வாய்த்த பகவதிக்கும் மாது உமைக்கும் மண்டைக்காட்டாள் பார்வதிக்கும் தீதகலும் வள்ளி தெய்வானை நாயகிக்கும் கலிமுடியு முன்னே கலியுகத்தோர் கண்காண வலியான மாதர்களை மணமுகிக்க வேணுமென்று முன்னுரைத்த ஆகமத்தின் முறைநூற் படியாலே நன்னூல் வழியாய் நாமுகிக்க வேணுமென்று');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(462,'அய்யா கன்னிபகவதி பதியேகல்','உன்னித் திருமால் உள்ளில் மிகஅடக்கி வள்ளலந்த மாகுமரி வாழும் பகவதியை மாயமாய்க் கொண்டு வரவேணு மென்றுசொல்லி உபாயமாய் மனதில் உடைய பரனினைத்தார் கிழவனா கச்சமைத்து கிருதஞ்செய்ய வேணுமென்று தளதளெனத் தேகம் தன்னுடம் புமினுக்காய் மெக்குவாய் பொக்கனுமாய் முடியுமிக வெண்ணரையாய் கக்க லிருமலுமாய்க் கையதிலே கோலூன்றி  தள்ளாடி மெள்ளச் சன்னை மிகப்போட்டுப் பிள்ளாய்ப் பகவதியே பேத்தியென்றன் பொன்மகளே கண்ணு மயிலே கனகவொளி மாமணியே பெண்ணும்பிள்ளாய்ப் பேத்தி பேரனுக்குக் கஞ்சிவிடு என்றுசொல்லி மெள்ள ஏந்திழையாள் தானிருக்கும் குன்றுமணிக் கோவிலுக்குள் குன்னிமெள்ளச் சென்றனரே பேத்தியென்ற சொல்லைப் பிரியமுடன் நாயகியும் காதில் மிகக்கேட்டு கன்னி மிகப்பார்த்து ஆர்காணும் நானிருக்கும் அரங்குக்குள் வாறதுதான் பாரழியும் நாளோ பையரங்குக் குள்ளேவந்தாய் எங்கிருந்து நீதான் எவ்வூ ருன்பேரேது கிங்கிலுக்க வந்தவனோ கிழவன்தா னோவுரைநீ அம்மா என்பேத்தி ஆயிழையே நாயகியே சும்மா மயங்காதே சூட்சமொன்று மில்லையம்மா பரமார்த்த மம்மா பழனிமலை யென்றனக்கு விதமாற்ற மில்லை வினோதவித்தை தானுமில்லை இலாடகுருவம்மா இராமனென்றன் பேர்பேத்தி திலாடம திலிருந்து தீர்த்தமிங் காடவந்தேன் வந்தேனான் கண்மயக்காய் வழிதப்பி யிங்கேதான் உந்தன் திருப்பதியை ஒருஅகர மென்றிருந்தேன்  மகளே யென்பேத்தி மாஞால மொன்றுமில்லை ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(463,'அய்யா கன்னிபகவதி பதியேகல்','செகமெல்லாந் தீர்த்தம் சென்றேனா னிவ்வயதுள் உன்னுடைய தீர்த்தம் உகந்தாட இப்போவந்தேன் பின்னுமொரு பேச்சு பேசுகிறா ரிவ்வுலகில் இன்றுந் தன்பதியில் இருந்திந்த ராவிடிந்தால் நின்றுநின்று போயாலும் நிச்சயம்பார்த் தேமகிழ்ந்து என்னுடைய வூருக்கு ஏகவே ணும்பேத்தி பொன்னு மகளே பேத்தியெனப் போத்திசொன்னார் பேத்தியென் றுரைத்த போது பொன்பக வதித்தாய் மெச்சிப் போத்தியே யுமக்குக் கொஞ்சம் பொரிமாவு பிசைந்து தாறேன் ஏற்றுநீ ரிளைப்பு மாறி இன்றிரா கழித்து ஏகும் சாற்றிய மொழியைக் கேட்டுத் தனதுள மகிழ்ந்தார் போத்தி அம்மா நீசொன்ன அருமை யெனக்கதிகம் மும்மால்க்கு மேற்று முகுந்தன் பதம்பெறுவாய் தாகமல்லால் பசிகள் தானெனக்கு இல்லையம்மா தேகமது வாடாமல் செலந்தந்தால் போதுமென்றார் பால்மோரு போலே பதங்கொடுத்து மாகுமரி காலாறிப் போமெனவே கன்னி மிகவுரைத்தாள் தாக மதுதீர்ந்து தானிருக்கு மப்பொழுது நாகரீகக் குமரி நாயகியு மேதுரைப்பாள்  போற்றி யுமக்குப் போதவய தானதினால் நாற்றிசைக ளெங்கும் நடமாட்ட மாயிருக்கும் ஏதேது தீர்த்தம் இகனைபல மாயுளது மாதோடே சொல்லும் மாபெரிய போத்தியென்றாள் உடனே கிழவன் உளமகிழ்ந்து நன்றெனவே மடமாதே காசி மகாதீர்த்தம் நல்லதுதான் கன்னி மாகுமரி காசித்தீர்த்தம் நிகராம் பின்னு மற்றதெல்லாம் பிரமாண மப்படிதான் இப்போ தொருசெய்தி இராச்சியத்தில் சொல்லுகிறார் எப்படியோ நிசமாய் இருந்தாலது மேன்மையதாம் என்றுரைக்க நல்ல இளங்குமரி ஏதுரைப்பாள்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(464,'அய்யா கன்னிபகவதி பதியேகல்','விண்டுரைத்த ஞாயமதை விளம்புவீர் போத்தியென்றாள் அப்போது கிழவன் ஆச்சரிய மாய்மகிந்து இப்போது வேணும் இவளை மிகஇளக்க என்று மனதுள் இருத்தி மிகத்தெளிந்து மன்றுதனில் கேட்ட வளமைகே ளென்றுசொல்லி ஆயிரத் தெட்டாண்டாம் ஆனதொரு மாசியிலாம் வாயிதமாய்ச் செந்தூர் வாரிதனி லேபிறந்து வைகுண்ட மென்று வையகத்தில் வந்திருந்து கைகண்ட அற்புதங்கள் கனகோடி செய்கிறாராம்  தண்ணீ ரதுவாம் தான்கொடுக்கு முத்திரியாம் மண்ணிலுள்ளோர் யாரும் வந்து வணங்குறாராம் தண்ணீர் மண்ணீந்து சகலவினை தீர்க்கிறாராம் புண்ணிய தானங்கள் போதமிகச் செய்கிறாராம் குட்டங் குறைநோவு குருடூமை யானதுவும் கட்டங் கொடியக் கர்மமுதல் தீர்க்கிறாராம் மைந்தர் கொடுக்கிறாராம் மகாதர்மஞ் செய்கிறாராம் மனுப்பேரில் பேயை மாகிலுக்க மாயாட்டித் தனுப்பெலங்கள் வாங்கி சருவி லெரிக்கிறாராம் தெய்வமட வாரெனவே தேவியேழு பெண்களையும் வைய மறிய மாலையிட்டுக் கொண்டாராம் நித்தந் திருநாள்போல் நீணிலத் துள்ளோர்கள் மெத்தக் குமுக்காய் மிகவந்து கூடுறாராம் டம்மான வெடிகள் டகுடகென நாகசுரம் இம்மாத்திர மெனவே எண்ணவுங் கூடாதாம் காட்சிரெம்ப வுண்டாம் கலிமுடிக்க வந்தோமென்றும் பேச்சுமிகச் சொல்லி பிரான்யாமங் கூறுறாராம் இப்படியே பேச்சு இதுவுறுதி யானாக்கால் எப்படியுங் கர்த்தன் இவரெனவே சொல்லிடலாம்  அல்லாமல் கலியுகங்கள் அழியுகின்ற நாளையிலே சொல்லால் பெரிய திருமால் சொரூபமதாய்த்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(465,'அய்யா கன்னிபகவதி பதியேகல்','தெச்சணத்தில் வந்து திருவிளையாட் டாகுமென அச்சமறப் பேச்சென்று ஆதியிலே கேட்டதுண்டு அந்த முறைதானோ அவனியிலே கேட்கிறது எந்த விதமோ தெரியுதில்லை யென்பேத்தி போய்ப்பார்த் தால்தெரியும் பொன்மகளே பேத்தியென்றார் வாய்பார்த்த போது மாது மிகமகிழ்ந்து கண்ணரிய  போத்தி காரணத்து நற்போத்தி திண்ணமுள்ள போத்தி செப்புவதை நீர்கேளும் தெய்வ மடவாரைத் திருமணங்கள் செய்தாரென்று வையமது சொன்ன வளமுரைத்தீ ரென்போத்தி மாதரையும் பார்க்க மனவிருப்ப மாயிருக்கு ஆதரவாய்ப் போத்தி அதுவரையும் என்னையும்நீர் கூட்டிக்கொண்டு போவீர் கிழவனா னபோத்தி பூட்டியுந் தன்பிறகே பிள்ளைபோ லேவருவேன் செய்தியென்ன வென்று தேவி யுருக்கமுடன் செய்தி மிகவடைந்த தாதனோ டேயுரைத்தாள் அப்பொழுது லாடகுரு அவரேது சொல்லலுற்றார் இப்படியே சொன்னதற்கு யான்கூட்டிப் போயிடுவேன்  ஓடி நடக்க ஓட்டாது தள்ளாட்டம் கூடி நடக்கக் குறுக்குப் பெலக்காது பேசி நடக்கப் பிசகுமம்மா என்னாக்கு வீசி நடக்க விழிக்குக்கொஞ் சமறைவு கக்க லிருமல் கால்பெலக்க வொட்டாது சிக்கெனவே நடந்தால் செருக்கிரும லீளைவரும் இத்தனை துன்பம் இருக்குங் கிழவனுடன் சிற்றிடையீர் நல்ல சிறுபிரா யம்நடந்தால் ஒக்குமோ பேத்தி உன்னடையு மென்னடையும் பக்குவமோ நான்தான் பாதை நடப்பதற்கு நானடப்பேன் காதவழி நாலுநாள் தங்கலென நீயென்னோ டேநடந்தால் நிகராமோ பேத்தியென்றார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(466,'அய்யா கன்னிபகவதி பதியேகல்','அப்போது நல்ல ஆயிழையு மேதுரைப்பாள் நற்போடு வொத்த நம்முடைய பேராநீர் வேறாட்கள் கூடவெளியேறப் போகாது வீறாகப் போத்தி மெல்லநடை யாகிடினும் உம்மோடே நடக்க உள்ளாசை யாயிருக்கு எம்மாத்திரம் நாட்கள் எவ்வளவு சென்றாலும் பைய இருந்திருந்து பாதை மிகநடந்து நெய்யதியக் கன்னி நேரிழைமா ரேழ்வரையும்  பார்த்து வரவேணும் பண்டையுள்ள போத்தியென்றாள் சாற்று மொழிகேட்டு தாதன் மிகமகிழ்ந்து அப்படியே யானால் ஆயிழையே யுன்னருகில் பொற்பணிவே லைபுரியும் பெண்ணா ரறியாமல் இங்குள்ள பேர்கள் எள்ளளவு மறியாமல் கங்கைக் கரைவழியே காணாது போய்விடுவோம் போக வென்றாலும் பொழுது புறப்படுமுன் ஏகவேணும் நாமென்று இயல்பா யுரைத்தனராம் அப்போது கன்னி ஆயிழையும் நல்லதென்று இப்போ தெழுந்திரியும் இதுகடந்து போவோமெனச் சொல்லிக் கிழவன் சிரித்து மனமகிழ்ந்து நல்லகா ரியமெனவே நாம்போவோ மென்றுவுன்னி அரிஹரி கோவிந்தா அலைதுயின்றாய் போற்றுயென்று கரிஹரி கோவிந்தா காரியங்கைக் கொண்டோமென்று குன்னி மிகவெழுந்து கோலு மிகபிடித்து உன்னி யிருப்பும்விட்டு உடனே வெளியில்வந்து சன்னை மிகஇருமித் தள்ளாடித் தள்ளாடிப் பின்னே விழுவார்போல் போத்திமுவ் னிற்கையிலே மாது கதவெல்லாம் வாங்கி மிகஅடைத்துக் கேது விளைத்துவந்த கிழவன்பின் னாலேகி  வந்தவளை யழைத்து வாவா பிறகெனவே சந்தமுடன் லாடகுரு சாடைசெய்து முன்னடந்தார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(467,'அய்யா கன்னிபகவதி பதியேகல்','முன்னடக்கப் போத்தி மொய்குழலாள் பின்னடக்க அன்னநடைகள் விட்டு அலைவாய்க் கரைவழியே முட்டாங்கு மிட்டு முகந்தெரியா வண்ணமவள் கட்டாய்க் கவிழ்ந்து கன்னி பகவதியும் கிழவன் பிறகே கிளிமொழியாள் தானடக்க மலர்மாரி தூவ வாயு மரைவீச பூமி குலுங்காமல் பொருப்பு மசையாமல் காமி குமரி காலசையா மல்நடந்தாள் நடக்கும் வழிதனிலே நாயகியோ கேகேட்பார் வடக்குநா டுநமக்கு வசந்தான் வழிநடப்பு தெச்சணா பூமி திசைமாற்ற மாயிருக்கும் பச்சை வளையணிந்த பாவையரே பார்த்துநட திக்குத் திசைகள் தெரியுதில்லை யென்பேத்தி முக்கொரு மூலைதன்னை முன்னாடிப் போவோமென வாரிக் கரைவழியே வாய்த்தநேர் மேற்காகக் காரிருளாகு முன்னே கால்விரைவாய்ப் போவோமெனச் சொல்லச் சிறுகன்னி தியங்கிமெள்ள வாயுரைப்பாள் அல்லல் வினையோ ஆண்டவன் தன்செயலோ  வெளிகாணா தேயிருந்த மெல்லி வெளியில்வந்தால் வழிதான் தெரியுமோகாண் மாபெரிய போத்தியென்றாள் பதறாதே பெண்கொடியே பகவான் துணையுண்டுமடி இதறாதே நாமள் இவ்வழியே சென்றாக்கால் தெச்சணத்தில் பள்ளிக்கொண்டு தெய்வமட வார்களையும் நிச்சயம் மணம்புரிந்த நீலவண்ண ருண்டுமடி கலங்காதே கண்ணே கடற்கரையே போவோம் விலங்காம லிவ்வழியே மேற்குநோக் கிப்போவோம் என்றுசொல்லி மாதை இளக்கிமிகப் போகையிலே அறைந்த வாரி அதற்கு வடபுறமாய் இடம்மான வோசை டகுடகா வென்றுமிக இடம்மானம் நாகசுரம் இரைச்சல் மிககேட்டுச்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(468,'அய்யா கன்னிபகவதி பதியேகல்','சற்றே பொறுபேத்தி சத்தமொன்று கேட்குதிங்கே மெத்த இரைச்சல் மேளத் தொனிகேட்குக் குரவை யொலியும் குத்து வெடியுங்கேட்குப் பரசா திக்குரல்கள் பலவிதமாய்க் கேட்குப் பார்த்து நடப்போம் பாதைவிட் டுவிலகிச் சற்று வடக்கேறி தான்பார்த்துப் போவோமென என்றுரைக்கப் போத்தி இயம்புவாள் நாயகியும் நன்றுநன்று கிழவா நடவுமுன் னேயெனவே  தண்டையணி மடவாள் சடைத்து முகம்வாடி கெண்டையக்கண் மடவாள் கிள்ளைபோ லேயுரைத்தாள் குன்னிமெள்ளக் கிழவன் கோலூன்றிக் கோலூன்றி மின்னிடையே பையவென்று மெல்லவடக் கேறினராம் வடக்கேறி மாதை வலம்விட்டுத் தான்கூட்டி நடக்கையிலே ஆளிரச்சல் நாடி மிக்ககேட்டு அன்ன நடையழகி அமிர்தவாய்ச் சொல்லழகி சின்ன இடையழகி திசைமயங்கி யேதுரைப்பாள் கண்ணினிய போத்தி காரணத்து நற்போத்தி நண்ணினிய போத்தி நானுரைக்குஞ் செய்தியைக்கேள் சனக்கூட்டம் ரெம்பத் தான்காணு மானதினால் எனைக்கூட்டிக் கொண்டு இதில்விட் டகலாதேயும் திக்குத் திசையெனக்குத் தெரியாது கண்டீரே பக்குவங்கள் சொன்னேன் பாத மடைக்கலமே கைக்குள் விட்டுக்கொண்டு கன்னியரைத் தான்காட்டிப் பைக்குள் வைத்துக்கொண்டு பகலோ னுதிக்குமுன்னே என்பதியில் கொண்டு இருத்தியெனை வைத்தீரால் பொன்பதி னாயிரத்தால் புனைந்தவொரு தாவடந்தான் உம்முடைய மார்பில் உடனணிவேன் கண்டீரே  எம்முடைய மானம் இருக்குதுகா ணும்மிடத்தில்  ஒருவ ரறியாமல் உபாயமாய்க் கொண்டுவென்னை திருவனப் பதிக்குள் சேர்த்துவையும் போத்தியென்றாள்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(469,'அய்யா கன்னிபகவதி பதியேகல்','பேத்தி பதறாதே பெரியபதி சேர்த்துவைப்பேன் காற்று அசுங்காத கண்ணர்பதி சேர்த்துவைப்பேன் மலங்கா தேபேத்தி வான்பதியில் வாழ்ந்திருக்க கலங்கா தேசேர்த்துக் கண்ணாணை வைத்திடுவேன் என்றுரைக்கப் போத்தி இளவரசி யுமகிழ்ந்து அன்றந்தக் கிழவன் அருகிலொண்டித் தானடந்து ஆளுக் கிடைநடுவே ஆயிழையும் பேத்தியுமாய்த் தோளுப் பிடித்தாற்போல் தோகையரும் வந்தனராம் வந்தனள் பதியின் சீரும் வளர்மணி மேடைக் காலும் சந்தன வாடை வீசும் தலமது நருட்கள் சீரும் செந்தமிழ் தர்மச் சீரும் சிவாலயத் தெருக்கள் நேரும் புந்தியில் மகிழ்ச்சை கூர்ந்து போத்தியை மெச்சு வாளே போற்றிநீ ருரைத்த தெல்லாம் பொய்யில்லை மெய்ய தாகும் சாற்றினீர் பின்னுந் தெய்வத் தார்குழ  லுண்டு மென்று பார்த்துநீர் நமக்குக் காட்டும் பைங்கிளி மாரை யெல்லாம் சீத்துவ மாக எந்தன் சிந்தையி லறிய என்றாள் உடனந்தக் கிழவன் தானும் உள்ளது தானே யென்று மடமயில் குமரி தன்னை வாவென அழைத்துக் கூட்டி  குடதன முடையா ரானக் கோதையர் கோவில் புக்கி நடைதனில் நின்று கொண்டு நாரிய ரிவர்தா மென்றார் பார்த்தந்த மடவா ளானப் பகவதி மாது சொல்வாள் சேர்த்திந்த மடவார் தம்மைத் திருமணஞ் செய்த மன்னர் ஏற்றெந்த இடத்தே வாழ்வார் இதுநமக் கறியக் காட்டிக் சாற்றிந்தத் தலமுங் காட்டித் தன்பதி போவோ மென்றாள் ஆதிக் கிழவா அரிவைகன்னி மார்களையும் நீதி யுடன்கண்டேன் நேரிழைமார் தம்மைமணம் செய்த கணவரையும் சென்றுபார்த்த தேநாமும் நெய்தரிய நம்பதியில் நாம்போவோம் வாருமையா என்றுரைக்க மாது இளங்கிழவ னேதுரைப்பார் நன்றுநன்று பேத்தி நாயகிமார் மன்னனையும் இன்றுநீ காண இப்போது காட்டுகிறேன் பார்த்துக்கோ வென்று பையவா வென்றுசொல்லிக் கோற்றுக் குருவம்பலத்தில் கும்மிமிகக் கொண்டாடி நாட்டுத்தீர்வைக் கணக்கு நவின்றிருக்கும் வேளையிலே காட்டிக் கொடுத்தார் கரியமால் நாரணரை');");


        //16th Day
        db.execSQL("INSERT INTO akilathirattu2 VALUES(470,'பகவதிக்குச் சச்சிதானந்த வடிவைக் காண அருளல்','பகவதியாள் வந்து பார்க்கின்ற அப்பொழுது சுகபதியாள் கண்ணதுக்குத் துய்யத் திருமாலும் பச்சை நிறமும் பவளவாய்க் கனியிதழும்  நச்சரவில் பள்ளிகொண்ட நல்லசொரூ பம்போலே தண்டை யணிக்காலும் தாமரைக்கை பொன்முகமும் தெண்டையக்கண் மாலும் கிரணவொளி ரத்தினம்போல் செம்பவள வாயும் சிறந்தபீதாம் பரத்துடனே அம்பலத்தே நின்று ஆனந்த மேபுரிய ஆனந்த மான அழகு பகவதியும் தானந்த மான சச்சிதானந் தவடிவைக் கண்டந்த மாது கண்மூடா வண்ணமங்கே நின்று அவள்பார்த்து நெஞ்சந்தடு மாறினதை கிழவ ரறிந்து கிளிமொழியை விட்டகல பழவ ரொருசொரூபம் பாச மிகஎடுத்துக் கைநெகிழ்ந்து மாயன் கனகபதி மீதில்வந்தார் மைவளைய மாது மாறிமன தேதிரும்பிப் பார்த்தா ளருகில் பண்பாக நின்றதொரு கூத்தாடித் தாதனையும் கோதைமிகக் காணாமல் பாவிக் கிழவன் பாதங்கள் செய்தானே லாவிலாவிக் கொஞ்சம் லாவிமிகத் தேடலுற்றாள் அய்யோ கெடுத்தானே ஆதிக் கிழவனம்மை மெய்யோ னென்றிருந்தோம் வெளியேற்றி விட்டானே எங்கே யினிக்கண்டு என்பதியில் போவேனான்  திங்க ளுதிக்குதல்லோ சேவல்குர லாகுதல்லோ நிலவெளிச்ச மாகுதல்லோ நிற்கிழவனையுங் காண்கிலையே  குலமுழிவ தாச்சே குவலயங்கே டாகுதல்லோ மான மழிந்தாச்சே வையகமும் பேசாச்சே தான மழிந்தாச்சே சங்கையினி கெட்டாச்சே இக்கிழவ னம்மை இளக்கியிங்கே கொண்டுவந்து மொக்கை மிகக்கெடுத்து மோசஞ்செய்ய வந்தானோ என்னபோ லாச்சு என்தலையி கூறிதுவோ அன்னம்போல் வார்த்தை ஆசார மாயுரைத்துச்  சந்தியில்பந் தாக்கினானே சளக்கிழவன் நம்மையின்று');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(471,'பகவதிக்குச் சச்சிதானந்த வடிவைக் காண அருளல்','சுந்தரியு மொத்தச் சோர்ந்துமன தேகலங்கி புலம்புவாள் தனியே பொன்னும் பகவதியும் சிலம்பணியும் நாயகியும் தியங்கிப் புலம்பினளாம் என்னைப் படைத்தவரே ஈசுரரே தஞ்சமென்று பொன்னனைய மாதும் புலம்பித் தவிக்கலுற்றாள்  தலையி லெழுதுஞ் சங்கரரே தஞ்சமென உலையில் மெழுகதுபோல் ஓவியமும் உள்ளுடைந்து இனம்பிரிந்த மானதுபோல் ஈயமது போலிளகி மனம்பிரிந்து மாது மதலை யழுதாப்போல் பிறப்பித்தச் சீமானே பிஞ்ஞகனே தஞ்சமென்று சிறப்பித்த மாது தியங்கி மயங்கலுற்றாள் மாது பகவதியாள் வயசுபதி னாறுடையாள் சாருமிட மற்றார்போல் தனியே புலம்பலுற்றாள் கண்ணினிய சொல்லாள் கரியமகா ஈசொரியாள் எண்ணுஞ் சாகாமல் இருக்கும் பகவதியாள் தாயில்லாப் பிள்ளை தயங்கினாற் போலேநின்று வாய்குளறிக் கண்ணீர் வடிய மிகஅழுதாள் சோடு பிரிந்த துய்யப்புறா வுமிரங்கிப் போடுகின்ற சத்தம் போலே குரல்நிகழ்த்தி உள்ளுக்குள் நோக்கும் ஓவியத்தின் தன்குரலும் எள்ளுக்குள் ளெண்ணெயென இருந்து மிருக்கலைத்தான் சங்குள் பிறந்த சமயத்திருமணியும் எங்கும் புகழ்பெற்ற ஈசொரியாள் மாமயிலும் தனியே யிருந்து தனதுள் ளகமடக்கி மனிதர் காணாமல் மறைந்துநின்று மாதுநல்லாள் கிழவன் தனைத்தேடி கிளிமொழி யாள்பார்த்துக் களப நிறத்தாள் காணாமல் வாடலுற்றாள் கூட்டிக்கொண்டு வந்த கூனுக் கிழவனையும் காட்டித்தர மாட்டீரோ கன்னிகுல மாதர்களே  ஆதிமகா லட்சுமியே அண்ட மளந்தவளே  சோதி மகாபரனே துணைசெய்ய மாட்டீரோ அன்னபட்சி மாமரங்காள் ஆவினங்கா ளூர்வனங்காள் என்னைமிகக் கூட்டிவந்த இளங்கிழவனைக் காட்டித்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(472,'பகவதிக்குச் சச்சிதானந்த வடிவைக் காண அருளல்','தாரீரோ என்றனுட சந்தபதி நான்போக வாரீரோ என்மனது வாட்டந் தவிர்ப்பதற்கு என்று புலம்பி இளங்குழலித் தேடுகையில் குன்றுமேல் திங்கள் குதிக்குமந்த வேளையதாம் வேளை யறிந்து மெல்லி பொன்வண்டதுபோல் சூழநிற்கும் புன்னைப் பூவிலொரு சூட்மதாய் இருந்தாள் பொன்வண்டாய் ஏற்றபக லேகும்வரைத் தருந்தார மார்பன் சுவாமி மிகஅறிந்து பகவதிக்கு மங்களங்கள் பாடி மகிழ்ச்சையுடன் சுகம்பெரிய மாயன் சோபனங்கள் கொண்டாட ஆடிக் களித்து அகமகிழ்ந்தா ரச்சுதரும் கூடிநிற்கும் பேரோடு கூறிமிக ஆடிடுவார் நாட்டை யழிக்க நமக்கு வொருமுகூர்த்தம் பேட்டைதர்மந் தோணப் பொகுதின் றென்றனக்கு என்று கும்மிபோட்டு இளங்குழலை யும்பார்த்து இன்று இருவென்று எம்பெருமா ளுமாட நல்ல பகவதியாள் நாடிப்பொன் வண்டதுபோல்  செல்லப் பதுங்கித் திசைமயக்க மாயிருந்தாள் பகற்பொழுது மாறி பகவா னடைந்தபின்பு உகப்படைப்பு மின்னாள் ஒருகுழந்தை போலாகி பத்து வயசுப் பிராயம்போ லேசமைந்து சித்துப் பலதுடையாள் சேர்ந்தவுயிர்க் கண்மணியாள் இரண்டு பொழுதாச்சே நம்பதியை விட்டிளகி பண்டு பதிக்கேகப் பாதைசற்றுங் காணலையே நாலுதிசை யுண்டுமென்பார் நமக்குசற்றுந் தோணலையே கோலூன்றுங் கிழவன் கூட்டிவந்த பாதையதும் சற்றுந் தெரியலையே தலையிலெழு துஞ்சிவனே முற்றுமிந்த நாடு முடிமாய்ந்து போவதற்கோ நரைக்கிழவ னம்மை நடுச்சபையில் கொண்டுவிட்டான் உரைத்திடுவா ரிந்த ஊரி லொருவசனம் மாப்பிள்ளைக்கு வீங்கி வறட்டுக்கிழ வன்கூட போய்ப்பிழைத்தா ளென்று புவனஞ்சொல் வாரிதுவே நம்முடைய மட்டும் நாயன் விதித்தானோ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(473,'பகவதிக்குச் சச்சிதானந்த வடிவைக் காண அருளல்','சும்மாயிந் தக்கிழவன் சோலிபண்ண வந்தானோ படைத்த பரனே பாவியே யென்றலையில் நடத்தை யெழுதி நவின்றதுவு மிப்படியோ பரதவித்து மாது பலபலவா யெண்ணிமிக  விரதமுற்று மாது மெல்லி யிளமயில்போல் ஆளுக் கிடையே அன்னம்போ லேதிரிந்து கூழு குடித்தக் குறுங்கிழவ னைத்தேடி திரியும் பொழுது செய்யதிரு மாலவரும் பரியேறும் பெருமாள் பகவதியைக் கண்டவரும் இனியிவளை யிந்த ராச்சியத்தில் நம்முடைய மனிதப்பெண் கூட்டிலிட்டு மாலையிட வேணுமென்று நினைத்துப் பெருமாள் நேரிழையைத் தான்மயக்கி புனைத்தொரு பெண்ணுடைய பொற்கூட்டுக் குள்ளடைத்து தாண்டவ மாடுஞ்சபையில் சனங்களெல்லோ ருமறிய காண்ட மிகப்படித்துக் கன்னிப் பகவதியை மனுவறிய அண்ட வானலோ கமறிய இனிமணங்க ளிவளை யாம்புரிய வேணுமென்று நிச்சித் தொருபெண் நிலையுங் குறிபார்த்து எச்சரிக்கை யான இளமயிலாள் தன்கூட்டில் அடைத்தார் பெருமாள் ஆயிழையும் வெகுவாய்ப் படைத்தோ ரருளால் பாரீரேழு மயங்கப் பாடினாள் காண்டம் பகவதித்தாய் நாயகியும் நாடி யவள்படித்த நற்காண்ட மானதுதான் புகன்றா லுலகம் பொடிப்போ லுதிர்ந்திடுமே  உகந்தான் முடிய உரைக்கிறாள் காண்டமது மனுவி னுடலதையும் மாகுலுக்கமாய்க் குலுக்கி தனுவளைத்தாற் போலே சடலந் தனைவளைத்து ஆளுக் கிடையதிலே ஆட்டிச் சடலமதைத் தூளு மிகப்பறந்து தூசிவா னமடையச் சடலந் தனையாட்டித் தான்கோப மாய்க்குலுக்கித் தடதடெனச் சுவாமியுட தாளடி யில்வீழ்ந்து ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(474,'பகவதியம்மை காண்டம் படித்தல்','பாவிநீ யிந்தப் பாரழிக்க வந்துஎங்கள் ஆவி மறுக அவனிதனி லெங்களையும் சீரழிக்க வென்றோ தெச்சணத்தில் வந்திருந்தாய் போரழிவு இல்லாமல் பொன்று கலியதிலே பொல்லாத நீசனுட பிதிரையெல் லாமழித்துச் சொல்லொன்றுக் குள்ளே தெய்வகன்னி மக்களையும் வைத்தாள நீயும் வந்தாக்கா லெங்களுட மெய்த்தான மழிக்க மேன்மையோ வுன்றனக்குப் பாவிநீ யென்னிடத்தில் பருங்கிழவ னாகவந்து தேவியென்றன் மானமெல்லாம் சீரழித்துப் போட்டாயே இவ்வளமை செய்வாய் என்றேநான் முன்னறிந்தால் பொவ்வாயில் தீயெரியப் போடுவே னக்கினியை என்கோவில் வந்து என்னைக்கண் மாயமதாய்  உன்கோவில் வாசலிலே விட்டாட்டுப் பார்க்கிறாயே என்னமாய மாக இங்கேகொண்டு வந்தாய்நீ பொன்னம்பலத் தீசன் பொடிப்படுவா னிங்கில்லையோ பொல்லா தகத்தீசன் பொருப்பேறி மாண்டானோ எல்லா ஆபத்தும் இத்தனைநாள் காத்துஇப்போ மாண்டானோ அகத்தீசன் மாமுனியாய்ப் போனானோ வேண்டு மென்றகாலம் வேம்புமினிப் பானதுபோல் பார்வதி மாதுமையும் பரமே சொரியாளும் சீர்பதியென் னக்காள் சிறந்தமண்டைக் காட்டாளும் ஒக்கொன்றாய்ச் சேர்ந்தாரோ ஓவியம்நான் வேண்டாமென்று அக்கறுகு சூடும் ஆதி வெறுத்தாரோ மூவாதி மூவருக்கு முன்னுதித்து வந்தவனோ இவனுடைய மாயம் என்னசொல்லப் போறோம்நாம் சிவனுக்கு மென்னுடைய செய்தி தெரியுமல்லோ ஆருக்கு மடங்காத அதிகாரம் பெற்றவனோ பாருக்குள் வந்து பரிசு கெடுக்கிறானே போகவழி சொல்வீர்களோ பொன்னுகன்னி யெம்பதிக்கு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(475,'பகவதியம்மை காண்டம் படித்தல்','ஏகவழி சற்றும் எனக்குத் தெரியல்லையே என்று பகவதியாள் இரைஞ்சிமிகக் கூச்சலிட்டு  மன்றுதனில் வீழ்ந்து மாபுலப்ப மாயழுதாள் புலம்பிடத் திருமால் மாதைப் பொறுபிள்ளாய்ப் பிள்ளாய்ப் பெண்ணே சலம்பிநீ யுரைத்தா லோகம் தட்டுண்டு போமோ சொல்லு பலம்பெற வுனக்கு இந்தப் பருவமா மணங்கள் செய்ய நலம்பெற வரங்கள் பெற்ற நற்பரத் தீசன் நானே நானென்ற ஈச னானால் நானிலம் பிறவார் கள்ளக் கோனென்ற இடையன் சாமிக் கோபால னான தாலே தானென்று உலகில் தோன்றி சடாட்சர ஆட்டு மாடி நானென்று வேடம் பூணும் நாரணன் நீதா னென்றாள் நாரணன்நீ நீதா னென்று நவின்றது சரிதான் பெண்ணே காரண வுகத்துக் கெல்லாம் கருவுதித் தோங்கு மாதி நாரண ஈச னல்லால் நடப்புவே றாரு சொல்லு வாரணக் குயில்போல் வன்ன மயிலென வுரைத்தா ரையா உரைத்திடு மொழியைக் கேட்டு உறுமியே கோப முற்று வரைத்தடம் புலிபோல் சீறி மங்கையு மாறிச் சொல்வாள் நரைத்துமே புலச்சை கெட்ட நாரண ஈச னென்று துரைத்தன மாகச் சொல்லச் சுணையுண்டோ வுனக்கு என்றாள் சாதிக்குச் சாதி நீயும் சமைந்துநல் வேடம் பூண்டு தாதிக்குக் கணவன் போலும் சடமெடுத் துடல்கள் போட்டு வாதிக்குப் பிறப்பாய்ப் பின்னும் வழியுன்றன் குலமே கந்தன்  சாதிக்குச் சரியே வுன்றன் தன்குல மறிவே ளானும் அறிவே ளானென்ற பெண்ணே ஆதியு மெனதுள் கண்டாய் தறுமொழி சொல்ல வேண்டாம் தாணுமா லயனும் நானே உறுமொழி யொருசொற் குள்ளே உகமதை யாள நானும் மறுமொழி யில்லா வண்ணம் வரம்பெற்ற நாதன் நானே அப்போ தரிவையரும் ஆதி முகம்நோக்கி இப்போ துன்மருட்டு எல்லா மிகஅறிவேன் சோலிமிகச் செய்யாதே கெஞ்சுகவாய்ப் பெண்ணாரைப்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(476,'பகவதியம்மை காண்டம் படித்தல்','பாதகங்க ளேராதே பாவையரை விட்டுவிடு தோதகமாய் வித்தைத் தோகையரோ டேறாது எங்கள் கண்மயக்கம் எல்லா மிகத்தெளித்து தங்கள்தங்கள் பதிக்குத் தானேக விட்டுவிடு கேட்டந்த நாரணரும் கிளிமொழியோ டேதுரைப்பார் நாட்ட மடவாரே நானென்ன செய்தேன்காண் உங்களைநான் கட்டி ஓடிமிகப் போகாமல் எங்களுட பொற்பதியில் இட்டிருக்கோ பிள்ளாய்ச்சொல் போகவே ணுமென்றால் பிடித்திழுப்பா ருண்டோசொல் ஏகவே ணுமென்றால் எழுந்திருந்து ஏகலாமே மாயத் திருமேனி மனதுள்ளொன் றேயடக்கிப்  வாய்த்திரு வாய்மலர்ந்து உரைத்திடவே பெண்ணாளும் வாயுரைத்தாற் போலே மனதுமிகச் சொல்லாதே நீயுரைத்தாய் நெஞ்சம் நினைப்புவே றாயிருக்கும் மாய மறிவாரோ மாயாதி யுன்சூட்சம் உபாய மறிய ஒருவரா லேலாது என்று மாகுமரி இப்படியே சொன்னவுடன் நன்று நன்றென்று நாரணர்பின் னேதுரைப்பார் பெண்ணே யெனதுடைய பேரழகிப் பொன்மயிலே கண்ணே யெனது கனியேயென் தெள்ளமுதே அமுதவாய்ச் சொல்லழகி ஆசார வீச்சழகி குமுதவாய்ப் பெண்ணே குமரிப் பகவதியே தேனே மயிலே திகட்டாத தெள்ளமுதே மானே குயிலே மரகதப்பெண் ணோவியமே  கிஞ்சுகவாய்ப் பெண்ணே கிளிமொழியென் மாமயிலே செஞ்சுடரே நல்ல திரவிய மாமணியே  பொன்னும் பகவதியே பிள்ளாய்நான் சொல்வதுகேள் என்னுடைய பேரில் எள்ளவுங் குற்றமில்லை கலியழிய வயது காலஞ்சரி யானதினால் வலிய யுகமான வாய்த்ததர்ம நற்புவிதான்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(477,'பகவதியம்மை காண்டம் படித்தல்','பிறப்பதினா லிந்தப் பெரும்புவியில் நாம்பிறந்து  சிறப்பதுகள் செய்து செய்கரும முமுடித்து நாடாள நமக்கு நல்லபல னானதினால் தாடாண்மை யான சத்தி பகவதியே  உண்மைதான் சொல்லுகிறேன் ஓவியமே நீகேளு  தண்மையல்லால் வேறு தப்பிதங்கள் சொல்லேனான் கயிலையங் கிரியில் கறைக்கண்ட ரீசுரரும் மயிலனையா ளான மாது சரஸ்வதியும் மாது உமையாள் வாய்த்தகுல பார்வதியாள் சீதுபுகழ் லட்சுமியாள் சிறந்த பகவதியாள் மண்டைக்காட் டாள்தெய்வ மடந்தையே யுபேர்கள் குன்றிலுறை வள்ளி கோதைதெய் வானைவரை பூமடந்தை நல்ல பொன்னரியத் தேவியர்கள் பார்மடந்தை கங்கை பாணி மடந்தைமுதல் எல்லோருங் கூடி இருக்க வொருதலத்தில் வல்லாரு மீசர் வகுத்ததுவே நீகேளு கலியன் வரம்பெற்றுக் கயிலைதனை விட்டவனும் திலிய னவனுகத்தில் தேவியவள் பெண்ணுடனே வருகுமந்த வேளையிலே  மாமுன சீராமரிஷி திருஉமையாள் பங்கரோடு சென்றுநின் றேதுரைப்பார் நல்லபர மேசுரரே நாட்டிலிந் தக்கலியன்  வெல்லக்கூ டாதவரம் வேண்டியிவன் போறதினால் எக்கால மையா இவன்முடி வாவதுதான் அக்கால மெல்லாம் அருளுமென்றா னீசுரரை  நல்லபர மேசுரரும் நாடிமுனி கேட்டதற்குச் சொல்லலுற்றார் பெண்ணே செவிகொடுத்துக் கேட்டிடுநீ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(478,'சிவன் ரோமரிஷிக்கு அருளியது','மாமுனி கேளென்று மாயாத ஈசுரரும் தாமுனிந்து சொன்னத் தன்மை மிகக்கேளு பொல்லாக் கலியன் பொன்றியடி வேரறுவ(து) எல்லா மறிய இலக்கறியக் கூறினராம் இவன்மா ளும்போது இனத்துக் கினம்பகையாம் சிவஞான நினைவு செல்லாது தேசமதில் கொலைகளவு ரெம்பக் கோள்கள் மிகுந்திருக்கும் தலைஞான வேதம் தடுமாறிக் கைவிடுவார் நேருக்குக் காலம் நெகிழ்ந்திருக்கும் பார்மீதில் போருக்கு யாரும் புத்தியாய்த் தானிருப்பார் பிள்ளைக்குத் தாயும் பெருத்தபகை யாயிருக்கும் கள்ளக் கணவருட கருத்துமிக மாதருக்காம் கூடி யுடன்பிறந்த கோதையரைப் பெண்ணெனவே நாடி யவரெண்ணி நாணியிறப் பார்கோடி வாரி கோபிக்கும் வாங்கும் சிலஇடங்கள் 960 மாரி மறைந்துவிடும் வாயுவது நோய்வீசும் கீழ்ச்சாதி யெல்லாம் கெறுவிதங்கள் மிஞ்சியவர் மேல்சாதி தன்னை வேலைகொண்டு தான்வருவார் வம்பருக்கு நோக்கம் வாக்கில் மிகஇருக்கும் அன்பருக்கு நோக்கம் அடங்கி மிகஇருக்கும் மனுவதிலே கற்பிகழா மாதர்கற் பேயழந்து தனுவாய்ப் புருஷனுட தன்வாக்குக் கேளார்கள் தெய்வ நிலைகள் தேசமதில் காணாது மைநெறியப் பெண்ணார் மனுநீதங் காணாது இராச நெறிநீதம் இராத்தியத்தில் காணாது பிராயம் வருமுன்னே பெண்கள் நிலையழிவார் தெய்வ மடவார் தேசமதி லேவருவார் வைய மறிய மாலையிட்டு வாழ்ந்திருப்பார் பார்பதியாள் ஈசொரியாள் பரமே சொரியாளும் சீர்பதியாள ளான சீதாதே விமுதலாய்க் கன்னி குமரி கயிலைபுகழ் மாதரெல்லாம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(479,'சிவன் ரோமரிஷிக்கு அருளியது','உன்னி யவர்கள் ஒருதலத்தி லேகூடி வாழ்ந்திருப்பார் மங்களமாய் மக்கள்கிளை வாழ்வுடனே தாழ்ந்திறந்து மாகலியன் தன்னால் மடிந்திடுவான் இப்படியே யிந்தக் கலிமாயும் லக்கெனவே  அப்படியே மாமுனிக்கு அருளச் சிவனாரும் பின்னு முனியும் பிஞ்ஞகனோ டேகேட்பான் துன்னுபுக ழீசுரரே தெய்வமட வார்களெல்லாம் கலியுகத் திலவர்கள் கட்டா யொருதலத்தில் சலிவில்லா தேவாழ்ந்து சந்ததிகள் வாழ்வுடனே வாழ்ந்திருப்பா ரென்று வகுப்புரைத்த ரீசுரரே கூர்ந்திவர்கள் தம்மைக் கோலமணஞ் செய்கிறதார் யானறியச் சொல்லும் அரனே எனத்தொழுதான் தானறியச் சொன்னத் தன்மை மிகக்கேளு நாடுகுற்றங் கேட்டு நல்லோரைத் தேர்ந்தெடுத்துக் கேடுகலி நீசருட கிளைவழிக ளுமறுத்துத் தெய்வ சான்றோர்களுக்குச் செங்கோல் மிகக்கொடுத்து மெய்வரம்பாய்த் தர்ம மேன்மைமுடி யுந்தரித்துத் தரும புவியாளத் தாட்டீக வைகுண்டரும் பொறுமைக்குல மானதிலே பிறக்கிறா ரானதினால் நாங்க ளெல்லோரும் நமனுவர்க் குள்ளாவோம் தாங்கி யொருபொருள்போல் தாமிருப்போ மப்பொழுது ஒருபொருளாய் வந்து உருவெடுப்ப தானதினால் திருமடந்தை யாவரையும் செய்யுமன்னர் தாமவராம் என்றீசர் சொல்ல இசைந்தமுனி நல்லதென்று  நன்று நன்றையா நல்லகா ரியமெனவே சந்தோசங் கொண்டு தானிருந்தான் பெண்ணரசே முந்தோர் மொழிந்த முறைநூற் படியாலே வந்ததுகா ணிந்தவிதி மங்கையரே யுங்களுக்கு எந்ததுகா ரியம்பெண்ணே என்னையொன்றுஞ் சொல்லாதே தலையில் விதிபிள்ளாய்த் தங்கடங்க விட்டுவிடு கிலேச மதையெல்லாம் கிளிமொழியே விட்டுவிடு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(480,'சிவன் ரோமரிஷிக்கு அருளியது','கொண்டுவந்தேன் பெண்ணே குவிந்தவதி எந்தலைக்குள் பண்டு அமைத்தபடி பதறாதே பாவையரே பெண்ணே நீயொருத்திப் பேர்பெரியக் கண்ணரசே கண்ணேயுனைக் காணாமல் கலங்கிமெத்த நானலைந்து வாடி யிருந்தேனடி மங்கையுனைக் காணாமல் கோடி யிருந்தேனடி குமரியுனைக் காணாமல் தாபத்தா லுன்னைத் தவமிருந்து கண்டேனடி கோபத்தால் கன்னி குமரியென்னைப் பேசாதே என்று மகாமாலும் இரக்கமுடன் மாதைமிகக் கொண்டு அணைவாய்க் குழைவாய் மிகவுரைக்க அம்மை பகவதியாள் ஆகங் களிகூர்ந்து நம்மைப் படைத்தது நாயன்முன்னா ளென்றுசொல்லி இருந்த பதியை எண்ணிöண்ணி மாதுநல்லாள்  பொருந்தும் விழியாள் பெருமிமிக நீருவிட்டு அழுதாளே சொல்லி அருவரைகள் தானிளக  ஒழுகாக நிற்கும் உலக நருளழவே மாமரங்க ளெல்லாம் பூத்துச் சொரிந்தழவே ஐயோ நானிருந்த அம்பலமும் வீதிகளும் வையா வழித்தெருவும் மண்டபமுந் தோற்றேனே சிங்கா சனமும் செகல்த்துறையும் வாவிகளும் மங்காத பொன்னு மாளிகையுந் தோற்றேனே இலாடக் கிழவன் இராத்திரியில் வந்துநம்மைக் கபாடமிகச் செய்ததினால் கனபதிகள் தோற்றேனே பொன்னா பரணமும் பெட்டகமுந் தோற்றேனே முன்னா ளெழுத்தோ முத்துமண்ட பமிழந்தேன் ஆண்டிலொரு தேரோட்டம் அதுவெல்லாந் தோற்றேனே வேண்டும் நருட்கள்வந்து விழுந்திறைகள் தோற்றேனே தண்டையணி சிலம்பும் தரளமெல்லாந் தோற்றேனே துணைக்கிள்ளை யானத் தோழியரைத் தோற்றேனே பணப் பெட்டகமும் பைம்பொன்னரிய பட்டுகளும் நவமணியால் செய்த நல்லவடந் தோற்றேனே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(481,'சிவன் ரோமரிஷிக்கு அருளியது','செகமெல்லாங் கூடிச் சேவிப்பதுந் தோற்றேனே  இத்தனைநா ளுமிருந்து இந்தக்கெதி யோஎனக்கு முத்திபெற்ற ஈசன் முன்னெழுதி வைத்ததுவோ அய்யோநான் பிறந்த அன்றுமுத லின்றுவரை வெய்யோன் முகம் வரையும் செல்லியறியேனே பாவிக் கிழவன் பரிகாசஞ் செய்வானென்று ஆவி யறிந்திலனே அங்கமது வாடுதையோ மூன்றுபொழு தாச்சே ஊறும்நீர் தானருந்தி மீண்டென் சரீரம் விழலா யெரியுதையோ பாலு பழமும் பருந்தேனுஞ் சர்க்கரையும் நாலு ரண்டான நல்ல ருசியதுவும் பாவித்த கும்பி பருங்கனல்போல் மீறுதையோ ஆவிதடு மாறுதையோ ஆதிபர மேசுரரே என்று பகவதியாள் எண்ணமுற்றுத் தானழவே மன்று தனையளந்த மாலுமிக அமர்த்தித் தேனினிய கண்ணே தேவி பகவதியே நானினித்தான் சொல்லும் நல்ல மொழிகேளாய் நான்மணங்கள் செய்யும் நாரியர்கள் தம்மிலுமோ மேன்மைய தாயுனக்கு வெகுபணிகள் நான்தருவேன் பொன்பணங்க ளானப் பெட்டக முந்தருவேன் என்பதி யுந்தருவேன் ஏறும்பல் லாக்கருள்வேன்  தண்டாய மேறிச் சனங்கள்மிகப் போற்றிவரக் கொண்டாடும் நல்ல குருபாக்கி யந்தருவேன் திருநாள் பவிசு தினமுனக்கு நானருள்வேன் அருள்ஞானப் பெண்ணே அங்கிருந்த சீரதிலும் அய்யிரட்டி யாக அணிவே னுனக்குடைமை பொய்யென் றிராதே பொன்முடியின் தன்னாணை என்று மாயாதி இப்படியே ஆணையிட்டுக் குன்று தனமின்னாள் குமரி மனமகிழச் சத்தியஞ் செய்து தலையைத் தொட் டாணையிட்டுப் புத்திவந்து பெண்ணாள் பொன்னும் பகவதியாள் நாதனுட தஞ்சமென நாடி யகமகிழ்ந்து மாது குமரி வாயுரை யாதிருந்தாள்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(482,'பகவதி திருக்கல்யாணம்','அப்போ திருமால் ஆனந்த மேபெருகி இப்போ பகவதியை ஏற்றமணஞ் செய்யவென்று உன்னித் திருமால் ஒருசொரூபங் கொண்டனராம் மின்னுமந்த ஈசுரராய் வேடமது பூண்டு எக்காள வாத்தியங்கள் இசைதாள நாகசுரம் அக்காலத் தேவர் ஆகாய மீதில்நின்று பந்தல் விதானமிட்டுப் பாவாணர் போற்றிநிற்கச் சந்தமுடன் மனிதச் சாதி மிகமகிழ  தெய்வரம்பை மாதர் திருக்குரவைத் தான்பாட ஞாய மனுவோர் நற்குரவைத் தான்பாட பகவதிக்கு நல்ல பாரமணக் கோலமிட்டுச் சுகபரனு மாலைமணச் சொரூப மெடுத்தவரும் பிச்சிமா லைபுரிந்து பொற்கடுக்கன் மீதணிந்து மிச்சமுடன் வெள்ளி விரலாளியோ டரைஞாண் பொன்தா வடங்களிட்டுப் பொன்னரிய தாலிதன்னை  மின்தா வடம்பூண்ட மெல்லி பகவதிக்குக் கண்ணானத் தாலி காரணத்து நற்தாலி விண்ணோர்கள் மெய்க்க விறலோன் மிகத்தரித்தார் கண்டு தெய்வாரும் கன்னி பகவதிக்கு இன்று மணம்புரிந்தார் இறையவர்தா னென்றுசொல்லிக் கொண்டாடிக் கயிலை குவித்துமிக வாழ்ந்திருந்தார் தொண்டான சான்றோர் துதித்து மிகப்போற்றிக் கலிமுடிந்த காலம் கன்னி பகவதிக்குச் சலிவில்லா மாமணங்கள் தானாச்சு நன்றெனவே எல்லோருங் கொண்டாடி இருந்தார்கா ணம்மானை நல்லநா ராயணரும் நாடி யகமகிழ்ந்து செல்லமட வாரோடு சேர்ந்துவிளை யாடிருந்தார் அந்தந்தப் பெண்களுக்கு அழகுசொரூ பம்வேறாய்ச்  சொந்த விளையாட்டுத் தோகையரோ டாடினராம் ஆடித் திருநாளும் ஆனந்தக் கும்மிகளும் நாடி யுகத்தீர்ப்பும் நல்லமக்க முக்கறிவும்  சொல்லி யாமங்கூறிச் சோபனங்கள் தான்பாடி நல்லியல்பாய் நாதன் நடத்தி வருகையிலே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(483,'பார்வதி திருக்கல்யாணம்','ஈசுரனுக் கேற்ற ஏந்திழையாள் பார்வதியை வீசுபுகழ் மாதுமையை வெற்றிமண்டைக் காட்டாளை மாமணங்கள் செய்ய மனதிலுற் றெம்பெருமாள் பூமணங்கள் னானப் பொன்னுநல்ல பார்வதிக்கும் மங்களங்கள் கூறி மாதை மிகநினைத்துத் திங்கள் சடையணிந்து சிவவேட முந்தரித்து மாத்திரைக் கோல்பிடித்து மார்பில்தா வடம்பூண்டு காத்திருந்து பார்வதியைக் கருத்தாய் நினைக்கலுற்றார் மாதுமைக்கு மங்களங்கள் மயேசு ரன்கூற சீதுகந்த மாதுகளைச் சிவனு மிகநினைத்துக் கருத்தா யிருந்து காண்டமது சொல்லியவர் உருத்தா யிருந்துஓதுவார் மோகமதாய்ப் பெண்ணே யென்பார்வதியே பேரான மாதுமையே கண்ணே யென்பார்வதியே கனகவொளி ரெத்தினமே கூட்டுக் கிளியே கொடியிடையே நிங்களெல்லாம்  நாட்டிலென்னை விட்டு நன்னகரில் வாழ்வீரோ தனியேநான் வந்திருந்து தவித்துமுகம் வாடுவது கனியேயென் மாமணியே கருத்தி லறியீரோ உள்ளுடைந்து வாடி உங்கள்மேல் காதல்கொண்டு தள்ளுடைந்து நானிருக்கும் தன்மை யறியீரோ ஊணுறக்க மில்லை உங்களைநா னெண்ணியெண்ணிக் கோணுதலாய் வாடி கோடிமுகம் வாடுறேனே வாரீரோ பெண்காள் வயிறுபார்த் தன்னமிடப் பாரீரோ என்முகத்தைப் பார்த்திரங்க மாட்டீரோ அன்னங்காய் வைத் அமுதருந்து மென்றுசொல்லி முன்னிங்கே வந்து முத்தமது தாரீரோ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(484,'பார்வதி திருக்கல்யாணம்','வாழை பழங்கனிகள் மாம்பழம் பாலக்கனிகள் கூழனைய சந்தனமும் கொண்டுவந்து தாரீரோ கண்ணரிய பெண்ணுகளே கள்ளமில்லா தோவியங்காள் பெண்ணரிய மாமயிலே பிள்ளா யென்கண்மணியே எண்ணுஞ் சிறுபிள்ளையாய் இருக்குமென்றன் பார்வதியே கண்ணு மயிலே கைக்குள்வந்து சேராயோ என்றுபர நாதனுமோ இவ்விசைகள் தான்கூற மன்றல்குழ லுமையாள் வாய்த்தகுலப் பார்வதியாள் வந்துதிருப் பாதமதில் மாகோபமாய் மகிழ்ந்து  முந்து மொழியெல்லாம் மொழிந்தாரே மாதரவர் அய்யோஈ  தென்ன அடிமாறு காலமதோ பொய்யோ கலியன் பிறக்கப்போய் நாங்களெல்லாம் இப்பா ருலகில் இப்படியே வந்ததென்ன முப்படியே யுள்ள முறைநூற் படியாலே பொல்லாக் கலியுகந்தான் பொடிப்பட்டுப் போறதற்கோ வல்லாத்த மங்கையெல்லாம் மாகலியில் வந்ததுதான் என்றந்த மாதர் இதுகூற மாயபரன் நன்றென்றன் மாதர்களே நாம்நினைத்த துபோலே வந்தீரே யின்று மணம்புரிய வேணுமென்று புந்தி யயர்ந்து பூவை முகங்கோடி அண்ணர்நா ராயணரும் அவனிதனில் வந்தாரென்று பெண்ணரசி நாங்கள் போய்ப்பார்க்க வேணுமென்று வந்தோமே யென்னுடைய வரம்பெரிய அண்ணரென்றாள் அண்ணரது நீயென்றாய் ஆயிழையே யிவ்வுகத்தில் இண்ணதுவே இப்படித்தான் என்னுடைய நாயகமே மனதயர்ந்து மாதர் வாயுரைக்கக் கூடாமல்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(485,'பார்வதி திருக்கல்யாணம்','தனதுள் முகங்கோடி தலையில் விதியெனவே நின்றார் மடவார் நினைவு தடுமாறிப் பண்டார மாகப் பரமன் வடிவெடுத்துத்  தாலி மிகத்தரித்தார் தாமனந்தப் பெண்ணார்க்கு ஆலித்துப் பார்வதியை அரவியுட னீராட்டி சந்தோ சமாகத் தாலி மிகத்தரித்தார் வெந்தோசந் தீர்ந்து மேலோர்கள் கொண்டாட வையகத் தோரும் மனமகிழ்ந்து கொண்டாட செய்யத் திருமால் செய்சடங்கு கள்முடித்து வாழ்ந்திருந் தார்பெண்களுடன் மாய பரநாதன் தாழ்ந்து மடவார் சரண மிகப்பூண்டு  பூண்டு பணிவிடைகள் பூவையர்கள் செய்துமிக வேண்டும்பல பாக்கியத்தோ(டு) இருந்துமிக வாழ்ந்தார் இப்படியே பெண்களொடு இருந்துமிக எம்பெருமாள் நற்புடைய தேர்திருநாள் நாளுங் குறையாமல் செய்து இகனை சிவசோ பனம்புரிந்து பைதுவளை மாதரோடு பண்பாக வேயிருந்தார் மாதருக்கு மாதவனாய் வானோர்க்கு நாயகமாய்த் தாரணிக்கும் நாதனெனத் தான்வாழ்ந் திருந்தனராம் வாழ்ந்திருக்கும் நாளில் மாதுகன்னி மார்களோடும் சேர்ந்திருக்கும் நாளில் திருமால் மனமகிழ்ந்து கூர்ந்தொரு தேவி குவிந்தமண்டைக் காட்டாள்க்கு சோபனங்கள் கூறி சுத்தமங்க ளம்பாடி  சேவரசி யான மெல்லியரை நாமணந்தான்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(486,'மோகினி வேடம்','புரிவதற்குக் கோலம்புகழ்ந்தணிய வேணுமென்று மதிபெரிய மாதை மயக்கமிகச் செய்திடவே மாதுக்கு நல்ல மகாவிருப்ப மானதொரு சீதுகந்த மாதரென செம்பவள மாயவரும் காதுக்குத் தோடணிந்து கைக்கு வளையணிந்து மார்பில் வடமணிந்து வாய்த்ததண்டைக் காலிலிட்டுச் சேலை யுடுத்துச் சிவந்தகொசு கமுடித்து மாலை யணிந்து மார்பில் கலையணிந்து கோர்வை யணிந்து குழலு மிகமுடித்துத் தோர்வை படாதே தோகையென வேசமைந்து மஞ்சணையும் பூசிபிச்சி மாலை மிக அணிந்து செஞ்சொல் மதனைச் சிந்தைக்குள் ளேயிருத்தி எடுத்தார் பெண்ணாக எம்பெருமாள் கோலமது கடுத்தகன்னி மார்கள் கன்னிக் கணவரென்று சொல்லி மகிழ்ந்து தோழிநா மென்றுசொல்லி முல்லையணி பெண்ணார் முகுந்தன்பதந் தொழுவார் நாட்டி லுள்ளோரும் நாரணர்பெண் ணானாரென்று தாட்டிமையாய்ப் போற்றி சரணம் பணிந்துநிற்பார் இப்படியே வேசம் எடுத்துத் திருமாலும்  பொற்படியில் வாழும் பொன்னுமண்டைக் காட்டாளை மயக்கிக் கொடுவரவே மாமோகப் பெண்ணேவ இச்சொரூபம் போலே ஏந்திழையாள் கண்ணிலொரு சச்சு ரூபமாகத் தான்கண் டுரைபகரும் பெண்ணேயென் னக்காள் டுரைபகரும் பெண்ணேயென் னக்காள் பேர்பெரிய நாயகமே கண்ணே யென்மாமணியே கனகவொளி மாதவமே கலியுகத்தி லேபெரிய கண்காட்சை தன்னுடனே வலிதான நல்ல வாய்த்த பதிச்சிறப்பும் நித்தந் திருநாளும் நீலமணி மண்டபமும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(487,'மோகினி வேடம்','சிற்றம் பலமும் திருநாள் சிறப்புடனே சத்தகன்னி மாரும் தண்டரள மாதுமையும் புத்தியுள்ள பார்வதியும் போர்மகள் பகவதியும் கங்கை மடவாரும் காட்சி மிகப்புரிந்து கொங்கை மடவார் கூடி மிகப்புரிந்து கும்மி யடித்துக் குரவை மிகக்கூறி எம்வடிவு போலே ஏலங் குழலார்கள் ஆடிப் படித்து அலங்கிருத மாகியவர் சாடிக் குலாவி சனங்கள்வெகுக் கூட்டமுடன் பாலு பழமருந்திப் பாவிக்கும் பாவனையை மேலுக் குகந்தவளே விடுத்துரைக்கக் கூடாது  என்றுரைக்க அந்த இளவரசிக் காட்டாளும் அன்று மதிமயங்கி ஆயிழையாள் மாய்கையொடு கூடி நடந்து கூண்டரிய நற்பதியில் ஆடிக் களிக்கும் ஆதியிகனைச் சுபையில் வந்தாளே மாய்கை மண்டைக்காட் டாளுடைய சிந்தை யதற்குள்ளே சென்று மிகவிருந்து திக்குத்திசைகள் தெரியாமல் தான்மயங்கி பக்குவ மாது பறிகொடுத்தப் பேர்களைப்போல் புலம்பித் தவித்துப் பெண்ணரசி நிற்கையிலே வலம்புரிக்கண் மாயன் மாதை மிகவருத்தி மணமுகிக்க வென்று மாயன் மனதிலுற்றுக் குணமுடைய மாயன் கூண்டுமுன் மாலையிட்டப் பகவதிக்குள் சேர்த்துப் பாரமணஞ் செய்யவென்று சுகபதியா ளந்தச் சோதி பகவதிக்குள் உள்ளாக்கிக் கொண்டு உற்ற திருச்சபையில் துள்ளாட்ட மாகித் தொகுத்தகாண் டம்படித்தாள் வையகத்தோர் காண வாழும் பகவதியாள் செய்யமண்டைக் காட்டாள் சிறந்தகாண் டம்பிடித்தாள்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(488,'மண்டைக் காட்டம்மை காண்டம் படித்தல்','அய்யோநான் கண்மயக்காய் அங்கிருந் திங்குவரப் பொய்யோ வொருமாய்கைப் பெண்ணாக வந்ததுதான்  இங்கே யிவர்போலே இருந்ததுகாண் வந்தபெண்ணும் சங்கை நமைக்கெடுக்கச் சமைந்தாரே பெண்ணாகிப் பெண்ணாய்ச் சமைந்து பேராசையு மருளிக் கண்ணான என்றன் கற்பு மழிந்தேனே என்றுமா தேவி இப்படியே சொன்னவுடன் ஒன்றுமறி யாதவர்போல் உள்ளந் தனிலடக்கிப் பெண்ணே நானிந்தப் பெருங்கலியைத் தான்முடிக்கக் கண்ணான பெண்ணரசி கரிய சொரூபமது எடுத்திகனை கூத்து யானிங்கே யாடுறவன்  முடுத்துந்த னூரில் முறைமயக்க வந்தேனோ எனக்குப்பெண் ணில்லையென்று இரந்துகொள்ள வந்தேனோ தனக்கிதுவோ ஞாயம் தரணியது சொல்லாதோ நாமிருப் பிங்கே நாமமெங்குங் கேட்குதல்லோ சோமவா ரமணிந்த சுவாமியுட கற்பனையோ என்று பெருமாள் இதுமொழிய மாதுசொல்வாள் குன்றெடுத்த மாயக் கோபாலா வுன்சூட்சம் எவரா லளவெடுக்க ஏலுங்கா ணுன்மாயம் கவராயிரங் கோடிக் கருத்திருக்கு மாயவரே இராமனாய்த் தோன்றி இராவணசங் காரமது சிராமர் படைவகுத்துச் செய்யம்பு கைப்பிடித்துப்  போரில்நீர் நின்று பெருதுகின்ற வேளையிலே மேரு குலுங்கி விண்தூள் மிகப்பறந்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(489,'மண்டைக் காட்டம்மை காண்டம் படித்தல்','ஆகாயத் தேகி அந்தரங்க மேகமதில் தூள்மூடக் கண்டு தேவர் மீகப்பதறி வேழதிய உன்போர் மிகுதேவர் பார்க்குகையில் போரிலும்நீர் நின்றுப் பொருது சரம்விளைக்க வாரியிலும் பாம்பணையில் வாய்த்தபள்ளி கொண்டீரே ஆய்ப்பாடி ஆயர் எல்லோருந் தாங்கூடிச் சாய்ப்பானப் பேய்களுக்குச் சருவில் கொடைகொடுக்கப் பேயையெல்லா மோட்டி பெரும்பூத மாயிருந்து ஆயர்கள் பார்த்திருக்க அங்குபூ சாரியுமாய்த் தின்றாயே யத்தினையும் சிலபூத மாயிருந்து தொட்டிலா யர்மனையில் சிறுபிள்ளையாய்க் கிடந்து மட்டுப் பருவமுள்ள மாதர் மனைபுகுந்து விளையாடி நீயும் வேறா ரறியாமல் குழைவாய் மதலையெனக் கூண்டதொட்டி லேகிடந்து சிறுகுழவி போலே சீறி மிகஅழுவாய்ப் பருவதங்க ளுமெடுப்பாய்ப் பாலன்போ லேகிடப்பாய் உன்சூட்ச வேலை உரைக்க எளிதாமோ தன்சூட்ச மெல்லாம் தானுரைக்கக் கூடாது  பலவேசங் கொண்டு பார்முழுதுஞ் சுற்றிடுவாய்க் குலவேசம் நீரும் கொண்ட தொழிலல்லவோ ஆரோடுஞ் சொல்லி அதட்டிவிட வேண்டாமே பாரோ டுதித்துவந்த பாவையொடு செல்லாது என்றுமண்டைக் காட்டாள் இசைந்தமொழி தான்கேட்டு நன்றுநன்று பெண்ணே நமக்கிதுவே ஞாயமுறை');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(490,'மண்டைகாட்டம்மை திருக்கல்யாணம்','பெண்ணேயுன் றன்பேரில் பேராசை ரெம்பவுண்டு கண்ணே யுனையிப்போ கலியாணஞ் செய்வதற்கு வேளையி தாகும் மெல்லியரே யென்றுரைக்க மானதிய மங்கை மனதயர்ந்து வாய்குழறித் தலையி லெழுத்தெனவே சத்தமுரை யாடாமல் சிலைநுதலிக் கன்னி சொல்லுரையா தேயிருந்தாள் இருந்த நினைவை ஏகமூர்த்தி யறிந்து பொருந்து மதியானப் பிரமாதி வேசமதாய் வேச மெடுத்து மேளத் தொனியுடனே வாசவருந் தேவர்களும் மலர்மாரி தூவிநிற்க தேச நருளறியத் தெய்வமட வாரறிய மாய பரனும் மகாபெரிய நூல்முறையாய் வாசக் குழலாளை மாலையிட்டா ரம்மானை மாலையிட்டு நாதன் வாய்த்தசடங் குங்கழித்துச்  சாலையத் துள்ளேகித் தானிருந்தா ரம்மானை');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(491,'வள்ளி திருக்கல்யாணம்','மணமுகித்து நல்ல மணவறையக லுமிருந்து துணைபெரிய மாயன் சுருதி முறைப்படியே ஆகமத் திகனை அலங்கிருத மாய்ப்புரிந்து நாகரீக நாதன் நடத்திவரும் நாளையிலே கந்தனுக்கும் பெண்ணைக் கலியாணஞ் செய்யவென்று சிந்தித்து நல்ல திருமால் மனமகிழ்ந்து கேட்டு வரையாள் கோதைவள்ளி நாயகியைக் கந்தன் சொரூபம் கரியமால் தானெடுத்து எந்தன் பிரானும் ஏற்றவள்ளி நாயகியை மாலையிட்டு நல்ல மணமுகித்தார் கந்தனுமே வேலையிட்ட மான வேண்டுஞ் சடங்குசெய்து நாளிட்டு வந்து நாளேழு மேகழித்து வாழட்டு மென்று வாய்த்தசடங் குமுகித்து மாதரோ டெல்லாம் மகிழ்ந்திருந்தா ரம்மானை தாரணியோ ரறிய தான்வாழ்ந் திருந்தனராம் பெண்ணார் தமக்குப் பேர்பெரியத் தற்சொரூபம் கண்ணான மாயவரும் காட்டி மிகவாழ்ந்தார் வாழ்ந்திருக்கும் நாளயிலே மங்கை தெய்வ மாதர்களில்  ஏந்திழையில் சிலர்கள் இளங்குழலி பெற்றனராம் பாலராண் பெண்ணும் பாவையர்கள் பெற்றுமிகக் கோலமுடன் வாழ்ந்திருந்தார் கூண்டரிய செல்வமோடு பாக்கியங்கள் ரெம்பப் பவிசு குறையாமல் நோக்கியல்பாய் மாதர் நுண்ணிமையாய் வாழ்ந்திருந்தார் நாரா யணர்க்கு நல்லமுது தான்படைத்து காரணரு மமுது கலந்துமிக வுண்டிருந்தார் வள்ளி திருக்கல்யாணம் மணமுகித்து நல்ல மணவறையக லுமிருந்து துணைபெரிய மாயன் சுருதி முறைப்படியே ஆகமத் திகனை அலங்கிருத மாய்ப்புரிந்து நாகரீக நாதன் நடத்திவரும் நாளையிலே கந்தனுக்கும் பெண்ணைக் கலியாணஞ் செய்யவென்று சிந்தித்து நல்ல திருமால் மனமகிழ்ந்து கேட்டு வரையாள் கோதைவள்ளி நாயகியைக் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(492,'வள்ளி திருக்கல்யாணம்','கந்தன் சொரூபம் கரியமால் தானெடுத்து எந்தன் பிரானும் ஏற்றவள்ளி நாயகியை மாலையிட்டு நல்ல மணமுகித்தார் கந்தனுமே வேலையிட்ட மான வேண்டுஞ் சடங்குசெய்து நாளிட்டு வந்து நாளேழு மேகழித்து வாழட்டு மென்று வாய்த்தசடங் குமுகித்து மாதரோ டெல்லாம் மகிழ்ந்திருந்தா ரம்மானை தாரணியோ ரறிய தான்வாழ்ந் திருந்தனராம் பெண்ணார் தமக்குப் பேர்பெரியத் தற்சொரூபம் கண்ணான மாயவரும் காட்டி மிகவாழ்ந்தார் வாழ்ந்திருக்கும் நாளயிலே மங்கை தெய்வ மாதர்களில்  ஏந்திழையில் சிலர்கள் இளங்குழலி பெற்றனராம் பாலராண் பெண்ணும் பாவையர்கள் பெற்றுமிகக் கோலமுடன் வாழ்ந்திருந்தார் கூண்டரிய செல்வமோடு பாக்கியங்கள் ரெம்பப் பவிசு குறையாமல் நோக்கியல்பாய் மாதர் நுண்ணிமையாய் வாழ்ந்திருந்தார் நாரா யணர்க்கு நல்லமுது தான்படைத்து காரணரு மமுது கலந்துமிக வுண்டிருந்தார்  கூடி யிருந்து குலாவி மிகவாழ நாடி மகிழ்ந்து நாரணருந் தேவியுமாய் வாழ்ந்திருக்கும் நாளை மங்கை பகவதியாள் சார்ந்த குழலாள் தையல்நல்லாள் தோழியர்கள் இருந்து மிகவாழ்ந்த இரணவொளி மண்டபத்தில் திருந்து பகவதியைத் தேவியர்கள் காணாமல் காணாமல் தேடிக் கானகங்க ளும்பார்த்து வாணாள் மறுகி வந்தாரே நற்பதியில் பதியில் பகவதியைப் பண்பாகப் பார்த்தவர்கள் விதியிதுவோ தாயே வெயிலுகந்த மாதாவே நீயிருந் தப்பதிதான் நிதானம்போ தாதெனவோ நீயுமிந் தப்பதியை நினைத்துவந்த வாறேது அப்போ பகவதியாள் அவர்கள் தமைநோக்கி இப்போ நமக்கு எழுத்தின் படியாலே நடந்திருக்குப் பெண்ணே ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(493,'வள்ளி திருக்கல்யாணம்','நவிலக்கூ டாதினிமேல் கடந்த பொருள்காணும் கன்னியிவர் கண்டீரே தலையில் விதியெனவே சாற்றினாள் தோழியுடன் மலையு தெளிந்து மாதே யென்தாய்மாரே எங்களுக்குத் தாயே இனியாரு நல்லதுணை  சங்கடங்கள் தீரச் சாற்றுமெங்கள் மாதாவே 20 மாதே யென்தோழியரே மன்னருக்கு மென்றனக்கும் தீதேது மில்லாத் தேசத் திருப்பதியில் நின்றுபணி செய்து நிறைவாக நில்லுமöன்று அன்றந்தத் தோழியர்க்கு அருளி விடைகொடுக்க விடைபோலே தோழியர்கள் வேதம்புகழ் பதியில் சடைவில்லா தேமகிழ்ந்து தாழ்ந்தேவல் செய்திருந்தார் நன்றாக நாரணரும் நாடும்மட வார்களுமாய் ஒன்றாக மக்களொடு ஒத்தளமாய் வாழுகையில் பெற்ற உபதேசப் பெருநூல் முறைப்படியே கற்றைக் குழலார் கனமான தேவியரை ஏக மறிய இசைந்த மணம்புரிந்து வாகாய்ப் பரனும் மகிழ்ந்திருந்தா ரம்மானை இனியிந்த இவ்வுகத்தில் யாமனுப்புங் கோலமதைத்  தனுவைக் குறைக்கத் தனதுள் மிகஅடக்கி நடத்தும் நருட்கள் நல்லன்ன மீகிறதை இடத்தில் கொடுவந்து ஈயுங்க ளென்றுமிகச் சொல்லிநாம் கூட சுவாமி மிகஇருந்து நல்லமக்க ளோடு நாம்கூடி வாழவென்று ஆரா தனைச்சொரூபம் அடக்கி னாருள்மனதுள் ஊரா னதிற்பிச்சை உங்களுட மூப்பாலே 40 இடஞாய மில்லை என்னிடத்தில் கொண்டுவந்து இடநாமுங் கூடி இருந்துதர்ம மேற்றிடலாம் என்றுரைக்க நருட்கள் எல்லோருஞ் சம்மதித்து கொண்டுவந் திட்டார் கூடிருந்து நாரணரும் மாதர் மக்களொடு மாயன் மனமகிழ்ந்து தாதர் இருந்து சாப்பிட்டா ரம்மானை');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(494,'அய்யா இணத்தாங்கல்களுக்கு எழுந்தருளல்','கூடி யிருந்து குலாவி யமுதேற்றுப் பாடி மகிழ்ந்து பரமன்வரும் நாளையிலே பின்னும் பெருமாள் பிள்ளைகளைத் தான்பார்த்து மன்னும் பெரிய மக்கள்மக்கள் மாதர்களே உங்களுட வீட்டில் உற்ற விருந்தருந்த மங்களமா யென்றனக்கு மாவிருப்ப மாயிருக்கு நான்வந்து தென்பேரால் நாட்டுமிணத் தாங்கல்களை தான்வந்து பார்க்கச் சந்தோச மாயிருக்கு என்றுரைக்க நாதன் இசைந்தகுலச் சான்றோர்கள் நன்றுநன் றெங்கள் நாரா யணக்குருவே எப்போ நீர்வந்து இரட்சிப்பீ ரென்றுமிகத் தற்பரனே நாங்கள் சடைத்து முகங்கோடி மலைகாணாப் பயிர்போல் வாடி யிருந்தோமையா பிழையா னதுபொறுத்துப் பிள்ளைகளை யாண்டுகொள்ளும்  ஆண்டுகொள்ளு மையா ஆதி மகாபரனே பூண்டுகொண் டோமுமது பொற்பாதம் நாங்களெனத் தொழுது வணங்கி சுவாமிவர வேணுமென்று முழுது மவர்மனையில் முகூர்த்தமுறை பந்தலிட்டுப் பந்தலிட்டுப் நல்ல பரிமேற் கட்டிகட்டி சந்த முடனே தலைவாழையும் நிறுத்தி அம்மைமா ரவர்க்கு அரங்கு மிகவகுத்துச் செம்மையுடன் மாயன் சிறந்திருக்க மேடையிட்டுக்  கட்டிமேற் கட்டியெனக் கனிபல துங்கொணர்ந்து வெட்டி யிராமிச்சி மிகுத்தபன்னீர் சந்தனமும் வாடைக் கமகமென மலர்பிச்சித் தார்தூக்கி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(495,'அய்யா இணத்தாங்கல்களுக்கு எழுந்தருளல்','மேடை யலங்கரித்து விதானம்ச பலதணிந்து அய்யாவுக் கமுது ஆனக் கனிவகையும் மெய்யானத் தாய்மார்க்கு வேண்டுகின்ற தீன்வகையும்  கூடவந தநருட்குக் கோப்புபல சேகரித்து வாட விடாமல் வல்லபல தீனதுவும் மேளத் தொனியும் வெடிவாணக் கோப்புடனே காளடம் மானம் கடிய சிறப்புடனே அய்யானை நன்றாய் ஆனபரி மேலேற்றி வையாளி கொண்டு மாதாவைத் தொட்டில்வைத்து ஆடல்பா டலுடனே அரம்பைக் குரவையுடன் நாடதிய நாரணர்க்கு நல்ல விருந்தெனவே சான்றோ ரவர்கள் தாங்கூட்டி தங்களுட மீண்டேவார் மனையில் விருந்து மிகக்கொடுத்து நாரணர்க்குச் சான்றோர் நல்ல சுருளும்வைத்துக் காரணர்க்கு நல்ல கனத்தபட்டு கள்கொடுத்து அம்மைமா ரவர்க்கு அதிகப்பட்டுச் சேலைகளும் செம்மை யுடன்கொடுத்துச் செய்வார் விருந்தெனவே விருந்து கொடுத்து மேலதிய நற்சிறப்பாய்த் திருந்து மவர்பதிக்குத் திரும்பக்கொண் டேவிடுவார் இப்படியே மாயவரும் ஏற்றசான் றோர்களுட மைப்புடைய வீடோறும் மாயன் பதிதோறும் விருந்து மருந்தி வேதா கமம்போலே பொருந்து மிகனை புரிந்து மிகமகிழ்ந்து ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(496,'பூமடந்தையம்மை திருக்கல்யாணம்','இன்ன மொருபெண் ஏற்ற மடந்தையரை நன்னகரி மெய்க்க நாமணங்கள் செய்யவென்று கேட்டுப் பரிசமிட்டுக் கிளர்ந்த முகூர்த்தமிட்டு நாட்டு நருளறிய நாரிமின்னாள் தானிருக்கும் ஊரிலவர் சென்று உற்றமங்க ளம்புரிந்து பாரி லகமகிழப் பதியில்வந்து தானிருந்தார்  பதிதனிலே வந்து பாவித் தகமகிழ்ந்து விதியால் பெரிய விருதுக் கொடிகள்கட்டிச் சொத்தாஸ்தி வஸ்து தொகையெண்ண மில்லாமல் வத்தாஸ்தி பேரில் மனது மிகநாடி நிலங்கரைகள் சுற்றி நிறைபயிர்கள் தன்னிலைவாய் தலம்புகழும் நாதன் தனதுள் ளகமகிழ்ந்து பொன்னான பெண்களுக்குப் பொசிப்பும்பல வஸ்துக்களும் கண்ணான மாயன் கையார வேகொடுத்து நடத்திப் பெண்ணாரை நல்ல வொருங்காக இடத்தி லிருத்தி இகனை புரிகையிலே எம்பெருமாள் நாட்டு இகனை நிறைவேற்றிப் பொன்பதியில் நாளேழும் புரிந்து திருநாளும் நடத்தித் திருமால் நாட்டுக்கணக் கும்பார்த்துத் தடத்தின் வழியாய்த் தானிருந் தாராய்ந்து வந்தநாள் வந்து வையகத்துச் சோதனைகள் இந்தநாள் வரைக்கும் இருந்துநாம் பார்த்ததிலே மிச்சமெந்தச் சாதியென்று மேலோர் மிகஅறிந்து அச்சமில்லாச் சாதி ஆதிச்சான் றோர்களையும் விலைபோட்டு ஆளடிமை மேவிக்கொள்ள வேணுமென்று மலையாம லெம்பெருமாள் மனதிலுற்றுச் சான்றோரை  உயிரறிந்து நல்ல விலைகூறி ஆளடிமை மெயிதறிந்து நாதன் முழுதடிமை கொண்டனராம் கொண்ட அடிமை குலச்சாதி யானோரை அண்ட மறிய ஆகந்தெளிந் தெடுத்துச் சேர்த்துவைத்துக் கொண்டார் சிவஞானப் பொக்கணத்துள் காத்தந்தப் பெண்களுடக் கற்பறிய வேணுமென்று பெற்றுவந்தத் தீர்ப்பின் பிரமாணத் தின்படியே சித்துவொன்று செய்யச் சிந்தைதனி லுற்றனராம் உற்று மனதில் உபாய மதுவாக  வத்து வகைபேரில் மகாநேட்ட மாயிருந்து பொன்னு பணங்காசு பெரிய நிலமதிலும் தன்னுள் நினைவாய்த் தானிருந்தா ரம்மானை');");

