        db.execSQL("INSERT INTO akilathirattu2 VALUES(497,'அய்யா வைகுண்டம் எழுந்தருளல்','இப்படி இவர்தான் பாரில் எண்ணிய கரும மெல்லாம் ஒப்புடன் முடித்துத் தன்னால் ஊணிய தெல்லாந் தோண  வைப்பபுடன் யாமங் கூறி மனுக்களைத் தெளித்து ஏற்று இப்புவி விட்டுக் குண்டம் ஏகிட மனதி லுற்றார் ஏகிட மனதி லுற்று இருக்கின்ற உபாயந் தன்னை வாகிட மிருக்கு மாதர் மனைவிகள் மக்கள் சான்றோர் தாகிடச் சீசன் மார்கள் தங்களு மறியா வண்ணம் கோகிடக் குண்டஞ் செல்லக் குருவையும் நாட்ட மானார்  மனதினி லுற்று மாயன் மாதர்கள் மக்க ளோடு தனதுள மகிழ்ந்து கூடித் தயவுட னிருக்கும் போது என்துடல் காணா வண்ணம் இன்னமுஞ் சிலநாள் பாரில் தினமுடல் வாடி நீங்கள் தேடுவீ ரென்னைத் தானே தேடியே யிருக்கும் போதுத் தேவியர் மக்கள் காண  நாடியே சொரூபங் கொண்டு நான்வந்து நடுக்கள் கேட்பேன் பேடிகள் நினைத்தி டாமல் பேசின நூற்போல் கண்டால் வாடியே மலையா வண்ணம் வாழ்தர்மப் புவியில் வாழ்வோம் என்றவர் சிரித்துக் கொண்டு இருந்ததைக் கவனி யாமல் விண்டவர் நளியாய்ப் பேசி விடுத்ததை மக்கள் சான்றோர் என்றைக்கும் ஆள்வா ரென்று இருந்தன ரவர்க ளெல்லாம் குண்டத்தை மனதி லெண்ணிக் குருவையும் நாட்ட மானார் குருவே யெனக்கு ரருளிக் குறித்த ஆண்டு இதுவரைக்கும் ஒருசொல் மொழியே குறையாமல் உகத்துத் தீர்ப்புக் கூறிக்கலி கருக யாம மிகவுரைத்துக் கைக்குள் ளெடுத்தென் மக்களையும் வருகக் குண்ட மனதிலுற்றேன் வந்தே கூட்டிக் கொடுபோவீர் ஆதியே யென்றன் அப்பு அனந்தகுரு சோதியே யென்றன் சொந்தத் திருவுளமே இந்நாள் வரைக்கும் இவ்வுலகில் பேதயிரென்று அந்நா ளுரைத்த அப்புநா ராயணரே  இனியா னுமது இலங்கும் பதியில்வர முனியா னவற்கு முற்ற விடையருளும் ஆண்டாச்சு லக்கு அடுத்திருக்கு தையாவே வேண்டா முலகம் மேலுலக மேயழையும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(498,'அய்யா வைகுண்டம் எழுந்தருளல்','அங்கழையு மையா ஆதிமுனி யென்றனையும் தங்க இனிமாட்டேன் தரிப்பிட மங்கேயல்லால் என்று வருந்தி இவரிருக்கும் நாளையிலே மன்று தனையாளும் மாயத் திருமாலும் வரங்கொடுத்த நாளும் வைத்தலக் கும்பார்த்துப் பரமிருந்து தேர்ந்து பாலன் தனைவருத்த வேணுமென் றீசுரரும் வேதத் திருமாலும் கண்ணு மகனைக் கடிய விமானமதில் ஏற்றிவைத்து மாமுனிவர் இருபுறமுஞ் சூழ்ந்துவரச் சாத்திரியோர் ஞான சற்குருவைத் தானனுப்பி வைகுண்ட வீட்டை வானோ ரலங்கரிக்க மெய்கொண்ட நாதன் விளம்பினார் மேலுகில் வானோர்க ளெல்லாம் வைகுண்ட வீடதையும் தானமுட னெல்லாம் சணமே யலங்கரித்தார் சற்குரு வான சடாமுனி யிங்குவந்து மெய்க்குரு வான வீர விசயனுக்குச நல்லதூ னென்று நவின்றவரை யும்புகழ்ந்து செல்ல அரைமணிக்கு சென்றிங்கே நில்லுமென மாதர் மனைமறந்து வாழ்வை மிகமறந்து தாத ரூண்மறந்து சடல வுடைமறந்து நன்மைபல சோபனமும் நளிப்பேச்சு மறந்து செம்மைக் குருவாய் செம்மிமூ லமடக்கி மூல மதிலடக்கி உடலூற்றைத் தானிறக்கிக் காலை மடக்கிக் கண்ணனூர் தானோக்கி கைக்குள்நின்ற மக்கள் கலங்கி மிகப்பயந்து நிற்கும் நினைவை நினைவி லறிந்துவைத்து நடக்கும் படியான நல்மொழிக ளுமுரைத்து உடற்குள் குறியாய் ஒத்துமிக வாழுமென்று சொல்லிமக்கள் கையைத் திருமுகத்தோ டேசேர்த்து இல்லியல்பாய் வாழும் யாமுழித்து வாறோமெனப் பதறாமல் நீங்கள் பண்பா யொருப்போலே சிதறாமல் நீங்கள் செய்யஅனு கூலமுமாய்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(499,'அய்யா வைகுண்டம் எழுந்தருளல்','இருந்துமிக வாழும் என்றுநாமங் கொடுத்துத் திருந்துபுகழ் மாயன் சிறந்தவோ ராயிரத்து ஓரிருபத் தாறாம் ஓங்கு மிடபமதில்  சீரியல்பா யான தேதி யிருபத்தொன்றில் பூருவ பட்சம் பூச நட்சேத்திரத்தில் வாறுடைய சோம வாரம் பொழுதூர்ந்து பன்னிரண்டு வேளை பாவிக்கும் நேரமதில் மெய்கொண்ட சான்றோர் மேலாசை யுள்ளிருத்திக் கைகண்ட மாமுனிவர் கட்டாய் மிகச்சூழ வைகுண்ட மேக வழிகொண்டா ரம்மானை வைகுண்ட மானார் வாய்த்த விமானமோடு வான லோகத்தார் மலர்மாரி தாந்தூவ விமான மதிலேறி மேலோக மீதில்வரத் தேவாதி யெல்லாம் திருப்பாட்டுக் கூறிவர மூவாதி யெல்லாம் முகமலர்ந்து தாமிருக்கத் தெய்வரம்பை மாதர் திருக்குரவை தாம்பாட மெய்வரம்பு நாதன் மெய்குண்ட மீதில்வந்தார் வந்த பொழுதே மறையோரும் வானோர் தேவ ரிஷிமாரும் நந்தன் பெருமான் மகனெனவே நாடி வணங்கி மிகப்போற்றி எந்தன்பிரனே யெங்களுக்கு இரங்கியெ மையாட் கொண்டவரே சிந்த ரெவரு மிகப்போற்றத் திருமால் மகனைக்கண் டாவினரே கண்டே மகனை மிகஆவி கமல முகத்தோ டுடனணைத்துப் பண்டே செகலில் மகரமதுள் பண்பா யிருத்திய ருளிவைத்த  நன்றோர் மொழியுங் குறையாமல் நடத்திக் கணக்கின் பிரகாரம் இன்றே யெழுந் திங் கேகிவந்த இளமான் கன்றென் றாவினரே ஆவி மகனை அன்போ டுறவணைத்துத் தாவி மகன்மேல் தயவு மிகக்கூர்ந்து மாமுனிவர் தங்களையும் வாருங்கோ நல்லதெனத் தாமுனிந்து மகன்மேல் தயவாகத் தாயாரும் அன்பு மிகக்கூர்ந்து அருமைமக னையாவி இன்புருக நன்றாய் இருந்தாள்காண் லட்சுமியும் சந்தோ சமாக சங்கரருந் தான்மகிழ்ந்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(500,'அய்யா வைகுண்டம் எழுந்தருளல்','வந்தாயோ என்று மகிழ்ந்துகொண்டா ரம்மானை நான்முனிவனும் வேத நல்லதெய் வேந்திரனும் தாமு மிகமகிழ்ந்து சந்தோசங் கொண்டனராம் சரசு பதிமாதும் தாயீசொரி யாளும் பரசுரா மன்முதலாய்ப் பண்பாய் மகிழ்ந்திருந்தார் கயிலை யுகமும் கமண்டல மேழ்புவியும் அகிலமது அறிய ஆனார்வை குண்டமென எக்காள பூரிகையும் இடமாம் மானமுடன் முக்காலத் துள்ள முனிமார் முழக்கிமிக ஈரே ழுலகும் இவர்செய்த நற்றவம்போல் ஆரேதுஞ் செய்ய அடங்கா திவர்தவந்தான்  பிறவிநா சக்கலியன் பொல்லாத வையகத்தில் திறவி தனிலிருந்து செய்துதவ மேற்றுவரோ கண்டாற் பவஞ்சூடும் கலியனுட வையகத்தில் தண்டரள மானதவம் தாக்கிநிறை வேற்றுவரோ கலிதொடரு முன்னே கனகதுவா பரயுகத்தில் சலியாமல் வாழ்ந்த தர்மி முதல்தவத்தோர் பதறி நடுங்கிப் படாதினிமே லென்றுசொல்லிக் குதறி மலைந்து கொடுவான மாமுகடும் கெடுவுங் குகையும் கீழுமேலும் நுழைந்து முடுகி முன்னோடி நுழைந்தார் மிகப்பதறி அப்படியே நல்ல அறிவோர் பதறிமிகத் தப்பிவர யிவரும் சடையாம லவ்வுக்ததில் முப்பிறவி யுந்தியிலும் உதித்துக் குதித்தவராய் அவ்வுகத்துக் குள்ளே ஆண்டுமூ வாறுவரை செவ்வுமகா ஞானத் திறவி வெளிநாடிப் பவமணுகா வண்ணம் பாரத் தவம்புரிந்து சிவமதுவைக் கண்டு செயல்பெற்று அவ்வுகத்தில் மாது கிளையோடும் மக்கள்பல சொத்தோடும் சீது மனையோடும் சிறந்தபல ஞாயமொடும் இருந்துமிக வாழ்ந்து எள்பாவஞ் சூடமால்  சிறந்து வைகுண்ட சிவவீடு காண்பவரார் பெரிது பெரிதெனவே பெரிய முனிவோரும் அரிது அரிதெனவே அகமகிழ்ந்து கொண்டாடிக் மன்றாடுங் கயிலை வானவரும் போற்றிநிற்க');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(501,'சான்றோர் கோவில் அமைத்தல்','பூமிதனில் மக்கள் பெரியகுலச் சான்றோர்கள் சாமியுட தேவி சுற்றமட மாதர்களும் அன்பான தொண்டர்களும் அயர்ந்து முகங்கோடி வம்பான பேர்கள் வசையாய் மிகநகைக்க வைகுண்ட மேக வழிகொண்டார் நம்மளையா என்று புலம்பி எல்லோருந் தாந்தேறி முன்று மொழிந்த மொழிப்படியே நம்மளையா நடந்தார் தானல்லால் ஞாயம்வே றில்லையென்று மாதத் திருநாளும் வாரமற வாதபடி நாதன் தனக்கு நாமள்செய் வோமெனவே கோவிலது வைத்துக் கூண்டதிரு நாள்நடத்திச் சேவைசெய்து மக்கள் தினஞ்சூழ போற்றிநிற்க நாதனுட தேவி நாயகிமா ரெல்லோரும் சீதமண வாளருட சீர்பாத முண்டெனவே துயரமதாய் வாடி சிவனே செயலெனவே  இயல்புதரு வீரெனவே இருந்தார் தவமுறைபோல் நண்பரெல்லாம் வாடி நாரணா போற்றியென்று அன்பர் முறைபோல் அகமகிழ்ந்து தாமிருந்தார் இப்படியே அன்பர் ஏந்திழைமா ரெல்லோரும் முப்படியே சொன்ன முறையெனவே யிங்கிருக்க அப்போ கயிலைதனில் ஆனகுரு நாரணரும் மைப்போ லினிய மகனைமிக முன்னிறுத்திக் கூண்ட மணியே கோகுலமே கற்பகமே ஆண்ட மணியே அருளுமெந்தன் கண்மணியே என்றன் மணியே என்னாத ஓவியமே உன்றனைநான் பெற்று உற்ற கலியுகத்தில் இருத்தி தவமும் ஏற்றமுறை யும்நடத்தி வருத்தி நருளை மகாசோ தனைப்பார்த்துத் களித்து கொண்டாடிக் கலிநெகிழ நல்லோரைத் தெளித்துவா வென்று சொன்ன முறைவரையும் இருந்துநீ யங்கே இகனை நடத்தினதைப்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(502,'சான்றோர் கோவில் அமைத்தல்','பொருந்தும் படியே புகன்றிடுநீ நாமறிய மாயவர் தாங்கேட்க மனது மிகமகிழ்ந்து ஞாயக் குருநாதன் நாடி மிகவுரைப்பார் அய்யரே கோவே அப்புவே நீர்கேளும் பொய்யர்வாழ் கலியில் புரிந்துத் தவசாக ஆறு வருசம் அதிகத் தவம்புரிந்து வாறுமுன் னீரும் வாரியிலே வந்திருந்துக் கேட்டு மகிழ்ந்து கெணித்ததுமேல் நல்வளங்கள் தாட்டாண்மையாய் நீரும் தாமொழிந்த சொற்படியே நித்தந் திருநாளும் நேரிழைமா ருள்ளதையும் புத்திரரை யுஞ்சேர்த்துப் பெண்களையு மாலையிட்டு மாலையிட்டுப் பெண்களோடு வாழ்ந்து மகவீந்துக் கோலமணி மாதருக்குக் கோடிபல வஸ்துவகை தேடிக் கொடுத்துத் தேசக்கலி நாடழிய ஆடிக் களித்து யாம மிகக்கூறி நல்லோரை யெல்லாம் நாடித் தெளித்துமிக  வல்லோரே யுந்தன் வைகுண்ட மீதில்வந்தேன் ஆஸ்தி பலதும் ஆயளைமார் மக்களையும் கோத்திர முங்கண்டேன் கூறிவைத்த லக்கில்வந்தேன் அந்தச் சிறப்போடு அங்கிருந்தே னானாக்கால் இந்தவை குண்டம் யான்வந்து காண்பேனோ என்று குருமகனார் இசைந்தவுடன் நாரணரும் அன்று அயைச்சிருந்த ஆனமுனி தங்களையும் இருபேரை யும்வருத்தி இவர்மொழிந்த வார்த்தையதில்  ஒருமொழிதா னாகிடினும் உலைவதிங்க ளுண்டோசொல் தவறாமல் சொல்வீர் தப்பிதங்க ளுண்டானால் பதறாமல் சொல்வீர் பரம்பெரிய மாமுனியே எனக்கறியா வண்ணம் யார்செய்வார் மாமுனியே தனக்கறிய நீங்கள் சாற்றுவீ ருள்ளபடி என்று மகாகுருவும் இயம்ப முனிவோரும் நின்று வாய்புதைத்து நெடியோன் பதம்பூண்டு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(503,'கலைமுனி, ஞானமுனி சாட்சியம்','கண்ணுக்குள் மணியாய் நின்ற காரணக் குருவே ஞான விண்ணுக்கு ளெவர்க்கும் ஞான வெளிச்சுட ரான மூர்த்தி ஒண்ணுக்கு ளொண்ணாய்நின்று உலகீரே ழனைத்துங்காக்கும் அண்ணுக்குங் கடந்த சோதி அறிவுள மறியா தேதோ எறும்புகடை யானைமுதல் பேதா பேதம் எண்பத்து நான்குயிர்க்கு மேக மாக உறும்பொருளாய் நின்றகுரு நீயே யல்லால் உலகமதி லாருளதோ வுடைய மாலே செறும்பொருளா முமதுமக னங்கே வந்து செய்தவசு முறையதிலும் நடத்தை மேலும் தறும்போருள்போ லெங்கள்மன மறிய வொன்றும் தப்பிதங்க ளில்லையெனத் தாழ்ந்து நின்றார் தப்பிதங்க ளில்லையையா தவத்துக் காதி தண்மைமிகுக் குணமுடைய தர்ம சீலன் உப்பரிகை மீதிருந்து அரசே யாள உன்புவிக்கு ஆசையல்லா லுலகி லில்லை மைப்பிதுக்க மானதொரு குழலா ரோடும் மக்களொடும் வாழ்வோடும் வாழ்ந்த உள்ளம் நற்பிதுக்க மானகுரு நாட்ட மல்லால் நானிலத்துள் ளாசையில்லை நாங்கள் கண்டோம் நாங்கள்மிகக் கண்டோமிவ ருள்ளம் போலே நாடுபதி னாலதுலுந் தவத்தோர் பார்த்தால்  காங்கரிது எங்களுட கருணை நாதா கலியுகத்தை வேரறுத்த கடவுள் பாதம் ஒங்கஅவ ரடியிணையைப் பணிந்து நாளும் ஊழியங்கள் செய்தவர்க்கு உவந்து நிற்கப் பாங்கருளிச் செய்துமிக ஆண்டு கொள்ளும் பரமகுரு நாதனெனப் பணிந்து நின்றார்.  நின்றவரை முகம்நோக்கி மாயன் தானும் நிலவரங்க ளுள்ளதெல்லாம் நினக்குள் ளாச்சு இன்றிவரை நீங்கள்கொண்டு நமது குண்டத்(து)');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(504,'கலைமுனி, ஞானமுனி சாட்சியம்','ஏகபதி வாசல்தெரு எல்லாங் காட்டிக் கொண்டுஅந்தத் தையிலமதில் மூழ்க்கிப் பின்னும் கூட்டிக்கொண்டு வாவெனவே கூற வானோர் டுண்டுபல மேளமொடு தாளத் தோடு உற்றரத மீதில்வைத்து உம்பர் சென்றார் சென்றவர்கள் நாரணரின் குண்ட மானச் சிறப்பையெல்லாங் காட்டிமிகத் தெளிந்த சந்த தன்றமலர் வாழ்தங்கப் பதத்தில் மூழ்க்கித் தண்டிகையின் மீதிருத்தித் தயவாய் வானோர் கொண்டவர்கள் மாயனுட பதத்தில் விட்டுக் குலாவியந்த வானோர்கள் கும்பிட டேற்ற  மன்றலணி மாயவரு மீசர் தானும் வைகுண்டமா முனியெனவே வழங்கி னாரே வழங்கியந்த மாயவரும் வானோர் தம்மை வரவழைத்து நீங்கள்மகா மேர்வு சென்று இளங்குருமா னானகுரு வைந்த ராசர் இன்றுபுவி யீரேழு மடக்கி யாளப் பழங்குருநூல் முறைபோலே பாரில் தோன்றிப் பதியாள வந்தாரெனப் பதிப்பீ ரென்று வழங்கமிக மறையோர்கள் மேர்விற் சென்று மாயனுரை தவறாமல் வகுத்தார் தாமே வகுத்திடவே முன்னுரைத்த முறையோ லுள்ள மகாபெரிய கிரீடமு மவர்க்குச் சூட்டிப் பகுத்துடைய செங்கோலும் பரம னார்தன் பரம்பெரிய முத்திரியும் பலன்கள் யாவும் தொகுத்திடப்பொன் னீராளத் துகிலு மீந்து துதிசிங்கா சனமீதில் தூக்கி வைத்து மகுத்துவமாய் வாழுமென வானோர் போற்ற  மறையவரு மீசுரரும் வரமே யீந்தார் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(505,'அய்யா சான்றோர்க்கு இரங்கல்','ஈந்திடச் சிங்கா சனத்தில் இருந்தவ ரேதோ சொல்வார்  கூர்ந்திட எனக்கு இங்கே குணமெல்லாம் நலம தாகச் சார்ந்திட மருளிச் செய்தீர் தற்பரா வொப்பில் லானே ஓர்ந்திட எனக்கு அங்கே உகந்தவ ருண்டே முன்னம் ஆடையும் பொன்னுங் காசும் அன்னமும் பாலு முந்தன் நீடிய உதவி யாலே நினக்கவ ரிதுநாள் மட்டும் வாடியே முகங் கோடாமல் மனத்தய வதனா லீந்து தேடியே மக்கள் தம்டமச் சிந்தையில் நினைக்கு தென்றார் ஐயரே இதுநாள் மட்டும் அவரெனக் களித்த செல்வம் வையகமே தரியா தென்றன் வாயினா லுரைக்கப் போமோ மெய்யெல்லா மவர்கள் பேரில் மேனியு முருகு தையா செய்யனே யவர்க ளென்றன் திருப்பதம் வணங்கச் செய்வாய் திருப்பதம் வணங்கச் செய்வாய் என்றெனத் தெளிந்த ஞானி கருவதி லுதித்த சான்றோர் சாதியில் கௌவை யாகிக் குருபதம் வணக்கஞ் செய்து கூறிய மொழியைக் கேட்டு மருவணி துளசி பூணும் மாயனு மகிழ்ந்து சொல்வார் கைகண்ட மணியே யென்றன் காரணக் குலமே கன்றே வைகுண்ட மணியே யுன்றன் மனதலைந் திருக்க வேண்டாம் மெய்கொண்ட மணிக ளான மேன்மக்கள் சான்றோ ரெல்லாம் பொய்கொண்டக் கலியை விட்டுன் பொற்பதம் பெறுவர் கண்டாய் கலியை யெரிக்கத் தவசிருந்த கருத்தை யறிந்து உன்னிடத்தில் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(506,'அய்யா சான்றோர்க்கு இரங்கல்','வலிய வந்துன் பதம்வணங்கி மனதுக் கேற்க நின்றவரும் பொலிய உனக்கு அமுதுடன் புடவை பலது மீந்தவரும் ஒலியத் தான தர்மமதும் உகந்தே யளித்தோர் மிகவாழ்வார் இரக்கும் படிபோல் வடிவெடுத்து இருந்து கலியை முடிக்கஅங்கே உரைக்கு மொழியைக் கேட்டுனக்கு உதவி புரிந்த உத்தமரும்  கரைக்கும் படியே யமுதளித்துக் கைக்குள் ளேவல் புரிந்தவரும் மறைக்குங் குருவே யென்னாணை வந்தே சேர்வா ருன்பாதம் உடுக்கத் துணிக ளில்லாமல் உலக மதிலே யெளியவனாய்  முடுக்க மதுவே யில்லாமல் முடியும் விரித்துப் பேயனைப்போல் கடுக்கக் கலியை யெரிப்பதற்குக் கவிழ்ந்து சிறைநீ யங்கிருக்க அடுக்க உதவி செய்தவர்கள் அவரே யுனக்கு மகவாமே ஆமே யவர்க ளல்லாமல் அதிக மகனே நீயிருந்த ஓமே யறியா வண்ணமுந்தான் உலகி லுன்னைப் பழித்தவர்கள் சாமே தீயில் தாங்கிடந்து சடமே நரகுக் குள்ளாகி வேமே யுன்றன் மேலாணை விறுமா பதத்தி னாணையிதே ஆணை மகனே அசையாதே கண்மணியே சாணா ரினக்குலங்கள் தப்பாம லுன்னுடையத் தர்மபதி ராச்சியத்தில் தாம்வாழ்வார் சத்தியமாய் வர்ம முனைப்பேசி வம்புசெய்த நீசர்குலம் அமுந்த நரகமதுள் ஆகுவார் சத்தியமாய்க் குளிர்ந்த மனதுடைய கோவேயென் கண்மணியே மலங்காமல் வாழ்ந்திருநீ வையகத்தி லெப்போதும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(507,'பொய் வேசம்','பெலங்க ளதுபார்க்கப் பொய்வேச மொன்றனுப்பி மகனே நீசொன்ன மாநூல் முறைப்படியே உகமீதே யார்தான் உகந்திருக்கிறா ரெனவே பார்த்துவர வோர்சொரூபம் படைத்து அனுப்புகிறேன் ஏற்றந்தச் சாதிக்கு இன்னமீ ராறுகலை அடுக்கும் வரைநீ அவர்பேரில் நாட்டமதாய் முடுக்க முடனிந்த உற்றசிங் காசனத்தில் தவம்போ லிருந்து தானாளு குண்டமதில் ஆளுநீ யரசு ஆண்மை குறையாமல் நாளு வரைமகிழ்ந்து நலமாக வாழ்ந்திருநீ அனுப்புகின்ற பொய்ச்சொரூபம் ஆங்கார மற்றவுடன் மனுப்புகழ நாமள் வையகத்தின் மீதிறங்கி நிச்சித்து வைத்த நினைவா னதின்படியே வச்சிருந்த நற்பதியில் வாழலாங் கண்மணியே என்றரைத்து மாகலியில் ஏற்றபொய் வேசமதாய் குன்று தனிலிருந்து கோலமொன்று தானனுப்ப வந்தங்கு குதித்ததுகாண் வையகத்தி லம்மானை நந்த னானென்பான் நாரணர்க்கோன் ராமனென்பான்  முந்தச் சுவாமிகட்டு முன்னுதித்து வந்தேனென்பான் செந்தழல்போல் நின்றிடுவான் சிட்டுப்போ லேபறப்பான் கண்டகண்ட அற்புதங்கள் கண்ணாரக் காட்டிடுவான் பண்டையுள்ள வைப்பைப் பாரறியக் காட்டிடுவான் தீர்க்கமுட னற்புதங்கள் திடீரெனவே காட்டிடுவான் மார்க்கம் பலதணிவான் வைகுண்ட மென்றிடுவான் கடலில் நடப்பேனென்பான் கனலி லிருப்பேனென்பான் மடவாரை யெல்லாம் மாலை யிடுவேனென்பான் இப்படியே கோடி எண்ணிறந்த அற்புதங்கள் செப்பிடுவான் பூமியிலே தேச நருள்மலங்க சூட்ச மநோகம் சொல்லொணா வித்தையதாம் தூட்ச மறியாமல் தொல்புவியெல் லாமயங்கும் இந்தப் படியாய் இருக்கின்ற நாளையிலே முந்த வைகுண்ட முடிசூடி வாழ்ந்திருப்பாய்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(508,'நல்லவை முழித்தல்','சிவனு முமையாளும் செய்யத் திருமாலும் தவமுனி வர்களும் சரசுபதி மாதுமையம் நான்முகனும் வேத நல்ல மறையோரும் தான்முனிந் தாகமத்தின் தன்மை யதின்படியே தொடுத்த கலியன்று தோன்றி குதித்தவரைக் கொடுத்தகணக் கின்படியே கொல்லக் கலிதனையும்  வானமது குழவி மண்பூமி யம்மியெனத் தானமது நிரப்பாய்த் தன்னா லிருந்திடவும் உண்டான தெல்லாம் உடன்கரிந்து நிறீடவும் அண்ட ருலகம்வரை அசைந்து முழித்தடவும் மாயவரு மீசர் வைகுண்ட மாமணியும் தாய்தமர்க ளானச் சத்தி திருவுடனே வைகுண்ட ரான மகனை நடுநிறுத்தி மெய்குண்டத் தேவர் மிகுகூல மாகிவர முத்திச் செங்கோலும் முழிப்புச்சங் கும்பிரம்பும் பத்தியுள்ள நாரணர்தான் பாலனுட கைக்கொடுத்து ஆகாய மாக அவர்கள்மிக வந்துநிற்க வாகாக நாரணரும் மகனை முகம்நோக்கி நல்ல மகனே நாடுந் தவத்தோனே செல்ல மகனே திருபெற்ற கண்மணியே உநத்னக்கு ஏற்ற உற்ற மனிதர்களும் சந்தமுள்ளத் தேவியரும் தனதுகந்த புற்பூடும் ஆகின்ற பட்சிகளும் ஆன மிருகமதும் பாகுசெறி விருட்சம் பண்பான நற்பதியும் யாதாக முன்னே யாமமிட்ட நூற்படியே நீதான் வருகவென நினைத்துவிடும் என்மகனே  நினைத்தார் வைகுண்டர் நினைவின் படிபோலே மனத்தயவு கூர்ந்து மலமலென நல்மனுவோர் முழித்தார் வைகுண்டர் மொய்குழலார் மக்களுமே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(509,'நல்லவை முழித்தல்','களித்தே யிருந்த கருத்தின் படியாலே பட்சி மிருகம் பலமிருகச் செந்துக்களும் அச்சுதர்க கேற்ற ஆதி விருட்சங்களும் புதுப்பூமி புதுநிலவு புதுவானம் புதுவாயு குருபூமி யானக் கூண்டதாயகப் பொற்பதியும் பொன்பதியும் நற்றெருவம் பெரியதெப்ப வாவிகளும் அன்பதிய நல்மனுவும் அனுகூல மாயுதித்தார் எம்பெருமாள் முன்னே யாம முரைத்தபடி வம்பரெல்லா மாண்டார் மனதுகந்தோர் தாமுழித்தார் தன்ம யுகத்துக்குத் தானேற்ற வஸ்துக்களும் நன்மையுடன் தன்னால் நாடி மிகக்குதித்தார் நல்ல யுகத்தர்ம நாடு மிகக்குதித்தால் வல்லவை குண்டமதாய் வந்தவுடன் வாருமெனச்  சொல்லி யயைச்சிருத்த சுத்த மிருகமதும் நல்ல மனுவோரும் நற்பறவை யானதுவும் விருட்ச மதுவும் மேலுகந்த வஸ்துக்களும் வாருமெனச் சான்ன வகையெல்லா மேமுழித்தார் ஆருமிக வொவ்வாத அரியோ னகமகிழ்ந்து சீருகந்த நாதன் திருமால்சந் தோசமதால் ஆகாத்த தெல்லாம் ஆழிதனை வருத்தி வாகா யரித்து வன்னரகில் தள்ளிமிகச்  சுத்த யுகத்தைச் சுத்தி வருத்துமென்று கற்றைக் கங்கையாட்குக் கரியோன் விடைகொடுத்தார் விடைவேண்டி வாரி விமலன் மொழிந்தபடிக் கடல்வாரி செய்து கமலயுகஞ்சுத்திபண்ணிக் கர்த்தனரி நாரணரைக் கடலுமிகத் தெண்டனிட்டு முத்தனரி நாதன் முன்னுரைத்த நீசனெனக் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(510,'நடுத்தீர்ப்பு','குறோணி அவனுயிரைக் கொண்டுவந்து முன்னிறுத்திச் சுறோணிதப் பாவி சொல்லடா உத்தரங்கள் ஏழு பிறவி இதுவரையுஞ் செய்துவுன்னை வாழுநீ யென்று வரமருளிப் பார்த்தேனே இக்கலியில் நானும் இரப்பனைப்போல் வந்திருந்து மிக்க உலகறிய வெறிப்பேயை யுமெரித்துத் தண்ணீரால் மானிடர்க்கு சகலநோய் தான்தீர்த்துப் புண்ணியமாய்த் தருமம் புரிந்து மிகவருளிக் காணிக்கை கைக்கூலி காவடி யும்நிறுத்தி ஆணிக்க மாக அம்புவியில் யாவரையும்  ஒருதலத்தில் விட்டு உற்றபுத்தி சொன்னேனே இருபுத்தி யால்நீயும் என்னைப் பிடித்தடித்தாய் கடடியென்னைப் பேயனெனக் கடுவிலங்கில் வைத்தாயே அட்டிசெய்ய வில்லையல்லோ ஆண்டியாய் நானிருந்து பண்டார மாகப் பார்மீதி லன்பரிடம் நன்றாகப் பிச்சை நான்வேண்டி யேகுடித்துத் தர்மமா யல்லோ சனங்களுக்கு ஞாயமுண்டோ வர்மமா யென்னை வந்தடிக்க ஞாயமுண்டோ அல்லாமல் முன்னே அரனிடத்தி லாணையிட்டு எல்லா மினிமேல் இராச்சியத்தில் யாதொருவர் பண்டார மென்று பாரறிய வந்தவரை அண்டே னவரை அட்டிமிகச் செய்யேனென்று ஆண்டிகளை நானும் அட்டிமிகச் செய்ததுண்டால் கூண்டிறந்து போவோம் கொடிப்பிதி ரானதெல்லாம் சேனைத் தளமிழந்து செல்வமது தானிழந்து ஏனைக் குழலோடே என்கிளைக ளெல்லோரும் தன்னா லிறந்து சளநரகம் போய்விடுவோம் முன்னாள் நீசொல்லி மொழிந்தாணை யின்படியே பண்டாரந் தன்னைப் பதைக்க விடாதபடி அண்டாமல் நீயும் அடித்ததினால் ஞாயமென்ன ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(511,'நடுத்தீர்ப்பு','சொல்லடா நீதான் சொன்னமுறைப் படியே கல்லடா நீதான் கவிழ்ந்துநிற்கும் ஞாயமென்ன பாவிநீ யேழ்பிறவி பார்மீதில் தோன்றியதில் ஆவி யறிய அனுப்போலும் நன்மையது செய்ததுண்டோ சொல்லு செவியுனக்குக் கேட்கலையோ மைதரிய யிப்பிறவி மிகுஇரப்பன் போல்நானும் எளிய குலத்தில் எகாபரனே தஞ்சமெனச் சுழிமுனையை முத்தி சோமனருள் உள்ளிருத்தி உடுக்கத் துணிகளற்று உண்ணவூண் தானுமற்று படுக்க இடமுமற்றுப் பரமார்த்த மாயிருந்து சாதிபதி னெட்டதுக்கும் யாம முறைப்படியே நீதியுடன் தண்ணீரால் நொம்பலங்கள் நீக்கிவைத்தேன் நீயறிந்துங் கண்டும் நீணிலத்தோர் தாமறிய வாயிலிடும் வெற்றிலைக்கு மனுவோர் தடவுகின்ற சுண்ணாம் பானாலும் தொட்டுநீ தந்தாயோ எண்ணாமல் நீயும் என்னை மிகப்பழித்துப் பாவிநீ என்னைப் பரிசுகெடத் தானடித்து மேவிநா னிட்டுருந்த வெற்றிசுரக் கூடதையும் உடைத்துத் தகர்த்தாயே உற்றகந்தைக் காவியையும் மடத்தனமாய் நீயும் வலித்துக் கிழித்தாயே  தோளிலிடும் பொக்கணத்தைத் தூக்கியென்னைக் கீழ்ப்போட்டுத் தூளிபட வுதைத்துத் துண்டுதுண்டாய்க் கீறினையே ஐயோ நீசெய்த அநியாயஞ் சொல்லவென்றால் வைய மீரேழும் வழிந்து நிரம்பிடுமே பத்தினியாள் பெற்ற பாலதியச் சான்றோர்கள் மெத்த அவர்நன்றாய் விளங்கிருக்கணுஞ் சிவனே ஆடை யில்லாமல் அலமாந் திருக்கையிலே நாடதிக மான நல்லபட்டு நீராளம் தங்கச் சரிகைத் தலைப்பாக் குல்லாவுடனே மங்களமா யென்னை வந்தெடுத்துத் தானுடுத்திப்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(512,'நடுத்தீர்ப்பு','பாலும் பழமும் பருந்தேனும் சர்க்கரையும் மேலும் நவநிதியம் வேண்டும் பலகாரம் அன்னங்காய் சொர்ணம் அன்பாகக் கொண்டுவந்துப் பொன்னப்ப நாரணரே பெற்றவரே  வாயி லெடுத்திட்டு வளமாக என்றனக்குச் சாயத் தலையணையும் சரிகைத்தொட்டில் மெத்தைகளும் பாதங்கீழ்க் கொள்ளாமல் பாலதியச் சான்றோர்கள் ஈத லிரக்கமுடன் எடுத்தென்னைத் தோளில்வைத்துப் போற்றி மகிழ்ந்து பூமெத்தை மேலிருத்தி ஏற்றிப் பலகாலும் என்சொல் மிகக்கேட்டுத்  தாங்கி யிருந்த சான்றோர்க ளின்பெருமை நாங்க ளுரைக்க நாடுமிகத் தாங்காதே என்னென்ன பவிசு எமக்கவர்கள் தாமீந்து பொன்னம் பலமீதில் புகழ்ந்துமிக வாழ்வாரே பாவிநீ யென்னைப் பரிசுகெடத் தானடித்து ஏவிநீ விட்ட ஏவலா ளிப்போதெங்கே ஆணுவங்க ளெங்கேஉன் ஆனைப் படைகளெங்கே பூணுகின்ற தங்கப் பொன்னா பரணமெங்கே விஸ்தார மெங்கேநீ வீற்றிருக்கு மேடையெங்கே சுற்றார் கிளைகளெங்கே தோகைமயி லார்களெங்கே குதிரைத் தளங்களெங்கே கோட்டையெங்கே வாசலெங்கே சதுரா யணிவகுத்தத் தாண்டும் படைகளெங்கே தேரெங்கே யுன்றன் சிங்கா சனங்களெங்கே ஊரெங்கே யுன்றன் ஒழுங்குதளச் சேனையெங்கே மாடமெங்கே கூடமெங்கே மணிமேடை யாரமெங்கே தோடமெங்கே யுன்றன் தோழரெங்கே சூரமெங்கே ஆயுதங்க ளெங்கேவுன் அம்புதடிக் காரரெங்கே வாயுரங்க ளெங்கேவுன் வாயில்காப் போர்களெங்கே துட்டமெங்கே யட்டியெங்கே துரைத்தனங்க ளானதெங்கே பட்டமகங் காரமெங்கே பருங்கிள்ளாக் கானதெங்கே ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(513,'நடுத்தீர்ப்பு','மாடெங்கே நீதான் வளர்த்த மிகமெங்கே வேடிக்கை யெங்கேநீ விதித்த கணக்குமெங்கே ஓடி யுலாவும் உற்றசா ரட்டுமெங்கே வண்டியெங்கே யுன்றன் வாழ்வெங்கே மாட்சியெங்கே சண்டிப் பயலேவுன் தாடாண்மை யெங்கேசொல் இத்தனையுந் தோற்று இப்போ நரகமதில் சத்திறந்து போகவுன் தலையில் விதியாச்சே உன்விதியாங் நீயும் உயிரழிந்தா யல்லாது என்னதி காரத்தால் ஆக்கினைகள் செய்கிறேனோ உன்னால் நீமாள ஊழிவிதி யானதல்லால் தன்னாலே தான்கெட்டுத் தான்போவா ரென்றபடி உன்னாலே நீயும் உயிரழிந்தாய் மாபாவி ஒருபிறவி தன்னிலுன்னை உயிரழிக்கக் கூடாதே கருவருளும் நாதன் கற்பினைக்கே ராதெனவே பிறவியே ழுன்னைப் பெரும்புவியி லேபடைத்துத் திறவிப் பொருளோன் சிந்தை மிகஅறிய உகத்துக் குகங்கள் உத்தமனாய் நான்பிறந்து மகத்துவமா யுன்றன் மனதைமிகப் பார்த்தேன் எட்போலே தர்மம் ஈத லிரக்கமுடன் நட்பேதுங் கண்டிலனே நன்றகெட்ட மாபாவி ஏழு பிறவியிலும் என்பேரு சொல்வோரைக் கோளுசெய் தவரைக் கோட்டிக்கொண் டேயடித்துச் சிறைக்கா வல்தன்னில் திட்ட விலங்கில்வைத்து அறையான துகொடுத்து அட்டிமிகச் செய்ததல்லால் இரக்க முடன்தயவு இல்லையே உன்னிடத்தில் அரக்கர் குடும்பம் ஆனதிலா லுன்னுடைய மனதிரக்க மாகாது மாபாவி நீகேளு முன்னாள் குறோணி உதித்துவந்த மூன்றுகமும் தன்னாலே நீபிறந்து தாண்டி யதுகளித்துப் பின்னாள் பிறந்தாய்ப் பெருங்கிறே தாயுகத்தில்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(514,'நடுத்தீர்ப்பு','அந்நாளில் நீங்கள் அண்ணனென்றும் தம்பியெனும் பிறந்தீர் படைகள் பெருத்தசனக் கூட்டமுடன் சிறந்த மதிலும்பெரிய செல்வமா யாண்டிருந்தீர் ஆண்டிருக் கும்போது ஆங்காரந் தான்மீறித் தாண்டி பதமறந்து தானவரை யும்பிடித்துத் தெய்வமட வார்களையும் சிறையில் மிகப்போட்டு மெய்வரம்பு விட்டு மேலோகத் தாரையெல்லாம் ஊழியங்கள் கொள்ள உனக்கு மனதாகி  நாளி லவரை நட்டிமிக அட்டிசெய்து  வம்புசெய்து நீயும் வானலோ கம்வரையும் அம்புவி யெங்கும் உன்னநியாய மேமீறி ஈரே ழுலகும் இராமா ராமாவெனவே பாரேழு தேசம் பண்பாய் முறையமிட  முறையமிட்ட சத்தம் உடைய பரனறிந்து இறையவரும் நம்மிடத்தில் இந்தஅநி யாயமதை மாற்றிவைக்க வென்று மலரோ னெனையனுப்பப் பார்த்துன்னை நானும் பழையவர முந்தேர்ந்து ஆயுதத்தா லம்பால் அஞ்சு முகத்தாலும் மாயும் படியே வகையில்லை யென்றுசொல்லி ஆறு முகமாய் ஆனபுகழ் சத்திதனை வீறுடனே நல்ல வேலாயுத மெனவே எடுத்தே சொரூபம் யானுனக்கு நல்லபுத்தி விடுத்தே யுரைக்க மிகுதூத னையனுப்பிச் சொல்லியுங் கேளாமல் சூரப் படைகூட்டிக் கொல்லுவே னென்று கூண்டப் படையோடு சண்டைக்கு நீயும் சமைந்துவந்தா யென்னோடு கண்டே யுனைநான் கருணைபோல் புத்திசொன்னேன் தேவர்களை விட்டு தெய்வமட வாரைவிட்டு  மூவர்களை நெஞ்சில்வைத்து உகமாளு என்றேனே அப்போது நீயும் ஆக்கிரமந் தன்னாலே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(515,'நடுத்தீர்ப்பு','இப்போது பேயாண்டி யார்கேட்பா ருன்பேச்சை பிச்சைக்காரா வுன்சொல் பேருலக மாளுகின்ற செஞ்செல்வள ராசனுக்குச் செல்லாது போடாயென்றாய் என்னோ டேபோர்க்கு எதிராக வந்தாயென்றால் உன்னோ டேயுள்ள உற்ற திறத்தாலே சண்டைக்கு வாடா தாட்டாண்மைப் பார்ப்போமென்றாய் விண்டதெல்லாம் பார்த்து வேலா யுதமெடுத்து அப்போது வேலால் அறுத்தேனா னுன்சிரசை முப்போது உள்ள முழுக்கிளைக ளத்தனையும் சேரக் குலமறுத்து சேனையெல் லாமழித்துக் கோட்டை யழித்துன் குவாலத்தைத் தானழித்துக் கேட்டேனா னுன்னோடு கெறுவிதமே னென்றுரைத்தேன் சத்தி வேலாலே சத்தியென்னைச் சங்கரித்தாள் புத்திகெட்ட ஆண்டி போதுமோ என்னையெல் என்றாயே பாவி ஏற்றயுக மன்றழித்து அன்றே யுனையும் அதிலோர் பிறவிசெய்தேன் அந்நா ளுன்பேர்தான் அதிக இரணியனாய் துன்ஞாய மாய்நீ தோன்றி முடுக்கமதாய் அரிநமோ வென்ற அட்சரத்தையு மாற்றி மதியாம லுன்பேரை வளங்கினா யவ்வுகத்தில் ஆதி சிவமறிந்து அசுரா வுனையறுக்க மாதிரிபோ லென்னை வகுத்தாரே யுன்மகவாய் மகவா யுனக்கு மாய வுருவெடுத்து உகமே ழறிய உதித்து வளருகையில் பள்ளியிலே சென்று படிக்கின்ற நாளையிலே தெள்ளிமையச யுன்பேரைச் செய்பென்றான் வாத்தியானும் உடனே நான்மாறி ஒருஅசுரன் பேரதையும் தடமே லுரைத்தால் தருணமது காத்திடுமோ படைத்த குருவின் பருநாமஞ் சொன்னதுண்டால் சடத்தை மிகக்காக்கத் தருண முதவிசெய்வார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(516,'நடுத்தீர்ப்பு','ஆனதாற் பெரிய அரிநமோ அல்லாது மானமில்லாப் பாவி மாபாவிச் சூரனுட ஏற்ற பேரான இரணிய நமாவெனவே சாற்றக் கூடாது தானிதுவே சொன்னவுடன் வாத்தி யுன்னோடு வந்துவளங் கூறிடவே பார்த்தென்னை நீயும் பற்கடித்துச் சொல்லெனவே இறுக்கி நெருக்க இசையாமல் நான்மாறி உறுக்கிப் பெரியோன் உற்ற அரி யோன்பேரைச்  சொல்லவே நீயும் சொன்னஅரி யெங்கேஎன்றாய் வல்லப் பொருளான மாய அரியோனும் எங்கெங் குமாகி எவ்வுயிர்க்கும் தானாகி அங்கெங் குமாகி அளவுக் களவாகி நிரந்தரமா யெங்கும் நிறைந்த சொரூபமதாய்ப் பரப்பிரம்மமாய் நிற்பார் பாரஅரி யென்றேனே கோணி நீவாடி குருவென்ற உன்னரிதான் தூணிலு முண்டோசொல் என்றே எனைப்பார்த்துக் கேட்கநா னுண்டெனவே கிறுங்காமல் சொல்லிடவே வாடகணையால் நீயும் வாயில்நடை தன்னில்நிற்கும் தூண்பேரில் வெட்டச் சிங்கமாய் நான்குதித்து வீண்கொண்டப் பாவுயுனை வெய்யோ னடைவதிலே  இடைநடையில் வைத்து என்ற னொருநகத்தால் குடல்நெளியக் கீறிக் கொன்றேனா னுன்னையுமே உயிர்நெகிழு முன்னே உன்னுடைய கண்முன்னின்று செய்த யுத்தத் திறன்சொன்ன அப்போது உன்னாலே யென்னை உயிரழிக்க ஏலாது முன்னா லுன்னகத்தில் உற்றமலைப் பத்ததனைச் சேர்த்துப் பதித்துச் செய்ய நகமாக்கிக் கீற்றுநீ செய்தாய் கெறுவிதமா யல்லாது ஏலாது உன்னாலென்று இயம்பவுட னான்மாறி மேலாகப் பின்னும் விளம்பினதை நீகேளு');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(517,'நடுத்தீர்ப்பு','பத்து மலையைப் பருந்தலையா யுனக்கு எத்திசைகள் மெய்க்க ஏற்றதிகக் கைத்திறமும் கைத்திறமும் வில்திறமும் கணையாளி வாள்திறமும் புத்திரருங் கூடப் பிறப்போர்கள் தந்திறமும் மெத்தப் பவிசும் வேண்டும் படையோடே கொற்றவனா யுன்னைக் குவலயத்தி லேயருளி உன்னிடுக்கத் தாலே ஒருராம பாணமதால் கொன்னுன்னை யன்றின்று கூறுமொழி கேட்பேனான் என்றே யுனக்கு இருந்தவுயி ருமழித்து அன்றே கிறதா யுகமு மழித்துமிகப் பின்னுந் திரேதா பெரும்புவி யில்நீயும் மன்ன னிராவணனாய் பாவிநீ ராவணனாய் மற்றும் நிகரொவ்வா வாய்த்ததம்பி தங்களொடும் சேனைப் படையுடனே செல்வரோடு நீபிறந்து வானலோ கம்வரைக்கும் மாபாவி யாண்டனையே ஆண்டிருந்து மல்லாமல் ஆதிசீதா லட்சுமியை மாண்டிறந்து போங்காலம் மாபாவி நீயவளைக்  கொண்டுபோ யன்னுடையக் கோட்டையதுள் வைத்தனையே மன்றுதனில் நானும் மாதைவிடு வென்றுசொல்லி வேண்டுகின்ற புத்தி விதவிதமாய்ச் சொன்னேனே கூண்டுன்றன் தம்பி கும்பன் விபீஷணனும் நாரா யணனுடைய நல்லசீதா லட்சமியை ஆராலே கூடும் அருஞ்சிறையில் வைத்திருக்கப் பாவிநீ வம்பால் பழிக்கிரையாய்ப் போகாதே கூவுமொழி சீதைதனை கோட்டைவிட் டனுப்பிவிடு மாதை விடாதே வம்புசெய்தா யானாக்கால் பாதகா வுன்னுடைய பவிசெல்லாம் போகுமெனச்   சொன்னதம்பி மாரைச் சொல்லால் மிகத்துரத்திச் சின்னஞ் சிறுவன் தசரதனார் பாலனுடப் பெண்ணவளாஞ் சீதையெனும் போகச்சொல் லென்றனையே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(518,'நடுத்தீர்ப்பு','மண்ணாள வேணுமென்றால் வணங்கிப் பணியென்றும் அல்லாதே போனால் அலக்கழி வாகுமென எல்லாம் பெரிதாய் என்னோ டுரைத்தாயே பார்த்தால் சிறுவன் பைங்கிளியாள் தன்புருஷன் காற்றா னதிற்பறக்கும் கடிய துரும்பெனக்கு அவனுடைய பெண்ணாம் ஆதிசீதா லட்சுமியாம் இவளுடையப் பேரால் இராச்சியங்கே டாயிடுமாம்  ஆமோடா நீங்கள் அரக்கர் குலமோடா போமோடா என்றன் பூமுகத்தில் நில்லாதே என்றே யெனையும் இழப்ப மிகப்பேசி அன்றே யவர்பேச்சை அல்லவென்று தட்டிவிட்டாய் அப்போ தவர்கள் அன்பாக என்னுடைய செப்போடு வொத்தத் திறமெல்லாஞ் சொல்லிடவே சின்னக் குழந்தையென்றுஞ் சீதையொரு பெண்ணெனவும் மன்னவனே யுன்மனதில் வைத்துமிகக் கொள்ளாதே நாட்டுக் குடைய நாரணரே ராமனென்றும் கூட்டுக் கிளியானக் கோதைசீதா லட்சுமியாள் முட்டாளா வுன்றன் முழுநீசப் புத்தியினால் அட்டாள பூமி அடக்கியர சாளுகின்ற பகுத்தைக் குலையாத பழிக்கிரையாய்ப் போகாதே தொகுத்த வுரைபோலே சீதைதனை விட்டுவிடு என்றுரைத்தார் பின்னும் இருவ ருன்தம்பியர்கள் அன்று வுனக்கு அதிகக்கோப முண்டாகி என்னுடைய கண்முன் இப்போது நீங்கள்நின்றால் உன்னிருபேர் தங்கள் உற்றச் சிரசதையும் அறுத்து வதைப்பேன் வனமதிலே போயிடுங்கோ மறுத்து உரையாமல் வனமதிலே போயிடுங்கோ என்றாயே நீயும் இருவரையு மப்போது அன்றே யவர்கள் அயர்ந்து மிகவிருக்க அப்போது என்னுடைய ஆதிசீதா லட்சுமியை');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(519,'நடுத்தீர்ப்பு','நற்போ டுயர்ந்த நாயகியை நான்தேடி அனுமன் தனையும் அங்கே அனுப்பிவைத்துத் தனுவான வாளி தார்குழற்குத் தானீந்து உன்கோட்டை வாசலிலே உடனேநான் வந்துநின்று என்கூட்டி லான ஏற்றசீதா லட்சுமியை விடுநீ யென்றேனே வீணாய் கேளாமல் படுவ தறியாமல் படையெடுத்தா யென்னோடு அப்போதுன் தம்பி ஆன விபீஷ்ணனும் நற்போடு என்னை வந்துமிக நவ்வியவன் பொல்லாத பாவியுடன் பிறந்ததோ சங்கழித்து எல்லாம் பொறுத்து எனையாண்டு கொள்ளுமென்றான் நல்லதுதா னென்று நானவனை யுமேற்றுப் பொல்லாத பாவியென் பெண்ணைவிடு என்றேனே பாவிநீ கேளாமல் படையெடுத்து வந்தனையே தாவிநீ விட்டச் சரங்களெல் லாந்தடுத்து என்கை யினாலே எடுத்து வொருபாணம் சங்கையுட னெய்து தலையறுத்தே னுன்றனையும்  உன்னா லுன்படைகள் உயிரழிந்து மாண்டபின்பு முன்னா ளுரைத்த மொழிகேட்டே னுன்னோடு அப்போது பாவி அதற்கேது நீயுரைத்தாய் இப்போது என்னுடைய ஏற்றதம்பி தானொருவன் உன்னோடு சேர்ந்து உயிர்ப்பெலங்கள் தானுரைத்துச் சொன்னதா லென்னுடைய சிரசறுத்தா யல்லாது ஏலுமோ ராமா இழப்பம்பே சாதேயென்றாய் மாலும்நா னப்போ மகாகோப மாய்வெகுண்டு சுட்டிப் பயலே சுணைவந்து தில்லையென்று மட்டிப் பயலே மாறிப்பின் னேதுரைத்தேன் உன்றனுட தம்பி ஒருவன்மிக வந்தெனக்கு சிந்தையுற்ற உன்பெலங்கள் தெரியப் படுத்தியல்லோ கொன்றாய்நீ யென்று கூறினா யின்னமுனை');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(520,'நடுத்தீர்ப்பு','இன்னம் பிறவி ஏற்றதுரி யோதனனாய் துவாபர யுகத்தில் தோன்ற உனையருளிப் பவரா யுனக்குப் பக்கத் துணையாக ஒருநூறு பேராய் உலகில்மிக நீதோன்றி இருபேர்க்கும் நான்பொதுவாய் இருந்து வுனைவதைத்து இன்றுரைத்தப் பேச்சு யானன்று கேட்பேனென அன்று உனதுடைய அன்னசுற்றம் வேரறுத்து உன்னுயி ரைமழித்து உற்றயுக முமழித்து என்னுடைய லட்சுமியை யான்மீட்டு என்னுள்வைத்து உற்ற திரேதா யுகமழித் துன்றனையும் சுத்ததுவா பரயுகத்தை தொல்புவியில் தோணவைத்தேன் பிறந்தாய்ப் புவியில் பிறப்பொரு நூறுங்கூட அறந்தான் பெரிய ஐவர்களு மங்குதித்தார் அப்படியே நீபிறந்து ஆளுகின்ற நாளையிலே முப்படியே நானும் உகத்தில்கோ பாலனெனப் பாலனென வுதித்து பாண்டவர்க ளோடிருந்து தூலமொன்று வீமனுக்குச் சொல்லியுனைச் சங்கரித்தேன் சங்கரித்து உன்னைச் சகுனி யிழுக்கையிலே பங்கமாய் முன்னுரைத்த பாங்கு மிகக்கேட்டேன் அப்போது நீயும் அகமகிழ்ந்து கொள்ளாமல் இப்போது வீமன் எனைக்கொன்றா னல்லாது ஏலுமோ போடா இடையா எனவுரைத்தாய் மேலும்வந் தயுகத்தில் மேட்டிமையா யுன்னையிப்போ தன்னால் பிறக்கவைத்து தன்னா ழிவையென்று சொன்னே னானுன்னைச் சொன்ன மொழிப்படியே உன்னால் குதித்து உற்ற கலியனென இந்நாள் வரைக்கும் இருந்தாயே பார்மீதில்  பார்மீதில் நானும் பரதேசிப் போலிருந்து போரேது மில்லாமல் பொறுதி யுடனிருக்கக் கர்ம வயசுனக்குக் காலஞ் சரியாகி வர்மம்வந்து மூடி மாண்டாயே தன்னாலே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(521,'கலி முடிவு','முன்னுனக்குத் தந்த முடியு மென்சக்கரமும் மன்னுகந்த நல்ல வரங்கள்மிகத் தத்துவமும் எல்லாம் நீயிப்போ என்முன் னெடுத்துவைத்துப் பொல்லாத வனேநகரம் புக்கிடுநீ யென்றனராம் மாறி யுரைக்க வாய்மொழிக ளில்லாமல் ஊறிக் கலியன் உடக்கடித்து வைத்தனனே எல்லா வரமும்வைத்து என்றன் முடியும்வைத்து பொல்லாப்பு மானதொருப் பொய்கள வுமுருட்டும் தந்திர மாஞாலத் தத்துவங்க ளானதுவும் அந்திர மதான அன்னீத வஞ்சனையும் மாய்கை பலதும் வளக்கோர வாரமதும் சாய்கை பலதும் சர்வபொல்லாப் பானதெல்லாம் என்னோடு கூட யானு மதுகூட வன்னகரம் புக்கிடுவோம் என்றுவரம் வைத்தனனே வைக்க அவனுடைய மாய்மால மாய்கையதும் பொய்க்கலிய னுயிரைப் பொதிந்துத் திரையாக  ஆயிரத்திரு நூறு அணியாய்ப் பவஞ்சூடித் தீயிரத்த மான தீரா தருநரகில் சுற்றி யெடுத்துத் தூக்கிக்கொண் டேயவளை இத்தனைநா ளும்நம்மை இரட்சித்த இராசனென்று மாய்க்கையெல் லாங்கூடி வளைந்தவ னைத்தூக்கிப் பேயலகை வாழும் புழுக்குழிக் குள்ளாக  முக்கி நிமிர்ந்து மிதக்காமல் மாய்கையெல்லாம் தாக்கி யிருக்கத் தடதடெனச் சேடனது நிரந்ததை மூடி நிரத்தியதே யம்மானை பரந்தந்த சேடன் பாரைநிரப் பாக்கியபின்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(522,'தர்ம யுகம்','உற்ற வைகுண்டர் உறுசங் கூதினரே வெற்றியாய்ச் சங்கு விரைவாக ஊதிடவே எத்திசை யிரேழும் இந்தசங் கோசைமிக கற்பு நெறியானக் கடிய பலவகையும் நல்லோர்க ளான நாடு மனுவோரும் நல்பூமி நல்விருட்சம் நல்மிருக மூர்வனமும் கீழ்மேல் நடுவுங் கிரணமண்ட பமூன்றில் நாள்மேல் பெரிய நல்லவகை யானதெல்லாம் தன்ம முதல்நீதம் தவசு நிலைமைமுதல் நன்மை பலதும் நாடிமிக வந்ததுவாம்  எல்லாம் வைகுண்டர் இட்டசத்த மீதில்வந்து நல்லாகக் கண்டு நாரணரைத் தான்போற்றி வைகுண்ட சுவாமி வரவேணு மென்றுசொல்லி மெய்கொண்ட ஞான மிக்கத தவம்புரிந்தால் வந்துசந்த மொன்றில் வரவழைப்போ மென்றுசொன்னச் செந்துயிர்க் காக்கும் சிவகுண்டம் வந்தீரோ தன்ம வைகுண்ட சுவாமிவந்தா ரென்றுசொல்லி நன்மை பலசெந்தும் நன்னதிகப் பட்சிகளும் பசுமைக் குணமான பலமிருக வூர்வனமும் கசுவிரக்க மானக் கற்றாவின் தன்னினமும் நால்வேத நீதம் நாடுகின்ற சாஸ்திரமும் சில்வாடை பிச்சி செந்தா மரைமலரும் தங்கநவ ரத்தினமும் சமுத்திரத்து நல்வகையும் கங்கைக் கண்ணாளும் கமலப்பூ வாணிமுதல் தர்ம மதும்போற்றி சுவாமி யெனத்தொழுமாம் முழித்த மனுக்களுக்கும் மொய்குழலார் தங்களுக்கும் களித்தே விழிஞானக் கண்ணுமிகஅருளி நல்ல மனுக்களுக்கு நாலுவர முங்கொடுத்துச் செல்ல மனுக்கள் தேகமது பொன்னிறமாய் கல்வித் தமிழ்ஞானக் கலைக்கியா னமுதல் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(523,'தர்ம யுகம்','நல்விச் சிறப்பாய் நாடியவர்க் கீந்து தெய்வ மடவார் வரவழைத் தேழ்பேர்க்கும் பெற்ற மதலையெல்லாம் பிரமாண மாய்த்தெரிந்து தத்தியா யேழ்பேர்க்கும் தரந்தரமாய்த் தானீந்து வைகை தவிலிறந்த மக்கள்வரை நாரணரும் தையலவ ரேழ்பேர்க்கும் தான்தெரிந் தீந்தனரே ஏழுபேர்க் குமதலை இனமினமாய்த் தான்கொடுத்து வாழுங்கோ புவியில் வயதுபதி னாறெனவே எல்லோரும் நன்றாய் இருந்து வொருஇனமாய் நல்லோராய்ச் சாகாமல் நீடூழி காலமெல்லாம் ஆணுபெண் ணுடனே அதிகப்பல பாக்கியமும் காணக்காண நீங்கள் கௌவையற்று வாழுமென்றார் பட்சி பறவை பலசீவ செந்துகட்கும் அச்சமில்லாப் புவியில் அல்லல்வினை யில்லாமல் பெற்றுப் பெருகிப் பிதிரெல்லா மோரினம்போல் ஒத்து மிகக்கூடி ஒருதலத்து நீர்குடித்து வாழ்ந்திருங்கோ தர்ம வையகத்தி லென்றனராம் சார்ந்திருங்கோ வென்று தாமன் விடைகொடுத்தார் நல்லபூச வாச நளிர்விருட்ச மானதுக்கும்  அல்ல லகற்றி அமர்ந்துமிக வாழுமென்று தில்லையா டும்பெருமாள் சொன்னா ரதுகளுக்கு புற்பூடுங் கூடிப் பொருந்திமிக வாழுமென்று நற்பூ டதற்கு நவின்று விடைகொடுத்தார் அசையாமல் வானம் அதுநேர் நிலவுடனே பிசகான தில்லாமல் பொழுதுமிகச் சாயாமல் நேராக வாழுமென்று நெடியோன் விடைகொடுத்தார் வருணனுக்கும் நல்ல வாக்கு மிகக்கொடுத்துத் தருண மதுபார்த்து சாற்றியிரு என்றுரைத்தார் வாயு வதற்கு மரைபோல் வழங்கெனவே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(524,'தர்ம யுகம்','வீசு புகழ்நாதன் விடைகொடுத்தா ரம்மானை நீதமது மூன்றும் நிலையாக நில்லுமென்று  சீதக் குருநாதன் சொன்னா ரதுகளுக்கு மானுவ தர்ம வரம்பு தவறாமல் நானுப தேசம் நவின்றதுபோல் நில்லுமென்றார் பூமகள் வாணி பொருந்திக் கலைபுரிந்து சீர்முக தர்மச் சீமையி லும்வாழ்ந்து மகிழ்ந்திரு மென்று மாய னருள்புரிந்தார் குவிந்து மலர்மகளும் கொண்டாடிக் கொண்டிருந்தாள் கங்கை முதலாய்க் கனகரத்தி னாதிகளும்  சங்கையுட னீங்கள் தன்னால் துலங்கிமிக வாழுவீ ரென்று வரமு மிகக்கொடுத்து என்னென்ன பாக்கியங்கள் எல்லா மிகத்தழைத்துப் பொன்னம் பலம்போல் பொருந்திமிக வாழுமென்று நல்ல வகையெவர்க்கும் நாடி மிகக்கொடுத்தார் வல்ல புவிக்கு வாழ்வுவர முங்கொடுத்து நல்ல வைகுண்டர் நாரா யணர்மகற்கு வல்ல மகற்கு வாய்த்தமுடி யுஞ்சூடிச் செல்ல கலியாணம் செய்யவே ணுமெனவே வைகுண்ட ரான வாய்த்தகுரு நாதனுக்கு மெய்குண்ட ரான விமலக்குரு நாதனுக்கு நாதக் குருவான நாரா யணமணிக்கு சீதமங்கை மார்கள் தேவிதெய்ட கன்னியரை கன்னியரை நன்றாய்க் கலியாண மும்புரிந்து மன்ன ரவரவர்க்கும் மாலை யுடன்புரிந்து பொன்னம் பலர்க்கும் பெரிய பொருளதுக்கும் தன்னம் பெரிய சதாபிரம னானவர்க்கும் நாதன்மார்க் கெல்லாம் நல்ல மணம்புரிந்து மாதவர்க ளான வாய்த்ததே வாதிகட்கும் ஆதவ நாதன் அரிகேச வன்தனக்கும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(525,'தர்ம யுகம்','இப்படியே மங்களங்கள் எல்லோருங் கொண்டாடிச் செப்பமுள்ள நாயகிமார் சேர்ந்தங் கொருப்போலே சரசு பதிமாது தண்டரள சுந்தரியும் விரச குழலுமையும் வீரமகா லட்சுமியும் வாய்த்த பகவதியும் வாழுகின்ற பார்வதியும் ஏற்றபுகழ் தெய்வ இளங்குழலா ரேழ்பேரும் வள்ளி தெய்வாணை வாயீசொரி யுடனே தெள்ளிமையா யுள்ளத் தேவி பராபரையும் இந்தமா தர்கள் வந்தபோ திலே யாகியத் தெய்வமா தர்கள் முந்தநாங் களு மீன்றபா லரை முற்றுமா தவம்போல வளர்த் திடும்  சிந்தர்மா மணி தெய்வநா யகி தேவி காளி வராகி சுந்தரி இந்த மங்கள மானதிற் கண்டிலேம் öங்கள் நாயகா என்றவர் போற்றினார் போற்றுமா தரைப் பார்த்துநா தனும் போத மாமென வரமிது கூறுவார் சாற்றுமா தரே தைய லேழ்வரே சந்த மாகிய அந்தரி யானவள் பார்த்துன் மைந்தரைக் காத்திடா மலே பாலரண் டுயி ரிப்படி யானதால் ஏற்றுக் கௌவையாய் வீற்றிருக் கிறாள் எண்ணந் தீர்த்தவள் தன்னை யழைக்கிறேன் பெண்ணேகே ளுன்றன் பிள்ளைரண்டு தன்னுயிரைக் கள்ளக் கவுசலமாய்க் கரிகாலச் சோழனவன் கொன்னதினாற் காளி கூண்ட மனமிடைந்து என்னிடத்தில் வந்து இவ்வளமை யுமுரைத்துப்  பாலர் முழித்துப் பரம்பெரிய வைகுண்டரும் சீலமுள்ள தர்மச் சீமையர சாளுகையில் வருவே னதுமட்டும் வடவா முகமதிலே குருவே துணையெனவே குவிந்திருப்பே னென்றிருந்தாள் இப்போ தவட்கு இங்குவர ஞாயமுண்டு நற்போடு வொத்த நல்லபர காளியைத்தான் அழைக்கிறே னென்று ஆதி நினைக்கலுற்றார் பிழைக்கிறே னென்று ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(526,'தர்ம யுகம்','பெரியகுல மாகாளி உடன்வந்து நாரணரை உவந்து பதங்குவித்துத் திடன்வந்து நின்று சிவனை யடிதொழுது பாலரெல்லா முன்றன் பாதமது சேர்ந்தாரென்று மாலவரே யென்றன் மனது மகிழ்ந்துதையா நாரணரு மெச்சி நன்றா யகமகிழ்ந்து காரணம தாகக் களிகூர்ந் தினிதாக வைகுண்ட நாதனுக்கும் வாழ்மடந்தை மாதருக்கும் கைகண்ட நல்ல கலியாண முமுகித்து மணிவை குண்டருக்கு மாமகுட முஞ்சூட்டி அணியா பரணம் அநேக மெடுத்தணிந்து தங்கக் குல்லாவம் தளிருநிறச் சட்டையிட்டுப் பெண்கள் குரவையிடப் பொன்மா லையுஞ்சூடிப்   பன்னீர் பரிமளமும் பவளநிறப் பொட்டுமிட்டு நன்னீர்க ளாடி நாரணக் கண்மணிக்கு ஆலத்தி வன்னி ஆகாயத்தீ வெட்டமுடன் கோல மடவார்கள் குக்குளித்து நீராடிப் பட்டுப் பணிகள் பரிமளங்க ளும்புரிந்து கட்டு முறையாய்க் கன்னியர்கள் தாமொயிலாய் தேவர்களு மூவர்களும் திசைவென்ற மன்னர்களும் மூவர்களும் நல்ல முழித்தபல செந்துகளும் நான்முகனும் வேத நல்ல மறையோரும் வானுகமு மண்ணகமும் மன்னகமு மொன்றெனவே எல்லோரும் நன்றாய் ஏக மகிழ்ச்சையுடன் நல்லோர்க ளெல்லாம் நாரணனார் பொற்பதிக்குள் தெருப்பவிசு வந்து சிங்கார பொற்பதிக்குள் மருப்புகழுஞ் சிங்கா சனத்தில் மகிழ்ந்திருக்க போவோ மெனவே பெரியவை குண்டரையும் கோவேங் கிரிபோல் அருவைரத மீதேற்றி தேவாதி யெல்லாம் சிவசிவா போற்றியெனச் சீவசெந் தெல்லாம் திருப்பாட்டுக் கூறிவரத்  தெய்வ மடவார்கள் திருக்குரவை பாடிவர மெய்வதிந்த சான்றோர் மொகுமொகென வேகூடிக் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(527,'தர்ம யுகம்','கட்டியங்கள் கூறிக் கனகப்பொடி யுந்தூவிக் கெட்டிகெட்டி யென்றுக் கீர்த்தனங்கள் பாடிவர வாரி சங்கூத வாயு மலர்தூவ நாரி வருணன் நல்லந்தி மலர்தூவ இந்தக் கொலுவாய் எழுந்துரத மீதேறி சிந்தர் மகிழச் சிவமுந் திருமாலும் கூட ரதமீதில் கூண்டங் கினிதிருந்து லாடர் மகிழ நல்ல தெருப்பவிசு நேராக வந்து நெடியோன் பதிதவிலே சீராய்ப் பதிமுடுகச் செகலதுவே தானீங்கி அமைத்து அலைகொண்டிருந்த அழகுபதி கோபுரமும் சமைத்து இருந்த தங்கமணி மண்டபமும் மண்டபமும் மேடைகளும் மணிவீதி பொற்றெருவும் குண்டரைக் கண்டந்தக் கொடிமரங்க ளுந்தோன்ற வாரியது நீங்கி வைத்தலக்கில் போயிடவே சாதிவை குண்டர் சாபம் நிறைவேற்றிச் சாபம் நிறைவேற்றித் தானாய் நினைத்ததெல்லாம் யாம முறையாய் அங்கே குதித்திடுமாம் என்னென்ன யாமம் ஏலமே யிட்டதெல்லாம் பொன்னம் பதிதான் புரந்தாள வந்ததினால்  நிறைவேறி நானும் நிச்சித்த மெய்வரம்போல் குறைபடிகள் வராமல் குணமாக வாழுமென்றார் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(528,'பட்டாபிஷேகம்','இப்படியே யாமம் எல்லாம் நிறைவேற்றி முப்பத்தி ரண்டறத்தால் முகித்தசிங் காசனத்தில் செப்பொத்த மாணாக்கர் சேவிக்க இருபுறமும் ஒப்பற்ற பொற்பதிக்குள் உயர்ந்தசிங் காசனத்தில் மறுமஞ்ஞ ரெதிரி வையகத்தி லில்லாமல் விறுமஞ்ஞ ரான வெற்றிவை குண்டருமே சிங்கா சனமிருந்து தெய்வச்செங் கோல்நடத்தி பொங்கா ரமான புவிதர்ம ராச்சியத்தில் ஆளுவா ரென்ற ஆகம நூற்படியே ஏழுபெண் மக்கள் இனமொன்றாய்த் தான்கூடி வாழவே ணுமெனவே வாய்த்தசிங் காசனத்தில் ஆளவை குண்டர் அவரிருந்தார் பொன்மாதே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(529,'அய்யா அருள்வாக்கு','ஆதியாம் வைந்த ராசர் அருள்செங்கோ லேந்தித் தர்ம சோதியி னொளிபோல் ரத்தினந் துலங்கிய முடியுஞ் சூடி நீதிபோல் தர்ம ஞாய நெறிபுரிந் தரசே யாள சாதியா முயர்ந்த சான்றோர் தம்மையே வருத்திச் சொல்வார் மக்களே நீங்க ளெல்லாம் வாழ்பொன்னம் பதியிற் சென்று முக்கிய மான தர்ம யுகநில மதிலே தன்னால்  கக்கிய பொன்கள் சொர்ணம் கைமனங் குளிர அன்னம் பொக்கிஷம் நிறைய வைத்துப் புகழவுண் டினிதாய் வாழ்வீர் வாழுவீர் தாழ்வில் லாமல் மக்களுங் கிளைகள் கொஞ்சி நாளுமே மகிழ்ச்சை கூர்ந்து நலமுடன் வாழும் போது நீளுமே யெனது செங்கோல் நீதியும் நெறிபோல் வந்து ஆளுமே யுங்கள் தம்மை அன்புட னலையா வண்ணம் உன்னிலும் பெரியோ னாக ஒருவனுள் ளுயர்த்தி கண்டால் தன்னிலும் பெரியோ னாகத் தழைத்தினி திருந்து வாழ்வீர் என்னிலும் பெரியோ னீங்கள் யானுங்கள் தனிலு மேலோன் பொன்னில் வூற்று வீசும் பொன்பதி யுகத்து வாழ்வே ஆணுடன் பெண்ணும் பெற்று அதிகமாய்ச் செல்வ மாகித் தாணுட நினைவு முற்றுத் தர்மமும் நெறியுங் கற்று வேணுநீள் கால மெல்லாம் ருடனே வாழ்ந்து பூணுதல் கமல நாதன் பொற்பதம் பெற்று வாழ்வீர்  மனுக்க ளவர்க்கு வாய்த்தசட்ட மீதருள தனுக்கள் பெரிய சந்தமிரு கங்களுக்கு  ஒன்றாகக் கூடி ஒக்க வொருஇனம்போல் நன்றாக வோர்தலத்தில் நன்னீர் குடித்துமிக வாழ்ந்திருங்கோ வென்று வைந்தர்மிகச் சட்டமிட்டார் மெச்சிக் குழைந்து மேவி யொருஇனம்போல் பட்சி பறவைகட்கும் பாங்காகச் சட்டமிட்டு ஊர் வனங்களுக்கும் ஒருப்போலே சட்டமிட்டு பாருகத்தில் நீங்கள் பசுமையாய் வாழுமென்றார் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(530,'அய்யா அருள்வாக்கு','தேவ தெய்வார்க்கும் தேவ ஸ்திரிமார்க்கும் மூவர் முனிவருக்கும் முக்கோடித் தேவருக்கும் எல்லோர்க்கும் நன்றாய் இயல்பாகச் சட்டமிட்டு வல்லோர்க ளான வைகுண்ட மாமணியும் செங்கோ லுமேந்தி சிங்காசன மிருந்து மங்காத தேவரம்பை மாத ரிருபுறமும் சிங்கார மாகத் தேன்போல் மரைவீச சான்றோர்க ளேவல் தமதுள் பணிமாறக் கட்டியங்கள் கூறி கவரி மிகவீசக்  கெட்டியாய்ச் சான்றோர் கிருபை யுடன்மகிழ்ந்து பாவித்து நித்தம் பரமவை குண்டரையும் சேவித்துப் போற்றி தினமேவல் செய்திடவே கன்னியர்க ளோடும் காதலாய்த் தானீன்ற மன்னதிச் சான்றோர் மக்கள் மனைவியோடும் ஆதி வைகுண்ட ஆனந்த நாரணரும் நிதியாய்த் தர்மம் நேரோர் மணிதூக்கிச் சிங்கா சனத்தில் சிவசூர்ய குடைக்குள் கங்கா தரனார் கற்பினையை யுள்ளிருத்தி ஆண்ட பரனும் ஆதி முறைப்படியே சான்றோர்கள் போற்ற தர்மபதி யாண்டிருந்தார் ஆண்டிருந் தரசு செய்ய அணிவரை போலே நீதம் பூண்டிருந் தினிது வாழ பூதல மனுவோர் வாழ கூண்டிருந் தருளாய்ச் செல்வம் குணமுடன் மகிழ்ச்சை கூர்ந்து வேண்டிருஞ் செல்வ மோங்க வேற்றுமை யில்லா வாழ்ந்தார் தருமமாய்ப் புவியி லுள்ள சனங்களும் பலது செந்தும் பொறுமையாய் வாழும் போது புரந்தர வானோர் விண்ணோர் நன்மையா யவருங் கூட நாடொன்றாய் மேவி வாழ வன்மமே யில்லா வண்ணம் வைந்தரும் புவியை யாண்டார் ஆண்டனர் புவிதிரி மூன்றினு மோரினம் கூண்டநற் குலமெனக் குலாவி வைந்தரும் தாண்டிய வோர்குடைத் தாங்கு குவலயம் மூன்றினு மோர்மொழி முகுந்தன் வாழ்ந்தனர்  பருதியு மதியெனப் பவந்து சேவனர் கருதியு ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(531,'அய்யா அருள்வாக்கு','முகமனும் கமழ்ந்து கைமலர் அருதியு மலர்மகள் அணிந்து பூவினர் கருதியு முறைவழி தூக்கி வாழ்ந்தனர் பொன்முக வருளது பொதுமி யாவியே அன்முக மதிலு மமர்ந்து புகுந்திரு இன்முக மதிலு மிருந்து லாவியே பொன்முக வைந்தர் புயத்தில் வாழ்ந்தனர் முதமுக வானவர் மூன்றென வொன்றினர் சதயித காலெனச் சமைந்து வாழ்ந்தனர் உதவென மனமு முவந்து லாவியே நிதம்நினை வளர்வறா நிரந்து வாழ்ந்தனர் சீரணி யுமையாள் பங்கர் சிவமகிழ்ந் தினிது வாழ நாரணர் திருவும் வாழ நான்முக வேதன் வாழ வாரண வான லோக மாதவ ரெல்லாம் வாழ காரணக் கன்னி யானக் கமலப்பூ மாதும் வாழ பூமாது வானபக வதியும் வாழ பொன்மாது சரசுபதி புரிந்து வாழ நாமாது வானபூ மடந்தை வாழ நாகரிகத் தேவியர்கள் நலமாய் வாழ போர்மாது வானமா காளி வாழ பொன்னுலகத் தர்மபதி பொருந்தி வாழ சீர்மாது கொண்டபுகழ்ச் சான்றோர் வாழ சிவவைந்த ராசருமே சிறந்து வாழ   மாதுதிரு லட்சுமியாள் மகிழ்ந்து போற்றி மாயனுட முகம்நோக்கி மாது தானும் தீதகலும் நாயகமே சிறந்த மாலே தேசமதி லுமக்கெதிரித் தோன்றிற் றென்று நீதமுடன் தோன்றியங்கே யுகங்கள் தோறும் நிந்தனைகள் படுவதெனக் கறியச் சொல்வீர் ஈதுரைக்க மாயவட்கு மாயன் தானும் இத்தனையு மெடுத்துரைக்க இசைவாள் பின்னும் பின்னுமந்த நாதனுட அடியைப் போற்றிப் பொன்மானே யெனதுடைய தேனே கண்ணே இன்னுவரைக் குறோணியுயிர் தன்னை நீரும் எழுபிறவி செய்தவனை யிசைந்து பார்த்தும் நன்னியெள்ளுப் போலினிவு காணா வண்ணம் ஞாயநடுக் கேட்டவனைத் தன்னால் கொன்னீர்');");
