        db.execSQL("INSERT INTO akilathirattu2 VALUES(1,'காப்பு','ஏரணியும் மாயோன் இவ்வுலகில் தவசுபண்ணி காரணம்போல் செய்தகதை கட்டுரைக்க - பூரணமாய் ஆராய்ந்து பாட அடியேன்சொல் தமிழ்க்குதவி நாராயணர் பாதம் நாவினில் பாண்டவர் தமக்காய்த் தோன்றி பகைதனை முடித்து மாயோன் வீன்றிய கலியன் வந்த விசளத்தால் கயிலை யேகி சான்றவர் தமக்கா யிந்தத் தரணியில் வந்த ஞாயம் ஆண்டவர் அருளிச் செய்ய அம்மானை எழுத லுற்றேன் சிவமே சிவமே சிவமணியே தெய்வ முதலே சிதம்பரமே தவமே தவமே தவக்கொழுந்தே தாண்டவசங் காராதமியே எங்களுட பவமே பவமே பலநாளுஞ் செய்த பவம றுத்தன் அகமேவைத் தெங்களை யாட்கொள்வாய் சிவசிவசிவசிவா அரகரா அரகரா அலையிலே துயில் ஆதிவராகவா ஆயிரத்தெட் டாண்டினில் ஓர்பிள்ளை சிலையிலே பொன்மகர வயிற்றினுள் செல்லப்பெற்றுத் திருச்சம் பதியதில் முலையிலே மகரப்பாலை யுமிழ்ந்துபின் உற்றதெச்சண மீதில் இருந்துதான் உலகில் சோதனை பார்த்தவர் வைந்தரின் உவமைசொல்ல உகதர்மமாகுமே திருமொழி சீதை யாட்குச் சிவதலம் புகழ எங்கும் ஒருபிள்ளை உருவாய்த் தோன்றி உகபர சோதனைகள் பார்த்துத் திருமுடி சூடித் தர்மச் சீமையில் செங்கோ லேந்தி ஒருமொழி யதற்குள் ளாண்ட உவமையை உரைக்க லுற்றார்.');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(2,'நூல் சுருக்கம்','சிவமே சிவமே சிவமே சிவமணியே தவமே தவமே தவமே தவப்பொருளே சீரான கன்னி செய்குமரி நன்னாட்டில் பாரான தெச்சணமே பரம னுறுதலத்தில் போர்மேனி மாயன் பிறந்து தவம்புரிந்து ஓர்மேனிச் சாதி ஒக்க வரவழைத்து நன்னியாய் நானூறு நாலுபத் தெட்டதினோய் தண்ணீரால் தீர்த்த தர்மமது ஆனதையும் செய்திருந்த நன்மை தீங்குகலி கண்டிருந்து வயது முகிந்தகலி மாளவந்த வாறுகளும் முன்னாள் குறோணி முச்சூர னுடல்துணித்துப் பின்னா ளிரணியனைப் பிளந்துஇரு கூறாக்கி ஈரஞ்சு சென்னி இராட்சதரை யுஞ்செயித்து வீரஞ்செய் மூர்க்கர் விடைதவிர்த் தேதுவரா தேசாதி தேசர் சென்னிகவிழ்ந் தேபணியும் தீசாதி யான துரியோதனன் முதலாய் அவ்வுகத் திலுள்ள அவ்வோரை யும்வதைத்து எவ்வுகமும் காணா(து) ஏகக்குண் டமேகி பொல்லாத நீசன் பொருளறியா மாபாவி கல்லாத கட்டன் கபடக்கலி யுகத்தில் 20 வம்பா லநியாயம் மாதேவர் கொண்டேகி அம்பாரி மக்களுக்(கு) ஆக இரக்கமதாய் அறுகரத் தோன்வாழும் ஆழிக் கரையாண்டி நறுகரத் தோனான நாராயண மூர்த்தி உருவெடுத்து நாமம் உலகமேழுந் தழைக்கத் திருவெடுத்த கோலம் சிவனா ரருள்புரிய ஈச னருள்புரிய இறையோ னருள்புரிய மாய னருள்புரிய மாதும் அருள்புரிய பூமாது நாமாது புவிமாது போர்மாது நாமாது லட்சுமியும் நன்றா யருள்புரிய சரசுபதி மாதே தண்டரள மாமணியே அரசுக் கினிதிருத்தும் ஆத்தாளே அம்பிகையே ஈரே ழுலகும் இரட்சித்த உத்தமியே பாரேழு மளந்த பரமே சொரித்தாயே பத்துச் சிரசுடைய பாவிதனைச் செயிக்க மற்று நிகரொவ்வா வாய்த்த தசரதற்குச் சேயா யுதிக்கச் செடமெடுத் ததுபோலே மாயாதி சூட்சன் மனுவாய்க் கலியுகத்தில் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(3,'நூல் சுருக்கம்','சிறந்த குழலாள் சீதை சிறையதுபோல் 40 இருந்த சொரூபமதும் இமசூட்ச அற்புதமும் மருந்தாகத் தண்ணீர்மண் வைத்தியங்கள் செய்ததுவும் தர்ம வைத்தியமாய்த் தாரணியி லுள்ளோர்க்குக் கர்மமது தீரக் கணக்கெடுத்துப் பார்த்ததுவும் ஏகாபுரிக் கணக்கும் ஏழுயுகக் கணக்கும் மாகாளி மக்கள் வைகுண்டர் பாதமதைக் கண்டு தொழுததுவும் கைகட்டிச்சே விப்பதுவும் தொண்டராய்ச் சான்றோர் சூழ்ந்துநின்ற வாறுகளும் பண்டார வேசம் பத்தினியாள் பெற்றமக்கள் கொண்டாடி நன்றாய்க் குளித்துத் துவைத்ததுவும் கண்டுமா பாவி கலைத்து அடித்ததுவும் சாணா ரினத்தில் சாமிவந்தா ரென்றவரை வீணாட்ட மாக வீறுசெய்த ஞாயமதும் மனிதனோ சுவாமி வம்பென்று தானடித்துத் தனுவறியாப் பாவி தடியிரும்பி லிட்டதுவும் அன்புபார்த் தெடுத்து ஆளடிமை கொண்டதுவும் வம்பை யழித்துயுகம், வைகுண்டந் தானாக்கி எல்லா இடும்பும் இறையு மிகத்தவிர்த்துச் சொல்லொன்றால் நாதன் சீமையர சாண்டதுவும் நாலுமூணு கணக்கும் நடுத்தீர்த்த ஞாயமதும் 60 மேலெதிரி யில்லாமல் வினையற்று ஆண்டதுவும் இன்னாள் விவரமெல்லாம் எடுத்து வியாகரரும் முன்னாள் மொழிந்த முறைநூற் படியாலே நாரணரும் வந்து நடத்தும் வளமைதன்னைக் காரணமா யெழுதிக் கதையாய்ப் படித்தோர்க்கு ஒய்யார மாக உள்வினைநோய் தீருமென்று அய்யாவு மிக்கதையை அருளுகிறா ரன்போரே பேயை யெரித்துப் புதுமைமிகச் செய்ததுவும் ஆயர் குலத்தை ஆளாக்கிக் கொண்டதுவும் சான்றோர் குலத்தைத் தற்காத்துக் கொண்டதுவும் நீண்டபுகழ் தர்மம் நிறுத்தியர சாண்டதுவும் பத்தும் பெரிய பாலருக்கா கவேண்டி சத்தழியும் பாவி தடியிரும்பி லுமிருந்து படுத்தின பாட்டையெல்லாம் பாலருக்கா கப்பொறுத்து உடுத்த துணிகளைந்து ஒருதுகிலைத் தான்வருத்தித் தேவ ஸ்திரீகளையும் சேர்த்தெடுத்துப் பாலருக்காய்ப் பாவக் கலியுகத்தில் பாராத்தியங் கள்பட்டு நாலு பிறவி நானிலத்தி லேபிறந்து பாலுகுடித் தாண்டி பருவதத்தின் மேல்தாண்டி நல்லோரை யெழுப்பி நாலுவரமுங் கொடுத்துப் 80 பொல்லாரை நரகில் போட்டுக் கதவடைத்து வானம் இடியால் மலைக ளிளகிடவும் கானமது நாடாய்க் கண்டதுவுஞ் சூரியனும் தெக்கு வடக்காய்த் திசைமாறி நின்றதுவும் ஒக்கவே நாதன் உரைக்கிறா ரன்போரே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(4,'அவையடக்கம்','அய்யா உரைக்க அன்போர்கள் தங்கள்முன்னே மெய்யா யெழுதி விதிப்பேனா னென்பதெல்லாம் ஆனை நடைகண்ட அன்றில் நடையதொக்கும் சேனை சரிவதென்ன சிற்றெறும்பு பத்தியென்ன குயில்கூவக் கண்டு கூகைக் குரலாமோ மயிலாடக் கண்டு வான்கோழி யாடினதென மடந்தை தேனீக்கள் வைத்த ரசமறிந்து கடந்தை தாடேற்றும் கதைபோலே யானடியேன் நாலுமூணு யுகமும் நடுத்தீர்க்க வந்தபிரான் மேலோன் திருக்கதையை எழுதுவே னென்றதொக்கும் எம்பிரா னான இறையோ னருள்புரிய தம்பிரான் சொல்லத் தமியே னெழுதுகிறேன் எழுதுவே னென்றதெல்லாம் ஈசனருள் செயலால் பழுதுமிக வாராமல் பரமே ஸ்வரிகாக்க');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(5,'தெய்வம் பராவல்','ஈசன் மகனே இயல்வாய்வா இக்கதைக்குத் 100 தோச மகலச் சூழாமல் வல்வினைகள் காலக் கிரகம் காமசஞ்சல மானதுவும் வாலைக் குருவே வாரா மலேகாரும் காரு மடியேன் கௌவை வினைதீர வாரு மடியேன் மனதுள் குடிகொள்ளவே தர்ம யுகமாக்கித் தாரணியை யாளுதற்குக் கர்மக் கலியில் கடவுளார் வந்தகதை சாகா திருக்கும் தர்மஅன் புள்ளோர்முன் வாகாகத் தர்ம அம்மானை தான்வகுத்தார்.');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(6,'அடியெடுத்தருளல்','வகுத்த பிரம்மனுக்கும் மாதா பிதாவதுக்கும் தொகுத்த குருவதுக்கும் தோத்திரந் தோத்திரமே தோத்திர மென்று சுவாமி தனைத்தொழுது ராத்திரி தூக்கம் நான்வைத் திருக்கையிலே ஆண்டான ஆண்டு ஆயிரத்துப் பதினாறில் கண்டானைக் கண்டேன் கார்த்திகை மாதமதில் தெய்தியிரு பத்தேழில் சிறந்தவெள்ளி நாளையிலே சுகுதியுடன் நித்திரையில் சுபக்கியான லக்கமதில் நாதனென் னருகில் நலமாக வந்திருந்து சீதமுட னெழுப்பிச் செப்பினார் காரணத்தை காப்பி லொருசீரு கனிவாய் மிகத்திறந்து 120 தாப்பிரிய மாகச் சாற்றினா ரெம்பெருமாள் மகனேயிவ் வாய்மொழியை வகுக்குங் காண்டமதுக்கு உகமோ ரறிய உரைநீ முதற்காப்பாய் அதின்மேல் நடப்புன் உள்ளே யகமிருந்து சரிசமனாய் நான்வகுப்பேன் தானெழுது காண்டமதை நானுரைக்க நீயெழுதி நாடுபதி னாலறிய யானுரைக்க நீயெழுதி அன்போர்கள் தங்கள்முன்னே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(7,'நூற்பயன்','வாசிக்கக் கேட்டு மகிழ்ந்திருந்த அன்போர்க்குப் பூசித்து நின்ற பூரணங்கள் கிட்டுமடா பழித்தோர் நகைத்தோர் பதிலெதிரி யாய்ப்பறைந்தோர் கழித்தோர் வறுமைகண்டு கடுநரகம் புக்கிடுவார் மலடியு மிக்கதையை மாவிருப்பத் தோடிளகி தலமளந்தோ னைநாடி தான்கேட்பா ளாமாகில் என்னாணை பார்வதியாள் எகாபரத்தின் தன்னாணை உன்னாணை மதலை உடனே கிடைக்குமடா குட்டமது கொண்டோர் குணம்வைத்துக் கேட்பாரேல் திட்டமது சொன்னோம் தீருந் திருவாணை');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(8,'தெச்சணா புதுமை','நாராயணரும் நல்லதிருச் செந்தூரில் பாரோர்கள் மெய்க்கப் பள்ளிகொண் டங்கிருந்து ஆண்டா யிரத்து அவென்ற லக்கமதில் 140 நன்றான மாசி நாளான நாளையிலே சான்றோர் வளரும் தாமரையூர் நற்பதியில் மூன்றான சோதி உறைந்திருக்குந் தெச்சணத்தில் வந்திருந்த நற்பதியின் வளமைகே ளம்மானை மூவாதி மூவர் உறைந்திருக்குந் தெச்சணமே தேவாதி தேவர் திருக்கூட்டந் தெச்சணமே நாதாந்த வேதம் நாடுகின்ற தெச்சணமே அகத்தீ சுவரரும் அமர்ந்திருக்குந் தெச்சணமே மகத்தான மாமுனிவர் வாழுகின்ற தெச்சணமே தாணுமால் வேதன் தாமதிக்குந் தெச்சணமே ஆணுவஞ்சேர் காளி அமர்ந்திருக்குந் தெச்சணமே தோசமிகு கர்மம் தொலைக்கின்ற தெச்சணமே நீச வினைதீர நீராடுந் தெச்சணமே மாது குமரி மகிழ்ந்திருக்குந் தெச்சணமே பாறு படவு பரிந்துநிற்குந் தெச்சணமே ஆனைப் படைகள் அலங்கரிக்குந் தெச்சணமே சேனைப் படைத்தளங்கள் சேருகின்ற தெச்சணமே ஆகமக் கூத்து ஆடுகின்ற தெச்சணமே பார்வதியாள் வந்து பரிந்திருந்த தெச்சணமே 160 சீர்பதியை ஈசன் செய்திருந்த தெச்சணமே மாயனாய்த் தோன்றி வந்திருந்த தெச்சணமே ஆயனார் கூத்து ஆடுகின்ற தெச்சணமே தெச்சணத்தின் புதுமை செப்பத் தொலையாது அச்சமில்லாப் பூம அடவுகே ளம்மானை கச்சணி தனத்தா ளோடு கறைமிடற் றண்ண லீசர் பச்சமால் முனிவர் தேவர் பதுமலர்க் கமலத் தேவி நிச்சய மான கன்னி நிறைந்திடும் பூமி யான தெச்சாண புதுமை சொல்லிச் சீமையி னியல்புஞ் சொல்வோம்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(9,'சீமையின் இயல்பு','புன்னை மலர்க்காவில் பொறிவண் டிசைபாட அன்ன மதுகுதித்து ஆராடுஞ் சோலைகளும் கன்னல் கதலி கரும்பு பலாச்சுளையும் எந்நேர மும்பெருகி இலங்கிநிற்குஞ் சோலைகளும் எங்கெங்கும் நாதம் இடுகின்ற பேரொலியும் பொங்கு கதிரோன் பூமேற் குடைநிழற்ற நந்தா வனம்பூத்து நகரி மணம்வீச செந்தா மரைபூத்துச் செலம் புமணம்வீச அரகரா வென்று அபயமிடு மொலியும் சிவசிவா வென்று துதிக்கின்ற பேரொலியும் முடியு மடியுமில்லா முதலோனைப் போற்றொலியும்             மடியில் பணம்போட்டு மார்க்குத்தும் பேரொலியும் முத்தாலே பாண்டி முதன்மடவா ராடொலியும் மத்தாலே மோரு மடமடென்ற பேரொலியும் சமுத்திரத்து முத்து தான்கரையில் சேருவதும் குமுத்திரளாய்ப் பெண்கள் குரவை யிடுமொலியும் சங்கீத மேளம் தானோது மாலயமும் மங்கள கீதம் வகுக்கின்ற ஆலயமும் காரண வேதக் கல்விமொழி ஆலயமும் வாரணத்தின் மீதில் வரும்பவனி யாரபமும் மாவேறி வீதி வரும்பவனி வீதிகளும் கூரை யிலேமுத்து குலைசாய்க்கும் கன்னல்களும் பாதையிலே பார்ப்பார்க்குப் பைம்பொன் னளிப்பாரும் அன்னமிடுஞ் சாலைகளும் ஆலயங்கள் வைப்பாரும் சொர்ண மளித்துச் சொகுசுபெற நிற்பாரும் சிவனே சிவனேயென்று சிவகருத்தாய் நிற்பாரும் தவமே பெரிதெனவே தவநிலைகள் செய்வாரும் கோவிந்தா வென்று குருபூசை செய்வாரும் நாவிந் தையாக நால்வேதம் பார்ப்பாரும் மாரி பொழியும் மாதமொரு மூன்றுதரம் ஏரி பெருகி ஏரடிக்க மள்ளரெல்லாம் 200 நாத்து நடும்புரசி நளினமிகு சொல்லொலியும் கூத்து ஒலியும் குருபூசை தன்னொலியும் எப்பாரெல் லாம்புகழும் ஏகா பதியதுபோல் தப்பா தெச்சணத்தின் தன்மையீ தம்மானை காமனை எரித்த ஈசன் கழலிணை மறவா வண்ணம் பூமணக் குழலாள் கன்னி பொருந்திடும் நகரி யான சீமையின் குணமுஞ் சொல்லிச் சிறந்திடும் பூமி தன்னில் நேமவர் தர்ம ஞாய நிலைதனை நிகழ்த்து வாரே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(10,'தர்மநீதம் ','ஆதிப் பொருளை அனுதினமுந் தானோதி சோதி யுடநீதம் சொல்லுவே னம்மானை தெய்ன மனுநீதம் தேசாதி ராசநீதம் மெய்யருந்தும் நீதம் விளம்புவே னம்மானை ஆயிரத்து எட்டு ஆனதிருப் பதிக்கும் வாயிதமாய்ப் பூசை வகுப்பு முடங்காது கோயில் கிணறு குளங்கரைக ளானதையும் தேய்வு வராதே சுற்றுமதில் கட்டிடுவார் எளியோர் வலியோர் என்றெண்ணிமிகப் பாராமல் களிகூர நன்றாய்க் கண்டவழக் கேயுரைப்பார் அன்ன மடம்வைத்து ஆகங் களிகூர எந்நேரம் பிச்சை இடுவா ரெளியோர்க்குப் 220 பந்தலிட்டுத் தண்ணீர் பக்தர்க் களிப்பாரும் எந்த இரவும் இருந்துபிச்சை யீவாரும் ஆறி லொருகடமை அசையாமல் தான்வேண்டி தேறியே சோழன் சீமையர சாண்டிருந்தான் சிவாயநம வென்ற சிவவேத மல்லாது கவாயமாய்ச் சொல்லக் கருத்தறிய மாட்டார்கள் ஆறி லொருகடமை அதுதரவே மாட்டோமென்று மாறொருவர் சொன்னால் மன்னன்மறுத் தேகேளான் பன்னிரண் டாண்டு பரிவா யிறையிறுத்தால் பின்னிரண் டாண்டு பொறுத்திறை தாருமென்பான் இவ்வகையாய்ச் சோழன் இராச்சியத்தை யாண்டிருந்தான் அவ்வகையாய்ச் சோழன் ஆண்டிருக்கும் நாளையிலே மெய்யறிவு கொண்ட மேலாம்பதத் தெளிவோன் தெய்வத் திருநிலைமை செப்புவே னம்மானை இவ்வகை யாகச் சோழன் இருந்து ராச்சியத் தையாள கவ்வைக ளில்லா வண்ணம் கலியுகம் வாழும் நாளில் செவ்வகைத் திருவே யான திருவுளக் கிருபை கூர்ந்து தெய்வமெய் நீதம் வந்த செய்தியைச் செலுத்து வாரே.');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(11,'தெய்வ நீதம்','வாரம தில்லாமல் மன்னன் அதிசோழன் நீதமாய்ப் பூமி நிறுத்தியர சாளுகையில் கண்டு வேதாவும் கமலத் திருமகளும் நன்று தெய்வாரும் நாரா யணருமெச்சி அன்றந்த மாமுனிவர் அல்லோருந் தான்கூடி சென்று சிவனார் திருப்பாதந் தெண்டனிட்டுச் சாகா திருக்கும் சமூலத் திருப்பொருளே ஏகா பரனே எங்கும் நிறைந்தோனே மூலப் பொருளே முதற்பொருளே காரணரே சாலப் பொருளே தவத்தோ ரரும்பொருளே நாரணரும் வேதா நாடிப் பொரும்போரில் காரணரே நீரும் கனல்கம்ப மானோரே ஆலமு தருந்தி அரவை மிகவுரித்துக் கோலத் திருக்கழுத்தில் கோர்வையா யிட்டோனே ஆனைதனை யுரித்து அங்கமெல் லாம்புனைந்து மானேந்திய கரனே மழுவேந்திய சிவனே கோனேந்திர கிரியில் குடியிருக் குங்கோவே தானே யிருக்கும் தவமே தவப்பொருளே ஆதியாய் நின்ற அதியத் திருமுதலே சோதியே சோழன் சொல்நெறியைக் கேளுமையா வாடிவந்த பச்சினுக்கு வாளா லவனுடம்பைத் தேடிவந்த வேடனுக்குத் துடையரிந் தீந்தவன்காண் 260 ஆறி லொருகடமை அவன்வேண்டிட் டானெனவே மாறி யவன்புவியோர் மனதிற்கௌ வைகளில்லை கோவில் சிவாலயங்கள் குளங்கூபம் வாவிகளும் சேவித் தனுதினமும் செய்வானே தானதர்மம் ஆதலால் சோழன் அரசாளுஞ் சீமையிலே நீதமாய்த் தெய்வம் நிலைநிறுத்த வேணுமையா');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(12,'தெய்வ நீதம்','என்று மிகத்தேவர் இறைஞ்சித் தொழுதிடவே கன்றிருந்த ஈசர் கரியமா லோடுரைப்பார் நல்லதுகாண் மாயவனே நாட்டை மிகக்காக்க வல்லவனே பூமா தேவி தனைவருத்தும் வருணன் தனையழைநீ மாதமும் மாரிபெய்ய கருணைக் குடைவிரிக்க கங்குல் தனையழையும் வாசி யதுபூவாய் வழங்க வரவழையும் தோசி மறலியையும் சொல்லி விலக்கிடுநீ குருபூசை செய்யும் கூட்டமதிற் சிவமாய் திருவீற் றிருக்கச் செய்திடுநீ கோலமது நித்திரா தேவி நித்தமந்தப் பூமியிலே மத்திபமாய்க் காக்க வையென்றா ரீசுரரும் நினைத்தோர்க் குறுதி நினைவிலறி வுதோன்ற எனைத்தோத் திரங்கள் இகழாமல் வையுமென்றார் பன்றியோ டேகடுவாய் பண்புற் றிருந்திடவும் அன்றிலோ டேகுயிலும் அன்புற் றிருந்திடவும் கீரியும் பாம்பும் கிளைபோல் குழைந்திடவும் வாரி குளம்போல் வரம்பிலே நின்றிடவும் இட்ட விரைகள் ஈரெட்டுக் கண்டிடவும் பட்டியும் முயலும் பண்புற் றிருந்திடவும் பசுவும் புலியுமொரு பக்கம்நீ ருண்டிடவும் கசுவுங் கரைபுரளக் கரும்புமுத் தீன்றிடவும் சாத்திர வேதம் சமயம் வழுகாமல் சூத்திர மாகத் துல்வப் படுத்திடவும் மனுவோர் தழைத்து மக்களொரு கோடிபெற்று இனிதாக நாளும் இறவா திருந்திடவும் சந்திர சூரியர்கள் தட்டுமிக மாறாமல் இந்திரரும் தேவர் இருஷிநிலை மாறாமல் தான தவங்கள் தப்பிமிகப் போகாமல் வானவர்கள் தேவர் வளமாக நின்றிடவும் ஈன மில்லாமல் இதுதெய்வ நீதமெல்லாம் மானம் நிறுத்தி வையென்றா ரீசுரரும் இப்படித் தெய்வ நீதம் ஈசுர ரிதுவே கூற சொற்படி மறவா வண்ணம் திருமரு கோனுஞ் செய்தார்   அப்படித் தவறா நீதம் அம்புவி தனிலே வாழ மற்பணி குழலார் தங்கள் மனுநெறி வகுத்தார் தாமே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(13,'பெண்கள் நிலைமை','தெய்வத்திரு நிலைமை செப்பியபின் தேசமதில் நெய்நிதியப் பெண்கள் நிலைமைகே ளம்மானை கணவன் மொழியக் கலவுமொழி பேசாத துணைவி நிலைமை சொல்லுவே னம்மானை கற்கதவு போலே கற்பு மனக்கதவு தொற்கதவு ஞானத் திருவுகோ லவள்மனது அன்பாகப் பெற்ற அன்னை பிதாவதுக்கும் முன்பான சோதி முறைபோ லுறவாடிப் போற்றியே நித்தம் பூசித் தவள்மனதில் சாற்றிய சொல்லைத் தவறா மலேமொழிவாள் அரசன் துயில ஆராட்டி ஓராட்டிக் கரமா னதுதடவிக் கால்தடவி நின்றிடுவாள் துயின்ற தறிந்து துஞ்சுவாள் மங்கையரும் மயன்ற ஒருசாமம் மங்கை யெழுந்திருந்து முகத்து நீரிட்டு நான்முகத்தோ னையுந்தொழுது அகத்துத் தெருமுற்றம் அலங்கார மாய்ப்பெருக்கிப் பகுத்துவ மாகப் பாரிப்பார் பெண்ணார்கள் தவத்துக் கரிய தையல்நல்லார் தங்களுட மனுநீதஞ் சொல்லி வகுக்க முடியாது கனியான பெண்கள் கற்புநிலை மாறாமல் இனிதாக நாளும் இருக்குமந்த நாளையிலே வனியான சாதி வளமைகே ளம்மானை');");


