        db.execSQL("INSERT INTO akilathirattu2 VALUES(79,'சப்தகன்னியரும் சான்றோர் பிறப்பும்','அரிகோண மாமலையில் அயோகவமிர்த கங்கை பரிகோண மாமலையின் பகுத்துரைக்கக் கூடாது தேன்கமுகு மாங்கமுகு தென்னங் கமுகுகளும் வான்கமுகு வாழை வழுவிலா நற்கமுகும் சோலை மரமும் சுபசோப னமரமும் ஆல மரமும் அகில்தேக்கு மாமரமும் புன்னை மரமும் புஷ்ப மலர்க்காவும் தென்னை மரமும் செஞ்சந் தனமரமும் மாவு பலமரமும் வாய்த்த பலாமரமும் தாவு மரத்தின் தண்மைசொல்லக் கூடாது சோலையிலே வீற்றிருக்கும் சீர்பறவை யின்பெருமை தூல மின்னதென்று சொல்லத் துலையாது பார்வதியும் ஈஸ்வரனும் பாவித் திருப்பதுபோல்  தேர்பதியும் மேடைகளும் சிங்கா சனங்காணும் அலையில் துயில்வோர் அங்கிருந்த பாவனைபோல் நிலையில் முனிவோர் நிற்பதெண்ணக் கூடாது கயிலை யீதென்று கண்ணான மாமுனிவர் ஒயிலாகக் கூடி உவந்திருப்ப தவ்வனத்தில் வைகுண்டங் காண வந்ததர்மி யெல்லோரும் மெய்குண்டங் கண்டோ மெனஇருப்பா ரவ்வனத்தில் அப்படியே நல்ல அயோக அமிர்தவனம் இப்படியே நன்றாய் இயல்பா யிருப்பதுதான் புட்டாபுரங் கிழக்கு பூங்காவு நேர்மேற்கு வட்டமுள்ள ஸ்ரீரங்கம் வடக்கு வனந்தெற்காகத் தெற்கே திரிகோணம் செங்காவு நேர்வடக்கு மிக்கவகை மேற்கு மிகுத்தவனம் நேர்கிழக்கு இவ்வெல்கை சூழ்ந்த அயோக அமிர்தவனம் அவ்வனத்தி லுள்ள அமிர்தகங்கை யானதிலே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(80,'சப்தகன்னியரும் சான்றோர் பிறப்பும்','குளித்து விளையாடிக் கூபந் தனிலிறங்கி  களித்து மகிழ்ந்து கையில்நீர் தான்திரட்டி ஈசருட முடியில் இட்டுக் கரங்குவித்து வாசமுடன் கயிலை வாழ்ந்திருக்கு மாமடவார் மரகத வல்லி வள்ளி சலிகையெனும்  சரகதக் கன்னி சரிதை அரிமடவும் எழு மடவும் எண்ணெண்ணு மிப்படியே நாளு முறையாய் நடத்திவரும் நாளையிலே மாலறிந்து கன்னிமுன்னே வந்தார் சன்னாசியென ஏலறிந்து கன்னி இவரல்ல ஈசரென்று சாய்ந்து விலகித் தையல்நல்லார் போகுகையில் ஆய்ந்து தெளிந்த அச்சுதரு முன்னேகி பலநாளு மீசுரர்க்குப் பாவையரே நீங்களுந்தான் செலந்திரட்டி மேன்முடியில் செய்தீ ரனுஷ்டானம் இனியெனக்கு நீங்கள் எல்லோரு மிக்கவந்து கனிநீர்தனையு மெந்தலையில் கவிழுமென்றா ரெம்பெருமாள் அப்போது கன்னி எல்லோரு மேதுரைப்பார் எப்போதுந் தானாய் இருப்பவர்க்கே யல்லாது மாயவர்க்கு மற்றுமுள்ள மயேசு ரர்தன்றனக்கும் வாயமுள்ள வானவர்கோன் மறைமுனிவர் தன்றனக்கும் எருதேறி நித்தம் இறவா திருக்குகின்ற ஒருவனுக்கே யல்லால் ஊழியங்கள் வேறில்லையே கேட்டுஸ்ரீ கிருஷ்ணரும் கிளிமொழியோ டேதுரைப்பார் ஒட்டி லிரந்துண்ண ஊர்வழியே தான்திரியும் ஆண்டிக்கே யல்லாது அரவணையி லேதுயிலும்  காண்டீபனுக் கேவல் கருதோ மெனவுரைத்தீர் தோகையரே கங்கையினிச் சுருட்டுவ தைப்பார்ப்போம் ஆகட்டு மென்று அச்சுதருங் கோபமுற்று மேலோக மாயிருக்கும் வேதயேழு வுகத்தில்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(81,'சப்தகன்னியரும் சான்றோர் பிறப்பும்','சாலோக மான சத்திபர லோகமதில் ஆருரூ மில்லாத ஆகாச சத்தியொன்றும் சீருரூப மான சிவலோக மானதிலே மெய்கொண்ட வானோர் வித்தொன்று ஆனதுவும் வைகுண்ட லோகமதில் வாய்த்ததர்மி யானதிலே தன்மியொரு வித்துத் தானெடுத்து வேதாவின் சென்மித் தெடுத்தார் சிவயிருஷி யொன்றதிலே தபோதனராய்ச் சண்டன் தன்னுகத்தில் வாழுவரில் சகோதரரா யொன்று தானெடுத்தா ரம்மானை சொர்க்கலோ கமதிலே ஸ்ரீராமர் தன்றனக்கு பக்குவங்க ளாகப் பணிவிடைகள் செய்வோரில் நல்லகுல மான நயனவித் தொன்றெடுத்து வெல்லமர் கோன்வாழும் வெற்றிதெய்வ லோகமதில் புத்தியுள்ள நாதன் பின்யுகத்தை யாளுதற்கு சத்தியுள்ள வித்தொன்று தானெடுத்தா ரம்மானை இப்படியே மேலோகம் ஏழு லோகமதிலும்  அப்படியே நல்ல ஆர்க்கமுள்ள வித்தேழு எடுத்துத் திருமால் இருதயத்தி லேயடக்கிக் கொடுத்துநின்ற தாதாவைக் குவித்துப் பதம்போற்றிக் கன்னியுட கற்பதுக்குக் கருத்தேது செய்வோமென்று உன்னி மனதில் ஒருமித்துப் பார்த்தனரே பார்த்தனரே கற்பதுக்குப் பக்குவம்வே றில்லையென்று தீத்தழலாய்ப் போகத் திருவுருவங் கொண்டனரே பிரமா உபதேசம் பிறப்பு உருவேற்றி குரமாய் வருணன் குளிரத் தொளிந்திடவே காமத் தழலாய்க் கருமேனி யானதிலே வேமக் கனல்போல் விழிகொழுந் திட்டெரிய சாந்தணியுங் கன்னி தையல்தெய்வ மாமணிகள் கூந்தல் விரித்துக் கூபந் தனிலிறங்கி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(82,'சப்தகன்னியரும் சான்றோர் பிறப்பும்','அரிஓம் எனவே ஆடிக் கரையேறி தரிதோம் மெனவே சலக்கரையை விட்டவர்கள் உயர வரவே உள்ளுதறத் துள்ளல்கொண்டு அயரக் கரங்கால் அங்கமெல்லாந் தொங்கலிட கிடுகிடெனத் தேகம் கிளிமொழிவாய் கொட்டிடவே திடுதிடென அக்கனியைத் திரைபோல் வளையலுற்றார் கன்னி யேழுபேரும் கனலை மிகஆவ உன்னித் திருமால் ஓங்கார மோகமதால் மங்கை யேழுபேர்க்கும் வயிற்றிலுற்ற தம்மானை சங்குவண்ண மாலோன் தற்சொரூபங் கொண்டனரே உடனே ஸ்திரீகள் உள்தரித்த பிள்ளைகளை தடமேலே பெற்றுச் சஞ்சலித்து மாமடவார் வெருவிப் பயந்து விழிமடவா ரெல்லோரும் கருவிதொண்ணூற் றாறும் கலங்கியே தானோடி துகிலை யெடுத்துடுத்துச் சுருட்டினார் கங்கைதனை கையில் சலந்தான் கட்டித் திரளாமல் கலங்கி யழுது கண்ணீர் மிகச்சொரிந்து மலங்கி யழுது மண்ணிலவர் புரண்டு அய்யோ பொருளை அறியாமல் விட்டோமே மெய்யோடே குத்தி விழுந்தழுதா ரம்மானை கனலைத் துணையா மென்றாவிக் கற்பை இழந்தோங் கன்னியரே புனலைத் திரட்டப் பெலமின்றி புத்தி யழிந்தோம் பூவையரே அனலைத் தரித்த அரன்முன்னே அங்கே சென்றால் பங்கம்வரும் இனத்தைப் பிரிந்த மானதுபோல் இருப்போம் வனத்தி லென்றனரே');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(83,'அம்மைமார் தவம்','கன்னியர்கள் மேனி கனிந்து மினுமினுத்து மின்னித் தனங்கள் மிகுபால் சுரந்திடவே கற்புக் குழறி கயிலையுக மானதற்கு   அற்புத வேள்கங்கை அவர்நினை வில்லாமல் எல்லோரு மிக்க ஈசுரரைத் தானினைத்து வல்லோனே யென்று வரம்பெறவே நின்றனராம் நின்றார் தவத்தின் நிலைமைகே ளம்மானை இன்றெங்கள் கற்பை ஈடழித்த மாமுனிவர் வந்தெங்கள் தம்மை மாலையிட வேணுமென்று பந்துத் தனமின்னார் பாவையேழு பேரும் ஈசுரரே தஞ்சமென இருந்தார் தவசதிலே மாசொன்று மில்லா மாத ரேழுபேரும் தெற்கு முகமாய்த் தேவி யேழுபேரும் மிக்கத் தவசு மிகப்புரிந்தா ரம்மானை கற்பழித் தெங்கள்கையில் கன்னிதிர ளாமல்வைத்தோர் பொற்பாத முண்டெனவே பூவை தவசுநின்றார் பாலிளகி நல்லமிர்தம் பாலாறா யோடிடவே காலிளகா வண்ணம் கடுந்தவசு செய்தனரே பிள்ளை யேழுபேரும் பெற்றுப் பெருகிடவும் கள்ளஞ்செய்த மாமுனிவர் கைப்பிடிக்க எங்களையும் மக்களையு மெங்களையும் மாமுனிவர் வந்தெடுத்து ஒக்க வொருமித்து உலகாள வைத்திடவும் நின்றார் தவசு நேரிழைமா ரெல்லோரும்  நன்றான மாமடவார் நாடித் தவசிருக்க பிறந்த பிள்ளைதன்னுடைய புதுமைகே ளன்போரே அறந்தழைக்கும் நாரா யணர்மக வாகியதோர் சான்றோர் பிறந்ததுவும் தரணியர சாண்டதுவும் வேண்டும் பெரிய விருதுவகை பெற்றதுவும் அய்யா உரைக்க அடியே னதையெழுதி மெய்யான போத மேலோர்கள் முன்பதிலே அன்பான இந்த அகிலத்திரட் டம்மானை தன்போத மாயிருந்து தாழ்மையுடன் கேட்டவர்க்குக் கன்மமுதல் சஞ்சலங்கள் கழியுமென் றெம்பெருமாள் உண்மையுள்ள லட்சுமிக்கு உபதேசமா யுரைத்தார் இப்படியே பிள்ளைதனை ஈன்றபின்பு கன்னியர்கள் அப்படியே சென்று அவர்போய்த் தவசிருக்க ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(84,'திருமால் அமுதளித்தல்','பிள்ளை களைப்போட்டுப் புண்ணியனார் போகாமல் வள்ளலந்த மாலும் மதலை தனையெடுத்து ஆரிடத்தி லிம்மதலை அடைக்கலமாய் வைப்போமென்று விசாரித்து நன்றாய் விசாரமுற்றா ரம்மானை தெய்வேந்திரன் பசுக்கள் திரைமேயக் கண்டவரும் கையதிலே சீங்குழலைக் கனிவாயில் வைத்திருத்தி நிரைவா வெனவே நியமித்தங் கூதிடவே  அரை நொடியிலாவு அங்கொன்று மில்லாமல் அங்குவந்து மாயனிடம் அழைத்ததென்னக் கேட்டிடுமாம் சங்குதனில் பாலுமிழ்ந்து தாருமென்றா ரெம்பெருமாள் பாலுமிழ்ந் தாவு பலநாளும் பாலருக்கு நாலொருநாள் மட்டும் நடந்துவரும் வேளையிலே கன்றுக்குப் பாலு காணாமல் மேய்ப்போர்கள் ஏதென்றெனப் பார்த்து இயலறிந்து வானவர்கோன் தானறியச் சொல்லிச் சண்டையிட வந்தனனே வந்தவனுக் கெதிரே மாமுனிவன் சூலமதை இந்தாப்பா ரென்று எறிந்தா ரவன்பயந்து ஆரோ வெனப்பயந்து அயிராவதத் தோனும் போரொல்கிப் போனான் பொன்னுலோ கந்தனிலே நல்லதென மாமுனியும் நளின முடன்மகிழ்ந்து செல்லமக வான சிறுவர் தமைவளர்க்க');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(85,'காளி வரவு','மாகாளி யென்ற வடபத்தி ரகாளி ஓகாளி யென்ற உயர்ந்த பெலக்காரி பெண்ணல்லப் போர்க்குப் புருடர்மிகப் போராது விண்ணவரும் மண்ணவரும் விறுமாவும் போராது அப்படியே துஷ்ட ஆங்கார மாகாளி இப்படியே நன்றாய் இவளிருக்கும் நாளையிலே  ஆணொருவர் தன்னால் அழியா வரங்கள்பெற்ற தாணொருவன் தனையும் சத்தி உமைதனையும் கெணியா வரங்கள்பெற்றுக் கீழுமே லுமடக்கித் துணிவாகத் தேவர்களைத் தூளிபட ஏவல்கொண்டு தேட்ட முடனேழு செகல்கடந் தப்புறத்தில் கோட்டை யதிட்டுக் குறும்புசெய் தாண்டனனே தேவர்கள் சென்று சிவனார்க் கபயமிட மூவரு மொத்திருந்து மழுதும் விசாரமிட்டார் ஆணாலே தக்கனையும் அழிக்கவகை யில்லையிங்கே பூணாரம் பூண்ட புட்டா புரக்காளி காளி படையும் கமண்டலத்தில் சென்றதுண்டால் தூளிபடத் தக்கன் சிரசறுப்பா ளென்றுமிக மாலுரைக்க ஈசுரரும் மறையோருஞ் சம்மதித்து வேலுகந்த காளிதனை விளித்தார்கா ணம்மானை உடனறிந்து மாகாளி உடையோன் பதம்பணிந்து வடவாக் கினிமுகத்தாள் வருத்தினதே னென்னையென்றாள் தக்கன் தலையறுத்துச் சங்காரஞ் செய்திடவே மிக்கநீ போவெனவே விடைகொடுத்தா ரீசுரரும் விடைவேண்டி காளி விமல னடிபோற்றிப் படைக்காரி பின்னுமொன்று பரமனோ டேகேட்டாள்  என்னோ டுதவி இயல்படையாய்த் தான்வரவே வன்னப் புதல்வர் வகிருமென்றாள் மாகாளி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(86,'காளி வரவு','அப்போது ஈசுரரும் ஆங்காரியை நோக்கி கற்போடு வொத்த கன்னியே வுன்றனக்கு பெண்ணா ணுமில்லாப் பெரிய பெலக்காரி கண்ணான காளி காரிகையே நீகேளு தரித்துப் பிறக்கத் தகாதே மாகாளி மரித்துப் பிறக்காத மாகாளி யேயுனக்கு விடையா யொருவசனம் விரிக்கக்கே ளொண்ணுதலே படைக்காகப் பாலர் பச்சைமால் தாவெனவே தவசு மிகப்புரிந்தால் சங்குசரத் தாமன் விபுசு தனிற்பிறந்த வீரரேழு பேர்களையும் உன்னை யழைத்து உன்கையி லேதருவார் முன்னே தவசு மிகப்புரியப் போவெனவே அரனார் விடையும் அருளி மிகக்கொடுக்கப் பரமான தேவி பச்சைமால் தன்றனைத்தான் நினைத்துத் தவசு நெடுநாளாய் நின்றிடவே அனைத்துயி ருங்காக்கும் அச்சுதருந் தானறிந்து மக்களேழு பேர்களையும் மாகாளி கைக்கொடுக்கக் கொக்கரித்துக் காளி கொண்டாடித் தான்மகிழ்ந்து  வாங்கு மளவில் மாமுனியைத் தானோக்கி தாங்கிநின்று பாதத்(து) அடிதாழ்ந்து ஏதுசொல்வாள் வேத முனியே வித்தைக் கருத்தோனே மாதவங்கள் கற்ற மாமுனியே யிம்மதலை ஆனோர்க்கு நாமம் அருளிநீர் தாருமென்றாள் வானோர்கள் போற்றும் மாமுனியுந் தான்மகிழ்ந்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(87,'சான்றோர்க்கு நாமம் அருளல்','உள்ளதுதா னென்று உடனே மனமகிழ்ந்து வள்ளல்சிவ னாரறிந்து மறைவேதனை யழைத்து முப்பத்து முக்கோடி முனிவரவர் தங்களையும் நாற்பத்து நாற்கோடி நல்ல ரிஷிகளையும் தேவர் முதலாய்த் தேவேந் திரன்வரையும் மூவ ரறுவர் உள்ளோரையு மழைத்து கிணநாதர் வேதா கிம்புருடரை யும்வருத்தி குணமான தந்தி குமரனை யும்வருத்தி சத்தி உமையும் சரசுவதி பார்பதியும் எத்திசையும் வானோர் எல்லோரை யும்வருத்தி சங்கமது கூடி சாஸ்திரங்கள் தானோதி மங்கள வாத்தியங்கள் மடமடென நின்றதிர இப்படியே சங்கம் எல்லோருந் தான்கூடி அப்படியே தானிருக்க அருளுவா ரச்சுதரும்  பிறந்தபிள்ளை யேழதுக்கும் பேரிட வேணுமென்று அறந்தழைக்கு மீசர்முன் அவர்வைத்தா ரம்மானை அப்போது ஈசுரரும் அன்பா யகமகிழ்ந்து இப்போது மாயவரே எல்லோருக் கும்போதுவாய் நீர்தானே நாமம் இட்ட லதுபோதும் பார்தா னளந்த பாலவண்ணா வென்றுரைத்தார் கார்வண்ணருங் கேட்டுக் கறைக்கண்ட ரோடுரைப்பார் தார்வண்ணரே முதற்பேர் தானுரைக்க வேணுமென்றார் நல்லதுதா னென்று நாட்டமுற்று ஈசுரரும் வல்லவனே உன்னாத விந்தில்வந்து தோன்றினதால் தோணாப் பொருளைத் தொடர்ந்துகண்ட மன்னவர்க்கு சாணா ரெனநாமம் சாற்றினா ரீசுரரும் முதற்பேர்தா னீசர் மொழிந்தபின்பு வேதாவும் மதமான விந்து மாயமுனி சேயதற்கு சான்றோ ரெனநாமம் சாற்றினார் வேதாவும் ஆண்டா ரிதுவுரைக்க அச்சுதரும் பின்சொல்லுவார் நாடாள்வா ரென்று நாமமிட்டார் பாலருக்கு தாடாண்மை யுள்ள சத்தியங் கேதுரைப்பாள் அண்ணர் விநோதமதில் அவதரித்த பிள்ளைகட்கு வண்ணமுள்ள பேரு வாழ்த்தி விடைகொடுப்பாள் ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(88,'சான்றோர்க்கு நாமம் அருளல்','எங்கும் புகழ்பெற்று இராஜபட்டந் தான்சூடும் சங்குமன்ன ரென்று தானுரைத்தாள் சத்தியுமே பேறுபெற்ற பாலரென்று பிரிய முடன்மகிழ்ந்து பாருபதி நாமம் பகருவா ளம்மானை சென்றஇடம் வென்று சீமைகட்டித் தானாண்டு மண்டலங்கள் தோறும் வரிசைபெற்று வாழ்ந்திருக்கும் பொற்பமுடி மன்னரென்று பேரிட்டாள் பார்பதியும் கற்பகத்துக் கொத்த கன்னி சரசுவதியும் வெள்ளானை வேந்தரென்று வெண்டாமரை யுரைத்தாள் பிள்ளையார் தானும் பிரியமுடன் மகிழ்ந்து நன்றான வீர நகுலவேந் தரெனவே அன்றானை முகத்தோன் அருளினர்கா ணம்மானை சண்முகனுந் தான்மகிழ்ந்து தவலோக மன்னரென்று விண்ணுகமு மெய்க்க விளம்பினர்கா ணம்மானை வானோர்கள் வேத மாமுனிவர் தாமகிழ்ந்து தானான மாயவனார் தான்பெற்ற பாலருக்குத் தர்மகுல வேந்தரென்று சாற்றினா ரம்மானை கர்மமில்லாத் தேவர் கரியமால் பாலருக்கு மெய்யுடைய பாலர் மென்மேலும் வாழ்ந்திருக்க தெய்வகுல மன்னரென்று திருநாம மிட்டனரே  வீரியமாய்ச் சூரியனும் வெற்றிமால் பாலருக்குச் சூரியகுல வேந்தரென்று சொன்னார்கா ணம்மானை வாசவனுந் தான்மகிழ்ந்து மாயனுட பாலருக்கு வீசவிசைய வேந்தரென்று நாம மிட்டார் இப்படியே நாமம் இவர்மொழிந்த தின்பிறகு கற்புடைய சன்னாசி கருத்தாக வேயுரைப்பார் நாட்டுக் குடைய நாதனுட கண்மணிக்குக் காட்டுரா சனெனவே கருத்தாக நாமமிட்டார் இப்படியே ஈசர்முதல் எல்லோரும் நாமமிட்டு அப்படியே பிள்ளைகட்கு அவரவரே காப்பணிந்து சத்தி யுமையும் சரசுவதி பார்பதியும் எத்திசையு மெய்க்க எடுத்துநீ ராட்டுவாராம் அமுதமது சேனையிட்டு எல்லோரும் தாமகிழ்ந்து குமுதமொழி மாதர் குரவையிட்டுத் தாமகிழ்ந்து அண்டர் முனிவோர் எல்லோரும் பார்த்திருக்க தண்டாமரை மாது தாலாட்ட வுத்தரித்தாள்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(89,'சரஸ்வதி தாலாட்டு','வைகுண்ட கண்ணோ வரம்பெற்ற மாதவமோ கைகண்ட வித்தை கருத்தறிந்த உத்தமரோ தங்கமுடி பெற்றவரோ சங்குமுடி காவலரோ வங்கம்நிசங் கண்ட மங்காத சான்றவரோ  சிங்கமுகத் தண்டிகையும் சிலம்புனைந்த ரத்தினமும் சங்கக் கொடிவிருது சங்குரட்டை பெற்றவரோ முத்துச் சிலாப முதலாளி யானவரோ கொத்துமுங்கை யாபரணம் கொடிவிருது பெற்றவரோ மூலப்பொருள் கண்ட முதற்சாதி யானவரோ தாலம்பா லுண்டு தானிருந்த மன்னவரோ நடைக்கா வணங்களிட்டு நல்லதீ வட்டியுடன் படையோடே வீற்றிருக்கும் பாரமுடி மன்னவரோ வெள்ளானை மேலே வீதிவலஞ் சுற்றிவந்து துள்ளாடி சிங்கா சனம்வீற் றிருப்பவரோ பூதமது பந்தம் பிடித்துமுன்னே தான்வரவே நாதமிரட் டையூதி நாடாளு மன்னவரோ எக்கா ளமூதி இடமடம் மானமுடன் மிக்கான சீமையெங்கும் மேவிவரு மன்னவரோ வாரணங்கள் கட்டி வையகத்தைத் தானாண்டு தோரணங்கள் நாட்டிவைத்த தெய்வத்திருச் சான்றவரோ கர்மம தில்லாமல் களிகூர்ந் திருப்பவரோ தர்மமுடி பெற்றவரோ சாஸ்திரத்துக் குற்றவரோ உடற்கூறு சத்தி உயிரோ டுதித்துவந்த சடக்கூறு மூலச் சட்டமது கொண்டவரோ  கல்விக் குகந்த கருணாகர ரானவரோ செல்விக் குகந்த சென்மமது கொண்டவரோ தேவர்க்கும் வானவர்க்கும் திருப்பதிக ளாவதற்கும் மூவர்க்கு முதவிசெய்து உதித்துவந்த கற்பகமோ சென்ற இடமெல்லாம் சிறப்புவெகு மானமுடன் மண்டலங்கள் மேய்க்க வாழுகின்ற சான்றவரோ சாணா ருக்குள்ளே சர்வது மேயடக்கிக் கோணாத மாயன் குருக்கொடுத் தீன்றகண்ணோ அறிவுஞா னத்தோடும் ஆதிப் பிறவியோடும் செறியுங் கலையோடும் செடமெடுத்த சான்றவரோ துட்டரென்ற பேரைச் சூரசங் காரமிட்டுக் கொட்டமிட்டுக் கோட்டை கொடிவிருது பெற்றகண்ணோ ஆண்டிருக்கு மன்னவரோ அச்சுதரின் பாலகரோ தாண்டி வரம்பெற்றுத் தரணியர சாண்டவரோ பாண்டவரோ ஆண்டவரோ பாலவண்ணர் பெற்றெடுத்த சான்றவரோ ராராரோ தழைத்திருக்க ராராரோ தங்கமணியோ நவமணியோ சலத்தில்விளைந்த தரளமுதோ சிங்கக்கொடிகள் பெற்றவரோ சீமையடக்கி யாண்டவரோ துங்கவரிசை பெற்றவரோ திருமால்விந்தி லுதித்தவரோ சங்கமகிழ வந்தவரோ சான்றோர்வளர ராராரோ  ');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(90,'கற்பகத்தரு','சந்தோ சமாகச் சரசுவதி தாலாட்டி வெந்தோச மெல்லாம் விலகவே நீராட்டி வைகுண்ட மூர்த்தி மாமுனிதன் கைக்கொடுக்க மைகொண்ட வேதன் மதலை தனைவாங்கி ஈசர் முதலாய் எல்லோருங் கூடிருந்து வாசமுள்ள பிள்ளைகள்தாம் வளரத் திருவமிர்தம் சேனைமிக வூட்டுதற்கும் செல்வ முண்டாவதற்கும் வானத் தமிர்தம் வருத்திமிக ஈயவென்று கயிலை தனிலிருக்கும் கண்ணான பேர்களுக்கு அகிலமதிற் பாயும் ஆகாய வூறலதை வருத்திக் கொடுக்கவென்று மாயவரு மீசுரரும் பொருத்தமுள்ள வானோரைப் போய்வேண்டி வாருமென்றார் அப்படியே வானோர் ஆகாய மீதேகி செப்பமுட னமிர்தம் சென்றவர்கள் பார்க்கையிலே ஆருரூப மில்லா ஆகாச மேல்வழியே சீருரூப மான சிவகயிலை யானதிலே பாயு மளவில் பலசாஸ்தி ரங்கள்கற்ற வீயுமறை வேதியனும் விழிநுதலாள் கன்னியரும் அமுதமதை யெல்லாம் அள்ளித் தலைமேலும் குமுதமுடன் குடித்துக் கொழுத்துமிகப் பாளைவைத்துத்    தேகமது நிமிர்ந்து தேவியு மன்னவனும் ஆகமது கூர்ந்து அலங்கரித்து நிற்பளவில் வானோர்கள் பார்த்து வாய்த்தமிர்தங் காணாமல் ஏனோயிது மாயமென்று எண்ணிமிகப் பார்ப்பளவில் கொண்டாடி நின்ற கூர்மறையவன் தனையும் பெண்டாட்டி யான பெண்ணதையும் வானோர்கள் பிடித்து இழுத்துப் பின்னுமுன்னுந் தள்ளிமிக அடித்துச் சிவன்முன்னே அச்சுதரும் பார்த்திருக்க கொண்டுவந்து விட்டுக் கூறுவார் வானோர்கள்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(91,'கற்பகத்தரு','பண்டுமுத லின்றுவரை பாய்ந்த அமிர்தமெல்லாம் உண்டுகொண்டு தேகம் உரத்துமிகப் பாளைவைத்து வண்டுறுக்கி மிக்க வலுப்பேசி னானெனவே சொல்லிடவே வானோர் திருமா லதுகேட்டு நல்லதுதா னென்று நாடிச்சிவ னோடுரைக்க அப்போது நல்ல ஆதி சிவமுரைப்பார் எப்போதும் பாலர் இவருண் ணமிர்தமெல்லாம் கொடுக்கும் படியாய்க் குருக்கவையு மென்றுரைத்தார் அடுக்கநின்ற தேவர் அதுகேட்டுச் சீக்கிரமாய் மறையோனையும் மாதுவையும் வந்தெடுத்துச் சாபமிட இறையவரைப் பார்த்து ஏதுரைப் பான்மறையோன்  இப்போது இட்ட இச்சாப மானதுதான் எப்போது நீங்கும் என்றேகேட் டான்மறையோன் நீசக் குலமறுத்து நெடிய திருமாலும் மாசில்லாத் தர்ம வையகத்தை யாளுதற்கு அன்பு சேகரிக்க அங்குவரு வார்கண்டீர் வம்பு மாறும்போது மாறுமுங்கள் சாபமென்றார் உடனே மறையோனும் ஓவியமு ள்ளதென்று தடமேலே நின்று தால மெனவளர்ந்தார் வளர்ந்த அமிர்தமதை மக்களேழு பேர்களுக்கும் பழமமிர்தக் காயோடு பலவகையுந் தானாண்டு அமிர்தமதை நீங்கள் ஆக்கிரகந் தானடக்கிக் குமிர்தமுட னீங்கள் குடித்திருங்கோ வென்றுசொல்லி உங்களுக்குப் பாலமிர்தம் ஊறுமல்லா லித்தாலம் எங்களுக்கு மித்தாலம் இசையாது கண்டீரோ பாலருக்கு இந்தவரம் பரமயுகத் தோர்கொடுத்து ஞாலமுள்ள காளி நாயகியைத் தானழைத்து');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(92,'பத்திரத்தாள் பெற்றமக்கள்','பாலரையுங் காளி பண்பாக வாங்குகையில் ஆலமுத முண்ட அச்சுதரு மேதுரைப்பார் அடவு பதினெட்டும் அலங்கார வர்மமதும் கடகரியின் தொழிலும் கந்துகத் தின்தொழிலும்  மாவேறுந் தொழிலும் வாள்வீசுந் தொழிலும் பாவேறு பாட்டும் பழமறைநூ லானதுவும் அடவு மேலான அதிகப்பல வித்தைகளும் திடமு மிகவருத்தி சேனா பதியாக்கி எல்லா விதத்தொழிலும் இசைவான ராகமதும் நல்லா வருத்தி நாட்டமுட னேகொடுத்து உயிர்ச்சேதம் வராமல் ஒன்றுபோ லென்மகவை நெய்ச்சீதம் போலே நீவளர்த்துத் தாவெனவே அல்லாம லென்மகவை ஆரொருவ ரானாலும் கொல்லாமல் காக்கக் கூடுமோ வுன்னாலே அன்பான காளி அதற்கேது சொல்லலுற்றாள் என்பாலகர் தனையும் ஈடுசெய்ய யிங்கொருவர் உண்டோகா ணிந்த உலகி லெனக்கெதிரி என்றேதான் காளி இப்படியே சொல்லியபின் பாலரையுந் தானெடுத்துப் பரமசிவ னாரருளால் கோலமுள்ள மாயன் கொடுத்தாரொரு வார்த்தைசொல்லி சீலமுள்ள காளியென் சித்திரப் பாலருக்கு பாலருக்குப் பங்கமது பற்றாமல் காத்திடுநீ மதலைதனக் கோர்தீங்கு வந்ததே யுண்டானால் குதலையரே வுன்றனக்குக் கொடுஞ்சிறைதான் சிக்குமென்று     சொல்லியே காளிகையில் சிறுவரையுந் தான்கொடுத்து வல்ல பெலமுள்ள மாடுதனி லேறியையும் வேதாவும் நல்லநெற்றி விழியுடை யாள்தனையும் மாதாவு மான வாய்த்தசர சோதியையும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(93,'பத்திரத்தாள் பெற்றமக்கள்','தேவர்முதல் வானவரை சிட்டர்முனி வோர்களையும் மூவர்களை யுங்கயிலை ஊரேபோ மென்றனுப்பி காளிதனைப் பிள்ளைகளைக் கருத்தாய்வள நீயென்று ஆழி யடைத்த அச்சுதருந் தான்நடந்து சீரங்க மாபதியில் சென்றிருந்தா ரம்மானை சாரங்கர் சீரங்கம் தான்வந்தா ரென்றுசொல்லி சீரங்க மெல்லாம் செழித்து மிகவாழ்ந்து பாரெங்கு மெச்சிப் பரந்துகா ணம்மானை அப்படியே அச்சுதரும் அப்பதியி லங்கிருக்க எப்படியும் சீரங்கம் இனிதழைக்கு மென்றுமிக மூவர் முதலாய் முப்பத்து முக்கோடித் தேவருங் கொண்டாடி சிந்தைமகிழ்ந் தேயிருந்தார் சீரங்க மான திருப்பதியின் வளமை சாரங்க ராயர் தாமுரைத்தா ரம்மானை மாரியது மூன்று வருசிக்கத் தான்பொழிந்து ஏரி பெருகி ஏற்றசெந்நெல் தான்விளைந்து  படியொன்று கோட்டையொன்றாய்ப் பழுத்துவருஞ் செந்நெலது கரும்பு முத்தீனும் கசுவுங் கரைபுரளும் அரும்பு வனமும் அகில்தேக்கு மாமரமும் கோவுஞ் ஸ்ரீரங்கரெனக் கூப்பிடுங்கா ணச்சுதரை மாவுஞ் ஸ்ரீரங்கரென வந்தழைக்கு மச்சுதரை மயில்குயில் மான்கலையும் வாய்த்தஅணில் பட்சிகளும் ஒயிலாக அச்சுதரை உள்ளாக்கி யாடிவரும் அவ்வூரி லுள்ள அந்தணர்க ளெல்லோரும் கவ்வைவே றில்லாமல் கரியமால் தன்றனக்கு பூசை முறைவைத்துப் பிராமணர்கள் செய்துவர ஆசையுட னச்சுதரும் அங்கே யினிதிருந்தார்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(94,'சான்றோர் பெருமை','சீரங்க மாபதியில் சிறந்திருக்கு மப்போது சாரங்கர் பெற்ற சான்றோர் களின்பெருமை சொல்லுகிறா ரெங்கள் திருமால்கா ணம்மானை நல்லதுகா ணென்று நாடித் திருகேட்க அன்பான சான்றோர்க்கு ஆனமா காளியம்மை தன்பாலர் போலே தான்வளர்ந்து வித்தைகளும் வருத்திக் கொடுத்து மன்னர்மன்னர் தானாக்கி கருத்திலுறக் காளி கண்ணான மக்களுக்கு வேண்டும் பணிகளெல்லாம் விதவிதமாய்த் தானெடுத்துப்     பூண்டந்த மக்களையும் பூத்தான மாய்வளர்த்தாள் மிக்கநல்ல பிள்ளை வீரரென ஆகுகையில் தக்கனென்ற சூரன் தன்பேரில் மாகாளி படைக்கு யெழுந்தருளிப் பாலரையுந் தான்கூட்டி நடைக்கு அதிகமுள்ள நாயகியும் பாலருக்குப் போர்க் கோலமிட்டுப் புட்டாபுரங் கடந்து ஆர்க்க முள்ளகாளி அலகைப் படையுடனே சான்றோரை விட்டுத் தக்கனையுந் தான்வதைத்து மூன்றோரை நெஞ்சில்வைத்து ஓங்கார மாகாளி தக்கனையுங் கொன்று சான்றோ ரவர்தமக்கு மிக்க வரிசை மிகக்கொடுத்து மாகாளி புட்டா புரத்தில் பேய்க்கணங்கள் சூழ்ந்துநிற்க கட்டாத காளி கமலத்தில் வீற்றிருந்தாள்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(95,'சான்றோர் திருமணம்','அப்போது நல்ல ஆர்க்கமுள்ள பாலருக்கு இப்போது மாலையிட ஏழுபேர்க்கும் வேணுமென்று பார்த்து விசாரித்துப் பத்திர மாகாளி நாற்றிசையும் பார்த்து நாரத மாமுனியை அழைத்துநல்ல மாகாளி அந்தமுனி யோடுரைப்பாள் விழைத்துநல்ல புத்தி விபரமிட்டுச் சொல்லுகின்ற மாமுனியே யென்றன் மக்களேழு பேர்களுக்கும்  நீமுயன்று பெண்ணேழு நிச்சயிக்க வேணுமென்றாள் நல்லதுதா னென்று நாரத மாமுனிவன் வல்லவகை யாலுன் மக்களுக்குப் பெண்ணேழு பார்த்துவரு வேனென்று பகர்ந்துமுனி போயினனே சாற்று மொழியிசையத் தானுரைக்கு மாமுனிவன் நீதமுள்ள நல்ல நிருபதி ராசனுந்தான் பெற்ற மதலைதனைப் பெண்கேட்கப் போயினனே மாமுனிவன் கேட்க மன்ன னதிசயித்துத் தாமுனிந் தேது தானுரைப்பா னம்மானை நல்ல முனியே நானுரைக்க நீர்கேளும் செல்ல மகவு தேவிக்கு மென்றனக்கும் இல்லாம லனேகநாள் இருந்தோந் தவசாக நல்லான ஈசர் நாட்டமுட னிரங்கி ஆண்பிள்ளை யுன்றனக்கு ஆகமத்தி லில்லையென்று பெண்பிள்ளை யேழு பிறக்குமென்று சொன்னார்காண் அப்போது ஈசுரரை அடியேன் மிகப்பார்த்து இப்போது என்றனக்கு இரணமுடி வாகுகையில் கொள்ளிவைக்கப் பிள்ளை ஒன்று கொடுவுமென்றேன் தெள்ளிமை யாயீசர் சொன்ன மொழிகேளும் தெய்வச் சான்றோராய்த் திருவான மாகாளி  கையதுக்குள் வாழ்ந்துன் கன்னியேழு பேர்களையும் மாலையிட் டுன்றனக்கு வருமாபத் தையெல்லாம் மேலவராய்க் காத்து மேதினியோர் தாமறிய உன்றனக்கு நல்ல உதவிமிகச் செய்வதற்கும்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(96,'சான்றோர் திருமணம்','வந்தங் கிருப்பாரென வகுத்தாரே ஈசுரரும் ஆனதால் பெண்ணேழும் அவர்க்கேதா னல்லாது மான முனியே மற்றெவர்க்கு மாகாதே என்று நிருபதனும் ஏற்றமுனி யோடுரைக்க நன்று நன்றென்று நாரதருஞ் சம்மதித்து மன்னவனே கேளு மாகாளி தன்னிடத்தில் சொன்னபிள்ளை யேழும் சிறந்திருக்கி றாரெனவே அந்தமன்ன ரான ஆண்பிள்ளைக ளானோர்க்கு இந்த முகூர்த்தம் யான்கேட்க வந்தேனென்று சொல்லமுனி மன்னவனும் சோபித மாய்மகிழ்ந்து நல்ல முகூர்த்தம் நாளிட்டா னம்மானை நாளிட்டு நாரதரும் நல்லமா காளியுட தாளிணையைப் போற்றித் தானுரைத்தா ரம்மானை மாகாளி யுன்றனுட மக்களேழு பேர்களுக்கும் வாகான மன்னன் வாய்த்த நிருபனுட மக்களேழு பெண்களையும் மாலைமணஞ் சூட்டுதற்கு   மிக்கநா ளிட்டு மேவிவந்தேன் மெல்லியரே என்றுமுனி சொல்ல ஏற்றமா காளிசொல்வாள் நன்றுநல்ல மாமுனியே நான்வளர்த்த கண்மணிகள் தெய்வப் பிறவியல்லோ திசைவென்ற சான்றோர்கள் மெய்வரம் புள்ள மெல்லியரைச் சொல்லுமென்றாள் பியற்றிவந்த பெண்ணதுக்குப் பிறவியென்ன மாமுனியே உய்த்துவந்த பெண்ணேழின் உற்பவஞ்சொல் மாமுனியே அப்போது பெண்ணார்கள் ஆதிப் பிறவியெல்லாம் செப்புகிறோ மென்று சிவகாளிதனைத் தெண்டனிட்டு கேளாய் நீயென்று கிருபையுட னேதுரைப்பான் நாளான நாளதிலே நல்லதெய்வ லோகமதில் தெய்வேந் திரனார்க்குச் சொல்லேவல் செய்திருந்த நெய்நெடியக் கன்னி நேரிழைமா ரேழ்பேரும் வேலையின்ன தென்று விரித்துக்கேள் மாகாளி மாலையிலுங் காலையிலும் வானவர் கோன்றனக்குப்');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(97,'சான்றோர் திருமணம்','பூவெடுத் திட்டுப் பூசைபுனக் காரமிட்டுக் கோவெடுத்த ராசனுக்குக் குஞ்சமிட ஆடிநிதம் ஏவல் புரியுகின்ற இராசதெய்வக் கன்னியர்கள் நாவுலகு மெய்க்க நடந்துவரும் நாளையிலே பூவுலகு மன்னர்களைப் பூத்தான மாகவெண்ணி பாவினியக் கன்னி பஞ்சமது வேறாகிப் பூவெடுக்கக் கன்னி போகாமல் வாட்டமதாய் ஆவடுக்கங் கொண்டு அவர்க ளொருப்போலே காச்சல் குளிரெனவே கவ்வையுற்றுத் தானிருக்க மாச்சல் தனையறிந்தான் மகவானு மப்போது ஆனதால் வேதாவின் அண்டை யவனணுகி தீனம்வந்த தேதெனவே தேவியர்க்கு வேதாவே வேதா தெளிந்து விரித்துரைப்பா ரப்போது சூதான மான தோகை யேழுபேரும் பூலோக மன்னருக்குப் பிரியமுற்று மையல்கொண்டு காலோயு தென்று கவ்வையுற்ற தல்லாது வேறில்லை யென்று வேதா இதுவுரைக்க தேறியே வாசவர்கோன் தேவியெழு பேர்களையும் அப்படியே பூவுலகில் அமையுமென்றார் வேதாவை இப்படியே வந்து இவர்பிறந்தா ரென்றுமுனி சொல்லச் சிவகாளி சிரித்து மனமகிழ்ந்து நல்லதிது மாமுனியே நன்முகூர்த்தம் பார்த்துரைநீ உடனே முனியும் உகந்த முகூர்த்தமென்று திடமே நிருபனுக்குச் செய்திசொல்ல ஆளும்விட்டு நாளை முகூர்த்தமென்று நாற்றிசையுந் தானறிய கோழையில்லா மன்னர் குலதெய்வச் சான்றோர்க்கு முகூர்த்தமென்று சாற்றி மூவுலகுந் தானறிய பகுத்துவமாய் நல்ல பந்தலிட்டா ரம்மானை வயிரக்கால் நாட்டி வயிர வளைகளிட்டுத் துயிரமுள்ள தங்கத் தூண்கள் மிகநாட்டி முத்து நிரைத்து முதுபவள வன்னியிட்டுக் கொத்துச் சரப்பளியைக் கோர்மாலையாய்த் தூக்கி');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(98,'சான்றோர் திருமணம்','மேற்கட்டி கட்டி மேடைபொன் னாலேயிட்டு காற்கட்டி பட்டுக் கனமா யலங்கரித்து வாழை கரும்பு வகைவகையாய் நாட்டிமிக நாளை மணமென்று நாட்டுக்குப் பாக்குவைத்து வெடிபூ வாணம் மிகுத்தகம்பச் சக்கரமும் திடிம னுடன்மேளம் சேவித்தார் பந்தலுக்குள் வாச்சியங்க ளின்னதென்று வகைசொல்லக் கூடாது நாச்சியார் மாகாளி நாயகியுங் கொண்டாடி பாலருக் கேற்ற பணியெல்லாந் தான்பூட்டிச் சாலமுள்ள சிங்கத் தண்டிகையில் தானிருத்தி பட்டணப் பிரவேசம் பகல்மூன்று ராமூன்று இட்டமுடன் பூதம் எடுத்துமிகச் சுற்றிவந்து மண்டபத்துள் வைத்து மாபூதஞ் சூழ்ந்துநிற்க  தெண்டனிட்டு மன்னர் தேசாதி சூழ்ந்துநிற்க பதினெட்டு வாத்தியமும் பட்டணமெங் குமுழங்க ததிதொம் மெனவேசில தம்புருசா ரங்கிகெம்ப பந்தமது பிடித்துப் பலபூதஞ் சூழ்ந்துநிற்க நந்தகோ பால நாரா யணர்மகிழ்ந்து மைந்தருக் கின்று மணமுண்டு என்றுசொல்லி வைந்தர்பல தேவர்களை வாருமென வேயழைத்துப் பாலருட முகூர்த்தம் பார்க்கப்போ மென்றுசொல்லி சீலமுட னனுப்பி ஸ்ரீரங்க மேயிருந்தார் தேவதே வர்புகழத் திசைவென்ற சான்றோர்க்கு மூவர்களும் வந்து உதவிசெய்தா ரம்மானை நல்ல முகூர்த்தம் நாள்பார்த்துச் சான்றோர்கள் எல்லாச் சடங்கும் இயற்றிவைத்தா ரம்மானை சடங்கு முகித்துச் சான்றோரை யொப்பமிட்டுத் தடங்கொண்ட மன்னவரைத் தண்டிகையின் மீதிருத்தி அரசர் மிகச்சூழ அணியீட்டி யாட்கள்வர விரைவாகக் காளி வித்தகலை யேறிவர அரம்பையர்க ளாடிவர அயிராவதத் தோன்வரவே வரம்பெரிய தேவர் மலர்மாரி தூவிவர');");
        db.execSQL("INSERT INTO akilathirattu2 VALUES(99,'சான்றோர் திருமணம்','ஆலத்தி யேந்தி அணியணியாய்த் தான்வரவே  மூலத்தி கன்னி மோகினியாள் தன்னுடனே பூதம தாடி பிடாரியது சூழ்ந்துவர நாதம் ரெட்டையூதி நடந்தார்மாப் பிள்ளைகொண்டு விருதிற் பெரிய வீரதெய்வச் சான்றோர்கள் நிருபன் மகளை நியமித்து மாலையிட வீரியமாய் நல்ல விவாகமிட வாறாரெனப் பூரியலிற் சொல்லி பேர்கள் சிலரூத இப்படியே வாத்தியங்கள் இசைந்திசைந் தேவூதி அப்படியே நிருபன் அரசன் மகள்தனையும் மாலை மணமிட்டு வாழவந்தா ரம்மானை வாலையது வான மாகாளி வாழுகின்ற மண்டபத்துள் வந்து மகிழ்ந்திருந்தா ரம்மானை தண்டமிழுங் கன்னி சான்றோர்க ளானோர்க்குக் கோட்டையு மிட்டுக் குமாரரையும் பெண்ணதையும் தாட்டிமையாய்ச் சான்றோர்க்குத் தரந்தரமே மாளிகையும் வைத்துக் கொடுத்தாள் வாழ்ந்திருந்தார் சான்றோர்கள் மெய்த்துப்புவி கொண்டாட மெல்லியர்கள் தங்களுக்கு வயிற்றில் கெற்பமாகி மதலைபெற்றா ரம்மானை நெய்த்தெளிய கன்னி நேரிழைமா ரெல்லோரும் இப்படியே கன்னி எல்லோரும் நன்றாக அப்படியே மதலைபெற்று அகமகிழ்ந்தா ரம்மானை பாலருக்குப் பாலர் பரிவாய் மிகவளர சீலமுள்ள வித்தை சிறக்க அவர்வருத்தி எமத்திறைவ ராக எங்கும் புகழ்ந்திடவே சமர்த்த ரெனஅவர்கள் தலையெடுத்தா ரம்மானை முத்து வியாபாரம் முதல்வியா பாரமுதல் பத்தரை மாற்றுப் பசும்பொன்வியா பாரமுடன் மாணிக்க வியாபாரம் வியிரப்பொன் வியாபாரம் ஆணிப்பொன் வியாபாரம் அழகுகச்சை வியாபாரம் கப்பல் வியாபாரம் கறிமிளகு வியாபாரம் ஒப்பமுள்ள வியாபாரம் ஒக்கமிகச் செய்தனராம்');");
