    public AkilathirattuDAO getAkilamContent(int id) {
        SQLiteDatabase db = this.getReadableDatabase();

        Cursor cursor = db.query("akilathirattu2", new String[] { "rollno",
                        "title", "content" }, "rollno" + "=?",
                new String[] { String.valueOf(id) }, null, null, null, null);
        if (cursor != null)
            cursor.moveToFirst();

        AkilathirattuDAO akilamContent = new AkilathirattuDAO(Integer.parseInt(cursor.getString(0)),
                cursor.getString(1), cursor.getString(2));

        return akilamContent;
    }




